window.QURAN_EN_AHMEDRAZA = {"quran":{"en.ahmedraza":{"1":{"id":1,"surah":1,"ayah":1,"verse":"All praise is to Allah, the Lord Of The Creation."},"2":{"id":2,"surah":1,"ayah":2,"verse":"The Most Gracious, the Most Merciful"},"3":{"id":3,"surah":1,"ayah":3,"verse":"Owner of the Day of Recompense"},"4":{"id":4,"surah":1,"ayah":4,"verse":"You alone we worship and from You alone we seek help (and may we always)."},"5":{"id":5,"surah":1,"ayah":5,"verse":"Guide us on the Straight Path."},"6":{"id":6,"surah":1,"ayah":6,"verse":"The path of those whom You have favoured \u2013"},"7":{"id":7,"surah":1,"ayah":7,"verse":"Not the path of those who earned Your anger \u2013 nor of those who are astray."},"8":{"id":8,"surah":2,"ayah":1,"verse":"Alif-Laam-Meem. (Alphabets of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"9":{"id":9,"surah":2,"ayah":2,"verse":"This is the exalted Book (the Qur\u2019an), in which there is no place for doubt; a guidance for the pious."},"10":{"id":10,"surah":2,"ayah":3,"verse":"Those who believe without seeing (the hidden), and keep the (obligatory) prayer established, and spend in Our cause from what We have bestowed upon them."},"11":{"id":11,"surah":2,"ayah":4,"verse":"And who believe in this (Qur\u2019an) which has been sent down upon you, O beloved Prophet, (Mohammed \u2013 peace and blessings be upon him) and what was sent down before you; and are certain of the Hereafter."},"12":{"id":12,"surah":2,"ayah":5,"verse":"It is they who are on guidance from their Lord; and they are the successful."},"13":{"id":13,"surah":2,"ayah":6,"verse":"As for those whose fate is disbelief, whether you warn them or do not warn them \u2013 it is all one for them; they will not believe. (Because their hearts are sealed \u2013 see next verse)."},"14":{"id":14,"surah":2,"ayah":7,"verse":"Allah has sealed their hearts and their ears, and on their eyes is a covering; and for them is a terrible punishment."},"15":{"id":15,"surah":2,"ayah":8,"verse":"And among the people are some* that say, \u201cWe believe in Allah and the Last Day**\u201d whereas they are not believers! (* The hypocrites ** the Day of Resurrection)"},"16":{"id":16,"surah":2,"ayah":9,"verse":"They wish to deceive Allah and the believers; and in fact they deceive none except themselves and they do not have any understanding."},"17":{"id":17,"surah":2,"ayah":10,"verse":"In their hearts is a disease, so Allah has increased their disease; and for them is a painful punishment, because of their lies."},"18":{"id":18,"surah":2,"ayah":11,"verse":"And when it is said to them, \u201cDo not cause turmoil in the earth\u201d, they say, \u201cWe are only peacemakers!\u201d"},"19":{"id":19,"surah":2,"ayah":12,"verse":"Pay heed! It is they who are mischievous, but they do not have sense."},"20":{"id":20,"surah":2,"ayah":13,"verse":"And when it is said to them, \u201cBelieve as the others believe\u201d, they say, \u201cShall we believe as the foolish believe?\u201d It is they who are the fools, but they do not know."},"21":{"id":21,"surah":2,"ayah":14,"verse":"And when they meet with the believers, they say, \u201cWe believe\u201d; and when they are alone with their devils, they say, \u201cWe are undoubtedly with you, we were just mocking!\u201d"},"22":{"id":22,"surah":2,"ayah":15,"verse":"Allah (befitting His Majesty) mocks them, leaving them to wander blindly in their rebellion."},"23":{"id":23,"surah":2,"ayah":16,"verse":"These are the people who purchased error in exchange of guidance \u2013 so their bargain did not profit them, and they did not know how to trade."},"24":{"id":24,"surah":2,"ayah":17,"verse":"Their example is like that of one who kindled a fire; and when it lit up all that was around it, Allah took away their light and left them in darkness, unable to see anything."},"25":{"id":25,"surah":2,"ayah":18,"verse":"Deaf, dumb and blind; and they are not to return."},"26":{"id":26,"surah":2,"ayah":19,"verse":"Or like a rainstorm from the sky, in which are darkness, thunder and lightning; they thrust their fingers in their ears due to the thunderclaps, fearing death; and Allah has the disbelievers encompassed."},"27":{"id":27,"surah":2,"ayah":20,"verse":"It seems the lightning may snatch away their sight from them; whenever it flashes they walk in it, and when it darkens they stand still; if Allah willed, He could take away their hearing and their sight; indeed Allah is Able to do all things."},"28":{"id":28,"surah":2,"ayah":21,"verse":"O mankind! Worship your Lord, Who has created you and those before you, in the hope of attaining piety."},"29":{"id":29,"surah":2,"ayah":22,"verse":"The One Who has appointed the earth a base for you, and the sky a canopy \u2013 and caused water to pour down from the sky, thereby producing fruits as food for you; and do not knowingly set up rivals to Allah!"},"30":{"id":30,"surah":2,"ayah":23,"verse":"And if you are in any doubt concerning what We have sent down upon Our distinguished bondman (Prophet Mohammed \u2013 peace and blessings be upon him), bring forth a single surah (chapter) equal to it; and call upon all your supporters, other than Allah, if you are truthful."},"31":{"id":31,"surah":2,"ayah":24,"verse":"And if you are unable to bring forth (one chapter) \u2013 and We declare that you can never bring one \u2013 then fear the fire (of hell), the fuel of which is men and stones; kept ready for the disbelievers."},"32":{"id":32,"surah":2,"ayah":25,"verse":"And give glad tidings to those who believe and do good deeds; that for them are Gardens beneath which rivers flow; when they are provided with a fruit of the Gardens, they will say, \u201cThis is the same food as what was given to us before\u201d whereas it is in resemblance; and in the Gardens are pure spouses for them; and they shall abide in it forever."},"33":{"id":33,"surah":2,"ayah":26,"verse":"Indeed Allah does not, for the sake of explanation, shy to illustrate an example of anything, whether it is of a gnat or something further (inferior) than it; so the believers know it is the Truth from their Lord; as for the disbelievers, they say, \u201cWhat does Allah intend by such an example?\u201d He misleads many thereby, and He guides many thereby; and with it He misleads only those who are rebellious."},"34":{"id":34,"surah":2,"ayah":27,"verse":"Those who break the covenant of Allah after ratifying it \u2013 and sever what Allah has ordered to join, and who cause turmoil (evil \/ religious chaos) in the earth; it is they who are the losers."},"35":{"id":35,"surah":2,"ayah":28,"verse":"What has made you disbelieve in Allah? Whereas you were dead and He gave you life; then He will give you death, then bring you to life again, and then it is to Him you will return!"},"36":{"id":36,"surah":2,"ayah":29,"verse":"It is He Who created for you all that is in the earth; then He inclined towards the heaven, therefore fashioning it as proper seven heavens; and He knows everything."},"37":{"id":37,"surah":2,"ayah":30,"verse":"And (remember) when your Lord said to the angels, \u201cI am about to place My Caliph in the earth\u201d; they said, \u201cWill You place (as a caliph) one who will spread turmoil in it and shed blood? Whereas we glorify You with praise and proclaim Your Sanctity\u201d; He said, \u201cI know what you do not.\u201d"},"38":{"id":38,"surah":2,"ayah":31,"verse":"And Allah the Supreme taught Adam all the names (of things), then presented them to the angels, saying, \u201cTell Me the names of these, if you are truthful.\u201d"},"39":{"id":39,"surah":2,"ayah":32,"verse":"They said, \u201cPurity is to You! We do not have any knowledge except what You have taught us! Indeed You only are the All Knowing, the Wise.\u201d"},"40":{"id":40,"surah":2,"ayah":33,"verse":"He said \u201cO Adam! Inform them the names\u201d; and when Adam had informed them their names, He said, \u201cDid I not tell you that I know all the secrets of the heavens and the earth? And I know all what you disclose and all what you hide?\u201d"},"41":{"id":41,"surah":2,"ayah":34,"verse":"And (remember) when We ordered the angels to prostrate before Adam, so they all prostrated, except Iblis (Satan \u2013 devil); he refused and was proud \u2013 and became a disbeliever."},"42":{"id":42,"surah":2,"ayah":35,"verse":"And We said, \u201cO Adam! You and your wife dwell in this Garden, and eat freely from it wherever you please \u2013 but do not approach this tree for you will become of those who transgress.\u201d"},"43":{"id":43,"surah":2,"ayah":36,"verse":"So the devil destabilised them in it and removed them from where they were \u2013 and We said, \u201cGo down, one of you is an enemy to the other; and for a fixed time you shall stay on earth and feed in it.\u201d"},"44":{"id":44,"surah":2,"ayah":37,"verse":"Then Adam learnt from his Lord certain words (of revelation), therefore Allah accepted his repentance; indeed He only is the Most Acceptor of Repentance, the Most Merciful. (See Verse 7:23)"},"45":{"id":45,"surah":2,"ayah":38,"verse":"We said, \u201cGo down from Paradise, all of you; then if some guidance comes to you from Me \u2013 so whoever follows My guidance, for such is neither fear nor any grief.\u201d"},"46":{"id":46,"surah":2,"ayah":39,"verse":"And those who disbelieve and deny Our signs, are the people of fire (hell); they will remain in it forever."},"47":{"id":47,"surah":2,"ayah":40,"verse":"O Descendants of Israel (Jacob)! Remember My favour which I bestowed upon you, and fulfil your covenant towards Me, I shall fulfil My covenant towards you; and fear Me alone."},"48":{"id":48,"surah":2,"ayah":41,"verse":"And accept faith in what I have sent down (the Qur\u2019an), which confirms what is with you (the Torah \/ Bible), and do not be the first to disbelieve in it \u2013 and do not exchange My verses for an abject \u2013 and fear Me alone."},"49":{"id":49,"surah":2,"ayah":42,"verse":"And do not mix the truth with falsehood, nor purposely conceal the truth."},"50":{"id":50,"surah":2,"ayah":43,"verse":"And keep the (obligatory) prayer established, and pay the charity, and bow your heads with those who bow (in prayer)."},"51":{"id":51,"surah":2,"ayah":44,"verse":"What! You enjoin righteousness upon people while you forget (to practise it) yourselves, whereas you read the Book? Do you not have sense?"},"52":{"id":52,"surah":2,"ayah":45,"verse":"And seek help in patience and prayer; and truly it is hard except for those who prostrate before Me with sincerity."},"53":{"id":53,"surah":2,"ayah":46,"verse":"Who know that they have to meet their Lord, and that it is to Him they are to return."},"54":{"id":54,"surah":2,"ayah":47,"verse":"O Descendants of Israel! Remember the favour of Mine, which I bestowed upon you and gave you superiority over others of your time. (by sending the Noble Messengers to your nation)"},"55":{"id":55,"surah":2,"ayah":48,"verse":"And fear the Day (of Resurrection) when no soul will be exchanged for another, nor will any intercession be accepted for the disbelievers, nor will they be set free in lieu of compensation nor will they be helped."},"56":{"id":56,"surah":2,"ayah":49,"verse":"(And remember) When We rescued you from Firaun\u2019s people, for they were inflicting you with a dreadful torment, slaying your sons and sparing your daughters; that was a tremendous trial from your Lord (or a great reward)."},"57":{"id":57,"surah":2,"ayah":50,"verse":"And when We split the sea for you thereby rescuing you, and drowned the Firaun's people in front of your eyes."},"58":{"id":58,"surah":2,"ayah":51,"verse":"And when We made a commitment with Moosa (Moses) for forty nights \u2013 then behind him you started worshipping the calf, and you were unjust."},"59":{"id":59,"surah":2,"ayah":52,"verse":"Then after that We pardoned you so that you may be grateful."},"60":{"id":60,"surah":2,"ayah":53,"verse":"And when We gave Moosa the Book (Taurat \/ Torah) and the criterion to judge right from wrong, so that you may attain guidance."},"61":{"id":61,"surah":2,"ayah":54,"verse":"And when Moosa said to his people, \u201cO my people! You have wronged yourselves by taking the calf,* therefore turn in repentance to your Creator, therefore kill each other; this is better for you before your Creator\u201d; He therefore accepted your repentance; indeed He only is the Most Acceptor of Repentance, the Most Merciful. (* as your deity for worship)"},"62":{"id":62,"surah":2,"ayah":55,"verse":"And when you said \u201cO Moosa! We will not believe you till we clearly see Allah\u201d; so the thunder seized you while you were watching."},"63":{"id":63,"surah":2,"ayah":56,"verse":"Then We brought you back to life after your death, so that you may be grateful."},"64":{"id":64,"surah":2,"ayah":57,"verse":"And We made the clouds a canopy for you and sent down Manna and Salwa (birds) on you; \u201cEat of the pure things We have provided you\u201d; they did not wrong Us in the least, but indeed they wronged themselves."},"65":{"id":65,"surah":2,"ayah":58,"verse":"And when We said, \u201cEnter this town and eat freely from what is in it, and enter the gate whilst prostrating, and say, \u2018May our sins be forgiven\u2019 \u2013 We will forgive you your sins; and We will soon increase the reward for the righteous.\u201d"},"66":{"id":66,"surah":2,"ayah":59,"verse":"But the unjust changed the word that had been ordered for another one, so We sent down a punishment on them from the skies, the recompense of their disobedience."},"67":{"id":67,"surah":2,"ayah":60,"verse":"And when Moosa asked for water for his people, We said, \u201cStrike this rock with your staff\u201d; thereupon twelve springs gushed forth from it; each group recognised its drinking-place; \u201cEat and drink from what Allah has provided, and do not roam about the earth making turmoil in it.\u201d"},"68":{"id":68,"surah":2,"ayah":61,"verse":"And when you said, \u201cO Moosa! We shall never put up with only one kind of food, so call upon your Lord to produce for us what the earth grows \u2013 some herbs, cucumbers, corn, lentils and onions\u201d; he said, \u201cWhat! You wish to exchange the better for something inferior? Therefore settle down in Egypt or any city, where you will get what you demand\u201d; and disgrace and misery were destined for them; and they returned towards Allah\u2019s wrath; that was because they disbelieved in Allah\u2019s signs and wrongfully martyred the Prophets; that was for their disobedience and transgression."},"69":{"id":69,"surah":2,"ayah":62,"verse":"Indeed the believers (the Muslims) and those among the Jews, the Christians, and the Sabeans who sincerely accept faith in Allah and the Last Day* and do good deeds \u2013 their reward is with their Lord; and there shall be no fear upon them nor shall they grieve. (* i.e. convert to Islam)"},"70":{"id":70,"surah":2,"ayah":63,"verse":"And when We made a covenant with you and raised the Mount above you; \u201cAccept and hold fast to what We give you, and remember what is in it, so that you may attain piety.\u201d"},"71":{"id":71,"surah":2,"ayah":64,"verse":"Then after that, you turned away; and were it not for the munificence of Allah and His mercy, you would be among the losers."},"72":{"id":72,"surah":2,"ayah":65,"verse":"And you certainly know of those amongst you who transgressed in the matter of Sabth (Sabbath \u2013 Saturday) \u2013 We therefore said to them, \u201cBecome apes, despised!\u201d"},"73":{"id":73,"surah":2,"ayah":66,"verse":"So We made this incident (of that town) a warning to the surrounding towns (others of their time) and to succeeding generations, and a lesson for the pious."},"74":{"id":74,"surah":2,"ayah":67,"verse":"And (remember) when Moosa said to his people, \u201cAllah commands you to sacrifice a cow\u201d; they said, \u201cAre you making fun of us?\u201d He answered, \u201cAllah forbid that I should be of the ignorant!\u201d"},"75":{"id":75,"surah":2,"ayah":68,"verse":"They said, \u201cPray to your Lord that He may describe the cow\u201d; said Moosa, \u201cHe says that it is a cow neither old nor very young but between the two conditions; so do what you are commanded.\u201d"},"76":{"id":76,"surah":2,"ayah":69,"verse":"They said, \u201cPray to your Lord that He may reveal its colour to us\u201d; answered Moosa, \u201cIndeed He says it is a yellow cow, of bright colour, pleasing to the beholders.\u201d"},"77":{"id":77,"surah":2,"ayah":70,"verse":"They said, \u201cPray to your Lord that He may clearly describe the cow to us, we are really in a doubt as to which cow it is; and if Allah wills, we will attain guidance.\u201d"},"78":{"id":78,"surah":2,"ayah":71,"verse":"Said Moosa, \u201cHe says, \u2018She is a cow not made to work, neither ploughing the soil nor watering the fields; flawless and spotless\u2019; they said, \u201cYou have now conveyed the proper fact\u201d; so they sacrificed it, but seemed not to be sacrificing it (with sincerity)."},"79":{"id":79,"surah":2,"ayah":72,"verse":"And (remember) when you slew a man and were therefore accusing each other concerning it; and Allah wanted to expose what you were hiding."},"80":{"id":80,"surah":2,"ayah":73,"verse":"We therefore said, \u201cStrike the dead man with a part of the sacrificed cow\u201d; this is how Allah will bring the dead to life, and shows you His signs so that you may understand!"},"81":{"id":81,"surah":2,"ayah":74,"verse":"Then after it, your hearts hardened \u2013 so they are like rocks, or even harder; for there are some rocks that rivers gush forth from them; and some that water flows from them when they split asunder; and there are rocks that fall down for the fear of Allah; and Allah is not unaware of your deeds."},"82":{"id":82,"surah":2,"ayah":75,"verse":"So O Muslims, do you wish for the Jews to accept faith in you whereas a group of them used to listen to the Words of Allah, and then after having understood it, purposely changed it?"},"83":{"id":83,"surah":2,"ayah":76,"verse":"And when they meet the believers, they say, \u201cWe believe\u201d; but when they are in isolation with one another they say, \u201cYou clarify to the believers from what Allah has disclosed to you, so that they may evidence it against you before your Lord? So have you no sense?\u201d"},"84":{"id":84,"surah":2,"ayah":77,"verse":"Do they not know that Allah knows all whatever they hide and whatever they disclose?"},"85":{"id":85,"surah":2,"ayah":78,"verse":"And among them are the unlearned that do not know anything of the Book except to recite something therefrom or parts of their own fabrications; they are in absolute illusion."},"86":{"id":86,"surah":2,"ayah":79,"verse":"Therefore woe is to those who write the Book with their hands; and they then claim, \u201cThis is from Allah\u201d in order to gain an abject (worldly) price for it; therefore woe to them for what their hands have written, and woe to them for what they earn with it."},"87":{"id":87,"surah":2,"ayah":80,"verse":"And they said, \u201cThe fire will not touch us except for a certain number of days\u201d; say, \u201cHave you taken a covenant from Allah \u2013 then Allah will certainly not break His covenant \u2013 or do you say something concerning Allah what you do not know?\u201d"},"88":{"id":88,"surah":2,"ayah":81,"verse":"Yes, why not?* The one who earns evil and his sin surrounds him; he is from the people of fire (hell); they will remain in it forever. (You will remain in the fire forever)."},"89":{"id":89,"surah":2,"ayah":82,"verse":"And those who believe and do good deeds \u2013 they are the People of Paradise; they will abide in it forever."},"90":{"id":90,"surah":2,"ayah":83,"verse":"And (remember) when We took a covenant from the Descendants of Israel that, \u201cDo not worship anyone except Allah; and be good to parents, relatives, orphans and the needy, and speak kindly to people and keep the prayer established and pay the charity\u201d; thereafter you retracted, except some of you; and you are those who turn away."},"91":{"id":91,"surah":2,"ayah":84,"verse":"And when We took a covenant from you that, \u201cDo not shed the blood of your own people nor turn out your own people from your colonies\u201d; you then acknowledged it and you are witnesses."},"92":{"id":92,"surah":2,"ayah":85,"verse":"Then it is you who began slaying each other and you drive out a group of your people from their homeland \u2013 providing support against them (to their opponents) through sin and injustice; and if they come to you as captives you redeem them, whereas their expulsion itself is forbidden to you; so do you believe in some of Allah\u2019s commands and disbelieve in some? So what is the reward of those who do so, except disgrace in this world? And on the Day of Resurrection they will be assigned to the most grievous punishment; and Allah is not unaware of your deeds."},"93":{"id":93,"surah":2,"ayah":86,"verse":"These are the people who bought the worldly life in exchange of the Hereafter \u2013 so their punishment will not be lightened, nor will they be helped."},"94":{"id":94,"surah":2,"ayah":87,"verse":"And indeed We gave Moosa (Moses) the Book and subsequent to him, sent Noble Messengers one after another \u2013 and We gave Eisa (Jesus), the son of Maryam (Mary), clear proofs and supported him with the Holy Spirit; so when a Noble Messenger from Allah comes to you bringing what you yourselves do not desire, you grow arrogant; so you disbelieve in a group of the Prophets and another group of Prophets you slay!"},"95":{"id":95,"surah":2,"ayah":88,"verse":"And the Jews said, \u201cOur hearts are covered\u201d; in fact Allah has cursed them because of their disbelief, so only a few of them accept faith."},"96":{"id":96,"surah":2,"ayah":89,"verse":"And when the Book from Allah (the Holy Qu\u2019ran) came to them, which confirms the Book in their possession (the Taurat \/ Torah) \u2013 and before that they used to seek victory through the medium of this very Prophet (Mohammed \u2013 peace and blessings be upon him) over the disbelievers; so when the one whom they fully recognised (the Holy Prophet) came to them, they turned disbelievers \u2013 therefore Allah\u2019s curse is upon the disbelievers."},"97":{"id":97,"surah":2,"ayah":90,"verse":"How abject is the price for which they exchange their lives that they should disbelieve in what Allah has sent down, jealous that Allah should reveal of His grace to whomever He wills of His bondmen! So they deserved wrath upon wrath; and for the disbelievers is a disgraceful punishment."},"98":{"id":98,"surah":2,"ayah":91,"verse":"And when it is said to them, \u201cBelieve in what Allah has sent down\", they say, \u201cWe believe in what was sent down to us, and disbelieve in the rest\u201d \u2013 whereas it is the Truth confirming what they possess! Say (to them, O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhy did you then martyr the earlier Prophets, if you believed in your Book?\u201d"},"99":{"id":99,"surah":2,"ayah":92,"verse":"And indeed Moosa came to you with clear signs, and after it you worshipped the calf \u2013 and you were unjust."},"100":{"id":100,"surah":2,"ayah":93,"verse":"And remember when We made a covenant with you and raised the Mount Tur (Sinai) above you; \u201cAccept and hold fast to what We give you, and listen\u201d; they said, \u201cWe hear and we disobey\u201d; and the calf was still embedded in their hearts because of their disbelief; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhat an evil command is what your faith orders you, if you are believers!\u201d"},"101":{"id":101,"surah":2,"ayah":94,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIf the abode of the Hereafter in the sight of Allah is for you alone and none else, then long for death if you are truthful!\u201d"},"102":{"id":102,"surah":2,"ayah":95,"verse":"And they will never long for it, because of the evil deeds they have done in the past; and Allah knows the unjust, very well."},"103":{"id":103,"surah":2,"ayah":96,"verse":"And you will surely find them the greediest among mankind for life; and (likewise) among the polytheists (idolaters); each one of them yearns to live a thousand years; and the grant of such age will not distance him from the punishment; and Allah is seeing their misdeeds."},"104":{"id":104,"surah":2,"ayah":97,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhoever is an enemy to Jibreel (Gabriel)\u201d \u2013 for it is he who has brought down this Qu\u2019ran to your heart by Allah\u2019s command, confirming the Books before it, and a guidance and glad tidings to Muslims. \u2013"},"105":{"id":105,"surah":2,"ayah":98,"verse":"\u201cWhoever is an enemy to Allah, and His angels and His Noble Messengers, and Jibreel and Mikaeel (Michael) -, then (know that), Allah is an enemy of the disbelievers.\u201d"},"106":{"id":106,"surah":2,"ayah":99,"verse":"We have indeed sent down to you clear signs; and none will disbelieve in them except the sinners."},"107":{"id":107,"surah":2,"ayah":100,"verse":"And is it that whenever they make a covenant, only a group of them throws it aside? In fact, most of them do not have faith."},"108":{"id":108,"surah":2,"ayah":101,"verse":"And when a Noble Messenger from Allah came to them, confirming the Book(s) which they possessed, a group of those who have received the Book(s) flung the Book of Allah behind their backs as if they were totally unaware!"},"109":{"id":109,"surah":2,"ayah":102,"verse":"And they followed what the devils used to read during the rule of Sulaiman (Solomon \u2013 peace and blessings be upon him); and Sulaiman did not disbelieve, but the devils disbelieved \u2013 they teach people magic; and that which was sent down to the two angels, Harut and Marut in Babylon; and the two (angels) never taught a thing to anyone until they used to say, \u201cWe are only a trial, therefore do not lose your faith\u201d and they used to learn from them that by which they cause division between man and his wife; and they cannot harm anyone by it except by Allah\u2019s command; and they learn what will harm them, not benefit them; and surely they know that whoever bargains for this will not have a share in the Hereafter; and for what an abject thing they have sold themselves; if only they knew!"},"110":{"id":110,"surah":2,"ayah":103,"verse":"And had they believed and been pious, then the recompense from Allah is extremely good; if only they knew!"},"111":{"id":111,"surah":2,"ayah":104,"verse":"O People who Believe, do not say (to the Prophet Mohammed- peace and blessings be upon him), \u201cRaena (Be considerate towards us)\u201d but say, \u201cUnzurna (Look mercifully upon us)\", and listen attentively from the start; and for the disbelievers is a painful punishment. (To disrespect the Holy Prophet \u2013 peace and blessings be upon him \u2013 is blasphemy.)"},"112":{"id":112,"surah":2,"ayah":105,"verse":"Those who disbelieve \u2013 the People given the Book(s) or the polytheists \u2013 do not wish that any good be sent down upon you from your Lord; and Allah chooses whomever He wills by His Mercy; and Allah is the Most Munificent."},"113":{"id":113,"surah":2,"ayah":106,"verse":"When We abrogate a verse or cause it to be forgotten, We will bring one better than it or one similar; do you not know that Allah is Able to do all things?"},"114":{"id":114,"surah":2,"ayah":107,"verse":"Do you not know that for Allah only is the kingship of the heavens and the earth? And except Allah, you have neither a protector nor any supporter?"},"115":{"id":115,"surah":2,"ayah":108,"verse":"Do you wish to ask your Noble Messenger a question similar to what Moosa was asked before? And whoever chooses disbelief instead of faith has gone astray from the Right Path."},"116":{"id":116,"surah":2,"ayah":109,"verse":"Many among People given the Book(s) wished to turn you to disbelief after you had accepted faith; out of hearts\u2019 envy, after the truth has become very clear to them; so leave them and be tolerant, until Allah brings His command; indeed Allah is Able to do all things."},"117":{"id":117,"surah":2,"ayah":110,"verse":"And keep the prayer established, and pay the charity; and whatever good you send ahead for yourselves, you will find it with Allah; indeed Allah is seeing your deeds."},"118":{"id":118,"surah":2,"ayah":111,"verse":"And the People given the Book(s) said, \u201cNone will enter Paradise unless he is a Jew or a Christian\u201d; these are their own imaginations; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cBring your proof, if you are truthful.\u201d"},"119":{"id":119,"surah":2,"ayah":112,"verse":"Yes, why not? Whoever submits his face for the sake of Allah, and is virtuous, his reward is with his Lord; and there shall be no fear upon them nor shall they grieve."},"120":{"id":120,"surah":2,"ayah":113,"verse":"And the Jews said, \u201cThe Christians are nothing\u201d \u2013 and the Christians said, \u201cThe Jews are nothing\u201d whereas they both read the Book; and the ignorant spoke similarly; so Allah will judge between them on the Day of Resurrection, concerning the matter in which they dispute."},"121":{"id":121,"surah":2,"ayah":114,"verse":"And who is more unjust than one who prevents the name of Allah being mentioned in the mosques, and strives for their ruin? It did not befit them to enter the mosques except in fear; for them is disgrace in this world, and a terrible punishment in the Hereafter."},"122":{"id":122,"surah":2,"ayah":115,"verse":"And the East and the West, all belong to Allah \u2013 so whichever direction you face, there is Allah\u2019s Entity (Allah\u2019s Mercy is directed towards you); indeed Allah is the All Capable, (His powers and reach are limitless), the All Knowing."},"123":{"id":123,"surah":2,"ayah":116,"verse":"And they said, \u201cAllah has taken an offspring for Himself\u201d \u2013 Purity is to Him! In fact, all that is in the heavens and the earth, is His dominion; all are submissive to Him."},"124":{"id":124,"surah":2,"ayah":117,"verse":"The Originator of the heavens and the earth \u2013 and when He commands a thing, He only says to it, \u201cBe\", and it thereupon happens."},"125":{"id":125,"surah":2,"ayah":118,"verse":"And the ignorant people said, \u201cWhy does not Allah speak to us, or some sign come to us?\u201d Those before them had also spoken in the same way as they speak; their hearts (and of those before them) are all alike; undoubtedly, We have made the signs clear for the people who have faith."},"126":{"id":126,"surah":2,"ayah":119,"verse":"Undoubtedly, We have sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) with the truth, giving glad tidings and conveying warning, and you will not be questioned about the people of hell."},"127":{"id":127,"surah":2,"ayah":120,"verse":"And never will the Jews or the Christians be pleased with you, until you follow their religion; say, \u201cThe guidance of Allah only is the (true) guidance\u201d; and were you (the followers of this Prophet) to follow their desires after the knowledge has come to you, you would then not have a protector or aide against Allah."},"128":{"id":128,"surah":2,"ayah":121,"verse":"Those to whom We have given the Book, read it in the manner it should be read; it is they who believe in it; and those who deny it \u2013 it is they who are the losers."},"129":{"id":129,"surah":2,"ayah":122,"verse":"O Descendants of Israel! Remember the favour of Mine which I bestowed upon you and made you superior to all others of your time. (By sending Noble Messengers among you)."},"130":{"id":130,"surah":2,"ayah":123,"verse":"And fear the day when no soul will be exchanged for another, nor will they be set free in lieu of compensation, nor will any intercession benefit the disbelievers, nor will they be helped."},"131":{"id":131,"surah":2,"ayah":124,"verse":"And (remember) when Ibrahim\u2019s (Abraham\u2019s) Lord tested him in some matters and he fulfilled them; He said, \u201cI am going to appoint you as a leader for mankind\u201d; invoked Ibrahim, \u201cAnd of my offspring\u201d; He said, \u201cMy covenant does not include the unjust (wrong-doers).\u201d"},"132":{"id":132,"surah":2,"ayah":125,"verse":"And remember when We made this House (at Mecca) a recourse for mankind and a sanctuary; and take the place where Ibrahim stood, as your place of prayer; and We imposed a duty upon Ibrahim and Ismail (Ishmael), to fully purify My house for those who go around it, and those who stay in it (for worship), and those who bow down and prostrate themselves."},"133":{"id":133,"surah":2,"ayah":126,"verse":"And (remember) when Ibrahim prayed, \u201cMy Lord! Make this city a place of security and bestow upon its people various fruits as providence \u2013 for those among them who believe in Allah and the Last Day (of Resurrection)\u201d; He answered, \u201cAnd whoever disbelieves, I shall provide him also some subsistence and then compel him towards the punishment of fire (hell); and that is a wretched place to return.\u201d"},"134":{"id":134,"surah":2,"ayah":127,"verse":"And (remember) when Ibrahim was raising the foundations of the House, along with Ismail; (saying), \u201cOur Lord! Accept it from us; indeed You only are the All Hearing, the All Knowing.\u201d"},"135":{"id":135,"surah":2,"ayah":128,"verse":"\u201cOur Lord! And make us submissive towards you and from our offspring a nation obedient to You \u2013 and show us the ways of our worship, and incline towards us with Your mercy; indeed You only are the Most Acceptor of Repentance, the Most Merciful.\u201d"},"136":{"id":136,"surah":2,"ayah":129,"verse":"\u201cOur Lord! And send towards them a Noble Messenger, from amongst them, to recite to them Your verses, and to instruct them in Your Book and sound wisdom*, and to fully purify them; indeed You only are the Almighty, the Wise.\u201d (The traditions of the Holy Prophet \u2013 sunnah and hadith \u2013 are called wisdom.)"},"137":{"id":137,"surah":2,"ayah":130,"verse":"And who will renounce the religion of Ibrahim except him who is a fool at heart? We indeed chose him (Ibrahim) in this world; and indeed in the Hereafter he is among those worthy of being closest to Us."},"138":{"id":138,"surah":2,"ayah":131,"verse":"When his Lord said to him, \u201cSubmit\u201d, he said, \u201cI have submitted to the Lord Of The Creation.\u201d"},"139":{"id":139,"surah":2,"ayah":132,"verse":"And Ibrahim willed the same religion upon his sons, and also did Yaqub (Jacob); (saying), \u201cO my sons \u2013 indeed Allah has chosen this religion for you; therefore do not die except as Muslims (those who submit to Him).\u201d"},"140":{"id":140,"surah":2,"ayah":133,"verse":"In fact, some of you yourselves were present when death approached Yaqub and when he said to his sons, \u201cWhat will you worship after me?\u201d They said, \u201cWe shall worship Him Who is your God, and is the God of your fathers, Ibrahim and Ismail and Ishaq (Isaac) \u2013 the One God; and to Him we have submitted ourselves.\u201d"},"141":{"id":141,"surah":2,"ayah":134,"verse":"This was a nation that has passed away; for them is what they earned, and yours is what you earn; and you will not be questioned about their deeds."},"142":{"id":142,"surah":2,"ayah":135,"verse":"And the People given the Book(s) said, \u201cBecome Jews or Christians \u2013 you will attain the right path\u201d; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cNo \u2013 rather we take the religion of Ibrahim, who was far removed from all falsehood; and was not of the polytheists.\u201d"},"143":{"id":143,"surah":2,"ayah":136,"verse":"Say, \u201cWe believe in Allah and what is sent down to us and what was sent down to Ibrahim, and Ismael, and Ishaq, and Yaqub, and to their offspring, and what was bestowed upon Moosa and Eisa (Jesus), and what was bestowed upon other Prophets \u2013 from their Lord; we do not make any distinction, in belief, between any of them; and to Allah we have submitted ourselves.\u201d"},"144":{"id":144,"surah":2,"ayah":137,"verse":"And if they believe in the same way you have believed, they have attained guidance; and if they turn away, they are clearly being stubborn; so Allah will soon suffice you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) against them; and He only is the All Hearing, the All Knowing."},"145":{"id":145,"surah":2,"ayah":138,"verse":"\u201cWe have taken the colour (religion) of Allah; and whose colour (religion) is better than that of Allah? And only Him do we worship.\u201d"},"146":{"id":146,"surah":2,"ayah":139,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhat! You dispute with us concerning Allah, whereas He is our Lord and also yours? Our deeds are with us and with you are your deeds; and only to Him do we sincerely belong.\u201d"},"147":{"id":147,"surah":2,"ayah":140,"verse":"\u201cIn fact you claim that Ibrahim, and Ismail, and Ishaq, and Yaqub, and their offspring were Jews or Christians\u201d; say, \u201cDo you know better, or does Allah?\u201d; and who is more unjust than one who has the testimony from Allah and he hides it? And Allah is not unaware of your deeds."},"148":{"id":148,"surah":2,"ayah":141,"verse":"They were a group that has passed away; for them is what they earned, and for you is what you earn; and you will not be questioned about their deeds."},"149":{"id":149,"surah":2,"ayah":142,"verse":"So now the foolish people will say, \u201cWhat has turned the Muslims away from the qiblah (prayer direction) which they formerly observed?\u201d; proclaim, \u201cTo Allah only belong the East and the West; He guides whomever He wills upon the Straight Path.\u201d"},"150":{"id":150,"surah":2,"ayah":143,"verse":"And so it is that We have made you the best nation* for you are witnesses** against mankind, and the Noble Messenger is your guardian and your witness; and (O dear Prophet Mohammed \u2013 peace and blessings be upon him) We had appointed the qiblah which you formerly observed only to see (test) who follows the Noble Messenger, and who turns away; and it was indeed hard except for those whom Allah guided; and it does not befit Allah\u2019s Majesty to waste your faith! Indeed Allah is Most Compassionate, Most Merciful towards mankind. (* The best Ummah is that of Prophet Mohammed \u2013 peace and blessings be upon him. ** The Holy Prophet is a witness from Allah.)"},"151":{"id":151,"surah":2,"ayah":144,"verse":"We observe you turning your face, several times towards heaven (O dear Prophet Mohammed \u2013 peace and blessings be upon him); so We will definitely make you turn (for prayer) towards a qiblah which pleases you; therefore now turn your face towards the Sacred Mosque (in Mecca); and O Muslims, wherever you may be, turn your faces (for prayer) towards it only; and those who have received the Book surely know that this is the truth from their Lord; and Allah is not unaware of their deeds. (Allah seeks to please the Holy Prophet \u2013 peace and blessings be upon him.)"},"152":{"id":152,"surah":2,"ayah":145,"verse":"And even if you were to bring all the signs to the People given the Book(s), they would not follow your qiblah; nor should you follow their qiblah; nor do they follow each others qiblah; and were you (the followers of this Prophet) to follow their desires after having received knowledge, you would then surely be unjust."},"153":{"id":153,"surah":2,"ayah":146,"verse":"Those to whom We gave the Book(s) recognise the Prophet (Mohammed \u2013 peace and blessings be upon him) as men (or they) recognise their own sons; and undoubtedly a group among them purposely conceals the truth."},"154":{"id":154,"surah":2,"ayah":147,"verse":"This is the Truth from your Lord, therefore (O those who listen) beware \u2013 do not be in doubt."},"155":{"id":155,"surah":2,"ayah":148,"verse":"And each one has a direction towards which he inclines, therefore strive to surpass others in good deeds; Allah will bring you all together, wherever you may be; indeed Allah may do as He wills."},"156":{"id":156,"surah":2,"ayah":149,"verse":"And wherever you come from, turn your face towards the Sacred Mosque; and indeed it is the truth from your Lord; and Allah is not unaware of your deeds."},"157":{"id":157,"surah":2,"ayah":150,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) wherever you come from, turn your face towards the Sacred Mosque; and wherever you may be, O Muslims, turn your faces towards it only, so that people may not have an argument against you \u2013 except those among them who do injustice; therefore do not fear them, and fear Me; and this is in order that I complete My favour upon you and that you may attain guidance. \u2013"},"158":{"id":158,"surah":2,"ayah":151,"verse":"The way We have sent to you a Noble Messenger from among you, who recites to you Our verses and purifies you, and teaches you the Book and sound wisdom*, and teaches you what you did not know. (The traditions \/ sayings of the Holy Prophet \u2013 peace and blessings be upon him)."},"159":{"id":159,"surah":2,"ayah":152,"verse":"Therefore remember Me, I will cause you to be spoken of and acknowledge My rights, and do not be ungrateful."},"160":{"id":160,"surah":2,"ayah":153,"verse":"O People who Believe! Seek help from patience and prayer; indeed Allah is with those who patiently endure."},"161":{"id":161,"surah":2,"ayah":154,"verse":"And do not utter regarding those who are slain in Allah's cause as \u201cdead\u201d; in fact they are alive, but it is you who are unaware."},"162":{"id":162,"surah":2,"ayah":155,"verse":"And We will surely test you with some fear and hunger, and with paucity of wealth and lives and crops; and give glad tidings to those who patiently endure. \u2013"},"163":{"id":163,"surah":2,"ayah":156,"verse":"Those who say when calamity befalls them, \u201cIndeed we belong to Allah and indeed it is to Him we are to return.\u201d"},"164":{"id":164,"surah":2,"ayah":157,"verse":"These are the people upon whom are the blessings from their Lord, and mercy; and it is they who are on guidance."},"165":{"id":165,"surah":2,"ayah":158,"verse":"Undoubtedly Safa and Marwah* are among the symbols of Allah; so there is no sin on him, for whoever performs the Hajj (pilgrimage) of this House (of Allah) or the Umrah (lesser pilgrimage), to go back and forth between them; and whoever does good of his own accord, then (know that) indeed Allah is Most Appreciative (rewards virtue), the All Knowing. (These are 2 hillocks near the Holy Ka\u2019aba)"},"166":{"id":166,"surah":2,"ayah":159,"verse":"Indeed those who hide the clear proofs and the guidance which We sent down, after We made it clear to mankind in the Book \u2013 upon them is the curse of Allah and the curse of those who curse."},"167":{"id":167,"surah":2,"ayah":160,"verse":"Except those who repent and do reform and disclose (the truth) \u2013 so I will accept their repentance; and I only am the Most Acceptor of Repentance, the Most Merciful."},"168":{"id":168,"surah":2,"ayah":161,"verse":"Indeed upon those who disbelieved, and died as disbelievers, is the curse of Allah and of the angels and of men combined."},"169":{"id":169,"surah":2,"ayah":162,"verse":"They will remain in it forever; neither will the punishment be lightened for them, nor will they be given respite."},"170":{"id":170,"surah":2,"ayah":163,"verse":"Your God is One God; there is no God except Him \u2013 the Most Gracious, the Most Merciful."},"171":{"id":171,"surah":2,"ayah":164,"verse":"Indeed in the creation of the heavens and the earth, and in the continuous alternation of night and day, and the ships which sail the seas carrying what is of use to men, and the water which Allah sends down from the sky thereby reviving the dead earth and dispersing all kinds of beasts in it, and the movement of the winds, and the obedient clouds between heaven and earth \u2013 certainly in all these are signs for the intelligent."},"172":{"id":172,"surah":2,"ayah":165,"verse":"And some people create for themselves Gods (objects of worship) other than Allah, with devotion (love) equal to the devotion of Allah; and the believers do not love anybody with love equal to the love of Allah; and what will be their state, when the punishment will be before the eyes of the unjust (disbelievers)? For all power belongs wholly to Allah, and because Allah\u2019s punishment is very severe."},"173":{"id":173,"surah":2,"ayah":166,"verse":"(The day) when the leaders will be disgusted with their followers \u2013 and they shall see their punishment, and all their links will be cut off."},"174":{"id":174,"surah":2,"ayah":167,"verse":"And the followers will say, \u201cIf we were to return (to earth), we would break off from them like they have broken off from us\u201d; this is how Allah will show them their deeds as despair for them; and they will never come out from the fire (hell)."},"175":{"id":175,"surah":2,"ayah":168,"verse":"O mankind! Eat from what is lawful and clean in the earth; and do not follow the footsteps of the devil; undoubtedly he is your open enemy."},"176":{"id":176,"surah":2,"ayah":169,"verse":"For he will only instruct you towards the evil and the shameful, and that you fabricate matters concerning Allah, what you do not know."},"177":{"id":177,"surah":2,"ayah":170,"verse":"And when it is said to them, \u201cFollow what Allah has sent down\u201d, they say, \u201cOn the contrary, we shall follow what we found our forefathers upon\u201d; What! Even if their forefathers had no intelligence, or guidance?!"},"178":{"id":178,"surah":2,"ayah":171,"verse":"And the example of the disbelievers is similar to one who calls upon one that hears nothing except screaming and yelling; deaf, dumb, blind \u2013 so they do not have sense."},"179":{"id":179,"surah":2,"ayah":172,"verse":"O People who Believe! Eat of the good things We have provided you, and be grateful to Allah if it is only Him you worship."},"180":{"id":180,"surah":2,"ayah":173,"verse":"He has forbidden for you only the carrion, and blood, and flesh of swine, and the animal that has been slaughtered while proclaiming the name of anyone other than Allah; so there is no sin on him who is compelled and does not eat out of desire, nor eats more than what is necessary; indeed Allah is Oft Forgiving, Most Merciful."},"181":{"id":181,"surah":2,"ayah":174,"verse":"Those who hide the Book sent down by Allah and exchange it for an abject price \u2013 they only fill their bellies with fire and Allah will not speak to them on the Day of Resurrection nor will He purify them; and for them is a painful punishment."},"182":{"id":182,"surah":2,"ayah":175,"verse":"They are the people who purchased error in exchange of guidance, and torment in exchange of pardon; so how much can they bear the fire?"},"183":{"id":183,"surah":2,"ayah":176,"verse":"This is because Allah has sent down the Book with the truth; and indeed those who caused disagreement in the Book are, surely, disputants in the extreme."},"184":{"id":184,"surah":2,"ayah":177,"verse":"Basic virtue is not just to turn faces to the East and the West, but true righteousness is that one must believe in Allah and the Last Day and the angels and the Book and the Prophets; and out of love for Allah, to give treasured wealth to relatives and to the orphans and the needy and the traveller, and to those who ask, and to set slaves free; and to keep the prayer established and to pay the charity; and those who fulfil their obligations when they make an agreement; and the patient during times of calamity, in hardships and during holy war; it is they who have proved true to their word; it is they who are the pious."},"185":{"id":185,"surah":2,"ayah":178,"verse":"O People who Believe! Retribution is made obligatory for you in the matter of those killed unjustly; a freeman for a freeman, and a slave for a slave, and a female for a female; and for him who is partly forgiven by his brother, seek compensation with courtesy and make payment in proper manner; this is a relief and a mercy upon you, from your Lord; so after this, a painful punishment is for whoever exceeds the limits."},"186":{"id":186,"surah":2,"ayah":179,"verse":"And there is life for you in retribution, O men of understanding, so that you may avoid."},"187":{"id":187,"surah":2,"ayah":180,"verse":"It is ordained for you that when death approaches one of you, and he leaves behind wealth, he must bequeath it to parents and near relatives in accordance with tradition; this is a duty upon the pious."},"188":{"id":188,"surah":2,"ayah":181,"verse":"So whoever changes the will after he has heard it \u2013 its sin is only upon those who change it; indeed Allah is the All Hearing, the All Knowing."},"189":{"id":189,"surah":2,"ayah":182,"verse":"Then if one fears that the will maker (the deceased) has done injustice or sin, and he makes a reconciliation between the parties, there shall be no sin upon him; indeed Allah is Oft Forgiving, Most Merciful."},"190":{"id":190,"surah":2,"ayah":183,"verse":"O People who Believe! Fasting is made compulsory for you, like it was ordained for those before you, so that you may attain piety."},"191":{"id":191,"surah":2,"ayah":184,"verse":"For a certain number of days only; so whoever is sick among you, or on a journey, the same number in other days; and those who do not have the strength for it must give a redemption by feeding a needy person; so whoever increases the good of his own accord, it is better for him; and fasting is better for you, if only you realise."},"192":{"id":192,"surah":2,"ayah":185,"verse":"The month of Ramadan in which was sent down the Qur\u2019an \u2013 the guidance for mankind, the direction and the clear criteria (to judge between right and wrong); so whoever among you witnesses this month, must fast for the (whole) month; and whoever is sick or on a journey, may fast the same number in other days; Allah desires ease for you and does not desire hardship for you \u2013 so that you complete the count (of fasts), and glorify Allah\u2019s greatness for having guided you, and so that you may be grateful."},"193":{"id":193,"surah":2,"ayah":186,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him), when My bondmen question you concerning Me, then surely I am close; I answer the prayer of the supplicant when he calls on Me, so they must obey Me and believe in Me, so that they may attain guidance."},"194":{"id":194,"surah":2,"ayah":187,"verse":"Going to your wives during the nights of the fast is made lawful for you; they are coverings for you and you are coverings for them; Allah knows that you were deceiving yourselves (in this respect), so He accepted your penance and forgave you; so cohabit with them and seek what Allah has destined for you \u2013 and eat and drink until the white thread becomes distinct to you from the black thread at dawn \u2013 then complete the fast till nightfall; and do not touch women while staying in seclusion for worship in the mosques; these are the limits imposed by Allah, so do not go near them; this is how Allah explains His verses to mankind so that they may attain piety."},"195":{"id":195,"surah":2,"ayah":188,"verse":"And do not unjustly devour the property of each other, nor take their cases to judges in order that you may wrongfully devour a portion of other peoples\u2019 property on purpose."},"196":{"id":196,"surah":2,"ayah":189,"verse":"They ask you, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), regarding the crescents; say, \u201cThey are indicators of time for mankind and for Hajj (the pilgrimage)\u201d; and it is not a virtue at all that you enter the houses by demolishing their back portions, but in reality virtue is piety; and enter the houses using their gates \u2013 and keep fearing Allah, hoping that you achieve success."},"197":{"id":197,"surah":2,"ayah":190,"verse":"And fight in Allah's cause against those who fight you and do not exceed the limits; and Allah does not like the transgressors."},"198":{"id":198,"surah":2,"ayah":191,"verse":"And slay the disbelievers wherever you find them, and drive them out from where they drove you out, and the turmoil they cause is worse than slaying; and do not fight them near the Sacred Mosque until they fight you there; so if they fight you, slay them; this is the punishment of the disbelievers."},"199":{"id":199,"surah":2,"ayah":192,"verse":"Then if they desist, then indeed Allah is Oft Forgiving, Most Merciful."},"200":{"id":200,"surah":2,"ayah":193,"verse":"And fight them until no mischief remains, and only Allah is worshipped; then if they desist, do not harm them, except the unjust."},"201":{"id":201,"surah":2,"ayah":194,"verse":"The sacred month for the sacred month, and respect in lieu of respect; harm the one who harms you, to the extent as he did \u2013 and keep fearing Allah, and know well that Allah is with the pious."},"202":{"id":202,"surah":2,"ayah":195,"verse":"And spend your wealth in Allah's cause, and do not fall into ruin with your own hands; and be virtuous; undoubtedly the righteous are the beloved of Allah."},"203":{"id":203,"surah":2,"ayah":196,"verse":"And perform Hajj (greater pilgrimage) and Umrah (lesser pilgrimage) for Allah; and if you are prevented, send sacrifice whatever is available; and do not shave your heads until the sacrifice reaches its destination; so whoever among you is sick or has an ailment in the head, must pay a compensation by fasting or charity or sacrifice; then when you are in peace \u2013 and whoever takes the advantage of combining the Hajj and Umrah, it is compulsory for him to sacrifice whatever is available; and whoever cannot afford it, must fast for three days while on the pilgrimage, and seven when you have returned to your homes; these are ten in all; this decree is for him who is not a resident of Mecca; and keep fearing Allah and know well that Allah\u2019s punishment is severe."},"204":{"id":204,"surah":2,"ayah":197,"verse":"The Hajj is during the well-known months; and for one who intends to perform the Hajj in it \u2013 neither is there to be mention of cohabitation in the presence of women, nor any sin, nor a fight with anyone till the completion of Hajj; and whatever good you do, Allah knows it; and take provision along with you for the best provision is piety; and keep fearing Me, O men of understanding!"},"205":{"id":205,"surah":2,"ayah":198,"verse":"It is no sin for you that you seek the bounty of your Lord; so when you return from Arafat, remember Allah near the Sacred Symbol (Mash\u2019ar al Haram) \u2013 and remember Him in the manner He has guided you; and indeed, before this, you were of the astray."},"206":{"id":206,"surah":2,"ayah":199,"verse":"Then, O people of Quraish, you too must return from the place where the people return from, and ask forgiveness from Allah; indeed Allah is Oft Forgiving, Most Merciful."},"207":{"id":207,"surah":2,"ayah":200,"verse":"So when you have completed your Hajj rites, remember Allah as you used to remember your forefathers, in fact more than that; and among the people are some that say, \u201cOur Lord! Give us in this world\u201d \u2013 and he does not have a portion in the Hereafter."},"208":{"id":208,"surah":2,"ayah":201,"verse":"And among them are some that say, \u201cOur Lord! Give us good in the world and good in the Hereafter, and save us from the punishment of fire!\u201d"},"209":{"id":209,"surah":2,"ayah":202,"verse":"For such is a portion from what they have earned; and Allah is Swift At Taking Account."},"210":{"id":210,"surah":2,"ayah":203,"verse":"And remember Allah in the counted days; so whoever hastens by departing in two days, there is no sin on him; and whoever stays on, there is no sin for him \u2013 for the pious; and keep fearing Allah, and know well that it is to Him you will be raised."},"211":{"id":211,"surah":2,"ayah":204,"verse":"And among men is one whose conversation may please you in the life of this world, and he brings Allah as witness to what is in his heart, whereas he is the biggest quarreller!"},"212":{"id":212,"surah":2,"ayah":205,"verse":"And when he turns away, he creates turmoil in the earth and destroys crops and lives; and Allah is not pleased with turmoil."},"213":{"id":213,"surah":2,"ayah":206,"verse":"And when it is said to him, \u201cFear Allah\u201d, he becomes more resolute in committing sin \u2013 therefore hell is sufficient for such; and that is indeed, a very wretched resting place."},"214":{"id":214,"surah":2,"ayah":207,"verse":"And among men is one who sells himself to seek the pleasure of Allah; and Allah is Most Compassionate towards the bondmen."},"215":{"id":215,"surah":2,"ayah":208,"verse":"O People who Believe! Enter Islam in full \u2013 and do not follow the footsteps of the devil; indeed he is your open enemy."},"216":{"id":216,"surah":2,"ayah":209,"verse":"And if you renege, even after the clear commands have come to you, then know well that Allah is Almighty, Wise."},"217":{"id":217,"surah":2,"ayah":210,"verse":"What are they waiting for, except that Allah\u2019s punishment should come through stretched clouds and the angels descend and the matter be finished? And all matters are directed only towards Allah."},"218":{"id":218,"surah":2,"ayah":211,"verse":"Ask the Descendants of Israel how many clear signs We gave them; and whoever alters Allah\u2019s favour which came to him, then indeed Allah\u2019s punishment is severe."},"219":{"id":219,"surah":2,"ayah":212,"verse":"The life of this world is made to appear beautiful in the sight of the disbelievers, and they make fun of the believers; and the pious will be above them on the Day of Resurrection; and Allah may give to whomever He wills, without account."},"220":{"id":220,"surah":2,"ayah":213,"verse":"Mankind was on one religion; so Allah sent Prophets giving glad tidings and warnings \u2013 and with them sent down the true Book to judge between mankind on their differences; and only those to whom it was given created disputes regarding the Book, after clear commands had come to them, due to hostility of one another; so Allah, by His command, made the truth clear to the believers, concerning their disputes; and Allah may guide whomever He wills to the Straight Path."},"221":{"id":221,"surah":2,"ayah":214,"verse":"Are you under the illusion that you will enter Paradise whereas the suffering, which came to those before you, has not yet come to you? Hardship and adversity befell them and they were shaken, to the extent that the Noble Messenger and the believers along with him said, \u201cWhen will the help of Allah come?\u201d; pay heed! Allah\u2019s help is surely near."},"222":{"id":222,"surah":2,"ayah":215,"verse":"They ask you what they should spend; say, \u201cWhatever you spend for good, is for parents and near relatives and orphans and the needy and the traveller\u201d; and whatever good you do, indeed Allah knows it."},"223":{"id":223,"surah":2,"ayah":216,"verse":"Fighting in Allah's cause is ordained for you, whereas it is disliked by you; and it is possible that you hate a thing which is better for you; and it is possible that you like a thing which is bad for you; and Allah knows, and you do not know."},"224":{"id":224,"surah":2,"ayah":217,"verse":"They ask you the decree regarding fighting in the sacred month; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cFighting in it is a great sin; and to prevent from the way of Allah, and not to believe in Him and to prevent (people) from the Sacred Mosque and to expel its residents \u2013 these are greater sins before Allah\"; and the turmoil they cause is worse than killing; and they will keep fighting you till they turn you away from your religion, if they can; and whoever among you turns renegade and dies as a disbeliever, then their deeds are wasted in this world and in the Hereafter; and they are the people of hell; they will remain in it forever."},"225":{"id":225,"surah":2,"ayah":218,"verse":"Those who believed, and those who migrated for the sake of Allah, and fought in Allah's cause \u2013 they are hopeful of gaining Allah\u2019s mercy; and Allah is Oft Forgiving, Most Merciful."},"226":{"id":226,"surah":2,"ayah":219,"verse":"They ask you the decree regarding wine (intoxicants) and gambling; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIn both is great sin, and some worldly benefit for men \u2013 but their sin is greater than their benefit\u201d; and they ask you what they should spend; say, \u201cThat which remains spare (surplus)\u201d; this is how Allah explains His verses to you, so that you may think."},"227":{"id":227,"surah":2,"ayah":220,"verse":"Before you execute your deeds of this world and of the Hereafter; and they ask you the decree regarding orphans; say, \u201cTo do good towards them is better; and if you combine your expenses with theirs, they are your brothers\u201d; and Allah knows very well the one who spoils from him who improves; and if Allah willed, He could have put you in hardship; and Allah is Almighty, Wise."},"228":{"id":228,"surah":2,"ayah":221,"verse":"And do not marry polytheist women until they become Muslims; for undoubtedly a Muslim bondwoman is better than a polytheist woman, although you may like her; and do not give your women in marriage to polytheist men until they accept faith; for undoubtedly a Muslim slave is better than a polytheist, although you may like him; they invite you towards the fire; and Allah invites towards Paradise and forgiveness by His command; and explains His verses to mankind so that they may accept guidance."},"229":{"id":229,"surah":2,"ayah":222,"verse":"And they ask you the decree concerning menstruation; say, \u201cIt is an impurity, so stay away from women at such times, and do not cohabit with them until they have cleansed themselves; so when they have cleansed themselves, cohabit with them the way Allah has determined for you\u201d; indeed Allah loves those who repent profusely, and loves those who keep clean."},"230":{"id":230,"surah":2,"ayah":223,"verse":"Your women are a tillage for you; so come into your tillage as you will; and first perform the deeds that benefit you; and keep fearing Allah, and know well that you have to meet Him; and (O dear Prophet Mohammed \u2013 peace and blessings be upon him) give glad tidings to the Muslims."},"231":{"id":231,"surah":2,"ayah":224,"verse":"And do not make Allah a target of your oaths, by pledging against being virtuous and pious, and against making peace among mankind; and Allah is All Hearing, All Knowing."},"232":{"id":232,"surah":2,"ayah":225,"verse":"Allah does not take you to task for oaths which are made unintentionally but He does take you to task for deeds which your hearts have done; and Allah is Oft Forgiving, Most Forbearing."},"233":{"id":233,"surah":2,"ayah":226,"verse":"Those who swear not to touch their wives have four months\u2019 time; so if they turn back during this period, indeed Allah is Oft Forgiving, Most Merciful."},"234":{"id":234,"surah":2,"ayah":227,"verse":"And if they firmly decide to divorce them, Allah is All Hearing, All Knowing."},"235":{"id":235,"surah":2,"ayah":228,"verse":"And divorced women shall restrain themselves for three menstrual periods; and it is not lawful for them to conceal what Allah has created in their wombs if they believe in Allah and the Last Day; and their husbands have the right to take them back, during this time, if they desire reconciliation; and the women also have rights similar to those of men over them, in accordance with Islamic law \u2013 and men have superiority over them; and Allah is Almighty, Wise."},"236":{"id":236,"surah":2,"ayah":229,"verse":"This type of divorce is up to twice; the woman must then be retained on good terms or released with kindness; and it is not lawful for you to take back from women a part of what you have given them except when both fear that they may not be able to stay within the limits established by Allah; so if you fear that they may not be able to observe the limits of Allah, then it is no sin on them if the woman pays to get her release; these are the limits set by Allah, so do not exceed them; and those who transgress Allah\u2019s limits are the unjust."},"237":{"id":237,"surah":2,"ayah":230,"verse":"Then if he divorces her the third time, she will not be lawful to him until she has stayed with another husband; then if the other husband divorces her, it is no sin for these two to reunite if they consider that they can keep the limits of Allah established; these are the limits set by Allah which He explains for people of intellect."},"238":{"id":238,"surah":2,"ayah":231,"verse":"And when you have divorced women, and their term reaches its end, either retain them on good terms within this period or release them with kindness; and do not retain them in order to hurt them, hence transgressing the limits; and he who does so harms only himself; and do not make the signs of Allah the objects of ridicule; and remember Allah\u2019s favour that is bestowed upon you and that He has sent down to you the Book and wisdom, for your guidance; keep fearing Allah and know well that Allah knows everything. (The traditions of the Holy Prophet \u2013 sunnah and hadith \u2013 are called wisdom.)"},"239":{"id":239,"surah":2,"ayah":232,"verse":"And when you have divorced women and they complete their waiting period \u2013 then O guardians of such women, do not prevent them from marrying their husbands if they agree between themselves in accordance with Islamic law; this lesson is for those among People who Believe in Allah and the Last Day; this is purer for you, and cleaner; and Allah knows and you do not know."},"240":{"id":240,"surah":2,"ayah":233,"verse":"And mothers shall breast-feed their children for two full years \u2013 for those who wish to complete the term of milk feeding; and the father of the child must provide for food and clothing of the mother in accordance with custom; no one will be burdened except with what he can bear; a mother should not be harmed because of her child, nor he to whom the child is born be harmed because of his child (or a mother should not harm the child nor he to whom the child is born should harm the child); and the same is incumbent on the guardian in place of the father; then if the parents desire to wean the child by mutual consent and consultation, it is no sin for them; and if you wish to give your children out to a (milk feeding) nurse, it is no sin for you, provided you pay to them what is agreed, with kindness; and keep fearing Allah, and know well that Allah is seeing what you do."},"241":{"id":241,"surah":2,"ayah":234,"verse":"And those among you who die leaving wives behind them, then such widows shall restrain themselves for four months and ten days; so when their term is completed, O guardians of such women, there is no sin on you in what the women may decide for themselves in accordance with Islamic law; and Allah is Well Aware of what you do."},"242":{"id":242,"surah":2,"ayah":235,"verse":"And there is no sin on you if you propose marriage to women while they are hidden from your view, or hide it in your hearts; Allah knows that you will now remember them, but do not make secret pacts with women except by decent words recognised by Islamic law; and do not consummate the marriage until the written command reaches its completion; know well that Allah knows what is in your hearts, therefore fear Him; and know well that Allah is Oft Forgiving, Most Forbearing."},"243":{"id":243,"surah":2,"ayah":236,"verse":"There is no sin upon you if you divorce women while you have not touched them or appointed their bridal money; and give them some provision; the rich according to their means, and the poor according to their means; a fair provision according to custom; this is a duty upon the virtuous."},"244":{"id":244,"surah":2,"ayah":237,"verse":"If you divorce them before you have touched them and have appointed the bridal money, then payment of half of what is agreed is ordained unless the women forgo some of it, or he in whose hand is the marriage tie, pays more; and O men, your paying more is closer to piety; and do not forget the favours to each other; indeed Allah is seeing what you do."},"245":{"id":245,"surah":2,"ayah":238,"verse":"Guard all your prayers, and the middle prayer; and stand with reverence before Allah."},"246":{"id":246,"surah":2,"ayah":239,"verse":"And if you are in fear, pray while on foot or while riding, as you can; when you are in peace remember Allah the way He has taught you, which you did not know."},"247":{"id":247,"surah":2,"ayah":240,"verse":"And those among you who die leaving wives behind them \u2013 they should bequeath for their wives a complete provision for one full year without turning them out; so if they go out themselves, there is no sin on you regarding what they do of themselves in a reasonable manner; and Allah is Almighty, Wise."},"248":{"id":248,"surah":2,"ayah":241,"verse":"And for divorced women also, is a complete provision in reasonable manner; this is a duty upon the pious."},"249":{"id":249,"surah":2,"ayah":242,"verse":"This is how Allah explains His verses to you so that you may understand."},"250":{"id":250,"surah":2,"ayah":243,"verse":"Did you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) not see those who left their homes, whereas they numbered in thousands, fearing death? So Allah said to them, \u201cDie\u201d; He then brought them back to life; indeed Allah is Most Munificent towards mankind, but most men are ungrateful."},"251":{"id":251,"surah":2,"ayah":244,"verse":"And fight in Allah's cause and know well that Allah is All Hearing, All Knowing."},"252":{"id":252,"surah":2,"ayah":245,"verse":"Is there someone who will lend an excellent loan to Allah, so that He may increase it for him several times over? And Allah restricts and eases (the sustenance) \u2013 and it is to Him that you will return."},"253":{"id":253,"surah":2,"ayah":246,"verse":"Did you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) not see a group of the Descendants of Israel, after Moosa? When they said to one of their Prophets (Shamueel \u2013 Samuel), \u201cAppoint a king for us so that we may fight in Allah\u2019s way\u201d? He said, \u201cDo you think you would refrain from fighting if it is made obligatory for you?\u201d They said, \u201cWhat is the matter with us that we should not fight in Allah\u2019s cause, whereas we have been driven away from our homeland and our children?\u201d So when fighting was ordained for them, they all turned away, except a few; and Allah is Well Aware of the unjust."},"254":{"id":254,"surah":2,"ayah":247,"verse":"And their Prophet said to them, \u201cIndeed Allah has sent Talut (Saul) as your king\u201d; they said, \u201cWhy should he have kingship over us whereas we deserve the kingship more than he, and nor has he been given enough wealth?\u201d He said, \u201cIndeed Allah has chosen him above you, and has bestowed him with vast knowledge and physique\u201d; and Allah may bestow His kingdom on whomever He wills; and Allah is Most Capable, All Knowing."},"255":{"id":255,"surah":2,"ayah":248,"verse":"And their Prophet said to them, \u201cIndeed the sign of his kingdom will be the coming of a (wooden) box to you, in which from your Lord is the contentment of hearts and containing some souvenirs (remnants) left behind by the honourable Moosa and the honourable Haroon (Aaron), borne by the angels; indeed in it is a great sign* for you if you are believers.\u201d (The remnants of pious persons are blessed by Allah.)"},"256":{"id":256,"surah":2,"ayah":249,"verse":"So when Talut left the city along with the armies, he said, \u201cAllah will surely test you with a river; so whoever drinks its water is not mine \u2013 and whoever does not drink is mine \u2013 except him who takes it in the hollow of his hand\u201d; so they all drank it, except a few of them; thereafter when Talut and the believers with him had crossed the river, they said, \u201cWe do not have power this day to face Jalut (Goliath) and his armies\u201d; those who were certain of meeting Allah said, \u201cMany a times has a smaller group overcome a bigger group by Allah\u2019s command; and Allah is with the steadfast.\u201d"},"257":{"id":257,"surah":2,"ayah":250,"verse":"And when they confronted Jalut and his armies they invoked, \u201cOur Lord! Pour (bestow abundantly) on us patience (fortitude), and keep our feet steady, and help us against the disbelieving people.\u201d"},"258":{"id":258,"surah":2,"ayah":251,"verse":"So they routed them by the command of Allah; and Dawud (David) slew Jalut, and Allah gave him the kingdom and wisdom, and taught him all whatever He willed; and if Allah does not ward off some men by others, the earth will be destroyed, but Allah is Most Munificent towards the entire creation."},"259":{"id":259,"surah":2,"ayah":252,"verse":"These are the verses of Allah, which We recite to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) with truth; and undoubtedly you are one of the Noble Messengers."},"260":{"id":260,"surah":2,"ayah":253,"verse":"These are the Noble Messengers, to whom We gave excellence over each other; of them are some with whom Allah spoke, and some whom He exalted high above all others; and We gave Eisa (Jesus), the son of Maryam, clear signs and We aided him with the Holy Spirit; and if Allah willed, those after them would not have fought each other after the clear evidences had come to them, but they differed \u2013 some remained on faith and some turned disbelievers; and had Allah willed, they would not have fought each other; but Allah may do as He wills."},"261":{"id":261,"surah":2,"ayah":254,"verse":"O People who Believe! Spend in Allah's cause, from what We have provided you, before the advent of a day in which there is no trade, and for the disbelievers neither any friendship nor intercession; and the disbelievers themselves are the unjust."},"262":{"id":262,"surah":2,"ayah":255,"verse":"Allah \u2013 there is no God except Him; He is Alive (eternally, on His own) and the Upholder (keeps others established); He never feels drowsy nor does He sleep; to Him only belongs all whatever is in the heavens and all whatever is in the earth; who is he that can intercede* with Him except by His command? He knows what is in front of them and what is behind them; and they do not achieve anything of His knowledge except what He wills; His Throne (of Sovereignty) encompasses the heavens and the earth; and it is not difficult for Him to guard them; and He is the Supreme, the Greatest. (This Verse is popularly known as Ayat Al-Kursi. It has a special status and reciting it carries great reward. *Prophet Mohammed \u2013 peace and blessings be upon him \u2013 will be the first one to be granted the permission to intercede, others will follow.)"},"263":{"id":263,"surah":2,"ayah":256,"verse":"There is no compulsion at all in religion; undoubtedly the right path has become very distinct from error; and whoever rejects faith in the devil (false deities) and believes in Allah has grasped a very firm handhold; it will never loosen; and Allah is All Hearing, All Knowing."},"264":{"id":264,"surah":2,"ayah":257,"verse":"Allah is the Guardian of the Muslims \u2013 He removes them from realms of darkness towards light; and the supporters of disbelievers are the devils \u2013 they remove them from light towards the realms of darkness; it is they who are the people of fire; they will remain in it forever."},"265":{"id":265,"surah":2,"ayah":258,"verse":"Did you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) not see him who argued with Ibrahim (Abraham) concerning his Lord, as Allah had given him the kingdom? When Ibrahim said, \u201cMy Lord is He Who gives life and causes death\u201d, he answered, \u201cI give life and cause death\u201d; Ibrahim said, \u201cSo indeed it is Allah Who brings the sun from the East \u2013 you bring it from the West!\u201d \u2013 the disbeliever was therefore baffled; and Allah does not guide the unjust."},"266":{"id":266,"surah":2,"ayah":259,"verse":"Or like him* who passed by a dwelling and it had fallen flat on its roofs; he said, \u201cHow will Allah bring it to life, after its death?\u201d; so Allah kept him dead for a hundred years, then brought him back to life; He said, \u201cHow long have you stayed here?\u201d; he replied, \u201cI may have stayed for a day or little less\u201d; He said, \u201cIn fact, you have spent a hundred years \u2013 so look at your food and drink which do not even smell stale; and look at your donkey whose bones even are not intact \u2013 in order that We may make you a sign for mankind \u2013 and look at the bones how We assemble them and then cover them with flesh\u201d; so when the matter became clear to him, he said, \u201cI know well that Allah is Able to do all things.\u201d (Prophet Uzair \u2013 peace be upon him.)"},"267":{"id":267,"surah":2,"ayah":260,"verse":"And when Ibrahim said, \u201cMy Lord! Show me how You will give life to the dead\u201d; He said, \u201cAre you not certain (of it)?\u201d Ibrahim said, \u201cSurely yes, why not? But because I wish to put my heart at ease\u201d; He said, \u201cTherefore take four birds (as pets) and cause them to become familiar to you, then place a part of each of them on separate hills, then call them \u2013 they will come running towards you; and know well that Allah is Almighty, Wise.\u201d (Prophet Ibrahim called the dead birds and they did come running towards him.)"},"268":{"id":268,"surah":2,"ayah":261,"verse":"The example of those who spend their wealth in Allah\u2019s way is similar to that of a grain which has sprouted seven stalks and in each stalk are a hundred grains; and Allah may increase it still more than this, for whomever He wills; and Allah is Most Capable, All Knowing."},"269":{"id":269,"surah":2,"ayah":262,"verse":"Those who spend their wealth in Allah\u2019s way and thereafter do not express favour nor cause injury (hurt the recipient\u2019s feelings), their reward is with their Lord; there shall be no fear upon them nor shall they grieve."},"270":{"id":270,"surah":2,"ayah":263,"verse":"Speaking kind words and pardoning are better than charity followed by injury; and Allah is the Independent, Most Forbearing."},"271":{"id":271,"surah":2,"ayah":264,"verse":"O People who Believe! Do not invalidate your charity by expressing favour and causing injury \u2013 like one who spends his wealth for people to see, and does not believe in Allah and the Last Day; his example is similar to that of a rock covered with dust and hard rain fell on it, leaving it as a bare rock; they shall get no control over (or benefit from) anything they have earned; and Allah does not guide the disbelievers."},"272":{"id":272,"surah":2,"ayah":265,"verse":"And the example of those who spend their wealth in order to seek Allah\u2019s pleasure and to make their hearts steadfast, is similar to that of a garden on a height \u2013 hard rain fell on it, so bringing forth its fruit twofold; so if hard rain does not reach it, the dew is enough; and Allah is seeing your deeds."},"273":{"id":273,"surah":2,"ayah":266,"verse":"Would any of you like that he may own a garden of dates and grapes, with rivers flowing beneath it \u2013 in it are all kinds of fruits for him \u2013 and he reaches old age and has young children; therefore a windstorm containing fire came to the garden, burning it? This is how Allah explains His verses to you, so that you may give thought."},"274":{"id":274,"surah":2,"ayah":267,"verse":"O People who Believe! Spend a part of your lawful earnings, and part of what We have produced from the earth for you \u2013 and do not (purposely) choose upon the flawed to give from it (in charity) whereas you would not accept it yourselves except with your eyes closed towards it; and know well that Allah is Independent, Most Praised."},"275":{"id":275,"surah":2,"ayah":268,"verse":"The devil scares you of poverty and bids you to the shameful; and Allah promises you forgiveness from Him, and munificence; and Allah is Most Capable, All Knowing."},"276":{"id":276,"surah":2,"ayah":269,"verse":"Allah bestows wisdom on whomever He wills; and whoever receives wisdom has received abundant goodness; but none heed advice except men of understanding."},"277":{"id":277,"surah":2,"ayah":270,"verse":"And whatever you spend or pledge to do, Allah is aware of it; and the unjust do not have supporters."},"278":{"id":278,"surah":2,"ayah":271,"verse":"If you give charity openly, what an excellent deed it is! And if you secretly give it to the poor, it is the best for you; and it will redeem some of your sins; and Allah is Aware of your deeds."},"279":{"id":279,"surah":2,"ayah":272,"verse":"It is not your duty (O dear Prophet Mohammed \u2013 peace and blessings be upon him) to make them accept guidance \u2013 but Allah guides whomever He wills; and whatever good thing you spend is beneficial for yourselves; and it is not right for you to spend except to seek Allah\u2019s pleasure; and whatever you spend will be repaid to you in full, and you will not be wronged. \u2013"},"280":{"id":280,"surah":2,"ayah":273,"verse":"(Spend) For the poor who are restricted while in Allah's cause, who cannot travel in the land (for earning) \u2013 the unwise think they are wealthy because of their restraint; you will recognise them by their faces; they do not seek from people in order to avoid grovelling; and Allah knows whatever you spend in charity."},"281":{"id":281,"surah":2,"ayah":274,"verse":"Those who spend their wealth by night and day, secretly and openly \u2013 their reward is with their Lord; and there shall be no fear upon them nor shall they grieve."},"282":{"id":282,"surah":2,"ayah":275,"verse":"Those who devour usury will not stand up on the Day of Judgement, except like the one whom an evil jinn has deranged by his touch; that is because they said, \u201cTrade is also like usury!\u201d; whereas Allah has made trading lawful and forbidden usury; for one to whom the guidance has come from his Lord, and he refrained therefrom, is lawful what he has taken in the past; and his affair is with Allah; and whoever continues earning it henceforth, is of the people of fire; they will remain in it for ages."},"283":{"id":283,"surah":2,"ayah":276,"verse":"Allah destroys usury and increases charity; and Allah does not like any ungrateful, excessive sinner."},"284":{"id":284,"surah":2,"ayah":277,"verse":"Indeed those who believed and did good deeds and kept the prayer established and paid the charity \u2013 their reward is with their Lord; and there shall be no fear upon them nor shall they grieve."},"285":{"id":285,"surah":2,"ayah":278,"verse":"O People who Believe! Fear Allah and forego the remaining usury, if you are Muslims."},"286":{"id":286,"surah":2,"ayah":279,"verse":"And if you do not, then be certain of a war with Allah and His Noble Messenger; and if you repent, take back your principal amount; neither you cause harm to someone, nor you be harmed."},"287":{"id":287,"surah":2,"ayah":280,"verse":"And if the debtor is in difficulty, give him respite till the time of ease; and your foregoing the entire debt from him is still better for you, if only you realise."},"288":{"id":288,"surah":2,"ayah":281,"verse":"And fear the day in which you will be returned to Allah; and every soul will be paid back in full what it had earned, and they will not be wronged."},"289":{"id":289,"surah":2,"ayah":282,"verse":"O People who Believe! If you make an agreement for debt for a specified time, write it down; and appoint a scribe to write it for you with accuracy; and the scribe must not refuse to write in the manner Allah has taught him, so he must write; and the liable person (debtor) should dictate it to him and fear Allah, Who is his Lord, and not hide anything of the truth; but if the debtor is of poor reasoning, or weak, or unable to dictate, then his guardian must dictate with justice; and appoint two witnesses from your men; then if two men are not available, one man and two women from those you would prefer to be witnesses, so that if one of them forgets, the other can remind her; and the witnesses must not refuse when called upon to testify; do not feel burdened to write it, whether the transaction is small or big \u2013 write it for up to its term\u2019s end; this is closer to justice before Allah and will be a strong evidence and more convenient to dispel doubts amongst yourselves \u2013 except when it is an instant trade in which exchange is carried out immediately, there is no sin on you if it is not written down; and take witnesses whenever you perform trade; and neither the scribe nor the witnesses be caused any harm (or they cause any harm); and if you do, it would be an offence on your part; and fear Allah; and Allah teaches you; and Allah knows everything."},"290":{"id":290,"surah":2,"ayah":283,"verse":"And if you are on a journey and cannot find a scribe, then a mortgage (deposit) must be handed over; and if one of you trusts the other, the one who is trusted may return the mortgage entrusted to him and fear Allah, his Lord; and do not hide testimony; and if one hides it, his heart is sinful from within; and Allah knows what you do."},"291":{"id":291,"surah":2,"ayah":284,"verse":"To Allah only belongs all whatever is in the heavens and all whatever is in the earth; and whether you disclose what is in your hearts or hide it, Allah will take account of it from you; so He will forgive whomever He wills and punish whomever He wills; and Allah is Able to do all things."},"292":{"id":292,"surah":2,"ayah":285,"verse":"The Noble Messenger believes in what has been sent down to him by his Lord, and so do the believers; all have accepted faith in Allah and His angels and His Books and His Noble Messengers; saying, \u201cWe do not make any distinction, in believing, between any of His Noble Messengers\u201d; and they said, \u201cWe hear, and we obey; Your forgiveness be granted, O our Lord, and towards You is our return.\u201d"},"293":{"id":293,"surah":2,"ayah":286,"verse":"Allah does not burden anyone, except with something within its capacity; beneficial for it is the virtue it earned, and harmful for it is the evil it earned; \u201cOur Lord! Do not seize us if we forget or are mistaken; our Lord! And do not place on us a heavy burden (responsibility) as You did on those before us; our Lord! And do not impose on us a burden, for which we do not have the strength; and pardon us \u2013 and forgive us \u2013 and have mercy on us \u2013 You are our Master, therefore help us against the disbelievers.\u201d"},"294":{"id":294,"surah":3,"ayah":1,"verse":"Alif-Laam-Meem. (Alphabets of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"295":{"id":295,"surah":3,"ayah":2,"verse":"Allah \u2013 none is worthy of worship, except Him, He is Alive (eternally, on His own) and the Upholder (keeps others established)."},"296":{"id":296,"surah":3,"ayah":3,"verse":"He has sent down to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) this true Book (the Holy Qur\u2019an), confirming the Books before it, and He sent down the Taurat (Torah) and the Injeel (Bible). \u2013"},"297":{"id":297,"surah":3,"ayah":4,"verse":"Before this, a guidance to mankind; and sent down the Judgement (Criterion to judge between right and wrong); indeed for those who disbelieved in the verses of Allah, is a severe punishment; and Allah is the Almighty, the Avenger (of the wrong)."},"298":{"id":298,"surah":3,"ayah":5,"verse":"Indeed nothing is hidden from Allah, neither in the earth nor in the heavens."},"299":{"id":299,"surah":3,"ayah":6,"verse":"It is He Who fashions (moulds) you in your mothers\u2019 wombs as He wills; none is worthy of worship except Him, the Almighty (the Most Honourable), the Wise."},"300":{"id":300,"surah":3,"ayah":7,"verse":"It is He Who has sent down to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) this Book (the Qur\u2019an) containing the verses that have a clear meaning \u2013 they are the core of the Book \u2013 and other verses the meanings of which are indistinct; those in whose hearts is deviation pursue the verses having indistinct meanings, in order to cause turmoil and seeking its (wrongful) interpretation; and only Allah knows its proper interpretation; and those having sound knowledge say, \u201cWe believe in it, all of it is from our Lord\u201d; and none accept guidance except the men of understanding."},"301":{"id":301,"surah":3,"ayah":8,"verse":"\u201cOur Lord! Do not deviate our hearts after You have guided us, and bestow mercy on us from Yourself; indeed You only are the Great Bestower.\u201d"},"302":{"id":302,"surah":3,"ayah":9,"verse":"\u201cOur Lord! Indeed You will gather all mankind for a Day about which there is no doubt\"; indeed Allah\u2019s promise does not change."},"303":{"id":303,"surah":3,"ayah":10,"verse":"Indeed for those who disbelieve, neither their wealth nor their offspring will help to save them in the least from Allah; and it is they who are fuel for the fire."},"304":{"id":304,"surah":3,"ayah":11,"verse":"Like the way of Firaun\u2019s people, and those before them; they denied Our signs; so Allah seized them because of their sins; and Allah\u2019s punishment is severe."},"305":{"id":305,"surah":3,"ayah":12,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him) to the disbelievers, \u201cVery soon you shall be overcome and driven towards hell; and that is a wretched resting-place.\u201d"},"306":{"id":306,"surah":3,"ayah":13,"verse":"Indeed there was a sign for you in the two groups that clashed; one army fighting in Allah's cause, against the other of disbelievers, whom they (the Muslims) saw with their eyes, as twice their own number; and Allah strengthens with His help whomever He wills; indeed in this is a lesson for the intelligent, to be learnt by observing."},"307":{"id":307,"surah":3,"ayah":14,"verse":"Beautified is for mankind the love of these desires \u2013 women, and sons, and heaps of gold and piled up silver, and branded horses, and cattle and fields; this is the wealth of the life of this world; and it is Allah, with Whom is the excellent abode."},"308":{"id":308,"surah":3,"ayah":15,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cShall I inform you of something better than that? For the pious, with their Lord, are Gardens beneath which rivers flow \u2013 they will abide in it forever \u2013 and pure wives, and Allah\u2019s pleasure\u201d; and Allah sees the bondmen."},"309":{"id":309,"surah":3,"ayah":16,"verse":"Those who say, \u201cOur Lord! we have accepted faith, so forgive us our sins and save us from the punishment of fire.\u201d"},"310":{"id":310,"surah":3,"ayah":17,"verse":"The steadfast, and the truthful, and the reverent, and who spend in Allah's cause, and who seek forgiveness in the last hours of the night (before dawn)."},"311":{"id":311,"surah":3,"ayah":18,"verse":"Allah has given witness that there is none worthy of worship (God) except Him \u2013 and the angels and the scholars also give witness, established with justice (with truth) \u2013 there is no God except Him, the Almighty, the Wise."},"312":{"id":312,"surah":3,"ayah":19,"verse":"Indeed the only true religion in the sight of Allah is Islam; those who had received the Books differed only after the knowledge came to them, due to their hearts\u2019 envy; and whoever disbelieves in the signs of Allah, then Allah is Swift At Taking Account."},"313":{"id":313,"surah":3,"ayah":20,"verse":"Then if they argue with you, (O dear Prophet Mohammed \u2013 peace and blessings be upon him) say, \u201cI have submitted my face (self) to Allah and likewise have my followers\u201d; and say to the People given the Book(s) and the illiterate, \u201cHave you submitted (accepted Islam)?\u201d If they submit, they have attained the right path \u2013 and if they turn away (reject), then your duty is only to convey this command; and Allah is seeing the bondmen."},"314":{"id":314,"surah":3,"ayah":21,"verse":"Those who disbelieve in the signs of Allah, and wrongfully martyr the Prophets, and slay people who enjoin justice \u2013 so give them the glad tidings of a painful punishment."},"315":{"id":315,"surah":3,"ayah":22,"verse":"They are those whose deeds are wasted in this world and in the Hereafter; and they do not have any aides."},"316":{"id":316,"surah":3,"ayah":23,"verse":"Did you not see them who have received a part of the Book \u2013 when called towards the Book of Allah for judging between them, a group of them opposes it and turns away?"},"317":{"id":317,"surah":3,"ayah":24,"verse":"They dared to do this because they say, \u201cThe fire will definitely not touch us except for a certain number of days\u201d; and they are deceived in their religion by the lies they fabricated."},"318":{"id":318,"surah":3,"ayah":25,"verse":"So what will be (their state) when We bring all of them together for the Day (of Resurrection) about which there is no doubt; and every soul will be paid back in full for what it has earned, and they will not be wronged."},"319":{"id":319,"surah":3,"ayah":26,"verse":"Invoke (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO Allah! Owner Of The Kingdom \u2013 You bestow the kingdom on whomever You will, and You take back the kingdom from whomever You will; and You give honour to whomever You will, and You humiliate whomever You will; only in Your Hand (control) lies all goodness; indeed, You are Able to do all things.\u201d"},"320":{"id":320,"surah":3,"ayah":27,"verse":"\u201cYou cause part of the night to pass into the day, and You cause part of the day to pass into the night; and You bring forth the living from the dead, and You bring forth the dead from the living; and You give to whomever You will, without account.\u201d"},"321":{"id":321,"surah":3,"ayah":28,"verse":"The Muslims must not befriend the disbelievers, in preference over the Muslims; whoever does that has no connection whatsoever with Allah, except if you fear them; Allah warns you of His wrath; and towards Allah only is the return."},"322":{"id":322,"surah":3,"ayah":29,"verse":"Say, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhether you hide or reveal whatever is in your hearts, Allah knows it all; and He knows all whatever is in the heavens and all whatever is in the earth; and Allah has control over all things.\u201d"},"323":{"id":323,"surah":3,"ayah":30,"verse":"On the Day when every soul will be confronted with all the good that it has done; and all the evil that it has done \u2013 it will wish that perhaps there would have been a great distance between itself and the punishment; and Allah warns you of His punishment; and Allah is Most Compassionate towards His bondmen."},"324":{"id":324,"surah":3,"ayah":31,"verse":"Proclaim, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO mankind! If you love Allah, follow me \u2013 Allah will love you and forgive you your sins\u201d; and Allah is Oft Forgiving, Most Merciful."},"325":{"id":325,"surah":3,"ayah":32,"verse":"Proclaim, \u201cObey Allah and the Noble Messenger\u201d; so if they turn away \u2013 then Allah is not pleased with the disbelievers."},"326":{"id":326,"surah":3,"ayah":33,"verse":"Indeed Allah chose Adam, and Nooh, and the Family of Ibrahim, and the Family of Imran over the creation."},"327":{"id":327,"surah":3,"ayah":34,"verse":"They are the descendants one of another; and Allah is the All Hearing, the All Knowing."},"328":{"id":328,"surah":3,"ayah":35,"verse":"(Remember) When the wife of Imran said, \u201cMy Lord! I pledge to you what is in my womb \u2013 that it shall be dedicated purely in Your service, so accept it from me; indeed You only are the All Hearing, the All Knowing.\u201d"},"329":{"id":329,"surah":3,"ayah":36,"verse":"So when she gave birth to it, she said, \u201cMy Lord! I have indeed given birth to a girl!\u201d And Allah well knows what she gave birth to; and the boy she had prayed for is not like this girl; \u201cAnd I have named her Maryam and I give her and her offspring in Your protection, against Satan the outcast.\u201d"},"330":{"id":330,"surah":3,"ayah":37,"verse":"So her Lord fully accepted her (Maryam), and gave her an excellent development; and gave her in Zakaria\u2019s guardianship; whenever Zakaria visited her at her place of prayer, he found new food with her; he said, \u201cO Maryam! From where did this come to you?\u201d She answered, \u201cIt is from Allah; indeed Allah gives to whomever He wills, without limit account.\u201d (Miracles occur through the friends of Allah.)"},"331":{"id":331,"surah":3,"ayah":38,"verse":"It is here that Zakaria prayed to his Lord; he said, \u201cMy Lord! Give me from Yourself a righteous child; indeed You only are the Listener Of Prayer.\u201d"},"332":{"id":332,"surah":3,"ayah":39,"verse":"And the angels called out to him while he was standing, offering prayer at his place of worship, \u201cIndeed Allah gives you glad tidings of Yahya (John), who will confirm a Word (or sign) from Allah, \u2013 a leader, always refraining from women, a Prophet from one of Our devoted ones.\u201d"},"333":{"id":333,"surah":3,"ayah":40,"verse":"He said, \u201cMy Lord! How can I have a son when old age has reached me and my wife is barren?\u201d He said, \u201cThis is how Allah brings about, whatever He wills.\u201d"},"334":{"id":334,"surah":3,"ayah":41,"verse":"He said, \u201cMy Lord! Determine a sign for me\u201d; He said, \u201cThe sign is that you shall not be able to speak to mankind for three days except by signs; and remember your Lord profusely, and proclaim His Purity before sunset and at dawn.\u201d"},"335":{"id":335,"surah":3,"ayah":42,"verse":"And when the angels said, \u201cO Maryam! Indeed Allah has chosen you and purified you, and has this day, chosen you among all the women of the world.\u201d"},"336":{"id":336,"surah":3,"ayah":43,"verse":"\u201cO Maryam! Stand in reverence before your Lord, prostrate yourself and bow along with those who bow.\u201d"},"337":{"id":337,"surah":3,"ayah":44,"verse":"These are tidings of the hidden, which We secretly reveal to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him); and you were not present with them when they threw their pens to draw lots, to know who should be the guardian of Maryam; nor were you present with them when they were quarrelling. (Yet you know of these things \u2013 this is a proof of your being a Prophet.)"},"338":{"id":338,"surah":3,"ayah":45,"verse":"And remember when the angels said, \u201cO Maryam! Allah gives you glad tidings of a Word from Him, whose name is the Messiah, Eisa the son of Maryam \u2013 he will be honourable in this world and in the Hereafter, and among the close ones (to Allah).\u201d"},"339":{"id":339,"surah":3,"ayah":46,"verse":"\u201cHe will speak to people while he is in the cradle and in his adulthood, and will be of the devoted ones.\u201d"},"340":{"id":340,"surah":3,"ayah":47,"verse":"She said, \u201cMy Lord! How can I bear a child when no man has ever touched me?\u201d He said, \u201cThis is how Allah creates whatever He wills; when He wills a thing, He only says to it, \u2018Be\u2019 \u2013 and it happens immediately.\u201d"},"341":{"id":341,"surah":3,"ayah":48,"verse":"\u201cAnd Allah will teach him the Book and wisdom, and the Taurat (Torah) and the Injeel (Bible).\u201d"},"342":{"id":342,"surah":3,"ayah":49,"verse":"\u201cAnd he will be a Noble Messenger towards the Descendants of Israel saying, \u2018I have come to you with a sign from your Lord, for I mould a birdlike sculpture from clay for you, and I blow into it and it instantly becomes a (living) bird, by Allah\u2019s command; and I heal him who was born blind, and the leper, and I revive the dead, by Allah\u2019s command; and I tell you what you eat and what you store in your houses; undoubtedly in these (miracles) is a great sign for you, if you are believers.\u2019 (Several miracles bestowed to Prophet Eisa are mentioned here.)"},"343":{"id":343,"surah":3,"ayah":50,"verse":"\u2018And I come confirming the Taurat (Torah) \u2013 the Book before me \u2013 and to make lawful for you some of the things which were forbidden to you, and I have come to you with a sign from your Lord \u2013 therefore fear Allah and obey me.\u2019"},"344":{"id":344,"surah":3,"ayah":51,"verse":"\u2018Undoubtedly Allah is the Lord of all \u2013 mine and yours \u2013 so worship Him only; this is the Straight Path.\u2019\u201d"},"345":{"id":345,"surah":3,"ayah":52,"verse":"So when Eisa sensed their disbelief he said, \u201cWho will be my aides towards (in the cause of) Allah?\u201d The disciples said, \u201cWe are the aides of Allah\u2019s religion; we believe in Allah, and you bear witness that we are Muslims.\u201d"},"346":{"id":346,"surah":3,"ayah":53,"verse":"\u201cOur Lord! We have believed in what You have sent down and we follow the Noble Messenger, therefore record us among the witnesses of the truth.\u201d"},"347":{"id":347,"surah":3,"ayah":54,"verse":"And the disbelievers conspired (to kill Eisa), and Allah covertly planned to destroy them; and Allah is the best of secret planners."},"348":{"id":348,"surah":3,"ayah":55,"verse":"Remember when Allah said, \u201cO Eisa! I will keep you alive till your full age, and raise you towards Me, and cleanse you of the disbelievers and give your followers dominance over the disbelievers until the Day of Resurrection; then you will all return to Me, so I shall judge between you concerning the matter in which you dispute.\u201d"},"349":{"id":349,"surah":3,"ayah":56,"verse":"\u201cSo I shall mete a severe punishment to those who disbelieve, in this world and in the Hereafter; and they will not have any supporters.\u201d"},"350":{"id":350,"surah":3,"ayah":57,"verse":"\u201cAnd for those who believed and did good deeds, Allah will give them their full reward; and Allah does not like the unjust.\u201d"},"351":{"id":351,"surah":3,"ayah":58,"verse":"These are some verses that We recite to you, and advice full of wisdom."},"352":{"id":352,"surah":3,"ayah":59,"verse":"The example of Eisa with Allah is like that of Adam; He created him (Adam) from clay and then said to him, \u201cBe\u201d \u2013 and it thereupon happens!"},"353":{"id":353,"surah":3,"ayah":60,"verse":"O listener! (followers of this Prophet) This is the Truth from your Lord, so never be of those who doubt."},"354":{"id":354,"surah":3,"ayah":61,"verse":"Therefore say to those who dispute with you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) concerning Eisa after the knowledge has come to you, \u201cCome! Let us summon our sons and your sons, and our women and your women, and ourselves and yourselves \u2013 then pray humbly, thereby casting the curse of Allah upon the liars!\u201d (The Christians did not accept this challenge.)"},"355":{"id":355,"surah":3,"ayah":62,"verse":"This undoubtedly is the true narrative; there is none worthy of worship except Allah; and Allah is the Almighty, the Wise."},"356":{"id":356,"surah":3,"ayah":63,"verse":"So if they turn away, then indeed Allah knows those who cause turmoil."},"357":{"id":357,"surah":3,"ayah":64,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO People given the Book(s)! Come towards a word which is common between us and you, that we shall worship no one except Allah, and that we shall not ascribe any partner to Him, and that none of us shall take one another as lords besides Allah\u201d; then if they do not accept say, \u201cBe witness that (only) we are Muslims.\u201d"},"358":{"id":358,"surah":3,"ayah":65,"verse":"O People given the Book(s)! Why do you argue about Ibrahim, whereas the Taurat (Torah) and the Injeel (Bible) were not sent down until after him? So do you not have sense?"},"359":{"id":359,"surah":3,"ayah":66,"verse":"Listen! This was what you argued about \u2013 of which you have some knowledge \u2013 why do you then argue about the matter you do not have any knowledge of? And Allah knows whereas you do not know."},"360":{"id":360,"surah":3,"ayah":67,"verse":"Ibrahim was neither a Jew nor a Christian; but he was a Muslim, free from all falsehood; and was not of the polytheists."},"361":{"id":361,"surah":3,"ayah":68,"verse":"Undoubtedly among all mankind who have the best claim to Ibrahim are those who followed him, and this Prophet (Mohammed \u2013 peace and blessings be upon him) and the believers; and Allah is the Guardian of the believers."},"362":{"id":362,"surah":3,"ayah":69,"verse":"A group among the People given the Book(s) desire that if only they could lead you astray; and they only make themselves astray, and they do not have sense."},"363":{"id":363,"surah":3,"ayah":70,"verse":"O People given the Book(s)! Why do you disbelieve in the signs of Allah, whereas you yourselves are witnesses?"},"364":{"id":364,"surah":3,"ayah":71,"verse":"O People given the Book(s)! Why do you mix the truth with falsehood and conceal the truth, whereas you know?"},"365":{"id":365,"surah":3,"ayah":72,"verse":"And a group among the People given the Book(s) said, \u201cBelieve in what has been sent down to the believers in the morning and deny it by evening \u2013 perhaps they (the Muslims) may turn back (disbelieve).\u201d"},"366":{"id":366,"surah":3,"ayah":73,"verse":"\u201cAnd do not believe in anyone except him who follows your religion\u201d; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cOnly Allah\u2019s guidance is the true guidance\u201d \u2013 (so why not believe in it) if someone has been given similar to what was given to you, or if someone may be able to evidence it against you before your Lord; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cUndoubtedly the munificence lies only in Allah\u2019s Hand (control); He may bestow upon whomever He wills; and Allah is Most Capable, All Knowing.\u201d"},"367":{"id":367,"surah":3,"ayah":74,"verse":"\u201cHe chooses by His mercy, whomever He wills; and Allah is the Owner of Great Munificence.\u201d"},"368":{"id":368,"surah":3,"ayah":75,"verse":"Among the People given the Book(s) is one who, if you trust him with a heap of treasure, will return it to you; and among them is one who, if you trust him with (just) one coin, will not return it to you unless you constantly stand over him (keep demanding); that is because they say, \u201cWe are not obliged in any way, in the case of illiterates\u201d; and they purposely fabricate lies against Allah."},"369":{"id":369,"surah":3,"ayah":76,"verse":"Yes, why not? Whoever fulfilled his pledge and practised piety \u2013 and indeed Allah loves the pious."},"370":{"id":370,"surah":3,"ayah":77,"verse":"Those who accept abject prices in exchange of Allah\u2019s covenant and their oaths, do not have a portion in the Hereafter \u2013 Allah will neither speak to them nor look towards them on the Day of Resurrection, nor will He purify them; and for them is a painful punishment."},"371":{"id":371,"surah":3,"ayah":78,"verse":"And amongst them are some who distort (change words of) the Book with their tongues, so that you may think that this also is in the Book whereas it is not in the Book; and they say, \u201cThis is from Allah\u201d whereas it is not from Allah; and they fabricate lies against Allah, whereas they know."},"372":{"id":372,"surah":3,"ayah":79,"verse":"It is not for any human to whom Allah has given the Book and wisdom and Prophethood, that he should afterwards say to the people, \u201cLeave (the worship of) Allah and be my worshippers\u201d \u2013 but he will surely say, \u201cBe sincere worshippers of Allah, because you teach the Book and you preach from it.\u201d"},"373":{"id":373,"surah":3,"ayah":80,"verse":"And nor will he command you to appoint the angels and the Prophets as Gods; would he (a Prophet) command you to disbelieve after you have become Muslims?"},"374":{"id":374,"surah":3,"ayah":81,"verse":"And remember when Allah took a covenant from the Prophets; \u201cIf I give you the Book and knowledge and the (promised) Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him) comes to you, confirming the Books you possess, you shall positively, definitely believe in him and you shall positively, definitely help him\u201d; He said, \u201cDo you agree, and accept My binding responsibility in this matter?\u201d They all answered, \u201cWe agree\u201d; He said, \u201cThen bear witness amongst yourselves, and I Myself am a witness with you.\u201d"},"375":{"id":375,"surah":3,"ayah":82,"verse":"So those who turn away after this \u2013 it is they who are the sinners."},"376":{"id":376,"surah":3,"ayah":83,"verse":"So do they desire a religion other than the religion of Allah, whereas to Him has submitted whoever is in the heavens and the earth, willingly or grudgingly, and it is to Him they will return?"},"377":{"id":377,"surah":3,"ayah":84,"verse":"Say, \u201cWe believe in Allah and what is sent down to us and what was sent down to Ibrahim, and Ismael, and Ishaq, and Yaqub (Jacob) and their sons, and that which came to Moosa and Eisa and the Prophets, from their Lord; we do not make any distinction, in belief, between any of them, and to Him we have submitted ourselves.\u201d"},"378":{"id":378,"surah":3,"ayah":85,"verse":"And if one seeks a religion other than Islam, it will never be accepted from him; and he is among the losers in the Hereafter."},"379":{"id":379,"surah":3,"ayah":86,"verse":"Why should Allah will guidance for the people who disbelieved, after their having accepted faith and bearing witness that the Noble Messenger is a true one, and after clear signs had come to them? And Allah does not guide the unjust."},"380":{"id":380,"surah":3,"ayah":87,"verse":"The recompense of such is that on them is the curse of Allah and of the angels and of men combined."},"381":{"id":381,"surah":3,"ayah":88,"verse":"Remaining in it forever; their punishment will not be lightened, nor will they get any respite."},"382":{"id":382,"surah":3,"ayah":89,"verse":"Except those who repented thereafter and reformed themselves; then indeed Allah is Oft Forgiving, Most Merciful."},"383":{"id":383,"surah":3,"ayah":90,"verse":"Indeed those who disbelieve after having accepted faith, and advance further in their disbelief \u2013 their repentance will never be accepted; and it is they who are the astray."},"384":{"id":384,"surah":3,"ayah":91,"verse":"Those who disbelieved and died as disbelievers \u2013 an earth full of gold will never be accepted from any one of them, even if he offers it, for his freedom; for them is a painful punishment and they do not have any aides."},"385":{"id":385,"surah":3,"ayah":92,"verse":"You can never attain virtue until you spend in Allah's cause the things you love; and Allah is Aware of whatever you spend."},"386":{"id":386,"surah":3,"ayah":93,"verse":"All food was lawful for the Descendants of Israel (Jacob), except what Israel forbade for himself, before the Taurat (Torah) was sent down; say, \u201cBring the Taurat and read it, if you are truthful.\u201d"},"387":{"id":387,"surah":3,"ayah":94,"verse":"So henceforth whoever fabricates lies against Allah \u2013 it is they who are the unjust."},"388":{"id":388,"surah":3,"ayah":95,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAllah is truthful; so follow the religion of Ibrahim, who was free from all falsehood; and he was not of the polytheists.\u201d"},"389":{"id":389,"surah":3,"ayah":96,"verse":"Indeed the first house that was appointed as a place of worship for mankind, is the one at Mecca (the Holy Ka\u2019aba), blessed and a guidance to the whole world;"},"390":{"id":390,"surah":3,"ayah":97,"verse":"In it are clear signs \u2013 the place where Ibrahim stood (is one of them); and whoever enters it shall be safe; and performing the Hajj (pilgrimage) of this house, for the sake of Allah, is a duty upon mankind, for those who can reach it; and whoever disbelieves \u2013 then Allah is Independent (Unwanting) of the entire creation!"},"391":{"id":391,"surah":3,"ayah":98,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO People given the Book(s)! Why do you not believe in the verses (or signs) of Allah, whereas your deeds are being witnessed by Allah?\u201d"},"392":{"id":392,"surah":3,"ayah":99,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO People given the Book(s)! Why do you prevent those who have accepted faith from the way of Allah, seeking to cause deviation in it, whereas you yourselves are witnesses to it? And Allah is not unaware of your deeds!\u201d"},"393":{"id":393,"surah":3,"ayah":100,"verse":"O People who Believe! If you obey some of People given the Book(s), they will definitely turn you into disbelievers after your having accepted faith."},"394":{"id":394,"surah":3,"ayah":101,"verse":"And how can you disbelieve, whereas Allah\u2019s verses are recited to you and His Noble Messenger is present amongst you?! And whoever takes the support of Allah is indeed guided to the right path."},"395":{"id":395,"surah":3,"ayah":102,"verse":"O People who Believe! Fear Allah in the manner He should rightfully be feared, and do not die except as Muslims."},"396":{"id":396,"surah":3,"ayah":103,"verse":"And hold fast to the rope of Allah, all of you together, and do not be divided; and remember Allah\u2019s favour on you, that when there was enmity between you, He created affection between your hearts, so due to His grace you became like brothers to each other; and you were on the edge of a pit of fire (hell), so He saved you from it; this is how Allah explains His verses to you, so that you may be guided."},"397":{"id":397,"surah":3,"ayah":104,"verse":"And there should be a group among you that invites to goodness, and enjoins good deeds and forbids immorality; it is they who are the successful."},"398":{"id":398,"surah":3,"ayah":105,"verse":"And do not be like those who became divided and disputed after the clear signs had come to them; and for them is a terrible punishment."},"399":{"id":399,"surah":3,"ayah":106,"verse":"On the Day (of Resurrection) when some faces will be shining and some faces black; so, (to) those whose faces are blackened, \u201cWhat! You disbelieved after you had accepted faith! Therefore now taste the punishment, the result of your disbelief.\u201d"},"400":{"id":400,"surah":3,"ayah":107,"verse":"And those whose faces will be shining, are in the mercy of Allah; they will abide in it forever."},"401":{"id":401,"surah":3,"ayah":108,"verse":"These are the verses of Allah, which We recite to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), with truth; and Allah does not wish any injustice to the creation."},"402":{"id":402,"surah":3,"ayah":109,"verse":"And to Allah only belongs all whatever is in the heavens and all whatever is in the earth; and towards Allah only is the return of all matters."},"403":{"id":403,"surah":3,"ayah":110,"verse":"You are the best among all the nations that were raised among mankind \u2013 you enjoin good deeds and forbid immorality and you believe in Allah; and if the People given the Book(s) believed it would be better for them; some of them are believers (Muslims) and most of them are disbelievers. (The best Ummah is that of Prophet Mohammed \u2013 peace and blessings be upon him.)"},"404":{"id":404,"surah":3,"ayah":111,"verse":"They cannot harm you except cause some trouble; and if they fight against you, they will turn their backs on you; then they will not be helped."},"405":{"id":405,"surah":3,"ayah":112,"verse":"Disgrace has been destined for them \u2013 wherever they are they shall not find peace, except by a rope from Allah and a rope from men \u2013 and they have deserved the wrath of Allah, and misery is destined for them; that is because they used to disbelieve in the signs of Allah, and unjustly martyr the Prophets; that was for their disobedience and transgression."},"406":{"id":406,"surah":3,"ayah":113,"verse":"All of them are not alike; among the People given the Book(s) are some who are firm on the truth \u2013 they recite the verses of Allah in the night hours and prostrate (before Him)."},"407":{"id":407,"surah":3,"ayah":114,"verse":"They accept faith in Allah and the Last Day, and enjoin good deeds and forbid immorality, and hasten to perform good deeds; and they are the righteous."},"408":{"id":408,"surah":3,"ayah":115,"verse":"And they will not be denied the reward of whatever good they do; and Allah knows the pious."},"409":{"id":409,"surah":3,"ayah":116,"verse":"Indeed those who disbelieve \u2013 neither their wealth nor their offspring will help save them in the least, from Allah; and they are the people of fire; remaining in it forever."},"410":{"id":410,"surah":3,"ayah":117,"verse":"The example of what they spend in this worldly life is similar to the freezing cold wind which struck the fields of a nation who wronged themselves, and destroyed it completely; and Allah did not oppress them, but it is they who wronged themselves."},"411":{"id":411,"surah":3,"ayah":118,"verse":"O People who Believe! Do not share your secrets with others \u2013 they do all they can to ruin you; they desire the maximum harm for you; enmity has been revealed from their speech, and what they hide in their breasts is greater; we have explained the signs clearly to you, if you have intelligence."},"412":{"id":412,"surah":3,"ayah":119,"verse":"Listen! It is you who love them and they do not love you, whereas the fact is that you believe in all the Books; when they meet you they say, \u201cWe believe\u201d; and when they are alone they chew their fingers at you with rage; say, \u201cDie in your frenzy!\u201d Allah knows well what lies within the hearts."},"413":{"id":413,"surah":3,"ayah":120,"verse":"If some good reaches you, they feel unhappy; and if misfortune befalls you, they will rejoice; and if you remain steadfast and pious, their evil scheme will not harm you in the least; undoubtedly all what they do is encompassed by Allah."},"414":{"id":414,"surah":3,"ayah":121,"verse":"And remember O dear Prophet (Mohammed \u2013 peace and blessings be upon him) when you came forth from your house in the morning, appointing the believers on positions of battle; and Allah is All Hearing, All Knowing."},"415":{"id":415,"surah":3,"ayah":122,"verse":"When two groups among you almost decided to show cowardice \u2013 and Allah is their Protector; and in Allah only should the believers trust."},"416":{"id":416,"surah":3,"ayah":123,"verse":"Allah indeed aided you at Badr when you had no means; so fear Allah \u2013 so that you may be thankful."},"417":{"id":417,"surah":3,"ayah":124,"verse":"When you O dear Prophet (Mohammed \u2013 peace and blessings be upon him) said to the believers, \u201cIs it not sufficient for you that your Lord may support you by sending down three thousand angels?\u201d"},"418":{"id":418,"surah":3,"ayah":125,"verse":"Yes, why not? If you patiently endure and remain pious, and the disbelievers attack you suddenly, your Lord will send down five thousand marked angels to help you."},"419":{"id":419,"surah":3,"ayah":126,"verse":"And Allah did not bestow this victory except for your happiness, and only that your hearts may attain peace with it; and there is no help except from Allah, the Almighty, the Wise."},"420":{"id":420,"surah":3,"ayah":127,"verse":"So He may cut off a part of the disbelievers, or disgrace them so that they return unsuccessful."},"421":{"id":421,"surah":3,"ayah":128,"verse":"This matter is not for you to decide \u2013 whether He guides them to repent or punishes them, for they are the unjust."},"422":{"id":422,"surah":3,"ayah":129,"verse":"And to Allah only belongs all whatever is in the heavens and all whatever is in the earth; He may forgive whomever He wills, and punish whomever He wills; and Allah is Oft Forgiving, Most Merciful."},"423":{"id":423,"surah":3,"ayah":130,"verse":"O People who Believe! Do not devour usury doubling and quadrupling it; and fear Allah, hoping that you achieve success."},"424":{"id":424,"surah":3,"ayah":131,"verse":"And save yourselves from the fire which is prepared for disbelievers."},"425":{"id":425,"surah":3,"ayah":132,"verse":"And obey Allah and the Noble Messenger, hoping that you gain mercy. (Obeying the Prophet is in fact obeying Allah.)"},"426":{"id":426,"surah":3,"ayah":133,"verse":"And rush towards forgiveness from your Lord, and towards a Paradise that can hold all the heavens and the earth in its width \u2013 prepared for the pious."},"427":{"id":427,"surah":3,"ayah":134,"verse":"Those who spend in Allah\u2019s cause, in happiness and in grief, and who control their anger and are forgiving towards mankind; and the righteous are the beloved of Allah."},"428":{"id":428,"surah":3,"ayah":135,"verse":"And those who, when they commit an immoral act or wrong themselves, remember Allah and seek forgiveness of their sins \u2013 and who forgives sins except Allah? And those who do not purposely become stubborn regarding what they did."},"429":{"id":429,"surah":3,"ayah":136,"verse":"For such the reward is forgiveness from their Lord, and Gardens beneath which rivers flow \u2013 abiding in it forever; what an excellent reward for the performers (of good deeds)!"},"430":{"id":430,"surah":3,"ayah":137,"verse":"Some traditions have been tried before you \u2013 therefore travel in the land and see what sort of fate befell those who denied."},"431":{"id":431,"surah":3,"ayah":138,"verse":"This is an explanation for mankind, a guidance and an advice to the pious."},"432":{"id":432,"surah":3,"ayah":139,"verse":"And do not be negligent nor grieve \u2013 it is you who will be victorious if you are believers."},"433":{"id":433,"surah":3,"ayah":140,"verse":"If you have been struck by some misfortune, so they (the disbelievers) too have been struck earlier with the same misfortune; these are the days in which We have allotted turns to people; and so that Allah may make known the believers and may bestow martyrdom to some of you; and Allah does not befriend the unjust."},"434":{"id":434,"surah":3,"ayah":141,"verse":"And so that Allah may purify* the believers, and destroy the disbelievers. (* Forgive them their sins, if any.)"},"435":{"id":435,"surah":3,"ayah":142,"verse":"What! You assume that you will enter Paradise while Allah has not yet tested your warriors, nor yet tested the steadfast?"},"436":{"id":436,"surah":3,"ayah":143,"verse":"And you used to wish for death before you met it; so now you see it before your eyes."},"437":{"id":437,"surah":3,"ayah":144,"verse":"And Mohammed (peace and blessings be upon him) is purely* a Noble Messenger; there have been Noble Messengers before him; so if he departs or is martyred, will you turn back on your heels? So whoever turns back on his heels does not cause any harm to Allah; and Allah will soon reward the thankful. (* Neither God nor an angel, but a human being with the highest spiritual status.)"},"438":{"id":438,"surah":3,"ayah":145,"verse":"And no soul can die except by Allah\u2019s command \u2013 a time has been appointed for each; whoever desires the rewards of this world, We bestow upon him from it; and whoever desires the reward of the Hereafter, We bestow upon him from it; and We shall soon reward the thankful."},"439":{"id":439,"surah":3,"ayah":146,"verse":"And many Prophets have fought in Allah\u2019s cause \u2013 and many devoted men were with them; so neither did they lose heart due to the calamities that befell them in Allah's cause, nor did they weaken and nor were they subdued; and the steadfast are the beloved of Allah."},"440":{"id":440,"surah":3,"ayah":147,"verse":"And they never said anything except that they invoked, \u201cOur Lord! Forgive us our sins and the excesses committed during our efforts, and keep our feet steady, and bestow us help over the disbelievers.\u201d"},"441":{"id":441,"surah":3,"ayah":148,"verse":"So Allah gave them the reward of this world and the best of rewards in the Hereafter; and Allah loves the righteous."},"442":{"id":442,"surah":3,"ayah":149,"verse":"O People who Believe! If you obey the disbelievers, they will make you turn back on your heels, so you will then turn back as losers."},"443":{"id":443,"surah":3,"ayah":150,"verse":"In fact, Allah is your Master; and He is the Best Supporter."},"444":{"id":444,"surah":3,"ayah":151,"verse":"Soon We shall cast awe in the hearts of disbelievers because they have appointed partners to Allah, for which He has not sent any proof; their destination is hell; and what a wretched abode for the unjust!"},"445":{"id":445,"surah":3,"ayah":152,"verse":"And indeed Allah has proved true His promise to you, when you used to slay the disbelievers by His command; until the time you people lost courage and disputed about the order and disobeyed after Allah had shown you what pleases you; some of you desired the world, and some of you desired the Hereafter; thereafter He turned you away from them in order to test you; and undoubtedly He has forgiven you; and Allah is Most Munificent towards the Muslims."},"446":{"id":446,"surah":3,"ayah":153,"verse":"When you were leaving, unconcerned without turning to look back at anyone, and from another group Our Noble Messenger was calling you (to fight); so He gave you sorrow in lieu of sorrow, and then forgave you so that you do not grieve over what has been lost or over the calamity that befell you; and Allah is Well Aware of what you do."},"447":{"id":447,"surah":3,"ayah":154,"verse":"Then after grief, He sent down a peaceful slumber (calm), which engulfed a group among you \u2013 and another party kept fearing for their own lives, thinking wrongfully of Allah \u2013 like the thoughts of ignorance; they say, \u201cDo we have any authority in this matter?\u201d Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAll authority lies only with Allah\u201d; they hide in their hearts what they do not reveal to you; they say, \u201cHad we any control, we would not have been slain here\u201d; say, \u201cEven if you had been in your houses, those destined to be slain would have come forth to their places of slaying; and in order that Allah may test what is in your breasts and reveal whatever is in your hearts\u201d; and Allah knows well what lies within the hearts."},"448":{"id":448,"surah":3,"ayah":155,"verse":"Indeed those of you who turned back on the day when the two armies met \u2013 for it was the devil who caused them to waver, because of some of their deeds; and undoubtedly Allah has forgiven them; indeed Allah is Oft Forgiving, Most Forbearing."},"449":{"id":449,"surah":3,"ayah":156,"verse":"O People who Believe! Do not be like the disbelievers who said regarding their brothers who went on a journey or on holy war, \u201cIf they had been here with us they would not have died or been killed\u201d \u2013 so that Allah may make it as despair in their hearts; and Allah gives life and causes death; and Allah is seeing your deeds."},"450":{"id":450,"surah":3,"ayah":157,"verse":"And if you are killed in Allah\u2019s way or die, then the pardon from Allah and mercy are better than all what they hoard."},"451":{"id":451,"surah":3,"ayah":158,"verse":"And whether you are killed or you die, towards Allah you will be gathered."},"452":{"id":452,"surah":3,"ayah":159,"verse":"So what a great mercy it is from Allah that you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), are lenient towards them; and if you had been stern and hardhearted (unsympathetic) they would have certainly been uneasy in your company; so forgive them and intercede for them and consult with them in the conduct of affairs; and when you decide upon something, rely upon Allah; indeed Allah loves those who trust (Him)."},"453":{"id":453,"surah":3,"ayah":160,"verse":"If Allah supports you, no one can overcome you; and if He forsakes you, who is there who can then help you? And in Allah only should the Muslims trust."},"454":{"id":454,"surah":3,"ayah":161,"verse":"And it is unimaginable that any Prophet would hide something; and whoever hides it, will bring what he hid with him on the Day of Resurrection; then every soul will be paid in full for whatever they earned; and they will not be wronged."},"455":{"id":455,"surah":3,"ayah":162,"verse":"So is one who follows the will of Allah equal to one who has incurred the wrath from Allah and whose home is the fire? And what a wretched place to return!"},"456":{"id":456,"surah":3,"ayah":163,"verse":"They are in different ranks before Allah; and Allah sees their deeds."},"457":{"id":457,"surah":3,"ayah":164,"verse":"Allah has indeed bestowed a great favour upon the Muslims, in that He sent to them a Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him) from among them, who recites to them His verses, and purifies them, and teaches them the Book and wisdom; and before it, they were definitely in open error. (The Holy Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is one of Allah\u2019s greatest favours to mankind.)"},"458":{"id":458,"surah":3,"ayah":165,"verse":"So will you say when disaster strikes you, though you had struck them with twice as much, \u201cWhere has this come from?\u201d Say (to them, O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIt has come from yourselves\u201d; indeed Allah is Able to do all things."},"459":{"id":459,"surah":3,"ayah":166,"verse":"And the calamity that struck you on the day the two armies met, was by Allah\u2019s command \u2013 and in order that He may make known the believers."},"460":{"id":460,"surah":3,"ayah":167,"verse":"And in order to expose them who turned hypocrites; and it was said to them, \u201cCome, fight in Allah's cause, or repel the enemy\u201d; they answered, \u201cIf we knew how to fight, we would certainly have been with you\u201d; and on that day they were nearer to open disbelief than to professed faith; they utter with their mouths what is not in their hearts; and Allah knows well what they hide. \u2013"},"461":{"id":461,"surah":3,"ayah":168,"verse":"Those who spoke regarding their brothers while they themselves stayed back at home, \u201cHad they listened to us, they would not have been slain\u201d; say (to them, O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cThen avert your own death, if you are truthful!\u201d"},"462":{"id":462,"surah":3,"ayah":169,"verse":"And do not ever assume that those who are slain in Allah's cause, are dead; in fact they are alive with their Lord, receiving sustenance. (Death does not mean extinction, it is the passing of the soul from this world to another. In case of virtuous believers, their bodies do not rot after death and they remain \u201calive\u201d, in the manner Allah has ordained for them.)"},"463":{"id":463,"surah":3,"ayah":170,"verse":"Happy over what Allah has bestowed upon them from His grace, and rejoicing for those who will succeed them, and have not yet joined them; for on them is no fear nor any grief."},"464":{"id":464,"surah":3,"ayah":171,"verse":"They rejoice because of the favours from Allah and (His) munificence, and because Allah does not waste the reward of the believers."},"465":{"id":465,"surah":3,"ayah":172,"verse":"Those who responded to the call of Allah and His Noble Messenger after they had been grieved; for the virtuous and the pious among them is a great reward."},"466":{"id":466,"surah":3,"ayah":173,"verse":"Those to whom the people said, \u201cThe people have gathered against you, therefore fear them\u201d, so their faith was further increased; and they said, \u201cAllah is Sufficient for us \u2013 and what an excellent (reliable) Trustee (of affairs) is He!\u201d"},"467":{"id":467,"surah":3,"ayah":174,"verse":"So they returned with the favour and munificence from Allah, in that no harm reached them; they followed what pleased Allah; and Allah is Extremely Munificent."},"468":{"id":468,"surah":3,"ayah":175,"verse":"It is the devil who threatens with his friends; so do not fear them and fear Me, if you have faith."},"469":{"id":469,"surah":3,"ayah":176,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) do not grieve for those who rush towards disbelief; they cannot cause any harm to Allah; and Allah does not will to assign them any portion in the Hereafter; and for them is a terrible punishment."},"470":{"id":470,"surah":3,"ayah":177,"verse":"Those who have purchased disbelief in exchange of faith cannot cause any harm to Allah; and for them is a painful punishment."},"471":{"id":471,"surah":3,"ayah":178,"verse":"And never must the disbelievers be under the illusion that the respite We give them is any good for them; We give them respite only for them to further advance in their sins; and for them is a disgraceful punishment."},"472":{"id":472,"surah":3,"ayah":179,"verse":"Allah will not leave you, O People who Believe (the Muslims) in your present state, till He separates the evil from the good; and it does not befit Allah\u2019s Majesty to give you, the common men, knowledge of the hidden, but Allah does chose from His Noble Messengers whomever He wills; so believe in Allah and His Noble Messengers; and if you believe and practice piety, for you is a great reward. (Allah gave the knowledge of the hidden to the Holy Prophet \u2013 peace and blessings be upon him.)"},"473":{"id":473,"surah":3,"ayah":180,"verse":"And never must those who act miserly upon what Allah has bestowed upon them of His grace, think that it is good for them; in fact it is harmful for them; soon what they had withheld will be collars round their necks on the Day of Resurrection; and Allah only is the Inheritor (Owner) of the heavens and the earth; and Allah is Well Aware of what you do."},"474":{"id":474,"surah":3,"ayah":181,"verse":"Undoubtedly Allah heard them who said, \u201cAllah is needy, and we are the wealthy\u201d; We shall keep recorded their saying and their wrongfully martyring of the Prophets; and We shall say, \u201cTaste the punishment of the fire!\u201d"},"475":{"id":475,"surah":3,"ayah":182,"verse":"\u201cThis is the recompense of what your hands have sent ahead and Allah does not oppress the bondmen.\u201d"},"476":{"id":476,"surah":3,"ayah":183,"verse":"Those who say, \u201cAllah has agreed with us that we should not believe in any Noble Messenger until he comes with the command to offer a sacrifice, which a fire (from heaven) shall devour\u201d; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cMany Noble Messengers did come to you before me, with clear signs and with this command which you state \u2013 why did you then martyr them, if you are truthful?\u201d"},"477":{"id":477,"surah":3,"ayah":184,"verse":"So O dear Prophet (Mohammed \u2013 peace and blessings be upon him) if they are denying you, Noble Messengers who came before you had also been denied, who had come with clear signs and Scriptures and the clear Book."},"478":{"id":478,"surah":3,"ayah":185,"verse":"Every soul must taste death; and only on the Day of Resurrection will you be fully recompensed; so the one who is saved from the fire and is admitted into Paradise \u2013 he is undoubtedly successful; and the life of this world is just counterfeit wealth."},"479":{"id":479,"surah":3,"ayah":186,"verse":"You will surely be tried in respect of your property and lives, and you will surely hear much wrong from those who were given the Book before you, and from the polytheists; and if you remain steadfast and remain pious, it is then an act of great courage."},"480":{"id":480,"surah":3,"ayah":187,"verse":"And remember when Allah took a covenant from the People given the Book(s) that, \u201cYou shall definitely preach it to the people and not hide it\u201d; so they flung it behind their backs and gained an abject price for it; so what a wretched bargain it is!"},"481":{"id":481,"surah":3,"ayah":188,"verse":"Do not ever think of those who rejoice for their deeds and wish to be praised without doing (good deeds) \u2013 do not ever think that they are safe from the punishment; and for them is a painful punishment."},"482":{"id":482,"surah":3,"ayah":189,"verse":"And for Allah only is the kingship of the heavens and the earth; and Allah is Able to do all things."},"483":{"id":483,"surah":3,"ayah":190,"verse":"Undoubtedly in the creation of the heavens and the earth and the alternation of night and day are signs for the intelligent."},"484":{"id":484,"surah":3,"ayah":191,"verse":"Those who remember Allah while standing, and sitting, and reclining on their sides, and ponder about the creation of the heavens and the earth; \u201cO our Lord! You have not created this without purpose; Purity is to You, therefore save us from the punishment of fire.\u201d"},"485":{"id":485,"surah":3,"ayah":192,"verse":"\u201cO our Lord! You have indeed given disgrace to whomever You put in the fire (of hell); and the unjust do not have any supporters.\u201d"},"486":{"id":486,"surah":3,"ayah":193,"verse":"\u201cO our Lord! We have heard a proclaimer calling towards faith, (saying) \u2018Believe in your Lord\u2019 so we have accepted faith; Our Lord! Therefore forgive us our sins, and wipe out our evil deeds, and make us die among the virtuous.\u201d"},"487":{"id":487,"surah":3,"ayah":194,"verse":"\u201cO our Lord! And give us what You have promised us through Your Noble Messengers, and do not humiliate us on the Day of Resurrection; indeed You do not break the promise.\u201d"},"488":{"id":488,"surah":3,"ayah":195,"verse":"So their Lord accepted their prayer, for I do not waste the efforts of any (righteous) worker, male or female; you are all one among yourselves; so those who migrated and were driven out from their homes and were harassed in My cause, and fought, and were slain \u2013 I will certainly wipe out all their sins and will certainly admit them into Gardens beneath which rivers flow; a reward from Allah; and only with Allah is the best reward."},"489":{"id":489,"surah":3,"ayah":196,"verse":"O listener (followers of this Prophet)! Do not ever be deceived by the disbelievers\u2019 free movements in the cities."},"490":{"id":490,"surah":3,"ayah":197,"verse":"It is a brief usage; their home is hell; and what an evil resting-place!"},"491":{"id":491,"surah":3,"ayah":198,"verse":"But for those who fear their Lord are Gardens beneath which rivers flow \u2013 abiding in it for ever \u2013 the hospitality from their Lord; and that which is with Allah, is the best for the righteous."},"492":{"id":492,"surah":3,"ayah":199,"verse":"And undoubtedly there are some among the People given the Book(s), who accept faith in Allah and what is sent down to you and what was sent down to them \u2013 their hearts are submitted humbly before Allah \u2013 they do not exchange the verses of Allah for an abject price; they are those whose reward is with their Lord; indeed Allah is Swift At Taking Account."},"493":{"id":493,"surah":3,"ayah":200,"verse":"O People who Believe! Endure and surpass your enemies in endurance, and guard the frontiers of the Islamic nation; and keep fearing Allah, hoping that you may succeed."},"494":{"id":494,"surah":4,"ayah":1,"verse":"O mankind! Fear your Lord Who created you from a single soul and from it created its spouse and from them both has spread the multitude of men and women; fear Allah in Whose name you claim (your rights from one another) and be mindful of your blood relations; indeed Allah is always seeing you."},"495":{"id":495,"surah":4,"ayah":2,"verse":"And give the orphans their wealth and do not exchange the pure for the foul; and do not devour (or use up) their wealth, mixing it with your own; this is indeed a great sin."},"496":{"id":496,"surah":4,"ayah":3,"verse":"And if you fear that you will not be just towards orphan girls, marry the women whom you like \u2013 two at a time, or three or four; then if you fear that you cannot keep two women equally then marry only one or the bondwomen you own; this is closer to your not doing injustice."},"497":{"id":497,"surah":4,"ayah":4,"verse":"And give the women their bridal money willingly; then if they willingly give you a part of it, eat (use) it with joy and fruition."},"498":{"id":498,"surah":4,"ayah":5,"verse":"And do not give the foolish their wealth which is in your custody \u2013 those for whom Allah has put you in charge to maintain (look after) \u2013 and feed and clothe them from it, and speak kindly to them."},"499":{"id":499,"surah":4,"ayah":6,"verse":"And test the orphans till they are fit to get married (reach full age); then if you find them of proper judgement, hand over their wealth to them; and do not devour it by spending excessively and hastily, fearing that they will grow up; and whoever is not in need must abstain; and whoever is needy may use from it in a reasonable measure; and when you hand over their wealth to them, get witnesses over them; and Allah is Sufficient to take account."},"500":{"id":500,"surah":4,"ayah":7,"verse":"For men is a share from what the parents and near relatives leave behind, and for women is a share from what the parents and near relatives leave behind, whether the wealth (inheritance) is small or large; the share is a fixed one."},"501":{"id":501,"surah":4,"ayah":8,"verse":"And if relatives and orphans and the needy are present at the time of disbursement, give them something from it and speak to them with kindness."},"502":{"id":502,"surah":4,"ayah":9,"verse":"And those people must fear, who if they die leaving behind them young children would be afraid for them; so they must fear Allah and speak with fairness."},"503":{"id":503,"surah":4,"ayah":10,"verse":"Indeed those who unjustly devour the wealth of orphans only fill their bellies with fire; and soon they will go into a blazing pit."},"504":{"id":504,"surah":4,"ayah":11,"verse":"Allah commands you concerning your children; the son\u2019s share is equal to that of two daughters; and if there are only daughters, for them is two-thirds of the inheritance, even if they are more than two; and if there is only one daughter, for her is half; and to each of the deceased\u2019s parents a sixth of the inheritance, if he has children; and if the deceased has no children but leaves behind parents, then one third for the mother; and if he has several brothers and sisters, a sixth for the mother, after any will he may have made and payment of debt; your fathers and your sons \u2013 you do not know which of them will be more useful to you; this is the share fixed by Allah; indeed Allah is All Knowing, Wise."},"505":{"id":505,"surah":4,"ayah":12,"verse":"And from what your wives leave, half is for you if they do not have any child; or if they have a child for you is a fourth of what they leave, after any will they may have made or debt to be paid; and to the women is a fourth of what you leave behind, if you do not have any child; or if you have a child then an eighth of what you leave behind, after any will you may have made, or debt to be paid; and if a deceased does not leave behind a mother, father or children but has a brother or a sister through a common mother, then to each of them a sixth; and if they (brothers and sisters) are more than two, then they shall all share in a third, after any will that may have been made or debt to be paid, in which the deceased has not caused a loss (to the heirs); this is the decree of Allah; and Allah is All Knowing, Most Forbearing."},"506":{"id":506,"surah":4,"ayah":13,"verse":"These are the limits of Allah; and whoever obeys Allah and His Noble Messenger \u2013 Allah will admit him into Gardens beneath which rivers flow \u2013 abiding in it forever; and this is the great success."},"507":{"id":507,"surah":4,"ayah":14,"verse":"And whoever disobeys Allah and His Noble Messenger and crosses all His limits \u2013 Allah will put him in the fire (of hell), in which he will remain forever; and for him is a disgraceful punishment."},"508":{"id":508,"surah":4,"ayah":15,"verse":"And take testimony from four chosen men amongst you, against the women among you who commit adultery; and if they testify, confine those women in the houses until death takes them away or Allah creates a solution for them."},"509":{"id":509,"surah":4,"ayah":16,"verse":"And punish them both, the man and the woman, whoever are guilty of it (adultery); then if they repent and become pious, leave them; indeed Allah is the Most Acceptor Of Repentance, Most Merciful."},"510":{"id":510,"surah":4,"ayah":17,"verse":"Undoubtedly the repentance which Allah has by His grace made obligatory upon Himself to accept, is only the repentance of those who commit sin in folly and then soon repent \u2013 towards them does Allah incline in mercy; and Allah is the All Knowing, the Wise."},"511":{"id":511,"surah":4,"ayah":18,"verse":"And that repentance is not of those who constantly commit sins, and when death approaches one of them, he says, \u201cI repent now\u201d, nor of those who die as disbelievers; for them, We have kept prepared a painful punishment."},"512":{"id":512,"surah":4,"ayah":19,"verse":"O People who Believe! It is not lawful for you to forcibly become the women\u2019s heirs; and do not restrain women with the intention of taking away a part of bridal money you gave them, unless they openly commit the shameful; and deal kindly with them; and if you do not like them, so it is possible that you dislike a thing in which Allah has placed abundant good."},"513":{"id":513,"surah":4,"ayah":20,"verse":"And if you wish to change one wife for another and you have given her heaps of treasure, do not take back anything from it; will you take it back by slander and open sin?"},"514":{"id":514,"surah":4,"ayah":21,"verse":"And how will you take it back whereas you have become unveiled before each other, and they have taken a strong pledge from you?"},"515":{"id":515,"surah":4,"ayah":22,"verse":"And do not marry the women who were wedded to your fathers (and grand fathers), except what has already passed; that is indeed an act of shame and great wrong; and an evil way."},"516":{"id":516,"surah":4,"ayah":23,"verse":"Forbidden for you are your mothers, and your daughters, and your sisters, and your father\u2019s sisters, and your mother\u2019s sisters, and your brothers\u2019 daughters and your sisters\u2019 daughters, and your foster-mothers (who breast-fed you), and their daughters (your foster-sisters), and your wives\u2019 mothers (mothers-in-law), and your wives\u2019 daughters who are under your protection \u2013 born of the women with whom you have cohabited; and if you have not cohabited with them, then it is no sin for you to marry their daughters; and (forbidden are) the wives of your own sons (and foster-sons and grandsons) and the keeping of two sisters together in marriage, except what has already passed; indeed Allah is Oft Forgiving, Most Merciful."},"517":{"id":517,"surah":4,"ayah":24,"verse":"And all married women are forbidden for you except the wives of disbelievers who come into your possession as bondwomen; this is Allah\u2019s decree for you; and other than these, all women are lawful for you so that you seek them in exchange of your wealth in proper wedlock, not adultery; therefore give the women you wish to marry, their appointed bridal money; and after the appointment (of bridal money) there is no sin on you if you come to a mutual agreement; indeed Allah is All Knowing, Wise."},"518":{"id":518,"surah":4,"ayah":25,"verse":"And whoever among you does not have in marriage free, believing women due to poverty, should marry from the believing bondwomen you own; and Allah knows well your faith; you are from one another; therefore marry them with the permission of their masters, and give them their bridal money according to custom, they becoming (faithful) wives, not committing mischief or secretly making friends; so when they are married and commit the shameful, for them is half the punishment prescribed for free women; this is for one among you who fears falling into adultery; and to practice patience is better for you; and Allah is Oft Forgiving, Most Merciful."},"519":{"id":519,"surah":4,"ayah":26,"verse":"Allah wills to explain His commands to you and show you the ways of those before you, and to incline towards you with His mercy; and Allah is All Knowing, Wise."},"520":{"id":520,"surah":4,"ayah":27,"verse":"And Allah wills to incline towards you with His mercy; and those who pursue their own pleasures wish that you be far separated from the Straight Path."},"521":{"id":521,"surah":4,"ayah":28,"verse":"Allah wills to lessen your burden; and man was created weak."},"522":{"id":522,"surah":4,"ayah":29,"verse":"O People who Believe! Do not unjustly devour the property of each other, except through trade by mutual agreement; and do not kill one another; indeed Allah is Most Merciful upon you."},"523":{"id":523,"surah":4,"ayah":30,"verse":"And whoever does that through injustice and oppression, We shall soon put him in the fire; and this is easy for Allah."},"524":{"id":524,"surah":4,"ayah":31,"verse":"If you keep avoiding the cardinal sins that are forbidden to you, We will forgive you your other (lesser) sins and admit you into a noble place."},"525":{"id":525,"surah":4,"ayah":32,"verse":"And do not long for things by which Allah has given superiority to some of you over others; for men is the share of what they earn; and for women the share from what they earn; and seek from Allah His munificence; indeed Allah knows everything."},"526":{"id":526,"surah":4,"ayah":33,"verse":"And for all, We have appointed heirs \u2013 from whatever the parents and near relatives leave behind; and to those with whom you have made an agreement, give them their dues; indeed all things are present before Allah."},"527":{"id":527,"surah":4,"ayah":34,"verse":"Men are in charge of women, as Allah has made one of them superior to the other, and because men spend their wealth for the women; so virtuous women are the reverent ones, guarding behind their husbands the way Allah has decreed guarding; and the women from whom you fear disobedience, (at first) advise them and (then) do not cohabit with them, and (lastly) beat them; then if they obey you, do not seek to do injustice to them; indeed Allah is Supreme, Great."},"528":{"id":528,"surah":4,"ayah":35,"verse":"And if you fear a dispute between husband and wife, send an arbitrator from the man\u2019s family and an arbitrator from the woman\u2019s family; if these two wish conciliation, Allah will unite them; indeed Allah is All Knowing, Well Aware."},"529":{"id":529,"surah":4,"ayah":36,"verse":"And worship Allah and do not ascribe any partner to Him, and be good to parents, and to relatives, and orphans, and the needy, and the related neighbour and the unrelated neighbour, and the close companion and the traveller; and your bondwomen; indeed Allah does not like any boastful, proud person. \u2013"},"530":{"id":530,"surah":4,"ayah":37,"verse":"Those who are misers and preach miserliness to others, and hide what Allah has bestowed upon them from His munificence; and We have kept ready a disgraceful punishment for the disbelievers."},"531":{"id":531,"surah":4,"ayah":38,"verse":"And those who spend their wealth in order for people to see, and do not accept faith in Allah nor the Last Day; and whoever has Satan (the devil) as a companion \u2013 so what an evil companion is he!"},"532":{"id":532,"surah":4,"ayah":39,"verse":"And what would they lose, if they had believed in Allah and the Last Day and spent from what Allah has bestowed upon them? And Allah knows them very well."},"533":{"id":533,"surah":4,"ayah":40,"verse":"Allah does not commit even the least injustice; and if there is a good deed, He doubles it and gives from Himself a great reward."},"534":{"id":534,"surah":4,"ayah":41,"verse":"So how will it be when We bring a witness from each nation (religion), and We bring you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) as a witness and a watcher over them? (The Holy Prophet is a witness from Allah.)"},"535":{"id":535,"surah":4,"ayah":42,"verse":"On that day, those who disbelieved and disobeyed the Noble Messenger will wish that they be buried and the ground levelled above them; and they will not be able to hide anything from Allah."},"536":{"id":536,"surah":4,"ayah":43,"verse":"O People who Believe! Do not approach the prayer when you are intoxicated until you have the sense to understand what you say, nor in the state of impurity until you have bathed except while travelling; and if you are ill or on a journey or one of you returns from answering the call of nature or you have cohabited with women, and you do not find water, then cleanse (yourself) with clean soil \u2013 therefore stroke your faces and your hands with it; indeed Allah is Most Pardoning, Oft Forgiving."},"537":{"id":537,"surah":4,"ayah":44,"verse":"Did you not see those who received a portion of the Book, that they purchase error and wish that you too go astray from the right path?"},"538":{"id":538,"surah":4,"ayah":45,"verse":"Allah well knows your enemies; Allah is Sufficient as a Guardian, and Allah is Sufficient as a Supporter."},"539":{"id":539,"surah":4,"ayah":46,"verse":"Some of the Jews interchange the words from their places and say, \u201cWe hear and disobey\u201d \u2013 and they say \u201cHear- may you not be able to hear\u201d \u2013 and they say \u201cRaena (Be considerate towards us)\u201d distorting it with their tongues and in order to slander religion; had they said, \u201cWe hear and we obey\u201d and \u201cKindly listen to us, O dear Prophet,\u201d and \u201cLook mercifully upon us, O dear Prophet\u201d, it would have been much better for them and more just \u2013 but Allah has cursed them for their disbelief; so they do not believe except a little."},"540":{"id":540,"surah":4,"ayah":47,"verse":"O People given the Book(s)! Believe in what We have sent down confirming the Book which you possess, before We transform some faces so turning them towards their backs, or curse them like We had cursed the people of Sabth; and (know that) the Allah\u2019s command is always carried out!"},"541":{"id":541,"surah":4,"ayah":48,"verse":"Undoubtedly Allah does not forgive (the sin of) disbelieving in Him and forgives anything lower than it to whomever He wills; and whoever ascribes partners to Allah has invented a tremendous sin."},"542":{"id":542,"surah":4,"ayah":49,"verse":"Did you not see those who proclaim their piety (cleanliness of deeds)? In fact Allah purifies whomever He wills, and no injustice, even equal to the hair upon a date seed will be done to them."},"543":{"id":543,"surah":4,"ayah":50,"verse":"See how they fabricate lies against Allah! And this is a sufficient manifest sin."},"544":{"id":544,"surah":4,"ayah":51,"verse":"Did you not see those who received a portion of the Book, that they believe in idols and the devil, and say regarding the disbelievers that they are more rightly guided than the Muslims?"},"545":{"id":545,"surah":4,"ayah":52,"verse":"It is they whom Allah has cursed; and for those whom Allah has cursed, you (Prophet Mohammed \u2013 peace and blessings be upon him) will never find any supporter."},"546":{"id":546,"surah":4,"ayah":53,"verse":"Do they have some share in the kingship?- if it were, they would not give to mankind even a single sesame."},"547":{"id":547,"surah":4,"ayah":54,"verse":"Or do they envy people due to what Allah has given them from His grace? In that case, We bestowed the Book and the wisdom upon the family of Ibrahim (Abraham), and We gave them a great kingdom."},"548":{"id":548,"surah":4,"ayah":55,"verse":"So some of them believed in it and some of them turned away from it; and sufficient is hell, a blazing fire!"},"549":{"id":549,"surah":4,"ayah":56,"verse":"We shall soon put those who disbelieve in Our signs into the fire; whenever their skins are cooked (fully burnt) We shall change them for new skins so they may taste the punishment (again and again); indeed Allah is Almighty, Wise."},"550":{"id":550,"surah":4,"ayah":57,"verse":"And those who believed and did good deeds, We shall soon admit them into Gardens beneath which rivers flow \u2013 abiding in it forever; in it for them are pure wives \u2013 and We shall admit them into places of plentiful shade."},"551":{"id":551,"surah":4,"ayah":58,"verse":"Indeed Allah commands you to hand over whatever you hold in trust, to their owners \u2013 and that whenever you judge between people, judge with fairness; undoubtedly Allah gives you an excellent advice; indeed Allah is All Hearing, All Seeing."},"552":{"id":552,"surah":4,"ayah":59,"verse":"O People who Believe! Obey Allah and the Noble Messenger and those amongst you who are in authority; so if there is a dispute amongst you concerning any matter, refer it to Allah and the Noble Messenger (for judgement) if you believe in Allah and the Last Day; this is better and has the best outcome."},"553":{"id":553,"surah":4,"ayah":60,"verse":"Did you not see those whose claim is that they believe in what has been sent down on you and what was sent down before you, and they then wish to make the devil their judge, whereas they were ordered to completely reject him? And the devil wishes to mislead them far astray."},"554":{"id":554,"surah":4,"ayah":61,"verse":"And when they are told, \u201cCome towards the Book sent down by Allah and to the Noble Messenger,\u201d you will see that the hypocrites turn their faces away from you."},"555":{"id":555,"surah":4,"ayah":62,"verse":"What will be their state, if some calamity befalls them as a result of what their own hands have sent before them \u2013 and then they come to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), swearing by Allah that, \u201cOur goal was only to do good and create harmony\"?"},"556":{"id":556,"surah":4,"ayah":63,"verse":"The secrets of their hearts are well known to Allah; so avoid them and explain to them, and speak to them clearly in their affairs."},"557":{"id":557,"surah":4,"ayah":64,"verse":"And We did not send any Noble Messenger except that he be obeyed by Allah\u2019s command; and if they, when they have wronged their own souls, come humbly to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) and seek forgiveness from Allah, and the Noble Messenger intercedes for them, they will certainly find Allah as the Most Acceptor Of Repentance, the Most Merciful."},"558":{"id":558,"surah":4,"ayah":65,"verse":"So O dear Prophet (Mohammed \u2013 peace and blessings be upon him), by oath of your Lord, they will not be Muslims until they appoint you a judge for the disputes between them \u2013 and then whatever you have decided, they should not find opposition to it within their hearts, and they must accept it wholeheartedly."},"559":{"id":559,"surah":4,"ayah":66,"verse":"And had We decreed for them to slay themselves or to leave their homes and families, only a few of them would have done it; and if they did what they are advised to, it would be good for them, and would have strengthened faith."},"560":{"id":560,"surah":4,"ayah":67,"verse":"And were it so, We would bestow upon them a great reward from Ourselves."},"561":{"id":561,"surah":4,"ayah":68,"verse":"And would certainly guide them to the Straight Path."},"562":{"id":562,"surah":4,"ayah":69,"verse":"And whoever obeys Allah and His Noble Messenger, will be with those upon whom Allah has bestowed grace \u2013 that is, the Prophets and the truthful and the martyrs and the virtuous; and what excellent companions they are!"},"563":{"id":563,"surah":4,"ayah":70,"verse":"This is Allah\u2019s munificence; and Allah is Sufficient, the All Knowing."},"564":{"id":564,"surah":4,"ayah":71,"verse":"O People who Believe! Be cautious, then advance towards the enemy in small numbers or all together."},"565":{"id":565,"surah":4,"ayah":72,"verse":"Indeed among you is one who will certainly loiter behind; then if some disaster were to befall you, he would say, \u201cIt was Allah\u2019s grace upon me that I was not present with them!\u201d"},"566":{"id":566,"surah":4,"ayah":73,"verse":"And were you to receive Allah\u2019s munificence (a bounty), he would surely say \u2013 as if there had been no friendship between you and him \u2013 \u201cAlas \u2013 if only I had been with them, I would have achieved a great success!\u201d"},"567":{"id":567,"surah":4,"ayah":74,"verse":"So those who sell the life of this world for the Hereafter, must fight in Allah's cause; and We shall bestow a great reward upon whoever fights in Allah's cause, whether he is martyred or is victorious."},"568":{"id":568,"surah":4,"ayah":75,"verse":"And what is the matter with you, that you should not fight in Allah\u2019s cause and for the feeble men, and women, and children, who invoke, \u201cOur Lord! Liberate us from this town, the people of which are unjust; and give us a protector from Yourself; and give us a supporter from Yourself.\u201d (Allah has created many supporters for the believers.)"},"569":{"id":569,"surah":4,"ayah":76,"verse":"The believers fight for Allah\u2019s cause; and the disbelievers fight for the devil\u2019s cause \u2013 so fight against the friends of the devil; undoubtedly the devil\u2019s conspiracy is weak."},"570":{"id":570,"surah":4,"ayah":77,"verse":"Did you not see those to whom it was said, \u201cRestrain your hands, keep the prayer established and pay the charity\u201d; but when fighting was ordained for them, some of them started fearing people, the way they feared Allah \u2013 or even greater! And they said, \u201cOur Lord! Why have You ordained fighting for us? If only You would have let us live some more!\u201d Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cThe usage of this world is meagre; and the Hereafter is better for the pious; and you will not be wronged even (the weight of) a single thread.\u201d"},"571":{"id":571,"surah":4,"ayah":78,"verse":"Death will come to you wherever you may be, even if you were in strong fortresses; if some good reaches them they say, \u201cThis is from Allah\u201d; and if any misfortune reaches them, they say, \u201cThis is from you\u201d; say, \u201cEverything is from Allah\u201d; what is wrong with these people, that they do not seem to understand anything?"},"572":{"id":572,"surah":4,"ayah":79,"verse":"Whatever good reaches you, O listeners, is from Allah, and whatever ill reaches you is from yourselves; and We have sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) as a Noble Messenger towards all mankind; and Allah is Sufficient, as a Witness."},"573":{"id":573,"surah":4,"ayah":80,"verse":"Whoever obeys the Noble Messenger has indeed obeyed Allah; and for those who turn away \u2013 We have not sent you as their saviour."},"574":{"id":574,"surah":4,"ayah":81,"verse":"And they say, \u201cWe have obeyed\u201d; and when they go away from you, a group of them spend the night conspiring against what they had said; and Allah records what they conspired by night; therefore O dear Prophet (Mohammed \u2013 peace and blessings be upon him) avoid them and rely upon Allah; and Allah is Sufficient as a Trustee (of affairs)."},"575":{"id":575,"surah":4,"ayah":82,"verse":"So do they not ponder about the Qur\u2019an? And had it been from anyone besides Allah, they would certainly find much contradiction in it."},"576":{"id":576,"surah":4,"ayah":83,"verse":"And when any news of safety or fear comes to them, they speak of it publicly; and had they referred it to the Noble Messenger and to those among them having authority, those among them who are able to infer would certainly learn the truth of the matter from them; and were it not for Allah\u2019s munificence upon you, and His mercy, all of you would have certainly followed Satan \u2013 except a few."},"577":{"id":577,"surah":4,"ayah":84,"verse":"Therefore O dear Prophet, fight in Allah's cause; you will not be burdened except for yourself, and urge the believers (to fight); it is likely that Allah will curb the strength of the disbelievers; and Allah's strike is most stinging and His punishment the most severe."},"578":{"id":578,"surah":4,"ayah":85,"verse":"Whoever makes a noble intercession will have a share of it, and whoever makes an evil intercession will have a share of it; and Allah is Able to do all things."},"579":{"id":579,"surah":4,"ayah":86,"verse":"And when you are greeted with some words, greet back with words better than it or with the same; indeed Allah will take account of everything."},"580":{"id":580,"surah":4,"ayah":87,"verse":"Allah! There is none worthy of worship except Him; He will surely gather you all on the Day of Resurrection in which there is no doubt; and whose Words are more true than those of Allah? (Allah does not lie.)"},"581":{"id":581,"surah":4,"ayah":88,"verse":"So what is the matter with you that you got divided into two groups concerning the hypocrites, whereas Allah has inverted them because of their misdeeds? Do you wish to guide one whom Allah has sent astray? And for one whom Allah sends astray, you will not find a way."},"582":{"id":582,"surah":4,"ayah":89,"verse":"They wish that you too should turn disbelievers the way they did, so that you all may become equal \u2013 so do not befriend any of them until they forsake their homes and families in Allah\u2019s cause; then if they turn back, seize them and kill them wherever you find them; and do not take any of them as a friend nor as a supporter."},"583":{"id":583,"surah":4,"ayah":90,"verse":"Except those who are related to the people between whom and you is a treaty, or who come to you with their hearts no longer having the will to fight you or to fight their own people; had Allah willed, He would certainly have given them power over you so that they would have surely fought you; then if they avoid you and do not wage war against you and make an offer of peace \u2013 then Allah has not kept for you a way against them."},"584":{"id":584,"surah":4,"ayah":91,"verse":"You will now find others who desire that they should be safe from you and also safe from their own people; whenever their people turn them towards war, they fall headlong into it; so if they do not avoid (confronting) you nor submit an offer of peace nor restrain their hands, seize them and kill them wherever you find them; and they are the ones against whom We have given you clear authority."},"585":{"id":585,"surah":4,"ayah":92,"verse":"It is not rightful for a Muslim to kill another Muslim, unless it occurs by mistake; and the one who mistakenly kills a Muslim must set free a Muslim slave and pay blood-money to the family of the slain, except if they forego it; and if the victim is of a people who are hostile to you, and the killer is a Muslim, then only the setting free of a Muslim slave (is obligatory); and if the victim is from a people between whom and you there is a treaty, then blood-money must be paid to his family and the setting free of a Muslim slave; therefore one who has no means must fast for two consecutive months; this is his penance before Allah; and Allah is All Knowing, Wise."},"586":{"id":586,"surah":4,"ayah":93,"verse":"And whoever slays a Muslim on purpose, his reward will be hell \u2013 to remain in it for ages \u2013 and Allah has wreaked wrath upon him and has cursed him and kept prepared a terrible punishment for him."},"587":{"id":587,"surah":4,"ayah":94,"verse":"O People who Believe! When you go forth to fight in holy war, make a proper study, and do not say to the one who greets you, \u201cYou are not a Muslim,\u201d \u2013 you seek the means of this worldly life; so with Allah are the bounties in plenty; you too were like this before, then Allah bestowed His favour on you, therefore you must make a proper study; indeed Allah knows whatever you do."},"588":{"id":588,"surah":4,"ayah":95,"verse":"The Muslims who stay back from holy war without proper excuse, are not equal to the Muslims who fight in Allah's cause with their wealth and lives; Allah has bestowed higher ranks to the warriors who strive with their wealth and lives, than those who stay back; and Allah has promised good to all; and Allah has favoured the warriors upon those who stay back, with a great reward."},"589":{"id":589,"surah":4,"ayah":96,"verse":"Ranks (of honour) from Him, and forgiveness, and mercy; and Allah is Oft Forgiving, Most Merciful."},"590":{"id":590,"surah":4,"ayah":97,"verse":"The angels ask the people whose souls they remove while they were wronging themselves, \u201cWhat were you engaged in?\u201d They reply, \u201cWe were powerless in the land\u201d; the angels say, \u201cWas Allah\u2019s earth not spacious enough for you to have migrated in it?\u201d The destination for such is hell; and a very wretched place to return."},"591":{"id":591,"surah":4,"ayah":98,"verse":"Except those who were forcibly subdued among men, and the women and children, unable to devise a plan and unaware of the way."},"592":{"id":592,"surah":4,"ayah":99,"verse":"So for such, it is likely that Allah will pardon them; and Allah is Most Pardoning, Oft Forgiving."},"593":{"id":593,"surah":4,"ayah":100,"verse":"Whoever migrates for Allah's cause will find much shelter and abundant capacity in the earth; and whoever leaves his home, migrating towards Allah and His Noble Messenger, and death seizes him, his reward then lies entrusted with Allah; and Allah is Oft Forgiving, Most Merciful."},"594":{"id":594,"surah":4,"ayah":101,"verse":"And when you travel in the land, it is no sin for you to curtail some of your obligatory prayers; if you fear that disbelievers may cause you harm; undoubtedly the disbelievers are open enemies to you."},"595":{"id":595,"surah":4,"ayah":102,"verse":"And when you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) are among them and lead them in prayer, only a group of them should be with you, and they must keep their weapons with them; so when they have performed their prostrations they should move away behind you; and the other group that had not prayed, must come and offer prayers in your leadership, keeping their guard and weapons with them; the disbelievers wish that you neglect your arms and your means so they may overpower you with a single attack; it is no sin for you to lay aside your arms due to rain or if you are sick; and keep your guard; undoubtedly Allah has kept prepared a disgraceful punishment for the disbelievers."},"596":{"id":596,"surah":4,"ayah":103,"verse":"So when you have offered your prayers remember Allah while standing, sitting and reclining; and when you feel secure, offer prayers in the usual manner; indeed prayers are a time bound obligatory duty upon the Muslims."},"597":{"id":597,"surah":4,"ayah":104,"verse":"Do not relax in pursuit of the disbelievers; if you are suffering, they also suffer as you do; and you expect from Allah what they do not; and Allah is All Knowing, Wise."},"598":{"id":598,"surah":4,"ayah":105,"verse":"We have indeed sent down the true Book towards you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), so that you may judge between men, in the way Allah may show you; and do not plead on behalf of the treacherous."},"599":{"id":599,"surah":4,"ayah":106,"verse":"And seek forgiveness from Allah; indeed Allah is Oft Forgiving, Most Merciful."},"600":{"id":600,"surah":4,"ayah":107,"verse":"And do not plead on behalf of those who deceive themselves; indeed Allah does not like any treacherous, excessive sinner."},"601":{"id":601,"surah":4,"ayah":108,"verse":"They hide from men and do not hide from Allah, whereas Allah is with them when they plan in their hearts that which displeases Him; and Allah has their deeds encompassed."},"602":{"id":602,"surah":4,"ayah":109,"verse":"Pay heed! It is you people who argued on their behalf in the life of this world; so who will fight on their behalf with Allah on the Day of Resurrection, or who will be their pleader?"},"603":{"id":603,"surah":4,"ayah":110,"verse":"And whoever does evil or wrongs his own soul and then seeks forgiveness from Allah, will find Allah Oft Forgiving, Most Merciful."},"604":{"id":604,"surah":4,"ayah":111,"verse":"And whoever earns sin, his earnings will only be against himself; and Allah is the Knowing, Wise."},"605":{"id":605,"surah":4,"ayah":112,"verse":"And whoever commits a mistake or a sin, then blames it on someone innocent has indeed burdened himself with infamy and a manifest crime."},"606":{"id":606,"surah":4,"ayah":113,"verse":"And O dear Prophet, were it not for Allah\u2019s munificence and His mercy upon you, a group among them would have wished to deceive you; and they only mislead themselves and will not harm you at all; and Allah has sent down upon you the Book and wisdom, and taught you all what you did not know*; and upon you is Allah\u2019s great munificence. (Allah gave the knowledge of the hidden to the Holy Prophet \u2013 peace and blessings be upon him.)"},"607":{"id":607,"surah":4,"ayah":114,"verse":"Most of their discussions do not contain any good, except of the one who enjoins charity or goodness or peace-making among people; whoever does that to seek the pleasure of Allah \u2013 We shall soon give him a great reward."},"608":{"id":608,"surah":4,"ayah":115,"verse":"And whoever opposes the Noble Messenger after the right path has been made clear to him, and follows a way other than that of the Muslims, We shall leave him as he is, and put him in hell; and what a wretched place to return!"},"609":{"id":609,"surah":4,"ayah":116,"verse":"Allah does not forgive (the greatest sin) that partners be ascribed with Him \u2013 and He forgives all that is below (lesser sins) it, to whomever He wills; and whoever ascribes partners with Allah has indeed wandered far astray."},"610":{"id":610,"surah":4,"ayah":117,"verse":"The polytheists do not worship Allah, except some females; and they do not worship anyone except the rebellious Satan."},"611":{"id":611,"surah":4,"ayah":118,"verse":"The one whom Allah has cursed; and the devil said, \u201cI swear, I will certainly take an appointed portion of Your bondmen,\u201d \u2013"},"612":{"id":612,"surah":4,"ayah":119,"verse":"\u201cAnd I will surely lead them astray, and I will certainly arouse desires in them, and I will definitely order them so they will pierce animals\u2019 ears, and I will definitely order them so they will alter Allah\u2019s creation\u201d; and whoever chooses the devil for a friend instead of Allah, has indeed suffered a manifest loss."},"613":{"id":613,"surah":4,"ayah":120,"verse":"The devil promises them and arouses desires in them; and the devil does not give them promises except of deceit."},"614":{"id":614,"surah":4,"ayah":121,"verse":"The destination for such is hell; they will not find any refuge from it."},"615":{"id":615,"surah":4,"ayah":122,"verse":"And those who believed and did good deeds \u2013 We shall soon admit them into Gardens beneath which rivers flow, abiding in them for ever and ever; a true promise from Allah; and whose Words are more true than those of Allah? (Allah does not lie.)"},"616":{"id":616,"surah":4,"ayah":123,"verse":"The affair does not rest on your thoughts, nor the cravings of the People given the Book(s); whoever does wrong will get the recompense of it \u2013 and will not find, other than Allah, any friend or any supporter."},"617":{"id":617,"surah":4,"ayah":124,"verse":"And whoever does some good deeds, be it a man or woman, and is a Muslim, will be admitted to Paradise and they will not be wronged even to the extent of one sesame."},"618":{"id":618,"surah":4,"ayah":125,"verse":"And whose religion is better than one who submits his self to Allah and is virtuous and follows the religion of Ibrahim, who was far removed from all falsehood? And Allah made Ibrahim His close friend."},"619":{"id":619,"surah":4,"ayah":126,"verse":"And to Allah only belongs all whatever is in the heavens and all whatever is in the earth; and Allah has control over all things."},"620":{"id":620,"surah":4,"ayah":127,"verse":"And they ask you the decree concerning women; say, \u201cAllah gives you a decree concerning them \u2013 and what is recited to you from the Qur\u2019an concerning orphan girls, that you are not giving them which is ordained for them, and you are avoiding marrying them \u2013 and concerning the weak children, and that you must firmly establish justice in dealing with the orphans\u2019 rights; and whatever good deeds you do, then Allah is Well Aware of it.\u201d"},"621":{"id":621,"surah":4,"ayah":128,"verse":"And if a woman fears ill treatment from her husband or disinterest, so it is no sin for them if they reach an agreement of peace between themselves; and peace is better; and the heart is trapped in greed; and if you do good and practice piety, then Allah is Well Aware of it."},"622":{"id":622,"surah":4,"ayah":129,"verse":"And you will never be able to deal equally between women however much you may desire \u2013 therefore do not be totally inclined towards one leaving the other in uncertainty; and if you do good and practice piety, then (know that) Allah is Oft Forgiving, Most Merciful."},"623":{"id":623,"surah":4,"ayah":130,"verse":"And if the two separate, Allah will make each one independent of the other, with His Capability; and Allah is Most Capable, Wise."},"624":{"id":624,"surah":4,"ayah":131,"verse":"And to Allah only belongs all whatever is in the heavens and all whatever is in the earth; and indeed We have commanded those who received the Books before you, and commanded you, that keep fearing Allah; and if you disbelieve, undoubtedly to Allah only belongs all whatever is in the heavens and all whatever is in the earth; and Allah is Independent, Worthy Of All Praise."},"625":{"id":625,"surah":4,"ayah":132,"verse":"And to Allah only belongs all whatever is in the heavens and all whatever is in the earth; and Allah is Sufficient as a Trustee (of affairs)."},"626":{"id":626,"surah":4,"ayah":133,"verse":"O people! He can remove you and bring others, if He wills; and Allah is Able to do that."},"627":{"id":627,"surah":4,"ayah":134,"verse":"Whoever desires the reward of this world, then with Allah only lie both \u2013 the rewards of this world and of the Hereafter; and Allah is All Hearing, All Seeing."},"628":{"id":628,"surah":4,"ayah":135,"verse":"O People who Believe! Be firm in establishing justice, giving witness for Allah, even if it is harmful to yourselves or parents or relatives; whether the one you testify against is wealthy or poor, for in any case Allah has the greater right over it; then do not follow your wishes for you may stray from the truth; and if you distort testimony or turn away, then Allah is Well Aware of your deeds."},"629":{"id":629,"surah":4,"ayah":136,"verse":"O People who Believe! Have faith in Allah and His Noble Messenger and the Book He has sent down upon this Noble Messenger of His, and the Book He sent down before; and whoever does not accept faith in Allah and His angels and His Books and His Noble Messengers and the Last Day, has undoubtedly wandered far astray."},"630":{"id":630,"surah":4,"ayah":137,"verse":"Indeed those who believe, then disbelieve and then again believe, then again disbelieve, and go further in their disbelief \u2013 Allah will never forgive them, nor ever guide them to the path."},"631":{"id":631,"surah":4,"ayah":138,"verse":"Give glad tidings to the hypocrites, that for them is a painful punishment."},"632":{"id":632,"surah":4,"ayah":139,"verse":"Those who leave the Muslims to befriend the disbelievers; do they seek honour from them? Then (know that) undoubtedly all honour is for Allah."},"633":{"id":633,"surah":4,"ayah":140,"verse":"And indeed Allah has sent down to you in the Book that whenever you hear the signs of Allah being rejected or being made fun of, do not sit with those people, until they engage in some other conversation; or else you too are like them; undoubtedly Allah will gather the hypocrites and the disbelievers, all together, into hell."},"634":{"id":634,"surah":4,"ayah":141,"verse":"Those who keep watching your circumstances; so if a victory comes to you from Allah, they say, \u201cWere we not with you?\u201d; and if victory is for disbelievers, they say, \u201cDid we not have control over you, and protect you from the Muslims?\u201d Allah will judge between you all on the Day of Resurrection; and Allah will not provide the disbelievers any way over the Muslims."},"635":{"id":635,"surah":4,"ayah":142,"verse":"Undoubtedly the hypocrites, in their fancy, seek to deceive Allah whereas He will extinguish them while making them oblivious; and when they stand up for prayer, they do it unwillingly and for others to see, and they do not remember Allah except a little."},"636":{"id":636,"surah":4,"ayah":143,"verse":"Fluctuating in the middle; neither here (in faith) nor there (in disbelief); and for one whom Allah sends astray, you will not find a way."},"637":{"id":637,"surah":4,"ayah":144,"verse":"O People who Believe! Do not befriend disbelievers in place of Muslims; do you wish to give Allah a clear proof against you?"},"638":{"id":638,"surah":4,"ayah":145,"verse":"Undoubtedly the hypocrites are in the deepest segment of hell; and you will never find any supporter for them."},"639":{"id":639,"surah":4,"ayah":146,"verse":"Except those who repented and reformed themselves and held fast to Allah\u2019s rope and made their religion sincerely only for Allah \u2013 so they are with the Muslims; and Allah will soon bestow upon the believers a great reward."},"640":{"id":640,"surah":4,"ayah":147,"verse":"And what will Allah gain by punishing you, if you acknowledge the truth and accept faith? And Allah is Most Appreciative, All Knowing."},"641":{"id":641,"surah":4,"ayah":148,"verse":"Allah does not like disclosure of evil matters except by the oppressed; and Allah is All Hearing, All Knowing."},"642":{"id":642,"surah":4,"ayah":149,"verse":"If you do any good openly or in secret, or pardon someone\u2019s evil \u2013 then indeed Allah is Oft Forgiving, Able."},"643":{"id":643,"surah":4,"ayah":150,"verse":"Those who disbelieve in Allah and His Noble Messengers, and seek to cause division between Allah and His Noble Messengers, and say, \u201cWe believe in some and disbelieve in others,\u201d and wish to choose a way between faith and disbelief; \u2013"},"644":{"id":644,"surah":4,"ayah":151,"verse":"These are the real disbelievers; and for the disbelievers We have kept prepared a disgraceful punishment."},"645":{"id":645,"surah":4,"ayah":152,"verse":"And those who believe in Allah and all His Noble Messengers and do not make any distinction in belief between any of them \u2013 to them Allah will soon give them their reward; and Allah is Oft Forgiving, Most Merciful."},"646":{"id":646,"surah":4,"ayah":153,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him) the People given the Book(s) ask you, to cause a Book to be sent down on them from heaven \u2013 so they had asked something even greater from Moosa, for they said, \u201cShow Allah to us, clearly\u201d \u2013 so the thunder seized them on account of their sins; then they chose the calf (for worship) after clear signs had come to them, then We forgave this; and We bestowed Moosa with a clear dominance."},"647":{"id":647,"surah":4,"ayah":154,"verse":"We then raised the mount (Sinai) above them to take a covenant from them and decreed them that, \u201cEnter the gate while prostrating\u201d and decreed them that, \u201cDo not cross the limits of the Sabbath,\u201d and We took from them a firm covenant."},"648":{"id":648,"surah":4,"ayah":155,"verse":"So Allah cursed them because of their constantly breaking their covenant \u2013 and they disbelieved in the signs of Allah, and they used to wrongfully martyr the Prophets, and because they said, \u201cOur hearts are covered\u201d; in fact Allah has set a seal upon their hearts due to their disbelief, so that they do not accept faith, except a few."},"649":{"id":649,"surah":4,"ayah":156,"verse":"And because they disbelieved and slandered Maryam with a tremendous accusation."},"650":{"id":650,"surah":4,"ayah":157,"verse":"And because they said, \u201cWe have killed the Messiah, Eisa the son of Maryam, the Messenger of Allah\u201d; they did not slay him nor did they crucify him, but a look-alike was created for them; and those who disagree concerning it are in doubt about it; they know nothing of it, except the following of assumptions; and without doubt, they did not kill him."},"651":{"id":651,"surah":4,"ayah":158,"verse":"In fact Allah raised him towards Himself; and Allah is Almighty, Wise."},"652":{"id":652,"surah":4,"ayah":159,"verse":"There is not one of the People given the Book(s), who will not believe in him (Eisa) before his death; and on the Day of Resurrection he will be a witness against them."},"653":{"id":653,"surah":4,"ayah":160,"verse":"So due to the great injustices committed by the Jews, We forbade them some of the good things which were earlier lawful for them, and because they prevented many people from Allah\u2019s way."},"654":{"id":654,"surah":4,"ayah":161,"verse":"And because they used to take usury whereas they were forbidden from it, and they used to wrongfully devour people\u2019s wealth; and for the disbelievers among them, We have kept prepared a painful punishment."},"655":{"id":655,"surah":4,"ayah":162,"verse":"But those among them who are firm in knowledge and who have faith, believe in what is sent down upon you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), and what was sent down before you, and those who keep the prayer established and those who pay the charity, and those who believe in Allah and the Last Day; to such, We shall soon bestow a great reward."},"656":{"id":656,"surah":4,"ayah":163,"verse":"Indeed We sent a divine revelation to you (Prophet Mohammed \u2013 peace and blessing be upon him) as We did send divine revelations to Nooh (Noah) and the Prophets after him; and We sent divine revelations to Ibrahim (Abraham) and Ismael (Ishmael) and Ishaq (Isaac) and Yaqub (Jacob) and their offspring, and Eisa (Jesus) and Ayyub (Job) and Yunus (Jonah) and Haroon (Aaron) and Sulaiman (Solomon), and We bestowed the Zaboor (the Holy Book) upon Dawud (David)."},"657":{"id":657,"surah":4,"ayah":164,"verse":"And to the Noble Messengers whom We have mentioned to you before, and to the Noble Messengers We have not mentioned to you; and Allah really did speak to Moosa."},"658":{"id":658,"surah":4,"ayah":165,"verse":"Noble Messengers giving glad tidings and declaring warnings, in order that people may not have any argument against Allah, after the (advent of) Noble Messengers; and Allah is Almighty, Wise."},"659":{"id":659,"surah":4,"ayah":166,"verse":"But Allah is the Witness of what He has sent down upon you \u2013 He has sent it down by His knowledge; and the angels are witnesses; and sufficient is Allah\u2019s testimony."},"660":{"id":660,"surah":4,"ayah":167,"verse":"Indeed those who disbelieved and prevented (others) from the way of Allah, have undoubtedly wandered far astray."},"661":{"id":661,"surah":4,"ayah":168,"verse":"Indeed those who disbelieved and crossed the limits \u2013 Allah will never forgive them, nor guide them to a way."},"662":{"id":662,"surah":4,"ayah":169,"verse":"Except the path of hell, in which they will remain for ever and ever; and that is easy for Allah."},"663":{"id":663,"surah":4,"ayah":170,"verse":"O mankind! This Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him) has come to you with the truth from your Lord, so accept faith for your own good; and if you disbelieve, then undoubtedly to Allah only belongs all whatever is in the heavens and in the earth; and Allah is All Knowing, Wise."},"664":{"id":664,"surah":4,"ayah":171,"verse":"O People given the Book(s)! Do not exaggerate in your religion nor say anything concerning Allah, but the truth; the Messiah, Eisa the son of Maryam, is purely a Noble Messenger of Allah, and His Word; which He sent towards Maryam, and a Spirit from Him; so believe in Allah and His Noble Messengers; and do not say \u201cThree\u201d; desist, for your own good; undoubtedly Allah is the only One God; Purity is to Him from begetting a child; to Him only belongs all whatever is in the heavens and all whatever is in the earth; and Allah is a Sufficient Trustee (of affairs)."},"665":{"id":665,"surah":4,"ayah":172,"verse":"The Messiah does not at all hate being a bondman of Allah, and nor do the close angels; and whoever hates worshipping Him and is conceited \u2013 so very soon He will gather them all towards Him."},"666":{"id":666,"surah":4,"ayah":173,"verse":"Then to those who believed and did good deeds, He will pay their wages in full and by His munificence, give them more; and to those who hated (worshipping Him) and were proud, He will inflict a painful punishment; and they will not find for themselves, other than Allah, any supporter nor any aide."},"667":{"id":667,"surah":4,"ayah":174,"verse":"O mankind! Indeed the clear proof from your Lord has come to you, and We have sent down to you a bright light. (The Holy Prophet is a Clear Proof from Allah.)"},"668":{"id":668,"surah":4,"ayah":175,"verse":"So those who believed in Allah and held fast to His rope \u2013 He will admit them into His mercy and munificence, and will guide them on the Straight Path towards Himself."},"669":{"id":669,"surah":4,"ayah":176,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him), they ask you for a decree; say, \u201cAllah decrees you concerning the solitary person (without parents or children); if a man dies childless and has a sister, for her is half the inheritance; and the man is his sister\u2019s heir if the sister dies childless; and if there are two sisters, for them is two-thirds of the inheritance; so if there are brothers and sisters, both men and women, the male\u2019s share is equal to that of two females; and Allah explains clearly to you, so that you do not go astray; and Allah knows all things.\u201d"},"670":{"id":670,"surah":5,"ayah":1,"verse":"O People who Believe! Fulfil your words (agreements); the mute animals are made lawful for you (to eat) except what will be declared to you after this, but do not assume hunting as lawful when you are on the pilgrimage; indeed Allah commands whatever He wills."},"671":{"id":671,"surah":5,"ayah":2,"verse":"O People who Believe! Do not make lawful the symbols of Allah nor the sacred months nor the sacrificial animals sent to Sacred Territory (around Mecca) nor the animals marked with garlands, nor the lives and wealth of those travelling towards the Sacred House (Kaa\u2019bah) seeking the munificence and pleasure of their Lord; and when you have completed the pilgrimage, you may hunt; and let not the enmity of the people who had stopped you from going to the Sacred Mosque tempt you to do injustice; and help one another in righteousness and piety \u2013 and do not help one another in sin and injustice \u2013 and keep fearing Allah; indeed Allah\u2019s punishment is severe."},"672":{"id":672,"surah":5,"ayah":3,"verse":"Forbidden for you are carrion, and blood, and flesh of swine, and that which has been slaughtered while proclaiming the name of any other than Allah, and one killed by strangling, and one killed with blunt weapons, and one which died by falling, and that which was gored by the horns of some animal, and one eaten by a wild beast, except those whom you slaughter; and (also forbidden is) that which is slaughtered at the altar (of idols) and that which is distributed by the throwing of arrows (as an omen); this is an act of sin; this day, the disbelievers are in despair concerning your religion, so do not fear them and fear Me; this day have I perfected your religion for you* and completed My favour upon you, and have chosen Islam as your religion; so whoever is forced by intense hunger and thirst and does not incline towards sin, then indeed Allah is Oft Forgiving, Most Merciful. (*Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is the Last Prophet.)"},"673":{"id":673,"surah":5,"ayah":4,"verse":"They ask you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) what is made lawful for them; say, \u201cPure things are made lawful for you, and the beasts (and birds) of prey which you have trained and use for hunting, (and) you teach them what Allah has taught you; so eat what they kill and leave for you, and mention Allah\u2019s name upon it \u2013 and keep fearing Allah; indeed Allah is Swift At Taking Account.\u201d"},"674":{"id":674,"surah":5,"ayah":5,"verse":"This day the pure things are made lawful for you; and the food of the People given the Book(s) is lawful for you \u2013 and your food is lawful for them \u2013 and likewise are the virtuous Muslim women and the virtuous women from the people who received the Book(s) before you when you give them their bridal money \u2013 marrying them, not committing adultery nor as mistresses; and whoever turns a disbeliever after being a Muslim, all his deeds are wasted and he will be among the losers in the Hereafter."},"675":{"id":675,"surah":5,"ayah":6,"verse":"O People who Believe! When you wish to stand up for prayer, wash your faces, and your hands up to the elbows, and pass wet palms over your heads and wash your feet up to the ankles; and if you need a bath, clean yourselves thoroughly; and if you are sick or on a journey, or one of you returns from answering the call of nature, or you have cohabited with women, and you do not find water, then cleanse (yourself) with clean soil \u2013 therefore stroke your soiled palms over your faces and your hands with it; Allah does not will to place you in hardship, but He wills to fully purify you and complete His favour upon you, so that you may be grateful."},"676":{"id":676,"surah":5,"ayah":7,"verse":"And remember Allah\u2019s favour upon you and the covenant He took from you when you said, \u201cWe hear and we obey\u201d \u2013 and fear Allah; indeed Allah knows what lies within the hearts."},"677":{"id":677,"surah":5,"ayah":8,"verse":"O People who Believe! Firmly establish the commands of Allah, giving testimony with justice \u2013 and do not let the enmity of anyone tempt you not to do justice; be just; that is nearer to piety \u2013 and fear Allah; indeed Allah is Well Aware of your deeds."},"678":{"id":678,"surah":5,"ayah":9,"verse":"It is Allah\u2019s promise to those who believe and are righteous, that for them will be forgiveness and a great reward."},"679":{"id":679,"surah":5,"ayah":10,"verse":"And those who disbelieve and deny Our signs \u2013 it is they who are the people of hell."},"680":{"id":680,"surah":5,"ayah":11,"verse":"O People who Believe! Remember Allah\u2019s favour upon you, when a people wished to extend their hands against you, so He restrained their hands from you; and keep fearing Allah; and Muslims must rely only upon Allah."},"681":{"id":681,"surah":5,"ayah":12,"verse":"Undoubtedly Allah made a covenant with the Descendants of Israel, and We appointed twelve chiefs among them; and Allah said, \u201cIndeed I am with you; surely, if you establish the prayer and pay the charity, and believe in My Noble Messengers and respect* them, and lend an excellent loan to Allah, I will surely forgive your sins, and I will surely admit you into Gardens beneath which rivers flow; then after this, if any of you disbelieves, he has certainly gone astray from the Straight Path.\" (To honour the Holy Prophet \u2013 peace and blessings be upon him \u2013 is part of faith. To disrespect him is blasphemy.)"},"682":{"id":682,"surah":5,"ayah":13,"verse":"So We cursed them because of them constantly breaking their covenant, and hardened their hearts; they shift the Words of Allah from their places, and have forgotten a large portion of the advices that were given to them; and you will constantly learn of deceits from them, except a few; so forgive them and excuse them; indeed Allah loves the virtuous."},"683":{"id":683,"surah":5,"ayah":14,"verse":"And We made a covenant with those who proclaimed, \u201cWe are Christians\u201d \u2013 they then forgot a large portion of the advices given to them; We have therefore instilled enmity and hatred between them till the Day of Resurrection; and Allah will soon inform them of what they were doing."},"684":{"id":684,"surah":5,"ayah":15,"verse":"O People given the Book(s)! Indeed this Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him) of Ours has come to you, revealing to you a lot of the things which you had hidden in the Book, and forgiving a lot of them; indeed towards you has come a light* from Allah, and a clear Book. (* The Holy Prophet is a light from Allah)."},"685":{"id":685,"surah":5,"ayah":16,"verse":"With it, Allah guides whoever obeys the will of Allah to the paths of peace, and takes them out of darkness towards light by His command, and guides them to the Straight Path."},"686":{"id":686,"surah":5,"ayah":17,"verse":"They have indeed become disbelievers who say, \u201cMessiah, the son of Maryam is certainly Allah\u201d; say, \u201cWho can then do anything against Allah, if He wills to destroy the Messiah, the son of Maryam, and his mother and everyone on earth?\u201d And for Allah only is the kingship of the heavens and the earth and all that is between them; He creates whatever He wills; and Allah is Able to do all things."},"687":{"id":687,"surah":5,"ayah":18,"verse":"The Jews and the Christians said, \u201cWe are the sons of Allah and His beloved ones\u201d; say, \u201cWhy does He then punish you for your sins? In fact you are human beings, part of His creation; He forgives whomever He wills, and punishes whomever He wills; and for Allah only is the kingship of the heavens and the earth and all that is between them \u2013 and towards Him is the return.\u201d"},"688":{"id":688,"surah":5,"ayah":19,"verse":"O People given the Book(s)! Indeed this Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him) of Ours has come to you, revealing to you Our commands, after the Noble Messengers had stopped arriving for ages, for you might claim, \u201cNever did any Harbinger of Glad Tidings or Herald of Warning come to us\u201d \u2013 so this Harbinger of Glad Tidings and Herald of Warning has come to you; and Allah is Able to do all things."},"689":{"id":689,"surah":5,"ayah":20,"verse":"And when Moosa said to his people, \u201cO my people! Remember Allah\u2019s favour upon you, that He created Prophets among you, and made you kings, and has now given you what He has not given to any one else in this world.\u201d"},"690":{"id":690,"surah":5,"ayah":21,"verse":"\u201cO my people! Enter the holy land which Allah has decreed for you and do not turn back, for you will turn back as losers.\u201d"},"691":{"id":691,"surah":5,"ayah":22,"verse":"They said, \u201cO Moosa! In it lives a very strong nation \u2013 and we shall never enter it, until they have gone away from there; so when they have gone away from there, we will enter it.\u201d"},"692":{"id":692,"surah":5,"ayah":23,"verse":"So two men who were among those who feared Allah and whom Allah had favoured said, \u201cEnter upon them by force, through the gate; if you enter the gate, victory will be yours; and depend only upon Allah, if you have faith.\u201d"},"693":{"id":693,"surah":5,"ayah":24,"verse":"They said, \u201cO Moosa! We will never enter it whilst they are there, so you and your Lord go and fight \u2013 we shall remain seated here.\u201d"},"694":{"id":694,"surah":5,"ayah":25,"verse":"Said Moosa, \u201cMy Lord! I have no control except over myself and my brother, so keep us separated from the disobedient nation.\u201d (Do not count us among them)."},"695":{"id":695,"surah":5,"ayah":26,"verse":"Said Allah, \u201cThe (holy) land is therefore forbidden for them for forty years; they will wander in the earth; so do not grieve for these disobedient people.\u201d"},"696":{"id":696,"surah":5,"ayah":27,"verse":"And recite to them the true tale of the two sons of Adam; when both of them offered a sacrifice each \u2013 hence the sacrifice of one was accepted and not accepted from the other; he (the other) said, \u201cI swear I will kill you\u201d; he answered, \u201cAllah accepts only from the pious.\u201d"},"697":{"id":697,"surah":5,"ayah":28,"verse":"\u201cIndeed, if you do extend your hand against me to kill me, I will not extend my hand against you to kill you; I fear Allah, the Lord Of The Creation.\u201d"},"698":{"id":698,"surah":5,"ayah":29,"verse":"\u201cI only desire that you alone should bear my sin and your own sin \u2013 hence you become of the people of hell; and that is the proper punishment of the unjust.\u201d"},"699":{"id":699,"surah":5,"ayah":30,"verse":"So his soul incited him to kill his brother \u2013 he therefore killed him, and was therefore ruined."},"700":{"id":700,"surah":5,"ayah":31,"verse":"So Allah sent a crow scratching the ground, to show him how to hide his brother\u2019s corpse; he said, \u201cWoe to me! I was not even capable enough to be like this crow, so I would hide my brother\u2019s corpse\u201d; and he turned remorseful."},"701":{"id":701,"surah":5,"ayah":32,"verse":"For this reason; We decreed for the Descendants of Israel that whoever kills a human being except in lieu of killing or causing turmoil in the earth, so it shall be as if he had killed all mankind; and whoever saves the life of one person, is as if he had saved the life of all mankind; and undoubtedly Our Noble Messengers came to them with clear proofs \u2013 then after this indeed many of them are oppressors in the earth."},"702":{"id":702,"surah":5,"ayah":33,"verse":"The only reward of those who make war upon Allah and His Noble Messenger and cause turmoil in the land is that they be all be killed or crucified, or their hands and feet cut off from alternate sides, or they be banished far away from the land; this is their degradation in the world, and for them in the Hereafter is a great punishment."},"703":{"id":703,"surah":5,"ayah":34,"verse":"Except those who repent before you apprehend them; so know well that Allah is Oft Forgiving, Most Merciful."},"704":{"id":704,"surah":5,"ayah":35,"verse":"O People who Believe! Fear Allah, and seek the means towards Him, and strive in His cause, in the hope of attaining success."},"705":{"id":705,"surah":5,"ayah":36,"verse":"Indeed if the disbelievers owned all that is in the earth and a similar one in addition to it, and they offered it as a ransom to save themselves from the punishment of the Day of Resurrection, it would not be accepted from them; and for them is a painful punishment."},"706":{"id":706,"surah":5,"ayah":37,"verse":"They will wish to come out of hell, and will not come out of it \u2013 and for them is an unending punishment."},"707":{"id":707,"surah":5,"ayah":38,"verse":"And cut off the hands of those men or women who are thieves \u2013 a recompense of their deeds, a punishment from Allah; and Allah is Almighty, Wise."},"708":{"id":708,"surah":5,"ayah":39,"verse":"So if one repents after his wrongdoing and reforms himself, Allah will incline towards him with His mercy; indeed Allah is Oft Forgiving, Most Merciful."},"709":{"id":709,"surah":5,"ayah":40,"verse":"Do you not know that to Allah only belongs the kingship of the heavens and the earth? He punishes whomever He wills, and forgives whomever He wills; and Allah is Able to do all things."},"710":{"id":710,"surah":5,"ayah":41,"verse":"O Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him)! Do not let yourself be aggrieved by the people who rush towards disbelief \u2013 those who say with their mouths, \u201cWe believe\u201d but whose hearts are not Muslims; and some Jews; they listen a great deal to falsehood, and to other people who do not come to you; shifting Allah\u2019s Words from their correct places; and say, \u201cIf this command is given to you, obey it, but if this is not given to you, then refrain\u201d; and the one whom Allah wills to send astray, you will never be able to help him in the least against Allah; they are those whose hearts Allah did not will to cleanse; for them is disgrace in this world, and for them is a great punishment in the Hereafter."},"711":{"id":711,"surah":5,"ayah":42,"verse":"The excessively eager listeners of falsehood, extreme devourers of the forbidden; so if they come humbly to you (Prophet Mohammed \u2013 peace and blessings be upon him), judge between them or shun them; and if you turn away from them they cannot harm you at all; and if you do judge between them, judge with fairness; indeed Allah loves the equitable."},"712":{"id":712,"surah":5,"ayah":43,"verse":"And why should they wish to be judged by you, when they have the Taurat with them, in which exist the commands of Allah and yet they turn away from it? And they will not accept faith."},"713":{"id":713,"surah":5,"ayah":44,"verse":"Undoubtedly We sent down the Taurat, in which is guidance and light; the Jews were commanded according to it by Our obedient Prophets, and the rabbis and the religious jurists for they were commanded to protect Allah\u2019s Book, and were witnesses to it; so do not fear men, and fear Me, and do not exchange My verses for an abject price; and whoever does not judge according to what is sent down by Allah \u2013 it is they who are disbelievers."},"714":{"id":714,"surah":5,"ayah":45,"verse":"And in the Taurat, We made it obligatory upon them that, \u201cA life (is the retribution) for a life, and an eye for an eye, and a nose for a nose, and an ear for an ear, and a tooth for a tooth, and for wounds is a retribution\u201d; then whoever willingly agrees to retribution, it shall redeem him of his sin; and whoever does not judge according to what is sent down by Allah \u2013 it is they who are the unjust."},"715":{"id":715,"surah":5,"ayah":46,"verse":"And We brought Eisa the son of Maryam, following the footsteps of those Prophets, confirming the Taurat which preceded him \u2013 and We bestowed upon him the Injeel (Bible) in which is guidance and light, and confirms the Taurat which preceded it, and a guidance and an advice to the pious."},"716":{"id":716,"surah":5,"ayah":47,"verse":"And the People of Injeel must judge by what Allah has sent down in it; and whoever does not judge according to what is sent down by Allah \u2013 it is they who are the sinners."},"717":{"id":717,"surah":5,"ayah":48,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) We have sent down the true Book upon you, confirming the Books preceding it, and a protector and witness over them \u2013 therefore judge between them according to what is sent down by Allah, and O listener, do not follow their desires, abandoning the truth which has come to you; We have appointed for you all, a separate (religious) law and a way; and had Allah willed He could have made you one nation, but the purpose (His will) is to test you by what He has given you, therefore seek to surpass one another in good deeds; towards Allah only you will all return, so He will inform you concerning the matter in which you disputed."},"718":{"id":718,"surah":5,"ayah":49,"verse":"And therefore, O Muslims, judge between them according to what is sent down by Allah, and do not follow their desires, and be cautious of them so that they may not divert you from some commands which have been sent down to you; then if they turn away, know that Allah\u2019s will is to punish them for some of their sins; and indeed many men are disobedient."},"719":{"id":719,"surah":5,"ayah":50,"verse":"So do they wish a judgement of ignorance? And whose judgement is better than that of Allah, for the people who are certain?"},"720":{"id":720,"surah":5,"ayah":51,"verse":"O People who Believe! Do not make the Jews and the Christians your friends; they are friends of one another; and whoever among you befriends them, is one of them; indeed Allah does not guide the unjust."},"721":{"id":721,"surah":5,"ayah":52,"verse":"You will now see those in whose hearts is a disease, that they rush towards the Jews and the Christians, saying, \u201cWe fear that a misfortune will possibly befall us\u201d; so it is likely that Allah may soon bring victory, or a command from Himself, so they will remain regretting what they had hidden in their hearts."},"722":{"id":722,"surah":5,"ayah":53,"verse":"And the believers say, \u201cAre these the ones who swore by Allah most vehemently in their oaths, that they are with you?\u201d All their deeds are destroyed, so they are ruined."},"723":{"id":723,"surah":5,"ayah":54,"verse":"O People who Believe! Whoever among you reneges from his religion, so Allah will soon bring a people who are His beloved ones and Allah is their beloved, lenient with the Muslims and stern towards disbelievers \u2013 they will strive in Allah\u2019s cause, and not fear the criticism of any accuser; this is Allah\u2019s munificence, He may give to whomever He wills; and Allah is the Most Capable, the All Knowing."},"724":{"id":724,"surah":5,"ayah":55,"verse":"You do not have any friends except Allah and His Noble Messenger and the believers who establish the prayer and pay the charity, and are bowed down before Allah."},"725":{"id":725,"surah":5,"ayah":56,"verse":"And whoever takes Allah and His Noble Messenger and the Muslims as friends \u2013 so undoubtedly only the party of Allah is victorious."},"726":{"id":726,"surah":5,"ayah":57,"verse":"O People who Believe! Those who have made your religion a mockery and a sport, and those who received the Book before you, and the disbelievers \u2013 do not befriend any of them; and keep fearing Allah, if you have faith."},"727":{"id":727,"surah":5,"ayah":58,"verse":"And when you call to prayer, they mock and make fun of it; this is because they are people without any sense."},"728":{"id":728,"surah":5,"ayah":59,"verse":"Say, \u201cO People given the Book(s)! What do you dislike in us \u2013 except that we believe in Allah and what is sent down to us and what was sent down before, and because most of you are disobedient?\u201d"},"729":{"id":729,"surah":5,"ayah":60,"verse":"Say, \u201cShall I tell you of those who are in a worse position than this, in Allah\u2019s sight? It is those whom Allah has cursed and has wreaked His wrath upon and turned some of them into apes and swine, and worshippers of the devil; theirs is a worse destination and they have wandered further astray from the Straight Path.\u201d"},"730":{"id":730,"surah":5,"ayah":61,"verse":"And when they come to you, they say, \u201cWe are Muslims\u201d whereas they were disbelievers when they came in, and disbelievers when they went out; and Allah knows very well, what they hide."},"731":{"id":731,"surah":5,"ayah":62,"verse":"And you will see many of them rushing towards sin and oppression, and to devour the forbidden; undoubtedly what they do is extremely evil."},"732":{"id":732,"surah":5,"ayah":63,"verse":"Why do not their priests and monks forbid them from speaking evil and devouring the forbidden? Undoubtedly what they do is extremely evil."},"733":{"id":733,"surah":5,"ayah":64,"verse":"And the Jews said, \u201cAllah\u2019s hand is tied\"; may their hands be tied \u2013 and they are accursed for saying so! In fact, both His hands* are free, He bestows upon whomever He wills; and O dear Prophet, this Book which has been sent down upon you from your Lord will cause many of them to advance in their rebellion and disbelief; and We have instilled enmity and hatred between them till the Day of Resurrection; whenever they ignite the flame of war, Allah extinguishes it, and they strive to create chaos in the land; and Allah does not love the mischievous. (* This is a metaphor used to express Allah\u2019s power)."},"734":{"id":734,"surah":5,"ayah":65,"verse":"If the People given the Book(s) had accepted faith and been pious, We would have certainly redeemed them of their sins and would have certainly taken them into serene Gardens."},"735":{"id":735,"surah":5,"ayah":66,"verse":"And had they kept the Taurat and the Injeel established, and what was sent down towards them from their Lord, they would have received sustenance from above and from beneath their feet; among them is a group who is fair; but most of them commit extremely evil deeds."},"736":{"id":736,"surah":5,"ayah":67,"verse":"O Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him)! Convey all what has been sent down upon you from your Lord; and if you do not, then you have not conveyed any of His messages; and Allah will protect you from the people; indeed Allah does not guide the disbelievers."},"737":{"id":737,"surah":5,"ayah":68,"verse":"Say, \u201cO People given the Book(s)! You are nothing until you establish the Taurat and the Injeel, and what was sent down towards you from your Lord\u201d; and this Book which has been sent down upon you from your Lord will cause many of them to advance in their rebellion and disbelief; so do not at all grieve for the disbelievers."},"738":{"id":738,"surah":5,"ayah":69,"verse":"Indeed those who call themselves Muslims \u2013 and similarly among the Jews, and the Sabeans, and the Christians* \u2013 whoever sincerely accepts faith in Allah and the Last Day, and does good deeds \u2013 so there shall be no fear upon them nor shall they grieve. (Whoever among them converts to Islam)."},"739":{"id":739,"surah":5,"ayah":70,"verse":"We made a covenant with the Descendants of Israel and sent Noble Messengers towards them; whenever a Noble Messenger came to them with whatever was not according to their own desires, they denied some of them and some they slew."},"740":{"id":740,"surah":5,"ayah":71,"verse":"And they assumed that there will be no punishment, so they turned blind and deaf \u2013 then Allah accepted their penance, then again many of them turned blind and deaf; and Allah is seeing their deeds."},"741":{"id":741,"surah":5,"ayah":72,"verse":"They are certainly disbelievers who say, \u201cAllah is actually the Messiah, the son of Maryam\u201d; whereas the Messiah had said, \u201cO Descendants of Israel, worship Allah Who is my Lord and (also) your Lord\u201d; undoubtedly whoever ascribes partners with Allah, then Allah has forbidden Paradise for him; his destination is hell; and the unjust do not have any supporters."},"742":{"id":742,"surah":5,"ayah":73,"verse":"They are certainly disbelievers who say, \u201cIndeed Allah is the third of the three Gods\u201d; whereas there is no God except the One God; and if they do not desist from their speech, undoubtedly a painful punishment will reach those among them who die as disbelievers."},"743":{"id":743,"surah":5,"ayah":74,"verse":"So why do they not incline towards Allah and seek His forgiveness? And Allah is Oft Forgiving, Most Merciful."},"744":{"id":744,"surah":5,"ayah":75,"verse":"The Messiah, the son of Maryam, is purely a Noble Messenger; many Noble Messengers have passed before him; and his mother is a truthful woman; they both used to eat food; see how We make the signs clear for them, and see how they turn away!"},"745":{"id":745,"surah":5,"ayah":76,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhat! You worship, other than Allah, that which is neither the controller of your losses nor of your benefits? And Allah only is the All Hearing, the All Knowing.\u201d"},"746":{"id":746,"surah":5,"ayah":77,"verse":"Say, \u201cO People given the Book(s)! Do not wrongfully commit injustice in your religion, and do not follow the people who earlier went astray, and led many others astray, and wandered away from the Straight Path.\u201d"},"747":{"id":747,"surah":5,"ayah":78,"verse":"Those among the Descendants of Israel who turned disbelievers were cursed by the tongue of Dawud, and of Eisa the son of Maryam; it was because of their disobedience and their rebellion."},"748":{"id":748,"surah":5,"ayah":79,"verse":"They did not restrain one another from the evil they used to do; undoubtedly they used to commit extremely evil deeds."},"749":{"id":749,"surah":5,"ayah":80,"verse":"You will see that many of them make friends with the disbelievers; what an evil thing they had sent ahead for themselves, for Allah\u2019s wrath came upon them and they will remain in the punishment, forever."},"750":{"id":750,"surah":5,"ayah":81,"verse":"Had they believed in Allah and this Prophet (Mohammed \u2013 peace and blessings be upon him) and what is sent down upon him, they would not befriend the disbelievers, but most of them are disobedient."},"751":{"id":751,"surah":5,"ayah":82,"verse":"You will certainly find the Jews and the polytheists as the greatest enemies of the Muslims; and you will find the closest in friendship to the Muslims those who said, \u201cWe are Christians\u201d; that is because scholars and monks are among them, and they are not proud."},"752":{"id":752,"surah":5,"ayah":83,"verse":"And when they listen to what has been sent down to the Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him), you observe their eyes overflowing with tears because they have recognised the truth; they say, \u201cOur Lord, we have accepted faith \u2013 therefore record us among the witnesses of the truth.\u201d"},"753":{"id":753,"surah":5,"ayah":84,"verse":"\u201cAnd what is the matter with us, that we should not believe in Allah and this truth which has come to us? And we hope that our Lord will admit us along with the righteous.\u201d"},"754":{"id":754,"surah":5,"ayah":85,"verse":"So because of their saying, Allah bestowed them with Gardens beneath which rivers flow, in which they will abide forever; and this is the reward of the virtuous."},"755":{"id":755,"surah":5,"ayah":86,"verse":"And those who disbelieved and denied Our signs, are the people of hell."},"756":{"id":756,"surah":5,"ayah":87,"verse":"O People who Believe! Do not forbid the pure things, which Allah has made lawful for you, and do not cross the limits; indeed Allah dislikes the transgressors."},"757":{"id":757,"surah":5,"ayah":88,"verse":"Eat of the sustenance which Allah has given you, the lawful and the pure \u2013 and fear Allah in Whom you believe."},"758":{"id":758,"surah":5,"ayah":89,"verse":"Allah does not take you to task for oaths which are made unintentionally but He does take you to task for oaths which you ratify; so the redemption of such oaths is to provide food to ten needy persons equal to the average of what you feed your family, or to clothe them, or to free one slave; and for one who has no means, is the fasting for three days; this is the redemption of your oaths when you have sworn; and fulfil your oaths; this is how Allah explains His verses to you, so that you may be thankful."},"759":{"id":759,"surah":5,"ayah":90,"verse":"O People who Believe! Wine (all intoxicants), and gambling, and idols, and the darts are impure \u2013 the works of Satan, therefore keep avoiding them so that you may succeed."},"760":{"id":760,"surah":5,"ayah":91,"verse":"The devil only seeks to instil hatred and enmity between you with wine and gambling, and to prevent you from the remembrance of Allah and from prayer; so have you desisted?"},"761":{"id":761,"surah":5,"ayah":92,"verse":"And obey Allah and obey the Noble Messenger, and be cautious; then if you turn away, so know that the duty of Our Noble Messenger is only to plainly convey the message."},"762":{"id":762,"surah":5,"ayah":93,"verse":"Upon those who accepted faith and did good deeds, there shall be no sin for whatever they have consumed in the past, provided they fear and continue to believe and do good deeds, then again fear and continue to believe, and then again fear and remain virtuous; and Allah loves the virtuous."},"763":{"id":763,"surah":5,"ayah":94,"verse":"O People who Believe! Allah will surely test you with some prey that is within reach of your hands and your spears, so that Allah may make known those who fear Him without seeing; so henceforth, a painful punishment is for anyone who transgresses."},"764":{"id":764,"surah":5,"ayah":95,"verse":"O People who Believe! Do not kill prey while you are on the pilgrimage; and whoever among you kills it intentionally, so its recompense is that he shall give a similar domestic animal (for sacrifice), two honest men among you rendering the command, the sacrifice being brought to the Kaa\u2019bah \u2013 or he gives as redemption, food for some needy persons, or fasts for the same number of days, so that he may taste the consequences of his deed; Allah has forgiven what has passed; and henceforth whoever does it, Allah will take recompense from him; and Allah is Almighty, Avenger."},"765":{"id":765,"surah":5,"ayah":96,"verse":"It is lawful for you to hunt from the sea and to eat from it, for your benefit and that of the travellers; and hunting on land is forbidden for you while you are on the pilgrimage; and fear Allah, towards Whom you will arise."},"766":{"id":766,"surah":5,"ayah":97,"verse":"Allah has made the Kaa\u2019bah, the respected house, a cause for peoples survival, and the Sacred Month, and the sacrifices in the holy land, and the garlanded animals; this is so that you may be convinced that Allah knows all whatever is in the heavens and all whatever is in the earth, and that Allah is the All Knowing."},"767":{"id":767,"surah":5,"ayah":98,"verse":"Know well that Allah\u2019s punishment is severe, and that Allah is Oft Forgiving, Most Merciful."},"768":{"id":768,"surah":5,"ayah":99,"verse":"There is no duty upon the Noble Messenger except to convey the command; and Allah knows all what you disclose and all what you hide."},"769":{"id":769,"surah":5,"ayah":100,"verse":"Say, \u201cThe impure (evil) and the pure (good) are not equal, even though the abundance of the impure may attract you; therefore keep fearing Allah, O men of intellect, so that you may succeed.\u201d"},"770":{"id":770,"surah":5,"ayah":101,"verse":"O People who Believe! Do not ask about matters which, if disclosed to you, would be disliked by you; and if you ask about them while the Qur\u2019an is being sent down, they will be disclosed to you; Allah has forgiven these; and Allah is Oft Forgiving, Most Forbearing."},"771":{"id":771,"surah":5,"ayah":102,"verse":"A nation before you asked about such matters and then disbelieved in them."},"772":{"id":772,"surah":5,"ayah":103,"verse":"Allah has not appointed (commanded to sacrifice) the camel with ears sliced (Bahira) or the she-camel (Saibah) or the she-goat (Wasilah) or the breeding camel (Hami), but the disbelievers fabricate lies against Allah; and most of them do not have any sense at all."},"773":{"id":773,"surah":5,"ayah":104,"verse":"And when it is said to them, \u201cCome towards what Allah has sent down and towards the Noble Messenger\u201d, they say, \u201cSufficient for us is what we found our forefathers upon\u201d; even if their forefathers did not have knowledge nor had guidance?"},"774":{"id":774,"surah":5,"ayah":105,"verse":"O People who Believe! Fear for your own souls; he who has strayed cannot harm you in the least if you are on guidance; towards Allah only you will all return \u2013 He will then inform you of what you used to do."},"775":{"id":775,"surah":5,"ayah":106,"verse":"O People who Believe! The witnesses between you when death approaches any one of you, at the time of making a will, should be two reliable men from among you, or two from another tribe in case you are travelling in the land and the event of death approaches you; engage them after the prayers, and if you doubt, they must swear by Allah that, \u201cWe shall not exchange our oaths for any price even if he is a near relative, nor will we hide the testimony of Allah \u2013 if we do, then surely we are of the sinners.\u201d"},"776":{"id":776,"surah":5,"ayah":107,"verse":"Then if it is later known that both of them committed sin, two others may take their place, from those who were caused the most harm by the false testimony, and they must swear by Allah that, \u201cOur testimony is more accurate than the testimony of these two, and we have not exceeded the limits \u2013 if we do, then surely we shall be of the unjust.\u201d"},"777":{"id":777,"surah":5,"ayah":108,"verse":"This is more suitable than their bearing testimony as they wish or fear that some oaths may be made void, after their taking oath; so fear Allah and heed the commands; and Allah does not guide the disobedient."},"778":{"id":778,"surah":5,"ayah":109,"verse":"On the day when Allah will gather all the Noble Messengers, and then say, \u201cWhat response did you get?\u201d They will submit, \u201cWe do not have any knowledge; indeed it is only You, Who knows all the hidden.\u201d"},"779":{"id":779,"surah":5,"ayah":110,"verse":"When Allah will say, \u201cO Eisa, the son of Maryam! Remember My favour upon you and your mother; when I supported you with the Holy Spirit; you were speaking to people from the cradle and in maturity; and when I taught you the Book and wisdom and the Taurat and the Injeel; and when you used to mould a birdlike sculpture from clay, by My command, and blow into it \u2013 so it (the living bird) used to fly by My command, and you used to cure him who was born blind and cure the leper, by My command; and when you used to raise up the dead, by My command; and when I restrained the Descendants of Israel against you when you came to them with clear proofs, and the disbelievers among them said, \u2018This is nothing but clear magic\u2019.\u201d"},"780":{"id":780,"surah":5,"ayah":111,"verse":"\u201cAnd when I inspired into the hearts of the disciples that, \u2018Believe in Me and in My Noble Messenger\u2019; they said, \u2018We accept faith, and be witness that we are Muslims.\u2019\u201d"},"781":{"id":781,"surah":5,"ayah":112,"verse":"When the disciples said, \u201cO Eisa, the son of Maryam! Will your Lord send down a table spread (with food) for us from heaven?\u201d He said, \u201cFear Allah, if you are believers.\u201d"},"782":{"id":782,"surah":5,"ayah":113,"verse":"They said, \u201cWe wish to eat from it and so that our hearts be convinced and to see that you have spoken the truth to us, and that we may be witnesses upon it.\u201d"},"783":{"id":783,"surah":5,"ayah":114,"verse":"Eisa, the son of Maryam, said, \u201cO Allah, O our Lord! Send down to us a table spread from heaven, so that it may become a day of celebration for us \u2013 for our former and latter people \u2013 and a sign from You; and give us sustenance \u2013 and You are the Best Provider Of Sustenance.\u201d"},"784":{"id":784,"surah":5,"ayah":115,"verse":"Said Allah, \u201cIndeed I shall send it down to you; so thereafter whoever disbelieves amongst you \u2013 I will surely mete out to him a punishment with which I shall not punish anyone else in the whole world.\u201d"},"785":{"id":785,"surah":5,"ayah":116,"verse":"And when Allah will say, \u201cO Eisa, the son of Maryam! Did you say to the people, \u2018Appoint me and my mother as two Gods, besides Allah\u2019?\u201d He will submit, \u201cPurity is to You! It is not proper for me to say something for which I do not have a right; if I have said it, then surely You know it; You know what lies in my heart, and I do not know what is in Your knowledge; indeed You only know all the hidden.\u201d"},"786":{"id":786,"surah":5,"ayah":117,"verse":"\u201cI have not told them except what You commanded me, that \u2018Worship Allah, Who is my Lord and is also your Lord\u2019; I was aware of them till I was among them; and when You raised me, only You watched over them; and all things are present before You.\u201d"},"787":{"id":787,"surah":5,"ayah":118,"verse":"\u201cIf you punish them, then indeed they are Your slaves; and if you forgive them, then indeed You only are the Almighty, the Wise.\u201d"},"788":{"id":788,"surah":5,"ayah":119,"verse":"Proclaimed Allah, \u201cThis is a day on which the truthful will benefit from their truthfulness; for them are Gardens beneath which rivers flow, in which they will abide for ever and ever; Allah is pleased with them and they are pleased with Allah; this is the greatest success.\u201d"},"789":{"id":789,"surah":5,"ayah":120,"verse":"To Allah only belongs the kingship of the heavens and the earth and all that is in them: and He is Able to do all things."},"790":{"id":790,"surah":6,"ayah":1,"verse":"All praise is to Allah, Who has created the heavens and the earth, and has created darkness and light; yet the disbelievers appoint equals (false deities) to their Lord!"},"791":{"id":791,"surah":6,"ayah":2,"verse":"It is He Who has created you from clay, and then decreed a term for you; and it is a fixed promise before Him, yet you still doubt!"},"792":{"id":792,"surah":6,"ayah":3,"verse":"And He is Allah (the God) of the heavens and in the earth; He knows all, your secrets and your disclosures, and He knows your deeds."},"793":{"id":793,"surah":6,"ayah":4,"verse":"And never does a sign come to them from among the signs of Allah, except that they* turn away from it. (* The disbelievers)"},"794":{"id":794,"surah":6,"ayah":5,"verse":"So indeed they denied the truth* when it came to them; so now the tidings of the thing they used to mock at, will come to them. (Prophet Mohammed -peace and blessings be upon him, or the Holy Qur\u2019an)."},"795":{"id":795,"surah":6,"ayah":6,"verse":"Do they not see how many civilisations We destroyed before them, whom We had established more firmly in the earth than We have established you, and We sent on them abundant rain from the sky, and made the rivers flow beneath them? So we destroyed them because of their sins, and created another civilisation after them."},"796":{"id":796,"surah":6,"ayah":7,"verse":"And had We sent down to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) something written on paper so that they could feel it with their hands, even then the disbelievers would have said, \u201cThis is nothing but clear magic.\u201d"},"797":{"id":797,"surah":6,"ayah":8,"verse":"And they said, \u201cWhy has not an angel been sent down to him?\u201d And had We sent down an angel, then the matter would have been finished, and they would not get any respite."},"798":{"id":798,"surah":6,"ayah":9,"verse":"And had we appointed an angel as a Prophet, We would still have made him as a man and would keep them in the same doubt, as they are now in."},"799":{"id":799,"surah":6,"ayah":10,"verse":"And certainly, O dear Prophet (Mohammed \u2013 peace and blessings be upon him) the Noble Messengers before you have also been mocked at, so those who laughed at them were themselves ruined by their own mocking."},"800":{"id":800,"surah":6,"ayah":11,"verse":"Proclaim, \u201cTravel in the land, and see what sort of fate befell those who denied.\u201d"},"801":{"id":801,"surah":6,"ayah":12,"verse":"Say, \u201cTo Whom does all whatever is in the heavens and the earth, belong?\u201d Proclaim, \u201cTo Allah\u201d; He has made mercy obligatory upon His grace; undoubtedly, He will surely gather you all together on the Day of Resurrection in which there is no doubt; those who put their souls to ruin, do not accept faith."},"802":{"id":802,"surah":6,"ayah":13,"verse":"And to Him only belongs all whatever exists in the night and in the day; and He only is the All Hearing, the All Knowing."},"803":{"id":803,"surah":6,"ayah":14,"verse":"Say, \u201cShall I choose as a supporter someone other than Allah, Who is the Originator of the heavens and the earth and Who feeds and does not need to eat?\u201d Say, \u201cI have been ordered to be the first to submit myself (to Him), and O people, do not be of the polytheists.\u201d"},"804":{"id":804,"surah":6,"ayah":15,"verse":"Say, \u201cIf I disobey my Lord, I then fear the punishment of the Great Day (of Resurrection).\u201d"},"805":{"id":805,"surah":6,"ayah":16,"verse":"Indeed Allah\u2019s mercy has been upon him, from whom the punishment has been averted on that Day; and this is the clear success."},"806":{"id":806,"surah":6,"ayah":17,"verse":"And if Allah afflicts you with some misfortune, then there is none who can remove it, except Him; and if He sends you some good fortune, then (know that) He is Able to do all things."},"807":{"id":807,"surah":6,"ayah":18,"verse":"He is the Omnipotent over His bondmen; and He is the Wise, the Aware."},"808":{"id":808,"surah":6,"ayah":19,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhose testimony is the greatest?\u201d Say, \u201cAllah is the Witness between me and you; and this Qur\u2019an has been sent down upon me, so I may warn you with it and whomever it may reach; so do you bear witness that there are other Gods along with Allah?\u201d Say, \u201cI do not bear witness to it\u201d; Say, \u201cHe is the only One God and I do not have any relation with whatever you ascribe as partners (to Him).\u201d"},"809":{"id":809,"surah":6,"ayah":20,"verse":"The people to whom We gave the Book(s) recognise this Prophet (Mohammed \u2013 peace and blessings be upon him) as they recognise their own sons; those who put their own souls to ruin, do not accept faith."},"810":{"id":810,"surah":6,"ayah":21,"verse":"And who is more unjust than one who fabricates lies against Allah or denies His signs? Undoubtedly, the unjust will never succeed."},"811":{"id":811,"surah":6,"ayah":22,"verse":"And on the day when We will resurrect everyone together, then say to the polytheists, \u201cWhere are those partners (your false deities) whom you professed?\u201d"},"812":{"id":812,"surah":6,"ayah":23,"verse":"Then they had no fabrication except that they said, \u201cBy Allah, our Lord, we were never polytheists!\u201d"},"813":{"id":813,"surah":6,"ayah":24,"verse":"Observe how they lied against themselves, and how they lost the things they fabricated!"},"814":{"id":814,"surah":6,"ayah":25,"verse":"And among them is one who listens to you; and We have put covers upon their hearts so they may not understand it, and deafness in their ears; and (even) if they see all the signs, they will not believe in them; to the extent that when they come to you to debate with you, the disbelievers say, \u201cThis is nothing but stories of former people.\u201d"},"815":{"id":815,"surah":6,"ayah":26,"verse":"And they stop (others) from it and run away from it; and they ruin none except themselves, and they do not have sense."},"816":{"id":816,"surah":6,"ayah":27,"verse":"And if you see them when they will be placed above hell, they will say, \u201cAlas, if only we might be sent back and not deny the signs of our Lord, and become Muslims!\u201d"},"817":{"id":817,"surah":6,"ayah":28,"verse":"In fact it has become clear to them what they used to hide before; and were they to be sent back, they would commit the same which they were forbidden to, and undoubtedly they are liars."},"818":{"id":818,"surah":6,"ayah":29,"verse":"And they said, \u201cThere is no other life except our life of this world, and we are not to be raised.\u201d"},"819":{"id":819,"surah":6,"ayah":30,"verse":"And if you see when they are placed before their Lord, He will say, \u201cIs this not real (the truth)? They will say, \u201cYes \u2013 why not, by our Lord!\u201d He will say, \u201cSo now taste the punishment \u2013 the recompense of your disbelief.\u201d"},"820":{"id":820,"surah":6,"ayah":31,"verse":"Those who denied their meeting with Allah, have indeed failed; to the extent that when the Last Day suddenly came upon them they said, \u201cWoe to us that we failed to believe in it\u201d \u2013 and they carry their burdens on their backs; what an evil burden they carry!"},"821":{"id":821,"surah":6,"ayah":32,"verse":"The life of this world is nothing except a pastime and sport; and undoubtedly the abode of the Hereafter is better for the pious; so do you not have sense?"},"822":{"id":822,"surah":6,"ayah":33,"verse":"We know well that their statement grieves you \u2013 so they do not deny you (Prophet Mohammed \u2013 peace and blessings be upon him) but in fact the unjust deny the signs of Allah."},"823":{"id":823,"surah":6,"ayah":34,"verse":"And indeed Noble Messengers have been denied before you, so they patiently bore the denial and torture till Our help reached them; and there is none to alter the decisions of Allah; and the news of the Noble Messengers have already reached you."},"824":{"id":824,"surah":6,"ayah":35,"verse":"And if their turning away has grieved you, then if you can, seek a tunnel into the earth or a ladder into the sky to bring a sign for them; and if Allah willed, He could have brought them all together upon guidance, so O listener (followers of this Prophet) do not ever be of the ignorant."},"825":{"id":825,"surah":6,"ayah":36,"verse":"It is only those who hear (with sincere hearts) that accept faith; and Allah will raise these people dead of heart, then towards Him they will be herded."},"826":{"id":826,"surah":6,"ayah":37,"verse":"And they said, \u201cWhy has no sign been sent down upon him from his Lord?\u201d Say, \u201cIndeed Allah is Able to send down a sign\u201d, but most of them are totally ignorant."},"827":{"id":827,"surah":6,"ayah":38,"verse":"And there is not an animal moving in the earth nor a bird flying on its wings, but they are a nation like you; We have left out nothing in this Book \u2013 then towards their Lord they will be raised."},"828":{"id":828,"surah":6,"ayah":39,"verse":"And those who deny Our signs are deaf and dumb in realms of darkness; Allah may send astray whomever He wills; and may place on the Straight Path whomever He wills."},"829":{"id":829,"surah":6,"ayah":40,"verse":"Say, \u201cWhat is your opinion \u2013 if the punishment of Allah comes upon you or the Hour arrives, will you call upon anyone (deity) besides Allah; if you are truthful?\u201d"},"830":{"id":830,"surah":6,"ayah":41,"verse":"\u201cIn fact you will only call upon Him, and if He wills, He may remove that because of which you prayed to Him, and you will forget the partners (you ascribe to Him).\u201d"},"831":{"id":831,"surah":6,"ayah":42,"verse":"And We have indeed sent Noble Messengers towards the nations that were before you \u2013 We therefore seized them with hardship and adversity so that, in some way, they may humbly plead."},"832":{"id":832,"surah":6,"ayah":43,"verse":"So why did they not humbly plead when Our punishment came to them? But their hearts were hardened and the devil made all their deeds appear good to them!"},"833":{"id":833,"surah":6,"ayah":44,"verse":"So when they forgot the advices made to them, We opened the gates of all things for them; to the extent that when they were rejoicing for what they had received, We seized them suddenly, hence they remained dejected."},"834":{"id":834,"surah":6,"ayah":45,"verse":"The origins of the unjust people were therefore cut off; and all praise is to Allah, Lord Of The Creation."},"835":{"id":835,"surah":6,"ayah":46,"verse":"Say, \u201cWhat is your opinion \u2013 if Allah were to take away your hearing and your sight and seal your hearts, then is there a God besides Allah who could restore it for you?\u201d Observe how We explain the verses to them, yet they turn away!"},"836":{"id":836,"surah":6,"ayah":47,"verse":"Say, \u201cWhat is your opinion \u2013 if the punishment of Allah were to come upon you suddenly or openly (with forewarning), who would be destroyed except the disbelieving people?\u201d"},"837":{"id":837,"surah":6,"ayah":48,"verse":"And We did not send Noble Messengers except as Heralds of glad tidings and warnings; so upon those who accept faith and reform themselves, shall be no fear nor shall they grieve."},"838":{"id":838,"surah":6,"ayah":49,"verse":"And those who deny Our signs \u2013 the punishment will afflict them for their disobedience."},"839":{"id":839,"surah":6,"ayah":50,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI do not say to you that I possess the treasures of Allah, nor do I say that I gain knowledge of the hidden on my own, nor do I say to you that I am an angel; I only follow what is divinely revealed to me\u201d; say, \u201cWill the blind and the sighted ever be equal? So do you not think?\u201d"},"840":{"id":840,"surah":6,"ayah":51,"verse":"And with this Qur\u2019an warn those who fear, that they will be raised towards their Lord in a state when, except Allah, there will be no protector for them nor an intercessor, so that they may be pious."},"841":{"id":841,"surah":6,"ayah":52,"verse":"And do not repel those who call upon their Lord in the morning and evening, seeking His pleasure; you are not responsible for their account nor are they responsible for your account \u2013 then your repelling them would be far from justice."},"842":{"id":842,"surah":6,"ayah":53,"verse":"And similarly We have made some as a trial for others \u2013 that the wealthy disbelievers upon seeing the needy Muslims say, \u201cAre these whom Allah has favoured among us?\u201d Does not Allah recognise those who are thankful?"},"843":{"id":843,"surah":6,"ayah":54,"verse":"And when those who believe in Our signs come humbly in your presence, say to them, \u201cPeace be upon you \u2013 your Lord has prescribed mercy for Himself by His grace \u2013 that whoever among you commits a sin by folly and thereafter repents and reforms (himself), then indeed Allah is Oft Forgiving, Most Merciful.\u201d"},"844":{"id":844,"surah":6,"ayah":55,"verse":"And this is how We explain Our verses clearly and so that the way of the criminals become well exposed."},"845":{"id":845,"surah":6,"ayah":56,"verse":"Say, \u201cI have been forbidden to worship those whom you worship other than Allah\u201d; say, \u201cI do not follow your desires \u2013 if it were, I would then go astray and not be on the right path.\u201d"},"846":{"id":846,"surah":6,"ayah":57,"verse":"Say, \u201cI am on the clear proof from my Lord, whereas you deny Him; I do not have what you are impatient for; there is no command except that of Allah; He states the truth and He is the Best of Judges.\""},"847":{"id":847,"surah":6,"ayah":58,"verse":"Say, \u201cIf I had the thing for which you are impatient, then the matter between me and you would have already been decided\u201d; and Allah is Well Aware of the unjust."},"848":{"id":848,"surah":6,"ayah":59,"verse":"And it is He Who has the keys of the hidden \u2013 only He knows them; and He knows all that is in the land and the sea; and no leaf falls but He knows it \u2013 and there is not a grain in the darkness of the earth, nor anything wet or dry, which is not recorded in a clear Book."},"849":{"id":849,"surah":6,"ayah":60,"verse":"And it is He Who removes your souls at night (while asleep) and knows whatever you commit by day; then He raises you again during the day, to complete the term appointed (for you); then it is to Him that you are to return \u2013 He will then inform you of what you used to do."},"850":{"id":850,"surah":6,"ayah":61,"verse":"And He is Omnipotent over His bondmen and sends guardians over you; to the extent that when death comes to one of you, Our angels remove his soul, and they do not err."},"851":{"id":851,"surah":6,"ayah":62,"verse":"They (the souls) are then returned towards their True Lord \u2013 Allah; pay heed! Only His is the command; and He is the Swiftest At Taking Account."},"852":{"id":852,"surah":6,"ayah":63,"verse":"Say, \u201cWho rescues you from the calamities of the land and the sea \u2013 Whom you call upon crying loudly and in whispers that, \u2018If we are saved from this we will surely be grateful\u2019?\u201d"},"853":{"id":853,"surah":6,"ayah":64,"verse":"Say, \u201cAllah delivers you from these and from all distresses \u2013 yet you ascribe partners to Him!\u201d"},"854":{"id":854,"surah":6,"ayah":65,"verse":"Say, \u201cHe is Able to send punishment upon you from above you or from beneath your feet, or to cause you to fight each other by dividing you into different groups, and make you taste the harshness of one another\u201d; observe how We explain the verses so that they may understand."},"855":{"id":855,"surah":6,"ayah":66,"verse":"And your people (O dear Prophet Mohammed \u2013 peace and blessings be upon him) denied it whereas this is undoubtedly the truth; say, \u201cI am not responsible for you.\u201d"},"856":{"id":856,"surah":6,"ayah":67,"verse":"All matters have a fixed time and soon you will come to know."},"857":{"id":857,"surah":6,"ayah":68,"verse":"And O listener (followers of this Prophet) when you see those who argue in Our verses, turn away from them until they engage in another topic; and if the devil causes you to forget, then do not sit with the unjust after remembering."},"858":{"id":858,"surah":6,"ayah":69,"verse":"And the pious are not accountable for them in the least, apart from the giving of advice so that they may avoid."},"859":{"id":859,"surah":6,"ayah":70,"verse":"And forsake those who have made their religion a mockery and play, and whom the worldly life has deceived \u2013 and advise them with this Qur\u2019an so that a soul may not be seized for what it earns; other than Allah it will not have a protector nor an intercessor; and if it offers every recompense in exchange for itself, it will not be accepted from it; these are the ones who are seized for their own deeds; for them is boiling water to drink and a painful punishment, as a recompense of their disbelief. (The disbelievers will not have any intercessors.)"},"860":{"id":860,"surah":6,"ayah":71,"verse":"Say, \u201cShall we worship, other than Allah, that which neither benefits us nor harms us, and (therefore) be turned back after Allah has guided us, like one whom the devils have led astray in the earth \u2013 bewildered?; his companions call him to the path (saying), \u2018Come here\u2019\u201d; say, \u201cIndeed only the guidance of Allah is (the true) guidance; and we are commanded to submit to the Lord Of The Creation.\u201d"},"861":{"id":861,"surah":6,"ayah":72,"verse":"\u201cAnd to keep the (obligatory) prayer established and to fear Him; and it is to Him that you are to be raised.\u201d"},"862":{"id":862,"surah":6,"ayah":73,"verse":"And it is He Who perfectly created the heavens and the earth; and when He will say \u201cBe\u201d on the Day (of resurrection) to all the extinct things, it will happen immediately; His Word is true; and it will be His kingship on the day when the Trumpet is blown; All Knowing of all the hidden and the revealed; and He only is the Wise, the Aware."},"863":{"id":863,"surah":6,"ayah":74,"verse":"And remember when Ibrahim said to his father (paternal uncle) Azar, \u201cWhat! You appoint idols as Gods? Indeed I find you and your people in open error.\u201d (Prophet Ibrahim was rightly guided since birth)."},"864":{"id":864,"surah":6,"ayah":75,"verse":"And likewise We showed Ibrahim the entire kingdom of the heavens and the earth and so that he be of those who believe as eyewitnesses."},"865":{"id":865,"surah":6,"ayah":76,"verse":"So when the night became dark upon him he saw a star; he said (to Azar \/ the people), \u201c(You portray that) this is my Lord?\u201d; then when it set he said, \u201cI do not like the things that set.\u201d"},"866":{"id":866,"surah":6,"ayah":77,"verse":"Then when he saw the moon shining, he said, \u201c(You proclaim that) this is my Lord?\u201d; then when it set, he said, \u201cIf my Lord had not guided me*, I too would be one of these astray people.\u201d (* Prophet Ibrahim was rightly guided before this event)."},"867":{"id":867,"surah":6,"ayah":78,"verse":"Then when he saw the sun shining brightly, he said, \u201c(You say that) this is my Lord? This is the biggest of them all!\u201d; then when it set he said, \u201cO people! I do not have any relation with the whatever you ascribe as partners (to Him).\u201d"},"868":{"id":868,"surah":6,"ayah":79,"verse":"\u201cI have directed my attention towards Him Who has created the heavens and the earth, am devoted solely to Him, and am not of the polytheists.\u201d"},"869":{"id":869,"surah":6,"ayah":80,"verse":"And his people argued with him; he said, \u201cWhat! You dispute with me concerning Allah? So He has guided me; and I do not have any fear of whatever you ascribe as partners, except what my Lord wills (to happen); my Lord\u2019s knowledge encompasses all things; so will you not accept advice?\u201d"},"870":{"id":870,"surah":6,"ayah":81,"verse":"\u201cAnd how should I fear whatever you ascribe as partners, whilst you do not fear that you have ascribed partners to Allah \u2013 for which He has not sent down on you any proof? So which of the two groups has more right to refuge, if you know?\u201d"},"871":{"id":871,"surah":6,"ayah":82,"verse":"Those who believed and did not mix it with injustice (disbelief), the refuge is only for them, and only they are on guidance."},"872":{"id":872,"surah":6,"ayah":83,"verse":"And this is Our argument, which We gave Ibrahim against his people; We raise to high ranks whomever We will; indeed your Lord is Wise, All Knowing."},"873":{"id":873,"surah":6,"ayah":84,"verse":"And We bestowed upon him Ishaq (Isaac) and Yaqub (Jacob); We guided all of them; and We guided Nooh before them and of his descendants, Dawud and Sulaiman and Ayyub and Yusuf and Moosa and Haroon; and this is the way We reward the virtuous."},"874":{"id":874,"surah":6,"ayah":85,"verse":"And (We guided) Zakaria and Yahya (John) and Eisa and Elias; all these are worthy of Our proximity."},"875":{"id":875,"surah":6,"ayah":86,"verse":"And Ismael (Ishmael) and Yasa\u2019a (Elisha) and Yunus (Jonah) and Lut (Lot); and to each one during their times, We gave excellence over all others."},"876":{"id":876,"surah":6,"ayah":87,"verse":"And some of their ancestors and their descendants and their brothers; and We chose them and guided them to the Straight Path."},"877":{"id":877,"surah":6,"ayah":88,"verse":"This is the guidance of Allah, which He may give to whomever He wills among His bondmen; and had they ascribed partners (to Allah), their deeds would have been wasted."},"878":{"id":878,"surah":6,"ayah":89,"verse":"These are the ones whom We gave the Book and the wisdom and the Prophethood; so if these people do not believe in it, We have then kept ready for it a nation who do not reject (the truth)."},"879":{"id":879,"surah":6,"ayah":90,"verse":"These are the ones whom Allah guided, so follow their guidance; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI do not ask from you any fee for the Qur\u2019an; it is nothing but an advice to the entire world.\u201d"},"880":{"id":880,"surah":6,"ayah":91,"verse":"And they (the Jews) did not realise (or appreciate) the importance of Allah as was required when they said, \u201cAllah has not sent down anything upon any human being\u201d; say, \u201cWho has sent down the Book which Moosa brought, a light and guidance for mankind, which you have divided into different papers, some which you show and hide most of them? And (by which) you are taught what you did not know nor did your forefathers?\u201d Say, \u201cAllah\u201d \u2013 then leave them playing in their indecency."},"881":{"id":881,"surah":6,"ayah":92,"verse":"And this is the blessed Book which We have sent down, confirming the Books preceding it, and in order that you may warn the leader of all villages and all those around it in the entire world; and those who believe in the Hereafter accept faith in this Book, and guard their prayers."},"882":{"id":882,"surah":6,"ayah":93,"verse":"Who is more unjust than one who fabricates lies against Allah or says, \u201cI have received divine inspiration\u201d, whereas he has not been inspired at all \u2013 and one who says, \u201cI will now reveal something similar to what Allah has sent down\u201d? And if you see when the unjust are in the throes of death and the angels are with their hands outstretched; (saying) \u201cSurrender your souls; this day you shall be given a disgraceful punishment \u2013 the recompense of your fabricating lies against Allah, and your scorning His signs.\u201d"},"883":{"id":883,"surah":6,"ayah":94,"verse":"\u201cAnd indeed you (the disbelievers) have now come to Us alone as We had created you at first, and you have left behind you all the wealth and riches We had bestowed upon you; and We do not see your intercessors along with you, whom you claimed to possess a share in you; indeed the link between yourselves is cut off, and you have lost all what you contended.\u201d"},"884":{"id":884,"surah":6,"ayah":95,"verse":"Indeed it is Allah Who splits the grain and the seed; it is He Who brings forth living from the dead, and it is He Who brings forth dead from the living; such is Allah; so where are you reverting?"},"885":{"id":885,"surah":6,"ayah":96,"verse":"It is He Who breaks dawn (by splitting the dark); and He has made the night a calmness, and the sun and the moon a count (for time); this is the command set by the Almighty, the All Knowing."},"886":{"id":886,"surah":6,"ayah":97,"verse":"And it is He Who has created the stars for you, so that you may find your way by them in the darkness of the land and the sea; indeed We have explained Our verses in detail for the people of knowledge."},"887":{"id":887,"surah":6,"ayah":98,"verse":"And it is He Who has created you from a single soul \u2013 then you have to stop over* in one place and stay entrusted** in another; indeed We have explained Our verses in detail for people of understanding. (* This earth. ** The grave.)"},"888":{"id":888,"surah":6,"ayah":99,"verse":"And it is He Who sends down water from the sky; so with it We produced all things that grow; hence We produce from it vegetation from which We bring forth grains in clusters; and from the pollen of dates, dense bunches \u2013 and gardens of grapes and olives and pomegranates, similar in some ways and unlike in some; look at its fruit when it bears yield, and its ripening; indeed in it are signs for the people who believe."},"889":{"id":889,"surah":6,"ayah":100,"verse":"And out of sheer ignorance they have ascribed jinns as partners of Allah, whereas it is He Who created them, and they have invented sons and daughters for Him! Purity and Supremacy is to Him, from all what they ascribe."},"890":{"id":890,"surah":6,"ayah":101,"verse":"The Originator of the heavens and the earth; how can He possibly have a child when, in fact, He does not have a spouse? And He has created all things; and He knows everything."},"891":{"id":891,"surah":6,"ayah":102,"verse":"Such is Allah, your Lord; and none is worthy of worship except Him; the Creator of all things \u2013 therefore worship Him; and He is the Trustee over all things."},"892":{"id":892,"surah":6,"ayah":103,"verse":"Eyes do not encompass Him \u2013 and all eyes are within His domain*; He is the Most Subtle, the Fully Aware. (* control \/ knowledge)"},"893":{"id":893,"surah":6,"ayah":104,"verse":"\u201cEnlightening proofs came to you from your Lord; so whoever observes, it is for his own good; and whoever is blind, it is for his own harm; and I am not a guardian over you.\u201d"},"894":{"id":894,"surah":6,"ayah":105,"verse":"And this is how We explain Our verses in different ways that they (the disbelievers) may say to you, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cYou have studied\u201d \u2013 and to make it clear for the people of knowledge."},"895":{"id":895,"surah":6,"ayah":106,"verse":"Follow what is divinely revealed to you from your Lord; there is none worthy of worship except Him; and turn away from the polytheists."},"896":{"id":896,"surah":6,"ayah":107,"verse":"And if Allah willed, they would not ascribe (any partner to Him); We have not made you as a guardian over them; and you are not responsible for them."},"897":{"id":897,"surah":6,"ayah":108,"verse":"Do not abuse those whom they worship besides Allah lest they become disrespectful towards Allah\u2019s Majesty, through injustice and ignorance; likewise, in the eyes of every nation, We have made their deeds appear good \u2013 then towards their Lord they have to return and He will inform them of what they used to do."},"898":{"id":898,"surah":6,"ayah":109,"verse":"And they swore by Allah vehemently in their oaths that if any sign came to them, they will certainly believe in it; say, \u201cThe signs are with Allah, and what do you people know that if they came to them, they will not believe.\u201d"},"899":{"id":899,"surah":6,"ayah":110,"verse":"And We revert their hearts and their eyes \u2013 the way they had not believed the first time \u2013 and We leave them to keep wandering blindly in their rebellion."},"900":{"id":900,"surah":6,"ayah":111,"verse":"And had We sent down the angels towards them, and had the dead spoken to them, and had We raised all things in front of them, they would still not have believed unless Allah willed \u2013 but most of them are totally ignorant."},"901":{"id":901,"surah":6,"ayah":112,"verse":"And similarly We have appointed enemies for every Prophet \u2013 devils from men and jinns \u2013 one inspires the other with fabrications to deceive; and had your Lord willed they would not do so, therefore leave them with their fabrications."},"902":{"id":902,"surah":6,"ayah":113,"verse":"And in order that the hearts of those who do not believe in the Hereafter may lean towards it and that they may like it, and earn the sins which they are to earn."},"903":{"id":903,"surah":6,"ayah":114,"verse":"\u201cSo shall I seek the command other than that of Allah, whereas it is He Who has sent down the detailed Book towards you?\u201d And those whom We gave the Book know that this is the truth sent down from your Lord, so O listener, (followers of this Prophet) do not ever be of those who doubt."},"904":{"id":904,"surah":6,"ayah":115,"verse":"And the Word of your Lord is complete in truth and justice; there is none to change His Words; He is the All Hearing, the All Knowing."},"905":{"id":905,"surah":6,"ayah":116,"verse":"And O listener, (followers of the Prophet) most of the people on earth are such that were you to obey them, they would mislead you from Allah\u2019s way; they follow only assumptions and they only make guesses."},"906":{"id":906,"surah":6,"ayah":117,"verse":"Your Lord well knows who has strayed from His way; and He well knows the people on guidance."},"907":{"id":907,"surah":6,"ayah":118,"verse":"So eat from that over which Allah\u2019s name has been mentioned, if you believe in His signs."},"908":{"id":908,"surah":6,"ayah":119,"verse":"And what is the matter with you that you should not eat from that over which Allah\u2019s name has been mentioned whereas He has explained in detail to you all what is forbidden to you except when you are forced (by circumstances) towards it? And indeed many lead astray by their own desires, out of ignorance; indeed your Lord well knows the transgressors."},"909":{"id":909,"surah":6,"ayah":120,"verse":"And give up the open and hidden sins; those who earn sins will soon receive the punishment of their earnings."},"910":{"id":910,"surah":6,"ayah":121,"verse":"And do not eat that on which Allah\u2019s name has not been mentioned, and indeed that is disobedience; and undoubtedly the devils inspire in the hearts of their friends to fight with you; and if you obey them, you are then polytheists."},"911":{"id":911,"surah":6,"ayah":122,"verse":"And will the one who was dead and so We raised him to life and set for him a light with which he walks among the people, ever be like the one who is in realms of darkness never to emerge from them? Similarly, the deeds of disbelievers are made to appear good to them."},"912":{"id":912,"surah":6,"ayah":123,"verse":"And similarly, We have made in every town leaders among its criminals that they may conspire in it; and they do not conspire except against themselves and they do not have perception."},"913":{"id":913,"surah":6,"ayah":124,"verse":"And when a sign comes to them, they say, \u201cWe will not believe until we are given the same which Allah\u2019s Noble Messengers were given\u201d; Allah knows best where to place His message (prophethood); soon the guilty will be afflicted with disgrace before Allah and a severe punishment due to their scheming."},"914":{"id":914,"surah":6,"ayah":125,"verse":"And whomever Allah wills to guide, He opens his bosom for Islam; and whomever He wills to send astray, He makes his bosom narrow and firmly bound as if he were being forced by someone to climb the skies; this is how Allah places the punishment on those who do not believe."},"915":{"id":915,"surah":6,"ayah":126,"verse":"And this is the Straight Path of your Lord; We have explained in detail Our verses for the people who accept advice."},"916":{"id":916,"surah":6,"ayah":127,"verse":"For them is the abode of peace with their Lord and He is their Master \u2013 the result of their deeds."},"917":{"id":917,"surah":6,"ayah":128,"verse":"And the Day when He will raise them all and will proclaim, \u201cO you group of jinns, you have enticed a lot of men\u201d; and their human friends will submit, \u201cOur Lord, some of us have benefited from one another and have reached the appointed term which You had set for us\u201d; He will say, \u201cYour home is hell \u2013 remain in it for ever, except whomever Allah wills\u201d; O dear Prophet (Mohammed \u2013 peace and blessings be upon him), indeed your Lord is the Wise, the All Knowing."},"918":{"id":918,"surah":6,"ayah":129,"verse":"And similarly We empower some of the oppressors over others \u2013 the recompense of their deeds."},"919":{"id":919,"surah":6,"ayah":130,"verse":"\u201cO you groups of jinns and men! Did not the Noble Messengers amongst you come to you reciting My verses and warning you of confronting this day?\u201d They will say, \u201cWe testify against ourselves\u201d \u2013 and the worldly life deceived them and they will testify against themselves that they were disbelievers."},"920":{"id":920,"surah":6,"ayah":131,"verse":"This is because your Lord does not unjustly destroy townships for their people may be unaware."},"921":{"id":921,"surah":6,"ayah":132,"verse":"And for everyone are ranks from what they do; and your Lord is not unaware of their deeds."},"922":{"id":922,"surah":6,"ayah":133,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him), your Lord is the Perfect (Not needing anything), the Merciful; O people! If He wills, He can remove you and bring others in your stead \u2013 the way He created you from the descendants of others."},"923":{"id":923,"surah":6,"ayah":134,"verse":"Indeed the thing which you are promised will definitely come to pass, and you cannot escape."},"924":{"id":924,"surah":6,"ayah":135,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO my people! Keep on with your works* in your positions, I am doing mine; soon you will come to know for whom is the abode of the Hereafter; undoubtedly the unjust are never successful.\u201d (* This is said as a challenge)"},"925":{"id":925,"surah":6,"ayah":136,"verse":"And among the crops and animals that Allah has created, they assigned (only) a portion to Him and therefore said \u201cThis is for Allah\u201d \u2013 in their opinion \u2013 \u201cand this is for our partners (false deities)\u201d; so the portion for their partners does not reach Allah; and the portion for Allah reaches their partners; what an evil judgement they impose!"},"926":{"id":926,"surah":6,"ayah":137,"verse":"And similarly, their partners (the devils) have made the killing of their children seem righteous in the sight of many of the polytheists, in order to ruin them and make their religion blurred to them; and if Allah willed they would not do so, therefore leave them alone with their fabrications."},"927":{"id":927,"surah":6,"ayah":138,"verse":"And they said, \u201cThese cattle and crops are forbidden; only those whom we wish can eat them\u201d \u2013 in their opinion \u2013 and some cattle are those which they have forbidden riding upon, and some cattle over which they do not mention the name of Allah while slaughtering \u2013 all this is fabricating lies against Allah; He will soon repay them for their fabrications."},"928":{"id":928,"surah":6,"ayah":139,"verse":"And they said, \u201cThe animals in the bellies of such cattle are purely for our males and forbidden to our women; and if the animal is stillborn, they all have a share of it\u201d; soon Allah will repay them for their utterances; indeed He is Wise, All Knowing."},"929":{"id":929,"surah":6,"ayah":140,"verse":"Indeed ruined are those who slay their children out of senseless ignorance and forbid the sustenance which Allah has bestowed upon them, in order to fabricate lies against Allah; they have undoubtedly gone astray and not attained the path."},"930":{"id":930,"surah":6,"ayah":141,"verse":"It is He Who produces gardens spread on the ground and above, and the date-palm, and crops of various flavours, and the olive and the pomegranate, similar in some respects and unlike in others; eat from its fruit when it bears yield, and pay the due (obligatory charity) from it on the day it is harvested; and do not be wasteful; indeed the wasteful are not liked by Allah."},"931":{"id":931,"surah":6,"ayah":142,"verse":"And from the cattle, some for burdens, some spread on the earth; eat of the sustenance which Allah has bestowed upon you, and do not follow the footsteps of the devil; undoubtedly he is your open enemy."},"932":{"id":932,"surah":6,"ayah":143,"verse":"\u201cEight males and females; one pair of sheep and one of goats\u201d; say, \u201cHas He forbidden the two males or the two females, or what the two females carry in their wombs? Answer with some knowledge, if you are truthful.\u201d"},"933":{"id":933,"surah":6,"ayah":144,"verse":"\u201cAnd a pair of camels and a pair of oxen\u201d; say, \u201cHas He forbidden the two males or the two females, or what the two females carry in their wombs? Were you present when Allah commanded this to you?\u201d So who is more unjust than one who fabricates a lie against Allah in order to lead mankind astray with his ignorance? Indeed Allah does not guide the unjust."},"934":{"id":934,"surah":6,"ayah":145,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI do not find in what is sent down to me any eatable prohibited to a consumer, except if it is carrion, or blood flowing from blood vessels, or the flesh of swine \u2013 for that is indeed foul, or the sin causing animal over which the name of any other than Allah is taken at the time of slaughtering; so for one compelled by circumstances, neither himself desiring nor eating more than necessary, indeed your Lord is Oft Forgiving, Most Merciful.\u201d"},"935":{"id":935,"surah":6,"ayah":146,"verse":"And for the Jews We forbade all animals with claws; and forbade them the fat of oxen and sheep except which is on their backs or joined to their intestines or to the bone; We awarded this to them for their rebellion; and indeed, surely, We are truthful."},"936":{"id":936,"surah":6,"ayah":147,"verse":"Then if they deny you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) say, \u201cYour Lord has boundless mercy; and His wrath is never withdrawn from the culprits.\u201d"},"937":{"id":937,"surah":6,"ayah":148,"verse":"The polytheists will now say, \u201cHad Allah willed, we would not have ascribed partners (to Him) nor would have our forefathers, nor would we have forbidden anything\u201d; similarly those before them had denied, till the time they tasted Our punishment; say, \u201cDo you have any knowledge so you can offer it to us? You follow only assumptions and only make guesses.\u201d"},"938":{"id":938,"surah":6,"ayah":149,"verse":"Say, \u201cThen only Allah\u2019s argument is the complete one; so had He willed, He would have guided you all.\u201d"},"939":{"id":939,"surah":6,"ayah":150,"verse":"Say, \u201cBring your witnesses who can testify that Allah has forbidden this\u201d; then if they do testify, O listener (followers of this Prophet) do not bear witness along with them and do not follow the desires of those who deny Our signs, and of those who do not believe in the Hereafter and who ascribed equals to their Lord."},"940":{"id":940,"surah":6,"ayah":151,"verse":"Say, \u201cCome \u2013 so that I may recite to you what your Lord has forbidden for you that \u2018Do not ascribe any partner to Him and be good to parents; and do not kill your children because of poverty; We shall provide sustenance for all \u2013 you and them; and do not approach lewd things, the open among them or concealed; and do not unjustly kill any life which Allah has made sacred; this is the command to you, so that you may have sense.\u2019"},"941":{"id":941,"surah":6,"ayah":152,"verse":"\u2018And do not approach the wealth of an orphan except in the best manner, till he reaches his adulthood; and measure and weigh in full, with justice; We do not burden any soul except within its capacity; and always speak fairly, although it may be concerning your relative; and be faithful only to Allah\u2019s covenant; this is commanded to you, so that you may accept advice.\u2019\u201d"},"942":{"id":942,"surah":6,"ayah":153,"verse":"\u201cAnd that, \u2018This is My Straight Path, so follow it; and do not follow other ways for they will sever you from His way; this is commanded to you, so that you may attain piety.\u2019\u201d"},"943":{"id":943,"surah":6,"ayah":154,"verse":"Then We gave the Book to Moosa, to complete the favour on one who is virtuous, and an explanation of all things, a guidance and a mercy, so they may believe in meeting their Lord."},"944":{"id":944,"surah":6,"ayah":155,"verse":"And this (the Qur\u2019an) is the blessed Book which We have sent down; so follow it and be pious, so there may be mercy upon you."},"945":{"id":945,"surah":6,"ayah":156,"verse":"For you (the disbelievers) may say, \u201cThe Book was sent down only to two groups (Jews and Christians) before us; and we were totally unaware of what they read and taught.\u201d"},"946":{"id":946,"surah":6,"ayah":157,"verse":"Or may say that, \u201cIf the Book had been sent down to us, we would have been more upon guidance than them\u201d; so the clear proof and guidance and mercy has come to you, from your Lord; so who is more unjust than one who denies the signs of Allah, and turns away from them? We shall soon punish those who turn away from Our signs with a great punishment, the recompense of their turning away."},"947":{"id":947,"surah":6,"ayah":158,"verse":"What are they waiting for \u2013 except that the angels come to them, or the punishment from your Lord, or one of the signs of your Lord? On the day when the (foretold) sign of your Lord comes, not a single soul who had not earlier accepted faith nor earned any good from its faith, will benefit from accepting faith; say, \u201cWait \u2013 we too are waiting.\u201d"},"948":{"id":948,"surah":6,"ayah":159,"verse":"You (O dear Prophet Mohammed \u2013 peace and blessings be upon him) have no concern with those who divided their religion and became several groups; their case is only with Allah \u2013 He will then inform them of what they used to do."},"949":{"id":949,"surah":6,"ayah":160,"verse":"For one who brings one good deed, are ten like it; and one who brings an ill-deed will not be repaid but with one like it, and they will not be wronged."},"950":{"id":950,"surah":6,"ayah":161,"verse":"Say, \u201cIndeed my Lord has guided me to the Straight Path; the right religion, (of) the community of Ibrahim who was free from all falsehood; and was not a polytheist.\u201d"},"951":{"id":951,"surah":6,"ayah":162,"verse":"Say, \u201cUndoubtedly my prayers and my sacrifices, and my living and my dying are all for Allah, the Lord Of The Creation.\u201d"},"952":{"id":952,"surah":6,"ayah":163,"verse":"\u201cHe has no partner; this is what I have been commanded, and I am the first Muslim.\u201d"},"953":{"id":953,"surah":6,"ayah":164,"verse":"Say, \u201cShall I seek a Lord other than Allah, whereas He is Lord of all things?\u201d And whatever a soul earns is itself responsible for it; and no load bearing soul will bear anyone else\u2019s load; then towards your Lord you have to return and He will inform you about the matters you differed."},"954":{"id":954,"surah":6,"ayah":165,"verse":"And it is He who made you caliphs (viceroys) in the earth and ranked some of you high above others, in order that He may test you with what He has bestowed upon you; indeed it does not take time for your Lord to mete out punishment; and indeed, surely, He is Oft Forgiving, Most Merciful."},"955":{"id":955,"surah":7,"ayah":1,"verse":"Alif-Laam-Meem-Saad. (Alphabets of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"956":{"id":956,"surah":7,"ayah":2,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him), a Book has been sent down upon you, therefore may not your heart be disinclined towards it, so that you may give warning with it, and as an advice for the Muslims."},"957":{"id":957,"surah":7,"ayah":3,"verse":"O mankind, follow what has been sent down to you from your Lord, and do not follow other administrators, abandoning this (the Holy Qur\u2019an); very little do you understand."},"958":{"id":958,"surah":7,"ayah":4,"verse":"And many a township did We destroy \u2013 so Our punishment came to them at night or while they were sleeping at noon."},"959":{"id":959,"surah":7,"ayah":5,"verse":"Therefore they uttered nothing when Our punishment came to them, except that they said, \u201cIndeed we were the unjust.\u201d"},"960":{"id":960,"surah":7,"ayah":6,"verse":"So undoubtedly We shall question those to whom Our Noble Messengers went, and indeed We shall question the Noble Messengers."},"961":{"id":961,"surah":7,"ayah":7,"verse":"So indeed We shall inform them with Our knowledge and We were not absent."},"962":{"id":962,"surah":7,"ayah":8,"verse":"And on that Day, the weighing will truly be done; so those whose scales prove heavy are the successful."},"963":{"id":963,"surah":7,"ayah":9,"verse":"And those whose scales are light are the people who put themselves to ruin \u2013 the recompense of the injustice they used to do to Our signs."},"964":{"id":964,"surah":7,"ayah":10,"verse":"And indeed We established you in the earth and in it created for you the means of livelihood; very little thanks do you offer!"},"965":{"id":965,"surah":7,"ayah":11,"verse":"And indeed We created you, then designed you and then ordered the angels, \u201cProstrate before Adam\u201d; so they all prostrated, except Iblis (Satan); he did not become of those who prostrate."},"966":{"id":966,"surah":7,"ayah":12,"verse":"Said Allah, \u201cWhat prevented you, that you did not prostrate when I commanded you?\u201d Answered Iblis, \u201cI am better than him; You created me from fire whereas You created him from clay.\u201d"},"967":{"id":967,"surah":7,"ayah":13,"verse":"Said Allah, \u201cTherefore go down from here \u2013 it does not befit you to stay here and be proud \u2013 exit, you are of the degraded.\u201d"},"968":{"id":968,"surah":7,"ayah":14,"verse":"He said, \u201cGive me respite till the day when people will be resurrected.\u201d"},"969":{"id":969,"surah":7,"ayah":15,"verse":"Said Allah, \u201cYou are given respite.\u201d"},"970":{"id":970,"surah":7,"ayah":16,"verse":"He said, \u201cHence I swear by the fact that You sent me astray, I will certainly lay in wait for them on Your Straight Path.\u201d"},"971":{"id":971,"surah":7,"ayah":17,"verse":"\u201cThen I will certainly approach them \u2013 from their front and from behind them and from their right and from their left; and You will find most of them not thankful.\u201d"},"972":{"id":972,"surah":7,"ayah":18,"verse":"He said, \u201cExit from here, rejected, outcast; indeed whoever among them follows your bidding, I will fill hell with all of you.\u201d"},"973":{"id":973,"surah":7,"ayah":19,"verse":"And said, \u201cO Adam! You and your wife dwell in Paradise \u2013 therefore eat from it from wherever you wish, and do not approach this tree for you will become of those who transgress.\u201d"},"974":{"id":974,"surah":7,"ayah":20,"verse":"Then Satan created apprehensions in their hearts in order to disclose to them matters of their shame which were hidden from them, and said, \u201cYour Lord has forbidden you from this tree, for you may become angels or immortals.\u201d"},"975":{"id":975,"surah":7,"ayah":21,"verse":"And he swore to them, \u201cIndeed I am a well-wisher for both of you.\u201d"},"976":{"id":976,"surah":7,"ayah":22,"verse":"So he brought them down with deception; and when they tasted from that tree, their shame became manifest to them and they began attaching the leaves of Paradise on themselves; and their Lord said to them, \u201cDid I not forbid you from that tree, and tell you that Satan is an open enemy to you?\u201d"},"977":{"id":977,"surah":7,"ayah":23,"verse":"They both submitted, \u201cOur Lord! We have wronged ourselves; so if You do not forgive us and have mercy on us, then surely, we are of the losers.\u201d"},"978":{"id":978,"surah":7,"ayah":24,"verse":"He said, \u201cGo down, one of you is a foe unto the other; and for a fixed time you shall stay on earth and feed in it.\u201d"},"979":{"id":979,"surah":7,"ayah":25,"verse":"He said, \u201cYou shall live there and there shall you die, and from there only you will be raised.\u201d"},"980":{"id":980,"surah":7,"ayah":26,"verse":"O Descendants of Adam! We have sent down to you a garment to conceal your shame, and another garment for your elegance; and the garment of piety \u2013 that is the best; this is among the signs of Allah, so that they may remember."},"981":{"id":981,"surah":7,"ayah":27,"verse":"O Descendants of Adam, beware! Do not let Satan put you in trial the way he removed your parents from Paradise and had their garments removed so that their shame become visible to them; indeed he and his tribe see you from where you do not see them; indeed We have made the devils the friends of those who do not believe."},"982":{"id":982,"surah":7,"ayah":28,"verse":"And when they commit any shameful act they say, \u201cWe found our forefathers on it and Allah has commanded it to us\u201d; say, \u201cIndeed Allah does not ordain shamelessness; what! You attribute things to Allah, which you do not know?\u201d"},"983":{"id":983,"surah":7,"ayah":29,"verse":"Say, \u201cMy Lord has ordained justice; and set your attention straight every time you offer pray and worship Him, as only His devoted worshippers; the way He brought you into being, in the same manner will you return.\u201d"},"984":{"id":984,"surah":7,"ayah":30,"verse":"He has guided one group, and one group\u2019s error has been proved; instead of Allah, they have chosen the devil as their friend and they assume that they are on guidance!"},"985":{"id":985,"surah":7,"ayah":31,"verse":"O Descendants of Adam! Adorn yourself when you go to the mosque, and eat and drink, and do not cross limits; indeed He does not like the transgressors."},"986":{"id":986,"surah":7,"ayah":32,"verse":"Say, \u201cWho has forbidden the adornment of Allah which He has brought forth for His bondmen, and the good food?\u201d Say, \u201cThat is for the believers in this world, and on the Day of Resurrection it will be for them only\u201d; this is how We explain Our verses in detail for people of knowledge."},"987":{"id":987,"surah":7,"ayah":33,"verse":"Say, \u201cMy Lord has forbidden the indecencies, the apparent among them and the hidden, and sin and wrongful excesses, and forbidden that you ascribe partners with Allah for which He has not sent down any proof, and forbidden that you say things concerning Allah of which you do not have knowledge.\u201d"},"988":{"id":988,"surah":7,"ayah":34,"verse":"And every group has a promise; so when its promise comes, it cannot be postponed for a moment or brought forward."},"989":{"id":989,"surah":7,"ayah":35,"verse":"O Descendants of Adam! If Noble Messengers from among you come to you narrating My verses \u2013 so whoever practices piety and reforms \u2013 upon him shall be no fear nor shall he grieve."},"990":{"id":990,"surah":7,"ayah":36,"verse":"And those who denied Our signs and were conceited towards them, are the people of hell-fire; they will remain in it forever."},"991":{"id":991,"surah":7,"ayah":37,"verse":"So who is more unjust than one who fabricated a lie against Allah or denied His signs? Their written fate will reach them; until when Our sent angels come to remove their souls, hence they say to them, \u201cWhere are they whom you used to worship besides Allah?\u201d They say, \u201cWe have lost them\u201d and they testify against themselves that they were disbelievers."},"992":{"id":992,"surah":7,"ayah":38,"verse":"Allah says to them, \u201cJoin the groups of jinns and mankind who have entered hell before you\u201d; when a group enters, it curses the other; until when they have all gone in, the latter groups will say regarding the former, \u201cOur Lord! It is these who led us astray, so give them double the punishment of the fire\u201d; He will say, \u201cFor each one is double \u2013 but you do not know.\u201d"},"993":{"id":993,"surah":7,"ayah":39,"verse":"And the preceding groups will say to the latter, \u201cSo you too were no better than us, therefore taste the punishment for what you have done!\u201d"},"994":{"id":994,"surah":7,"ayah":40,"verse":"Indeed those who denied Our signs and were conceited towards them \u2013 the gates of the heavens will not be opened for them nor will they enter Paradise until the camel goes through the needle\u2019s eye*; and this is the sort of reward We give the guilty. (* Which will never happen.)"},"995":{"id":995,"surah":7,"ayah":41,"verse":"Their beds and their coverings \u2013 both are fire; and this is the sort of reward We give the unjust."},"996":{"id":996,"surah":7,"ayah":42,"verse":"And those who accepted faith and performed good deeds according to their capacity \u2013 We do not burden any one, except within its capacity \u2013 are the people of Paradise; they shall abide in it forever."},"997":{"id":997,"surah":7,"ayah":43,"verse":"And We have removed resentment from their hearts \u2013 rivers will flow beneath them; and (while entering Paradise) they will say, \u201cAll praise is to Allah, Who guided us to this; we would not have attained the right path if Allah had not guided us; indeed the Noble Messengers of our Lord brought the truth\u201d; and it is proclaimed, \u201cYou have received this Paradise as an inheritance for what you used to do.\u201d"},"998":{"id":998,"surah":7,"ayah":44,"verse":"And the people of Paradise said to the people of hell, \u201cWe have surely received what our Lord had truly promised us \u2013 so have you also received what your Lord had truly promised?\u201d They said, \u201cYes\u201d; and an announcer between them proclaimed, \u201cThe curse of Allah is upon the unjust.\u201d \u2013"},"999":{"id":999,"surah":7,"ayah":45,"verse":"\u201cThose who prevent from the path of Allah and wish to distort it; and who disbelieve in the Hereafter.\u201d"},"1000":{"id":1000,"surah":7,"ayah":46,"verse":"Between Paradise and Hell is a veil; and on the Heights will be some men who will recognise them all by their foreheads; and they call to the people of Paradise, \u201cPeace be upon you\u201d; they have not entered Paradise and they yearn for it."},"1001":{"id":1001,"surah":7,"ayah":47,"verse":"And when their eyes turn towards the people of hell, they will say, \u201cOur Lord! Do not put us along with the unjust.\u201d"},"1002":{"id":1002,"surah":7,"ayah":48,"verse":"And the men on the Heights will call to some men whom they recognise by their foreheads, and say, \u201cWhat benefit did your derive from your populace and from what you prided in?\u201d"},"1003":{"id":1003,"surah":7,"ayah":49,"verse":"\u201cAre these the people (Muslims) regarding whom you swore that Allah would not have mercy on them at all? Whereas to the Muslims it has been said \u2018Enter Paradise; you shall have no fear nor any grief.\u2019\u201d"},"1004":{"id":1004,"surah":7,"ayah":50,"verse":"And the people of hell will cry out to the people of Paradise, \u201cProvide us some benefit from your water or from the food Allah has provided you\u201d; they will say, \u201cIndeed Allah has forbidden both to the disbelievers.\u201d"},"1005":{"id":1005,"surah":7,"ayah":51,"verse":"People who made their religion a sport and pastime, and whom the worldly life deceived; so this day We will disregard them, the way they had neglected their confronting of this day, and the way they used to deny Our signs."},"1006":{"id":1006,"surah":7,"ayah":52,"verse":"And indeed We brought to them a Book, which We have explained in detail with a great knowledge \u2013 a guidance and a mercy for people who believe."},"1007":{"id":1007,"surah":7,"ayah":53,"verse":"What do they await, except for the result foretold by that Book to appear? The day when the result foretold by it occurs, those who had previously forgotten it from the beginning (the disbelievers) will exclaim, \u201cIndeed the Noble Messengers of our Lord had brought the truth! Do we have any intercessors who may intercede for us? Or can we be returned (to earth), so we may do contrary to what we have done before?\u201d Indeed they have put themselves into ruin, and have lost the things they fabricated."},"1008":{"id":1008,"surah":7,"ayah":54,"verse":"Indeed your Lord is Allah Who created the heavens and the earth in six days, then in the manner befitting His Majesty, ascended the Throne (of control); He covers the night with the day, which hastily follows it, and made the sun and the moon and the stars subservient to His command; pay heed! Only He has the power to create and command; Most Auspicious (Propitious) is Allah, the Lord Of The Creation."},"1009":{"id":1009,"surah":7,"ayah":55,"verse":"Pray to your Lord crying humbly, and softly; indeed He does not love the transgressors."},"1010":{"id":1010,"surah":7,"ayah":56,"verse":"And do not spread turmoil in the earth after its reform, and pray to Him with fear and hope; indeed Allah\u2019s mercy is close to the virtuous."},"1011":{"id":1011,"surah":7,"ayah":57,"verse":"And it is He Who sends the winds giving glad tidings, ahead of His mercy; until when they come bearing heavy clouds, We drove it towards a city devoid of vegetation, and then rained water upon it, then produced fruits of various kinds from it; this is how We will bring forth the dead, so that you may heed advice."},"1012":{"id":1012,"surah":7,"ayah":58,"verse":"And from the good land comes forth its vegetation by the command of Allah; and from the infertile land, nothing comes forth except a little with difficulty; this is how We explain Our signs in different ways, for people who are thankful."},"1013":{"id":1013,"surah":7,"ayah":59,"verse":"Indeed We sent Nooh to his people \u2013 he therefore said, \u201cO my people! Worship Allah \u2013 you do not have any God except Him; indeed I fear for you the punishment of the Great Day (of Resurrection).\u201d"},"1014":{"id":1014,"surah":7,"ayah":60,"verse":"The leaders of his people said, \u201cIndeed we see you in open error.\u201d"},"1015":{"id":1015,"surah":7,"ayah":61,"verse":"He said, \u201cO my people! There is no straying in me \u2013 I am in fact a Noble Messenger from the Lord Of The Creation.\u201d"},"1016":{"id":1016,"surah":7,"ayah":62,"verse":"\u201cConveying to you the messages of my Lord and wishing good for you, and I know from Allah what you do not know.\u201d"},"1017":{"id":1017,"surah":7,"ayah":63,"verse":"\u201cAnd are you surprised that an advice came to you from your Lord through a man amongst you, so that he may warn you and that you may fear, and so that there be mercy upon you?\u201d"},"1018":{"id":1018,"surah":7,"ayah":64,"verse":"In response they denied him, so We rescued him and those with him in the ship, and We drowned those who denied Our signs; indeed they were a blind group."},"1019":{"id":1019,"surah":7,"ayah":65,"verse":"And We sent Hud to the people of Aad from their own community; he said, \u201cO my people! Worship Allah \u2013 you do not have any God except Him; so do you not fear?\u201d"},"1020":{"id":1020,"surah":7,"ayah":66,"verse":"The disbelieving leaders of his people said, \u201cIndeed we consider you foolish and think you are a liar.\u201d"},"1021":{"id":1021,"surah":7,"ayah":67,"verse":"He said, \u201cO my people! I do not have any concern with foolishness and I am in fact a Noble Messenger from the Lord Of The Creation.\u201d"},"1022":{"id":1022,"surah":7,"ayah":68,"verse":"\u201cI convey to you the messages of my Lord and am your trustworthy well-wisher.\u201d"},"1023":{"id":1023,"surah":7,"ayah":69,"verse":"\u201cAnd are you surprised that an advice came to you from your Lord through a man amongst you, so that he may warn you? Remember when He made you the successors of Nooh\u2019s people, and enlarged your bodies; therefore remember Allah\u2019s favours, so that you may attain good.\u201d"},"1024":{"id":1024,"surah":7,"ayah":70,"verse":"They said, \u201cHave you come to us in order that we worship only Allah, and abandon those whom our ancestors worshipped?! So bring upon us what you promise us, if you are truthful.\u201d"},"1025":{"id":1025,"surah":7,"ayah":71,"verse":"He said, \u201cIndeed the punishment and the wrath of your Lord have fallen upon you; what! You needlessly dispute with me regarding the names you and your ancestors have fabricated? Allah has not sent down any proof concerning them; therefore wait \u2013 I too await with you.\u201d"},"1026":{"id":1026,"surah":7,"ayah":72,"verse":"We therefore rescued him and those with him by a great mercy from Us, and We cut off the lineage of those who denied Our signs \u2013 and they were not believers."},"1027":{"id":1027,"surah":7,"ayah":73,"verse":"And We sent Saleh to the tribe of Thamud, from their own community; he said, \u201cO my people! Worship Allah \u2013 you do not have any God except Him; indeed a clear proof has come to you from your Lord; this is Allah\u2019s she-camel \u2013 a sign for you \u2013 so leave her free to feed in Allah\u2019s earth, and do not touch her with evil intentions for a painful punishment will seize you.\u201d"},"1028":{"id":1028,"surah":7,"ayah":74,"verse":"\u201cAnd remember when He made you successors of A\u2019ad and gave you a region in the earth, so you now build palaces in the soft plains and carve houses in rocks; therefore remember Allah\u2019s favours and do not roam the earth spreading turmoil.\u201d"},"1029":{"id":1029,"surah":7,"ayah":75,"verse":"The proud leaders of his people (mockingly) said to the weak Muslims, \u201cDo you know that Saleh is (really) the Noble Messenger of his Lord?\u201d They said, \u201cWe believe in whatever he has been sent with.\u201d"},"1030":{"id":1030,"surah":7,"ayah":76,"verse":"The proud ones said, \u201cWe deny what you have believed in.\u201d"},"1031":{"id":1031,"surah":7,"ayah":77,"verse":"So they hamstrung the she-camel and rebelled against the command of their Lord and said, \u201cO Saleh! Bring upon us what you promise us, if you are a Noble Messenger.\u201d"},"1032":{"id":1032,"surah":7,"ayah":78,"verse":"Therefore the earthquake seized them, so at morning they remained lying flattened in their homes."},"1033":{"id":1033,"surah":7,"ayah":79,"verse":"Saleh therefore turned away from them and said, \u201cO my people! Indeed I did deliver my Lord\u2019s message to you and wished you good, but you do not want well-wishers.\u201d (The people in the graves can hear the speech of those who are on earth.)"},"1034":{"id":1034,"surah":7,"ayah":80,"verse":"And We sent Lut \u2013 when he said to his people, \u201cWhat! You commit the shameful acts which no one in the creation has ever done before you?\u201d"},"1035":{"id":1035,"surah":7,"ayah":81,"verse":"\u201cYou lustfully go towards men, instead of women! In fact, you have transgressed the limits.\u201d"},"1036":{"id":1036,"surah":7,"ayah":82,"verse":"And his people had no answer except to say, \u201cTurn them out of your dwellings; these are people who wish purity!\u201d"},"1037":{"id":1037,"surah":7,"ayah":83,"verse":"And We rescued him and his family, except his wife \u2013 she became of those who stayed behind."},"1038":{"id":1038,"surah":7,"ayah":84,"verse":"And We rained a shower (of stones) upon them; therefore see what sort of fate befell the culprits!"},"1039":{"id":1039,"surah":7,"ayah":85,"verse":"And We sent Shuaib to Madyan from their community; he said, \u201cO my people! Worship Allah \u2013 you do not have any God except Him; indeed a clear proof has come to you from your Lord, so measure and weigh in full and do not give the people their goods diminished, and do not spread turmoil in the earth after it is organised; this is for your good, if you believe.\u201d"},"1040":{"id":1040,"surah":7,"ayah":86,"verse":"\u201cAnd do not be seated on every road in order to scare the travellers, and to prevent from Allah\u2019s path the people who believe in Him, wishing to distort it; and remember when you were few and He increased your numbers; and see what sort of fate befell the mischievous!\u201d"},"1041":{"id":1041,"surah":7,"ayah":87,"verse":"\u201cAnd if a group among you believes in what I have been sent with, and another group does not believe, then wait until Allah judges between us; and Allah\u2019s judgement is the best of all.\u201d"},"1042":{"id":1042,"surah":7,"ayah":88,"verse":"The proud leaders of his people said, \u201cO Shuaib, we swear we will banish you and the Muslims who are with you, from our town or you must return to our religion\u201d; he said, \u201cEven though we detest it?\u201d"},"1043":{"id":1043,"surah":7,"ayah":89,"verse":"\u201cWe shall then have fabricated a lie against Allah if we return to your religion after Allah has rescued us from it; and it is not for any of us Muslims to return to your religion except if Allah, Who is our Lord, wills; the knowledge of our Lord encompasses all things; in Allah only we have trusted; our Lord! Decide with justice between us and our people \u2013 and Yours is the best decision.\u201d"},"1044":{"id":1044,"surah":7,"ayah":90,"verse":"And the disbelieving leaders of his people said, \u201cIf you obey Shuaib, you will indeed be in a loss.\u201d"},"1045":{"id":1045,"surah":7,"ayah":91,"verse":"Therefore the earthquake seized them \u2013 so at morning they remained lying flattened in their homes."},"1046":{"id":1046,"surah":7,"ayah":92,"verse":"As if those who denied Shoaib had never lived in those homes; those who denied Shoaib, were themselves ruined."},"1047":{"id":1047,"surah":7,"ayah":93,"verse":"So Shoaib turned away from them saying, \u201cO my people! Indeed I did deliver my Lord\u2019s message to you and gave you sound advice; so why should I grieve for the disbelievers?\u201d (The people in the graves can hear the speech of those who are on earth.)"},"1048":{"id":1048,"surah":7,"ayah":94,"verse":"And never did We send any Prophet to a dwelling but We seized its people with hardship and adversity so that they may become humble."},"1049":{"id":1049,"surah":7,"ayah":95,"verse":"Then We changed the misfortune into prosperity to the extent that they became numerous and said, \u201cIndeed grief and comfort did reach our ancestors\u201d \u2013 so We seized them suddenly in their neglect."},"1050":{"id":1050,"surah":7,"ayah":96,"verse":"And had the people of the dwellings believed and been pious, We would have surely opened for them the blessings from the sky and from the earth, but in fact they denied, and We therefore seized them on account of their deeds."},"1051":{"id":1051,"surah":7,"ayah":97,"verse":"Do the people of the dwellings not fear that Our wrath may come upon them at night while they are asleep?"},"1052":{"id":1052,"surah":7,"ayah":98,"verse":"Or do the people of the dwellings not fear that Our wrath may come upon them during the day, while they are playing?"},"1053":{"id":1053,"surah":7,"ayah":99,"verse":"Are they oblivious to Allah\u2019s secret plan? So none is unafraid of Allah\u2019s secret plan except the people of ruin!"},"1054":{"id":1054,"surah":7,"ayah":100,"verse":"Or did not those who inherited the land after its owners, get enough guidance that if We will, We can afflict them with calamity for their sins? And We set seals upon their hearts so they do not hear."},"1055":{"id":1055,"surah":7,"ayah":101,"verse":"These are the dwellings \u2013 the affairs of which We relate to you (Prophet Mohammed \u2013 peace and blessings be upon him); and indeed their (respective) Noble Messengers came to them with clear proofs; so they were not able to believe in what they had denied before; this is how Allah sets seals upon the hearts of disbelievers."},"1056":{"id":1056,"surah":7,"ayah":102,"verse":"And We found most of them not true to their words; and indeed We found most of them disobedient."},"1057":{"id":1057,"surah":7,"ayah":103,"verse":"Then after them, We sent Moosa with our signs to Firaun and his court members, but they did injustice to those signs; therefore see what sort of fate befell the mischievous!"},"1058":{"id":1058,"surah":7,"ayah":104,"verse":"And Moosa said, \u201cO Firaun! Indeed I am a Noble Messenger from the Lord Of The Creation.\u201d"},"1059":{"id":1059,"surah":7,"ayah":105,"verse":"\u201cIt is obligatory for me not to speak concerning Allah except the truth; I have come to you all with a clear sign from your Lord, therefore let the Descendants of Israel go with me.\u201d"},"1060":{"id":1060,"surah":7,"ayah":106,"verse":"Said Firaun, \u201cIf you have come with a sign, then present it if you are truthful!\u201d"},"1061":{"id":1061,"surah":7,"ayah":107,"verse":"Therefore Moosa put down his staff \u2013 it immediately turned into a visible python."},"1062":{"id":1062,"surah":7,"ayah":108,"verse":"And putting his hand in his bosom, withdrew it \u2013 so it shone brightly before the beholders."},"1063":{"id":1063,"surah":7,"ayah":109,"verse":"Said the chieftains of Firaun\u2019s people, \u201cHe is really an expert magician.\u201d"},"1064":{"id":1064,"surah":7,"ayah":110,"verse":"\u201cHe wishes to expel you all from your kingdom; so what do you advise?\u201d"},"1065":{"id":1065,"surah":7,"ayah":111,"verse":"They said, \u201cStop him and his brother, and send announcers to the cities to gather people.\u201d"},"1066":{"id":1066,"surah":7,"ayah":112,"verse":"\u201cTo bring all the expert magicians to you.\u201d"},"1067":{"id":1067,"surah":7,"ayah":113,"verse":"And the magicians came to Firaun, and said, \u201cWill we get some reward if we are victorious?\u201d"},"1068":{"id":1068,"surah":7,"ayah":114,"verse":"He said, \u201cYes, and you will then become close to me.\u201d"},"1069":{"id":1069,"surah":7,"ayah":115,"verse":"They said, \u201cO Moosa! You may throw first \u2013 or shall we be the first to throw?\u201d"},"1070":{"id":1070,"surah":7,"ayah":116,"verse":"He said, \u201cYou throw\u201d; when they threw, they cast a magic spell upon the people\u2019s eyes and terrified them, and they brought a great magic."},"1071":{"id":1071,"surah":7,"ayah":117,"verse":"And We inspired Moosa that, \u201cPut forth your staff\u201d; it immediately began swallowing up their fabrications."},"1072":{"id":1072,"surah":7,"ayah":118,"verse":"So the truth was proved and their works were disproved."},"1073":{"id":1073,"surah":7,"ayah":119,"verse":"They were therefore defeated here and they turned back humiliated."},"1074":{"id":1074,"surah":7,"ayah":120,"verse":"And the magicians were obliged to fall prostrate."},"1075":{"id":1075,"surah":7,"ayah":121,"verse":"They said, \u201cWe have accepted faith in the Lord Of The Creation.\u201d"},"1076":{"id":1076,"surah":7,"ayah":122,"verse":"\u201cThe Lord of Moosa and Haroon.\u201d"},"1077":{"id":1077,"surah":7,"ayah":123,"verse":"Said Firaun, \u201cYou have accepted faith in Him before I gave you permission! This is indeed a grand conspiracy you have plotted in the city, in order to expel its people from it; so now you will come to know!\u201d"},"1078":{"id":1078,"surah":7,"ayah":124,"verse":"\u201cI swear I will cut off your hands and your feet from alternate sides and then crucify you all.\u201d"},"1079":{"id":1079,"surah":7,"ayah":125,"verse":"They said, \u201cWe shall return to our Lord.\u201d"},"1080":{"id":1080,"surah":7,"ayah":126,"verse":"\u201cAnd what did you dislike in us, except that we believed in the signs of our Lord when they came to us? Our Lord! Pour (bestow abundantly) patience on us, and bestow us death as Muslims.\u201d"},"1081":{"id":1081,"surah":7,"ayah":127,"verse":"The chieftains of Firaun\u2019s people said, \u201cAre you releasing Moosa and his people to cause turmoil in the land, and for Moosa to abandon you and your appointed deities?\u201d He said, \u201cWe shall now slay their sons and spare their women; and indeed we have power over them.\u201d"},"1082":{"id":1082,"surah":7,"ayah":128,"verse":"Moosa said to his people, \u201cSeek the help of Allah and patiently endure; indeed the Owner of the earth is Allah \u2013 He appoints as its successor whomever He wills; and the final triumph is for the pious.\u201d"},"1083":{"id":1083,"surah":7,"ayah":129,"verse":"They said, \u201cWe have been oppressed before you came to us, and after you have come to us\u201d; he said, \u201cIt is likely that your Lord may destroy your enemy and in his place make you the rulers of the earth, and then see what deeds you perform.\u201d"},"1084":{"id":1084,"surah":7,"ayah":130,"verse":"And indeed We seized the people of Firaun with a famine of several years and with reduction of fruits, so that they may follow advice."},"1085":{"id":1085,"surah":7,"ayah":131,"verse":"So when good would reach them they would say, \u201cThis is for us\u201d; and when misfortune reached them, they would infer it as ill omens of Moosa and his companions; pay heed! The misfortune of their ill luck lies with Allah, but most of them are unaware."},"1086":{"id":1086,"surah":7,"ayah":132,"verse":"And said, \u201cYou may come with any sign to us, in order to cast a magic spell on us \u2013 yet by no means are we going to believe in you.\u201d"},"1087":{"id":1087,"surah":7,"ayah":133,"verse":"We therefore sent against them the flood and the locusts and the vermin (or insects) and the frogs and the blood \u2013 separate signs; in response they were proud and were a guilty people."},"1088":{"id":1088,"surah":7,"ayah":134,"verse":"And whenever the punishment came upon them they said, \u201cO Moosa! Pray to your Lord for us, by means of His covenant which you have; indeed if you lift the punishment from us we will surely accept faith in you and let the Descendants of Israel go with you.\u201d"},"1089":{"id":1089,"surah":7,"ayah":135,"verse":"Consequently whenever We lifted the punishment from them for a term which they must reach, they used to then turn away."},"1090":{"id":1090,"surah":7,"ayah":136,"verse":"We therefore took revenge from them; so We drowned them in the sea for they used to deny Our signs and were ignoring them."},"1091":{"id":1091,"surah":7,"ayah":137,"verse":"And We made the people who were oppressed, the inheritors of the eastern and western parts of the land in which We placed blessings; and the good promise of your Lord was fulfilled for the Descendants of Israel \u2013 the reward of their patience; and We destroyed whatever Firaun and his people built and whatever they had contrived."},"1092":{"id":1092,"surah":7,"ayah":138,"verse":"And We transported the Descendants of Israel across the sea \u2013 so they came across a people who used to squat in seclusion in front of their idols; they said, \u201cO Moosa! Make a God for us, the way they have so many Gods!\u201d He said, \u201cYou are indeed an ignorant people.\u201d"},"1093":{"id":1093,"surah":7,"ayah":139,"verse":"\u201cThe condition they are in is, in fact, one of destruction \u2013 and all what they do is utter falsehood.\u201d"},"1094":{"id":1094,"surah":7,"ayah":140,"verse":"He said, \u201cShall I seek for you a God other than Allah, whereas He has given you superiority above the entire world?\u201d (By sending His message towards you)."},"1095":{"id":1095,"surah":7,"ayah":141,"verse":"And remember when We rescued you from Firaun\u2019s people who were afflicting you with a dreadful torment; slaughtering your sons and sparing your daughters; and in it was a great favour from your Lord."},"1096":{"id":1096,"surah":7,"ayah":142,"verse":"And We agreed with Moosa a covenant for thirty nights (of solitude) and completed it by adding ten to them, so the covenant of His Lord amounted to forty nights in full; and Moosa said to his brother Haroon, \u201cBe my deputy over my people and make reform and do not allow the ways of the mischievous to enter.\u201d"},"1097":{"id":1097,"surah":7,"ayah":143,"verse":"And when Moosa presented himself upon Our promise, and his Lord spoke to him, he said, \u201cMy Lord! Show me Your Self, so that I may see You\u201d; He said, \u201cYou will never be able to see Me, but look towards the mountain \u2013 if it stays in its place, then you shall soon see Me\u201d; so when his Lord directed His light on the mountain, He blew it into bits and Moosa fell down unconscious; then upon regaining consciousness he said, \u201cPurity is to You! I incline towards You, and I am the first Muslim.\u201d"},"1098":{"id":1098,"surah":7,"ayah":144,"verse":"Said Allah, \u201cO Moosa! I have chosen you from mankind by (bestowing) My messages and by My speech; so accept what I have bestowed upon you and be among the thankful.\u201d"},"1099":{"id":1099,"surah":7,"ayah":145,"verse":"And We wrote for him on the tablets, the advice for all things and the details of all things; and commanded \u201cAccept it firmly and command your people to choose its good advices; soon I shall show you people the destination of the disobedient.\u201d"},"1100":{"id":1100,"surah":7,"ayah":146,"verse":"\u201cAnd I shall turn away from My signs the people who unjustly wish to be admired in the earth; and if they see all the signs, they would not believe them; and if they see the path of guidance, they would not prefer to tread it; and if they see the way of error, they would present themselves to tread it; that is because they denied Our signs and were ignoring them."},"1101":{"id":1101,"surah":7,"ayah":147,"verse":"And those who denied Our signs and the confronting of the Hereafter \u2013 all their deeds are wasted; what recompense will they get, except what they used to do?"},"1102":{"id":1102,"surah":7,"ayah":148,"verse":"And behind Moosa, his people moulded a calf from their ornaments \u2013 a lifeless body making sounds like a cow; did they not see that it neither speaks to them nor guides them in any way? They chose it (for worship), and were unjust."},"1103":{"id":1103,"surah":7,"ayah":149,"verse":"And when they repented and realised that they had gone astray, they said, \u201cIf our Lord does not have mercy on us and forgive us, we are ruined.\u201d"},"1104":{"id":1104,"surah":7,"ayah":150,"verse":"And when Moosa returned to his people, angry and upset, he said, \u201cWhat an evil way you have handled affairs on my behalf, behind me; did you hasten upon the command of your Lord?\u201d And he cast down the stone tablets, and catching hold of his brothers hair, began pulling him towards him; said Haroon said, \u201cO the son of my mother! The people thought I was weak and would have probably killed me; so do not make my enemies laugh at me and do not identify me with the unjust.\u201d"},"1105":{"id":1105,"surah":7,"ayah":151,"verse":"He submitted, \u201cMy Lord! Forgive me and my brother and admit us into Your mercy; and You are the Most Merciful of all those who show mercy.\u201d"},"1106":{"id":1106,"surah":7,"ayah":152,"verse":"Indeed those who took the calf \u2013 the punishment from their Lord, and humiliation will reach them in the life of this world; and this is the way We reward those who fabricate lies."},"1107":{"id":1107,"surah":7,"ayah":153,"verse":"And those who performed misdeeds and then repented and accepted faith \u2013 so after that, your Lord is Oft Forgiving, Most Merciful."},"1108":{"id":1108,"surah":7,"ayah":154,"verse":"And when the anger of Moosa abated, he picked up the stone tablets; and in their texts are guidance and mercy for those who fear their Lord."},"1109":{"id":1109,"surah":7,"ayah":155,"verse":"And Moosa chose seventy men from his people for Our promise; therefore when the earthquake seized them, he submitted, \u201cMy Lord! If You had willed You could have destroyed them and me, even earlier! Will You destroy us for the deeds which the ignorant among us did? That is not but Your testing us; with it You send astray whomever You will and guide whomever You will; You are our Master, so forgive us and have mercy on us, and You are the Best of the Forgiving.\u201d"},"1110":{"id":1110,"surah":7,"ayah":156,"verse":"\u201cAnd destine good for us in this world and in the Hereafter \u2013 We have indeed inclined towards You\u201d; He said, \u201cI give My punishment to whomever I will; and My mercy encompasses all things; so I shall soon destine favours for those who fear and pay the charity, and they believe in Our signs.\u201d"},"1111":{"id":1111,"surah":7,"ayah":157,"verse":"\u201cThose who will obey this Noble Messenger (Prophet Mohammed \u2013 peace and blessings be upon him), the Herald of the Hidden who is untutored* (except by Allah), whom they will find mentioned in the Taurat and the Injeel with them; he will command them to do good and forbid them from wrong, and he will make lawful for them the good clean things and prohibit the foul for them, and he will unburden the loads and the neck chains which were upon them; so those who believe in him, and revere** him, and help him, and follow the light which came down with him \u2013 it is they who have succeeded.\" (*The Holy Prophet was taught by Allah Himself \u2013 see Surah 55 Al-Rahman. **To honour the Holy Prophet \u2013 peace and blessings be upon him \u2013 is part of faith. To disrespect him is blasphemy.)"},"1112":{"id":1112,"surah":7,"ayah":158,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO people! Indeed I am, towards you all, the Noble Messenger of Allah \u2013 for Whom (Allah) only is the kingship of the heavens and the earth; there is none worthy of worship, except Him \u2013 giving life and giving death; therefore believe in Allah and His Noble Messenger, the Prophet who is untutored (except by Allah), who believes in Allah and His Words, and obey him (the Prophet) to attain guidance.\u201d (Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is the Prophet towards all mankind.)"},"1113":{"id":1113,"surah":7,"ayah":159,"verse":"And among the people of Moosa is a group that shows the true path, and establishes justice with it."},"1114":{"id":1114,"surah":7,"ayah":160,"verse":"And We divided them into twelve tribes, as separate groups; and when his people asked him for water, We revealed to Moosa, \u201cStrike the rock with your staff\u201d; so twelve springs gushed forth from it; each group recognised its drinking-place; and We made the clouds a canopy over them and sent down the Manna and the Salwa (birds) on them; \u201cEat of the good things we have provided you\u201d; and they did not wrong Us in the least, but they used to wrong themselves."},"1115":{"id":1115,"surah":7,"ayah":161,"verse":"And remember when they were commanded, \u201cReside in this township and eat whatever you wish in it, and say \u2018Sins are forgiven\u2019 and enter the gate prostrating \u2013 We will forgive you your sins; We shall soon bestow more upon the virtuous.\u201d"},"1116":{"id":1116,"surah":7,"ayah":162,"verse":"So the unjust among them changed the words, contrary to what they had been commanded \u2013 consequently We sent down upon them a punishment from the sky \u2013 the recompense of their injustice."},"1117":{"id":1117,"surah":7,"ayah":163,"verse":"And ask them (O dear Prophet Mohammed \u2013 peace and blessings be upon him) of the township that was by the sea; when they used to exceed in the matter of the Sabbath \u2013 when their fish used to come swimming atop the water in front of them on the day of Sabbath and not come on the days it was not Sabbath; this is how We used to test them, due to their disobedience."},"1118":{"id":1118,"surah":7,"ayah":164,"verse":"And when a group among them said, \u201cWhy do you preach to a people whom Allah is going to destroy or mete out a severe punishment?\u201d They said, \u201cTo have an excuse before your Lord, and that perhaps they may fear.\u201d"},"1119":{"id":1119,"surah":7,"ayah":165,"verse":"And when they forgot the advices they had been given, We rescued those who forbade evil, and seized the unjust with a dreadful punishment \u2013 the recompense of their disobedience."},"1120":{"id":1120,"surah":7,"ayah":166,"verse":"Consequently when they rebelled against the command to refrain, We said to them, \u201cBe apes, despised!\u201d"},"1121":{"id":1121,"surah":7,"ayah":167,"verse":"And remember when your Lord announced the command that till the Day of Resurrection I will certainly send such oppressors against them, who will inflict them with a dreadful punishment; indeed your Lord is swift in meting out punishment; and indeed He is Oft Forgiving, Most Merciful."},"1122":{"id":1122,"surah":7,"ayah":168,"verse":"And We divided them in the earth as separate groups; some of them are righteous and some are the other type; and We tested them with good (favours) and evil things (adversities) so that they may return."},"1123":{"id":1123,"surah":7,"ayah":169,"verse":"And after them in their place, came those unworthy successors who inherited the Books \u2013 they accept the goods of this world (as bribes) and say, \u201cWe shall soon be forgiven\u201d; and if similar goods come to them again, they would accept it; was not the covenant taken from them in the Book, that they must not relate anything to Allah except the truth, and they have studied it? And indeed the abode of the Hereafter is better for the pious; so do you not have sense?"},"1124":{"id":1124,"surah":7,"ayah":170,"verse":"And those who hold fast to the Book, and have kept the prayer established; and We do not waste the wages of the righteous."},"1125":{"id":1125,"surah":7,"ayah":171,"verse":"And when We raised the Mount (Sinai) above them as if it were a canopy, and they thought that it would fall upon them; \u201cAccept firmly what We have given you, and remember what is in it, so that you may become pious.\u201d"},"1126":{"id":1126,"surah":7,"ayah":172,"verse":"And remember when your Lord brought forth the generations from the backs of the Descendants of Adam, and made them their own witness; \u201cAm I not your Lord?\u201d; they all said, \u201cYes surely You are, why not? We testify\u201d; for you may say on the Day of Resurrection that, \u201cWe were unaware of this.\u201d"},"1127":{"id":1127,"surah":7,"ayah":173,"verse":"Or you may say, \u201cIt is our ancestors who first ascribed partners (to Allah) and we were (their) children after them; so will You destroy us on account of the deeds of the followers of falsehood?\u201d"},"1128":{"id":1128,"surah":7,"ayah":174,"verse":"And this is how We explain the verses in different ways, and so that they may return."},"1129":{"id":1129,"surah":7,"ayah":175,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) recite to them the case of the one to whom We gave Our revelations, and in response he departed from them completely \u2013 so Satan went after him \u2013 he therefore became of the astray."},"1130":{"id":1130,"surah":7,"ayah":176,"verse":"And had We willed We could have raised him because of the revelations, but he clung to the earth and followed his own desires; his condition therefore is like that of a dog; if you attack him he hangs out his tongue and if you leave him he hangs out his tongue; this is the state of the people who denied Our signs; therefore preach, so that they may give thought."},"1131":{"id":1131,"surah":7,"ayah":177,"verse":"What an evil example is of those who denied Our signs and used to wrong only their own souls."},"1132":{"id":1132,"surah":7,"ayah":178,"verse":"Whomever Allah guides \u2013 only he is on the right path; and whomever He sends astray \u2013 it is they who are the losers."},"1133":{"id":1133,"surah":7,"ayah":179,"verse":"And indeed We have created many jinns and men for hell; they have hearts in which their is no understanding; and the eyes they do not see with; and the ears they do not hear with; they are like cattle \u2013 in fact more astray; it is they who are the neglectful."},"1134":{"id":1134,"surah":7,"ayah":180,"verse":"And for Allah only are the best names, so invoke Him by them; and abandon those who depart from the truth regarding His names; they will soon receive the reward of their deeds."},"1135":{"id":1135,"surah":7,"ayah":181,"verse":"And from Our creation is a group that shows the truth and establishes justice with it."},"1136":{"id":1136,"surah":7,"ayah":182,"verse":"And those who denied Our signs \u2013 We shall soon steadily lead them towards the punishment, from the place they will not know."},"1137":{"id":1137,"surah":7,"ayah":183,"verse":"And I will give them respite; indeed My secret plan is extremely solid."},"1138":{"id":1138,"surah":7,"ayah":184,"verse":"Do they not ponder that their companion is far removed from insanity? In fact he is clearly a Herald of Warning."},"1139":{"id":1139,"surah":7,"ayah":185,"verse":"Have they not pondered deeply regarding the kingdom of the heavens and the earth, and whatever things Allah created? And that possibly their promise (of death) may have come near? So after this*, in what will they believe? (*Advent of the Last Prophet and the Holy Qur\u2019an.)"},"1140":{"id":1140,"surah":7,"ayah":186,"verse":"For one whom Allah sends astray, there is none to guide him; and He leaves them to wander in their rebellion."},"1141":{"id":1141,"surah":7,"ayah":187,"verse":"They ask you about the Resurrection, as to when it is destined; say, \u201cIndeed its knowledge is with my Lord; only He will manifest it at its time; it is proving cumbersome in the heavens and the earth; it will not come to you except suddenly\u201d; they question you as if you have researched it deeply; say, \u201cIndeed its knowledge is with Allah only but most people do not know.\u201d"},"1142":{"id":1142,"surah":7,"ayah":188,"verse":"Say, \u201cI have no autonomy to benefit or hurt myself, except what Allah wills; and were I to procure knowledge of the hidden on my own, it would be that I had accumulated a lot of good; and no misfortune would touch me; I am purely a Herald of Warning and Glad Tidings to the people who believe.\u201d"},"1143":{"id":1143,"surah":7,"ayah":189,"verse":"It is He Who created you from a single soul, and from him made its mate for him to gain comfort with her; so when the male covered her, she was burdened lightly in her womb, and she therefore moved easily carrying it; and when she felt the burden heavy, they both cried to their Lord Allah, \u201cIndeed You may give to us a child as You will, so we will surely be thankful.\u201d"},"1144":{"id":1144,"surah":7,"ayah":190,"verse":"So when He bestowed them a normal child, they ascribed partners (to Him) in respect of what He had bestowed upon them; therefore Supreme is Allah, above all that they ascribe as partners."},"1145":{"id":1145,"surah":7,"ayah":191,"verse":"Do they (the disbelievers) ascribe (false deities) that which do not create anything, but are themselves created?"},"1146":{"id":1146,"surah":7,"ayah":192,"verse":"And cannot provide any help to them, nor do they help themselves?"},"1147":{"id":1147,"surah":7,"ayah":193,"verse":"And if you call the disbelievers to guidance, they do not follow you; it is the same for you, whether you invite them or remain silent."},"1148":{"id":1148,"surah":7,"ayah":194,"verse":"Indeed those whom you (the disbelievers) worship besides Allah are slaves like you \u2013 so call them and they may answer you, if you are truthful!"},"1149":{"id":1149,"surah":7,"ayah":195,"verse":"Do they have feet to walk with? Or have they hands to hold with? Or have they eyes to see with? Or have they ears to hear with? Say, \u201cCall upon your ascribed partners and conspire against me, and do not give me respite.\u201d"},"1150":{"id":1150,"surah":7,"ayah":196,"verse":"\u201cIndeed my Protector is Allah Who has sent down the Book; and He befriends the righteous.\u201d"},"1151":{"id":1151,"surah":7,"ayah":197,"verse":"\u201cAnd those whom you worship besides Him cannot help you nor do they help themselves.\u201d"},"1152":{"id":1152,"surah":7,"ayah":198,"verse":"And if you call them to guidance they do not listen; and you (Prophet Mohammed \u2013 peace and blessings be upon him) observe them looking towards you, whereas they do not perceive anything."},"1153":{"id":1153,"surah":7,"ayah":199,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) adopt forgiveness, and enjoin virtue, and turn away from the ignorant."},"1154":{"id":1154,"surah":7,"ayah":200,"verse":"And O listener! If the devil provokes you, seek the refuge of Allah; indeed He is All Hearing, All Knowing."},"1155":{"id":1155,"surah":7,"ayah":201,"verse":"Indeed those who fear get alerted whenever a temptation from the devil troubles them, and they perceive immediately."},"1156":{"id":1156,"surah":7,"ayah":202,"verse":"And the devils pull those who their brothers into error, and then do not make any relaxation."},"1157":{"id":1157,"surah":7,"ayah":203,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) when you do not bring to them a verse, they say, \u201cWhy did you not fabricate it?\u201d Say, \u201cI follow only what is divinely revealed to me from my Lord\u201d; this (the Holy Qur\u2019an) is an enlightenment from your Lord, and a guidance and a mercy for the Muslims."},"1158":{"id":1158,"surah":7,"ayah":204,"verse":"And when the Qur\u2019an is recited, listen to it attentively and keep silent, so that you receive mercy."},"1159":{"id":1159,"surah":7,"ayah":205,"verse":"And remember your Lord within your hearts humbly and with fear, and softly with your tongues, morning and evening, and do not be of the neglectful."},"1160":{"id":1160,"surah":7,"ayah":206,"verse":"Indeed those who are with your Lord are not conceited towards worshipping Him, and they proclaim His Purity and it is to Him they prostrate. (Command of Prostration # 1)"},"1161":{"id":1161,"surah":8,"ayah":1,"verse":"They ask you O dear Prophet (Mohammed \u2013 peace and blessings be upon him) concerning the war booty; say, \u201cAllah and the Noble Messenger are the owners of the war booty; so fear Allah and maintain friendship among yourselves; and obey Allah and His Noble Messenger, if you have faith.\""},"1162":{"id":1162,"surah":8,"ayah":2,"verse":"Only they are the believers whose hearts fear when Allah is remembered, and their faith advances when His verses are recited to them, and who trust only in their Lord."},"1163":{"id":1163,"surah":8,"ayah":3,"verse":"Those who keep the prayer established and spend in Our cause from what We have bestowed upon them."},"1164":{"id":1164,"surah":8,"ayah":4,"verse":"These are the true Muslims; for them are ranks before their Lord, and forgiveness and an honourable sustenance."},"1165":{"id":1165,"surah":8,"ayah":5,"verse":"The way your Lord caused you, O dear Prophet to come forth from your home with the truth; and indeed a group of Muslims were unhappy about it."},"1166":{"id":1166,"surah":8,"ayah":6,"verse":"Disputing with you regarding the truth after it had been made clear, as if they were being herded towards a visible death."},"1167":{"id":1167,"surah":8,"ayah":7,"verse":"And remember when Allah promised you that one of the two groups (of enemies) is for you, and you wished to get the one that posed no danger, and Allah willed to prove the truth with His Words, and to cut the origins of the disbelievers."},"1168":{"id":1168,"surah":8,"ayah":8,"verse":"In order that He may prove the truth and disprove falsehood, even if the criminals get annoyed."},"1169":{"id":1169,"surah":8,"ayah":9,"verse":"When you (Prophet Mohammed \u2013 peace and blessings be upon him) were seeking the help of your Lord, so He answered your prayers that, \u201cI will help you with a row of thousands of angels.\u201d"},"1170":{"id":1170,"surah":8,"ayah":10,"verse":"And Allah did this just for your happiness and for your hearts to gain contentment; and help does not come except from Allah; indeed Allah is Almighty, Wise."},"1171":{"id":1171,"surah":8,"ayah":11,"verse":"When He made the slumber overcome you, so it was as a peacefulness from Him, and sent down water from the sky upon you to purify you with it, and to remove the impurity of Satan from you, and to give your hearts fortitude and firmly establish your feet with it."},"1172":{"id":1172,"surah":8,"ayah":12,"verse":"And when O dear Prophet, your Lord was inspiring the angels that, \u201cI am with you \u2013 so make the believers stand firm; I will soon instil fear into the hearts of the disbelievers, so strike above the disbelievers\u2019 necks and hit their each and every bone joint.\u201d"},"1173":{"id":1173,"surah":8,"ayah":13,"verse":"This is because they opposed Allah and His Noble Messenger; and whoever opposes Allah and His Noble Messenger \u2013 then indeed Allah\u2019s punishment is severe."},"1174":{"id":1174,"surah":8,"ayah":14,"verse":"Therefore taste this for now, and along with it for the disbelievers is the punishment of fire."},"1175":{"id":1175,"surah":8,"ayah":15,"verse":"O People who Believe! When you confront a large army of disbelievers in battle, do not turn your backs to them."},"1176":{"id":1176,"surah":8,"ayah":16,"verse":"And on that day whoever turns his back to them, except for a battle strategy or to join one\u2019s own company, has then turned towards Allah\u2019s wrath, and his destination is hell; and what an evil place of return!"},"1177":{"id":1177,"surah":8,"ayah":17,"verse":"So you did not slay them, but in fact Allah slew them; and O dear Prophet (Mohammed \u2013 peace and blessings be upon him) you did not throw (the sand) when you did throw, but in fact Allah threw; and in order to bestow an excellent reward upon the Muslims; indeed Allah is the All Hearing, the All Knowing."},"1178":{"id":1178,"surah":8,"ayah":18,"verse":"Therefore take this, and (know that) Allah will weaken the scheme of the disbelievers."},"1179":{"id":1179,"surah":8,"ayah":19,"verse":"O disbelievers! If you seek a judgement, then this judgement has come to you; and if you desist, it is better for you; and if you return to mischief, We will punish you again; and your populace will not benefit you, however large it may be \u2013 and besides this, Allah is with the Muslims."},"1180":{"id":1180,"surah":8,"ayah":20,"verse":"O People who Believe! Obey Allah and His Noble Messenger, and do not turn away from him after you have heard him speak."},"1181":{"id":1181,"surah":8,"ayah":21,"verse":"And do not be like those who said, \u201cWe have heard\u201d, whereas they do not hear."},"1182":{"id":1182,"surah":8,"ayah":22,"verse":"Indeed the worst beasts in the sight of Allah are those (people) who are deaf, dumb \u2013 who do not have any sense."},"1183":{"id":1183,"surah":8,"ayah":23,"verse":"And had Allah found any goodness in them, He would have made them hear; and had He made them hear they would, in the end, have turned away and gone back."},"1184":{"id":1184,"surah":8,"ayah":24,"verse":"O People who Believe! Present yourselves upon the command of Allah and His Noble Messenger, when the Noble Messenger calls you towards the matter that will bestow you life; and know that the command of Allah becomes a barrier between a man and his heart\u2019s intentions, and that you will all be raised towards Him."},"1185":{"id":1185,"surah":8,"ayah":25,"verse":"And fear the turmoil which will certainly not fall only upon a few selected unjust people among you; and know that Allah\u2019s punishment is severe."},"1186":{"id":1186,"surah":8,"ayah":26,"verse":"And remember when you were only a few and meek in the land, and feared that men may snatch you away \u2013 He therefore gave you refuge and strengthened you with His help, and gave you good things as sustenance, so that you may be thankful."},"1187":{"id":1187,"surah":8,"ayah":27,"verse":"O People who Believe! Do not betray Allah and His Noble Messenger, nor purposely defraud your trusts."},"1188":{"id":1188,"surah":8,"ayah":28,"verse":"And know that your wealth and your children are a test, and that with Allah is an immense reward."},"1189":{"id":1189,"surah":8,"ayah":29,"verse":"O People who Believe! If you fear Allah, He will bestow upon you (the criterion) that with which you will separate the truth from falsehood, and He will unburden your misdeeds and forgive you; and Allah is the Extremely Munificent."},"1190":{"id":1190,"surah":8,"ayah":30,"verse":"And remember O dear Prophet when the disbelievers were scheming against you to either imprison you, or to kill you or to banish you; and they were scheming, and Allah was making His secret plan; and Allah\u2019s secret plan is the best."},"1191":{"id":1191,"surah":8,"ayah":31,"verse":"And when Our verses are recited to them they say, \u201cYes, we have heard \u2013 if we wanted we could also say something like this \u2013 these are nothing but stories of former people!\u201d"},"1192":{"id":1192,"surah":8,"ayah":32,"verse":"And when they said, \u201cO Allah! If this (the Qur\u2019an) is really the truth from You, then shower upon us a rain of stones from the sky, or bring upon us some painful punishment.\u201d"},"1193":{"id":1193,"surah":8,"ayah":33,"verse":"And it is not for Allah to punish them while you O dear Prophet (Mohammed \u2013 peace and blessings be upon him) are amongst them; and Allah will not punish them as long as they are seeking forgiveness. (Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is a mercy unto mankind.)"},"1194":{"id":1194,"surah":8,"ayah":34,"verse":"And what is with them that Allah should not punish them, whereas in fact they prevent from the Sacred Mosque and they are not worthy (of being the custodians) of it; only the pious are its befitting custodians, but most of them do not have knowledge."},"1195":{"id":1195,"surah":8,"ayah":35,"verse":"And their prayer near the Kaa\u2019bah is nothing except whistling and clapping; \u201cSo now taste the punishment \u2013 the result of your disbelief.\u201d"},"1196":{"id":1196,"surah":8,"ayah":36,"verse":"Indeed the disbelievers spend their wealth in order to prevent people from the way of Allah; so they will spend it now, and then regret over it, then they will be defeated; and the disbelievers will be gathered towards hell."},"1197":{"id":1197,"surah":8,"ayah":37,"verse":"In order that Allah may separate the filthy from the pure, and placing the filthy atop one another, make a heap and throw them into hell; it is they who are the losers."},"1198":{"id":1198,"surah":8,"ayah":38,"verse":"Say to the disbelievers that if they desist, what has passed will be forgiven to them; and if they do the same, the tradition of former people has already passed."},"1199":{"id":1199,"surah":8,"ayah":39,"verse":"And fight them until no mischief remains and the entire religion is only for Allah; then if they desist, Allah sees all what they do."},"1200":{"id":1200,"surah":8,"ayah":40,"verse":"And if they turn away, then know that Allah is your Master; so what an Excellent Master and what an Excellent Supporter!"},"1201":{"id":1201,"surah":8,"ayah":41,"verse":"And know that whatever you take as war booty, a fifth of that belongs to Allah and His Noble Messenger, and to relatives, and orphans, and the needy, and the traveller \u2013 if you have accepted faith in Allah and what We sent down to Our bondman on the decisive day \u2013 the day when two armies had met; and Allah is Able to do all things."},"1202":{"id":1202,"surah":8,"ayah":42,"verse":"When you were on the near bank and the disbelievers on the far bank, and the caravan below you; and had you made an agreement between one another, you would have failed to reach on the appointed time \u2013 but this is in order that Allah may complete a thing that must be done \u2013 that he who dies may die by a clear proof and he who lives may live by a clear proof; and indeed Allah is surely, All Hearing, All Knowing."},"1203":{"id":1203,"surah":8,"ayah":43,"verse":"When O dear Prophet, Allah used to show the disbelievers in your dream as only a few*; and O Muslims, had He shown them to you as many, you would have certainly lost courage and disputed over the affair, but Allah rescued (you); indeed He knows what lies within the hearts. (* They numbered more but their actual strength was equal to only a few.)"},"1204":{"id":1204,"surah":8,"ayah":44,"verse":"And when at the time of fighting He made the disbelievers seem few to you, and you as few in their sight, in order for Allah to conclude the matter that must be done; and towards Allah is the return of all matters."},"1205":{"id":1205,"surah":8,"ayah":45,"verse":"O People who Believe! When you meet an army, hold firm and remember Allah profusely, in order that you succeed."},"1206":{"id":1206,"surah":8,"ayah":46,"verse":"And obey Allah and His Noble Messenger, and do not dispute with one another for you will lose courage again and your strength will be lost, and patiently endure; indeed Allah is with those who patiently endure."},"1207":{"id":1207,"surah":8,"ayah":47,"verse":"And do not be like those who came out from their houses proudly, and to be seen by men, and they prevent people from Allah\u2019s way; and all their actions are within Allah\u2019s control."},"1208":{"id":1208,"surah":8,"ayah":48,"verse":"And when Satan made their deeds seem good in their sight and said, \u201cThis day no one can overpower you, and you are under my protection\u201d; so when the two armies came face to face, he scrambled back and said, \u201cI am unconcerned with you \u2013 I can see what is not visible to you \u2013 I fear Allah\u201d; and Allah\u2019s punishment is severe."},"1209":{"id":1209,"surah":8,"ayah":49,"verse":"When the hypocrites and those in whose hearts is a disease were saying, \u201cThese Muslims are proud of their religion\u201d; and whoever trusts Allah, then indeed Allah is Almighty, Wise."},"1210":{"id":1210,"surah":8,"ayah":50,"verse":"And if you see the angels when they are removing the souls of the disbelievers, hitting them on their faces and their backs; \u201cTaste more of the punishment of the fire!\u201d (Punishment in the grave is proven by this verse.)"},"1211":{"id":1211,"surah":8,"ayah":51,"verse":"\u201cThis is the recompense of what your own hands have sent ahead, and Allah does not oppress His bondmen.\u201d"},"1212":{"id":1212,"surah":8,"ayah":52,"verse":"Like the ways of Firaun\u2019s people, and those before them; they disbelieved in the signs of Allah \u2013 therefore Allah seized them on account of their sins; indeed Allah is Most Powerful, Severe in Punishing."},"1213":{"id":1213,"surah":8,"ayah":53,"verse":"This is because Allah does not change the favour He has bestowed upon any people until they first change themselves, and indeed Allah is All Hearing, All Knowing."},"1214":{"id":1214,"surah":8,"ayah":54,"verse":"Like the ways of Firaun\u2019s people and those before them; they denied the signs of Allah \u2013 We therefore destroyed them on account of their sins and We drowned the people of Firaun; and they all were unjust."},"1215":{"id":1215,"surah":8,"ayah":55,"verse":"Indeed the worst beasts in the sight of Allah are the people who disbelieve and do not accept faith."},"1216":{"id":1216,"surah":8,"ayah":56,"verse":"Those with whom you made a treaty, then they break their agreement each time and do not fear."},"1217":{"id":1217,"surah":8,"ayah":57,"verse":"So if you find them in battle, kill them in a manner which makes those behind them scamper back, in the hope that they may learn a lesson."},"1218":{"id":1218,"surah":8,"ayah":58,"verse":"And if you apprehend treachery from a nation, then throw back their treaty towards them in reciprocity; indeed Allah does not like the treacherous."},"1219":{"id":1219,"surah":8,"ayah":59,"verse":"And never may the disbelievers pride that they have escaped; indeed they can never defeat."},"1220":{"id":1220,"surah":8,"ayah":60,"verse":"And keep ready for them the maximum of forces you can and the maximum number of horses you can keep tethered, in order to instil awe in the hearts of those who are the enemies of Allah and who are your enemies, and in the hearts of some others whom you do not know; Allah knows them; and whatever you spend in Allah's cause will be repaid to you in full and you will never be in a loss."},"1221":{"id":1221,"surah":8,"ayah":61,"verse":"And if they incline towards peace, you too lean towards it, and trust Allah; indeed He only is the All Hearing, the All Knowing."},"1222":{"id":1222,"surah":8,"ayah":62,"verse":"And if they wish to deceive you, then indeed Allah is Sufficient for you; it is He Who has given you strength, with His help and with the Muslims."},"1223":{"id":1223,"surah":8,"ayah":63,"verse":"And has created harmony among their hearts; if you had spent all that is in the earth you could not have created harmony among their hearts, but Allah has created harmony among them; indeed He only is Almighty, Wise."},"1224":{"id":1224,"surah":8,"ayah":64,"verse":"O Herald of the Hidden! Allah is Sufficient for you and for all these Muslims who follow you."},"1225":{"id":1225,"surah":8,"ayah":65,"verse":"O Herald of the Hidden! Urge the believers to fight; if there are twenty persevering men among you, they shall overcome two hundred; and if there are a hundred among you, they shall overcome a thousand disbelievers because the disbelievers are a people who do not have sense."},"1226":{"id":1226,"surah":8,"ayah":66,"verse":"So now Allah has made an ease upon you and He knows that you are weak; so if there are a hundred persevering men among you, they shall overcome two hundred; and if there are a thousand among you, they shall overcome two thousand by the command of Allah; and Allah is with those who patiently endure."},"1227":{"id":1227,"surah":8,"ayah":67,"verse":"It does not befit any Prophet to capture the disbelievers alive until he has profusely shed their blood in the land; you people desire the wealth of this world; and Allah wills the Hereafter, and Allah is Almighty, Wise."},"1228":{"id":1228,"surah":8,"ayah":68,"verse":"Had Allah not pre-destined a matter then, O Muslims, a terrible punishment would have come upon you due to the ransom you took from the disbelievers."},"1229":{"id":1229,"surah":8,"ayah":69,"verse":"Therefore benefit from the booty you have received, lawful and good; and keep fearing Allah; indeed Allah is Oft Forgiving, Most Merciful."},"1230":{"id":1230,"surah":8,"ayah":70,"verse":"O Herald of the Hidden! Say to the captives whom you possess, \u201cIf Allah finds any goodness in your hearts, He will give you better than what has been taken from you, and will forgive you; and Allah is Oft Forgiving, Most Merciful.\u201d"},"1231":{"id":1231,"surah":8,"ayah":71,"verse":"And if they wish to deceive you, they have already been disloyal to Allah, because of which He has given these disbelievers in your control; and Allah is All Knowing, Wise."},"1232":{"id":1232,"surah":8,"ayah":72,"verse":"Indeed those who accepted faith and left their homes and belongings for Allah, and fought with their wealth and their lives in Allah's cause, and those who gave shelter and provided help, are the heirs of one another; and those who believed but did not leave their homes \u2013 you have no right in their estates until they migrate; and if they seek help from you in the matter of religion then it is your duty to provide help, except against the people between whom and you is a treaty; and Allah sees your deeds."},"1233":{"id":1233,"surah":8,"ayah":73,"verse":"And the disbelievers are the heirs of one another \u2013 if you do not do so, there will be turmoil in the land and a great chaos."},"1234":{"id":1234,"surah":8,"ayah":74,"verse":"And those who believed and migrated and fought in Allah's cause, and those who gave shelter and provided help \u2013 it is they who are the true believers; for them is pardon, and an honourable sustenance."},"1235":{"id":1235,"surah":8,"ayah":75,"verse":"And those who afterwards believed and migrated and fought along with you \u2013 they too are from among you; and family members (blood relations) are nearer to one another in the Book of Allah; indeed Allah knows everything."},"1236":{"id":1236,"surah":9,"ayah":1,"verse":"Severance of ties is proclaimed by Allah and on behalf of His Noble Messenger, towards the polytheists with whom you had a treaty."},"1237":{"id":1237,"surah":9,"ayah":2,"verse":"Travel freely in the land for four months, and bear in mind that you cannot escape from Allah, and that Allah will humiliate the disbelievers."},"1238":{"id":1238,"surah":9,"ayah":3,"verse":"And proclaim from Allah and His Noble Messenger to all men on the day of the Great Pilgrimage (Haj) that Allah is disgusted with the polytheists, and so is His Noble Messenger; so if you repent it is better for you; but if you turn away, then know that you cannot escape from Allah; and give the disbelievers the glad tidings of a painful punishment."},"1239":{"id":1239,"surah":9,"ayah":4,"verse":"Other than those polytheists with whom you had a treaty, and they have not diminished anything from your treaty nor supported anyone against you \u2013 therefore fulfil their treaty up to the appointed term; indeed Allah befriends the pious."},"1240":{"id":1240,"surah":9,"ayah":5,"verse":"Then when the sacred months have passed, slay the polytheists wherever you find them, and catch them and make them captive, and wait in ambush for them at every place; then if they repent and keep the prayer established and pay the charity, leave their way free; indeed Allah is Oft Forgiving, Most Merciful."},"1241":{"id":1241,"surah":9,"ayah":6,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him), if a polytheist seeks your protection, give him protection so that he may hear the Word of Allah, and then transport him to his place of safety; this is because they are an unwise people."},"1242":{"id":1242,"surah":9,"ayah":7,"verse":"How can there be a treaty with Allah and with His Noble Messenger for the polytheists, except for those with whom you made a treaty near the Sacred Mosque? So as long as they remain firm on the treaty for you, you too remain firm for them; indeed Allah is pleased with the pious."},"1243":{"id":1243,"surah":9,"ayah":8,"verse":"Therefore how \u2013 when they are such that if they gain control over you, they would not have regard for any relations nor for any treaty? They please you with their mouths whereas their hearts contain rejection; and most of them are disobedient."},"1244":{"id":1244,"surah":9,"ayah":9,"verse":"They exchanged the verses of Allah for an abject price, therefore prevented from His way; indeed what they do is extremely evil."},"1245":{"id":1245,"surah":9,"ayah":10,"verse":"In respect of the Muslims, they do not keep regard for any relations nor any pacts; it is they who are the rebels."},"1246":{"id":1246,"surah":9,"ayah":11,"verse":"Then if they repent and keep the prayer established and pay the charity, they are your brothers in religion; and we explain Our verses in detail for the people of knowledge."},"1247":{"id":1247,"surah":9,"ayah":12,"verse":"And if they break their promises after making a treaty and malign your religion, then fight the leaders of disbelief \u2013 indeed their promises are nothing \u2013 in the hope that they may desist."},"1248":{"id":1248,"surah":9,"ayah":13,"verse":"Will you not fight the people who broke their promises, and intended to expel the Noble Messenger whereas they had started it? Do you fear them? So Allah has more right that you should fear Him, if you have faith."},"1249":{"id":1249,"surah":9,"ayah":14,"verse":"So fight them \u2013 Allah will punish them at your hands, and He will disgrace them and assist you over them, and He will soothe the hearts of the believers."},"1250":{"id":1250,"surah":9,"ayah":15,"verse":"And He will remove the anxiety of their hearts; and Allah may accept the repentance of whomever He wills; and Allah is All Knowing, Wise."},"1251":{"id":1251,"surah":9,"ayah":16,"verse":"Are you under the illusion that you would be left just like this, whereas Allah has not yet made known those of you who will fight and not confide their secrets with anyone except Allah and His Noble Messenger and the Muslims? And Allah is Well Aware of your deeds."},"1252":{"id":1252,"surah":9,"ayah":17,"verse":"It does not befit the polytheists to assemble in Allah\u2019s mosques after themselves bearing witness of their disbelief; in fact all their deeds are wasted; and they will remain in the fire forever."},"1253":{"id":1253,"surah":9,"ayah":18,"verse":"Only those enliven the mosques of Allah who believe in Allah and the Last Day and establish prayer and pay the obligatory charity and fear none except Allah \u2013 so it is likely that they will be among the people of guidance."},"1254":{"id":1254,"surah":9,"ayah":19,"verse":"So have they taken the quenching of the pilgrims\u2019 thirst and servicing of the Sacred Mosque as equal (in merit) to him who accepted faith in Allah and the Last Day, and fought in Allah\u2019s way? They are not equal before Allah; and Allah does not guide the unjust."},"1255":{"id":1255,"surah":9,"ayah":20,"verse":"Those who accepted faith, and left their homes and fought with their wealth and their lives in Allah\u2019s way have a greater rank before Allah; and it is they who have succeeded."},"1256":{"id":1256,"surah":9,"ayah":21,"verse":"Their Lord gives them the glad tidings of His mercy and His pleasure, and the Gardens in which are everlasting favours for them."},"1257":{"id":1257,"surah":9,"ayah":22,"verse":"They will abide in it for ever and ever; indeed with Allah is the great reward."},"1258":{"id":1258,"surah":9,"ayah":23,"verse":"O People who Believe! Do not consider your fathers and your brothers as your friends if they prefer disbelief over faith; and whoever among you befriends them \u2013 then it is he who is the unjust."},"1259":{"id":1259,"surah":9,"ayah":24,"verse":"Say, \u201cIf your fathers, and your sons, and your brothers, and your wives, and your tribe, and your acquired wealth, and the trade in which you fear a loss, and the houses of your liking \u2013 if all these are dearer to you than Allah and His Noble Messenger and fighting in His way, then wait until Allah brings about His command; and Allah does not guide the sinful.\u201d"},"1260":{"id":1260,"surah":9,"ayah":25,"verse":"Indeed Allah helped you on many occasions \u2013 and on the day of Huneyn \u2013 when you prided in your multitude, so it did not benefit you at all, and the earth despite being vast became restricted for you \u2013 then you turned back and returned."},"1261":{"id":1261,"surah":9,"ayah":26,"verse":"Then Allah sent down His calm upon His Noble Messenger and upon the Muslims, and sent down armies you did not see, and punished the disbelievers; and such is the reward of the deniers."},"1262":{"id":1262,"surah":9,"ayah":27,"verse":"Then afterwards Allah will give repentance to whomever He wills; and Allah is Oft Forgiving, Most Merciful."},"1263":{"id":1263,"surah":9,"ayah":28,"verse":"O People who Believe! The polytheists are utterly filthy*; so after this year do not let them come near the Sacred Mosque; and if you fear poverty**, then Allah will soon make you wealthy with His grace, if He wills; indeed Allah is All Knowing, Wise. (* Filthy in body and soul. **Due to loss of trade.)"},"1264":{"id":1264,"surah":9,"ayah":29,"verse":"Fight against the People given the Book(s) who do not accept faith in Allah and the Last Day, and who do not treat as forbidden what is forbidden by Allah and by His Noble Messenger, and who do not follow the true religion, until they pay the tariff with their own hands with humiliation."},"1265":{"id":1265,"surah":9,"ayah":30,"verse":"And the Jews said, \u201cUzair is the son of Allah\u201d, and the Christians said \u201cThe Messiah is the son of Allah\u201d; they utter this from their own mouths; they speak like the former disbelievers; may Allah kill them; where are they reverting!"},"1266":{"id":1266,"surah":9,"ayah":31,"verse":"They have taken their rabbis and their monks as Gods besides Allah and (also) Messiah the son of Maryam; and they were not commanded except to worship only One God \u2013 Allah; none is worthy of worship except Him; Purity is to Him from all that they ascribe as partners (to Him)."},"1267":{"id":1267,"surah":9,"ayah":32,"verse":"They wish to extinguish the light of Allah with their mouths, but Allah will not agree except that He will perfect His light, even if the disbelievers get annoyed."},"1268":{"id":1268,"surah":9,"ayah":33,"verse":"It is He Who has sent His Noble Messenger with guidance and the true religion, in order to prevail over all other religions \u2013 even if the polytheists get annoyed."},"1269":{"id":1269,"surah":9,"ayah":34,"verse":"O People who Believe! Indeed many of the (Jewish) rabbis and the (Christian) monks unjustly devour people's wealth and prevent from Allah\u2019s way; and those who hoard up gold and silver and do not spend it in Allah\u2019s way \u2013 so them give the glad tidings of a painful punishment."},"1270":{"id":1270,"surah":9,"ayah":35,"verse":"The day when it will be heated in the fire of hell, and their foreheads and their sides and their backs will be branded with them; \u201cHere is what you hoarded for yourselves; so now taste the joy of your hoarding!\u201d"},"1271":{"id":1271,"surah":9,"ayah":36,"verse":"Indeed the number of months before Allah is twelve \u2013 in the Book of Allah \u2013 since the day He created the heavens and the earth, of which four are sacred; this the straight religion; so do not wrong yourselves in those months; and constantly fight against the polytheists as they constantly fight against you; and know well that Allah is with the pious."},"1272":{"id":1272,"surah":9,"ayah":37,"verse":"Their postponing of the months is nothing but furtherance in disbelief \u2013 the disbelievers are misled by it \u2013 they decree it lawful in one year and regard it forbidden in another year, in order to equate to the number of the months which Allah has made sacred, and to make lawful what Allah has forbidden; their evil deeds seem good in their sight; and Allah does not guide the disbelievers."},"1273":{"id":1273,"surah":9,"ayah":38,"verse":"O People who Believe! What is the matter with you, that when it is said to you, \u201cMigrate in Allah's cause\u201d, you sit on the ground with heaviness? Have you preferred this worldly life over the Hereafter? And the wealth of the life of this world, in comparison with the Hereafter, is but only a little."},"1274":{"id":1274,"surah":9,"ayah":39,"verse":"If you do not migrate, He will afflict you with a painful punishment and bring other people in your stead and you will not be able to harm Him in the least; and Allah is Able to do all things."},"1275":{"id":1275,"surah":9,"ayah":40,"verse":"If you do not help him (Prophet Mohammed \u2013 peace and blessings be upon him), Allah has helped him \u2013 when he had to go forth due to the mischief of the disbelievers, just as two men* \u2013 when they were in the cave, when he was saying to his companion \u201cDo not grieve; indeed Allah is with us\u201d; then Allah caused His calm to descend upon him and helped him with armies you did not see, and disgraced the word of the disbelievers; and Allah\u2019s Word is supreme; and Allah is the Almighty, the Wise. (* The Holy Prophet migrated only with S. Abu Bakr (who later became the first caliph) as his sole companion.)"},"1276":{"id":1276,"surah":9,"ayah":41,"verse":"Migrate \u2013 whether willingly or with a heavy heart and fight in Allah's cause with your wealth and your lives; this is better for you, if you realise."},"1277":{"id":1277,"surah":9,"ayah":42,"verse":"Had there been some wealth near at hand or a short journey, they would have certainly accompanied you, but the difficult path became very distant for them; and they will now swear by Allah that \u201cHad we been able, we would have surely accompanied you\u201d; they destroy their own souls; and Allah knows that undoubtedly, they are indeed liars."},"1278":{"id":1278,"surah":9,"ayah":43,"verse":"May Allah forgive you*; why did you permit them** until the truthful ones had been manifested to you and the liars been exposed? (* This is an expression of love for the Holy Prophet \u2013 peace and blessings be upon him. ** The hypocrites had been permitted to stay back from the holy war.)"},"1279":{"id":1279,"surah":9,"ayah":44,"verse":"And those who believe in Allah and the Last Day will not seek exemption from you for not fighting with their wealth and their lives; and Allah well knows the pious."},"1280":{"id":1280,"surah":9,"ayah":45,"verse":"Only those ask for such an exemption from you who do not believe in Allah and the Last Day, and whose hearts are in doubt \u2013 so they waver in their doubts."},"1281":{"id":1281,"surah":9,"ayah":46,"verse":"And if going forth were acceptable to them, they would have made preparations for it, but Allah Himself disliked their getting up (to fight) so He filled them with laze and it was said \u201cContinue sitting with those who remain seated.\u201d"},"1282":{"id":1282,"surah":9,"ayah":47,"verse":"If they had gone forth among you, you would then not gain any increase from them except trouble, and seeking to cause turmoil they would run rumours among you; and their spies are among you; and Allah well knows the unjust."},"1283":{"id":1283,"surah":9,"ayah":48,"verse":"Indeed they had sought to cause turmoil at the outset, and O dear Prophet the scheme turned otherwise* for you, so much so that the truth came and the command of Allah appeared, and they disliked it. (* In your favour.)"},"1284":{"id":1284,"surah":9,"ayah":49,"verse":"And among them is one who requests you that, \u201cGrant me exemption (from fighting) and do not put me to test\u201d; pay heed! They have indeed fallen into trial; and indeed hell surrounds the disbelievers."},"1285":{"id":1285,"surah":9,"ayah":50,"verse":"If good befalls you they dislike it; and were some calamity to befall you, they would say, \u201cWe had resolved our matters in advance\u201d, and would turn away rejoicing."},"1286":{"id":1286,"surah":9,"ayah":51,"verse":"Say, O dear Prophet \u201cNothing shall befall us except what Allah has destined for us; He is our Master; and the Muslims must rely only on Allah.\u201d"},"1287":{"id":1287,"surah":9,"ayah":52,"verse":"Say, \u201cWhat do you wait for to happen to us, except one of the two good things?* And for you, we look forward to Allah afflicting you with a punishment from Himself or by our hands; so wait \u2013 we too await with you.\u201d (*Death in Allah\u2019s way or victory.)"},"1288":{"id":1288,"surah":9,"ayah":53,"verse":"Say, \u201cSpend willingly or with a heavy heart, it will never be accepted from you; indeed you are a disobedient people.\u201d"},"1289":{"id":1289,"surah":9,"ayah":54,"verse":"And their spending was not stopped being accepted, except because they disbelieved in Allah and His Noble Messenger, and they come to prayer with heavy hearts, and they do not spend except unwillingly."},"1290":{"id":1290,"surah":9,"ayah":55,"verse":"So let not their riches or their children surprise you; Allah only intends to punish them in the life of this world with these things and that they die only as disbelievers."},"1291":{"id":1291,"surah":9,"ayah":56,"verse":"And they (the hypocrites) swear by Allah that they are from among you (Muslims); and they are not from among you \u2013 however those people are afraid."},"1292":{"id":1292,"surah":9,"ayah":57,"verse":"If they find some refuge, or caves, or a place to hide, they will break the bonds and return there."},"1293":{"id":1293,"surah":9,"ayah":58,"verse":"And among them is one who slanders you regarding the distribution of charity; so if they receive some of it they would be happy \u2013 and if not, thereupon they get displeased!"},"1294":{"id":1294,"surah":9,"ayah":59,"verse":"How excellent it would be, if they were pleased with what Allah and His Noble Messenger had given them and said, \u201cAllah suffices us; Allah will now give us by His munificence, and (so will) Allah\u2019s Noble Messenger \u2013 and towards Allah only are we inclined.\u201d"},"1295":{"id":1295,"surah":9,"ayah":60,"verse":"The obligatory charity* is only for the destitute and the really needy, and those who collect it, and for those in whose hearts the love of Islam needs to be instilled**, and to free slaves, and to debtors, and in Allah's cause***, and to the traveller; this is decreed by Allah; and Allah is All Knowing, Wise. (* This applies only to Zakat. ** The new convert to Islam. *** To the fighter having no provisions for holy war.)"},"1296":{"id":1296,"surah":9,"ayah":61,"verse":"And among them (the hypocrites) are those who trouble* the Herald of the Hidden (the Prophet) and say, \u201cHe is only ears**\u201d; say \u201cHe is a listener for your good, he believes in Allah and believes in what the Muslims say, and is a mercy for the Muslims among you\u201d; and for those who trouble the Noble Messenger of Allah, is a painful punishment. (*To disrespect \/ trouble the Holy Prophet \u2013 peace and blessings be upon him \u2013 is blasphemy. **He believes whatever he is told.)"},"1297":{"id":1297,"surah":9,"ayah":62,"verse":"They swear by Allah in your presence in order to please you; whereas Allah \u2013 and His Noble Messenger \u2013 had more right that they should have pleased Him if they had faith."},"1298":{"id":1298,"surah":9,"ayah":63,"verse":"Do they not know that for one who opposes Allah and His Noble Messenger, is the fire of hell, to remain in it forever? This is the greatest humiliation."},"1299":{"id":1299,"surah":9,"ayah":64,"verse":"The hypocrites fear for a chapter being revealed regarding them, which may disclose what is hidden in their hearts; say, \u201cKeep mocking; Allah will certainly disclose what you fear.\u201d"},"1300":{"id":1300,"surah":9,"ayah":65,"verse":"And if you ask them, they will say, \u201cWe were just having fun and pastime\u201d; say, \u201cWhat! You mock at Allah and His verses and His Noble Messenger?\u201d"},"1301":{"id":1301,"surah":9,"ayah":66,"verse":"\u201cDo not feign excuses, you have turned disbelievers after becoming Muslims\u201d; if We forgive some of you*, We shall punish others because they were guilty. (* One who kept quiet and later repented.)"},"1302":{"id":1302,"surah":9,"ayah":67,"verse":"The hypocrite men and women are all the same; enjoining wrong and forbidding right, and being tight-fisted*; they have forgotten Allah, so Allah has forsaken them; indeed the hypocrites \u2013 it is they who are really disobedient. (* Not spending in Allah's cause)"},"1303":{"id":1303,"surah":9,"ayah":68,"verse":"Allah has promised the hypocrite men and hypocrite women and the disbelievers, the fire of hell in which they will remain forever; that is sufficient for them; and Allah\u2019s curse is upon them; and for them is a never ending punishment."},"1304":{"id":1304,"surah":9,"ayah":69,"verse":"Like those who were before you \u2013 they were mightier than you in strength, and had more wealth and children than you; so they spent their portion \u2013 you spent your portion just as those before you spent their portion and you fell into shame (sin) like they had fallen into shame; their deeds have been wasted in the world and in the Hereafter; it is they who are the losers."},"1305":{"id":1305,"surah":9,"ayah":70,"verse":"Did not the news of those before them reach them \u2013 the people of Nooh, and the A\u2019ad, and the Thamud \u2013 the people of Ibrahim, the people of Madyan and the dwellings that were overturned? Their Noble Messengers had brought clear proofs to them; so it did not befit Allah\u2019s Majesty to oppress them, but in fact they wronged themselves."},"1306":{"id":1306,"surah":9,"ayah":71,"verse":"And the Muslim men and Muslim women are the friends of one another; enjoining right and forbidding wrong, and keeping the prayer established and paying the obligatory charity, obeying Allah and His Noble Messenger; these are upon whom Allah will soon have mercy; indeed Allah is the Almighty, the Wise."},"1307":{"id":1307,"surah":9,"ayah":72,"verse":"Allah has promised the Muslim men and Muslim women, Gardens beneath which rivers flow \u2013 they will abide in it forever \u2013 and pure dwellings in Gardens of everlasting stay; and the greatest (reward) is Allah\u2019s pleasure; this is the supreme success."},"1308":{"id":1308,"surah":9,"ayah":73,"verse":"O Herald of the Hidden! Fight against the disbelievers and the hypocrites, and be stern with them; and their destination hell; and what an evil place to return!"},"1309":{"id":1309,"surah":9,"ayah":74,"verse":"And they swear by Allah that they did not say it; whereas indeed they had certainly uttered the words of disbelief, and after having entered Islam turned disbelievers and had wished for what they did not get; and what annoyed them except that Allah, and His Noble Messenger, made them prosperous with His grace? So if they repent, it is better for them; and if they turn away, Allah will afflict them with a painful punishment \u2013 in this world and the Hereafter; and they will have neither a protector nor any supporter in the entire earth."},"1310":{"id":1310,"surah":9,"ayah":75,"verse":"And among them are those who made a covenant with Allah that, \u201cIf He gives us by His munificence, we will surely give charity and surely become righteous.\u201d"},"1311":{"id":1311,"surah":9,"ayah":76,"verse":"Therefore when Allah gave them by His munificence, they hoarded it and turning their faces, went back.* (* Reneged on their promise)."},"1312":{"id":1312,"surah":9,"ayah":77,"verse":"So following this, Allah put hypocrisy in their hearts until the day when they will meet Him \u2013 the result of their breaching the promise made to Allah, and because they lied."},"1313":{"id":1313,"surah":9,"ayah":78,"verse":"Do they not know that Allah knows their secrets and the schemes they whisper, and that Allah is the All Knowing of all the hidden?"},"1314":{"id":1314,"surah":9,"ayah":79,"verse":"Those who find fault in the Muslims who give the charity wholeheartedly and in those who gain nothing except from their own toil \u2013 so they mock at them; Allah will punish them for their mocking; and for them is a painful punishment."},"1315":{"id":1315,"surah":9,"ayah":80,"verse":"Whether you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) ask forgiveness for them* or not ask forgiveness for them; even if you ask forgiveness for them seventy times, Allah will not forgive them; that is because they disbelieved in Allah and His Noble Messenger, and Allah does not guide the sinful. (* for the hypocrites.)"},"1316":{"id":1316,"surah":9,"ayah":81,"verse":"Those who were left behind rejoiced that behind the Noble Messenger of Allah they had remained seated, and they were unwilling to fight in Allah's cause with their lives or their wealth, and said \u201cDo not venture out in the heat\u201d; say, \u201cThe fire of hell is the hottest\u201d; if only they understood!"},"1317":{"id":1317,"surah":9,"ayah":82,"verse":"So they should laugh a little and weep much; the reward of what they used to earn."},"1318":{"id":1318,"surah":9,"ayah":83,"verse":"Then if Allah takes you back to a group of them and they seek permission from you to go out to fight, say to them, \u201cYou shall never go out with me nor ever fight with me against any enemy; you were happy to remain seated for the first time, therefore remain seated with those who stay behind.\u201d"},"1319":{"id":1319,"surah":9,"ayah":84,"verse":"And never offer funeral prayers for any of them* who dies, nor stand by his grave**; indeed they disbelieved in Allah and His Noble Messenger, and they died as sinners. (* It is forbidden to offer funeral prayers for the hypocrites. ** To ask forgiveness for them)."},"1320":{"id":1320,"surah":9,"ayah":85,"verse":"And do not be surprised at their wealth or their children; Allah only wills to torment them with it in this world, and that they pass away upon disbelief."},"1321":{"id":1321,"surah":9,"ayah":86,"verse":"And when a chapter is sent down that \u201cAccept faith in Allah and fight along with His Noble Messenger\u201d, the men of means among them seek exemption from you and say, \u201cLeave us, for us to be with those who sit.\u201d"},"1322":{"id":1322,"surah":9,"ayah":87,"verse":"They preferred to be with the women who stay behind, and their hearts have been sealed, so they do not understand."},"1323":{"id":1323,"surah":9,"ayah":88,"verse":"But the Noble Messenger and those who accepted faith with him, fought with their wealth and lives; and for them only are the virtues (rewards); and it is they who have achieved the goal."},"1324":{"id":1324,"surah":9,"ayah":89,"verse":"Allah has kept ready for them Gardens beneath which rivers flow, abiding in it forever; this is the greatest success."},"1325":{"id":1325,"surah":9,"ayah":90,"verse":"And came the ignorant* who make excuses seeking exemption, and those who lied to Allah and His Noble Messenger remained seated; a painful punishment will soon reach the disbelievers among them. (* of faith)"},"1326":{"id":1326,"surah":9,"ayah":91,"verse":"There is no reproach upon the old nor upon the sick nor upon those who do not have the means to spend, provided they remain faithful to Allah and His Noble Messenger; and there is no way of reproach against the virtuous; and Allah is Oft Forgiving, Most Merciful."},"1327":{"id":1327,"surah":9,"ayah":92,"verse":"Nor against those who humbly present themselves before you in order that you provide them a mount, and receive an answer from you that \u201cI do not have any beast to carry you\u201d \u2013 and so they turn back with eyes overflowing with tears, due to the sorrow that they could not find the means to spend."},"1328":{"id":1328,"surah":9,"ayah":93,"verse":"The way (of reproach) is only against those who seek exemption from you although they are rich; they preferred to be with the women who stay behind \u2013 and Allah has sealed their hearts, so they do not know anything."},"1329":{"id":1329,"surah":9,"ayah":94,"verse":"They will make excuses to you when you return to them; say, \u201cDo not make excuses \u2013 we shall never believe you \u2013 Allah has given us your tidings; and Allah and His Noble Messenger will now see your deeds, and then you will return to Him Who knows everything, the hidden and the visible \u2013 He will inform you of all what you used to do.\u201d"},"1330":{"id":1330,"surah":9,"ayah":95,"verse":"They will now swear by Allah before you, when you return to them, in order that you do not pay attention to them; so do not bother about them; they are indeed filthy; and their destination is hell; the reward of what they used to earn."},"1331":{"id":1331,"surah":9,"ayah":96,"verse":"They swear before you that you may be pleased with them; so if you become pleased with them, then indeed Allah will never be pleased with the sinful."},"1332":{"id":1332,"surah":9,"ayah":97,"verse":"The ignorant are more severe in disbelief and hypocrisy, and deserve to remain ignorant of the commands which Allah has revealed to His Noble Messenger; and Allah is All Knowing, Wise."},"1333":{"id":1333,"surah":9,"ayah":98,"verse":"And some of the ignorant are those who when spending in Allah's cause consider it a ransom, and await the coming of the cycles (of misfortunes) upon you; upon them only is the evil cycle of misfortune; and Allah is All Hearing, All Knowing."},"1334":{"id":1334,"surah":9,"ayah":99,"verse":"And some villagers are those who believe in Allah and the Last Day, and consider the spending as the means of obtaining proximity to Allah and obtaining the prayers of the Noble Messenger; pay heed! Yes indeed it is the means of proximity for them; Allah will soon admit them into His mercy; indeed Allah is Oft Forgiving, Most Merciful."},"1335":{"id":1335,"surah":9,"ayah":100,"verse":"And leading everyone, the first are the Muhajirs* and the Ansar**, and those who followed them with virtue \u2013 Allah is pleased with them and they are pleased with Him, and He has kept ready for them Gardens beneath which rivers flow, to abide in it for ever and ever; this is the greatest success. (* The immigrants. **Those who helped the immigrants.)"},"1336":{"id":1336,"surah":9,"ayah":101,"verse":"And some of the illiterates around you are hypocrites; and some of the people of Medinah; hypocrisy has become ingrained in them; you do not know them*; We know them; We shall soon punish them twice** \u2013 they will then be consigned towards the terrible punishment.*** (*Until now or as well as We do. ** In life and in the grave *** of hell.)"},"1337":{"id":1337,"surah":9,"ayah":102,"verse":"And there are others who have acknowledged their sins and mixed a good deed with another that was bad; it is likely that Allah will accept their repentance; indeed Allah is Oft Forgiving, Most Merciful."},"1338":{"id":1338,"surah":9,"ayah":103,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him) take the obligatory charity from their wealth, by which you may cleanse them and make them pure, and pray in their favour; indeed your prayer is the contentment of their hearts; and Allah is All Hearing, All Knowing."},"1339":{"id":1339,"surah":9,"ayah":104,"verse":"Do they not know that Allah only accepts repentance of His bondmen and He takes the charity* and that Allah only is the Most Acceptor of Repentance, the Most Merciful? (* into His control.)"},"1340":{"id":1340,"surah":9,"ayah":105,"verse":"And say \u201cKeep on with your works \u2013 Allah will now see your deeds, and so will His Noble Messenger and the Muslims; and soon you will return to the One Who knows everything \u2013 the hidden and the visible \u2013 so He will inform you of what you used to do.\u201d"},"1341":{"id":1341,"surah":9,"ayah":106,"verse":"And some are kept waiting for Allah\u2019s command \u2013 He may punish them or accept their repentance; and Allah is All Knowing, Wise."},"1342":{"id":1342,"surah":9,"ayah":107,"verse":"And those (hypocrites) who built a mosque to cause harm, and due to disbelief, and in order to cause divisions among the Muslims, and to await the one who is at the outset an opponent of Allah and His Noble Messenger; and they will surely swear that \u201cWe wished only good\u201d; and Allah is witness that they are indeed liars."},"1343":{"id":1343,"surah":9,"ayah":108,"verse":"Never stand (for worship) in that mosque*; indeed the mosque** that has been founded on piety from the very first day deserves that you should stand in it; in it are the people who wish to thoroughly cleanse themselves; and Allah loves the clean. (*The mosque built by the hypocrites. ** The mosque at Quba, built by the Holy Prophet and his companions. The merit of praying 2 Raka\u2019 Nawafil in it is equal to the reward of an Umrah.)"},"1344":{"id":1344,"surah":9,"ayah":109,"verse":"So is one who established his foundation upon the fear of Allah and upon His pleasure better, or the one who laid his foundation upon the brink of a falling precipice, so it fell along with him into the fire of hell? And Allah does not guide the unjust."},"1345":{"id":1345,"surah":9,"ayah":110,"verse":"The building which they erected will constantly keep disturbing their hearts unless their hearts are torn to pieces; and Allah is All Knowing, Wise."},"1346":{"id":1346,"surah":9,"ayah":111,"verse":"Indeed Allah has purchased from the Muslims their lives and their wealth in exchange of Paradise for them; fighting in Allah's cause, slaying and being slain; a true promise incumbent upon His mercy, (mentioned) in the Taurat and the Injeel and the Qur\u2019an; who fulfils His promise better than Allah? Therefore rejoice upon your deal that you have made with Him; and this is the great success."},"1347":{"id":1347,"surah":9,"ayah":112,"verse":"Those who repent, those who worship, those who praise, those who fast, those who bow, those who prostrate, those who show right and forbid wrong and those who keep the limits of Allah in sight; and give glad tidings to the Muslims."},"1348":{"id":1348,"surah":9,"ayah":113,"verse":"It does not befit the Prophet and those who believe, to pray for the forgiveness of polytheists even if they may be their relatives, after it has become clear to them that they are the people of hell."},"1349":{"id":1349,"surah":9,"ayah":114,"verse":"And the seeking of forgiveness for his father (paternal uncle) by Ibrahim was only because of a promise he had made to him; then when it became clear to him that he was an enemy of Allah, Ibrahim broke off ties with him; indeed Ibrahim is surely very soft-hearted, most forbearing."},"1350":{"id":1350,"surah":9,"ayah":115,"verse":"And it does not befit Allah\u2019s Majesty to send a nation astray after He has guided them until He has made clear to them what they should avoid; indeed Allah knows everything."},"1351":{"id":1351,"surah":9,"ayah":116,"verse":"Indeed for Allah only is the kingship of the heavens and the earth; He gives life and He gives death; and other than Allah, you have neither a Protector nor any Supporter."},"1352":{"id":1352,"surah":9,"ayah":117,"verse":"Indeed Allah\u2019s mercy inclined towards the Herald of the Hidden, and the Muhajirs and the Ansar who stood by him in the time of hardship, after it was likely that the hearts of a group among them would turn away \u2013 He then inclined towards them with mercy; indeed He is Most Compassionate, Most Merciful upon them."},"1353":{"id":1353,"surah":9,"ayah":118,"verse":"And also upon the three who were kept in waiting; when the earth, vast as it is, was restricted for them, and they became weary of their own lives and became certain that there is no refuge from Allah except with Him; He then accepted their repentance in order that they remain repentant; indeed Allah is the Most Acceptor of Repentance, the Most Merciful."},"1354":{"id":1354,"surah":9,"ayah":119,"verse":"O People who Believe! Fear Allah, and be with the truthful."},"1355":{"id":1355,"surah":9,"ayah":120,"verse":"It did not befit the people of Medinah and the people of the villages around them, to stay behind the Noble Messenger of Allah, nor to consider their own lives dearer than his life; that is because the thirst or the pain or the hunger that afflicts them in Allah's cause, and the step they tread on a place that angers the disbelievers, and whatever harm they cause the enemy \u2013 a good deed is recorded for them in lieu of all of these; indeed Allah does not waste the wages of the virtuous."},"1356":{"id":1356,"surah":9,"ayah":121,"verse":"And whatever they spend, small or great, or any valley they cross \u2013 it is all recorded for them, so that Allah may reward them for their best deeds."},"1357":{"id":1357,"surah":9,"ayah":122,"verse":"And it is not possible for the Muslims that all of them go out; so it should be that a party from each group goes forth in order to gain knowledge in religion, and upon returning they warn their people in the hope that they may avoid."},"1358":{"id":1358,"surah":9,"ayah":123,"verse":"O People who Believe \u2013 fight the disbelievers who are near to you, and let them find severity in you, and know well that Allah is with the pious."},"1359":{"id":1359,"surah":9,"ayah":124,"verse":"And whenever a chapter is sent down, some of them say, \u201cWhose faith among you has this promoted?\u201d So it has promoted the faith of the believers and they are rejoicing!"},"1360":{"id":1360,"surah":9,"ayah":125,"verse":"And for those in whose hearts is a disease, it has added filth to their filth, and they died as disbelievers."},"1361":{"id":1361,"surah":9,"ayah":126,"verse":"Do they not observe that they are tested once or twice every year? Yet they do not repent, nor do they heed advice!"},"1362":{"id":1362,"surah":9,"ayah":127,"verse":"And whenever a chapter is sent down, one of them looks at the other; \u201cIs there someone watching you?\u201d \u2013 and then they turn away; Allah has inverted their hearts because they are a people who do not understand."},"1363":{"id":1363,"surah":9,"ayah":128,"verse":"Indeed there has come to you a Noble Messenger from among you \u2013 your falling into hardship aggrieves him, most concerned for your well being, for the Muslims most compassionate, most merciful."},"1364":{"id":1364,"surah":9,"ayah":129,"verse":"Then if they turn away, say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAllah suffices me; there is no worship except for Him; only Him have I trusted, and He is the Lord Of The Great Throne.\u201d"},"1365":{"id":1365,"surah":10,"ayah":1,"verse":"Alif-Lam-Ra*; these are verses of the Book of wisdom. (*alphabets of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"1366":{"id":1366,"surah":10,"ayah":2,"verse":"Are people amazed that We have sent to a man among them, the divine revelation that \u201cWarn the people and convey glad tidings to the believers that for them with their Lord is the place* of truth\u201d? The disbelievers say, \u201cIndeed he is an open magician.\u201d (* Positions of honour on the Day of Resurrection or in heaven.)"},"1367":{"id":1367,"surah":10,"ayah":3,"verse":"Indeed your Lord is Allah Who created the heavens and the earth in six days, then as befits His Majesty, established Himself upon the Throne* \u2013 He plans all matters; there is no intercessor except after His permission;* such is Allah, your Lord \u2013 therefore worship Him; so do you not ponder? (* Of Control over everything.** Prophet Mohammed (peace and blessings be upon him) will be the first to be granted permission to intercede.)"},"1368":{"id":1368,"surah":10,"ayah":4,"verse":"Towards Him only you all are to return; the promise of Allah; indeed He creates for the first time and then after its extinction creates it again, in order to give those who believe and do good deeds, the reward of justice; and for the disbelievers, boiling water to drink and a painful punishment \u2013 the recompense of their disbelief."},"1369":{"id":1369,"surah":10,"ayah":5,"verse":"It is He Who created the sun radiating and the moon shining and appointed positions for it, for you to know the number of the years, and the account; Allah has not created it except with the truth; He explains the verses in detail for the people of knowledge."},"1370":{"id":1370,"surah":10,"ayah":6,"verse":"Indeed in the alternation of the day and night, and all that Allah has created in the heavens and the earth, are signs for people who are pious."},"1371":{"id":1371,"surah":10,"ayah":7,"verse":"Indeed those who do not expect to meet Us and have preferred the worldly life and are content with it, and those who neglect Our signs, \u2013"},"1372":{"id":1372,"surah":10,"ayah":8,"verse":"Their destination is hell \u2013 the recompense of their deeds."},"1373":{"id":1373,"surah":10,"ayah":9,"verse":"Indeed those who accepted faith and did good deeds, their Lord will guide them due to their faith; rivers will flow beneath them in the Gardens of favours."},"1374":{"id":1374,"surah":10,"ayah":10,"verse":"Their prayers in it will be, \u201cPurity is to You, O Allah\u201d and their greetings in it will be, \u201cPeace\u201d; and the conclusion of their prayers is, \u201cAll praise is to Allah, the Lord Of The Creation.\u201d"},"1375":{"id":1375,"surah":10,"ayah":11,"verse":"Were Allah to send misfortune to the people as quickly as they hasten for the good*, their appointed term would have been finished; so We leave those who do not expect to meet Us, to wander in their rebellion. (* The way they wish the good to reach them quickly.)"},"1376":{"id":1376,"surah":10,"ayah":12,"verse":"And when some hardship reaches man he prays to Us, while reclining and sitting and standing; and when We remove his hardship, he goes away as if he had never prayed to Us because of any hardship; this is how the deeds of the transgressors are made seeming good to them."},"1377":{"id":1377,"surah":10,"ayah":13,"verse":"And indeed We destroyed several generations before you when they crossed the limits \u2013 and their respective Noble Messengers came to them with clear proofs, but they were not such as would believe; this is how We reward the guilty."},"1378":{"id":1378,"surah":10,"ayah":14,"verse":"Then We appointed you as caliphs in the earth after them, in order that We might see what deeds you perform."},"1379":{"id":1379,"surah":10,"ayah":15,"verse":"And when Our clear verses are recited to them, the people who do not expect to meet Us say, \u201cBring a Qur\u2019an other than this one, or change it\u201d; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI do not have the right to change it on my own; I only follow what is divinely revealed to me; if I were to disobey my Lord*, then I fear the punishment of the Great Day (of Resurrection). (* Which is impossible.)"},"1380":{"id":1380,"surah":10,"ayah":16,"verse":"Say, \u201cHad Allah willed I would not have recited it to you nor would He have made it known to you; so before this* I have spent an age among you; so do you not have sense?\u201d (* Before Allah\u2019s command to recite the Qur\u2019an to you.)"},"1381":{"id":1381,"surah":10,"ayah":17,"verse":"So who is more unjust than one who fabricates a lie concerning Allah and denies His signs? Indeed the guilty shall never prosper."},"1382":{"id":1382,"surah":10,"ayah":18,"verse":"And they worship the thing other than Allah, which neither harms them nor benefits them*, and they say, \u201cThese are our intercessors** before Allah\u201d; say, \u201cWhat! You inform Allah of something which in His knowledge does not exist in the heavens or in the earth?\u201d Purity and Supremacy are to Him, above their association. (* Some things like the sun, stars, trees are useful but they too are created by Allah. Their worship will not benefit \u2013 in fact it is harmful ** Only the virtuous will be granted permission by Allah to intercede.)"},"1383":{"id":1383,"surah":10,"ayah":19,"verse":"Mankind were only one nation* and then they differed, and had it not been for a promise** from your Lord, the matters in which they differed would have been decided here itself. (* On one proper faith. ** The account on the Last Day.)"},"1384":{"id":1384,"surah":10,"ayah":20,"verse":"And they say, \u201cWhy is not a sign sent down upon him from his Lord?\u201d Proclaim, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cThe hidden is only for Allah, therefore wait; I too am waiting with you.\u201d"},"1385":{"id":1385,"surah":10,"ayah":21,"verse":"And when We give mankind the taste of mercy after some hardship which had afflicted them, they immediately start conspiring against Our signs; proclaim, \u201cThe secret plan of Allah is the fastest\u201d; indeed Our angels record your scheming."},"1386":{"id":1386,"surah":10,"ayah":22,"verse":"It is He Who transports you over the land and the sea; to the extent that when you are in ships \u2013 and the ships sail with them with a favourable breeze and they rejoice at it \u2013 a gust of strong wind reaches them and waves come to them from every side and they realise that they are surrounded, \u2013 thereupon they pray to Allah as His sincere bondmen that, \u201cIf You rescue us from this, we will surely be thankful.\u201d"},"1387":{"id":1387,"surah":10,"ayah":23,"verse":"Then when He rescues them, they start wrongfully committing oppression in the earth; O mankind! Your oppression is only a torment against yourselves; derive the benefit until you live in this world; you have then to return to Us and thereupon We shall show you your misdeeds."},"1388":{"id":1388,"surah":10,"ayah":24,"verse":"The example of the life of this world is similar to the water which We sent down from the sky, so due to it the earth\u2019s vegetation grew in abundance \u2013 that which men and cattle eat; to the extent that when the earth has taken on her ornaments and is well beautified, and her owners thought that it is within their control, Our command came to it at night or at day \u2013 so We made it harvested as if it had not existed yesterday; this is how We explain the verses for the people who ponder."},"1389":{"id":1389,"surah":10,"ayah":25,"verse":"And Allah calls to the abode of peace, and guides whomever He wills on the Straight Path."},"1390":{"id":1390,"surah":10,"ayah":26,"verse":"For the people of virtue, is goodness and more than that; and neither will the blackness nor disgrace come upon their faces; it is they who are the people of Paradise; they will abide in it forever."},"1391":{"id":1391,"surah":10,"ayah":27,"verse":"And (for) those who earned evil*, the recompense of evil is equal to it \u2013 and disgrace will come upon them; they will have no one to save them from Allah; as if their faces are covered with pieces of the dark night; it is they who are the people of the fire; they will remain in it forever. (* The disbelievers.)"},"1392":{"id":1392,"surah":10,"ayah":28,"verse":"And on the day when We raise all of them together, then say to the polytheists, \u201cStay in your place \u2013 you and your partners (false deities)\u201d; so We shall separate them from the believers, and their partners will say to them, \u201cWhen did you ever worship us!?\u201d"},"1393":{"id":1393,"surah":10,"ayah":29,"verse":"\u201cTherefore Allah suffices as a Witness between us and you, that we were not even aware that you worshipped us!\u201d"},"1394":{"id":1394,"surah":10,"ayah":30,"verse":"Here will every soul come to know what it has sent ahead, and they will be returned to Allah \u2013 their true Master, and they will lose all that they used to fabricate."},"1395":{"id":1395,"surah":10,"ayah":31,"verse":"Say, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWho provides you sustenance from the sky and the earth? Or Who is the Owner of the ears and the eyes? And Who brings forth the living from the dead and Who brings forth the dead from the living? And Who plans all matters?\u201d So they will now say, \u201cAllah\u201d; therefore say, \u201cThen why do you not fear?\u201d"},"1396":{"id":1396,"surah":10,"ayah":32,"verse":"So such is Allah, your True Lord; therefore what remains after the truth, except error? So where are you reverting?"},"1397":{"id":1397,"surah":10,"ayah":33,"verse":"This is how the Word of your Lord is proved concerning the sinful, so they will not believe."},"1398":{"id":1398,"surah":10,"ayah":34,"verse":"Say, \u201cIs there any one among your partners (false deities) that can create for the first time and then after its extinction, create it again?\u201d Proclaim, \u201cAllah creates for the first time and then after its extinction, creates it again \u2013 so where are you reverting?\u201d"},"1399":{"id":1399,"surah":10,"ayah":35,"verse":"Say, \u201cIs there any among your partners that shows the right path?\u201d Say, \u201cAllah shows the right path; so who should be obeyed \u2013 the One Who shows the right path, or one who does not even find the right path unless he is guided? So what has happened to you? What sort of a judgement you impose!\u201d"},"1400":{"id":1400,"surah":10,"ayah":36,"verse":"And most of them do not follow anything except assumptions; indeed assumption does not serve the least purpose (in place) of the truth; indeed Allah knows their deeds."},"1401":{"id":1401,"surah":10,"ayah":37,"verse":"And this noble Qur\u2019an is not such that anyone can invent it, without Allah revealing it \u2013 but it surely is a confirmation of the Books preceding it and is an explanation of all that is written on the (preserved) tablet \u2013 there is no doubt in it \u2013 it is from the Lord Of The Creation."},"1402":{"id":1402,"surah":10,"ayah":38,"verse":"What! They dare say that \u201cHe has fabricated it\u201d? Say, \u201cThen bring one chapter like it and, other than Allah, call everyone you can if you are truthful.\u201d"},"1403":{"id":1403,"surah":10,"ayah":39,"verse":"On the contrary, they denied the thing the knowledge of which they could not master, whereas they have not yet seen its outcome; similarly those before them had denied, therefore see what sort of fate befell the unjust!"},"1404":{"id":1404,"surah":10,"ayah":40,"verse":"And among them is one who accepts faith in it, and among them is one who does not accept faith in it; and your Lord well knows the mischievous."},"1405":{"id":1405,"surah":10,"ayah":41,"verse":"And if they deny you, say, \u201cFor me are my deeds, and for you are your deeds; you have no concern with what I do, and I have no relation with what you do.\u201d"},"1406":{"id":1406,"surah":10,"ayah":42,"verse":"And among them are some who listen to you; so will you make the deaf hear even if they do not have any sense?"},"1407":{"id":1407,"surah":10,"ayah":43,"verse":"And among them is one who gazes at you; so will you guide the blind* even if they cannot perceive? (* Deaf or blind to guidance.)"},"1408":{"id":1408,"surah":10,"ayah":44,"verse":"Indeed Allah does not oppress men at all, but they do wrong themselves."},"1409":{"id":1409,"surah":10,"ayah":45,"verse":"And on the day when He will raise all of them together, as if they had never stayed on earth except for a moment during the day, they will recognise one another; for completely ruined are those who denied the meeting with Allah and were not on guidance."},"1410":{"id":1410,"surah":10,"ayah":46,"verse":"And whether We show you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) part of what We promise them or whether We call you towards Us before it \u2013 in any case they have to return to Us \u2013 then Allah is the Witness of their deeds."},"1411":{"id":1411,"surah":10,"ayah":47,"verse":"And in every nation was a Noble Messenger (from Allah); so when their Noble Messenger came to them, they were judged with fairness, and they were not wronged."},"1412":{"id":1412,"surah":10,"ayah":48,"verse":"And they say, \u201cWhen will this promise come, if you are truthful?\u201d"},"1413":{"id":1413,"surah":10,"ayah":49,"verse":"Say, \u201cI have no autonomy to benefit or hurt myself, except what Allah wills*; for every nation is a fixed promise; when their promise comes, they cannot postpone it nor can they advance it one moment. (* To empower me.)"},"1414":{"id":1414,"surah":10,"ayah":50,"verse":"Say, \u201cWhat is your opinion \u2013 if His punishment comes upon you at night or during the day, so what is there in it for which the guilty are being hasty?\u201d"},"1415":{"id":1415,"surah":10,"ayah":51,"verse":"\u201cSo will you believe it only when it has occurred? What! You believe in it now, whereas you were impatient* for it before? (* Eagerly demanding for it to occur.)"},"1416":{"id":1416,"surah":10,"ayah":52,"verse":"Then the unjust will be told, \u201cTaste the punishment forever; and you will be repaid only what you used to earn.\u201d"},"1417":{"id":1417,"surah":10,"ayah":53,"verse":"And they ask you \u201cIs it a reality*?\u201d Say, \u201cYes, by oath of my Lord, indeed surely it is a reality, and you will not be able to escape.\u201d (* The punishment of the hereafter.)"},"1418":{"id":1418,"surah":10,"ayah":54,"verse":"And if each unjust soul owned everything that is in the earth, it would have certainly given it in order to redeem itself; and they secretly felt repentant when they saw the punishment; and it has been judged between them with fairness and they will not be wronged."},"1419":{"id":1419,"surah":10,"ayah":55,"verse":"Pay heed! Indeed everything that is in the heavens and the earth belongs only to Allah; pay heed! Indeed Allah\u2019s promise is true, but most of them do not know."},"1420":{"id":1420,"surah":10,"ayah":56,"verse":"And He gives life and gives death, and towards Him you will return."},"1421":{"id":1421,"surah":10,"ayah":57,"verse":"O mankind! The advice has come to you from your Lord and a cure for the hearts \u2013 and guidance and mercy for believers."},"1422":{"id":1422,"surah":10,"ayah":58,"verse":"Say, \u201cUpon Allah\u2019s munificence and upon His mercy \u2013 upon these should the people rejoice\u201d; that is better than all their wealth and possessions."},"1423":{"id":1423,"surah":10,"ayah":59,"verse":"Say, \u201cWhat is your opinion \u2013 (regarding) the sustenance that Allah has sent down for you? So you have, on your own, deemed lawful and unlawful in it!\u201d; say, \u201cHas Allah given you permission for it, or do you fabricate a lie against Allah?\u201d"},"1424":{"id":1424,"surah":10,"ayah":60,"verse":"And what do they, who fabricate lies against Allah, assume will be their state on the Day of Resurrection? Indeed Allah is Most Munificent upon mankind, but most people are not thankful."},"1425":{"id":1425,"surah":10,"ayah":61,"verse":"And whatever work you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) are engaged in and recite a part of the Qur\u2019an from Him, and whatever you people do, We are Witness upon you from the time you begin it; and there is nothing in the earth or in the heaven worth the weight of the smallest particle hidden from your Lord, nor anything smaller or greater than it, which is not recorded in a clear Book."},"1426":{"id":1426,"surah":10,"ayah":62,"verse":"Pay heed! Indeed upon the friends of Allah is neither any fear, nor any grief. (The friends of Allah are the best in the creation.)"},"1427":{"id":1427,"surah":10,"ayah":63,"verse":"Those who have accepted faith and practice piety."},"1428":{"id":1428,"surah":10,"ayah":64,"verse":"There are good tidings for them in the life of this world and in the Hereafter; the Words of Allah cannot change; this is the supreme success."},"1429":{"id":1429,"surah":10,"ayah":65,"verse":"And do not grieve at their speech (O dear Prophet Mohammed \u2013 peace and blessings be upon him); indeed all honour is for Allah and He is the All Hearing, the All Knowing."},"1430":{"id":1430,"surah":10,"ayah":66,"verse":"Indeed all those in the heavens and all those in the earth are in Allah\u2019s control; and what do those who pray to the partners instead of Allah, follow? They do not follow anything except assumption, and they only make guesses."},"1431":{"id":1431,"surah":10,"ayah":67,"verse":"It is He Who created the night for you so that you may gain rest in it and the day giving sight; indeed in this are signs for the people who heed."},"1432":{"id":1432,"surah":10,"ayah":68,"verse":"They said, \u201cAllah has created a son for Himself\u201d- Purity is to Him! He is the Perfect (Unwanting, free of needs); to Him only belongs all whatever is in the heavens and all whatever is in the earth; \u201cYou do not have any proof of this; do you say a thing concerning Allah which you do not know?\u201d"},"1433":{"id":1433,"surah":10,"ayah":69,"verse":"Say, \u201cIndeed those who fabricate lies against Allah will never succeed.\u201d"},"1434":{"id":1434,"surah":10,"ayah":70,"verse":"Enjoying a little in the earth, they have then to return to Us \u2013 We will then make them taste a severe punishment, the recompense of their disbelief."},"1435":{"id":1435,"surah":10,"ayah":71,"verse":"Read to them the story of Nooh; when he told his people, \u201cO my people \u2013 if my standing here and reminding you of Allah\u2019s signs have been intolerable for you \u2013 so I have trusted only Allah \u2013 then work jointly and strengthen your work along with your false deities, leaving no doubt regarding your work \u2013 then do whatever you can to me, and do not give me respite.\u201d"},"1436":{"id":1436,"surah":10,"ayah":72,"verse":"\u201cThen if you turn away, I do not ask any fee from you; my reward is only upon Allah, and I am commanded to be of the Muslims.\u201d"},"1437":{"id":1437,"surah":10,"ayah":73,"verse":"In response they denied him, so We rescued him and those with him in the ship, and made them caliphs, and We drowned those who denied Our signs; therefore see what sort of fate befell those who had been warned!"},"1438":{"id":1438,"surah":10,"ayah":74,"verse":"Then after him, We sent other Noble Messengers to their respective people \u2013 so they came to them with clear proofs \u2013 so they were not such as to believe in what they had denied before; this is how We set seals upon the hearts of rebels."},"1439":{"id":1439,"surah":10,"ayah":75,"verse":"Then after them, We sent Moosa and Haroon along with Our signs, to Firaun and his court members, so they were proud and were a guilty people."},"1440":{"id":1440,"surah":10,"ayah":76,"verse":"So when the truth came to them from Us, they said, \u201cIndeed this is a clear magic.\u201d"},"1441":{"id":1441,"surah":10,"ayah":77,"verse":"Said Moosa, \u201cWhat! You speak in this manner regarding the truth when it has come to you? Is this magic? And magicians never succeed.\u201d"},"1442":{"id":1442,"surah":10,"ayah":78,"verse":"They said, \u201cHave you two come to us to revert us from what we found our fathers upon, and that the leadership in the land may only be for you two? And we shall never believe in you both.\u201d"},"1443":{"id":1443,"surah":10,"ayah":79,"verse":"And Firaun said, \u201cBring every expert magician to me.\u201d"},"1444":{"id":1444,"surah":10,"ayah":80,"verse":"So when the magicians came, Moosa said to them, \u201cCast whatever you intend to cast.\u201d"},"1445":{"id":1445,"surah":10,"ayah":81,"verse":"Therefore when they had cast, Moosa said, \u201cThis what you have brought, is magic; Allah will now make it void; indeed Allah does not make the works of the mischievous successful.\u201d"},"1446":{"id":1446,"surah":10,"ayah":82,"verse":"\u201cAnd Allah will prove the truth by His Words, even if the guilty get annoyed.\u201d"},"1447":{"id":1447,"surah":10,"ayah":83,"verse":"So none accepted faith in Moosa, except a few descendants of his people, fearing Firaun and his court members, that they would force them to revert; and indeed Firaun was a rebel in the land; and indeed he crossed the limits."},"1448":{"id":1448,"surah":10,"ayah":84,"verse":"And Moosa said, \u201cO my people! If you have accepted faith in Allah, then you should rely only upon Him, if you are Muslims.\u201d"},"1449":{"id":1449,"surah":10,"ayah":85,"verse":"They said, \u201cWe have relied only upon Allah; Our Lord! Do not make us a test* for the unjust people.\u201d (* By giving them power over us.)"},"1450":{"id":1450,"surah":10,"ayah":86,"verse":"\u201cAnd with Your mercy, rescue us from the disbelievers.\u201d"},"1451":{"id":1451,"surah":10,"ayah":87,"verse":"And We sent the divine revelation to Moosa and his brother that, \u201cBuild houses for your people in Egypt and make your houses as places of worship, and keep the prayer established, and give glad tidings to the Muslims.\u201d"},"1452":{"id":1452,"surah":10,"ayah":88,"verse":"And Moosa prayed, \u201cOur Lord! You have given Firaun and his chiefs adornment and wealth in the life of this world, our Lord, that they may lead astray from Your path; our Lord! Destroy their riches and harden their hearts so that they may not accept faith until they witness the painful punishment.\u201d"},"1453":{"id":1453,"surah":10,"ayah":89,"verse":"He said, \u201cThe prayer of you two is accepted, therefore remain firm, and do not follow the way of the unwise.\u201d"},"1454":{"id":1454,"surah":10,"ayah":90,"verse":"And We transported the Descendants of Israel across the sea, therefore Firaun and his army pursued them with rebellion and injustice; until when the drowning overcame him he cried, \u201cI accept faith that there is no True God except the One in Whom the Descendants of Israel believe, and I am a Muslim.\u201d"},"1455":{"id":1455,"surah":10,"ayah":91,"verse":"\u201cWhat! Now?* Whereas you were disobedient from the start and you were mischievous!\u201d (This was said to Firaun.* Accepting faith at the time of death is of no use.)"},"1456":{"id":1456,"surah":10,"ayah":92,"verse":"\u201cThis day We shall salvage your body so that you may be a sign for those after you; and indeed most people are neglectful of Our signs.\u201d"},"1457":{"id":1457,"surah":10,"ayah":93,"verse":"And indeed We provided the Descendants of Israel a place of honour, and bestowed good sustenance to them; and they did not fall into dispute except after the knowledge had come to them; indeed your Lord will judge between them on the Day of Resurrection concerning the matters in which they used to differ."},"1458":{"id":1458,"surah":10,"ayah":94,"verse":"And if you, O listener, have any doubt in what We have sent down towards you, then question those who have read the Book before you; undoubtedly, towards you has come the truth from your Lord, therefore do not be of those who doubt."},"1459":{"id":1459,"surah":10,"ayah":95,"verse":"And never be of those who denied the signs of Allah, for you will then be of the losers."},"1460":{"id":1460,"surah":10,"ayah":96,"verse":"Indeed those for whom the Word of your Lord has proved true, will not believe."},"1461":{"id":1461,"surah":10,"ayah":97,"verse":"Even if every sign comes to them, until they witness the painful punishment."},"1462":{"id":1462,"surah":10,"ayah":98,"verse":"So if only had there been one dwelling* that believed and its belief would have benefited it \u2013 except the nation of Yunus (Jonah)! When they accepted faith, We removed the disgraceful punishment in the life of this world from them, and let them enjoy for a while. (* That was destroyed after being warned.)"},"1463":{"id":1463,"surah":10,"ayah":99,"verse":"And if your Lord willed, all those who are in the earth \u2013 everyone of them \u2013 would have accepted faith; so will you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) force the people until they become Muslims?"},"1464":{"id":1464,"surah":10,"ayah":100,"verse":"And no soul can accept faith except by the command of Allah; and He sets the punishment upon those who do not have sense."},"1465":{"id":1465,"surah":10,"ayah":101,"verse":"Proclaim, \u201cObserve what is in the heavens and the earth\u201d; and the signs and the Noble Messengers do not benefit the people who are not destined to believe."},"1466":{"id":1466,"surah":10,"ayah":102,"verse":"So what are they waiting for, except the days similar to the days of those who passed away before them? Say, \u201cThen wait! I too am waiting with you.\u201d"},"1467":{"id":1467,"surah":10,"ayah":103,"verse":"Then We shall save Our Noble Messengers and the believers \u2013 this is it; it is incumbent upon Our mercy to save the Muslims."},"1468":{"id":1468,"surah":10,"ayah":104,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO people! If you are in doubt concerning my religion, then (know that) I will never worship those whom you worship instead of Allah \u2013 but I worship Allah Who will give you death; and I am commanded to be of the believers.\u201d"},"1469":{"id":1469,"surah":10,"ayah":105,"verse":"\u201cAnd that \u2018Set your face straight* for religion, apart from all others; and never be of those who ascribe partners (to Allah)\u2019.\u201d (* Your intention sincerely for religion.)"},"1470":{"id":1470,"surah":10,"ayah":106,"verse":"\u201cAnd other than Allah, do not worship that which cannot benefit you nor cause you harm; then if you do so, you would be of the unjust.\u201d (* Some things like the sun, stars, trees are useful but they too are created by Allah. Their worship will not benefit \u2013 in fact it is harmful.)"},"1471":{"id":1471,"surah":10,"ayah":107,"verse":"\u201cAnd if Allah afflicts you with some suffering, there is none who can remove it except Him; and if He wills good for you, there is none who can repel His munificence; He bestows it upon whomever He wills from among His bondmen; He is the Oft Forgiving, the Most Merciful.\u201d (* Help, cure, monetary aid etc., whatever benefit derived from any source, reaches us because Allah has given the source this power and is actually through the grace and mercy of Allah.)"},"1472":{"id":1472,"surah":10,"ayah":108,"verse":"Say, \u201cO people! The truth* has come to you from your Lord; so whoever came to guidance, has come to guidance for his own benefit; and whoever strayed, has strayed for his own harm; and I am not a trustee over you.\u201d (* Prophet Mohammed \u2013 peace and blessings be upon him, or Islam or the Qur\u2019an.)"},"1473":{"id":1473,"surah":10,"ayah":109,"verse":"And (O dear Prophet Mohammed \u2013 peace and blessings be upon him) follow what is divinely revealed to you, and have patience till Allah gives His command; and He is the Best of Rulers."},"1474":{"id":1474,"surah":11,"ayah":1,"verse":"Alif-Lam-Ra*; \u201cThis is a Book, the verses of which are full of wisdom \u2013 then explained in detail \u2013 from the Wise, the Well Aware.\u201d (* Alphabets of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"1475":{"id":1475,"surah":11,"ayah":2,"verse":"\u201cThat do not worship anyone except Allah; indeed I am for you a Herald of Warning and Glad Tidings, from Him.\u201d"},"1476":{"id":1476,"surah":11,"ayah":3,"verse":"\u201cAnd ask forgiveness from your Lord and incline towards Him in repentance, He will provide you excellent sustenance until the appointed time and will bestow superiority to every one who excels; and if you turn away, I then fear the punishment of the Great Day upon you.\u201d"},"1477":{"id":1477,"surah":11,"ayah":4,"verse":"\u201cTo Allah only you have to return, and He is Able to do all things.\u201d"},"1478":{"id":1478,"surah":11,"ayah":5,"verse":"Pay heed! They fold up their breasts in order to conceal from Allah; pay heed! Even when they fully cover themselves up with their clothes, Allah knows all that what hide and all what they disclose; indeed He knows what is in the hearts."},"1479":{"id":1479,"surah":11,"ayah":6,"verse":"And there is none that walks upon the earth whose sustenance does not depend on the mercy of Allah \u2013 He knows where it shall stay and where it shall be deposited; everything is in a clearly explaining Book."},"1480":{"id":1480,"surah":11,"ayah":7,"verse":"And it is He Who created the heavens and the earth in six days \u2013 and His Throne was upon the water \u2013 so that He might test you, as to who among you have the best deeds*; and if you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) say, \u201cIndeed you will be raised again after death\u201d, the disbelievers will surely say, \u201cThis is nothing but clear magic.\u201d (* The creation of all things is in order to test mankind.)"},"1481":{"id":1481,"surah":11,"ayah":8,"verse":"And if We postpone the punishment upon them for a specified time, they will surely say, \u201cWhat holds it back?\u201d Pay heed! On the day when it comes upon them, it will not be averted from them, and the very punishment they mocked at will encompass them."},"1482":{"id":1482,"surah":11,"ayah":9,"verse":"And if We bestow man the enjoyment of some mercy from Us and later withdraw it from him; surely he is most despairing, ungrateful."},"1483":{"id":1483,"surah":11,"ayah":10,"verse":"And if We bestow upon him the enjoyment of a favour after a misfortune that had befallen him, he will surely say, \u201cThe evils have gone away from me\u201d; indeed he is jubilant, boastful."},"1484":{"id":1484,"surah":11,"ayah":11,"verse":"Except those who patiently endured and did good deeds; for them is forgiveness, and a great reward."},"1485":{"id":1485,"surah":11,"ayah":12,"verse":"So will you forsake part of what is divinely revealed to you and be disheartened because they say, \u201cWhy has not a treasure been sent down along with him?\u201d, or \u201cAn angel should have come with him\u201d? You are purely a Herald of Warning*; and Allah is the Guardian over all things. (* Therefore do not grieve over their sayings.)"},"1486":{"id":1486,"surah":11,"ayah":13,"verse":"What! They dare say that \u201cHe has fabricated it\u201d? Say \u201cTherefore bring ten fabricated chapters like these, and call on everyone you can other than Allah, if you are truthful.\u201d"},"1487":{"id":1487,"surah":11,"ayah":14,"verse":"So O Muslims, if they are unable to reply to this challenge of yours, then know well that it is sent down only with the knowledge of Allah; and that except Him, there is no real God; so will you now accept?"},"1488":{"id":1488,"surah":11,"ayah":15,"verse":"Whoever desires the life of this world and its comforts, We shall give them the full reward for their deeds in it, and not make any reduction in it."},"1489":{"id":1489,"surah":11,"ayah":16,"verse":"These are the ones for whom there is nothing in the Hereafter except the fire; and all that they did there has gone to waste and all their deeds are destroyed."},"1490":{"id":1490,"surah":11,"ayah":17,"verse":"So is the one who is upon the clear proof* from his Lord, and comes a witness upon it from Allah, and before it the Book of Moosa, a leader and a mercy; they accept faith in it; and whoever denies it from all the groups, then the fire is promised for him; so O listener, do not have any doubt concerning it; indeed it is the truth from your Lord; but most people do not believe. (* The Jews who accepted faith in the Qur\u2019an.)"},"1491":{"id":1491,"surah":11,"ayah":18,"verse":"And who is more unjust than the one who fabricates a lie against Allah? They will be presented before their Lord, and the witnesses* will say, \u201cThese are they who lied concerning their Lord; the curse of Allah be upon the unjust!\u201d (* The Prophets and angels.)"},"1492":{"id":1492,"surah":11,"ayah":19,"verse":"Those who prevent from the way of Allah and wish deviation in it; and it is they who disbelieve in the Hereafter."},"1493":{"id":1493,"surah":11,"ayah":20,"verse":"They will not be able to escape in the earth, nor do they have any protecting friends apart from Allah; they will have punishment upon punishment; they were unable to hear, nor used to see. (* Hear or see the truth.)"},"1494":{"id":1494,"surah":11,"ayah":21,"verse":"It is they who put their souls into ruin, and they lost all that they used to fabricate."},"1495":{"id":1495,"surah":11,"ayah":22,"verse":"They will be, unnecessarily, the greatest losers in the Hereafter."},"1496":{"id":1496,"surah":11,"ayah":23,"verse":"Indeed those who believed and performed good deeds and directed themselves towards their Lord \u2013 they are the people of Paradise; they will abide in it forever."},"1497":{"id":1497,"surah":11,"ayah":24,"verse":"The example of the two groups is like one being blind and deaf, and the other seeing and hearing; are they equal in condition? So do you not ponder?"},"1498":{"id":1498,"surah":11,"ayah":25,"verse":"And indeed We sent Nooh to his people that, \u201cIndeed I am for you a clear Herald of Warning.\u201d"},"1499":{"id":1499,"surah":11,"ayah":26,"verse":"\u201cThat you must worship none except Allah; indeed I fear the punishment of the calamitous day upon you.\u201d"},"1500":{"id":1500,"surah":11,"ayah":27,"verse":"The chiefs of his people, who were disbelievers, said, \u201cWe see that you are just a mortal like us, and we do not see anyone following you except the most lowly among us, without insight; and we do not find any merit in you above us \u2013 in fact, we consider you liars.\u201d"},"1501":{"id":1501,"surah":11,"ayah":28,"verse":"He said, \u201cO my people! What is your opinion \u2013 if I am on a clear proof from my Lord and He has bestowed mercy upon me from Him, so you remained blind towards it; shall we force it upon you although you dislike it?\u201d"},"1502":{"id":1502,"surah":11,"ayah":29,"verse":"\u201cAnd O my people! I do not ask any wealth from you for it; my reward is only upon Allah, and I am not going to repel the Muslims; indeed they will meet their Lord, but I find you people completely ignorant.\u201d"},"1503":{"id":1503,"surah":11,"ayah":30,"verse":"\u201cAnd O my people! Who would rescue me from Allah if I repel them? So do you not think?\u201d"},"1504":{"id":1504,"surah":11,"ayah":31,"verse":"\u201cAnd I do not say to you, \u2018I have the treasures of Allah\u2019 nor that \u2018I gain knowledge of the hidden\u2019, nor do I say that, \u2018I am an angel\u2019 nor do I say to those whom your sights consider lowly, that Allah will never give them any good; Allah well knows what is in their hearts; if I do so, I would then be of the unjust.\u201d"},"1505":{"id":1505,"surah":11,"ayah":32,"verse":"They said, \u201cO Nooh, you have disputed with us and disputed in the extreme, therefore bring upon us what you promise us, if you are truthful.\u201d"},"1506":{"id":1506,"surah":11,"ayah":33,"verse":"He said, \u201cAllah will surely bring that upon you if He wills, and you will not be able to escape.\u201d"},"1507":{"id":1507,"surah":11,"ayah":34,"verse":"\u201cAnd my advice will not benefit you if I wish you good, when Allah wills to keep you astray; He is your Lord and to Him you will return.\u201d"},"1508":{"id":1508,"surah":11,"ayah":35,"verse":"What! They dare say that, \u201cHe has fabricated it\u201d? Say, \u201cIf I may have fabricated it, then my sin is upon me, and I am unconcerned with your sins.\u201d"},"1509":{"id":1509,"surah":11,"ayah":36,"verse":"And it was divinely revealed to Nooh that \u201cNone of your people will become Muslims, except those who have already accepted faith \u2013 therefore do not grieve at what they do.\u201d"},"1510":{"id":1510,"surah":11,"ayah":37,"verse":"\u201cAnd build the ship in front of Us, and by Our command, and do not speak to Me regarding the unjust; they will surely be drowned.\u201d"},"1511":{"id":1511,"surah":11,"ayah":38,"verse":"And Nooh builds the ship; and whenever the chiefs of his people passed by him, they used to laugh at him; he said, \u201cIf you laugh at us, then a time will come when we will laugh at you just as you laugh.\u201d"},"1512":{"id":1512,"surah":11,"ayah":39,"verse":"\u201cSo now you will come to know \u2013 upon whom comes the punishment that disgraces him, and upon whom descends the punishment that continues forever.\u201d"},"1513":{"id":1513,"surah":11,"ayah":40,"verse":"To the extent that when Our command came and the oven overflowed, We said, \u201cBoard into the ship a couple \u2013 male and female \u2013 from every kind, and your family members, except those upon whom the Word has been passed*, and all other Muslims\u201d; and only a few Muslims were with him. (* Whose fate has been sealed.)"},"1514":{"id":1514,"surah":11,"ayah":41,"verse":"And he said, \u201cBoard it \u2013 upon Allah\u2019s name is its movement and its stopping; indeed my Lord is surely Oft Forgiving, Most Merciful.\u201d"},"1515":{"id":1515,"surah":11,"ayah":42,"verse":"And the same carries them amid waves like mountains; and Nooh called out to his son whereas he was standing apart, \u201cO my son! Embark along with us*, and do not be with the disbelievers.\u201d (* As a Muslim.)"},"1516":{"id":1516,"surah":11,"ayah":43,"verse":"He said, \u201cI shall take the refuge of a mountain \u2013 it will save me from the water\u201d; said Nooh, \u201cToday there is none who can rescue from the punishment of Allah, except upon whom He has mercy\u201d; and the wave came in between them, so he remained amongst the drowning."},"1517":{"id":1517,"surah":11,"ayah":44,"verse":"And it was commanded, \u201cO earth, swallow your water and, O sky, stop\u201d \u2013 and the water was dried up and the matter was completed \u2013 and the ship stopped upon the mount Al-Judi and it was said, \u201cAway with the unjust nation!\u201d"},"1518":{"id":1518,"surah":11,"ayah":45,"verse":"And Nooh prayed to his Lord \u2013 submitted he, \u201cMy Lord! Indeed my son is also of my family, and surely Your promise is true and You are the Greatest Ruler of all.\u201d"},"1519":{"id":1519,"surah":11,"ayah":46,"verse":"He said, \u201cO Nooh, he is not of your family; his deeds are most improper; therefore do not ask Me a thing of which you do not have knowledge; I advise you not to be unwise.\u201d"},"1520":{"id":1520,"surah":11,"ayah":47,"verse":"He submitted, \u201cMy Lord! I seek your refuge from asking you the thing of which I do not have knowledge; and if You do not forgive me and do not have mercy on me, I would then be a loser.\u201d"},"1521":{"id":1521,"surah":11,"ayah":48,"verse":"It was said, \u201cO Nooh! Alight from the ship along with peace from Us and the blessings that are upon you and upon some groups that are with you; and some groups are those whom We shall let enjoy this world and then a painful punishment from Us will reach them.\u201d"},"1522":{"id":1522,"surah":11,"ayah":49,"verse":"These are the tidings of the hidden, which We reveal to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him); you did not know them nor did your people, before this; therefore patiently endure; indeed the excellent fate is for the pious."},"1523":{"id":1523,"surah":11,"ayah":50,"verse":"And towards the people of A\u2019ad, their fellow man Hud; he said, \u201cO my people! Worship Allah \u2013 there is no other True God except Him; you are purely fabricators.\u201d"},"1524":{"id":1524,"surah":11,"ayah":51,"verse":"\u201cO my people! I do not ask any fee from you for it; my reward is only upon Him Who has created me; so do you not have sense?\u201d"},"1525":{"id":1525,"surah":11,"ayah":52,"verse":"\u201cAnd O my people! Seek forgiveness from your Lord, then incline towards Him in repentance \u2013 He will send abundant rain from the sky upon you and will give you much more strength than you have \u2013 and do not turn away committing crimes!\u201d"},"1526":{"id":1526,"surah":11,"ayah":53,"verse":"They said, \u201cO Hud! You have not come to us with any proof and we will not forsake our deities upon your saying, nor will we believe you.\u201d"},"1527":{"id":1527,"surah":11,"ayah":54,"verse":"\u201cWe only say that you have been severely struck by one of our deities\u201d; he said, \u201cI make Allah a witness, and you all bear witness that I have no relation with whatever you ascribe (as partners to Allah)\u201d-"},"1528":{"id":1528,"surah":11,"ayah":55,"verse":"\u201cBesides Him \u2013 therefore all of you scheme against me and do not give me respite.\u201d"},"1529":{"id":1529,"surah":11,"ayah":56,"verse":"\u201cI rely upon Allah, Who is my Lord and your Lord; there is not a creature that walks, whose forelock is not in His control; indeed my Lord can be found on the Straight Path.\u201d"},"1530":{"id":1530,"surah":11,"ayah":57,"verse":"\u201cThen if you turn away, I have conveyed to you what I was sent with towards you; and my Lord will bring others in your stead; and you will not be able to cause Him any harm; indeed my Lord is a Guardian over all things.\u201d"},"1531":{"id":1531,"surah":11,"ayah":58,"verse":"And when Our command came, We rescued Hud and the Muslims who were with him, by Our mercy; and We saved them from a severe punishment."},"1532":{"id":1532,"surah":11,"ayah":59,"verse":"And these are the A\u2019ad \u2013 they denied the signs of their Lord and disobeyed His Noble Messengers and followed the commands of every stubborn disobedient."},"1533":{"id":1533,"surah":11,"ayah":60,"verse":"And a curse followed them in the world, and on the Day of Resurrection; pay heed! Indeed A\u2019ad disbelieved in their Lord; away with A\u2019ad, the people of Hud!"},"1534":{"id":1534,"surah":11,"ayah":61,"verse":"And to the Thamud tribe, We sent their fellow man Saleh; he said, \u201cO my people! Worship Allah, there is no other True God except Him; He created you from the earth and established you in it, so seek forgiveness from Him and incline towards Him in repentance; indeed my Lord is Close, Acceptor of Prayer.\u201d"},"1535":{"id":1535,"surah":11,"ayah":62,"verse":"They said, \u201cO Saleh! You seemed very promising among us, before this \u2013 What! You forbid us from worshipping the deities of our forefathers? And indeed we have fallen into a deeply intriguing doubt concerning what you call us to.\u201d"},"1536":{"id":1536,"surah":11,"ayah":63,"verse":"He said, \u201cO my people! What is your opinion \u2013 if I am on clear proof from my Lord and He has bestowed upon me mercy from Him, so who will rescue me from Allah if I disobey Him? So you will not increase anything for me, except loss!\u201d"},"1537":{"id":1537,"surah":11,"ayah":64,"verse":"\u201cAnd O my people! This is the she-camel of Allah \u2013 a sign for you \u2013 so let her graze in Allah\u2019s earth, and do not touch her with an evil intention for an imminent punishment will reach you.\u201d"},"1538":{"id":1538,"surah":11,"ayah":65,"verse":"In response they hamstrung her, he therefore said, \u201cEnjoy a further three days in your homes; this is a promise that will not be untrue.\u201d"},"1539":{"id":1539,"surah":11,"ayah":66,"verse":"Therefore when Our command came, We rescued Saleh and the Muslims who were with him by Our mercy, and from the disgrace of that day; indeed your Lord is the Strong, the Almighty."},"1540":{"id":1540,"surah":11,"ayah":67,"verse":"And the terrible scream seized the unjust \u2013 so at morning they remained lying flattened in their homes."},"1541":{"id":1541,"surah":11,"ayah":68,"verse":"As if they had never lived there; indeed the tribe of Thamud disbelieved in their Lord; away with the Thamud!"},"1542":{"id":1542,"surah":11,"ayah":69,"verse":"And indeed Our angels came to Ibrahim with glad tidings \u2013 they said, \u201cPeace\u201d; he answered, \u201cPeace\u201d and without delay brought a roasted calf."},"1543":{"id":1543,"surah":11,"ayah":70,"verse":"And when he saw their hands not reaching towards it, he thought they were pretending and inwardly started fearing them; they said, \u201cDo not be afraid \u2013 we are sent to the people of Lut.\u201d"},"1544":{"id":1544,"surah":11,"ayah":71,"verse":"And his wife was standing by and she started laughing*, so We gave her glad tidings regarding Ishaq, and following Ishaq, regarding Yaqub.** (* She was glad that the disbelieving people of Lut would be destroyed.** The birth of these two.)"},"1545":{"id":1545,"surah":11,"ayah":72,"verse":"She said, \u201cOh woe to me \u2013 will I bear a child whereas I am an old woman, and this my husband, is an old man? This is something really extraordinary.\u201d"},"1546":{"id":1546,"surah":11,"ayah":73,"verse":"The angels said, \u201cDo you wonder at the command of Allah? Allah\u2019s mercy and His blessings be upon you, O people of this house; indeed He only is Most Praiseworthy, Most Honourable.\u201d"},"1547":{"id":1547,"surah":11,"ayah":74,"verse":"And when Ibrahim\u2019s fear abated and the glad tidings reached him, he argued with Us regarding the people of Lut."},"1548":{"id":1548,"surah":11,"ayah":75,"verse":"Indeed Ibrahim is most forbearing, very soft hearted, penitent."},"1549":{"id":1549,"surah":11,"ayah":76,"verse":"\u201cO Ibrahim, turn away from this; indeed your Lord\u2019s command has come; and indeed a punishment will come upon them, which will not be averted.\u201d"},"1550":{"id":1550,"surah":11,"ayah":77,"verse":"And when Our angels came to Lut, he was distressed for them and was disheartened due to them, and said, \u201cThis is a day of great hardship.\u201d"},"1551":{"id":1551,"surah":11,"ayah":78,"verse":"And his people came running towards him; and they were in the habit of committing evil deeds; he said, \u201cO my people! These women of the tribe are my daughters* \u2013 they are purer for you \u2013 therefore fear Allah and do not disgrace me in the midst of my guests; is there not even a single righteous man among you?\u201d (* The wives of those people.)"},"1552":{"id":1552,"surah":11,"ayah":79,"verse":"They said, \u201cYou know that we have no right to the daughters of your tribe; and you obviously know what we desire.\u201d"},"1553":{"id":1553,"surah":11,"ayah":80,"verse":"He said, \u201cIf only I had the strength against you or were able to get the refuge of some strong support!\u201d"},"1554":{"id":1554,"surah":11,"ayah":81,"verse":"The angels said, \u201cO Lut! We are the sent ones of your Lord \u2013 they cannot get to you, therefore during the night take your entire household with you \u2013 and not one of you may turn around and see \u2013 except your wife*; she too will be afflicted with the same as they will be; indeed their promise is at morn; is not the morning imminent?\u201d (* She was a disbeliever and sided with the culprits.)"},"1555":{"id":1555,"surah":11,"ayah":82,"verse":"So when Our command came, We turned that township upside down and showered it continuously with stones of fired clay."},"1556":{"id":1556,"surah":11,"ayah":83,"verse":"That are marked, in the custody your Lord; and those stones are not at all far from the unjust!"},"1557":{"id":1557,"surah":11,"ayah":84,"verse":"And to Madyan their fellow man Shuaib; he said, \u201cO my people! Worship Allah \u2013 there is no other True God except Him; and do not make reductions in measure and weight \u2013 indeed I see you prosperous, and I fear the punishment of the Besieging Day (of Resurrection) upon you.\u201d"},"1558":{"id":1558,"surah":11,"ayah":85,"verse":"\u201cO my people! Measure and weigh in full with justice, and do not give the people their goods diminished, and do not roam about in the earth causing turmoil.\u201d"},"1559":{"id":1559,"surah":11,"ayah":86,"verse":"\u201cThat which remains from Allah\u2019s bestowal is better for you, if you believe; and I am not at all a guardian over you.\u201d"},"1560":{"id":1560,"surah":11,"ayah":87,"verse":"They said, \u201cO Shuaib! Does your prayer command you that we forsake the deities of our forefathers or that we may not do as we wish with our own property? Yes indeed \u2013 only you are very intelligent, most righteous*.\u201d (* They mocked at him with sarcasm.)"},"1561":{"id":1561,"surah":11,"ayah":88,"verse":"He said, \u201cO my people! What is your opinion \u2013 if I am on a clear proof from my Lord and He has bestowed me with an excellent sustenance from Him (shall I ignore all this?); and the matter I forbid you to do, I do not wish that I myself act against it; I only intend to make improvements as far possible; my guidance is only from Allah; I rely only upon Him and towards Him only do I incline.\u201d"},"1562":{"id":1562,"surah":11,"ayah":89,"verse":"\u201cAnd O my people! May not your opposition to me occasion the coming upon you of the thing similar to what befell the people of Nooh or the people of Hud or the people of Saleh; and the people of Lut are not at all far from you!\u201d"},"1563":{"id":1563,"surah":11,"ayah":90,"verse":"\u201cAsk forgiveness from your Lord and then incline towards Him in repentance; indeed my Lord is Most Merciful, Most Affectionate.\u201d"},"1564":{"id":1564,"surah":11,"ayah":91,"verse":"They said, \u201cO Shuaib! We do not understand most of what you say, and indeed we perceive you weak among us; were it not for your relatives, we would have stoned you; and in our sight, you have no respect at all.\u201d"},"1565":{"id":1565,"surah":11,"ayah":92,"verse":"He said, \u201cO my people! Is the pressure upon you from my relatives, worth more than Allah? And you put Him* behind your backs; indeed whatever you do is all within my Lords\u2019 control.\u201d (* His command \/ my preaching.)"},"1566":{"id":1566,"surah":11,"ayah":93,"verse":"\u201cAnd O my people! Keep on with your works in your positions, I am doing mine; very soon you will come to know \u2013 upon whom comes the punishment that will disgrace him, and who is the liar; and wait \u2013 I too am waiting with you.\u201d"},"1567":{"id":1567,"surah":11,"ayah":94,"verse":"And when Our command came, We rescued Shuaib and the Muslims who were with him by Our mercy; and the terrible scream seized the unjust \u2013 so at morning they remained lying flattened in their homes."},"1568":{"id":1568,"surah":11,"ayah":95,"verse":"As if they had never lived there; away with the Madyan, just as the Thamud were removed afar!"},"1569":{"id":1569,"surah":11,"ayah":96,"verse":"And indeed We sent Moosa with Our revelations and a clear domination."},"1570":{"id":1570,"surah":11,"ayah":97,"verse":"Towards Firaun and his court members, thereupon they followed the commands of Firaun; and the work of Firaun was not proper."},"1571":{"id":1571,"surah":11,"ayah":98,"verse":"He will lead his people on the Day of Resurrection, therefore landing them into hell; and what a wretched place to land into!"},"1572":{"id":1572,"surah":11,"ayah":99,"verse":"And a curse followed them in the world, and on the Day of Resurrection; what a wretched gift is what they received."},"1573":{"id":1573,"surah":11,"ayah":100,"verse":"These are the tidings of the townships, which We relate to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u2013 some of them still stand and some are cut off."},"1574":{"id":1574,"surah":11,"ayah":101,"verse":"And We did not oppress them at all, but it is they who wronged themselves \u2013 therefore their deities, whom they worshipped other than Allah, did not in the least benefit them when your Lord\u2019s command came; and due to them, they increased nothing but ruin."},"1575":{"id":1575,"surah":11,"ayah":102,"verse":"And similar is the seizure of your Lord when He seizes the townships upon their injustice; indeed His seizure is painful, severe."},"1576":{"id":1576,"surah":11,"ayah":103,"verse":"Indeed in this is a sign for one who fears the punishment of the Hereafter; this is the day on which everyone will be gathered, and this is the Day of Attendance."},"1577":{"id":1577,"surah":11,"ayah":104,"verse":"And We do not postpone it except for a fixed term."},"1578":{"id":1578,"surah":11,"ayah":105,"verse":"When that day comes, no one will speak without His permission; so among them are the ill-fated and the fortunate."},"1579":{"id":1579,"surah":11,"ayah":106,"verse":"So those who are ill-fated, are in the fire \u2013 they will bray like donkeys in it."},"1580":{"id":1580,"surah":11,"ayah":107,"verse":"Remaining in it as long as the heavens and the earth remain, except as much as your Lord willed*; indeed your Lord may do whatever, whenever, He wills. (* Remaining forever even after the heavens and the earth are destroyed.)"},"1581":{"id":1581,"surah":11,"ayah":108,"verse":"So those who are fortunate are in Paradise \u2013 abiding in it as long as the heavens and the earth remain, except as much as your Lord willed; this is the everlasting favour."},"1582":{"id":1582,"surah":11,"ayah":109,"verse":"So O listener (followers of this Prophet), do not fall into doubt by what these disbelievers worship; they only worship just as their forefathers worshipped before; and indeed we shall pay them their due in full, undiminished."},"1583":{"id":1583,"surah":11,"ayah":110,"verse":"And indeed We gave Moosa the Book, hence there was discord regarding it; and were it not for a Word that had been previously passed from your Lord, the matter would have immediately been decided regarding them; and indeed they are in an intriguing doubt concerning it."},"1584":{"id":1584,"surah":11,"ayah":111,"verse":"And indeed to each and every one, your Lord will fully repay his deeds; He is Informed of their deeds."},"1585":{"id":1585,"surah":11,"ayah":112,"verse":"Therefore remain firm the way you are commanded to, and those who have turned along with you, and O people \u2013 do not rebel; indeed He is seeing your deeds."},"1586":{"id":1586,"surah":11,"ayah":113,"verse":"And do not incline towards the unjust, for the fire will touch you \u2013 and you do not have any supporter other than Allah \u2013 then you will not be helped."},"1587":{"id":1587,"surah":11,"ayah":114,"verse":"And keep the prayer established at the two ends of the day and in some parts of the night; indeed good deeds wipe out the evil deeds; this is an advice to those who heed it."},"1588":{"id":1588,"surah":11,"ayah":115,"verse":"And have patience, for Allah does not waste the wages of the righteous."},"1589":{"id":1589,"surah":11,"ayah":116,"verse":"So why were not there some people, from the generations before you, who had some goodness remaining in them in order to prevent (others) from causing turmoil in the earth? Except a few among them \u2013 the very ones whom We had rescued; and the unjust remained pursuing what they were given, and they were guilty."},"1590":{"id":1590,"surah":11,"ayah":117,"verse":"And your Lord is not such as to destroy townships without reason, while their people are righteous."},"1591":{"id":1591,"surah":11,"ayah":118,"verse":"And had your Lord willed, He would have made mankind one nation \u2013 and they will always keep differing."},"1592":{"id":1592,"surah":11,"ayah":119,"verse":"Except those upon whom your Lord had mercy; and this is why He created them; and the Word of your Lord has been concluded that, \u201cIndeed I will, surely, fill hell with jinns and men combined.\u201d"},"1593":{"id":1593,"surah":11,"ayah":120,"verse":"And We relate to you all the accounts of Noble Messengers, in order to steady your heart with it; and in this the truth has come to you, and for the Muslims a preaching and advice."},"1594":{"id":1594,"surah":11,"ayah":121,"verse":"And say to the disbelievers, \u201cKeep on with your works in your places; we are carrying out ours.\u201d"},"1595":{"id":1595,"surah":11,"ayah":122,"verse":"\u201cAnd wait \u2013 we too are waiting.\u201d"},"1596":{"id":1596,"surah":11,"ayah":123,"verse":"\u201cAnd for Allah only are the hidden of the heavens and of the earth, and towards Him only is the return of all matters \u2013 therefore worship Him and trust Him; and your Lord is not unaware of what you do.\u201d"},"1597":{"id":1597,"surah":12,"ayah":1,"verse":"Alif-Lam-Ra*; these are verses of the clear Book. (* Alphabets of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"1598":{"id":1598,"surah":12,"ayah":2,"verse":"Indeed We have sent down an Arabic Qur\u2019an, so that you may perceive."},"1599":{"id":1599,"surah":12,"ayah":3,"verse":"We relate to you the best narrative because We have sent the divine revelation of this Qur\u2019an, to you; although surely you were unaware before this."},"1600":{"id":1600,"surah":12,"ayah":4,"verse":"Remember when Yusuf (Joseph) said to his father, \u201cO my father! I saw eleven stars and the sun and the moon \u2013 I saw them prostrating to me.\u201d"},"1601":{"id":1601,"surah":12,"ayah":5,"verse":"He said, \u201cO my child! Do not relate your dream to your brothers, for they will hatch a plot against you; indeed Satan is an open enemy towards mankind.\u201d"},"1602":{"id":1602,"surah":12,"ayah":6,"verse":"\u201cAnd this is how your Lord will choose you and teach you how to interpret events, and will perfect His favours upon you and upon the family of Yaqub, the way He perfected it upon both your forefathers, Ibrahim and Ishaq; indeed your Lord is All Knowing, Wise.\u201d"},"1603":{"id":1603,"surah":12,"ayah":7,"verse":"Indeed in Yusuf and his brothers are signs* for those who enquire**. (* Of the truthfulness of Prophet Mohammed \u2013 peace and blessings be upon him. ** The Jews who enquired about their story.)"},"1604":{"id":1604,"surah":12,"ayah":8,"verse":"When they said, \u201cIndeed Yusuf and his brother* are dearer to our father than we are, and we are one group; undoubtedly our father is, clearly, deeply engrossed in love.\u201d (* Of the same mother.)"},"1605":{"id":1605,"surah":12,"ayah":9,"verse":"\u201cKill Yusuf or throw him somewhere in the land, so that your father\u2019s attention may be directed only towards you, and then after it you may again become righteous!\u201d"},"1606":{"id":1606,"surah":12,"ayah":10,"verse":"A speaker among them said, \u201cDo not kill Yusuf \u2013 and drop him into a dark well so that some traveller may come and take him away, if you have to.\u201d"},"1607":{"id":1607,"surah":12,"ayah":11,"verse":"They said, \u201cO our father! What is the matter with you that you do not trust us with Yusuf, although we are in fact his well-wishers?\u201d"},"1608":{"id":1608,"surah":12,"ayah":12,"verse":"\u201cSend him with us tomorrow so that he may eat some fruits and play, and indeed we are his protectors.\u201d"},"1609":{"id":1609,"surah":12,"ayah":13,"verse":"He said, \u201cI will indeed be saddened by your taking him away, and I fear that the wolf may devour him, whilst you are unaware of him.\u201d (* Prophet Yaqub knew of what was about to happen.)"},"1610":{"id":1610,"surah":12,"ayah":14,"verse":"They said, \u201cIf the wolf devours him, whereas we are a group, then surely we are useless!\u201d"},"1611":{"id":1611,"surah":12,"ayah":15,"verse":"So when they took him away \u2013 and all of them agreed that they should drop him in the dark well; and We sent the divine revelation to him, \u201cYou will surely tell them of their deed at a time when they will not know.\u201d"},"1612":{"id":1612,"surah":12,"ayah":16,"verse":"And at nightfall they came to their father, weeping."},"1613":{"id":1613,"surah":12,"ayah":17,"verse":"Saying, \u201cO our father! We went far ahead while racing, and left Yusuf near our resources \u2013 therefore the wolf devoured him; and you will not believe us although we may be truthful.\u201d"},"1614":{"id":1614,"surah":12,"ayah":18,"verse":"And they brought his shirt stained with faked blood; he said, \u201cOn the contrary \u2013 your hearts have fabricated an excuse for you; therefore patience is better; and from Allah only I seek help against the matters that you relate.\u201d"},"1615":{"id":1615,"surah":12,"ayah":19,"verse":"And there came a caravan \u2013 so they sent their water-drawer, he therefore lowered his pail; he said, \u201cWhat good luck, this is a boy!\u201d; and they hid him as a treasure; and Allah knows what they do."},"1616":{"id":1616,"surah":12,"ayah":20,"verse":"And the brothers sold him for an improper price, a limited number of coins; and they had no interest in him."},"1617":{"id":1617,"surah":12,"ayah":21,"verse":"And the Egyptian who purchased him said to his wife, \u201cKeep him honourably \u2013 we may derive some benefit due to him or we may adopt him as our son\u201d; and this is how we established Yusuf in the land, and that We might teach him how to interpret events; and Allah is Dominant upon His works, but most men do not know."},"1618":{"id":1618,"surah":12,"ayah":22,"verse":"And when he matured to his full strength, We bestowed him wisdom and knowledge; and this is how We reward the virtuous."},"1619":{"id":1619,"surah":12,"ayah":23,"verse":"And the woman in whose house he was, allured him not to restrain himself and she closed all the doors \u2013 and said, \u201cCome! It is you I address!\u201d; he said, \u201c(I seek) The refuge of Allah \u2013 indeed the governor is my master \u2013 he treats me well; undoubtedly the unjust never prosper.\u201d"},"1620":{"id":1620,"surah":12,"ayah":24,"verse":"And indeed the woman desired him; and he too would have desired her were he not to witness the sign of his Lord*; this is what We did, to turn away evil and lewdness from him; indeed He is one of Our chosen bondmen. (* Allah saved him and he never desired this immorality.)"},"1621":{"id":1621,"surah":12,"ayah":25,"verse":"And they both raced towards the door, and the woman tore his shirt from behind, and they both found her husband at the door; she said, \u201cWhat is the punishment of the one who sought evil with your wife, other than prison or a painful torture?\u201d"},"1622":{"id":1622,"surah":12,"ayah":26,"verse":"Said Yusuf, \u201cIt was she who lured me, that I may not guard myself\u201d \u2013 and a witness from her own household testified; \u201cIf his shirt is torn from the front, then the woman is truthful and he has spoken incorrectly.\u201d"},"1623":{"id":1623,"surah":12,"ayah":27,"verse":"\u201cAnd if his shirt is torn from behind, then the woman is a liar and he is truthful.\u201d"},"1624":{"id":1624,"surah":12,"ayah":28,"verse":"So when the governor saw his shirt torn from behind, he said, \u201cIndeed this is a deception of women; undoubtedly the deception of women is very great.\u201d"},"1625":{"id":1625,"surah":12,"ayah":29,"verse":"\u201cO Yusuf! Disregard this \u2013 and O woman! Seek forgiveness for your sin; indeed you are of the mistaken.\u201d"},"1626":{"id":1626,"surah":12,"ayah":30,"verse":"And some women of the city said, \u201cThe governor\u2019s wife is seducing her young slave; indeed his love has taken root in her heart; and we find her clearly lost.\u201d"},"1627":{"id":1627,"surah":12,"ayah":31,"verse":"And when she heard of their secret talk, she sent for them and prepared cushioned mattresses for them and gave a knife to each one of them and said to Yusuf, \u201cCome out before them!\u201d When the women saw him they praised him and cut their hands, and said, \u201cPurity is to Allah \u2013 this is no human being; this is not but an honourable angel!\u201d"},"1628":{"id":1628,"surah":12,"ayah":32,"verse":"She said, \u201cThis is he on whose account you used to insult me; and indeed I tried to entice him, so he safeguarded himself; and indeed if he does not do what I tell him to, he will surely be imprisoned, and will surely be humiliated.\u201d"},"1629":{"id":1629,"surah":12,"ayah":33,"verse":"He said, \u201cO my Lord! Prison is dearer to me than the deed they invite me to; and if You do not repel their deceit away from me, I may incline towards them and be unwise.\u201d"},"1630":{"id":1630,"surah":12,"ayah":34,"verse":"So his Lord heard his prayer and repelled the women\u2019s deceit away from him; indeed He only is the All Hearing, the All Knowing."},"1631":{"id":1631,"surah":12,"ayah":35,"verse":"Then after having seeing all the signs, they all decided that he should be imprisoned for some time."},"1632":{"id":1632,"surah":12,"ayah":36,"verse":"And two young men went to prison along with him; one of them said, \u201cI dreamt that I am pressing wine\u201d; the other said, \u201cI dreamt that I am carrying some bread upon my head from which birds were eating\u201d; \u201cTell us their interpretation; indeed we see that you are virtuous.\u201d"},"1633":{"id":1633,"surah":12,"ayah":37,"verse":"Said Yusuf, \u201cThe food which you get will not reach you, but I will tell you the interpretation of this before it comes to you; this is one of the sciences my Lord has taught me; indeed I did not accept the religion of the people who do not believe in Allah and are deniers of the Hereafter.\u201d"},"1634":{"id":1634,"surah":12,"ayah":38,"verse":"\u201cAnd I have chosen the religion of my forefathers, Ibrahim and Ishaq and Yaqub; it is not rightful for us to ascribe anything as a partner to Allah; this is a grace of Allah upon us and upon mankind, but most men are not thankful.\u201d"},"1635":{"id":1635,"surah":12,"ayah":39,"verse":"\u201cO both my fellow-prisoners! Are various lords better, or One Allah, the Dominant above all?\u201d"},"1636":{"id":1636,"surah":12,"ayah":40,"verse":"\u201cYou do not worship anything besides Him, but which are merely names coined by you and your forefathers \u2013 Allah has not sent down any proof regarding them; there is no command but that of Allah; He has commanded that you do not worship anyone except Him; this is the proper religion, but most people do not know.\u201d"},"1637":{"id":1637,"surah":12,"ayah":41,"verse":"\u201cO my two fellow-prisoners! One of you will give his lord (the king) wine to drink; regarding the other, he will be crucified, therefore birds will eat from his head; the command has been given concerning the matter you had enquired about.\u201d"},"1638":{"id":1638,"surah":12,"ayah":42,"verse":"And of the two, Yusuf said to the one whom he sensed would be released, \u201cRemember me, in the company of your lord\u201d; so Satan caused him to forget to mention Yusuf to his lord, he therefore stayed in prison for several years more."},"1639":{"id":1639,"surah":12,"ayah":43,"verse":"And the king said, \u201cI saw in a dream seven healthy cows whom seven lean cows were eating, and seven green ears of corn and seven others dry; O court-members! Explain my dream, if you can interpret dreams.\u201d"},"1640":{"id":1640,"surah":12,"ayah":44,"verse":"They answered, \u201cThese are confused dreams \u2013 and we do not know the interpretation of dreams.\u201d"},"1641":{"id":1641,"surah":12,"ayah":45,"verse":"And of the two the one who was released said \u2013 and after a long time he had remembered \u2013 \u201cI will tell you its interpretation, therefore send me forth.\u201d"},"1642":{"id":1642,"surah":12,"ayah":46,"verse":"\u201cO Yusuf! O truthful one! Explain for us the seven healthy cows which seven lean cows were eating and the seven green ears of corn and seven others dry, so I may return to the people, possibly they may come to know.\u201d"},"1643":{"id":1643,"surah":12,"ayah":47,"verse":"He said, \u201cYou will cultivate for seven years continuously; so leave all that you harvest in the ear, except a little which you eat.\u201d"},"1644":{"id":1644,"surah":12,"ayah":48,"verse":"\u201cThen after that will come seven hard years which will devour all that you had stored for them, except a little which you may save.\u201d"},"1645":{"id":1645,"surah":12,"ayah":49,"verse":"\u201cThen after that will come a year when the people will be given rain and in which they will extract juices.\u201d"},"1646":{"id":1646,"surah":12,"ayah":50,"verse":"And the king said, \u201cBring him to me\u201d; so when the bearer came to him, Yusuf said, \u201cReturn to your lord and ask him what is the status of the women who had cut their hands; indeed my Lord knows their deception.\u201d"},"1647":{"id":1647,"surah":12,"ayah":51,"verse":"The king said, \u201cO women! What was your role when you tried to entice Yusuf?\u201d They answered, \u201cPurity is to Allah \u2013 we did not find any immorality in him\u201d; said the wife of the governor, \u201cNow the truth is out; it was I who tried to entice him, and indeed he is truthful.\u201d"},"1648":{"id":1648,"surah":12,"ayah":52,"verse":"Said Yusuf, \u201cI did this so that the governor may realise that I did not betray him behind his back, and Allah does not let the deceit of betrayers be successful.\u201d"},"1649":{"id":1649,"surah":12,"ayah":53,"verse":"\u201cAnd I do not portray my soul as innocent; undoubtedly the soul excessively commands towards evil, except upon whom my Lord has mercy; indeed my Lord is Oft Forgiving, Most Merciful.\u201d"},"1650":{"id":1650,"surah":12,"ayah":54,"verse":"And the king said, \u201cBring him to me so that I may choose him especially for myself\u201d; and when he had talked to him he said, \u201cIndeed you are today, before us, the honourable, the trusted.\u201d"},"1651":{"id":1651,"surah":12,"ayah":55,"verse":"Said Yusuf, \u201cAppoint me over the treasures of the earth; indeed I am a protector, knowledgeable.\u201d"},"1652":{"id":1652,"surah":12,"ayah":56,"verse":"And this is how We gave Yusuf the control over that land; to stay in it wherever he pleased; We may convey Our mercy to whomever We will, and We do not waste the wages of the righteous."},"1653":{"id":1653,"surah":12,"ayah":57,"verse":"And undoubtedly the reward of the Hereafter is better, for those who accept faith and remain pious."},"1654":{"id":1654,"surah":12,"ayah":58,"verse":"And Yusuf\u2019s brothers came and presented themselves before him, so he recognised them whereas they remained unaware of him."},"1655":{"id":1655,"surah":12,"ayah":59,"verse":"And when he provided them with their provisions he said, \u201cBring your step-brother to me; do you not see that I measure in full and that I am the best host?\u201d"},"1656":{"id":1656,"surah":12,"ayah":60,"verse":"\u201cAnd if you do not bring him to me, there shall be no measure (provisions) for you with me and do not ever come near me.\u201d"},"1657":{"id":1657,"surah":12,"ayah":61,"verse":"They said, \u201cWe will seek him from his father \u2013 this we must surely do.\u201d"},"1658":{"id":1658,"surah":12,"ayah":62,"verse":"He said to his slaves, \u201cPlace their means back into their sacks, perhaps they may recognise it when they return to their homes, perhaps they may come again.\u201d"},"1659":{"id":1659,"surah":12,"ayah":63,"verse":"So when they returned to their father, they said, \u201cO our father! The provisions have been denied to us, therefore send our brother with us so that we may bring the provisions, and we will surely protect him.\u201d"},"1660":{"id":1660,"surah":12,"ayah":64,"verse":"He said, \u201cShall I trust you regarding him the same way I had trusted you earlier regarding his brother? Therefore Allah is the Best Protector; and He is More Merciful than all those who show mercy.\u201d"},"1661":{"id":1661,"surah":12,"ayah":65,"verse":"And when they opened their belongings they found that their means had been returned to them; they said, \u201cO our father! What more can we ask for? Here are our means returned to us; we may get the provision for our homes and guard our brother, and get a camel-load extra; giving all these is insignificant for the king.\u201d"},"1662":{"id":1662,"surah":12,"ayah":66,"verse":"He said, \u201cI shall never send him with you until you give me an oath upon Allah that you will bring him back to me, unless you are surrounded\u201d; and (recall) when they gave him their oath that \u201cAllah\u2019s guarantee is upon what we say.\u201d (* He knew that Bin Yamin would be restrained.)"},"1663":{"id":1663,"surah":12,"ayah":67,"verse":"And he said, \u201cO my sons! Do not enter all by one gate \u2013 and enter by different gates; I cannot save you against Allah; there is no command but that of Allah; upon Him do I rely; and all those who trust, must rely only upon Him.\u201d"},"1664":{"id":1664,"surah":12,"ayah":68,"verse":"And when they entered from the place where their father had commanded them to; that would not at all have saved them against Allah \u2013 except that it was Yaqub\u2019s secret wish, which he fulfilled; and indeed he is a possessor of knowledge, due to Our teaching, but most people do not know."},"1665":{"id":1665,"surah":12,"ayah":69,"verse":"And when they entered in the company Yusuf, he seated his brother close to him and said, \u201cBe assured, I really am your brother \u2013 therefore do not grieve for what they do.\u201d"},"1666":{"id":1666,"surah":12,"ayah":70,"verse":"And when he had provided them their provision, he put the drinking-cup in his brother\u2019s bag, and then an announcer cried, \u201cO people of the caravan! You are indeed thieves!\u201d"},"1667":{"id":1667,"surah":12,"ayah":71,"verse":"They answered and turned towards them, \u201cWhat is it you cannot find?\u201d"},"1668":{"id":1668,"surah":12,"ayah":72,"verse":"They said, \u201cWe cannot find the king\u2019s cup, and for him who brings it is a camel-load, and I am its guarantor.\u201d"},"1669":{"id":1669,"surah":12,"ayah":73,"verse":"They said, \u201cBy Allah, you know very well that we did not come here to cause turmoil in the land, and nor are we thieves!\u201d"},"1670":{"id":1670,"surah":12,"ayah":74,"verse":"They said, \u201cAnd what shall be the punishment for it, if you are liars?\u201d"},"1671":{"id":1671,"surah":12,"ayah":75,"verse":"They said, \u201cThe punishment for it is that he in whose bag it shall be found, shall himself become a slave for it; this is how we punish the unjust.\u201d"},"1672":{"id":1672,"surah":12,"ayah":76,"verse":"So he first searched their bags before his brother\u2019s bag, then removed it from his brother\u2019s bag; this was the plan We had taught Yusuf; he had no right to take his brother by the king\u2019s law, except if Allah wills; We may raise in ranks whomever We will; and above every possessor of knowledge is another scholar."},"1673":{"id":1673,"surah":12,"ayah":77,"verse":"They said, \u201cIf he steals, then indeed his brother has stolen before\u201d; so Yusuf kept this in his heart and did not reveal it to them; he replied within himself, \u201cIn fact, you are in a worse position; and Allah well knows the matters you fabricate.\u201d"},"1674":{"id":1674,"surah":12,"ayah":78,"verse":"They said, \u201cO governor! He has a very aged father, so take one of us in his stead; indeed we witness your favours.\u201d"},"1675":{"id":1675,"surah":12,"ayah":79,"verse":"He said, \u201cThe refuge of Allah from that we should take anyone except him with whom our property was found \u2013 we would then surely be unjust.\u201d"},"1676":{"id":1676,"surah":12,"ayah":80,"verse":"So when they did not anticipate anything from him, they went away and started consulting each other; their eldest brother said, \u201cDo you not know that your father has taken from you an oath upon Allah, and before this, how you had failed in respect of Yusuf? Therefore I will not move from here until my father permits or Allah commands me; and His is the best command.\u201d"},"1677":{"id":1677,"surah":12,"ayah":81,"verse":"\u201cReturn to your father and then submit, \u2018O our father! Indeed your son has stolen; we were witness only to what we know and we were not guardians of the unseen.\u2019"},"1678":{"id":1678,"surah":12,"ayah":82,"verse":"\u2018And ask the township in which we were, and the caravan in which we came; and indeed we are truthful.\u2019\u201d"},"1679":{"id":1679,"surah":12,"ayah":83,"verse":"Said Yaqub, \u201cYour souls have fabricated an excuse for you; therefore patience is excellent; it is likely that Allah will bring all* of them to me; undoubtedly only He is the All Knowing, the Wise.\u201d (* All three including Yusuf.)"},"1680":{"id":1680,"surah":12,"ayah":84,"verse":"And he turned away from them and said, \u201cAlas \u2013 the separation from Yusuf!\u201d and his eyes turned white with sorrow, he therefore kept suppressing his anger."},"1681":{"id":1681,"surah":12,"ayah":85,"verse":"They said, \u201cBy Allah, you will keep remembering Yusuf till your health fails you or you lose your life.\u201d"},"1682":{"id":1682,"surah":12,"ayah":86,"verse":"He said, \u201cI complain of my worry and grief only to Allah, and I know the great traits of Allah which you do not know.\u201d"},"1683":{"id":1683,"surah":12,"ayah":87,"verse":"\u201cO my sons, go and search for Yusuf and his brother, and do not lose hope in the mercy of Allah; indeed none lose hope in the mercy of Allah except the disbelieving people.\u201d"},"1684":{"id":1684,"surah":12,"ayah":88,"verse":"Then when they reached in the company of Yusuf they said, \u201cO governor! Calamity has struck us and our household, and we have brought goods of little value, so give us the full measure and be generous to us; undoubtedly Allah rewards the generous.\u201d"},"1685":{"id":1685,"surah":12,"ayah":89,"verse":"He said, \u201cAre you aware of what you did to Yusuf and his brother when you were unwise?\u201d"},"1686":{"id":1686,"surah":12,"ayah":90,"verse":"They said, \u201cAre you, in truth you, really Yusuf?\u201d He said, \u201cI am Yusuf and this is my brother; indeed Allah has bestowed favour upon us; undoubtedly whoever practices piety and patience \u2013 so Allah does not waste the wages of the righteous.\u201d"},"1687":{"id":1687,"surah":12,"ayah":91,"verse":"They said, \u201cBy Allah, undoubtedly Allah has given you superiority over us, and we were indeed guilty.\u201d"},"1688":{"id":1688,"surah":12,"ayah":92,"verse":"He said, \u201cThere is no reproach on you, this day! May Allah forgive you \u2013 and He is the Utmost Merciful, of all those who show mercy.\u201d"},"1689":{"id":1689,"surah":12,"ayah":93,"verse":"\u201cTake along this shirt of mine and lay it on my father\u2019s face, his vision will be restored; and bring your entire household to me.\u201d (Prophet Yusuf knew that this miracle would occur.)"},"1690":{"id":1690,"surah":12,"ayah":94,"verse":"When the caravan left Egypt, their father said*, \u201cIndeed I sense the fragrance of Yusuf, if you do not call me senile.\u201d (* Prophet Yaqub said this in Palestine, to other members of his family. He could discern the fragrance of Prophet Yusuf\u2019s shirt from far away.)"},"1691":{"id":1691,"surah":12,"ayah":95,"verse":"They said, \u201cBy Allah, you are still deeply engrossed in the same old love of yours.\u201d"},"1692":{"id":1692,"surah":12,"ayah":96,"verse":"Then when the bearer of glad tidings came, he laid the shirt on his face, he therefore immediately regained his eyesight*; he said, \u201cWas I not telling you? I know the great traits of Allah which you do not know!\u201d (This was a miracle that took place by applying Prophet Yusuf\u2019s shirt.)"},"1693":{"id":1693,"surah":12,"ayah":97,"verse":"They said, \u201cO our father! Seek forgiveness for our sins, for we were indeed guilty.\u201d"},"1694":{"id":1694,"surah":12,"ayah":98,"verse":"He said, \u201cI shall soon seek forgiveness for you from my Lord; indeed He only is the Oft Forgiving, the Most Merciful.\u201d"},"1695":{"id":1695,"surah":12,"ayah":99,"verse":"So when they all reached in Yusuf\u2019s company, he kept his parents close to him, and said, \u201cEnter Egypt, if Allah wills, in safety.\u201d"},"1696":{"id":1696,"surah":12,"ayah":100,"verse":"And he seated his parents on the throne, and they all prostrated before him; and Yusuf said, \u201cO my father! This is the interpretation of my former dream; my Lord has made it true; and indeed He has bestowed favour upon me, when He brought me out of prison and brought you all from the village, after Satan had created a resentment between me and my brothers; indeed my Lord may make easy whatever He wills; undoubtedly He is the All Knowing, the Wise.\u201d"},"1697":{"id":1697,"surah":12,"ayah":101,"verse":"\u201cO my Lord! you have given me a kingdom* and have taught me how to interpret some events; O Creator of the heavens and the earth \u2013 you are my Supporter in the world and in the Hereafter; cause me to die as a Muslim, and unite me with those who deserve Your proximity.\u201d (* Prophethood and the rule over Egypt. Prophet Yusuf said this prayer while his death approached him.)"},"1698":{"id":1698,"surah":12,"ayah":102,"verse":"These are some tidings of the Hidden which We divinely reveal to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him); and you were not with them when they set their task and when they were scheming."},"1699":{"id":1699,"surah":12,"ayah":103,"verse":"And however much you long for, most men will not accept faith."},"1700":{"id":1700,"surah":12,"ayah":104,"verse":"You do not ask them any fee in return for it; this is not but an advice to the entire world."},"1701":{"id":1701,"surah":12,"ayah":105,"verse":"And how many signs exist in the heavens and the earth, over which most people pass and remain unaware of them!"},"1702":{"id":1702,"surah":12,"ayah":106,"verse":"And most of them are such that they do not believe in Allah except while ascribing partners (to Him)!"},"1703":{"id":1703,"surah":12,"ayah":107,"verse":"Do they remain complacent over Allah\u2019s punishment coming and surrounding them, or the Last Day coming suddenly upon them whilst they are unaware?"},"1704":{"id":1704,"surah":12,"ayah":108,"verse":"Proclaim, \u201cThis is my path \u2013 I call towards Allah; I, and whoever follows me, are upon perception; and Purity is to Allah \u2013 and I am not of the polytheists.\u201d"},"1705":{"id":1705,"surah":12,"ayah":109,"verse":"And all the Noble Messengers We sent before you, were exclusively men \u2013 towards whom We sent the divine revelations, and were dwellers of townships; so have not these people travelled in the land and seen what sort of fate befell those before them? And undoubtedly the abode of the Hereafter is better for the pious; so do you not have sense?"},"1706":{"id":1706,"surah":12,"ayah":110,"verse":"To the extent that when the Noble Messengers lost hope from the visible means, and the people thought that they had spoken wrongly, Our help came to them \u2013 therefore whoever We willed was saved; and Our punishment is never averted from the guilty."},"1707":{"id":1707,"surah":12,"ayah":111,"verse":"Indeed in their tidings is enlightenment for the men of understanding; this (the Qur\u2019an) is not some made up story but a confirmation of the Books before it, and a detailed explanation of all things, and a guidance and a mercy for the Muslims."},"1708":{"id":1708,"surah":13,"ayah":1,"verse":"Alif-Lam-Meem-Ra*; these are verses of the Book; and that which has been sent down upon you from your Lord is true, but most men do not believe. (* Alphabets of the Arabic language \u2013 Allah and to whomever He reveals, know their precise meanings.)"},"1709":{"id":1709,"surah":13,"ayah":2,"verse":"It is Allah Who raised up the heavens without columns for you to observe, then (in the manner befitting His Majesty) established Himself upon the Throne (of control), then subjected the sun and the moon; each one runs for up to an appointed term; Allah plans the works and explain the signs in detail, so that you may believe in meeting with your Lord."},"1710":{"id":1710,"surah":13,"ayah":3,"verse":"And it is He Who spread out the earth and made mountains as anchors and rivers in it; and in it made all kinds of fruits in pairs \u2013 He covers the night with the day; indeed in this are signs for people who ponder."},"1711":{"id":1711,"surah":13,"ayah":4,"verse":"And in the earth are various regions, and are close to each other \u2013 and gardens of grapes and fields, and date-palms, growing from a single branch and separately, all being given one water; and in fruits, We make some better than others in eating; indeed in this are signs for people of intellect."},"1712":{"id":1712,"surah":13,"ayah":5,"verse":"And if you are amazed, then indeed the amazement is at their saying that, \u201cWill we, after having turned to dust, be created anew?\u201d They are those who have disbelieved in their Lord; and they are those who will have shackles around their necks; and they are the people of hell; remaining in it forever."},"1713":{"id":1713,"surah":13,"ayah":6,"verse":"And they urge you to hasten the punishment before the mercy, whereas the punishments of those before them have concluded; and indeed your Lord gives the people a sort of pardon* despite their injustice; and indeed the punishment of your Lord is severe. (* By delaying their punishment despite their disbelief.)"},"1714":{"id":1714,"surah":13,"ayah":7,"verse":"The disbelievers say, \u201cWhy is not a sign sent down upon him from his Lord?\u201d You are purely a Herald of Warning, and a guide for all nations."},"1715":{"id":1715,"surah":13,"ayah":8,"verse":"Allah knows all what is inside the womb of every female, and every increase and decrease of the wombs; and all things are with Him by a set measure."},"1716":{"id":1716,"surah":13,"ayah":9,"verse":"The All Knowing of all things hidden and visible, the Great, the Supreme."},"1717":{"id":1717,"surah":13,"ayah":10,"verse":"Equal* are the one among you who speaks softly and one who speaks aloud, and one who is hidden during the night and one who walks during the daytime. (* For Allah.)"},"1718":{"id":1718,"surah":13,"ayah":11,"verse":"For man are angels of alternating duties, in front and behind him, who guard him by Allah\u2019s command; indeed Allah does not change His favour upon any nation until they change their own condition; and when Allah wills misfortune for a nation, it cannot be repelled; and they do not have any supporter besides Him."},"1719":{"id":1719,"surah":13,"ayah":12,"verse":"It is He Who shows you the lightning, for fear and for hope, and raises the heavy clouds."},"1720":{"id":1720,"surah":13,"ayah":13,"verse":"And the thunder proclaims His purity with praise, and the angels out of fear of Him; and He sends the bolt of lightning \u2013 it therefore strikes upon whom He wills, whilst they are disputing concerning Allah; and severe is His seizure."},"1721":{"id":1721,"surah":13,"ayah":14,"verse":"Only the prayer to Him is truthful; and whomever they pray to besides Him, do not hear them at all, but like one who has his hands outstretched towards water that it may come into his mouth, and it will never come; and every prayer of the disbelievers remains wandering."},"1722":{"id":1722,"surah":13,"ayah":15,"verse":"And to Allah only prostrate all those who are in the heavens and in the earth, willingly or helplessly \u2013 and their shadows \u2013 every morning and evening. (Command of prostration # 2)."},"1723":{"id":1723,"surah":13,"ayah":16,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWho is the Lord of the heavens and the earth?\u201d Proclaim, \u201cAllah\u201d; Say, \u201cSo have you appointed such as supporters besides Him, who can neither benefit nor harm themselves?\u201d; say, \u201cWill the blind and the sighted ever be equal? Or will the realms of darkness and the light ever be equal?\u201d Have they appointed such as partners to Allah who created something like Allah did? Therefore their creation and His creation seemed alike to them? Proclaim, \u201cAllah is the Creator of all things, and He Alone is the Dominant over all.\u201d"},"1724":{"id":1724,"surah":13,"ayah":17,"verse":"He sent down water from the sky, so valleys flowed according to their measure, therefore the water flow brought forth the froth swollen upon it; and upon which they ignite the fire, to make ornaments and tools, from that too rises a similar froth; Allah illustrates that this is the example of the truth and the falsehood; the froth then bursts and disappears; and that which is of use to people, remains in the earth; this is how Allah illustrates the examples."},"1725":{"id":1725,"surah":13,"ayah":18,"verse":"For those who obeyed the command of Allah is goodness and if those who did not obey Him owned all that is in the earth and in addition a similar one like it, they would give it to redeem their souls; it is they who will have a wretched account, and their destination is hell; and what a wretched resting place!"},"1726":{"id":1726,"surah":13,"ayah":19,"verse":"So will he, who knows that what is sent down upon you from your Lord is the truth, ever be equal to him who is blind? Only the men of understanding heed advice."},"1727":{"id":1727,"surah":13,"ayah":20,"verse":"Those who fulfil the pact of Allah, and do not renege on the covenant."},"1728":{"id":1728,"surah":13,"ayah":21,"verse":"Those who unite what Allah has commanded to be united, and fear their Lord, and apprehend the evil of the account."},"1729":{"id":1729,"surah":13,"ayah":22,"verse":"Those who were patient in order to gain their Lord\u2019s pleasure and kept the prayer established and spent in Our cause part of what We bestowed upon them, secretly and openly, and repel evil by responding with goodness \u2013 for them is the gain of the final abode."},"1730":{"id":1730,"surah":13,"ayah":23,"verse":"The everlasting Gardens of Eden which they will enter, and the deserving among their forefathers and their wives and their descendants \u2013 the angels will enter upon them from every gate."},"1731":{"id":1731,"surah":13,"ayah":24,"verse":"(Saying), \u201cPeace be upon you, the recompense of your patience \u2013 so what an excellent gain is the final abode!\u201d"},"1732":{"id":1732,"surah":13,"ayah":25,"verse":"And those who break the pact of Allah after its ratification, and sever what Allah has commanded to be joined, and spread turmoil in the earth \u2013 their share is only the curse and their destiny is the wretched abode."},"1733":{"id":1733,"surah":13,"ayah":26,"verse":"Allah eases and restricts the sustenance for whomever He wills; and the disbelievers rejoiced upon the life of this world; and the life of this world, as compared with the Hereafter, is just a brief utilisation."},"1734":{"id":1734,"surah":13,"ayah":27,"verse":"And the disbelievers said, \u201cWhy was not a sign sent down upon him from his Lord?\u201d Proclaim, \u201cIndeed Allah sends astray whomever He wills, and guides towards Himself the one who comes towards Him.\u201d"},"1735":{"id":1735,"surah":13,"ayah":28,"verse":"\u201cThose who accepted faith and whose hearts gain solace from the remembrance of Allah; pay heed! Only in the remembrance of Allah is the solace of hearts!\u201d"},"1736":{"id":1736,"surah":13,"ayah":29,"verse":"\u201cThose who accepted faith and did good deeds \u2013 for them is joy and an excellent outcome.\u201d"},"1737":{"id":1737,"surah":13,"ayah":30,"verse":"Similarly We sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) towards the nation, before whom other nations have passed away, for you to recite to them the divine revelations We sent down to you, whereas they are denying the Most Gracious; proclaim, \u201cHe is my Lord \u2013 there is no worship except for Him; I rely only upon Him and only towards Him is my return.\u201d"},"1738":{"id":1738,"surah":13,"ayah":31,"verse":"And were such a Qur\u2019an to come that would cause the mountains to move, or the earth to split asunder, or the dead to speak, even then these disbelievers would not believe; in fact, all matters are only at Allah\u2019s discretion; so have not the Muslims despaired in that, had Allah willed, He would have guided all mankind? Disasters shall continue to strike the disbelievers on account of their deeds, or descend near their homes until Allah\u2019s promise comes; indeed Allah does not break the promise."},"1739":{"id":1739,"surah":13,"ayah":32,"verse":"And indeed the Noble Messengers before you were also mocked \u2013 I therefore gave the mockers respite for some days and then seized them; so how (dreadful) was My punishment!"},"1740":{"id":1740,"surah":13,"ayah":33,"verse":"So is He who keeps a watch over the deeds of every soul (equal to their appointed partners)? And (yet) they ascribe partners to Allah! Proclaim, \u201cJust name them \u2013 or is it that you inform Him of something which in His knowledge does not exist in the earth \u2013 or is it just superficial talk?\u201d In fact, their deceit seems good to the disbelievers and are prevented from the path; and whomever Allah sends astray, so there is none to guide him."},"1741":{"id":1741,"surah":13,"ayah":34,"verse":"They will be punished in the life of this world, and indeed the punishment of the Hereafter is the most severe; and there is none to save them from Allah."},"1742":{"id":1742,"surah":13,"ayah":35,"verse":"A description of the Paradise which is promised to the pious; rivers flow beneath it; its fruits are unending, and its shade; this is the reward of those who fear; and the fate of the disbelievers is fire."},"1743":{"id":1743,"surah":13,"ayah":36,"verse":"And those to whom We gave the Book* rejoice at what is divinely revealed to you**; and of those groups are some who deny parts of it; proclaim, \u201cI am commanded only that I worship Allah and not ascribe any partner to Him; towards Him do I call, and towards Him only I have to return.\u201d (* Scholars among the Jews and Christians who accepted faith. ** The Holy Qur\u2019an.)"},"1744":{"id":1744,"surah":13,"ayah":37,"verse":"And similarly We have revealed this as a command in Arabic; and O listener (follower of this Prophet), if you follow their desires after the knowledge having come to you, then you will have neither a supporter nor any saviour against Allah."},"1745":{"id":1745,"surah":13,"ayah":38,"verse":"And indeed We sent Noble Messengers before you, and made wives and children for them; and it is not the task of any Noble Messenger to bring a sign except by Allah\u2019s command; and all promises have a time prescribed."},"1746":{"id":1746,"surah":13,"ayah":39,"verse":"Allah erases and confirms whatever He wills; and only with Him is the real script."},"1747":{"id":1747,"surah":13,"ayah":40,"verse":"And whether We show you a promise that is given to them, or call you to Us before it \u2013 so in any case, upon you is just the conveyance*, and for Us is the taking of the account. (* Of the message.)"},"1748":{"id":1748,"surah":13,"ayah":41,"verse":"Do they not perceive that We are reducing their dwellings from all directions? And Allah gives the command \u2013 there is none that can postpone His command; and He spends no time in taking account."},"1749":{"id":1749,"surah":13,"ayah":42,"verse":"And indeed those before them had plotted; therefore Allah Himself is the Master of all strategies; He knows all what every soul earns; and soon will the disbelievers realise for whom is the final abode."},"1750":{"id":1750,"surah":13,"ayah":43,"verse":"The disbelievers say, \u201cYou are not a (Noble) Messenger (of Allah)\u201d; proclaim, \u201cAllah is a Sufficient Witness between me and you, and (so is) he who has knowledge of the Book.\u201d"},"1751":{"id":1751,"surah":14,"ayah":1,"verse":"Alif-Lam-Ra; it is a Book which We have sent down upon you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) so that you may bring forth people from the realms of darkness into light \u2013 by the command of their Lord, towards the path of the Most Honourable, the Most Praiseworthy."},"1752":{"id":1752,"surah":14,"ayah":2,"verse":"Allah \u2013 because all that is in the heavens and in the earth belongs only to Him; and woe is for the disbelievers through a severe punishment."},"1753":{"id":1753,"surah":14,"ayah":3,"verse":"To whom the worldly life is dearer than the Hereafter, and who prevent from the way of Allah and wish deviations in it; they are in extreme error."},"1754":{"id":1754,"surah":14,"ayah":4,"verse":"And We sent every Noble Messenger with the same language as his people*, so that he may clearly explain to them; then Allah sends astray whomever He wills, and He guides whomever He wills; and He only is the Most Honourable, the Wise. (* Prophet Mohammed (peace and blessings be upon him) was taught all the languages as he is sent towards all.)"},"1755":{"id":1755,"surah":14,"ayah":5,"verse":"And indeed We sent Moosa along with Our signs that, \u201cBring your people from the realms of darkness into light \u2013 and remind them of the days of Allah*; indeed in them are signs for every greatly enduring, grateful person.\u201d (* When various favours were bestowed \u2013 in order to give thanks and be patient.)"},"1756":{"id":1756,"surah":14,"ayah":6,"verse":"And when Moosa said to his people, \u201cRemember Allah\u2019s favour upon you when He rescued you from the people of Firaun who were inflicting you with a dreadful torment, and were slaying your sons and sparing your daughters; and in it occurred a great favour of your Lord.\u201d* (* On the 10th day of Moharram \u2013 therefore the Jews used to fast on that day as thanksgiving.)"},"1757":{"id":1757,"surah":14,"ayah":7,"verse":"And remember when your Lord proclaimed, \u201cIf you give thanks, I will give you more, and if you are ungrateful then (know that) My punishment is severe.\u201d"},"1758":{"id":1758,"surah":14,"ayah":8,"verse":"And Moosa said, \u201cIf you and all who are in the earth turn disbelievers, then indeed Allah is definitely Independent (Unwanting), Most Praiseworthy.\u201d"},"1759":{"id":1759,"surah":14,"ayah":9,"verse":"Did not the tidings of those before you reach you? The people of Nooh, and the A\u2019ad and the Thamud, and those after them? Only Allah knows them; their Noble Messengers came to them with clear proofs, they therefore raised their hands towards their own mouths, and said, \u201cWe disbelieve in what you have been sent with, and we are in a deeply intriguing doubt regarding the path you call us to.\u201d"},"1760":{"id":1760,"surah":14,"ayah":10,"verse":"Their Noble Messengers said, \u201cWhat! You doubt concerning Allah, the Creator of the heavens and the earth? He calls you that He may forgive some of your sins and until the time of death give you a life free from punishment\u201d; they said, \u201cYou are just human beings like us; you wish to prevent us from what our forefathers used to worship \u2013 therefore now bring some clear proof to us.\u201d"},"1761":{"id":1761,"surah":14,"ayah":11,"verse":"Their Noble Messengers said to them, \u201cWe are indeed human beings like you, but Allah bestows favour upon whomever He wills, among His bondmen*; it is not our task to bring any proof to you except by the command of Allah; and only upon Allah must the Muslims rely.\u201d (* Therefore Prophets and other men are not equal in status.)"},"1762":{"id":1762,"surah":14,"ayah":12,"verse":"\u201cAnd what is the matter with us that we should not rely on Allah? He has in fact shown us our ways; and we will surely be patient upon the troubles you cause us; and those who trust must rely only upon Allah.\u201d"},"1763":{"id":1763,"surah":14,"ayah":13,"verse":"And the disbelievers said to their Noble Messengers, \u201cWe will surely expel you from our land, unless you accept our religion\u201d; so their Lord sent them the divine revelation that, \u201cIndeed We will destroy these unjust people.\u201d"},"1764":{"id":1764,"surah":14,"ayah":14,"verse":"\u201cAnd indeed We will establish you in the land after them; this is for him who fears to stand before Me and fears the commands of punishment declared by Me.\u201d"},"1765":{"id":1765,"surah":14,"ayah":15,"verse":"And they sought a decision, and every stubborn rebel was destroyed."},"1766":{"id":1766,"surah":14,"ayah":16,"verse":"Hell went after him, and he will be made to drink liquid pus*. (* Discharged from the wounds of other people in hell.)"},"1767":{"id":1767,"surah":14,"ayah":17,"verse":"He will sip it with difficulty but be unable to swallow, and death will approach him from every side and he will not die; and a severe punishment is after him."},"1768":{"id":1768,"surah":14,"ayah":18,"verse":"The state of those who disbelieve in their Lord is that their deeds are like ashes which the strong wind blew away on a stormy day; they got nothing from all that they earned; this is the extreme error."},"1769":{"id":1769,"surah":14,"ayah":19,"verse":"Have you not seen that Allah has created the heavens and the earth with true purpose? If He wills, He can remove you all and bring a new creation."},"1770":{"id":1770,"surah":14,"ayah":20,"verse":"And this is not at all difficult for Allah."},"1771":{"id":1771,"surah":14,"ayah":21,"verse":"They will all publicly come in the presence of Allah \u2013 then those who were weak will say to those who were the leaders, \u201cWe were your followers \u2013 is it possible for you to avert some of Allah\u2019s punishment from us?\u201d They will answer, \u201cIf Allah had guided us, we would have guided you; it is the same for us, whether we panic or patiently endure \u2013 we have no place of refuge.\u201d (* This conversation will take place between the disbelievers and their leaders.)"},"1772":{"id":1772,"surah":14,"ayah":22,"verse":"And Satan will say when the matter has been decided, \u201cIndeed Allah had given you a true promise \u2013 and the promise I gave you, I made it untrue to you; and I had no control over you except that I called you and you obeyed me; so do not accuse me, blame only yourselves; neither can I help you, nor can you help me; I have no concern with your ascribing me as a partner (to Allah); indeed for the unjust is a painful punishment.\u201d"},"1773":{"id":1773,"surah":14,"ayah":23,"verse":"And those who believed and did good deeds will be admitted into Gardens beneath which rivers flow, abiding in it forever, by the command of their Lord; their greeting in it is \u201cPeace\u201d."},"1774":{"id":1774,"surah":14,"ayah":24,"verse":"Did you not see how Allah illustrated the example of a sacred saying? Like a sacred tree, which has firm roots and branches reaching into heaven."},"1775":{"id":1775,"surah":14,"ayah":25,"verse":"Bearing fruit at all times by the command of its Lord; and Allah illustrates examples for people so that they may understand."},"1776":{"id":1776,"surah":14,"ayah":26,"verse":"And the example of a bad saying is like a filthy tree, which is cut off above the ground, therefore not having stability."},"1777":{"id":1777,"surah":14,"ayah":27,"verse":"Allah keeps the believers firm upon the truth in the life of this world and in the Hereafter; and Allah sends the unjust astray; and Allah may do whatever He wills."},"1778":{"id":1778,"surah":14,"ayah":28,"verse":"Did you not see those who exchanged the grace of Allah for ungratefulness and led their people down to the home of destruction?"},"1779":{"id":1779,"surah":14,"ayah":29,"verse":"Which is hell; they will enter it; and what a wretched place of stay!"},"1780":{"id":1780,"surah":14,"ayah":30,"verse":"And they ascribed equals to Allah to mislead from His way; say, \u201cEnjoy a while, for your end will be the fire.\u201d"},"1781":{"id":1781,"surah":14,"ayah":31,"verse":"Say to the bondmen of Mine who believe, that they must keep the prayer established and spend in Our way from what We have given them, secretly and publicly, before the advent of a day in which there will be no trade nor friendship."},"1782":{"id":1782,"surah":14,"ayah":32,"verse":"It is Allah Who created the heavens and the earth, and sent down water from the sky, therefore producing some fruits for you to eat; and subjected the ships for you, that they may sail upon the sea by His command; and subjected the rivers for you."},"1783":{"id":1783,"surah":14,"ayah":33,"verse":"And subjected the sun and the moon for you, which are constantly moving; and has subjected the night and the day for you."},"1784":{"id":1784,"surah":14,"ayah":34,"verse":"And He gave you much of what you seek; and if you enumerate the favours of Allah, you will never be able to count them; indeed man is very unjust, most ungrateful."},"1785":{"id":1785,"surah":14,"ayah":35,"verse":"And remember when Ibrahim prayed, \u201cO my Lord! Make this town (Mecca) a safe one, and safeguard me and my sons from worshipping idols.\u201d"},"1786":{"id":1786,"surah":14,"ayah":36,"verse":"\u201cO my Lord! The idols have led many people astray; so whoever followed me, is indeed mine; and whoever disobeyed me \u2013 then indeed You are Oft Forgiving, Most Merciful.\u201d"},"1787":{"id":1787,"surah":14,"ayah":37,"verse":"\u201cO our Lord! I have settled some of my descendants in a valley having no cultivation, near Your Sacred House \u2013 O our Lord! So that they may keep the prayer established, therefore incline some hearts of men towards them, and provide them fruits to eat \u2013 perhaps they may be thankful.\u201d"},"1788":{"id":1788,"surah":14,"ayah":38,"verse":"\u201cO our Lord! You know what we hide and what we disclose; and nothing is hidden from Allah, neither in the earth nor in the heavens.\u201d"},"1789":{"id":1789,"surah":14,"ayah":39,"verse":"\u201cAll praise is to Allah Who gave me Ismail and Ishaq, in my old age; indeed my Lord is the Listener of Prayer.\u201d"},"1790":{"id":1790,"surah":14,"ayah":40,"verse":"\u201cO my Lord! Maintain me as one who establishes prayer, and some of my descendants; O our Lord! and accept my prayer.\u201d"},"1791":{"id":1791,"surah":14,"ayah":41,"verse":"\u201cO our Lord! And forgive me, and my parents, and all the Muslims on the day when the account will be established.\u201d"},"1792":{"id":1792,"surah":14,"ayah":42,"verse":"And do not ever assume that Allah is unaware of what the unjust do; He does not give them respite but for a day in which the eyes will become fixed, staring."},"1793":{"id":1793,"surah":14,"ayah":43,"verse":"They shall come out running in bewilderment, with their heads raised, their gaze not returning to them; and their hearts will not have any calm."},"1794":{"id":1794,"surah":14,"ayah":44,"verse":"And warn the people of a day when the punishment will come upon them, therefore the unjust will say, \u201cO our Lord! Give us respite for a little while \u2013 for us to obey Your call and follow the Noble Messengers\u201d; (It will be said) \u201cSo had you not sworn before that, \u2018We have not to move to any place else from the earth\u2019?\u201d"},"1795":{"id":1795,"surah":14,"ayah":45,"verse":"\u201cAnd you dwelt in the houses of those who had wronged themselves and it became very clear to you how We had dealt with them, and We illustrated several examples for you.\u201d"},"1796":{"id":1796,"surah":14,"ayah":46,"verse":"And indeed they carried out their scheme, and their scheming is within Allah\u2019s control; and their scheme was not such that could move these mountains*. (* The revelations \/ signs of Allah.)"},"1797":{"id":1797,"surah":14,"ayah":47,"verse":"So do not ever assume that Allah will not fulfil His promise to His Noble Messengers; indeed Allah is the Dominant, the Avenger."},"1798":{"id":1798,"surah":14,"ayah":48,"verse":"On the day when the earth will be changed to other than this earth, and the heavens \u2013 and they will all come forth standing before Allah, the One, the Dominant above all."},"1799":{"id":1799,"surah":14,"ayah":49,"verse":"And on that day you will see the guilty linked together in chains."},"1800":{"id":1800,"surah":14,"ayah":50,"verse":"Their cloaks will be of pitch and fire will cover their faces."},"1801":{"id":1801,"surah":14,"ayah":51,"verse":"In order that Allah may repay each soul what it had earned; indeed Allah spends no time in judging the account."},"1802":{"id":1802,"surah":14,"ayah":52,"verse":"This is the message to be conveyed to all mankind \u2013 and in order to warn them with it, and for them to know that He is the only One God, and for men of understanding to heed advice."},"1803":{"id":1803,"surah":15,"ayah":1,"verse":"Alif-Lam-Ra; these are verses of the Book and the bright Qur\u2019an."},"1804":{"id":1804,"surah":15,"ayah":2,"verse":"The disbelievers will ardently wish that if only they had been Muslims! (At the time of death \/ in their graves \/ on the Day of Resurrection.)"},"1805":{"id":1805,"surah":15,"ayah":3,"verse":"Leave them to eat and enjoy, and for aspiration to involve them in play \u2013 so they will shortly come to know."},"1806":{"id":1806,"surah":15,"ayah":4,"verse":"And every dwelling that We destroyed, had a known decree for it."},"1807":{"id":1807,"surah":15,"ayah":5,"verse":"No group may advance its appointed promise nor postpone it."},"1808":{"id":1808,"surah":15,"ayah":6,"verse":"And they said, \u201cO you upon whom the Qur\u2019an has been sent, you are indeed insane.\u201d"},"1809":{"id":1809,"surah":15,"ayah":7,"verse":"\u201cWhy do you not bring angels to us, if you are truthful?\u201d"},"1810":{"id":1810,"surah":15,"ayah":8,"verse":"We do not send down angels without reason, and if the angels descend they would not get any respite!"},"1811":{"id":1811,"surah":15,"ayah":9,"verse":"Indeed We have sent down the Qur\u2019an, and indeed We Ourselves surely are its Guardians. (This is one of the miracles of the Qur\u2019an \u2013 no one has been able to change even one letter of its text, despite every effort. It has remained in its original form since the 6th Century (A. D) and will remain so forever. Other Holy Books such as the Torah and the Bible have lost their originality.)"},"1812":{"id":1812,"surah":15,"ayah":10,"verse":"And indeed We sent Noble Messengers before you, to the former nations."},"1813":{"id":1813,"surah":15,"ayah":11,"verse":"And never does a Noble Messenger come to them, but they mock at him."},"1814":{"id":1814,"surah":15,"ayah":12,"verse":"This is how We make the mocking* enter the hearts of the guilty. (* The acts of disbelief.)"},"1815":{"id":1815,"surah":15,"ayah":13,"verse":"They do not believe in him, and the tradition of earlier nations has passed."},"1816":{"id":1816,"surah":15,"ayah":14,"verse":"And if We open for them a gate in the heavens, to ascend it through the day \u2013"},"1817":{"id":1817,"surah":15,"ayah":15,"verse":"Even then they would say, \u201cOur sights have been hypnotised \u2013 in fact, a magic spell has been cast upon us.\u201d"},"1818":{"id":1818,"surah":15,"ayah":16,"verse":"And indeed We created towers in the skies, and beautified it for the beholders."},"1819":{"id":1819,"surah":15,"ayah":17,"verse":"And We have kept it guarded from every outcast devil."},"1820":{"id":1820,"surah":15,"ayah":18,"verse":"Except one who comes to eavesdrop \u2013 therefore a bright flame goes after him."},"1821":{"id":1821,"surah":15,"ayah":19,"verse":"And We spread out the earth, and placed mountains as anchors in it, and in it grew all things by a proper measure."},"1822":{"id":1822,"surah":15,"ayah":20,"verse":"And created livelihoods for you in it, and created those for whom you do not provide the sustenance."},"1823":{"id":1823,"surah":15,"ayah":21,"verse":"And there is not a thing the treasure* of which is not with Us; and We do not send it down except by a known measure. (* The power to create it.)"},"1824":{"id":1824,"surah":15,"ayah":22,"verse":"And We sent the winds that relieve the clouds\u2019 burden, therefore caused water to descend from the sky, then give it to you to drink; and you are not at all its treasurers."},"1825":{"id":1825,"surah":15,"ayah":23,"verse":"And indeed it is We Who give life and it is We Who cause death, and We are the Inheritors."},"1826":{"id":1826,"surah":15,"ayah":24,"verse":"And indeed We know those among you who came forward and indeed We know those among you who remained behind."},"1827":{"id":1827,"surah":15,"ayah":25,"verse":"Indeed your Lord only will raise them all on the Day of Resurrection; indeed only He is Wise, All Knowing."},"1828":{"id":1828,"surah":15,"ayah":26,"verse":"Indeed We created man from sounding clay made out of black smelly mud."},"1829":{"id":1829,"surah":15,"ayah":27,"verse":"And created the jinn before him, from smokeless fire."},"1830":{"id":1830,"surah":15,"ayah":28,"verse":"And recall when your Lord said to the angels, \u201cI will create man from sounding clay made out of black smelly mud.\u201d"},"1831":{"id":1831,"surah":15,"ayah":29,"verse":"\u201cTherefore when I have properly fashioned him and breathed into him a chosen noble soul from Myself, fall down before him in prostration.\u201d"},"1832":{"id":1832,"surah":15,"ayah":30,"verse":"Therefore all the angels, each and every one of them, fell prostrate."},"1833":{"id":1833,"surah":15,"ayah":31,"verse":"Except Iblis (Satan); he refused to be among those who prostrated."},"1834":{"id":1834,"surah":15,"ayah":32,"verse":"Said Allah, \u201cO Iblis! What happened to you that you stayed apart from those who prostrated?\u201d"},"1835":{"id":1835,"surah":15,"ayah":33,"verse":"He answered, \u201cIt does not befit me to prostrate myself to a human whom You have created from sounding clay made out of black smelly mud.\u201d"},"1836":{"id":1836,"surah":15,"ayah":34,"verse":"Said Allah, \u201cTherefore exit from Paradise, for you are an outcast.\u201d"},"1837":{"id":1837,"surah":15,"ayah":35,"verse":"\u201cAnd indeed you are accursed till the Day of Judgement.\u201d"},"1838":{"id":1838,"surah":15,"ayah":36,"verse":"He said, \u201cMy Lord! Grant me respite till the day when they will all be raised.\u201d"},"1839":{"id":1839,"surah":15,"ayah":37,"verse":"Said Allah, \u201cYou are of those given respite.\u201d"},"1840":{"id":1840,"surah":15,"ayah":38,"verse":"\u201cTill the Day of a well-known time.\u201d"},"1841":{"id":1841,"surah":15,"ayah":39,"verse":"He said, \u201cMy Lord! I swear by the fact that You sent me astray, I shall distract them in the earth, and I shall lead all of them astray.\u201d"},"1842":{"id":1842,"surah":15,"ayah":40,"verse":"\u201cExcept those among them who are Your chosen bondmen.\u201d"},"1843":{"id":1843,"surah":15,"ayah":41,"verse":"Said Allah, \u201cThis path* leads straight towards Me.\u201d (* The path of the chosen bondmen.)"},"1844":{"id":1844,"surah":15,"ayah":42,"verse":"\u201cIndeed you do not have any power over My bondmen, except those wanderers who follow you.\u201d (The devil is unsuccessful in tempting Allah\u2019s chosen bondmen to commit sin.)"},"1845":{"id":1845,"surah":15,"ayah":43,"verse":"And indeed hell is the promise for all of them."},"1846":{"id":1846,"surah":15,"ayah":44,"verse":"It has seven gates; for each gate is a portion assigned from them."},"1847":{"id":1847,"surah":15,"ayah":45,"verse":"Indeed the pious are amidst Gardens and springs."},"1848":{"id":1848,"surah":15,"ayah":46,"verse":"\u201cEnter them with peace, in safety.\u201d"},"1849":{"id":1849,"surah":15,"ayah":47,"verse":"And We have removed any resentments which were in their breasts \u2013 they are brothers, sitting face to face on thrones."},"1850":{"id":1850,"surah":15,"ayah":48,"verse":"Neither any hardship is to reach them in it, nor are they to be expelled from it."},"1851":{"id":1851,"surah":15,"ayah":49,"verse":"Inform My bondmen that undoubtedly, I surely am the Oft-Forgiving, the Most Merciful."},"1852":{"id":1852,"surah":15,"ayah":50,"verse":"And that indeed the punishment of Mine is a painful punishment."},"1853":{"id":1853,"surah":15,"ayah":51,"verse":"And inform them of Ibrahim\u2019s guests."},"1854":{"id":1854,"surah":15,"ayah":52,"verse":"When the angels came to him and said, \u201cPeace\"; he said, \u201cWe feel afraid of you.\u201d"},"1855":{"id":1855,"surah":15,"ayah":53,"verse":"They said, \u201cDo not fear \u2013 we convey to you the glad tidings of a knowledgeable boy.\u201d"},"1856":{"id":1856,"surah":15,"ayah":54,"verse":"He said, \u201cDo you convey to me the glad tidings upon old age reaching me? So upon what do you convey glad tidings?\u201d (* Prophet Ibrahim said this out of surprise.)"},"1857":{"id":1857,"surah":15,"ayah":55,"verse":"They said, \u201cWe have given you true tidings, therefore do not lose hope.\u201d"},"1858":{"id":1858,"surah":15,"ayah":56,"verse":"He said, \u201cWho is it that despairs from the mercy of his Lord, except those who are astray?\u201d"},"1859":{"id":1859,"surah":15,"ayah":57,"verse":"He said, \u201cAnd what is your next task, O the sent angels?\u201d"},"1860":{"id":1860,"surah":15,"ayah":58,"verse":"They said, \u201cWe have been sent towards a guilty nation.\u201d"},"1861":{"id":1861,"surah":15,"ayah":59,"verse":"\u201cExcept the family of Lut; we shall rescue all of them.\u201d"},"1862":{"id":1862,"surah":15,"ayah":60,"verse":"\u201cExcept his wife \u2013 we have decided that she is of those who will stay behind.\u201d"},"1863":{"id":1863,"surah":15,"ayah":61,"verse":"And when the sent angels came to the house of Lut."},"1864":{"id":1864,"surah":15,"ayah":62,"verse":"He said, \u201cYou are an unfamiliar people.\u201d"},"1865":{"id":1865,"surah":15,"ayah":63,"verse":"They said, \u201cIn fact we have brought to you the matter which these people doubted.\u201d"},"1866":{"id":1866,"surah":15,"ayah":64,"verse":"\u201cAnd we have brought to you a true command, and indeed we are truthful.\u201d"},"1867":{"id":1867,"surah":15,"ayah":65,"verse":"\u201cTherefore journey with your household while a portion of the night remains, and you tread behind them \u2013 and none of you may turn around and see, and proceed directly to the place you are commanded to.\u201d"},"1868":{"id":1868,"surah":15,"ayah":66,"verse":"And We informed him the decision of this command, that at morning the root of the disbelievers would be cut off."},"1869":{"id":1869,"surah":15,"ayah":67,"verse":"And the people of the city came rejoicing. (* To the house of Prophet Lut.)"},"1870":{"id":1870,"surah":15,"ayah":68,"verse":"He said, \u201cThese are my guests \u2013 do not dishonour me.\u201d"},"1871":{"id":1871,"surah":15,"ayah":69,"verse":"\u201cAnd fear Allah and do not disgrace me.\u201d"},"1872":{"id":1872,"surah":15,"ayah":70,"verse":"They said, \u201cHad we not forbidden you from meddling in the affairs of anyone?\u201d"},"1873":{"id":1873,"surah":15,"ayah":71,"verse":"He said, \u201cThese women of our tribe are my daughters, if you have to.\u201d"},"1874":{"id":1874,"surah":15,"ayah":72,"verse":"By your life O dear Prophet (Mohammed \u2013 peace and blessings be upon him) \u2013 they are indeed straying in their intoxication."},"1875":{"id":1875,"surah":15,"ayah":73,"verse":"Therefore the scream overcame them at sunrise."},"1876":{"id":1876,"surah":15,"ayah":74,"verse":"And We turned the township upside down and rained upon them stones of heated clay."},"1877":{"id":1877,"surah":15,"ayah":75,"verse":"Indeed in this are signs for people who perceive."},"1878":{"id":1878,"surah":15,"ayah":76,"verse":"And indeed that township is upon a road still in use."},"1879":{"id":1879,"surah":15,"ayah":77,"verse":"Indeed in this are signs for the believers."},"1880":{"id":1880,"surah":15,"ayah":78,"verse":"And indeed the Dwellers of the Woods were unjust."},"1881":{"id":1881,"surah":15,"ayah":79,"verse":"We therefore took revenge from them; and indeed both these townships are situated on an open road."},"1882":{"id":1882,"surah":15,"ayah":80,"verse":"And the people of the Hijr (rocks) denied the Noble Messengers."},"1883":{"id":1883,"surah":15,"ayah":81,"verse":"And We gave them Our signs, and they remained averse to them."},"1884":{"id":1884,"surah":15,"ayah":82,"verse":"And they used to carve dwellings in the hills, without fear."},"1885":{"id":1885,"surah":15,"ayah":83,"verse":"So the scream overcame them at morning."},"1886":{"id":1886,"surah":15,"ayah":84,"verse":"Therefore their earnings did not in the least benefit them."},"1887":{"id":1887,"surah":15,"ayah":85,"verse":"And We have not created the heavens and the earth and all that is between them, except with the Truth; and indeed the Last Day will come, therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him), forbear graciously."},"1888":{"id":1888,"surah":15,"ayah":86,"verse":"Indeed your Lord only is the Great Creator, the All Knowing."},"1889":{"id":1889,"surah":15,"ayah":87,"verse":"And indeed We have given you seven verses that are repeated, and the great Qur\u2019an."},"1890":{"id":1890,"surah":15,"ayah":88,"verse":"Do not direct your eyes towards the things We have given to some of their couples to enjoy, and do not at all grieve because of them, and take the Muslims within the folds of your mercy."},"1891":{"id":1891,"surah":15,"ayah":89,"verse":"And proclaim, \u201cIndeed I, yes I, am the clear Herald of Warning.\u201d"},"1892":{"id":1892,"surah":15,"ayah":90,"verse":"(Of a punishment) Like the one We sent down upon the dividers."},"1893":{"id":1893,"surah":15,"ayah":91,"verse":"Those who broke the Word of Allah into several parts. (Those who broke the Torah \/ Bible or changed its text \u2013 or those who called the Qur\u2019an a fabrication \/ poetry.)"},"1894":{"id":1894,"surah":15,"ayah":92,"verse":"Therefore, by your Lord, We shall question every one of them."},"1895":{"id":1895,"surah":15,"ayah":93,"verse":"About all what they used to do."},"1896":{"id":1896,"surah":15,"ayah":94,"verse":"Therefore publicly announce what you are commanded, and turn away from the polytheists."},"1897":{"id":1897,"surah":15,"ayah":95,"verse":"Indeed We suffice you against these mockers."},"1898":{"id":1898,"surah":15,"ayah":96,"verse":"Those who ascribe another God along with Allah; so they will soon come to know."},"1899":{"id":1899,"surah":15,"ayah":97,"verse":"And indeed We know that you are disheartened by their speech."},"1900":{"id":1900,"surah":15,"ayah":98,"verse":"Therefore proclaim the Purity of your Lord with praise, and be of those who prostrate."},"1901":{"id":1901,"surah":15,"ayah":99,"verse":"And keep worshipping your Lord, till death* comes to you. (* The certainty.)"},"1902":{"id":1902,"surah":16,"ayah":1,"verse":"The command of Allah is arriving soon, therefore do not seek to hasten it; Purity and Supremacy are to Him, above all the partners (they ascribe)."},"1903":{"id":1903,"surah":16,"ayah":2,"verse":"He sends down the angels with the soul of faith \u2013 the divine revelations \u2013 towards those among His bondmen He wills that, \u201cThere is no worship except for Me, therefore fear Me.\u201d"},"1904":{"id":1904,"surah":16,"ayah":3,"verse":"He created the heavens and the earth with the truth; Supreme is He above their ascribing of partners (to Him)."},"1905":{"id":1905,"surah":16,"ayah":4,"verse":"He created man from a drop of fluid, yet he is an open quarreller!"},"1906":{"id":1906,"surah":16,"ayah":5,"verse":"And He created cattle; in them are warm clothing and uses for you, and you eat from them."},"1907":{"id":1907,"surah":16,"ayah":6,"verse":"And in them is your elegance, when you bring them home at evening, and when you leave them to graze."},"1908":{"id":1908,"surah":16,"ayah":7,"verse":"And they transport your loads to a town where you could not reach except utterly exhausted; indeed your Lord is Most Compassionate, Most Merciful."},"1909":{"id":1909,"surah":16,"ayah":8,"verse":"And horses, and mules, and donkeys so that you may ride them, and for adornment; and He will create what you do not know*. (* All modern means of transport \u2013 by air, sea, land and even in space \u2013 evidence this.)"},"1910":{"id":1910,"surah":16,"ayah":9,"verse":"And the middle path rightly leads to Allah \u2013 any other path is wayward; and had He willed He would have brought all of you upon guidance."},"1911":{"id":1911,"surah":16,"ayah":10,"verse":"It is He Who sent down water from the sky \u2013 you drink from it, and from it are trees you use as pasture."},"1912":{"id":1912,"surah":16,"ayah":11,"verse":"With this water He produces for you crops, and olives, and dates, and grapes, and all kinds of fruit; indeed in this is a sign for people who ponder."},"1913":{"id":1913,"surah":16,"ayah":12,"verse":"And He subjected the night and the day for you \u2013 and the sun and the moon; and the stars are subjected to His command; indeed in this are signs for people of intellect."},"1914":{"id":1914,"surah":16,"ayah":13,"verse":"And the things He has created for you in the earth, of numerous colours; indeed in this is a sign for people who remember."},"1915":{"id":1915,"surah":16,"ayah":14,"verse":"And it is He Who subjected the sea for you, so you eat fresh meat from it, and extract ornaments from it which you wear; and you see ships ploughing through it and in order that you may seek His munificence and that you may give thanks."},"1916":{"id":1916,"surah":16,"ayah":15,"verse":"And He placed mountains as anchors in the earth so that it may not shake along with you, and streams and roads for you to find course."},"1917":{"id":1917,"surah":16,"ayah":16,"verse":"And landmarks; and they are guided by the star."},"1918":{"id":1918,"surah":16,"ayah":17,"verse":"So will He Who creates ever be like one who does not create? So do you not heed advice?"},"1919":{"id":1919,"surah":16,"ayah":18,"verse":"And if you enumerate the favours of Allah, you will never be able to count them; indeed Allah is Oft Forgiving, Most Merciful."},"1920":{"id":1920,"surah":16,"ayah":19,"verse":"And Allah knows what you conceal and what you disclose."},"1921":{"id":1921,"surah":16,"ayah":20,"verse":"And those whom they worship other than Allah, do not create anything but are themselves created."},"1922":{"id":1922,"surah":16,"ayah":21,"verse":"They are dead, not alive; and they do not know when will the people be raised."},"1923":{"id":1923,"surah":16,"ayah":22,"verse":"Your God is One God; so those who do not believe in the Hereafter \u2013 their hearts deny and they are proud."},"1924":{"id":1924,"surah":16,"ayah":23,"verse":"Certainly Allah knows what they hide and what they disclose; indeed He does not like the proud."},"1925":{"id":1925,"surah":16,"ayah":24,"verse":"And when it is said to them, \u201cWhat has your Lord sent down?\u201d, they say, \u201cThe tales of former people.\u201d"},"1926":{"id":1926,"surah":16,"ayah":25,"verse":"In order to bear their full burdens on the Day of Resurrection, and some burdens of those whom they mislead with their ignorance; pay heed! What an evil burden they bear!"},"1927":{"id":1927,"surah":16,"ayah":26,"verse":"Indeed those before them had plotted, so Allah seized the foundations of their building, therefore the roof fell down upon them from a height, and the punishment came upon them from a place they did not know."},"1928":{"id":1928,"surah":16,"ayah":27,"verse":"Then on the Day of Resurrection He will disgrace them and proclaim, \u201cWhere are My partners, concerning whom you disputed\u201d; the people of knowledge will say, \u201cAll disgrace and evil is upon the disbelievers this day.\u201d"},"1929":{"id":1929,"surah":16,"ayah":28,"verse":"Those whose souls the angels remove whilst they were wronging themselves; so now they will plead \u201cWe never used to do any wrong\u201d; \"Yes you did, why not? Indeed Allah well knows what you used to do.\""},"1930":{"id":1930,"surah":16,"ayah":29,"verse":"\u201cSo now enter the gates of hell, remaining in it for ever\u201d; so what an evil destination for the arrogant!"},"1931":{"id":1931,"surah":16,"ayah":30,"verse":"And it was said to the pious, \u201cWhat has your Lord sent down?\u201d They said, \u201cGoodness\u201d; for those who did good in this world is goodness, and the final home is the best; and indeed what an excellent final home for the pious."},"1932":{"id":1932,"surah":16,"ayah":31,"verse":"Everlasting Gardens of Eden which they will enter, beneath which rivers flow \u2013 in it they will get whatever they wish; this is how Allah rewards the pious."},"1933":{"id":1933,"surah":16,"ayah":32,"verse":"Those whose souls the angels remove in a state of purity, saying, \u201cPeace be upon you \u2013 enter Paradise, the reward of your deeds.\u201d"},"1934":{"id":1934,"surah":16,"ayah":33,"verse":"So what are they waiting for, except that the angels come upon them or that your Lord\u2019s punishment comes? Those before them did exactly this; and Allah did not oppress them at all, but it is they who used to wrong themselves."},"1935":{"id":1935,"surah":16,"ayah":34,"verse":"So the evils of their deeds struck them, and what they used to mock at surrounded them."},"1936":{"id":1936,"surah":16,"ayah":35,"verse":"And the polytheists said, \u201cHad Allah willed, we would not have worshipped anything besides Him \u2013 neither we nor our forefathers \u2013 and nor would we have forbidden anything, whilst being disassociated with Him\u201d; those before them did exactly this; so what is (incumbent) upon the Noble Messengers, except to plainly convey (the message)?"},"1937":{"id":1937,"surah":16,"ayah":36,"verse":"And indeed We sent to every nation a Noble Messenger (proclaiming) that \u201cWorship Allah and beware of the devil\u201d; therefore Allah guided some of them, and error proved true upon some of them; therefore travel in the land and see what sort of fate befell the deniers!"},"1938":{"id":1938,"surah":16,"ayah":37,"verse":"If you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) desire for their guidance, then indeed Allah does not guide one whom He misleads, and they do not have any aides."},"1939":{"id":1939,"surah":16,"ayah":38,"verse":"And they swore by Allah most vehemently in their oaths that, \u201cAllah will not raise up the dead\u201d; yes He will, why not? A true promise obligatory upon Him, but most men do not know."},"1940":{"id":1940,"surah":16,"ayah":39,"verse":"In order that He may make clear to them the matter in which they differed, and the disbelievers may realise that they were liars."},"1941":{"id":1941,"surah":16,"ayah":40,"verse":"And Our command for anything to occur, when We will it, is that We only say to it, \u201cBe\u201d \u2013 and it thereupon happens."},"1942":{"id":1942,"surah":16,"ayah":41,"verse":"And to those who migrated in Allah's cause after being oppressed, We shall indeed give them a good place in the world, and indeed the reward of the Hereafter is extremely great; if only the people knew!"},"1943":{"id":1943,"surah":16,"ayah":42,"verse":"Those who patiently endured and who rely only upon Allah."},"1944":{"id":1944,"surah":16,"ayah":43,"verse":"And We did not send before you except men, towards whom We sent divine revelations \u2013 so O people, ask the people of knowledge if you do not know. (* All the Prophets were men.)"},"1945":{"id":1945,"surah":16,"ayah":44,"verse":"Along with clear proofs and writings; and We have sent down this Remembrance towards you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) so that you may explain to mankind what has been revealed towards them, and that they may ponder."},"1946":{"id":1946,"surah":16,"ayah":45,"verse":"So do they who conspire evils, not fear that Allah may bury them in the earth, or that the punishment may come to them from a place they do not know?"},"1947":{"id":1947,"surah":16,"ayah":46,"verse":"Or that He may seize them while they move here and there, for they cannot escape?"},"1948":{"id":1948,"surah":16,"ayah":47,"verse":"Or that He may seize them whilst constantly ruining them? For indeed your Lord is Most Compassionate, Most Merciful."},"1949":{"id":1949,"surah":16,"ayah":48,"verse":"And have they not observed that the shadows of the things Allah has created incline to the right and to the left, in prostration to Allah, and that they are servile?"},"1950":{"id":1950,"surah":16,"ayah":49,"verse":"And to Allah only prostrates whatsoever is in the heavens and whatsoever moves in the earth, and the angels \u2013 and they are not proud."},"1951":{"id":1951,"surah":16,"ayah":50,"verse":"They bear upon themselves the fear of their Lord, and do only what they are commanded."},"1952":{"id":1952,"surah":16,"ayah":51,"verse":"And Allah has proclaimed, \u201cDo not ascribe two Gods; indeed He is the only One God; therefore fear Me alone.\u201d"},"1953":{"id":1953,"surah":16,"ayah":52,"verse":"And to Him only belongs all whatever is in the heavens and in the earth, and obeying Him only is obligatory; so will you fear anyone other than Allah?"},"1954":{"id":1954,"surah":16,"ayah":53,"verse":"And whatever blessings you have, are all from Allah \u2013 then whenever misfortune reaches you, towards Him only do you seek refuge."},"1955":{"id":1955,"surah":16,"ayah":54,"verse":"And then, when He averts the misfortune from you, a group among you starts ascribing partners to their Lord!"},"1956":{"id":1956,"surah":16,"ayah":55,"verse":"In order to deny the favours We have given them; so enjoy a little; for you will soon come to know."},"1957":{"id":1957,"surah":16,"ayah":56,"verse":"And for things unknown, they assign a portion of the sustenance We have given them; by Allah \u2013 you will certainly be questioned regarding all that you used to fabricate."},"1958":{"id":1958,"surah":16,"ayah":57,"verse":"And they assign daughters to Allah \u2013 Purity is to Him! \u2013 and assign for themselves what they wish."},"1959":{"id":1959,"surah":16,"ayah":58,"verse":"And when one among of them receives the glad tidings of a daughter, his face turns black for the day, and he remains seething."},"1960":{"id":1960,"surah":16,"ayah":59,"verse":"Hiding from the people because of the evil of the tidings; \u201cWill he keep her with disgrace, or bury her beneath the earth?\u201d; pay heed! Very evil is the judgement they impose!"},"1961":{"id":1961,"surah":16,"ayah":60,"verse":"Those who do not believe in the Hereafter, for them only is the evil state; and the Majesty of Allah is Supreme; and He only is the Most Honourable, the Wise."},"1962":{"id":1962,"surah":16,"ayah":61,"verse":"Were Allah to seize people on account of their injustices, He would not have left anyone walking on the earth, but He gives them respite up to an appointed promise; then when their promise comes they cannot go back one moment nor come forward."},"1963":{"id":1963,"surah":16,"ayah":62,"verse":"And they assign to Allah that which is abhorred by themselves, and their tongues speak the lies that goodness is for them; so it occurred that for them is the fire, and they have crossed the limits."},"1964":{"id":1964,"surah":16,"ayah":63,"verse":"By Allah, We indeed sent Noble Messengers to several nations before you, but Satan made their misdeeds appear good to them \u2013 so he only is their companion this day, and for them is a punishment, most painful."},"1965":{"id":1965,"surah":16,"ayah":64,"verse":"And We did not send down this Book towards you except for you to clearly explain to them the matters in which they may differ, and a guidance and a mercy for the believers."},"1966":{"id":1966,"surah":16,"ayah":65,"verse":"And Allah sent down water from the sky and with it revived the earth after its death; indeed in this is a sign for people who keep ears*. (* That listen to the truth.)"},"1967":{"id":1967,"surah":16,"ayah":66,"verse":"And indeed in cattle is a lesson for you; We provide you drink from what is in their bellies \u2013 pure milk from between the excretion and the blood, palatable for the drinkers."},"1968":{"id":1968,"surah":16,"ayah":67,"verse":"And from the fruits of date and grapes, for you make juices and good nourishment from them; indeed in this is a sign for people of intellect."},"1969":{"id":1969,"surah":16,"ayah":68,"verse":"And your Lord inspired the bee that, \u201cBuild homes in hills, and in trees, and in rooftops.\u201d"},"1970":{"id":1970,"surah":16,"ayah":69,"verse":"\u201cThen eat from all kinds of fruits, and tread the ways of your Lord which are soft and easy for you\u201d; from their bellies comes a drink of various colours, in which is health for mankind; indeed in this is a sign for people who ponder."},"1971":{"id":1971,"surah":16,"ayah":70,"verse":"And Allah created you, and will then remove your souls; and among you is one who is sent back to the most lowly age, so knowing nothing after having had knowledge; indeed Allah knows everything, is Able to do all things."},"1972":{"id":1972,"surah":16,"ayah":71,"verse":"And Allah has made some among you superior above others in livelihood; so those to whom the superiority is given, will not return their livelihood to their slaves, so they may all become equal in this respect; so do they deny the favours of Allah?"},"1973":{"id":1973,"surah":16,"ayah":72,"verse":"And Allah has created for you wives of your own breed, and has given you from your wives, sons and grandsons, and has provided sustenance for you from good things; so do they believe in falsehood and deny the favours of Allah?"},"1974":{"id":1974,"surah":16,"ayah":73,"verse":"And they worship such besides Allah, who do not have power to provide them any sustenance from the heavens or the earth, nor can they do anything."},"1975":{"id":1975,"surah":16,"ayah":74,"verse":"Therefore do not ascribe equals to Allah; indeed Allah knows whereas you do not know."},"1976":{"id":1976,"surah":16,"ayah":75,"verse":"Allah has illustrated an example \u2013 there is a slave, himself the property of another, not owning anything \u2013 and another one upon whom We have bestowed a good livelihood from Us, he therefore spends from it, secretly and publicly; will they be equal? All praise is to Allah; in fact, most of them do not know."},"1977":{"id":1977,"surah":16,"ayah":76,"verse":"And Allah has illustrated an example \u2013 two men \u2013 one of them dumb, unable to do anything, and he is a burden on his master \u2013 wherever his master sends him, he brings back no good; will he be equal to one who gives the command of justice and is on the Straight Path?"},"1978":{"id":1978,"surah":16,"ayah":77,"verse":"And for Allah only are the hidden things of the heavens and the earth, and the matter of Resurrection is not but like the batting of an eyelid \u2013 in fact closer than this; indeed Allah is Able to do all things."},"1979":{"id":1979,"surah":16,"ayah":78,"verse":"And Allah brought you forth from your mothers\u2019 wombs, whilst you did not know anything* \u2013 and gave you ears and eyes and hearts, for you to be grateful. (* Prophet Mohammed and Prophet Jesus, among others, are exceptions to this rule \u2013 peace and blessings be upon them.)"},"1980":{"id":1980,"surah":16,"ayah":79,"verse":"Have they not seen the birds, subservient in the open skies? No one holds them up except Allah; indeed in this are signs for the people who believe."},"1981":{"id":1981,"surah":16,"ayah":80,"verse":"And Allah has given you houses for staying, and made some houses from the hides of animals, which are easy for you on your day of travel and on the day of stopover \u2013 and from their wool, and fur, and hair, some household items and utilities for a while."},"1982":{"id":1982,"surah":16,"ayah":81,"verse":"And Allah has provided you shade with the things He has created, and created for you refuge in the hills, and created some clothing for you to protect you from the heat, and some clothing to protect you during conflict; this is how He completes His favour upon you, so that you may obey."},"1983":{"id":1983,"surah":16,"ayah":82,"verse":"Then if they turn away, O dear Prophet, (Mohammed \u2013 peace and blessings be upon him) upon you is nothing but to clearly convey (the message)."},"1984":{"id":1984,"surah":16,"ayah":83,"verse":"They recognise the favour* of Allah and then deny it, and most of them are disbelievers. (* Prophet Mohammed (peace and blessings be upon him) and \/ or all the favours of Allah.)"},"1985":{"id":1985,"surah":16,"ayah":84,"verse":"And the day when We will raise up a witness from every nation \u2013 then there will be no leave for the disbelievers, nor will they be appeased."},"1986":{"id":1986,"surah":16,"ayah":85,"verse":"And when the unjust will see the punishment, from that time on it will not be lightened for them, nor will they get respite."},"1987":{"id":1987,"surah":16,"ayah":86,"verse":"And when the polytheists will see their ascribed partners, they will say, \u201cOur Lord! These are our partners whom we used to worship besides You\u201d; so they will strike back at them with the saying, \u201cYou are indeed liars!\u201d"},"1988":{"id":1988,"surah":16,"ayah":87,"verse":"And on that day they will fall humbly before Allah, and they will lose all that they used to fabricate."},"1989":{"id":1989,"surah":16,"ayah":88,"verse":"Those who disbelieved and prevented from the way of Allah \u2013 We added punishment upon the punishment \u2013 the recompense of their mischief."},"1990":{"id":1990,"surah":16,"ayah":89,"verse":"And the day when We will raise from every group, a witness from among them, in order to testify against them and will bring you O dear Prophet (Mohammed \u2013 peace and blessings be upon him) as a witness upon them all; and We have sent down this Qur\u2019an upon you which is a clear explanation of all things, and a guidance and a mercy and glad tidings to the Muslims."},"1991":{"id":1991,"surah":16,"ayah":90,"verse":"Indeed Allah decrees the commands of justice and kindness, and of giving to relatives, and forbids from the shameful and evil and rebellion; He advises you so that you may pay heed."},"1992":{"id":1992,"surah":16,"ayah":91,"verse":"And fulfil the covenant of Allah when you have made the promise, and do not break your oaths after ratifying them, and you have made Allah a Guarantor over you; indeed Allah knows your deeds."},"1993":{"id":1993,"surah":16,"ayah":92,"verse":"And do not be like the woman broke her thread into bits after she had manufactured it; you make your oaths a phoney excuse between yourselves lest one nation may not be more than the other; Allah just tries you with it; and He will surely clarify for you on the Day of Resurrection, the matters in which you differed."},"1994":{"id":1994,"surah":16,"ayah":93,"verse":"Had Allah willed He would have made you all one nation, but He sends astray whomever He wills and guides whomever He wills; and you will certainly be questioned regarding your deeds."},"1995":{"id":1995,"surah":16,"ayah":94,"verse":"And do not make your oaths phoney excuses between yourselves, so that a foot may not slip after being steadfast and you may taste evil because you were preventing from Allah\u2019s way; and lest you be severely punished."},"1996":{"id":1996,"surah":16,"ayah":95,"verse":"And do not exchange the covenant of Allah to procure an abject price; that which is with Allah is better for you, if you know."},"1997":{"id":1997,"surah":16,"ayah":96,"verse":"What you have will perish, and that which is with Allah will remain forever; and indeed We shall pay the patiently enduring a recompense which befits the best of their deeds."},"1998":{"id":1998,"surah":16,"ayah":97,"verse":"Whoever does good deeds \u2013 whether a male or female \u2013 and is a Muslim, We shall sustain him an excellent life, and shall certainly pay them a recompense which befits the best of their deeds."},"1999":{"id":1999,"surah":16,"ayah":98,"verse":"And when you recite the Qur\u2019an, seek the refuge of Allah from Satan the outcast."},"2000":{"id":2000,"surah":16,"ayah":99,"verse":"Indeed he has no power over the believers and who rely only upon their Lord."},"2001":{"id":2001,"surah":16,"ayah":100,"verse":"Satan\u2019s power is only over those who make friendship with him and ascribe him as a partner (in worship)."},"2002":{"id":2002,"surah":16,"ayah":101,"verse":"And when We replace a verse* by another \u2013 and Allah well knows what He sends down \u2013 the disbelievers say, \u201cYou are just fabricating\u201d; in fact most of them do not know. (* The command of one verse by another.)"},"2003":{"id":2003,"surah":16,"ayah":102,"verse":"Proclaim, \u201cThe Holy Spirit* has rightly brought it down from your Lord, to make the believers steadfast, and a guidance and glad tidings for the Muslims.\u201d (* Angel Jibreel \u2013 peace and blessings be upon him.)"},"2004":{"id":2004,"surah":16,"ayah":103,"verse":"And indeed We know that they say, \u201cThis Qur\u2019an is being taught by some other man\u201d; the one they refer to speaks a foreign language, whereas this is clear Arabic!"},"2005":{"id":2005,"surah":16,"ayah":104,"verse":"Those who disbelieve in the signs of Allah \u2013 Allah does not guide them, and for them is a punishment, most painful."},"2006":{"id":2006,"surah":16,"ayah":105,"verse":"Only those who do not believe in Allah\u2019s signs attribute lies and fabrications; and they themselves are liars."},"2007":{"id":2007,"surah":16,"ayah":106,"verse":"The one who disbelieves in Allah after accepting faith \u2013 except him who is forced to and whose heart is still firmly upon faith \u2013 but whoever is wholeheartedly a disbeliever, upon them is the wrath of Allah; and for them is a great punishment."},"2008":{"id":2008,"surah":16,"ayah":107,"verse":"This is because the worldly life was dearer to them than the Hereafter; and because Allah does not guide such disbelieving people."},"2009":{"id":2009,"surah":16,"ayah":108,"verse":"These are the ones whose hearts, ears, and eyes Allah has sealed; and they are the neglectful."},"2010":{"id":2010,"surah":16,"ayah":109,"verse":"So it occurred that they are the losers in the Hereafter."},"2011":{"id":2011,"surah":16,"ayah":110,"verse":"Then indeed your Lord \u2013 for those who migrated after they had been oppressed, and then fought and remained patient \u2013 indeed your Lord is then, surely, Oft Forgiving, Most Merciful."},"2012":{"id":2012,"surah":16,"ayah":111,"verse":"The day when every soul will come quarrelling against itself, and every soul will be fully repaid for what it did, and they will not be wronged."},"2013":{"id":2013,"surah":16,"ayah":112,"verse":"And Allah has illustrated an example of a township \u2013 which dwelt in peace and security, its provisions coming in abundance from every side \u2013 in response the township started being ungrateful of Allah\u2019s favours, therefore Allah made it taste the punishment by covering it with a cloak of starvation and fear, on account of their deeds."},"2014":{"id":2014,"surah":16,"ayah":113,"verse":"And indeed a Noble Messenger came to them from among them \u2013 in response they denied him, and therefore the punishment seized them, and they were unjust."},"2015":{"id":2015,"surah":16,"ayah":114,"verse":"Therefore eat the lawful and good sustenance Allah has provided you, and be grateful for the blessings of your Lord, if you worship Him."},"2016":{"id":2016,"surah":16,"ayah":115,"verse":"Only these are forbidden for you \u2013 the carrion, and blood, and flesh of swine, and that which has been slaughtered while proclaiming the name of any other besides Allah; so one who is compelled and does not eat out of desire, nor more than what is necessary, then indeed Allah is Oft Forgiving, Most Merciful."},"2017":{"id":2017,"surah":16,"ayah":116,"verse":"And do not say \u2013 the lie which your tongues speak \u2013 \u201cThis is lawful, and this is forbidden\u201d in order to fabricate a lie against Allah; indeed those who fabricate lies against Allah will never prosper."},"2018":{"id":2018,"surah":16,"ayah":117,"verse":"A little enjoyment \u2013 and for them is a punishment, most painful."},"2019":{"id":2019,"surah":16,"ayah":118,"verse":"And especially for the Jews We forbade which We related to you earlier; and We did not oppress them, but it is they who wronged themselves."},"2020":{"id":2020,"surah":16,"ayah":119,"verse":"Then indeed your Lord \u2013 for those who unwittingly commit evil and then repent and reform themselves \u2013 indeed your Lord is then, surely, Oft Forgiving, Most Merciful."},"2021":{"id":2021,"surah":16,"ayah":120,"verse":"Indeed Ibrahim was a leader, obedient to Allah, and detached from all; and he was not a polytheist."},"2022":{"id":2022,"surah":16,"ayah":121,"verse":"Grateful for His blessings; Allah chose him and guided him to the Straight Path."},"2023":{"id":2023,"surah":16,"ayah":122,"verse":"And We gave him goodness in this world; and indeed in the Hereafter he is worthy of proximity."},"2024":{"id":2024,"surah":16,"ayah":123,"verse":"And then We sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) the divine revelation that, \u201cFollow the religion of Ibrahim, who was free from all falsehood; and was not a polytheist.\u201d"},"2025":{"id":2025,"surah":16,"ayah":124,"verse":"The Sabbath was made obligatory only upon those who differed in it; and indeed your Lord will judge between them on the Day of Resurrection concerning the matter in which they differed."},"2026":{"id":2026,"surah":16,"ayah":125,"verse":"Call towards the path of your Lord with sound planning and good advice, and debate with them in the best possible way; indeed your Lord well knows him who has strayed from His path, and He well knows the guided."},"2027":{"id":2027,"surah":16,"ayah":126,"verse":"And if you mete out punishment, then punish similarly as you were afflicted; and if you patiently endure, then indeed patience is better for the patiently enduring."},"2028":{"id":2028,"surah":16,"ayah":127,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) patiently endure \u2013 and your patience is only due to the guidance of Allah \u2013 and do not grieve for them, and do not be disheartened by their deceits."},"2029":{"id":2029,"surah":16,"ayah":128,"verse":"Indeed Allah is with the pious and the virtuous."},"2030":{"id":2030,"surah":17,"ayah":1,"verse":"Purity is to Him Who took His bondman in a part of the night from the Sacred Mosque to the Aqsa Mosque around which We have placed blessings, in order that We may show him Our great signs; indeed he is the listener, the beholder. (This verse refers to the physical journey of Prophet Mohammed \u2013 peace and blessings be upon him \u2013 to Al Aqsa Mosque and from there to the heavens and beyond. The entire journey back to Mecca was completed within a small part of the night.)"},"2031":{"id":2031,"surah":17,"ayah":2,"verse":"And We gave Moosa the Book, and made it a guidance for the Descendants of Israel that, \u201cDo not appoint anyone as a Trustee besides Me.\u201d"},"2032":{"id":2032,"surah":17,"ayah":3,"verse":"The descendants of those whom We boarded along with Nooh; he was indeed a grateful bondman."},"2033":{"id":2033,"surah":17,"ayah":4,"verse":"And We decreed for the Descendants of Israel in the Book that, \u201cYou will indeed create great turmoil in the earth twice, and you will surely become very proud.\u201d"},"2034":{"id":2034,"surah":17,"ayah":5,"verse":"So when the first of those promises came, We sent upon you Our extremely militant bondmen \u2013 they therefore entered the cities pursuing you; and this was a promise that had to be fulfilled."},"2035":{"id":2035,"surah":17,"ayah":6,"verse":"We then reversed your attack upon them, and aided you with wealth and sons and increased your numbers."},"2036":{"id":2036,"surah":17,"ayah":7,"verse":"If you do good deeds, you will for your own good \u2013 and if you commit evil, it is for yourself; therefore when the second of those promises came \u2013 so the enemy maim your faces, and enter the mosque as they had entered it the first time, and destroy until they ruin all they could capture."},"2037":{"id":2037,"surah":17,"ayah":8,"verse":"It is likely that your Lord may have mercy on you; and if you repeat the mischief, We will repeat the punishment; and We have made hell a prison for the disbelievers."},"2038":{"id":2038,"surah":17,"ayah":9,"verse":"Indeed this Qur\u2019an guides to the most Straight Path, and gives glad tidings to the believers who do good deeds, that for them is a great reward."},"2039":{"id":2039,"surah":17,"ayah":10,"verse":"And those who do not believe in the Hereafter \u2013 We have kept prepared for them a punishment, most painful."},"2040":{"id":2040,"surah":17,"ayah":11,"verse":"And man prays for evil like the way he seeks goodness; and man is very hasty."},"2041":{"id":2041,"surah":17,"ayah":12,"verse":"And We created the night and the day as two signs \u2013 We therefore kept the sign of the night indistinct, and the sign of the day visible \u2013 so that you may seek the munificence of your Lord, and know the calculation of the years, and the accounting; and We have explained all things in detail, distinctively."},"2042":{"id":2042,"surah":17,"ayah":13,"verse":"And We have attached the destiny of every man to his neck; and We shall bring forth a register for him on the Day of Resurrection, which he will find open."},"2043":{"id":2043,"surah":17,"ayah":14,"verse":"It will be said, \u201cRead your ledger; this day you are sufficient to take your own account.\u201d"},"2044":{"id":2044,"surah":17,"ayah":15,"verse":"Whoever came to guidance, has come for his own good; and whoever went astray, has strayed for his own ruin; and no burdened soul will bear another\u2019s burden; and We never punish until We have sent a Noble Messenger."},"2045":{"id":2045,"surah":17,"ayah":16,"verse":"And when We will to destroy a township We send the commands to its prosperous people, thereupon they do not obey them, and so the Word is proved upon it \u2013 We therefore destroy and ruin it."},"2046":{"id":2046,"surah":17,"ayah":17,"verse":"And many a generation We did destroy after Nooh! And Allah is Sufficient, Aware of the sins of His bondmen, the Beholder."},"2047":{"id":2047,"surah":17,"ayah":18,"verse":"Whoever desires this fleeting one, We may give him quickly \u2013 whatever We will, to whomever We will; and then assign hell for him; for him to enter it condemned, pushed around."},"2048":{"id":2048,"surah":17,"ayah":19,"verse":"And whoever desires the Hereafter and strives for it accordingly, and is a believer \u2013 so only their effort has borne fruit."},"2049":{"id":2049,"surah":17,"ayah":20,"verse":"We provide help to all \u2013 to these and to those, by the bestowal of your Lord; and there is no constraint on the bestowal of your Lord."},"2050":{"id":2050,"surah":17,"ayah":21,"verse":"Observe how We have given superiority to some over others; and indeed the Hereafter is the greatest in rank and the highest in excellence."},"2051":{"id":2051,"surah":17,"ayah":22,"verse":"O listener, do not set up another God with Allah, for you will then remain seated condemned, helpless."},"2052":{"id":2052,"surah":17,"ayah":23,"verse":"And your Lord has ordained that you do not worship anyone except Him, and treat your parents with kindness; if either of them or both reach old age in your presence, do not say \u201cUff\u201d* to them and do not rebuff them, and speak to them with the utmost respect. (* Any expression of disgust.)"},"2053":{"id":2053,"surah":17,"ayah":24,"verse":"And lower your wing humbly for them, with mercy, and pray, \u201cMy Lord! Have mercy on them both, the way they nursed me when I was young.\u201d"},"2054":{"id":2054,"surah":17,"ayah":25,"verse":"Your Lord is Well Aware of what is in your hearts; if you are worthy, then indeed He is Oft Forgiving for those who repent."},"2055":{"id":2055,"surah":17,"ayah":26,"verse":"And give the relatives their rights, and to the needy, and to the traveller; and do not waste needlessly."},"2056":{"id":2056,"surah":17,"ayah":27,"verse":"Indeed those who needlessly waste are brothers of the devils; and the devil is very ungrateful to his Lord."},"2057":{"id":2057,"surah":17,"ayah":28,"verse":"And if you turn away from these*, expecting the mercy of your Lord, for which you hope, then speak to them an easy word. (* The companions of the Holy Prophet, who sought his assistance.)"},"2058":{"id":2058,"surah":17,"ayah":29,"verse":"And do not keep your hand tied to your neck nor open it completely, lest you remain seated \u2013 reproached, weary."},"2059":{"id":2059,"surah":17,"ayah":30,"verse":"Indeed your Lord eases the livelihood and restricts it, for whomever He wills; He Well Knows, Beholds His bondmen."},"2060":{"id":2060,"surah":17,"ayah":31,"verse":"And do not kill your children, fearing poverty; We shall provide sustenance to them as well as to you; indeed killing them is a great mistake."},"2061":{"id":2061,"surah":17,"ayah":32,"verse":"And do not approach adultery \u2013 it is indeed a shameful deed; and a very evil way."},"2062":{"id":2062,"surah":17,"ayah":33,"verse":"And do not wrongfully kill any living being which Allah has forbidden; and for whoever is slain wrongfully, We have given the authority to his heir, so he should not cross limits in slaying; he will surely be helped."},"2063":{"id":2063,"surah":17,"ayah":34,"verse":"And do not approach the wealth of the orphan except in the best possible way, till he reaches adulthood; and fulfil the promise; indeed the promise will be asked about."},"2064":{"id":2064,"surah":17,"ayah":35,"verse":"And correctly measure when you measure, and weigh correctly with the scales; this is better, and has a better outcome."},"2065":{"id":2065,"surah":17,"ayah":36,"verse":"And do not pursue the matter you do not have the knowledge of; indeed the ear, and the eye, and the heart \u2013 each of these will be questioned."},"2066":{"id":2066,"surah":17,"ayah":37,"verse":"And do not walk haughtily on the earth; you can never split the earth, nor be as high as the hills."},"2067":{"id":2067,"surah":17,"ayah":38,"verse":"Of all this related before, the evil among it is disliked by your Lord."},"2068":{"id":2068,"surah":17,"ayah":39,"verse":"This is part of the divine revelations of wisdom that your Lord has sent down to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him); and O listener, do not set up another God with Allah, for you will then be thrown into hell \u2013 rebuked, rebuffed."},"2069":{"id":2069,"surah":17,"ayah":40,"verse":"Has your Lord chosen sons for you, and created daughters for Himself from among the angels? Indeed you utter a profound word!"},"2070":{"id":2070,"surah":17,"ayah":41,"verse":"And We have explained in this Qur\u2019an in various ways, for them to understand; and it increases nothing except their hatred towards it."},"2071":{"id":2071,"surah":17,"ayah":42,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIf there were other Gods besides Him\u201d \u2013 as they utter \u2013 \u201cthen they would have certainly found a way towards* the Owner of the Throne!\u201d. (* To fight against Him.)"},"2072":{"id":2072,"surah":17,"ayah":43,"verse":"Purity and Supremacy are to Him from what they utter, a Great Supremacy!"},"2073":{"id":2073,"surah":17,"ayah":44,"verse":"The seven heavens and the earth and all those in them say His Purity; and there is not a thing that does not proclaim His purity with praise, but you do not understand their proclamation of purity; indeed He is Most Forbearing, Oft Forgiving."},"2074":{"id":2074,"surah":17,"ayah":45,"verse":"And when you read the Qur\u2019an O dear Prophet (Mohammed \u2013 peace and blessings upon him), We created an invisible barrier between you and those who do not believe in the Hereafter."},"2075":{"id":2075,"surah":17,"ayah":46,"verse":"And We placed covers upon their hearts so they may not understand it, and deafness in their ears; and when you mention your Only Lord in the Qur\u2019an, they flee turning their backs in hatred."},"2076":{"id":2076,"surah":17,"ayah":47,"verse":"We know well why they listen when they lend ears to you, and when they discuss among themselves, when the unjust say, \u201cYou have not followed except a man who is under a magic spell.\u201d"},"2077":{"id":2077,"surah":17,"ayah":48,"verse":"See the kind of examples they invent for you! They therefore strayed, so cannot find a path."},"2078":{"id":2078,"surah":17,"ayah":49,"verse":"And they say, \u201cWhen we have become bones and decomposed, will we really be raised up anew?\u201d"},"2079":{"id":2079,"surah":17,"ayah":50,"verse":"Proclaim, \u201cBecome stones or iron.\u201d"},"2080":{"id":2080,"surah":17,"ayah":51,"verse":"\u201cOr some other creation which you deem great\u201d; so they will now say, \u201cWho will create us again?\u201d; proclaim, \u201cHe Who created you for the first time\u201d; so now they will mockingly shake their heads at you, and question, \u201cWhen is this going to occur?\u201d; say, \u201cIt could perhaps be soon.\u201d"},"2081":{"id":2081,"surah":17,"ayah":52,"verse":"\u201cThe day when He will call you and you will come praising Him, thinking you have stayed only a little.\u201d"},"2082":{"id":2082,"surah":17,"ayah":53,"verse":"And tell My bondmen to speak that which is the best; undoubtedly Satan sows discord among them; indeed Satan is man\u2019s open enemy."},"2083":{"id":2083,"surah":17,"ayah":54,"verse":"Your Lord knows you well; if He wills He may have mercy upon you, or if He wills, He may punish you; and We have not sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) as a guardian over them."},"2084":{"id":2084,"surah":17,"ayah":55,"verse":"And your Lord knows well all those who are in the heavens and the earth; and indeed among the Prophets We gave excellence to some above others, and We gave the Zaboor to Dawud."},"2085":{"id":2085,"surah":17,"ayah":56,"verse":"Proclaim, \u201cCall upon those whom you assume besides Allah \u2013 so they do not have any power to relieve the misfortune from you nor to avert it.\u201d"},"2086":{"id":2086,"surah":17,"ayah":57,"verse":"The devoted bondmen whom these disbelievers worship, themselves seek the means of proximity from their Lord, that who among them is the closest (to his Lord), and hope for His mercy and fear His punishment; indeed the punishment of your Lord is to be feared."},"2087":{"id":2087,"surah":17,"ayah":58,"verse":"And there is no such dwelling but which We shall destroy before the Day of Resurrection, or punish it severely; this is written in the Book."},"2088":{"id":2088,"surah":17,"ayah":59,"verse":"And We remained constrained from sending such signs, because the former people denied them; and We gave the Thamud the she-camel for enlightenment, so they oppressed it; and We do not send such signs except to warn."},"2089":{"id":2089,"surah":17,"ayah":60,"verse":"And when We proclaimed to you, \u201cIndeed all mankind is within the control of your Lord\u201d; and We did not create the spectacle* which We showed you except to try mankind, and the Tree** which is cursed in the Qur\u2019an; and We warn them \u2013 so nothing increases for them except extreme rebellion. (* The Ascent of the Holy Prophet to the heavens and beyond, which the disbelievers denied as just a dream. ** The Zakkum tree which will grow in hell and be the food for its inhabitants.)"},"2090":{"id":2090,"surah":17,"ayah":61,"verse":"And recall when We ordered the angels that, \u201cProstrate before Adam\u201d \u2013 so they all prostrated except Iblis; he said, \u201cShall I prostrate before one whom You have created from clay?\u201d"},"2091":{"id":2091,"surah":17,"ayah":62,"verse":"He said, \u201cBehold this* \u2013 the one whom You have honoured above me \u2013 if You give me respite till the Day of Resurrection, I will surely crush his descendants, except a few.\u201d (* Prophet Adam.)"},"2092":{"id":2092,"surah":17,"ayah":63,"verse":"He said, \u201cBe gone \u2013 therefore whoever among them follows you \u2013 so hell is the recompense for you all, a sufficient punishment.\u201d"},"2093":{"id":2093,"surah":17,"ayah":64,"verse":"\u201cAnd mislead those whom you can among them with your voice, and raise an army against them with your cavalry and infantry, and be their partner in wealth and children, and give them promises\u201d; and Satan does not promise them except with deception."},"2094":{"id":2094,"surah":17,"ayah":65,"verse":"\u201cIndeed My bondmen* \u2013 you do not have any power over them\u201d; and your Lord is Sufficient as a Trustee. (* The chosen virtuous bondmen)"},"2095":{"id":2095,"surah":17,"ayah":66,"verse":"Your Lord is He Who sails the ship upon the seas for you, so that you may seek His munificence; indeed He is Most Merciful upon you."},"2096":{"id":2096,"surah":17,"ayah":67,"verse":"And when calamity strikes you upon the sea, all those whom you worship are lost, except Him; then when He rescues you towards land, you turn away; and man is extremely ungrateful."},"2097":{"id":2097,"surah":17,"ayah":68,"verse":"Are you unafraid that He may bury an edge of the same land along with you, or send a shower of stones upon you, and you find no supporter for yourselves?"},"2098":{"id":2098,"surah":17,"ayah":69,"verse":"Or are you unafraid that He may again take you back to the sea, then send against you a ship-breaking gust of wind, therefore drowning you because of your disbelief \u2013 then you may not find for yourself anyone to come after Us for this?"},"2099":{"id":2099,"surah":17,"ayah":70,"verse":"Indeed We have honoured the Descendants of Adam and transported them over land and sea, and gave them good things as sustenance, and made them better than most of Our creation."},"2100":{"id":2100,"surah":17,"ayah":71,"verse":"On the day when We shall summon every group along with its leader; so whoever is given his register in his right hand \u2013 these will read their accounts and their rights will not be suppressed even a thread. (* They will be given the full reward.)"},"2101":{"id":2101,"surah":17,"ayah":72,"verse":"Whoever is blind* in this life will be blind in the Hereafter, and even more astray. (* To the truth \u2013 disbelieving.)"},"2102":{"id":2102,"surah":17,"ayah":73,"verse":"And it was close that they were about to mislead you somewhat from the divine revelation We sent to you, for you to attribute something else* to Us; and if it were**, they would have accepted you as a friend. (* Other than the divine revelation. ** Which is impossible.)"},"2103":{"id":2103,"surah":17,"ayah":74,"verse":"And had We not kept you steadfast, possibly you might have inclined to them just a little."},"2104":{"id":2104,"surah":17,"ayah":75,"verse":"And if it were*, We would then have made you taste a double life and a double death \u2013 you would then not find any supporter against Us. (* Which is impossible.)"},"2105":{"id":2105,"surah":17,"ayah":76,"verse":"And indeed it was close that they frighten you in the land for them to oust you from it \u2013 and if it were, they would not have stayed after you, but a little."},"2106":{"id":2106,"surah":17,"ayah":77,"verse":"The tradition of the Noble Messengers We sent before you \u2013 and you will not find Our rules changing."},"2107":{"id":2107,"surah":17,"ayah":78,"verse":"Keep the prayer established, from the declining of the sun until darkness of the night, and the Qur\u2019an at dawn; indeed the angels witness the reading of the Qur\u2019an at dawn."},"2108":{"id":2108,"surah":17,"ayah":79,"verse":"And forego sleep* in some part of the night \u2013 an increase for you**; it is likely your Lord will set you on a place where everyone will praise you***. (* For worship. ** Obligatory only upon the Holy Prophet. *** On the Day of Resurrection.)"},"2109":{"id":2109,"surah":17,"ayah":80,"verse":"And pray, \u201cMy Lord! Admit me with the truth and take me out with the truth*, and give me from Yourself a helpful dominance**.\u201d (* Wherever I come or go ** Through spread of Islam.)"},"2110":{"id":2110,"surah":17,"ayah":81,"verse":"And proclaim, \u201cThe Truth has arrived and falsehood has vanished; indeed falsehood had to vanish.\u201d (* With the arrival of the Last Prophet \u2013 Mohammed peace and blessings be upon him)"},"2111":{"id":2111,"surah":17,"ayah":82,"verse":"And We send down in the Qur\u2019an that which is a cure for the Muslims, and a mercy \u2013 and it increases only ruin for the unjust."},"2112":{"id":2112,"surah":17,"ayah":83,"verse":"And when We bestow favours upon man, he turns away and goes far away towards himself; and when evil touches him he despairs."},"2113":{"id":2113,"surah":17,"ayah":84,"verse":"Proclaim, \u201cEach one works according to his own pattern; and your Lord well knows him who is more upon guidance.\u201d"},"2114":{"id":2114,"surah":17,"ayah":85,"verse":"They ask you concerning the soul; proclaim \u201cThe soul is an entity by the command of my Lord, and you have not received knowledge except a little.\u201d"},"2115":{"id":2115,"surah":17,"ayah":86,"verse":"And if We willed We could have taken away the revelations which We have sent to you \u2013 you would then not find anyone who could advocate for you before Us for this."},"2116":{"id":2116,"surah":17,"ayah":87,"verse":"Except the mercy of your Lord; indeed His munificence upon you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) is extremely great."},"2117":{"id":2117,"surah":17,"ayah":88,"verse":"Proclaim, \u201cIf all mankind and jinns agree to bring an equivalent to the Qur\u2019an, they will not be able to bring its equal \u2013 even if they were to help each other.\u201d"},"2118":{"id":2118,"surah":17,"ayah":89,"verse":"And indeed We have illustrated all kinds of examples in the Qur\u2019an for mankind \u2013 so most men did not accept, except to be ungrateful."},"2119":{"id":2119,"surah":17,"ayah":90,"verse":"And they said, \u201cWe will not accept faith in you, until you cause a spring to gush forth from the earth for us.\u201d"},"2120":{"id":2120,"surah":17,"ayah":91,"verse":"\u201cOr you have a garden of date-palms and grapes, and you make gushing rivers to flow in it.\u201d"},"2121":{"id":2121,"surah":17,"ayah":92,"verse":"\u201cOr you cause the sky to fall upon us in pieces like you said \u2013 or bring Allah and the angels as Witness.\u201d"},"2122":{"id":2122,"surah":17,"ayah":93,"verse":"\u201cOr you have a house of gold, or you ascend up into heaven; and even then we shall not believe in your ascent unless you send down a book upon us, which we may read\u201d; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cPurity is to my Lord \u2013 who am I except a human, sent by Allah?\u201d (* These can be done, only when Allah commands.)"},"2123":{"id":2123,"surah":17,"ayah":94,"verse":"And what prevented people from believing when the guidance came to them, except their saying that, \u201cWhat! Allah has sent a human as a Noble Messenger?\u201d?"},"2124":{"id":2124,"surah":17,"ayah":95,"verse":"Proclaim, \u201cIf there were angels walking peacefully on the earth, We would send down only an angel from heaven, as a Noble Messenger towards them.\u201d"},"2125":{"id":2125,"surah":17,"ayah":96,"verse":"Proclaim, \u201cAllah is Sufficient as Witness between me and you all; indeed He is Well Aware of, the Beholder of His bondmen.\u201d"},"2126":{"id":2126,"surah":17,"ayah":97,"verse":"And only he whom Allah guides, is upon guidance; and whomever He sends astray \u2013 you will therefore not find for them any supporters besides Him; and We shall raise them by their faces on the Day of Resurrection \u2013 blind, dumb and deaf; their destination is hell; whenever it is about to extinguish, We will inflame it more for them."},"2127":{"id":2127,"surah":17,"ayah":98,"verse":"This is their reward because they disbelieved in Our signs and said, \u201cWhen we are bones and decomposed, will we really be created again and raised up again?\u201d"},"2128":{"id":2128,"surah":17,"ayah":99,"verse":"Do they not see that Allah Who has created the heavens and the earth is Able to create people similar to them, and has set a term for them in which there is no doubt? So the unjust do not accept without being ungrateful."},"2129":{"id":2129,"surah":17,"ayah":100,"verse":"Proclaim, \u201cIf you owned the treasures of the mercy of my Lord, you would hoard them too for fear that they may get spent; and man is a big miser.\u201d"},"2130":{"id":2130,"surah":17,"ayah":101,"verse":"And indeed We gave Moosa nine clear signs, therefore ask the Descendants of Israel when he came to them \u2013 in response Firaun said, \u201cO Moosa \u2013 I think you are under a magic spell.\u201d"},"2131":{"id":2131,"surah":17,"ayah":102,"verse":"He said, \u201cYou certainly know that these have not been sent down except by the Lord of the heavens and the earth, the eye-openers* for the hearts; and I think that you, O Firaun, will surely be ruined.\u201d (* The signs which enlighten the hearts.)"},"2132":{"id":2132,"surah":17,"ayah":103,"verse":"He therefore wished to expel them from the earth, so We drowned him and his companions, all together."},"2133":{"id":2133,"surah":17,"ayah":104,"verse":"And after him, We said to the Descendants of Israel, \u201cReside in this land \u2013 then when the promise of the Hereafter comes, We will bring you all huddled together.\u201d"},"2134":{"id":2134,"surah":17,"ayah":105,"verse":"And We sent down the Qur\u2019an with the truth, and it has come down only for the truth; and We did not send you except as a Herald of glad tidings and warnings."},"2135":{"id":2135,"surah":17,"ayah":106,"verse":"And We sent down the Qur\u2019an in parts, that you may gradually recite it to the people, and We sent it down slowly in stages."},"2136":{"id":2136,"surah":17,"ayah":107,"verse":"Proclaim, \u201cWhether you accept faith in it or not\u201d; indeed those who received knowledge before the Qur\u2019an came, fall down prostrate on their faces when it is recited to them."},"2137":{"id":2137,"surah":17,"ayah":108,"verse":"And they say, \u201cPurity is to our Lord \u2013 indeed the promise of our Lord had to come true.\u201d"},"2138":{"id":2138,"surah":17,"ayah":109,"verse":"And they fall down on their faces weeping, and this Qur\u2019an increases their humility. (Command of prostration # 4)."},"2139":{"id":2139,"surah":17,"ayah":110,"verse":"Proclaim, \u201cPray calling (Him) Allah or calling (Him) the Most Gracious; whichever name you call with \u2013 they are all His magnificent names; and do not offer your prayers very loudly or very softly, and seek a way between them.\u201d"},"2140":{"id":2140,"surah":17,"ayah":111,"verse":"And say, \u201cAll praise is to Allah, Who has not chosen a son for Himself, and none is His partner in kingship, and none is His supporter due to weakness, and say \u2018Allah is Great\u2019 to proclaim His greatness.\u201d"},"2141":{"id":2141,"surah":18,"ayah":1,"verse":"All praise is to Allah Who sent down the Book upon His bondman, and has not kept any deviation in it."},"2142":{"id":2142,"surah":18,"ayah":2,"verse":"A just Book, to warn of Allah\u2019s severe punishment, and to give glad tidings to the believers who do good deeds, that for them is an excellent reward."},"2143":{"id":2143,"surah":18,"ayah":3,"verse":"In which they will abide forever."},"2144":{"id":2144,"surah":18,"ayah":4,"verse":"And to warn those who say \u201cAllah has chosen a child.\u201d"},"2145":{"id":2145,"surah":18,"ayah":5,"verse":"They do not have any knowledge of it \u2013 nor did their forefathers; profound is the word that comes out of their mouths; they only speak a lie."},"2146":{"id":2146,"surah":18,"ayah":6,"verse":"Possibly you may risk your life by grieving (O dear Prophet Mohammed \u2013 peace and blessings be upon him) for them if they do not believe in this narration."},"2147":{"id":2147,"surah":18,"ayah":7,"verse":"We have indeed placed all that is on the earth as its adornment in order that We may test them, who among them has the best deeds."},"2148":{"id":2148,"surah":18,"ayah":8,"verse":"And indeed We shall one day make all that is on it a barren plain."},"2149":{"id":2149,"surah":18,"ayah":9,"verse":"Did you know that the People of the Cave and People close to the Woods, were Our exceptional signs?"},"2150":{"id":2150,"surah":18,"ayah":10,"verse":"When the young men took refuge in the Cave \u2013 then said, \u201cOur Lord! Give us mercy from Yourself, and arrange guidance for us in our affair.\u201d"},"2151":{"id":2151,"surah":18,"ayah":11,"verse":"We then thumped upon their ears in the Cave for a number of years. (* Put them to sleep.)"},"2152":{"id":2152,"surah":18,"ayah":12,"verse":"We then awakened them to see which of the two groups more accurately tells the period they had stayed."},"2153":{"id":2153,"surah":18,"ayah":13,"verse":"We shall narrate their account to you accurately; they were young men who believed in their Lord, and We increased the guidance for them."},"2154":{"id":2154,"surah":18,"ayah":14,"verse":"And We made their hearts steadfast when they stood up and said, \u201cOur Lord is the Lord of the heavens and the earth \u2013 we shall not worship any other deity except Him \u2013 if it were, we have then said something excessive.\u201d"},"2155":{"id":2155,"surah":18,"ayah":15,"verse":"\u201cThese \u2013 the people of ours \u2013 have set up Gods besides Allah; why do they not bring a clear proof regarding them? And who is more unjust than one who fabricates a lie against Allah?\u201d"},"2156":{"id":2156,"surah":18,"ayah":16,"verse":"\u201cAnd when you have disassociated yourself from them and all what they worship besides Allah \u2013 so take refuge in the Cave \u2013 your Lord will spread His mercy for you and arrange ease for you in your affairs.\u201d"},"2157":{"id":2157,"surah":18,"ayah":17,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him) you will see the sun that when it rises it shifts away to the right of their cave, and when it sets it shifts away to their left, and they are in the open ground of that cave; this is from among the signs of Allah; whomever Allah guides \u2013 only he is therefore guided; and whomever He sends astray \u2013 you will never find for him a friend who guides."},"2158":{"id":2158,"surah":18,"ayah":18,"verse":"And you may think they are awake, whereas they are asleep; and We turn them over to the right and the left \u2013 and their dog is on the threshold of the cave, with its paws outstretched; O listener, were you to look at them closely, you would turn back running away from them, and be filled with their dread."},"2159":{"id":2159,"surah":18,"ayah":19,"verse":"And similarly We awakened them so that they may enquire about each other; a speaker among them said, \u201cHow long have you stayed here?\u201d Some among them said, \u201cWe have stayed a day or part of a day\u201d; the others said, \u201cYour Lord well knows how long you have stayed; therefore send one of you to the city with this silver coin \u2013 he may then check which food available there is purer, in order to bring some of it for you to eat \u2013 and he must be courteous and not inform anyone about you.\u201d"},"2160":{"id":2160,"surah":18,"ayah":20,"verse":"\u201cIndeed if they come to know about you, they will stone you or turn you back to their religion \u2013 and if so, you will never prosper.\u201d"},"2161":{"id":2161,"surah":18,"ayah":21,"verse":"And this is how We made them known for people to know that the promise of Allah is true and that there is no doubt concerning the Last Day; when the people began disputing among themselves regarding them, they said, \u201cConstruct a building over their cave\u201d; their Lord well knows them; those who dominated in this matter said, \u201cWe promise we will build a mosque over them.\u201d"},"2162":{"id":2162,"surah":18,"ayah":22,"verse":"So the people will now say, \u201cThey are three, their dog is the fourth\u201d; and some will say, \u201cThey are five, their dog is the sixth\u201d \u2013 just blind guesses; and some will say, \u201cThey are seven, and their dog is the eighth\u201d; proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cMy Lord well knows their number \u2013 no one knows them except a few\u201d; therefore do not debate concerning them except what has occurred, and do not ask any of the People of the Book(s) anything concerning them."},"2163":{"id":2163,"surah":18,"ayah":23,"verse":"And never say about anything that, \u201cI will do this tomorrow.\u201d"},"2164":{"id":2164,"surah":18,"ayah":24,"verse":"Except \u201cIf Allah wills\u201d; and remember your Lord when you forget, and say, \u201cIt is likely that my Lord will guide me to a more accurate way of virtue than this.\u201d"},"2165":{"id":2165,"surah":18,"ayah":25,"verse":"And they stayed in their Cave for three hundred years* and nine more*. (* 300 according to the Solar calendar and 309 according to the Lunar calendar.)"},"2166":{"id":2166,"surah":18,"ayah":26,"verse":"Say, \u201cAllah well knows how long they stayed; for Him only are the hidden of the heavens and the earth; how well He sees and hears! They do not have any supporter besides Him; and He does not associate anyone in His command.\u201d"},"2167":{"id":2167,"surah":18,"ayah":27,"verse":"And recite the Book of your Lord which has been divinely revealed to you; there is none who can change His Words; and you will never find a refuge besides Him."},"2168":{"id":2168,"surah":18,"ayah":28,"verse":"And restrain yourself along with those who pray to their Lord morning and evening, seeking His pleasure; and may not your sight fall on anything besides them; would you desire the adornment of the life of this world? And do not follow him whose heart We have made neglectful of Our remembrance \u2013 the one who has followed his own desires and his matter has crossed the limits."},"2169":{"id":2169,"surah":18,"ayah":29,"verse":"And proclaim, \u201cThe Truth is from your Lord\u201d; so whoever wills may accept faith, and whoever wills may disbelieve \u2013 We have indeed prepared for the disbelievers a fire the walls of which will surround them; if they plead for water, their plea will be answered with water like molten metal which shall scald their faces; what an evil drink it is; and what an evil destination is hell!"},"2170":{"id":2170,"surah":18,"ayah":30,"verse":"Indeed those who believed and did good deeds \u2013 We do not waste the reward of those whose deeds are good."},"2171":{"id":2171,"surah":18,"ayah":31,"verse":"For them are everlasting Gardens of Eden, beneath which rivers flow \u2013 in it they will be given bracelets of gold to adorn, and shall wear green clothes made of fine silk and gold embroidery, reclining upon thrones in it; what an excellent reward; and what an excellent abode is Paradise!"},"2172":{"id":2172,"surah":18,"ayah":32,"verse":"And relate to them the account of the two men \u2013 to one We gave two gardens of grapes, and covered them with date-palms and kept farms between them."},"2173":{"id":2173,"surah":18,"ayah":33,"verse":"Both the gardens gave yields and gave no shortfall in it \u2013 and We made a river to flow between the two."},"2174":{"id":2174,"surah":18,"ayah":34,"verse":"And he had fruit; he therefore said to his companion \u2013 and he used to debate with him \u2013 \u201cI exceed you in wealth, and am more powerful in respect of men.\u201d"},"2175":{"id":2175,"surah":18,"ayah":35,"verse":"He went into his garden and wronging himself said, \u201cI do not think that this will ever perish.\u201d"},"2176":{"id":2176,"surah":18,"ayah":36,"verse":"\u201cI do not think that the Last Day will ever be established \u2013 and even if I return to my Lord I will surely find a haven better than this garden.\u201d"},"2177":{"id":2177,"surah":18,"ayah":37,"verse":"His companion debating with him answered, \u201cWhat! You disbelieve in Him Who has created you from dust, then from a drop of liquid, and then created you as a complete man?\u201d"},"2178":{"id":2178,"surah":18,"ayah":38,"verse":"\u201cBut I just say that only Allah is my Lord, and I do not ascribe anyone as a partner to my Lord.\u201d"},"2179":{"id":2179,"surah":18,"ayah":39,"verse":"\u201cAnd why was it not that you would have said when you entered your garden, \u2018Whatever Allah wills \u2013 we do not have any strength except with the help of Allah\u2019 \u2013 if you had observed me lesser than you in wealth and children.\u201d?"},"2180":{"id":2180,"surah":18,"ayah":40,"verse":"\u201cSo it is likely that my Lord will give me a garden better than yours, and send bolts of lightning from the skies on your garden \u2013 it therefore turns into a barren plain.\u201d"},"2181":{"id":2181,"surah":18,"ayah":41,"verse":"\u201cOr its water may sink into the earth, so you may never be able to find it.\u201d"},"2182":{"id":2182,"surah":18,"ayah":42,"verse":"And his fruits were surrounded \u2013 he therefore remained helplessly wringing his hands upon all that he had spent on it \u2013 and it lay fallen on its canopy \u2013 and he says, \u201cIf only I had not ascribed any partner to my Lord!\u201d"},"2183":{"id":2183,"surah":18,"ayah":43,"verse":"And he had no group to help him against Allah, nor was he capable of taking revenge."},"2184":{"id":2184,"surah":18,"ayah":44,"verse":"Here brought to light is that the authority is only for Allah, the True; the reward He bestows is the best, and believing in Him has the best outcome."},"2185":{"id":2185,"surah":18,"ayah":45,"verse":"And relate to them the example of the life of this world \u2013 like water which We sent down from the sky, therefore vegetation of the earth grew forth in abundance with it to become dry hay which the winds scatter; and Allah is the Controller of all things."},"2186":{"id":2186,"surah":18,"ayah":46,"verse":"Wealth and sons are ornaments of the life of this world; and good deeds that last \u2013 their reward is better before your Lord, and are better in respect of hope."},"2187":{"id":2187,"surah":18,"ayah":47,"verse":"And the Day when We move the hills and you see the earth flattened plain, and We shall raise all of them together \u2013 so not leaving out any one of them."},"2188":{"id":2188,"surah":18,"ayah":48,"verse":"And everyone shall be presented before your Lord in rows; \u201cIndeed you have come to Us exactly as We had created you for the first time \u2013 in fact you thought that We shall never appoint a promised time for you!\u201d"},"2189":{"id":2189,"surah":18,"ayah":49,"verse":"And the Book shall be placed \u2013 and you will see the guilty dreading what is written in it and saying, \u201cWoe to us \u2013 what sort of a Book is this that it has not left out any small sin nor a great one, which it has not included!\u201d And they found all that they did confronting them; and your Lord does not wrong any one."},"2190":{"id":2190,"surah":18,"ayah":50,"verse":"And recall when We commanded the angels that, \u201cProstrate before Adam\u201d \u2013 so they all prostrated, except Iblis; he was of the jinn, he therefore rebelled against his Lord\u2019s command; \u201cWhat! You choose him and his offspring as your friends instead of Me, whereas they are your enemies?\u201d And what an evil alternative did the unjust get."},"2191":{"id":2191,"surah":18,"ayah":51,"verse":"Neither did I make them witness the creations of the heavens and the earth, nor witness their own creation; nor does it befit My Majesty to choose misleaders as aides."},"2192":{"id":2192,"surah":18,"ayah":52,"verse":"And the Day when He will proclaim, \u201cCall those partners of Mine whom you had assumed\u201d \u2013 so they will call out to them \u2013 they will not answer them, and We shall create a field of destruction between them."},"2193":{"id":2193,"surah":18,"ayah":53,"verse":"And when the guilty see hell, they will be certain of falling into it, and will find no place to escape from it."},"2194":{"id":2194,"surah":18,"ayah":54,"verse":"And We have indeed illustrated all kinds of examples for mankind in this Qur\u2019an; and man is the most quarrelsome of all."},"2195":{"id":2195,"surah":18,"ayah":55,"verse":"And what prevented men from accepting faith when guidance came to them, and from asking forgiveness from their Lord except that the tradition of the former nations come upon them or that they confront various kinds of punishments?"},"2196":{"id":2196,"surah":18,"ayah":56,"verse":"And We do not send the Noble Messengers except as Heralds of glad tidings and warnings; and the disbelievers debate by means of falsehood to drive away the Truth with it, and they took My signs and warnings they were given, as a mockery!"},"2197":{"id":2197,"surah":18,"ayah":57,"verse":"And who is more unjust than one who, when reminded of the signs of his Lord, turns away from them and forgets what his hands have sent forward? We have put covers on their hearts so as not to understand the Qur\u2019an, and deafness in their ears; and even if you call them to guidance, they will never attain the right path."},"2198":{"id":2198,"surah":18,"ayah":58,"verse":"And your Lord is the Oft Forgiver, the Merciful; if He seized them for their deeds, He would soon send the punishment upon them; but for them is an appointed time from which they will not find any refuge."},"2199":{"id":2199,"surah":18,"ayah":59,"verse":"And these towns \u2013 We destroyed them when they committed injustice, and We had set an appointed time for their destruction."},"2200":{"id":2200,"surah":18,"ayah":60,"verse":"And recall when Moosa said to his assistant, \u201cI will not give up until I reach the place where the two seas meet or until I have progressed for ages.\u201d"},"2201":{"id":2201,"surah":18,"ayah":61,"verse":"And when they reached the place where the two seas meet, they forgot about their fish, and it took its way into the sea, making a tunnel. (The dead fish came alive and went into the water.)"},"2202":{"id":2202,"surah":18,"ayah":62,"verse":"So when they had gone beyond that place, Moosa said to his assistant, \u201cBring our breakfast \u2013 we have indeed faced great exertion in this journey of ours.\u201d"},"2203":{"id":2203,"surah":18,"ayah":63,"verse":"He said, \u201cJust imagine \u2013 when we had taken shelter near the rock, so indeed I forgot the fish; and none but Satan caused me to forget to mention it; and the fish took its way into the sea \u2013 its amazing!\u201d"},"2204":{"id":2204,"surah":18,"ayah":64,"verse":"Said Moosa, \u201cThis is exactly what we wanted\u201d; so they came back retracing their steps."},"2205":{"id":2205,"surah":18,"ayah":65,"verse":"So they found a bondman* from amongst Our (chosen) bondmen, to whom We had given mercy from Us, and had bestowed the inspired knowledge from Ourselves. (* Hazrat Khidr \u2013 peace be upon him.)"},"2206":{"id":2206,"surah":18,"ayah":66,"verse":"Moosa said to him, \u201cMay I stay with you upon the condition that you will teach me the righteousness that you have been taught?\u201d"},"2207":{"id":2207,"surah":18,"ayah":67,"verse":"He said, \u201cYou will never be able to patiently stay with me.\u201d"},"2208":{"id":2208,"surah":18,"ayah":68,"verse":"\u201cAnd how will you bear something which your knowledge does not encompass?\u201d"},"2209":{"id":2209,"surah":18,"ayah":69,"verse":"Said Moosa, \u201cAllah willing, you will soon find me patient and I will not do anything against your instructions.\u201d"},"2210":{"id":2210,"surah":18,"ayah":70,"verse":"He said, \u201cTherefore if you stay with me, do not ask me about anything until I myself mention it to you.\u201d"},"2211":{"id":2211,"surah":18,"ayah":71,"verse":"So they both set out; until when they had boarded the boat, the chosen bondman ruptured the boat; said Moosa, \u201cDid you make a hole in the boat in order to drown its passengers? You have indeed done an evil thing.\u201d"},"2212":{"id":2212,"surah":18,"ayah":72,"verse":"He said, \u201cDid I not say that you will never be able to patiently stay with me?\u201d"},"2213":{"id":2213,"surah":18,"ayah":73,"verse":"Said Moosa, \u201cDo not apprehend me upon my forgetting, and do not impose difficulty on me in my task.\u201d"},"2214":{"id":2214,"surah":18,"ayah":74,"verse":"So they set out again; until when they met a boy, the chosen bondman slew him \u2013 Moosa said, \u201cDid you slay an innocent soul not in retribution for another? You have indeed done an extremely evil thing.\u201d"},"2215":{"id":2215,"surah":18,"ayah":75,"verse":"He said, \u201cDid I not tell you that you will never be able to patiently stay with me?\u201d"},"2216":{"id":2216,"surah":18,"ayah":76,"verse":"Said Moosa, \u201cIf I ask you anything after this, do not stay with me; indeed your condition from me is fulfilled.\u201d"},"2217":{"id":2217,"surah":18,"ayah":77,"verse":"So they both set out again; until they came to the people of a dwelling \u2013 they asked its people for food \u2013 they refused to invite them \u2013 then in the village they both found a wall about to collapse, and the chosen bondman straightened it; said Moosa, \u201cIf you wished, you could have taken some wages for it!\u201d"},"2218":{"id":2218,"surah":18,"ayah":78,"verse":"He said, \u201cThis is the parting between you and me; I shall now tell you the interpretation of the matters you could not patiently bear.\u201d"},"2219":{"id":2219,"surah":18,"ayah":79,"verse":"\u201cIn respect of the boat \u2013 it belonged to the poor people who worked on the river, so I wished to flaw it \u2013 and behind them was a king who would capture every sound ship.\u201d"},"2220":{"id":2220,"surah":18,"ayah":80,"verse":"\u201cAnd in respect of the boy \u2013 his parents were Muslims and we feared that he may incite them to rebellion and disbelief.\u201d"},"2221":{"id":2221,"surah":18,"ayah":81,"verse":"\u201cSo we wished that their Lord may bestow them a child \u2013 better, purer and nearer to mercy.\u201d"},"2222":{"id":2222,"surah":18,"ayah":82,"verse":"\u201cAnd in respect of the wall \u2013 it belonged to two orphan boys of the city, and beneath it was their treasure, and their father was a virtuous man; therefore your Lord willed that they should reach their maturity and remove their treasure; by the mercy of your Lord; and I have not done this at my own command; this is the interpretation of the matters you could not patiently bear.\u201d (* Hazrat Khidr was given the knowledge of the hidden \u2013 as in all three explanations he gave)."},"2223":{"id":2223,"surah":18,"ayah":83,"verse":"And they ask you regarding Zul-Qarnain; say, \u201cI shall recite his story to you.\u201d"},"2224":{"id":2224,"surah":18,"ayah":84,"verse":"Indeed We gave him authority in the land and bestowed him the means of everything."},"2225":{"id":2225,"surah":18,"ayah":85,"verse":"He therefore pursued a purpose."},"2226":{"id":2226,"surah":18,"ayah":86,"verse":"To the extent that when he reached the setting-place of the sun, he found it setting in a muddy spring, and found a nation there; We said, \u201cO Zul-Qarnain \u2013 either punish them or choose kindness for them.\u201d"},"2227":{"id":2227,"surah":18,"ayah":87,"verse":"He submitted, \u201cRegarding one who has done injustice, we shall soon punish him \u2013 he will then be brought back to his Lord, Who will punish him severely.\u201d"},"2228":{"id":2228,"surah":18,"ayah":88,"verse":"\u201cAnd regarding one who believed and did good deeds \u2013 so his reward is goodness; and we shall soon give him an easy command.\u201d"},"2229":{"id":2229,"surah":18,"ayah":89,"verse":"He again pursued a purpose."},"2230":{"id":2230,"surah":18,"ayah":90,"verse":"To the extent that when he reached the rising-place of the sun, he found it rising upon a nation for which We had not kept any shelter from it."},"2231":{"id":2231,"surah":18,"ayah":91,"verse":"So it is; and Our knowledge encompasses all that he possessed."},"2232":{"id":2232,"surah":18,"ayah":92,"verse":"He again pursued a purpose."},"2233":{"id":2233,"surah":18,"ayah":93,"verse":"Until, when he came between two mountains, he found before them a nation that did not appear to understand any speech."},"2234":{"id":2234,"surah":18,"ayah":94,"verse":"They said, \u201cO Zul-Qarnain \u2013 indeed Yajuj and Majuj* are spreading chaos in the land \u2013 so shall we assign for you a consideration upon the condition that you set up a wall between us and them?\u201d (* Gog and Magog.)"},"2235":{"id":2235,"surah":18,"ayah":95,"verse":"He said, \u201cThat which my Lord has given me control over is better, therefore help me with strength \u2013 I shall set up a barrier between you and them.\u201d"},"2236":{"id":2236,"surah":18,"ayah":96,"verse":"\u201cGive me sheets of iron\u201d; until when he had raised the wall equal to the edge of the two mountains, he said, \u201cBlow\u201d; to the extent that he made it ablaze \u2013 he said, \u201cBring me molten copper to pour upon it.\u201d"},"2237":{"id":2237,"surah":18,"ayah":97,"verse":"Therefore Yajuj and Majuj were neither able to surmount it, nor could they pierce it."},"2238":{"id":2238,"surah":18,"ayah":98,"verse":"He said, \u201cThis is the mercy of my Lord; then when the promise of my Lord arrives, He will blow it to bits; and my Lord\u2019s promise is true.\u201d"},"2239":{"id":2239,"surah":18,"ayah":99,"verse":"And on that day We shall release them in groups surging like waves one after another, and the Trumpet will be blown \u2013 so We shall gather them all together. (* Gog and Magog will come out during the time of Eisa (Jesus \u2013 when he comes back to earth) and cause great destruction in the land.)"},"2240":{"id":2240,"surah":18,"ayah":100,"verse":"And We shall bring hell in front of the disbelievers."},"2241":{"id":2241,"surah":18,"ayah":101,"verse":"The ones whose eyes were covered from My remembrance, and who could not bear to hear Truth."},"2242":{"id":2242,"surah":18,"ayah":102,"verse":"Do the disbelievers assume that they will be able to choose My bondmen as supporters other than Me? Indeed We have prepared hell to welcome the disbelievers."},"2243":{"id":2243,"surah":18,"ayah":103,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cShall we inform you whose are the most failed works?\u201d"},"2244":{"id":2244,"surah":18,"ayah":104,"verse":"\u201cOf those whose efforts are lost in (pursuit of) the life of this world, and they think that they are doing good deeds.\u201d"},"2245":{"id":2245,"surah":18,"ayah":105,"verse":"The people who disbelieved in the signs of their Lord and in the meeting with Him, therefore all their deeds are in vain \u2013We shall therefore not establish any weighing for them on the Day of Resurrection."},"2246":{"id":2246,"surah":18,"ayah":106,"verse":"This is their reward \u2013 hell \u2013 because they disbelieved, and made a mockery of My verses and My Noble Messengers."},"2247":{"id":2247,"surah":18,"ayah":107,"verse":"Indeed those who believed and did good deeds \u2013 their welcome are the Gardens of Paradise."},"2248":{"id":2248,"surah":18,"ayah":108,"verse":"They will abide in it for ever, never wanting to shift from it."},"2249":{"id":2249,"surah":18,"ayah":109,"verse":"Proclaim, \u201cIf the sea became ink for the Words of my Lord, the sea would indeed be used up and the Words of my Lord would never \u2013 even if we bring another like it for help.\u201d"},"2250":{"id":2250,"surah":18,"ayah":110,"verse":"Proclaim, \u201cPhysically I am a human* like you \u2013 my Lord sends divine revelations to me \u2013 that your God is only One God; so whoever expects to the meet his Lord must perform good deeds and not ascribe anyone as a partner in the worship of his Lord.\u201d (* Human but not equal to you, in fact the greatest in spiritual status.)"},"2251":{"id":2251,"surah":19,"ayah":1,"verse":"Kaf-Ha-Ya-A\u2019in-Sad. (* Alphabets of the Arabic language \u2013 Allah and to whomever He reveals know their precise meanings.)"},"2252":{"id":2252,"surah":19,"ayah":2,"verse":"This is the remembrance of the mercy of your Lord upon His bondman Zakaria."},"2253":{"id":2253,"surah":19,"ayah":3,"verse":"When he softly prayed to his Lord."},"2254":{"id":2254,"surah":19,"ayah":4,"verse":"He submitted, \u201cO my Lord \u2013 my bones have become weak and old age shines forth from my head, and O my Lord, I have never been disappointed in my prayer to you.\u201d"},"2255":{"id":2255,"surah":19,"ayah":5,"verse":"\u201cAnd I fear my relatives after me and my wife is barren therefore bestow upon me from Yourself one who will take up my work.\u201d"},"2256":{"id":2256,"surah":19,"ayah":6,"verse":"\u201cHe being my successor and the heir of the Descendants of Yaqub (Jacob); and my Lord, make him a cherished* one.\u201d (* Make him a Prophet among the Descendants of Israel.)"},"2257":{"id":2257,"surah":19,"ayah":7,"verse":"\u201cO Zakaria! We give you the glad tidings of a son whose name is Yahya (John) \u2013 before him, We have not created anyone of this name.\u201d"},"2258":{"id":2258,"surah":19,"ayah":8,"verse":"He submitted, \u201cMy Lord \u2013 how can I have a son whereas my wife is barren and I have reached infirmity due to old age?\u201d"},"2259":{"id":2259,"surah":19,"ayah":9,"verse":"He (the angel) said, \u201cSo it is; your Lord says, \u2018This is easy for Me \u2013 in fact I created you before this, at a time when you did not exist.\u2019\u201d"},"2260":{"id":2260,"surah":19,"ayah":10,"verse":"He said, \u201cMy Lord, give me a sign\u201d; He said, \u201cYour token is that you will not speak to people for three nights, although in proper health.\u201d"},"2261":{"id":2261,"surah":19,"ayah":11,"verse":"He therefore emerged upon his people from the mosque, and told them through gestures, \u201cKeep proclaiming the Purity (of your Lord) morning and evening.\u201d"},"2262":{"id":2262,"surah":19,"ayah":12,"verse":"\u201cO Yahya \u2013 hold the Book firmly\u201d; and We gave him Prophethood in his infancy. (Prophet Yahya was only 2 years old at that time.)"},"2263":{"id":2263,"surah":19,"ayah":13,"verse":"And compassion from Ourselves, and chastity; and he was extremely pious."},"2264":{"id":2264,"surah":19,"ayah":14,"verse":"And was good to his parents and not forceful, nor disobedient."},"2265":{"id":2265,"surah":19,"ayah":15,"verse":"And peace is upon him the day he was born, and the day he will taste death, and the day he will be raised alive."},"2266":{"id":2266,"surah":19,"ayah":16,"verse":"And remember Maryam in the Book; when she went away from her family to a place towards east."},"2267":{"id":2267,"surah":19,"ayah":17,"verse":"So there she screened herself from them; We therefore sent Our Spirit towards her \u2013 he appeared before her in the form of a healthy man. (Angel Jibreel \u2013 peace be upon him.)"},"2268":{"id":2268,"surah":19,"ayah":18,"verse":"She said, \u201cI seek the refuge of the Most Gracious from you \u2013 if you fear God.\u201d"},"2269":{"id":2269,"surah":19,"ayah":19,"verse":"He said, \u201cI am indeed one sent by your Lord; so that I may give you a chaste son.\u201d"},"2270":{"id":2270,"surah":19,"ayah":20,"verse":"She said, \u201cHow can I bear a son? No man has ever touched me, nor am I of poor conduct!\u201d"},"2271":{"id":2271,"surah":19,"ayah":21,"verse":"He said, \u201cSo it is; your Lord has said, \u2018This is easy for Me\u2019; and in order that We make him a sign for mankind and a Mercy from Us; and this matter has been decreed.\u201d"},"2272":{"id":2272,"surah":19,"ayah":22,"verse":"So she conceived him, and she went away with him to a far place."},"2273":{"id":2273,"surah":19,"ayah":23,"verse":"Then the pangs of childbirth brought her to the base of the palm-tree; she said, \u201cOh, if only had I died before this and had become forgotten, unremembered.\u201d"},"2274":{"id":2274,"surah":19,"ayah":24,"verse":"(The angel) Therefore called her from below her, \u201cDo not grieve \u2013 your Lord has made a river flow below you.\u201d"},"2275":{"id":2275,"surah":19,"ayah":25,"verse":"\u201cAnd shake the trunk of the palm-tree towards you \u2013 ripe fresh dates will fall upon you.\u201d (This was a miracle \u2013 the date palm was dry and it was winter season.)"},"2276":{"id":2276,"surah":19,"ayah":26,"verse":"\u201cTherefore eat and drink and appease your eyes; so if you meet any person then say, \u2018I have pledged a fast (of silence) to the Most Gracious \u2013 I will therefore not speak to any person today.\u2019\u201d"},"2277":{"id":2277,"surah":19,"ayah":27,"verse":"So carrying him in her arms, she brought him to her people; they said, \u201cO Maryam, you have indeed committed a great evil!\u201d"},"2278":{"id":2278,"surah":19,"ayah":28,"verse":"\u201cO sister of Haroon, neither was your father an evil man nor was your mother of poor conduct!\u201d"},"2279":{"id":2279,"surah":19,"ayah":29,"verse":"Thereupon she pointed towards the child; they said, \u201cHow can we speak to an infant who is in the cradle?\u201d"},"2280":{"id":2280,"surah":19,"ayah":30,"verse":"The child proclaimed, \u201cI am Allah\u2019s bondman; He has given me the Book and made me a Herald of the Hidden (a Prophet).\u201d"},"2281":{"id":2281,"surah":19,"ayah":31,"verse":"\u201cAnd He has made me blessed wherever I be; and ordained upon me prayer and charity, as long as I live.\u201d"},"2282":{"id":2282,"surah":19,"ayah":32,"verse":"\u201cAnd has made me good to my mother and not made me forceful, ill-fated.\u201d"},"2283":{"id":2283,"surah":19,"ayah":33,"verse":"\u201cAnd peace is upon me the day I was born, and on the day I shall taste death, and on the day I will be raised alive.\u201d"},"2284":{"id":2284,"surah":19,"ayah":34,"verse":"This is Eisa (Jesus), the son of Maryam; a true statement, in which they doubt."},"2285":{"id":2285,"surah":19,"ayah":35,"verse":"It does not befit Allah to appoint someone as His son \u2013 Purity is to Him! When He ordains a matter, He just commands it, \u201cBe\u201d \u2013 and it thereupon happens."},"2286":{"id":2286,"surah":19,"ayah":36,"verse":"And said Eisa, \u201cIndeed Allah is my Lord and your Lord \u2013 therefore worship Him; this is the Straight Path.\u201d"},"2287":{"id":2287,"surah":19,"ayah":37,"verse":"Then groups among them differed; so ruin is for the disbelievers from the witnessing of a Great Day."},"2288":{"id":2288,"surah":19,"ayah":38,"verse":"Much will they listen and much will they see, on the Day when they come to Us, but today the unjust are in open error."},"2289":{"id":2289,"surah":19,"ayah":39,"verse":"And warn them of the Day of Regret when the matter will have been decided; and they are in neglect, and they do not accept faith."},"2290":{"id":2290,"surah":19,"ayah":40,"verse":"Indeed We shall inherit the earth and all that is on it, and only towards Us will they return."},"2291":{"id":2291,"surah":19,"ayah":41,"verse":"And remember Ibrahim in the Book; he was very truthful, a Herald of the Hidden (a Prophet)."},"2292":{"id":2292,"surah":19,"ayah":42,"verse":"When he said to his father,* \u201cO my father \u2013 why do you worship one which neither hears nor sees, and cannot benefit you in any way?\u201d (* His uncle Azar.)"},"2293":{"id":2293,"surah":19,"ayah":43,"verse":"\u201cO my father, indeed a knowledge has come to me which did not come to you \u2013 therefore follow me, I will show you the Straight Path.\u201d"},"2294":{"id":2294,"surah":19,"ayah":44,"verse":"\u201cO my father, do not be a bondman of the devil; indeed the devil is disobedient towards the Most Gracious.\u201d"},"2295":{"id":2295,"surah":19,"ayah":45,"verse":"\u201cO my father, I fear that a punishment from the Most Gracious may reach you, so you would become a companion of the devil.\u201d"},"2296":{"id":2296,"surah":19,"ayah":46,"verse":"He said, \u201cWhat! You turn away from my Gods, O Ibrahim? If you do not desist, I will certainly stone you, and keep no relation with me for a long while.\u201d"},"2297":{"id":2297,"surah":19,"ayah":47,"verse":"He said, \u201cStop it \u2013 peace be upon you; I shall seek forgiveness for you from my Lord; indeed He is very kind to me.\u201d"},"2298":{"id":2298,"surah":19,"ayah":48,"verse":"\"And I shall remain separated from you and all that you worship other than Allah and shall worship my Lord; possibly, by worshipping my Lord, I will not be amongst the unfortunate.\u201d"},"2299":{"id":2299,"surah":19,"ayah":49,"verse":"So when he had separated from them and what they worshipped other than Allah, We bestowed him Ishaq and Yaqub; and We made each of them a Herald of the Hidden."},"2300":{"id":2300,"surah":19,"ayah":50,"verse":"And We gave them Our mercy, and assigned for them a true and high repute."},"2301":{"id":2301,"surah":19,"ayah":51,"verse":"And remember Moosa in the Book; he was indeed a chosen one, and he was a Noble Messenger, a Herald of the Hidden."},"2302":{"id":2302,"surah":19,"ayah":52,"verse":"We called him from the right side of the mountain Tur, and brought him close to reveal Our secret."},"2303":{"id":2303,"surah":19,"ayah":53,"verse":"And with Our mercy We bestowed upon him his brother Haroon, a Prophet."},"2304":{"id":2304,"surah":19,"ayah":54,"verse":"And remember Ismail in the Book; he was indeed true to his promise and was a Noble Messenger, a Prophet."},"2305":{"id":2305,"surah":19,"ayah":55,"verse":"He used to command his people to offer prayer and give charity, and was liked by his Lord."},"2306":{"id":2306,"surah":19,"ayah":56,"verse":"And remember Idrees in the Book; he was indeed very truthful, a Prophet."},"2307":{"id":2307,"surah":19,"ayah":57,"verse":"And We lifted him to a high position. (Living with soul & body in heaven, after his death.)"},"2308":{"id":2308,"surah":19,"ayah":58,"verse":"It is these upon whom Allah has bestowed favour among the Prophets, from the descendants of Adam; and from those whom We boarded along with Nooh; and from the descendants of Ibrahim and Israel; and from those whom We guided and chose; when the verses of the Most Gracious were recited to them, they fell down, prostrating and weeping. (* Command of Prostration # 5.)"},"2309":{"id":2309,"surah":19,"ayah":59,"verse":"And after them came the unworthy successors who squandered prayer and pursued their own desires, so they will soon encounter the forest of Gai in hell."},"2310":{"id":2310,"surah":19,"ayah":60,"verse":"Except those who repented and accepted faith and did good deeds \u2013 so these will enter heaven, and they will not be deprived* in the least. (* Of their due reward.)"},"2311":{"id":2311,"surah":19,"ayah":61,"verse":"Everlasting Gardens of Eden, which the Most Gracious has promised to His bondmen in the unseen; indeed His promise will come."},"2312":{"id":2312,"surah":19,"ayah":62,"verse":"They will not hear any lewd talk in it, but only Peace; and in it for them is sustenance, every morning and evening."},"2313":{"id":2313,"surah":19,"ayah":63,"verse":"It is the Paradise that We will bequeath to those among Our bondmen who remain pious."},"2314":{"id":2314,"surah":19,"ayah":64,"verse":"(Said Angel Jibreel to Prophet Mohammed \u2013 peace and blessings be upon them) \u201cAnd we angels do not come down except by the command of your Lord; to Him only belongs all that is ahead of us and all that is behind us and all that is between them; and your Lord is not forgetful.\u201d"},"2315":{"id":2315,"surah":19,"ayah":65,"verse":"Lord of the heavens and the earth and all that is between them \u2013 therefore worship Him and be firm in His worship; do you know any other of the same name as His?"},"2316":{"id":2316,"surah":19,"ayah":66,"verse":"And says man, \u201cWhen I am dead, will I soon be brought forth alive?\u201d"},"2317":{"id":2317,"surah":19,"ayah":67,"verse":"Does not man remember that We created him before this, and he was non existent?"},"2318":{"id":2318,"surah":19,"ayah":68,"verse":"So by your Lord, We shall assemble them and the devils \u2013 all of them \u2013 and bring them around hell, fallen on their knees."},"2319":{"id":2319,"surah":19,"ayah":69,"verse":"We shall then pick out from every group the one who was most arrogant towards the Most Gracious."},"2320":{"id":2320,"surah":19,"ayah":70,"verse":"Moreover, We well know those who most deserve to be burned in hell."},"2321":{"id":2321,"surah":19,"ayah":71,"verse":"And there is none among you who shall not pass over hell; this is an obligatory affair, binding upon your Lord. (Allah will make everyone pass over the back of hell \u2013 on a thin bridge.)"},"2322":{"id":2322,"surah":19,"ayah":72,"verse":"We shall then rescue the pious \u2013 and leave the unjust in it, fallen on their knees."},"2323":{"id":2323,"surah":19,"ayah":73,"verse":"And when Our clear verses are recited to them, the disbelievers say to the Muslims, \u201cWhich group has a better home, and a better alliance?\u201d"},"2324":{"id":2324,"surah":19,"ayah":74,"verse":"And many a generation We did destroy before them, who exceeded them in wealth and pomp!"},"2325":{"id":2325,"surah":19,"ayah":75,"verse":"Proclaim, \u201cFor one in error \u2013 so the Most Gracious may give him respite; to the extent that when they see the thing which they are promised \u2013 either the punishment or the Last Day; so then they will come to know for whom is the evil rank and whose army is weak.\u201d"},"2326":{"id":2326,"surah":19,"ayah":76,"verse":"And Allah will increase the guidance for those who have received guidance; and good deeds that remain have the best reward before your Lord, and the best outcome."},"2327":{"id":2327,"surah":19,"ayah":77,"verse":"So have you seen him who denied Our signs and says, \u201cI shall certainly be given wealth and children?\u201d"},"2328":{"id":2328,"surah":19,"ayah":78,"verse":"Has he seen the Hidden, or has he made a pact with the Most Gracious?"},"2329":{"id":2329,"surah":19,"ayah":79,"verse":"Never; We shall now record what he says and give him a prolonged punishment."},"2330":{"id":2330,"surah":19,"ayah":80,"verse":"And it is We only Who shall inherit what he says (belongs to him), and he will come to Us, alone."},"2331":{"id":2331,"surah":19,"ayah":81,"verse":"And they have chosen Gods besides Allah, so that they may provide them strength!"},"2332":{"id":2332,"surah":19,"ayah":82,"verse":"Never; soon they will deny ever worshipping them, and will turn into their opponents."},"2333":{"id":2333,"surah":19,"ayah":83,"verse":"Did you not see that We sent devils upon the disbelievers, so they excite them abundantly?"},"2334":{"id":2334,"surah":19,"ayah":84,"verse":"So do not be impatient for them (O dear Prophet Mohammed \u2013 peace and blessings be upon him); We are only completing their number.* (* The number of days left for them or their evil deeds.)"},"2335":{"id":2335,"surah":19,"ayah":85,"verse":"On the day when We shall assemble the righteous towards the Most Gracious, as guests."},"2336":{"id":2336,"surah":19,"ayah":86,"verse":"And drive the guilty towards hell, thirsty."},"2337":{"id":2337,"surah":19,"ayah":87,"verse":"People do not own the right to intercede, except those* who have made a covenant with the Most Gracious. (* The Holy Prophets and virtuous people will be given the permission to intercede. Prophet Mohammed \u2013 peace and blessings be upon him \u2013 will be the first to intercede.)"},"2338":{"id":2338,"surah":19,"ayah":88,"verse":"And the disbelievers said, \u201cThe Most Gracious has chosen an offspring.\u201d"},"2339":{"id":2339,"surah":19,"ayah":89,"verse":"You have indeed brought an extremely grave speech!"},"2340":{"id":2340,"surah":19,"ayah":90,"verse":"The heavens are close to being torn apart by it, and the earth being split asunder, and the mountains succumbing and falling down."},"2341":{"id":2341,"surah":19,"ayah":91,"verse":"Due to their ascribing of an offspring to the Most Gracious."},"2342":{"id":2342,"surah":19,"ayah":92,"verse":"And it does not befit the Most Gracious to choose an offspring!"},"2343":{"id":2343,"surah":19,"ayah":93,"verse":"All those who are in the heavens and the earth will come to the Most Gracious as His bondmen."},"2344":{"id":2344,"surah":19,"ayah":94,"verse":"He knows their number and has counted each one of them."},"2345":{"id":2345,"surah":19,"ayah":95,"verse":"And each one of them will come before Him on the Day of Resurrection, alone."},"2346":{"id":2346,"surah":19,"ayah":96,"verse":"Indeed those who believed and did good deeds \u2013 the Most Gracious will appoint love for them. (In the hearts of other believers.)"},"2347":{"id":2347,"surah":19,"ayah":97,"verse":"We have therefore made this Qur\u2019an easy upon your tongue, (O dear Prophet Mohammed \u2013 peace and blessings be upon him) for you to announce glad tidings with it to those who fear, and warn those who are quarrelsome."},"2348":{"id":2348,"surah":19,"ayah":98,"verse":"And many a generation We did destroy before them; do you see any one of them or hear their faintest sound?"},"2349":{"id":2349,"surah":20,"ayah":1,"verse":"Ta-Ha.* (O dear Prophet Mohammed \u2013 peace and blessings be upon him) (Alphabets of the Arabic language \u2013 Allah and to whomever He reveals know their precise meanings.)"},"2350":{"id":2350,"surah":20,"ayah":2,"verse":"We have not sent down this Qur\u2019an upon you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) for you to fall into hardship! (Either because he used to pray at length during the night or because he was distressed due to the disbelievers not accepting faith.)"},"2351":{"id":2351,"surah":20,"ayah":3,"verse":"Except as a reminder for one who fears."},"2352":{"id":2352,"surah":20,"ayah":4,"verse":"Sent down by One Who created the earth and the lofty heavens."},"2353":{"id":2353,"surah":20,"ayah":5,"verse":"The Most Gracious Who, befitting His Majesty, took to the Throne (of control)."},"2354":{"id":2354,"surah":20,"ayah":6,"verse":"To Him only belongs all whatever is in the heavens and all whatever is in the earth, and all whatever is between them, and all whatever is beneath this wet soil."},"2355":{"id":2355,"surah":20,"ayah":7,"verse":"And if you speak aloud \u2013 so He surely knows the secret and that which is more concealed."},"2356":{"id":2356,"surah":20,"ayah":8,"verse":"Allah \u2013 there is no worship except for Him; His only are the best names."},"2357":{"id":2357,"surah":20,"ayah":9,"verse":"And did the news of Moosa reach you?"},"2358":{"id":2358,"surah":20,"ayah":10,"verse":"When he saw a fire and said to his wife, \u201cWait \u2013 I have seen a fire \u2013 perhaps I may bring you an ember from it or find a way upon the fire.\u201d"},"2359":{"id":2359,"surah":20,"ayah":11,"verse":"So when he came near the fire, it was announced, \u201cO Moosa!\u201d"},"2360":{"id":2360,"surah":20,"ayah":12,"verse":"\u201cIndeed I am your Lord, therefore take off your shoes; indeed you are in the holy valley of Tuwa!\u201d"},"2361":{"id":2361,"surah":20,"ayah":13,"verse":"\u201cAnd I have chosen you, therefore listen carefully to what is divinely revealed to you.\u201d"},"2362":{"id":2362,"surah":20,"ayah":14,"verse":"\u201cIndeed it is Me, Allah \u2013 there is no God except I \u2013 therefore worship Me and keep the prayer established for My remembrance.\u201d"},"2363":{"id":2363,"surah":20,"ayah":15,"verse":"\u201cThe Last Day will surely come \u2013 it was close that I hide it from all \u2013 in order that every soul may get the reward of its effort.\u201d (He revealed it to His Prophets, so that people may fear and get ready. The exact time is not revealed to the people.)"},"2364":{"id":2364,"surah":20,"ayah":16,"verse":"\u201cTherefore never let one, who does not accept faith in it and follows his own desires, prevent you from accepting this, so then you become ruined.\u201d"},"2365":{"id":2365,"surah":20,"ayah":17,"verse":"\u201cAnd what is this in your right hand, O Moosa?\u201d"},"2366":{"id":2366,"surah":20,"ayah":18,"verse":"He said, \u201cThis is my staff; I support myself on it, and I knock down leaves for my sheep with it, and there are other uses for me in it.\u201d"},"2367":{"id":2367,"surah":20,"ayah":19,"verse":"He said, \u201cPut it down, O Moosa!"},"2368":{"id":2368,"surah":20,"ayah":20,"verse":"So Moosa put it down \u2013 thereupon it became a fast moving serpent."},"2369":{"id":2369,"surah":20,"ayah":21,"verse":"He said, \u201cPick it up and do not fear; We shall restore it to its former state.\u201d"},"2370":{"id":2370,"surah":20,"ayah":22,"verse":"\u201cAnd put your hand inside your armpit \u2013 it will come out shining white, not due to any illness \u2013 one more sign.\u201d"},"2371":{"id":2371,"surah":20,"ayah":23,"verse":"\u201cIn order that We may show you Our great signs.\u201d"},"2372":{"id":2372,"surah":20,"ayah":24,"verse":"\u201cGo to Firaun, he has rebelled.\u201d"},"2373":{"id":2373,"surah":20,"ayah":25,"verse":"Said Moosa, \u201cMy Lord, open up my breast for me.\u201d"},"2374":{"id":2374,"surah":20,"ayah":26,"verse":"\u201cAnd make my task easy for me.\u201d"},"2375":{"id":2375,"surah":20,"ayah":27,"verse":"\u201cAnd untie the knot of my tongue.\u201d"},"2376":{"id":2376,"surah":20,"ayah":28,"verse":"\u201cIn order that they may understand my speech.\u201d"},"2377":{"id":2377,"surah":20,"ayah":29,"verse":"\u201cAnd appoint for me a viceroy from among my family.\u201d"},"2378":{"id":2378,"surah":20,"ayah":30,"verse":"\u201cThat is Haroon, my brother.\u201d"},"2379":{"id":2379,"surah":20,"ayah":31,"verse":"\u201cBack me up with him.\u201d"},"2380":{"id":2380,"surah":20,"ayah":32,"verse":"\u201cAnd make him a partner in my task.\u201d"},"2381":{"id":2381,"surah":20,"ayah":33,"verse":"\u201cIn order that we may profusely proclaim Your Purity.\u201d"},"2382":{"id":2382,"surah":20,"ayah":34,"verse":"\u201cAnd profusely remember You.\u201d"},"2383":{"id":2383,"surah":20,"ayah":35,"verse":"\u201cIndeed You see us.\u201d"},"2384":{"id":2384,"surah":20,"ayah":36,"verse":"He said, \u201cO Moosa, you have been granted your prayer.\u201d"},"2385":{"id":2385,"surah":20,"ayah":37,"verse":"\u201cAnd indeed We had bestowed upon you a favour one more time.\u201d"},"2386":{"id":2386,"surah":20,"ayah":38,"verse":"\u201cWhen We inspired in your mother\u2019s heart whatever was to be inspired.\u201d"},"2387":{"id":2387,"surah":20,"ayah":39,"verse":"\u201cThat, \u2018Put him into a chest and cast it into the river, so the river shall deposit it on to a shore \u2013 therefore one who is an enemy to Me and you, shall pick him up\u2019; and I bestowed upon you love from Myself; and for you to be brought up in My sight.\u201d"},"2388":{"id":2388,"surah":20,"ayah":40,"verse":"\u201cWhen your sister went, then said, \u2018Shall I show you the people who may nurse him?\u2019 And We brought you back to your mother in order to soothe her eyes and that she may not grieve; and you killed a man, so We freed you from sorrow, and tested you to the maximum; you therefore stayed for several years among the people of Madyan; then you came (here) at an appointed time, O Moosa.\u201d"},"2389":{"id":2389,"surah":20,"ayah":41,"verse":"\u201cAnd I created you especially for Myself.\u201d"},"2390":{"id":2390,"surah":20,"ayah":42,"verse":"\u201cYou and your brother, both go with My signs, and do not slacken in My remembrance.\u201d"},"2391":{"id":2391,"surah":20,"ayah":43,"verse":"\u201cBoth of you go to Firaun; he has indeed rebelled.\u201d"},"2392":{"id":2392,"surah":20,"ayah":44,"verse":"\u201cAnd speak to him courteously, that perhaps he may ponder or have some fear.\u201d"},"2393":{"id":2393,"surah":20,"ayah":45,"verse":"They both submitted, \u201cOur Lord \u2013 indeed we fear that he may oppress us or deal mischievously.\u201d"},"2394":{"id":2394,"surah":20,"ayah":46,"verse":"He said, \u201cDo not fear \u2013 I am with you, All Hearing and All Seeing.\u201d"},"2395":{"id":2395,"surah":20,"ayah":47,"verse":"\u201cTherefore go to him and say, \u2018We are the sent ones of your Lord, therefore let the Descendants of Israel go with us, and do not trouble them; we have indeed brought to you a sign from your Lord; and peace be upon him who follows the guidance.\u2019"},"2396":{"id":2396,"surah":20,"ayah":48,"verse":"\u2018It has indeed been revealed to us that the punishment is upon the one who denies and turns away.\u2019\u201d"},"2397":{"id":2397,"surah":20,"ayah":49,"verse":"Said Firaun, \u201cSo who is the Lord of you both, O Moosa?\u201d"},"2398":{"id":2398,"surah":20,"ayah":50,"verse":"He said, \u201cOur Lord is One Who gave everything its proper shape, then showed the path.\u201d"},"2399":{"id":2399,"surah":20,"ayah":51,"verse":"Said Firaun, \u201cWhat is the state of the former generations?\u201d"},"2400":{"id":2400,"surah":20,"ayah":52,"verse":"He said, \u201cTheir knowledge is with my Lord, (recorded) in a Book; my Lord neither strays nor forgets.\u201d"},"2401":{"id":2401,"surah":20,"ayah":53,"verse":"The One Who has made the earth a bed for you and kept operative roads for you in it and sent down water from the sky; so with it We produced different pairs of vegetation."},"2402":{"id":2402,"surah":20,"ayah":54,"verse":"Eat, and graze your cattle; indeed in this are signs for people of intellect."},"2403":{"id":2403,"surah":20,"ayah":55,"verse":"From the earth We have created you, and to it We shall return you, and from it We shall raise you again."},"2404":{"id":2404,"surah":20,"ayah":56,"verse":"And indeed We showed him all Our signs \u2013 so he denied them and did not accept."},"2405":{"id":2405,"surah":20,"ayah":57,"verse":"He said, \u201cHave you come to us in order to expel us from our land by your magic, O Moosa?\u201d"},"2406":{"id":2406,"surah":20,"ayah":58,"verse":"\u201cSo we will also produce before you a similar magic, therefore set up an agreed time between us and you, from which neither we nor you shall turn away, at a level place.\u201d"},"2407":{"id":2407,"surah":20,"ayah":59,"verse":"Said Moosa, \u201cYour meeting is the day of the festival, and that the people be assembled at late morning.\u201d"},"2408":{"id":2408,"surah":20,"ayah":60,"verse":"So Firaun went away and gathered his schemes,* then came. (* 72000 magicians and their materials.)"},"2409":{"id":2409,"surah":20,"ayah":61,"verse":"Moosa said to them, \u201cRuin is to you \u2013 do not fabricate a lie against Allah, that He may destroy you by a punishment; and indeed one who fabricates lies has failed.\u201d"},"2410":{"id":2410,"surah":20,"ayah":62,"verse":"So they differed with one another in their task, and secretly conferred."},"2411":{"id":2411,"surah":20,"ayah":63,"verse":"They said, \u201cUndoubtedly these two are magicians for sure, who wish to expel you from your land by the strength of their magic, and destroy your exemplary religion!\u201d"},"2412":{"id":2412,"surah":20,"ayah":64,"verse":"\u201cTherefore strengthen your scheme, and come forth in rows; indeed whoever dominates this day has succeeded.\u201d"},"2413":{"id":2413,"surah":20,"ayah":65,"verse":"They said, \u201cO Moosa, either you throw first \u2013 or shall we throw first?\u201d"},"2414":{"id":2414,"surah":20,"ayah":66,"verse":"He said, \u201cRather, you may throw\u201d; thereupon their cords and their staves, by the strength of their magic, appeared to him as if they were (serpents) moving fast."},"2415":{"id":2415,"surah":20,"ayah":67,"verse":"And Moosa sensed fear in his heart."},"2416":{"id":2416,"surah":20,"ayah":68,"verse":"We said, \u201cDo not fear \u2013 it is you who is dominant.\u201d"},"2417":{"id":2417,"surah":20,"ayah":69,"verse":"\u201cAnd cast down which is in your right hand \u2013 it will devour all that they have fabricated; what they have made is only a magician\u2019s deceit; and a magician is never successful, wherever he comes.\u201d"},"2418":{"id":2418,"surah":20,"ayah":70,"verse":"Therefore all the magicians were thrown down prostrate \u2013 they said, \u201cWe accept faith in the One Who is the Lord of Haroon and Moosa.\u201d"},"2419":{"id":2419,"surah":20,"ayah":71,"verse":"Said Firaun, \u201cYou accepted faith in him before I permitted you! He is indeed your leader who taught you magic; I therefore swear, I will cut off your hands and your legs from alternate sides, and crucify you on the trunks of palm-trees, and you will surely come to know among us two, whose punishment is more severe and more lasting.\u201d"},"2420":{"id":2420,"surah":20,"ayah":72,"verse":"They said, \u201cWe shall never prefer you above the clear proofs that have come to us from the One Who has created us \u2013 therefore carry out what you want to; only in the life of this world will you be able to!\u201d"},"2421":{"id":2421,"surah":20,"ayah":73,"verse":"\u201cIndeed we have accepted faith in our Lord, so that He may forgive us our sins and the magic which you forced us to perform; and Allah is Better, and the Most Lasting.\u201d"},"2422":{"id":2422,"surah":20,"ayah":74,"verse":"Indeed the one who comes guilty to his Lord \u2013 so undoubtedly for him is hell; neither dying nor living in it."},"2423":{"id":2423,"surah":20,"ayah":75,"verse":"And the one who presents himself as a believer before Him, having done good deeds \u2013 so for them are the high ranks."},"2424":{"id":2424,"surah":20,"ayah":76,"verse":"Everlasting Gardens of Eden beneath which rivers flow, abiding in them for ever; and this is the reward of one who became pure."},"2425":{"id":2425,"surah":20,"ayah":77,"verse":"And We divinely inspired Moosa that, \u201cJourney with My bondmen in a part of the night and strike a dry path in the sea for them, you shall have no fear of Firaun getting to you, nor any danger.\u201d"},"2426":{"id":2426,"surah":20,"ayah":78,"verse":"So Firaun went after them with his army \u2013 therefore the sea enveloped them, the way it did."},"2427":{"id":2427,"surah":20,"ayah":79,"verse":"And Firaun led his people astray, and did not guide them."},"2428":{"id":2428,"surah":20,"ayah":80,"verse":"O Descendants of Israel, indeed We rescued you from your enemy, and We made a covenant with you on the right side of Mount Tur, and sent down Manna and Salwa upon you."},"2429":{"id":2429,"surah":20,"ayah":81,"verse":"\u201cEat the good clean things We have provided you, and do not exceed the limits in respect of it causing My wrath to descend upon you; and indeed the one on whom My wrath descended, has fallen.\u201d"},"2430":{"id":2430,"surah":20,"ayah":82,"verse":"And indeed I am Most Oft Forgiving for him who repented and accepted faith and did good deeds, and then remained upon guidance."},"2431":{"id":2431,"surah":20,"ayah":83,"verse":"\u201cAnd why did you come in haste ahead of your people, O Moosa?\u201d"},"2432":{"id":2432,"surah":20,"ayah":84,"verse":"He submitted, \u201cThey are here, just behind me \u2013 and O my Lord, I hastened towards You, in order to please You.\u201d"},"2433":{"id":2433,"surah":20,"ayah":85,"verse":"He said, \u201cWe have therefore tried your people after you came, and Samri has led them astray.\u201d"},"2434":{"id":2434,"surah":20,"ayah":86,"verse":"So Moosa turned back to his people, angry and grieving; he said, \u201cO my people, had not your Lord given you a good promise? Did a long time pass away for you, or did you wish that your Lord\u2019s wrath come upon you, therefore you broke your promise with me?\u201d"},"2435":{"id":2435,"surah":20,"ayah":87,"verse":"They said, \u201cWe did not renege on our promise to you on our own will, but we were made to carry the burdens of ornaments of the people, so we cast them \u2013 and similarly did Samri cast.\u201d"},"2436":{"id":2436,"surah":20,"ayah":88,"verse":"He therefore made a calf for them \u2013 a lifeless body, making sounds like a cow \u2013 so they said, \u201cThis is your God and the God of Moosa; whereas Moosa has forgotten.\u201d"},"2437":{"id":2437,"surah":20,"ayah":89,"verse":"So do they not see that it does not answer to any of their speech? And has no power to cause them any harm or benefit?"},"2438":{"id":2438,"surah":20,"ayah":90,"verse":"And undoubtedly Haroon had told them before it that, \u201cO my people \u2013 you have needlessly fallen into trial because of this; and indeed your Lord is the Most Gracious, therefore follow me and obey my command.\u201d"},"2439":{"id":2439,"surah":20,"ayah":91,"verse":"They said, \u201cWe will continue to squat* before it, till Moosa returns to us.\u201d (* Continue worshipping it.)"},"2440":{"id":2440,"surah":20,"ayah":92,"verse":"Said Moosa, \u201cO Haroon \u2013 what prevented you when you saw them going astray?\u201d"},"2441":{"id":2441,"surah":20,"ayah":93,"verse":"\u201cThat you did not come after me? So did you disobey my order?\u201d"},"2442":{"id":2442,"surah":20,"ayah":94,"verse":"He said, \u201cO son of my mother, do not clutch my beard nor the hair on my head; I feared that you may say, \u2018You have caused a division among the Descendants of Israel and did not wait for my advice.\u2019\u201d"},"2443":{"id":2443,"surah":20,"ayah":95,"verse":"Said Moosa, \u201cAnd what is your explanation, O Samri?\u201d"},"2444":{"id":2444,"surah":20,"ayah":96,"verse":"He said, \u201cI witnessed what the people did not witness \u2013 I therefore took a handful from the tracks* of the angel, then threw it** \u2013 and this is what seemed pleasing to my soul.\u201d (* The marks left behind by the mount of Angel Jibreel. ** Into the mouth of the calf.)"},"2445":{"id":2445,"surah":20,"ayah":97,"verse":"Said Moosa, \u201cTherefore go away, for in this life your punishment is that you exclaim \u2018Do not touch!\u2019* And indeed for you is a time appointed, which you cannot break; and look at your deity, in front of which you remained squatting the whole day; we swear we will surely burn it and, smashing it into bits, discharge it into the river.\u201d (* He was cursed with a disease.)"},"2446":{"id":2446,"surah":20,"ayah":98,"verse":"\u201cYour God is only Allah \u2013 other than for Whom there is no worship; His knowledge encompasses all things.\u201d"},"2447":{"id":2447,"surah":20,"ayah":99,"verse":"This is how We relate the former tidings to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him); and We have given you a Remembrance* from Ourselves. (*The Holy Qur\u2019an.)"},"2448":{"id":2448,"surah":20,"ayah":100,"verse":"Whoever turns away from it, will bear a burden on the Day of Resurrection."},"2449":{"id":2449,"surah":20,"ayah":101,"verse":"They will remain in it forever \u2013 what an evil burden it will be for them on the Day of Resurrection!"},"2450":{"id":2450,"surah":20,"ayah":102,"verse":"On the day when the Trumpet is blown and We shall assemble the guilty on that day, blue -eyed."},"2451":{"id":2451,"surah":20,"ayah":103,"verse":"Whispering secretly among themselves, \u201cYou have not stayed on earth but for ten days.\u201d"},"2452":{"id":2452,"surah":20,"ayah":104,"verse":"We know well what they will utter, whereas the wisest among them will say, \u201cYou have stayed just for a day.\u201d"},"2453":{"id":2453,"surah":20,"ayah":105,"verse":"They ask you regarding the mountains; proclaim, \u201cMy Lord will blow them into bits and scatter them.\u201d"},"2454":{"id":2454,"surah":20,"ayah":106,"verse":"\u201cTherefore leaving the earth just as an empty plain.\u201d"},"2455":{"id":2455,"surah":20,"ayah":107,"verse":"\u201cIn which you shall neither see ups nor downs.\u201d"},"2456":{"id":2456,"surah":20,"ayah":108,"verse":"On that day they will run after a caller, there will be no deviation in it; and voices shall become hushed before the Most Gracious, so you will not hear except a faint sound."},"2457":{"id":2457,"surah":20,"ayah":109,"verse":"On that day no one\u2019s intercession shall benefit except his to whom the Most Gracious has given permission and whose word He has liked. (The Holy Prophets and virtuous people will be given the permission to intercede. Prophet Mohammed \u2013 peace and blessings be upon him \u2013 will be the first to intercede.)"},"2458":{"id":2458,"surah":20,"ayah":110,"verse":"He knows all that is before them and all that is behind them, whereas their knowledge cannot encompass it."},"2459":{"id":2459,"surah":20,"ayah":111,"verse":"And all faces shall bow before the Living, the All Sustaining; and the one who bore the burden of injustice, has failed."},"2460":{"id":2460,"surah":20,"ayah":112,"verse":"And the one who does some good deeds, and is a Muslim \u2013 he shall have no fear of injustice, nor suffer any loss."},"2461":{"id":2461,"surah":20,"ayah":113,"verse":"And this is how We revealed it as an Arabic Qur\u2019an, and in different ways gave promises of punishment, that they may fear or it may create some pondering in their hearts."},"2462":{"id":2462,"surah":20,"ayah":114,"verse":"Therefore Supreme is Allah, the True King; and do not hasten in the Qur\u2019an (O dear Prophet Mohammed \u2013 peace and blessings be upon him) until its divine revelation has been completed to you; and pray, \u201cMy Lord, bestow me more knowledge.\u201d"},"2463":{"id":2463,"surah":20,"ayah":115,"verse":"And indeed before this We made a covenant with Adam, so he forgot, and We did not find its intention (in him)."},"2464":{"id":2464,"surah":20,"ayah":116,"verse":"And when We commanded the angels, \u201cProstrate before Adam\u201d \u2013 so they all prostrated, except Iblis; he refused."},"2465":{"id":2465,"surah":20,"ayah":117,"verse":"We therefore said, \u201cO Adam, he is your and your wife\u2019s enemy, so may he not get you both out from heaven, so you then fall into hardship.\u201d"},"2466":{"id":2466,"surah":20,"ayah":118,"verse":"\u201cIndeed for you in heaven is that you may never be hungry nor be unclothed.\u201d"},"2467":{"id":2467,"surah":20,"ayah":119,"verse":"\u201cAnd that you never feel thirsty nor hot sunshine hurt you.\u201d"},"2468":{"id":2468,"surah":20,"ayah":120,"verse":"So the devil incited him, saying, \u201cO Adam, shall I show you the tree of immortality and a kingdom that does not erode?\u201d"},"2469":{"id":2469,"surah":20,"ayah":121,"verse":"So they both ate from it \u2013 thereupon their shame became manifest to them, and they started applying on themselves the leaves of heaven; and Adam lapsed in obeying his Lord, so did not reach his goal. (Of achieving immortality)"},"2470":{"id":2470,"surah":20,"ayah":122,"verse":"Then his Lord chose him, and inclined towards him with His mercy, and guided him."},"2471":{"id":2471,"surah":20,"ayah":123,"verse":"He said, \u201cBoth of you go down from heaven, one of you is an enemy to the other; then if the guidance from Me comes to you \u2013 then whoever follows My guidance, will not go astray nor be ill-fated.\u201d"},"2472":{"id":2472,"surah":20,"ayah":124,"verse":"\u201cAnd the one who turned away from My remembrance \u2013 for him is a confined existence, and We shall raise him blind on the Day of Resurrection.\u201d"},"2473":{"id":2473,"surah":20,"ayah":125,"verse":"He will say, \u201cO my Lord, why have You raised me blind, whereas I was sighted?\u201d"},"2474":{"id":2474,"surah":20,"ayah":126,"verse":"He will say, \u201cSimilarly did Our signs come to you but you forgot them; and in the same way, nobody will heed you today.\u201d"},"2475":{"id":2475,"surah":20,"ayah":127,"verse":"And this is how We reward him who transgresses and does not accept faith in the signs of his Lord; and indeed the punishment of the Hereafter is the most severe and more lasting."},"2476":{"id":2476,"surah":20,"ayah":128,"verse":"So did they not gain guidance from (knowing) how many generations We have destroyed before them, among whose dwellings they walk? Indeed in it are signs for men of intellect."},"2477":{"id":2477,"surah":20,"ayah":129,"verse":"And had not a command of your Lord been passed, then the punishment would have gripped them \u2013 and had a term not been appointed."},"2478":{"id":2478,"surah":20,"ayah":130,"verse":"Therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him), patiently forbear upon their speech, and praising your Lord proclaim His Purity, before the sun rises and before it sets; and proclaim His Purity at some times of the night and at the two ends of the day, in the hope that you be pleased. (*With the great reward from your Lord)"},"2479":{"id":2479,"surah":20,"ayah":131,"verse":"And O listener, do not extend your eyes towards what We have given to disbelieving couples to enjoy \u2013 the bloom of the worldly life \u2013 so that We may test them with it; and the sustenance of your Lord is the best, and more lasting."},"2480":{"id":2480,"surah":20,"ayah":132,"verse":"And command your household to establish prayer, and yourself be steadfast in it; We do not ask any sustenance from you; We will provide you sustenance; and the excellent result is for piety."},"2481":{"id":2481,"surah":20,"ayah":133,"verse":"And the disbelievers said, \u201cWhy does he not bring to us a sign from his Lord?\u201d; did not the explanation of what is in the former Books, come to them?"},"2482":{"id":2482,"surah":20,"ayah":134,"verse":"And had We destroyed them with some punishment before the advent of a Noble Messenger, they would have certainly said, \u201cOur Lord, why did You not send a Noble Messenger to us, so we would have followed Your signs, before being humiliated and disgraced?\u201d"},"2483":{"id":2483,"surah":20,"ayah":135,"verse":"Proclaim, \u201cEach one is waiting; so you too wait; very soon you will come to know who are the people of the right path, and who has attained guidance.\u201d"},"2484":{"id":2484,"surah":21,"ayah":1,"verse":"The people\u2019s reckoning is near, whereas they are in neglect, turned away!"},"2485":{"id":2485,"surah":21,"ayah":2,"verse":"Whenever a new advice comes to them from their Lord, they do not listen to it except while playing."},"2486":{"id":2486,"surah":21,"ayah":3,"verse":"Their hearts are involved in play; and the unjust secretly conferred, \u201cWhat is he, except another a human like you?! So do you follow magic although you have perceived?\u201d"},"2487":{"id":2487,"surah":21,"ayah":4,"verse":"And the Prophet said, \u201cMy Lord knows all that is spoken in the heavens and in the earth; and He only is the All Hearing, the All Knowing.\""},"2488":{"id":2488,"surah":21,"ayah":5,"verse":"Rather they said, \u201cThese are confused dreams, but in fact he has fabricated it \u2013 but in fact he is a poet; so he must bring us some sign, like those who were sent before.\u201d"},"2489":{"id":2489,"surah":21,"ayah":6,"verse":"There is not a township before them which did not believe which We have not destroyed; so will they believe?"},"2490":{"id":2490,"surah":21,"ayah":7,"verse":"And We did not send (Prophets) before you except men, to whom We sent divine revelations \u2013 therefore, O people, ask the people of knowledge if you do not know."},"2491":{"id":2491,"surah":21,"ayah":8,"verse":"And We did not create them without bodies so they would not eat food \u2013 nor that they abide on earth forever."},"2492":{"id":2492,"surah":21,"ayah":9,"verse":"We then fulfilled the promise to them, therefore rescued them and whomever We willed, and destroyed the transgressors."},"2493":{"id":2493,"surah":21,"ayah":10,"verse":"We have indeed sent down towards you a Book, in which is your repute; so do you not have sense?"},"2494":{"id":2494,"surah":21,"ayah":11,"verse":"And many a township did We destroy, for they were unjust, and We created other nations after them."},"2495":{"id":2495,"surah":21,"ayah":12,"verse":"And when they tasted Our punishment, they immediately started fleeing from it."},"2496":{"id":2496,"surah":21,"ayah":13,"verse":"The angels said to them, \u201cDo not flee and return to the comforts that were given to you and to your homes, perhaps you will be questioned.\u201d"},"2497":{"id":2497,"surah":21,"ayah":14,"verse":"They cried, \u201cWoe to us, we were indeed unjust!\u201d"},"2498":{"id":2498,"surah":21,"ayah":15,"verse":"So they kept saying this until We made them cut off, extinguished."},"2499":{"id":2499,"surah":21,"ayah":16,"verse":"And We have not created the heavens and the earth and all that is between them, unnecessarily."},"2500":{"id":2500,"surah":21,"ayah":17,"verse":"If We willed to choose a pastime, We could have chosen it from Ourselves \u2013 if We wanted to."},"2501":{"id":2501,"surah":21,"ayah":18,"verse":"But in fact We hurl the truth upon falsehood, so it scatters its brains \u2013 thereupon it vanishes; and for you is the ruin due to the matters you fabricate."},"2502":{"id":2502,"surah":21,"ayah":19,"verse":"And to Him only belong all those who are in the heavens and in the earth; and those with Him are not conceited towards worshipping Him, nor do they tire."},"2503":{"id":2503,"surah":21,"ayah":20,"verse":"They say His Purity night and day, and do not slacken."},"2504":{"id":2504,"surah":21,"ayah":21,"verse":"Have they appointed from the earth, Gods that create something?"},"2505":{"id":2505,"surah":21,"ayah":22,"verse":"If other than Allah, there were Gods* in the heavens and the earth, they would be destroyed; therefore Purity is to Allah, Owner of the Throne, from the matters that they fabricate. (* Which is not possible. ** The heavens and the earth.)"},"2506":{"id":2506,"surah":21,"ayah":23,"verse":"He is not questioned whatever He does, whereas they will all be questioned."},"2507":{"id":2507,"surah":21,"ayah":24,"verse":"Or have they set up other Gods besides Allah? Say, \u201cBring your proof; this is the remembrance of those with me and those before me\u201d; but in fact most of them do not know the Truth, so they turn away."},"2508":{"id":2508,"surah":21,"ayah":25,"verse":"And We did not send any Noble Messenger before you, but We divinely revealed to him that, \u201cThere is no God except I (Allah), therefore worship Me alone.\u201d"},"2509":{"id":2509,"surah":21,"ayah":26,"verse":"And they said, \u201cThe Most Gracious has chosen a son \u2013 Purity is to Him! In fact they are honourable bondmen.\u201d"},"2510":{"id":2510,"surah":21,"ayah":27,"verse":"They do not speak before He has, and they act only at His command."},"2511":{"id":2511,"surah":21,"ayah":28,"verse":"He knows what is before them and what is behind them, and they do not intercede except for him whom He likes, and they fear with awe of Him. (The Holy Prophets and virtuous people will be given the permission to intercede. Prophet Mohammed \u2013 peace and blessings be upon him \u2013 will be the first to intercede.)"},"2512":{"id":2512,"surah":21,"ayah":29,"verse":"And the one among them who says, \u201cI am a God beside Allah\u201d \u2013 We shall reward him with hell; this is how We punish the unjust."},"2513":{"id":2513,"surah":21,"ayah":30,"verse":"Did not the disbelievers observe that the heavens and the earth were together, so We parted them, and we made every living thing from water? So will they not accept faith?"},"2514":{"id":2514,"surah":21,"ayah":31,"verse":"And We have placed mountains as anchors in the earth so that it may not shake with them; and We kept wide roads in it, so that they may find guidance."},"2515":{"id":2515,"surah":21,"ayah":32,"verse":"And We have made the sky a roof, protected; and they turn away from its signs."},"2516":{"id":2516,"surah":21,"ayah":33,"verse":"And it is He Who created the night and the day, and the sun and the moon; each one floats in an orbit."},"2517":{"id":2517,"surah":21,"ayah":34,"verse":"And before you, We did not appoint on earth a never-ending life for any human; will they, if you depart, become immortal?"},"2518":{"id":2518,"surah":21,"ayah":35,"verse":"Every living being must taste death; and We test you with harm and with good \u2013 a trial; and to Us only you have to return."},"2519":{"id":2519,"surah":21,"ayah":36,"verse":"And when the disbelievers see you, they do not appoint you except as an object of mockery; \u201cIs he the one who speaks ill of your Gods?\u201d; whereas they deny the remembrance of the Most Gracious Himself!"},"2520":{"id":2520,"surah":21,"ayah":37,"verse":"Man has been created hasty; very soon I shall show you My signs, do not be impatient."},"2521":{"id":2521,"surah":21,"ayah":38,"verse":"And they say, \u201cWhen will this promise occur, if you are truthful?"},"2522":{"id":2522,"surah":21,"ayah":39,"verse":"If only the disbelievers realised the time when they will not be able to stop the fire from their faces and from their backs \u2013 and nor are they to be helped."},"2523":{"id":2523,"surah":21,"ayah":40,"verse":"In fact it will suddenly come upon them, therefore shocking them, and they will not be able to repel it nor will they be given respite."},"2524":{"id":2524,"surah":21,"ayah":41,"verse":"And indeed the Noble Messengers before you were mocked at, but their mockery ruined the mockers themselves."},"2525":{"id":2525,"surah":21,"ayah":42,"verse":"Proclaim, \u201cWho guards you night and day from the Most Gracious?\u201d In fact they have turned away from the remembrance of their Lord."},"2526":{"id":2526,"surah":21,"ayah":43,"verse":"Do they have some Gods who protect them from Us? Neither can they save themselves nor save their friends from Us."},"2527":{"id":2527,"surah":21,"ayah":44,"verse":"But in fact We have given these (disbelievers) and their fathers a benefit to the extent that life became long for them; so do they not see that We are reducing the land from its borders? So will these be victorious?"},"2528":{"id":2528,"surah":21,"ayah":45,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u201cI warn you only with the divine revelation; and the deaf do not hear the call when warned.\u201d"},"2529":{"id":2529,"surah":21,"ayah":46,"verse":"And if a whiff of your Lord\u2019s punishment were to touch them, they would certainly say, \u201cWoe to us \u2013 we were indeed unjust!\u201d"},"2530":{"id":2530,"surah":21,"ayah":47,"verse":"And We shall set up the scales of justice on the Day of Resurrection \u2013 therefore no soul will be wronged in the least; and if a thing is equal to a grain of mustard seed, We will bring it; and We are Sufficient to (take) account."},"2531":{"id":2531,"surah":21,"ayah":48,"verse":"And indeed We gave Moosa and Haroon the Judgement* and a light and an advice for the pious. (* The Holy Book Taurat.)"},"2532":{"id":2532,"surah":21,"ayah":49,"verse":"Those who fear their Lord without seeing and who apprehend the Last Day."},"2533":{"id":2533,"surah":21,"ayah":50,"verse":"This is a blessed remembrance, sent down by Us; so do you deny it?"},"2534":{"id":2534,"surah":21,"ayah":51,"verse":"And indeed We bestowed Ibrahim with guidance from the beginning, and We were Aware of him."},"2535":{"id":2535,"surah":21,"ayah":52,"verse":"When he said to his father and his people, \u201cWhat are these idols before whom you squat (worshipping)?\u201d"},"2536":{"id":2536,"surah":21,"ayah":53,"verse":"They said, \u201cWe found our forefathers worshipping them.\u201d"},"2537":{"id":2537,"surah":21,"ayah":54,"verse":"He said, \u201cIndeed you all \u2013 you and your forefathers \u2013 were in open error.\u201d"},"2538":{"id":2538,"surah":21,"ayah":55,"verse":"They said, \u201cHave you brought the Truth to us, or are you just making fun?\u201d"},"2539":{"id":2539,"surah":21,"ayah":56,"verse":"He said, \u201cIn fact, your Lord is the Lord of the heavens and the earth, the One Who created them; and I am of those who testify to it.\u201d"},"2540":{"id":2540,"surah":21,"ayah":57,"verse":"\u201cAnd, by oath of Allah, I shall seek to harm your idols after you have gone away and turned your backs.\u201d"},"2541":{"id":2541,"surah":21,"ayah":58,"verse":"He shattered them all, except the biggest among them, that perhaps they may question it."},"2542":{"id":2542,"surah":21,"ayah":59,"verse":"They said, \u201cWho has done this to our Gods? He is indeed cruel!\u201d"},"2543":{"id":2543,"surah":21,"ayah":60,"verse":"Some among them said, \u201cWe heard a youth speak ill of them \u2013 the one called Ibrahim.\u201d"},"2544":{"id":2544,"surah":21,"ayah":61,"verse":"They said, \u201cTherefore bring him in front of the people, perhaps they may testify.\u201d"},"2545":{"id":2545,"surah":21,"ayah":62,"verse":"They said, \u201cDid you do this to our Gods, O Ibrahim?\u201d"},"2546":{"id":2546,"surah":21,"ayah":63,"verse":"Said he, \u201cRather, their chief may have done it; so question them, if they can speak.\u201d"},"2547":{"id":2547,"surah":21,"ayah":64,"verse":"So they turned towards their own selves and (inwardly) said, \u201cIndeed you yourselves are unjust.\u201d"},"2548":{"id":2548,"surah":21,"ayah":65,"verse":"Again they were inverted upon their heads; saying, \u201cYou know well that these do not speak.\u201d"},"2549":{"id":2549,"surah":21,"ayah":66,"verse":"He said, \u201cWhat! You worship, instead of Allah, one that neither benefits you nor harms you?\u201d"},"2550":{"id":2550,"surah":21,"ayah":67,"verse":"\u201cDisgrace be upon you and all the idols whom you worship instead of Allah; so do you not have sense?\u201d"},"2551":{"id":2551,"surah":21,"ayah":68,"verse":"They said, \u201cBurn him and help your Gods, if you want to.\u201d"},"2552":{"id":2552,"surah":21,"ayah":69,"verse":"We said, \u201cO fire, become cool and peaceful upon Ibrahim.\u201d"},"2553":{"id":2553,"surah":21,"ayah":70,"verse":"And they wished to cause him harm, so We made them the greatest of losers."},"2554":{"id":2554,"surah":21,"ayah":71,"verse":"And We rescued him and Lut towards the land which We have blessed for the entire world."},"2555":{"id":2555,"surah":21,"ayah":72,"verse":"And We bestowed him Ishaq, and Yaqub the grandson; and We made all of them worthy of Our proximity."},"2556":{"id":2556,"surah":21,"ayah":73,"verse":"And We made them leaders who guide by Our command, and We sent them the divine revelation to do good deeds and to keep the prayer established and to give charity; and they used to worship Us."},"2557":{"id":2557,"surah":21,"ayah":74,"verse":"And We gave Lut the kingdom and knowledge, and We rescued him from the township that committed vile deeds; indeed those evil people were disobedient."},"2558":{"id":2558,"surah":21,"ayah":75,"verse":"And We admitted him into Our mercy; indeed he is among those who deserve Our proximity."},"2559":{"id":2559,"surah":21,"ayah":76,"verse":"And before this when Nooh called Us \u2013 We therefore heard his prayer and rescued him and his household from the great calamity."},"2560":{"id":2560,"surah":21,"ayah":77,"verse":"And We helped him against the people who denied Our signs; indeed they were evil people, We therefore drowned them all."},"2561":{"id":2561,"surah":21,"ayah":78,"verse":"And remember Dawud and Sulaiman, when they were deciding the dispute of a field, when some people\u2019s sheep had strayed into it at night; and We were Present at the time of their deciding."},"2562":{"id":2562,"surah":21,"ayah":79,"verse":"And We explained the case to Sulaiman; and to both We gave the kingdom and knowledge; and subjected the hills to proclaim the Purity along with Dawud, and (also subjected) the birds; and these were Our works."},"2563":{"id":2563,"surah":21,"ayah":80,"verse":"And We taught him to make a garment for you, to protect you from your hurt; so will you be thankful?"},"2564":{"id":2564,"surah":21,"ayah":81,"verse":"And We subjected the fast wind for Sulaiman, which moved by his command towards the land in which We have placed blessings; and We know everything."},"2565":{"id":2565,"surah":21,"ayah":82,"verse":"And among the devils, were those who dived (in water) for him and did works other than this; and We had kept them restrained."},"2566":{"id":2566,"surah":21,"ayah":83,"verse":"And remember Ayyub (Job), when he called his Lord that, \u201cHardship has afflicted me, and You are the Most Merciful of all those who have mercy.\u201d"},"2567":{"id":2567,"surah":21,"ayah":84,"verse":"We therefore heard his prayer and removed the adversity that had afflicted him, and We gave him his family and in addition bestowed along with them a similar number, by mercy from Ourselves \u2013 and an advice for the people who worship."},"2568":{"id":2568,"surah":21,"ayah":85,"verse":"And remember Ismail, and Idrees, and Zul-Kifl; they were all patiently enduring."},"2569":{"id":2569,"surah":21,"ayah":86,"verse":"And We admitted them into Our mercy; indeed they are among those who deserve Our proximity."},"2570":{"id":2570,"surah":21,"ayah":87,"verse":"And remember Zun-Noon,* when he left in anger, assuming that We would not restrict him \u2013 he therefore called out in the realms of darkness, saying, \u201cThere is no God except You, Purity is to You; I have indeed committed a lapse.\u201d (* Prophet Yunus \u2013 peace and blessings upon him)"},"2571":{"id":2571,"surah":21,"ayah":88,"verse":"We therefore heard his prayer and rescued him from grief; and similarly We shall rescue the Muslims."},"2572":{"id":2572,"surah":21,"ayah":89,"verse":"And remember Zakaria, when he prayed to his Lord, \u201cO my Lord \u2013 do not leave me alone, and You are the Best Inheritor."},"2573":{"id":2573,"surah":21,"ayah":90,"verse":"We therefore heard his prayer; and bestowed him Yahya, and cured his wife for him; indeed they used to hasten to perform good deeds, and pray to Us with hope and fear; and used to weep before Us."},"2574":{"id":2574,"surah":21,"ayah":91,"verse":"And remember the woman who maintained her chastity, We therefore breathed Our Spirit into her and made her and her son a sign for the entire world."},"2575":{"id":2575,"surah":21,"ayah":92,"verse":"Indeed this religion of yours, is one religion; and I am your Lord, therefore worship Me."},"2576":{"id":2576,"surah":21,"ayah":93,"verse":"And others have shattered their works into pieces among themselves; all have to return to Us."},"2577":{"id":2577,"surah":21,"ayah":94,"verse":"So whoever does some good deeds, and is a believer, then his efforts will not be ignored; and We are recording it."},"2578":{"id":2578,"surah":21,"ayah":95,"verse":"And it is forbidden for any township which We have destroyed, that they may return. (Once the disbelievers face death, their return to earth is impossible.)"},"2579":{"id":2579,"surah":21,"ayah":96,"verse":"Till the time when Yajuj and Majuj are released, and they will be flowing down from every height."},"2580":{"id":2580,"surah":21,"ayah":97,"verse":"And the True Promise has come near \u2013 thereupon the eyes of the disbelievers will become fixed, staring wide; saying \u201cWoe to us \u2013 we were in neglect of this, but in fact we were unjust.\u201d"},"2581":{"id":2581,"surah":21,"ayah":98,"verse":"\u201cIndeed you* and all that you worship** besides Allah, are the fuel of hell; in it you must go.\u201d (* All disbelievers ** Idols and disbelievers who claimed to be Gods. The Prophets like Eisa and Uzair who were worshipped are exempt from this, and so are Maryam, and trees and the moon etc.)"},"2582":{"id":2582,"surah":21,"ayah":99,"verse":"\u201cHad these been Gods they would not go into hell; and they have to remain in it.\u201d"},"2583":{"id":2583,"surah":21,"ayah":100,"verse":"They will bray in it and not be able to hear anything in it."},"2584":{"id":2584,"surah":21,"ayah":101,"verse":"Indeed those to whom Our promise of goodness has been made, have been kept far away from hell."},"2585":{"id":2585,"surah":21,"ayah":102,"verse":"And they will not hear its faintest sound; and they will forever abide in whatever their hearts desire."},"2586":{"id":2586,"surah":21,"ayah":103,"verse":"The greatest fear will not aggrieve them, and the angels will welcome them; saying \u201cThis is your day, which you were promised.\u201d"},"2587":{"id":2587,"surah":21,"ayah":104,"verse":"The day when We shall roll up the heavens as the recording angel rolls up the register of deeds; We shall make him similar to Our making him the first time; this is a promise upon Us; We certainly have to do it."},"2588":{"id":2588,"surah":21,"ayah":105,"verse":"And indeed We wrote, after the reminder in the Zaboor that, \u201cMy virtuous bondmen will inherit the earth.\u201d"},"2589":{"id":2589,"surah":21,"ayah":106,"verse":"This Qur\u2019an is sufficient for people who are devout."},"2590":{"id":2590,"surah":21,"ayah":107,"verse":"And We did not send you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) except as a mercy for the entire world. (Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is the Prophet towards all mankind.)"},"2591":{"id":2591,"surah":21,"ayah":108,"verse":"Proclaim, \u201cIt is divinely revealed to me that your God is the only One God \u2013 Allah; do you therefore become Muslims?\u201d"},"2592":{"id":2592,"surah":21,"ayah":109,"verse":"Then if they turn away, proclaim, \u201cI have proclaimed a war against you on equal terms; and what do I know whether the promise which is given to you, is close or far?\u201d"},"2593":{"id":2593,"surah":21,"ayah":110,"verse":"\u201cIndeed Allah knows whatever is said, and knows all what you conceal.\u201d"},"2594":{"id":2594,"surah":21,"ayah":111,"verse":"\u201cAnd what do I know \u2013 it may be a trial for you, and an enjoyment for a time.\u201d"},"2595":{"id":2595,"surah":21,"ayah":112,"verse":"And the Prophet submitted, \u201cMy Lord \u2013 render the true judgement\u201d; \u201cAnd only the help of Our Lord, the Most Gracious, is sought against all what you fabricate.\u201d"},"2596":{"id":2596,"surah":22,"ayah":1,"verse":"O people, fear your Lord; indeed the earthquake of the Last Day is a tremendous thing."},"2597":{"id":2597,"surah":22,"ayah":2,"verse":"On the day when you will witness it, every nursing mother will forget her nurseling and every pregnant one will discharge her burden, and you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) will see people as if they are drunk, whereas they will not be intoxicated, but the fact is that Allah\u2019s punishment is very severe."},"2598":{"id":2598,"surah":22,"ayah":3,"verse":"And among people are some who argue concerning Allah without knowing, and blindly follow every rebellious devil."},"2599":{"id":2599,"surah":22,"ayah":4,"verse":"(The devil) Upon whom is decreed that whoever befriends him, he will certainly mislead him and show him the path to hell."},"2600":{"id":2600,"surah":22,"ayah":5,"verse":"O people, if you doubt your revival on the Day of Resurrection, then ponder that We created you from dust, then from a drop of liquid, then from a clot, then from a piece of flesh formed and without form, so that We show you Our signs for you; and We keep whomever We want inside the mothers\u2019 wombs up to an appointed time, then extract you as infants, then in order that you reach your puberty; and among you is one who dies earlier, and among you is one put to the most abject age, so after having knowledge, knows nothing; and you see the earth desolate, then when We sent down water upon it, it freshened up and developed and produced beautiful pairs of all kinds."},"2601":{"id":2601,"surah":22,"ayah":6,"verse":"This is because Allah only is True and because He will revive the dead, and because He is Able to do all things."},"2602":{"id":2602,"surah":22,"ayah":7,"verse":"And because the Last Day will come, there is no doubt in it \u2013 and because Allah will revive those who are in the graves."},"2603":{"id":2603,"surah":22,"ayah":8,"verse":"And there is one who argues concerning Allah without having knowledge nor any proof nor a clear text."},"2604":{"id":2604,"surah":22,"ayah":9,"verse":"With his neck turned away from the truth, in order to deceive from the way of Allah; for him is disgrace in this world and on the Day of Resurrection We shall make him taste the punishment of fire."},"2605":{"id":2605,"surah":22,"ayah":10,"verse":"\u201cThis is the recompense of what your hands have sent ahead, and Allah does not oppress His bondmen.\""},"2606":{"id":2606,"surah":22,"ayah":11,"verse":"And there are some men who worship Allah upon an edge; then if some good occurs to them, they are content; and if some trial comes, they turn way upon their faces; a loss of this world and the Hereafter; and this is the complete loss."},"2607":{"id":2607,"surah":22,"ayah":12,"verse":"They worship such, beside Allah, which neither harms them nor benefits them; this only is the extreme error."},"2608":{"id":2608,"surah":22,"ayah":13,"verse":"They worship one from whom harm is expected more than the benefit; indeed what an evil master and indeed what an evil friend!"},"2609":{"id":2609,"surah":22,"ayah":14,"verse":"Indeed Allah will admit those who believed and did good deeds, into Gardens beneath which rivers flow; indeed Allah does whatever He wills."},"2610":{"id":2610,"surah":22,"ayah":15,"verse":"Therefore whoever assumes that Allah will not assist His Prophet (Mohammed \u2013 peace and blessings be upon him) in this world and the Hereafter, should extend a rope upwards and hang himself, and then see whether his scheme has taken away what he envies."},"2611":{"id":2611,"surah":22,"ayah":16,"verse":"And so it is, that We sent down this Qur\u2019an \u2013 clear verses \u2013 and that Allah guides whomever He wills."},"2612":{"id":2612,"surah":22,"ayah":17,"verse":"Indeed the Muslims, and the Jews, and the Sabeans, and the Christians and the fire worshippers and the polytheists \u2013 indeed Allah will decide between all of them on the Day of Resurrection; indeed Allah witnesses all things."},"2613":{"id":2613,"surah":22,"ayah":18,"verse":"Did you not see that for Allah prostrate those who are in the heavens and in the earth, and the sun, and the moon, and the stars, and the hills, and the trees, and the beasts, and many among mankind; and there are many upon whom the punishment has been decreed; and he whom Allah disgraces \u2013 there is none to give him honour; indeed Allah may do whatever He wills. (Command of Prostration # 6)"},"2614":{"id":2614,"surah":22,"ayah":19,"verse":"These are the two groups who fought concerning their Lord; so those who disbelieved \u2013 garments of fire have been fashioned for them; and boiling water will be poured onto their heads."},"2615":{"id":2615,"surah":22,"ayah":20,"verse":"With which will melt what is in their bellies, and their skins."},"2616":{"id":2616,"surah":22,"ayah":21,"verse":"And for them are rods of iron."},"2617":{"id":2617,"surah":22,"ayah":22,"verse":"Whenever they wish to come out of it due to suffocation, they will be returned to it again and it will be commanded, \u201cTaste the punishment of fire!\u201d"},"2618":{"id":2618,"surah":22,"ayah":23,"verse":"Indeed Allah will admit those who believed and did good deeds into Gardens beneath which rivers flow \u2013 in it they will be made to wear armlets of gold, and pearls, and in it their garment is silk."},"2619":{"id":2619,"surah":22,"ayah":24,"verse":"And they were guided to sacred speech; and they were shown the path of the Most Praiseworthy."},"2620":{"id":2620,"surah":22,"ayah":25,"verse":"Indeed those who have disbelieved and prevent from the way of Allah and from this Sacred Mosque, which We have appointed for all mankind \u2013 its resident and the foreigner have the same rights in it; and whoever wrongfully intends injustice in it \u2013 We shall make him taste a painful punishment."},"2621":{"id":2621,"surah":22,"ayah":26,"verse":"And remember when We showed Ibrahim the right place of the Sacred House (mosque) and commanded that, \u201cDo not ascribe anything as a partner to Me, and keep My House clean for those who encircle it and those who stay in it (for worship) and those who bow and prostrate.\u201d"},"2622":{"id":2622,"surah":22,"ayah":27,"verse":"\u201cAnd publicly announce the pilgrimage to all people \u2013 they will come to you, on foot and on every lean she-camel, coming from every far distant journey.\u201d (The announcement by Prophet Ibrahim reached each and every soul.)"},"2623":{"id":2623,"surah":22,"ayah":28,"verse":"In order that they may gain their benefit, and mention the name of Allah on the appointed days as He has bestowed the sustenance to them \u2013 the inarticulate animals; therefore eat from them yourself and feed the distressed destitute."},"2624":{"id":2624,"surah":22,"ayah":29,"verse":"They must then remove their dirt and fulfil their pledges and go around the Free House."},"2625":{"id":2625,"surah":22,"ayah":30,"verse":"So it is; and whoever reveres the sacred things of Allah \u2013 that is then a goodness for him in the sight of his Lord; the inarticulate animals are lawful to you except those the forbidding of which is recited to you, therefore shun the filth of idols, and avoid false speech."},"2626":{"id":2626,"surah":22,"ayah":31,"verse":"Devoting yourself to Allah, not ascribing any partner to Him; and whoever ascribes partners to Allah is as if he has fallen from the sky and the birds snatch him or the wind blows him away to a far-off place."},"2627":{"id":2627,"surah":22,"ayah":32,"verse":"So it is; and whoever reveres the symbols of Allah \u2013 this is then part of the piety of the hearts."},"2628":{"id":2628,"surah":22,"ayah":33,"verse":"In the cattle are benefits for you up to a fixed time and then they are to be brought to the Free House."},"2629":{"id":2629,"surah":22,"ayah":34,"verse":"And for every nation We have appointed a sacrifice, that they may mention the name of Allah over the inarticulate animal which He has provided them; so (remember) your God is One God, therefore submit only to Him; and give glad tidings (O dear Prophet Mohammed \u2013 peace and blessings be upon him) to the humble."},"2630":{"id":2630,"surah":22,"ayah":35,"verse":"Those whose hearts fear when Allah is mentioned, and those who patiently endure whatever befalls them, and those who keep the prayer established, and who spend part of what We have provided to them."},"2631":{"id":2631,"surah":22,"ayah":36,"verse":"And the large sacrificial animals \u2013 the camels and the cows \u2013 We have made them among the symbols of Allah, there is goodness for you in them; therefore mention the name of Allah over them with their one leg tied and standing on three feet; then when their flanks have fallen, eat from it yourself and feed the one who patiently awaits, and the beggar; this is how We have given them in your control, so that you be grateful."},"2632":{"id":2632,"surah":22,"ayah":37,"verse":"Never does their flesh nor their blood reach Allah, but your piety successfully reaches Him; this is how We have given them in your control so that you may speak His Greatness for guiding you; and O dear Prophet (Mohammed \u2013 peace and blessings be upon him) give glad tidings to the virtuous."},"2633":{"id":2633,"surah":22,"ayah":38,"verse":"Indeed Allah repels the afflictions of the Muslims; indeed Allah does not like any extremely disloyal ingrate."},"2634":{"id":2634,"surah":22,"ayah":39,"verse":"Permission is granted to those against whom the disbelievers wage war, as they are being wronged; and indeed Allah is Able to assist them."},"2635":{"id":2635,"surah":22,"ayah":40,"verse":"Those who were unjustly expelled from their homes just because they said, \u201cAllah is Our Lord\u201d; and had Allah not repelled some men by means of other men, the abbeys, churches, synagogues and mosques \u2013 in which the name of Allah is profusely mentioned \u2013 would definitely be demolished; and indeed Allah will assist the one who helps His religion; indeed surely Allah is Almighty, Dominant."},"2636":{"id":2636,"surah":22,"ayah":41,"verse":"The people who, if We give them control in the land, would keep the prayer established and pay charity and enjoin virtue and forbid from evil; and for Allah only is the result of all works."},"2637":{"id":2637,"surah":22,"ayah":42,"verse":"If they belie you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), then indeed the people of Nooh, and the tribes of A\u2019ad and Thamud have belied before them."},"2638":{"id":2638,"surah":22,"ayah":43,"verse":"And (so did) the people of Ibrahim and the people of Lut."},"2639":{"id":2639,"surah":22,"ayah":44,"verse":"And the people of Madyan; and Moosa was belied, so I gave the disbelievers respite and then seized them, so how (dreadful) was My punishment!"},"2640":{"id":2640,"surah":22,"ayah":45,"verse":"And many a township did We destroy, for they were oppressors \u2013 so they now lie flat on their roofs, and many a well lying useless and many a palace in ruins."},"2641":{"id":2641,"surah":22,"ayah":46,"verse":"So have they not travelled in the land, to have hearts with which to understand and ears to hear with? So it is not the eyes that are blind, but it is the hearts in the bosoms, that are blind."},"2642":{"id":2642,"surah":22,"ayah":47,"verse":"And they ask you for the punishment \u2013 they are impatient \u2013 and Allah will not make His promise untrue; and indeed a single day before Allah is like a thousand years in your calculation."},"2643":{"id":2643,"surah":22,"ayah":48,"verse":"And to many a township did I give respite although they were oppressors; then I seized them; and towards Me is the return."},"2644":{"id":2644,"surah":22,"ayah":49,"verse":"Proclaim, \u201cO mankind, I am for you only a clear Herald of Warning.\u201d"},"2645":{"id":2645,"surah":22,"ayah":50,"verse":"So those who believed and did good deeds, for them are forgiveness, and an excellent sustenance."},"2646":{"id":2646,"surah":22,"ayah":51,"verse":"And those who strive in Our signs with the intention of disputing, are the people of fire."},"2647":{"id":2647,"surah":22,"ayah":52,"verse":"And all the Noble Messengers or Prophets whom We sent before you \u2013 it occurred with all of them \u2013 that whenever they recited (the message) Satan included a bit (from his own speech) in their recitation to the people; so Allah obliterates what Satan includes and then Allah fortifies His verses; and Allah is All Knowing, Wise."},"2648":{"id":2648,"surah":22,"ayah":53,"verse":"So that He may make what the devil includes a trial for those in whose hearts is a disease, and those whose hearts are hardened; indeed the unjust are extremely quarrelsome."},"2649":{"id":2649,"surah":22,"ayah":54,"verse":"And so that the people given the knowledge may know that it is the truth from your Lord, in order that they may accept faith in it, therefore their hearts may humble before Him; and indeed Allah will guide the believers on the Straight Path."},"2650":{"id":2650,"surah":22,"ayah":55,"verse":"And the disbelievers will always be in doubt of it, to the extent that the Last Day will suddenly come upon them, or will come upon them the punishment of a day the result of which is not at all good for them."},"2651":{"id":2651,"surah":22,"ayah":56,"verse":"For Allah only is the kingship on that day; He will judge between them; so those who believed and did good deeds are in Gardens of content."},"2652":{"id":2652,"surah":22,"ayah":57,"verse":"And those who disbelieved and denied Our signs \u2013 for them will be a disgraceful punishment."},"2653":{"id":2653,"surah":22,"ayah":58,"verse":"And those who left their homes and belongings in Allah's cause and were then killed or died \u2013 Allah will therefore indeed provide for them an excellent sustenance; and indeed the sustenance bestowed by Allah is the best."},"2654":{"id":2654,"surah":22,"ayah":59,"verse":"He will certainly admit them to a place they will love; and indeed Allah is All Knowing, Most Forbearing."},"2655":{"id":2655,"surah":22,"ayah":60,"verse":"So it is; and whoever retaliates similarly to the affliction he was made to suffer, and then he is wronged again \u2013 so Allah will definitely assist him; indeed Allah is Oft Pardoning, Oft Forgiving."},"2656":{"id":2656,"surah":22,"ayah":61,"verse":"This is because Allah inserts the night into a part of the day and inserts the day into a part of the night, and because Allah is All Hearing, All Seeing."},"2657":{"id":2657,"surah":22,"ayah":62,"verse":"This is because Allah only is the Truth, and what they worship other than Him, is itself falsehood, and because Allah only is the Supreme, the Great."},"2658":{"id":2658,"surah":22,"ayah":63,"verse":"Did you not see that Allah sent down water from the sky, so the earth became green at morn? Indeed Allah is Pure, Aware."},"2659":{"id":2659,"surah":22,"ayah":64,"verse":"To Him only belongs all that is in the heavens and all that is in the earth; and indeed Allah only is the Perfect (Not Needing Anything), the Most Praiseworthy."},"2660":{"id":2660,"surah":22,"ayah":65,"verse":"Did you not see that Allah has given in your control all that is in the earth \u2013 and the ship that moves upon the sea by His command? And He restricts the heavens that it may not fall on to the earth except by His command; indeed Allah is Most Compassionate, Most Merciful upon mankind."},"2661":{"id":2661,"surah":22,"ayah":66,"verse":"And it is He Who gave you life, then will cause you to die, and will then revive you; indeed man is very ungrateful."},"2662":{"id":2662,"surah":22,"ayah":67,"verse":"For each nation We have made the rules of worship for them to follow \u2013 so never should they quarrel with you in this matter \u2013 and call them towards your Lord; indeed you are upon the Straight Path."},"2663":{"id":2663,"surah":22,"ayah":68,"verse":"And if they quarrel with you, say, \u201cAllah well knows your evil deeds.\u201d"},"2664":{"id":2664,"surah":22,"ayah":69,"verse":"Allah will judge between you on the Day of Resurrection concerning what you dispute."},"2665":{"id":2665,"surah":22,"ayah":70,"verse":"Did you not realise that Allah knows all that is in the heavens and in the earth? Indeed all this is in a Book; indeed this is easy for Allah."},"2666":{"id":2666,"surah":22,"ayah":71,"verse":"And they worship those instead of Allah regarding whom He has not sent down any proof, and those regarding whom they themselves do not have any knowledge; and there are no supporters for the unjust."},"2667":{"id":2667,"surah":22,"ayah":72,"verse":"And when Our clear verses are recited to them, you will see traces of anger in the faces of the disbelievers; possibly they may attack those who recite Our verses to them; say, \u201cShall I show you what is worse than your current state? That is the fire! Allah has promised it to the disbelievers; and what a wretched place to return!\u201d"},"2668":{"id":2668,"surah":22,"ayah":73,"verse":"O people, an example is being illustrated therefore listen to it attentively; \u201cThose whom you worship besides Allah can never create a fly even if they all come together for it; and if a fly took away something from them, they cannot retrieve that from it; how weak are the seeker and the sought!\u201d"},"2669":{"id":2669,"surah":22,"ayah":74,"verse":"They did not realise the importance of Allah as was His right; indeed Allah is Almighty, Dominant."},"2670":{"id":2670,"surah":22,"ayah":75,"verse":"Allah chooses the Noble Messengers from the angels, and from men; indeed Allah is All Hearing, All Seeing."},"2671":{"id":2671,"surah":22,"ayah":76,"verse":"He knows all that is before them and all that is behind them; and towards Allah is the return of all matters."},"2672":{"id":2672,"surah":22,"ayah":77,"verse":"O People who Believe, bow and prostrate yourselves, and worship your Lord, and do good deeds in the hope of attaining salvation."},"2673":{"id":2673,"surah":22,"ayah":78,"verse":"And fight in Allah's cause as is the true manner of fighting; He has preferred you and has not kept any hardship upon you in religion; the religion of your father Ibrahim; Allah has named you Muslims \u2013 in the previous Books and in this Qur\u2019an, so that the Noble Messenger be your guardian and witness, and you be witness against other people; therefore keep the prayer established and give charity, and hold fast to the rope of Allah; He is your Master; so what an excellent Master and what an excellent Supporter!"},"2674":{"id":2674,"surah":23,"ayah":1,"verse":"Successful indeed are the believers."},"2675":{"id":2675,"surah":23,"ayah":2,"verse":"Those who humbly cry in their prayers."},"2676":{"id":2676,"surah":23,"ayah":3,"verse":"And who do not incline towards indecent matters."},"2677":{"id":2677,"surah":23,"ayah":4,"verse":"And who pay the (obligatory) charity."},"2678":{"id":2678,"surah":23,"ayah":5,"verse":"And who guard their private organs."},"2679":{"id":2679,"surah":23,"ayah":6,"verse":"Except from their wives or the legal bondwomen that they possess, for then there is no blame upon them."},"2680":{"id":2680,"surah":23,"ayah":7,"verse":"So whoever desires more than these two \u2013 they are crossing the limits."},"2681":{"id":2681,"surah":23,"ayah":8,"verse":"And those who keep proper regard for their trusts and their pledges."},"2682":{"id":2682,"surah":23,"ayah":9,"verse":"And who guard their prayers."},"2683":{"id":2683,"surah":23,"ayah":10,"verse":"They are the inheritors."},"2684":{"id":2684,"surah":23,"ayah":11,"verse":"Those who will get the inheritance of Paradise; they will abide in it forever."},"2685":{"id":2685,"surah":23,"ayah":12,"verse":"Indeed We created man from a chosen soil."},"2686":{"id":2686,"surah":23,"ayah":13,"verse":"Then made him a drop of fluid in a secure shelter."},"2687":{"id":2687,"surah":23,"ayah":14,"verse":"We then turned the drop of fluid into a clot of blood, then the clot into a small lump of flesh, then the lump into bones, then covered the bones with flesh; then developed it in a different mould; therefore Most Auspicious is Allah, the Best Creator."},"2688":{"id":2688,"surah":23,"ayah":15,"verse":"Then after that, certainly all of you are to die."},"2689":{"id":2689,"surah":23,"ayah":16,"verse":"Then you will all be raised on the Day of Resurrection."},"2690":{"id":2690,"surah":23,"ayah":17,"verse":"And indeed We have created seven paths above you; and We are not unmindful of the creation."},"2691":{"id":2691,"surah":23,"ayah":18,"verse":"And We sent down water from the sky in proper measure, then stored it in the earth; and indeed We are Able to take it away!"},"2692":{"id":2692,"surah":23,"ayah":19,"verse":"So with it We produced gardens of date-palms and grapes for you, in which is abundant fruit for you and you eat therefrom."},"2693":{"id":2693,"surah":23,"ayah":20,"verse":"And created the tree that comes forth from Mount Sinai \u2013 that grows containing oil and curry for the eaters."},"2694":{"id":2694,"surah":23,"ayah":21,"verse":"And indeed in the cattle is a lesson for you; We give you to drink what is in their bellies, and there are many benefits for you in them, and in them is your food."},"2695":{"id":2695,"surah":23,"ayah":22,"verse":"And you are carried on them and on the ship."},"2696":{"id":2696,"surah":23,"ayah":23,"verse":"And indeed We sent Nooh towards his people \u2013 he therefore said, \u201cO my people! Worship Allah, you do not have any other God except Him; so do you not fear?\u201d"},"2697":{"id":2697,"surah":23,"ayah":24,"verse":"So the disbelieving chieftains of his people said, \u201cHe is just a human like you, he wishes to become your leader; and had Allah willed, He would have sent down angels; We did not hear this in the case of our forefathers.\u201d"},"2698":{"id":2698,"surah":23,"ayah":25,"verse":"\u201cHe is not but a man insane, therefore wait for some time.\u201d"},"2699":{"id":2699,"surah":23,"ayah":26,"verse":"Submitted Nooh, \u201cMy Lord! Help me as they deny me.\u201d"},"2700":{"id":2700,"surah":23,"ayah":27,"verse":"So We sent him the divine revelation that, \u201cMake the ship in front of Our sight, and by Our command \u2013 then when Our command comes and the oven overflows, embark in it two of every couple, and from your household except those upon whom the Word has been decreed; and do not speak to Me in respect of these unjust people; they will surely be drowned.\u201d"},"2701":{"id":2701,"surah":23,"ayah":28,"verse":"\u201cAnd when you and those with you have safely boarded the ship say, \u2018All praise is to Allah Who has rescued us from the unjust.\u2019\u201d"},"2702":{"id":2702,"surah":23,"ayah":29,"verse":"And pray, \u201cMy Lord! Cause me to alight at a blessed place \u2013 and You are the Best of all who bring to settle.\u201d"},"2703":{"id":2703,"surah":23,"ayah":30,"verse":"Indeed, surely in this are signs and indeed surely, We were examining."},"2704":{"id":2704,"surah":23,"ayah":31,"verse":"Then after them, We created another generation."},"2705":{"id":2705,"surah":23,"ayah":32,"verse":"So We sent among them a Noble Messenger from among them (saying), \u201cWorship Allah, you do not have any other God except Him; so do you not fear?\u201d"},"2706":{"id":2706,"surah":23,"ayah":33,"verse":"And said the leaders of his people, who disbelieved and denied the confronting of the Hereafter \u2013 and We had given them comfort in the worldly life \u2013 that, \u201cHe is nothing but a human like you, he eats from what you eat and drinks from what you drink.\u201d"},"2707":{"id":2707,"surah":23,"ayah":34,"verse":"\u201cIf you were to obey a human like yourselves, then surely you are losers!\u201d"},"2708":{"id":2708,"surah":23,"ayah":35,"verse":"\u201cDoes he promise you that when you die and turn into dust and bones, you will be raised again?\u201d"},"2709":{"id":2709,"surah":23,"ayah":36,"verse":"\u201cHow remote, (really) how remote is the promise you are given!\u201d"},"2710":{"id":2710,"surah":23,"ayah":37,"verse":"\u201cThere is nothing except our life of this world, we die and we live, and we are not to be raised.\u201d"},"2711":{"id":2711,"surah":23,"ayah":38,"verse":"\u201cHe is just a man who has fabricated a lie against Allah, and we are not going to believe him.\u201d"},"2712":{"id":2712,"surah":23,"ayah":39,"verse":"He said, \u201cMy Lord! Help me as they deny me.\u201d"},"2713":{"id":2713,"surah":23,"ayah":40,"verse":"Said Allah, \u201cThey will soon wake up at morn, regretting.\u201d"},"2714":{"id":2714,"surah":23,"ayah":41,"verse":"So the true Scream seized them \u2013 We therefore made them like rotten hay; so away with the unjust people!"},"2715":{"id":2715,"surah":23,"ayah":42,"verse":"Then after them, We created other generations."},"2716":{"id":2716,"surah":23,"ayah":43,"verse":"No nation can go before its term ends nor stay back."},"2717":{"id":2717,"surah":23,"ayah":44,"verse":"We then sent our Noble Messengers, one after another; whenever a nation\u2019s Noble Messenger came to it they denied him, We therefore united the succeeding with the old, and made them history; so far removed be the people who do not believe!"},"2718":{"id":2718,"surah":23,"ayah":45,"verse":"We then sent Moosa and his brother Haroon, with Our signs and a clear proof."},"2719":{"id":2719,"surah":23,"ayah":46,"verse":"Towards Firaun and his court members \u2013 in response they were haughty, and they were in dominance."},"2720":{"id":2720,"surah":23,"ayah":47,"verse":"They therefore said, \u201cShall we believe in two humans like ourselves, whereas their nation is servile to us?\u201d"},"2721":{"id":2721,"surah":23,"ayah":48,"verse":"So they denied them \u2013 therefore became of those who were destroyed."},"2722":{"id":2722,"surah":23,"ayah":49,"verse":"And indeed We gave Moosa the Book, that they may attain guidance."},"2723":{"id":2723,"surah":23,"ayah":50,"verse":"And We made the son of Maryam (Prophet Eisa) and his mother a sign, and We gave them shelter on a height, a place to stay and visible springs."},"2724":{"id":2724,"surah":23,"ayah":51,"verse":"\u201cO Noble Messengers, eat good clean things, and do good deeds; I know all that you do.\u201d"},"2725":{"id":2725,"surah":23,"ayah":52,"verse":"\u201cAnd indeed this religion of yours is one religion only and I am your Lord, therefore fear Me.\u201d"},"2726":{"id":2726,"surah":23,"ayah":53,"verse":"But their nations broke their tasks into pieces; every group happy with what it has."},"2727":{"id":2727,"surah":23,"ayah":54,"verse":"So leave them in their intoxication till a time."},"2728":{"id":2728,"surah":23,"ayah":55,"verse":"Do they assume that the wealth and sons which We provide them \u2013"},"2729":{"id":2729,"surah":23,"ayah":56,"verse":"Are quickly giving them goodness? In fact, they do not know."},"2730":{"id":2730,"surah":23,"ayah":57,"verse":"Indeed those who are overwhelmed due to the fear of their Lord \u2013"},"2731":{"id":2731,"surah":23,"ayah":58,"verse":"And those who believe in the signs of their Lord \u2013"},"2732":{"id":2732,"surah":23,"ayah":59,"verse":"And those who do not ascribe any partner to their Lord \u2013"},"2733":{"id":2733,"surah":23,"ayah":60,"verse":"And those who give what they give and their hearts fear for they have to return to their Lord."},"2734":{"id":2734,"surah":23,"ayah":61,"verse":"These people hasten to perform goodness, and it is they who shall reach it first."},"2735":{"id":2735,"surah":23,"ayah":62,"verse":"And We do not burden any soul beyond its capacity, and with Us is a Book* that speaks the truth, and they will not be wronged. (* The preserved tablet or the record of one\u2019s deeds.)"},"2736":{"id":2736,"surah":23,"ayah":63,"verse":"On the contrary, their hearts are in neglect of this and their works are different than those of the believers, the works that they are doing."},"2737":{"id":2737,"surah":23,"ayah":64,"verse":"To the extent that when We seized the wealthy among them with punishment, they immediately began imploring."},"2738":{"id":2738,"surah":23,"ayah":65,"verse":"\u201cDo not implore this day; you will not be helped by Us.\u201d"},"2739":{"id":2739,"surah":23,"ayah":66,"verse":"\u201cMy verses were recited to you, so you used to turn back on your heels.\u201d"},"2740":{"id":2740,"surah":23,"ayah":67,"verse":"\u201cPriding yourself in serving the Sacred Mosque; at night you utter indecent stories in it, while discarding the truth.\u201d"},"2741":{"id":2741,"surah":23,"ayah":68,"verse":"Have they not pondered the matter, or did that come to them, which did not come to their forefathers?"},"2742":{"id":2742,"surah":23,"ayah":69,"verse":"Or is it that they did not recognise their Noble Messenger, therefore they consider him alien?"},"2743":{"id":2743,"surah":23,"ayah":70,"verse":"Or they say, \u201cHe is afflicted by a demon\u201d; in fact he brought the Truth to them, and most of them dislike the Truth."},"2744":{"id":2744,"surah":23,"ayah":71,"verse":"And had the Truth followed their desires, then indeed the heavens and the earth and all those who are in them would be destroyed; in fact We brought to them a thing in which lay their repute, so they are turned away from their own repute."},"2745":{"id":2745,"surah":23,"ayah":72,"verse":"Do you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) ask any fee from them? So the reward of your Lord is the best; and He is the Best Provider of Sustenance."},"2746":{"id":2746,"surah":23,"ayah":73,"verse":"And indeed you call them to the Straight Path."},"2747":{"id":2747,"surah":23,"ayah":74,"verse":"And indeed those who do not believe in the Hereafter are deviated from the Straight Path."},"2748":{"id":2748,"surah":23,"ayah":75,"verse":"And if We have mercy upon them and remove the calamity which has befallen them, they would still stubbornly persist, wandering in their rebellion."},"2749":{"id":2749,"surah":23,"ayah":76,"verse":"And indeed We grasped them with punishment, so neither did they submit themselves to their Lord, nor do they cry humbly."},"2750":{"id":2750,"surah":23,"ayah":77,"verse":"To the extent that when We opened the gate of a severe punishment for them, they thereupon lie despairingly in it."},"2751":{"id":2751,"surah":23,"ayah":78,"verse":"And it is He Who has created ears and eyes and hearts for you; very little is the right you acknowledge."},"2752":{"id":2752,"surah":23,"ayah":79,"verse":"And it is He Who has spread you in the earth, and towards Him you are to be raised."},"2753":{"id":2753,"surah":23,"ayah":80,"verse":"And it is He Who gives life and causes death, and for Him are the alternations of night and day; so do you not have sense?"},"2754":{"id":2754,"surah":23,"ayah":81,"verse":"On the contrary, they said the same as what the former people used to say."},"2755":{"id":2755,"surah":23,"ayah":82,"verse":"They say, \u201cWill we, when we die and turn into dust and bones, be raised again?\u201d"},"2756":{"id":2756,"surah":23,"ayah":83,"verse":"\u201cIndeed this promise was given to us and before us to our forefathers \u2013 this is nothing but stories of earlier people.\u201d"},"2757":{"id":2757,"surah":23,"ayah":84,"verse":"Say O dear Prophet (Mohammed \u2013 peace and blessings be upon him), \u201cTo Whom does the earth and all that is in it belong to, if you know?\u201d"},"2758":{"id":2758,"surah":23,"ayah":85,"verse":"Thereupon they will say, \u201cTo Allah\u201d; proclaim, \u201cTherefore why do you not ponder?\u201d"},"2759":{"id":2759,"surah":23,"ayah":86,"verse":"Say, \u201cWho is the Lord of the seven heavens, and the Lord of the Tremendous Throne?\u201d"},"2760":{"id":2760,"surah":23,"ayah":87,"verse":"Thereupon they will say, \u201cSuch greatness is only that of Allah\u201d; say, \u201cSo why do you not fear?\u201d"},"2761":{"id":2761,"surah":23,"ayah":88,"verse":"Say, \u201cIn Whose hand is the control over all things, and He provides refuge, and none can provide refuge against Him, if you know?\u201d"},"2762":{"id":2762,"surah":23,"ayah":89,"verse":"Thereupon they will say, \u201cSuch greatness is only that of Allah\u201d; say, \u201cThen by what magic are you deceived?\u201d"},"2763":{"id":2763,"surah":23,"ayah":90,"verse":"In fact We brought the Truth to them, and indeed they are liars."},"2764":{"id":2764,"surah":23,"ayah":91,"verse":"Allah has not chosen any child, nor any other God along with Him \u2013 were it so, each God would have taken away its creation, and each one would certainly wish superiority over the other; Purity is to Allah above all the matters they fabricate."},"2765":{"id":2765,"surah":23,"ayah":92,"verse":"The All Knowing, of every hidden and the visible \u2013 so Supremacy is to Him over their ascribing of partners (to Him)."},"2766":{"id":2766,"surah":23,"ayah":93,"verse":"Pray, \u201cMy Lord! If You show me the promise they are given,\u201d"},"2767":{"id":2767,"surah":23,"ayah":94,"verse":"\u201cTherefore my Lord, do not group me with the unjust.\u201d"},"2768":{"id":2768,"surah":23,"ayah":95,"verse":"And indeed We are Able to show you the promise they are given."},"2769":{"id":2769,"surah":23,"ayah":96,"verse":"Repel evil with the best deeds; We well know the matters that they fabricate."},"2770":{"id":2770,"surah":23,"ayah":97,"verse":"And submit, \u201cMy Lord! I seek Your refuge from the instigation of the devils.\u201d"},"2771":{"id":2771,"surah":23,"ayah":98,"verse":"\u201cAnd my Lord, (I seek) Your refuge from their coming to me.\u201d"},"2772":{"id":2772,"surah":23,"ayah":99,"verse":"Until, when death comes to one* of them, he says, \u201cO my Lord, send me back!\u201d (The disbelievers)"},"2773":{"id":2773,"surah":23,"ayah":100,"verse":"\u201cPerhaps I may do some good deeds in what I have left behind\u201d; this is just a word that he utters from his mouth; and confronting them is a barrier until the day in which they will be raised."},"2774":{"id":2774,"surah":23,"ayah":101,"verse":"So when the Trumpet is blown \u2013 so there will neither be any relationship among them* that day, nor will they ask about one another. (The disbelievers)"},"2775":{"id":2775,"surah":23,"ayah":102,"verse":"Therefore the ones whose scales prove heavy \u2013 they are the successful."},"2776":{"id":2776,"surah":23,"ayah":103,"verse":"And the ones whose scales prove light \u2013 it is they who put their lives into ruin, remaining in hell forever."},"2777":{"id":2777,"surah":23,"ayah":104,"verse":"The fire shall scorch their faces, and they will remain dejected in it."},"2778":{"id":2778,"surah":23,"ayah":105,"verse":"\u201cWere not My verses recited to you, so you used to deny them?\u201d"},"2779":{"id":2779,"surah":23,"ayah":106,"verse":"They will say, \u201cOur Lord! Our ill-fate overcame us, and we were the astray people.\u201d"},"2780":{"id":2780,"surah":23,"ayah":107,"verse":"\u201cOur Lord, remove us from hell \u2013 then if we do the same, we are the unjust.\u201d"},"2781":{"id":2781,"surah":23,"ayah":108,"verse":"Allah will say, \u201cRemain rebuked in it, and do not speak to Me.\u201d"},"2782":{"id":2782,"surah":23,"ayah":109,"verse":"\"Indeed there was a group among My bondmen who said, 'Our Lord! We have accepted faith, therefore forgive us and have mercy on us, and You are the Best Among The Merciful.' \u201d"},"2783":{"id":2783,"surah":23,"ayah":110,"verse":"\u201cSo you took them for a mockery until your mocking at them made you forget My remembrance, and you used to laugh at them!\u201d"},"2784":{"id":2784,"surah":23,"ayah":111,"verse":"\u201cIndeed this day I have rewarded them for their endurance, so that it is they who are the successful.\u201d"},"2785":{"id":2785,"surah":23,"ayah":112,"verse":"He will say, \u201cHow long did you stay on earth, counting by the number of years?"},"2786":{"id":2786,"surah":23,"ayah":113,"verse":"They will say, \u201cWe stayed a day or part of a day, so ask those who keep count.\u201d"},"2787":{"id":2787,"surah":23,"ayah":114,"verse":"He will say, \u201cYou stayed but only a little, if you knew.\u201d"},"2788":{"id":2788,"surah":23,"ayah":115,"verse":"So do you think that We have created you needlessly, and that you do not have to return to Us?"},"2789":{"id":2789,"surah":23,"ayah":116,"verse":"So Most Supreme is Allah, the True King; there is no God except Him; Lord of the Throne of Honour."},"2790":{"id":2790,"surah":23,"ayah":117,"verse":"And the one who worships any other God along with Allah \u2013 of which he does not have any proof \u2013 his account is with his Lord; indeed there is no salvation for the disbelievers."},"2791":{"id":2791,"surah":23,"ayah":118,"verse":"And (O dear Prophet Mohammed \u2013 peace and blessings be upon him) say, \u201cMy Lord! Forgive and have mercy, and You are the Best of all the Merciful."},"2792":{"id":2792,"surah":24,"ayah":1,"verse":"This is a chapter which We have sent down and decreed its commands, and in which We have sent down clear verses, in order that you may ponder."},"2793":{"id":2793,"surah":24,"ayah":2,"verse":"The adulteress and the adulterer \u2013 punish each one of them with a hundred lashes; and may you not have pity on them in the religion to Allah, if you believe in Allah and the Last Day; and a group of believers must witness their punishment."},"2794":{"id":2794,"surah":24,"ayah":3,"verse":"The adulterer shall not marry except an adulteress or a polytheist woman, and none shall marry an adulteress except an adulterer or a polytheist; and this is forbidden for the believers."},"2795":{"id":2795,"surah":24,"ayah":4,"verse":"And those who accuse chaste women and do not bring four witnesses to testify \u2013 punish them with eighty lashes and do not ever accept their testimony; and it is they who are the wicked."},"2796":{"id":2796,"surah":24,"ayah":5,"verse":"Except those who repent after this and reform themselves; so indeed Allah is Oft Forgiving, Most Merciful."},"2797":{"id":2797,"surah":24,"ayah":6,"verse":"And those who accuse their wives and do not have witnesses except their own statements \u2013 for such the testimony is that he bear the testimony four times by the name of Allah that he is truthful."},"2798":{"id":2798,"surah":24,"ayah":7,"verse":"And the fifth time, that the curse of Allah be upon him if he is a liar."},"2799":{"id":2799,"surah":24,"ayah":8,"verse":"And the punishment shall be averted from the woman if she bears the testimony four times by the name of Allah, that the man is a liar."},"2800":{"id":2800,"surah":24,"ayah":9,"verse":"And the fifth time, that the wrath of Allah be upon her if the man is truthful."},"2801":{"id":2801,"surah":24,"ayah":10,"verse":"And were it not for Allah\u2019s munificence and His mercy upon you and that Allah is the Acceptor of Repentance, the Wise \u2013 He would then have unveiled you."},"2802":{"id":2802,"surah":24,"ayah":11,"verse":"Indeed those who have brought this great accusation are a group from among you; do not consider it bad for you; on the contrary, it is good for you; for each man among them is the sin that he has earned; and for the one among them who played the greatest part in it \u2013 for him is a terrible punishment."},"2803":{"id":2803,"surah":24,"ayah":12,"verse":"Why was it not that the believing men and women, when you heard it, thought good of their own people, and had said, \u201cThis is a clear accusation\u201d?"},"2804":{"id":2804,"surah":24,"ayah":13,"verse":"Why did they not bring four witnesses upon it? Since they did not bring witnesses, they themselves are liars before Allah."},"2805":{"id":2805,"surah":24,"ayah":14,"verse":"And were it not for Allah\u2019s munificence and His mercy upon you in the world and in the Hereafter, a terrible punishment would have reached you for the discussions you fell into."},"2806":{"id":2806,"surah":24,"ayah":15,"verse":"When you rumoured with your tongues after hearing such matters, and uttered with your mouths about which you had no knowledge, and you considered it light; and that, in the sight of Allah, is very great."},"2807":{"id":2807,"surah":24,"ayah":16,"verse":"And why was it not that, when you heard it, you would have said, \u201cIt does not befit us to speak regarding this; Purity is to You, O Allah \u2013 this is a great accusation.\u201d"},"2808":{"id":2808,"surah":24,"ayah":17,"verse":"Allah advises you never to speak like this again, if you have faith."},"2809":{"id":2809,"surah":24,"ayah":18,"verse":"And Allah clearly explains the verses for you; and Allah is All Knowing, Wise."},"2810":{"id":2810,"surah":24,"ayah":19,"verse":"Indeed those who wish that slander should spread among the Muslims \u2013 for them is a painful punishment in this world and in the Hereafter; and Allah knows, and you do not know."},"2811":{"id":2811,"surah":24,"ayah":20,"verse":"And were it not for Allah\u2019s munificence and His mercy upon you, and that Allah is Forgiving, Most Merciful \u2013 you would have tasted its outcome."},"2812":{"id":2812,"surah":24,"ayah":21,"verse":"O People who Believe! Do not follow the footsteps of the devil; and whoever follows the footsteps of the devil \u2013 so he will only bid the indecent and the evil; and were it not for Allah\u2019s munificence and His mercy upon you, none of you would ever become pure \u2013 but Allah purifies whomever He wills; and Allah is All Hearing, All Knowing."},"2813":{"id":2813,"surah":24,"ayah":22,"verse":"And may not those who possess superiority (in wealth) among you and possess capacity, swear not to give to the relatives and to the needy, and to immigrants in Allah's cause; and they should forgive and forbear; do you not like that Allah may forgive you? And Allah is Oft Forgiving, Most Merciful."},"2814":{"id":2814,"surah":24,"ayah":23,"verse":"Indeed those who accuse the innocent virtuous, believing women \u2013 upon them is a curse in this world and in the Hereafter; and for them is a terrible punishment."},"2815":{"id":2815,"surah":24,"ayah":24,"verse":"On the day when their tongues and their hands and their feet will testify against them, regarding what they used to do."},"2816":{"id":2816,"surah":24,"ayah":25,"verse":"On that day Allah will give them their true punishment, and they will know that Allah only is the Clear Truth."},"2817":{"id":2817,"surah":24,"ayah":26,"verse":"Vile women for vile men, and vile men for vile women; and virtuous women for virtuous men, and virtuous men for virtuous women; such are innocent of what these people say; for them are forgiveness, and an honourable sustenance."},"2818":{"id":2818,"surah":24,"ayah":27,"verse":"O People who Believe! Do not enter the houses except your own until you obtain permission and have conveyed peace upon its inhabitants; this is better for you, in order that you may ponder."},"2819":{"id":2819,"surah":24,"ayah":28,"verse":"And if you do not find anyone in them, even then do not enter without the permission of their owners; and if it is said to you, \u201cGo away\u201d then go away \u2013 this is much purer for you; and Allah knows your deeds."},"2820":{"id":2820,"surah":24,"ayah":29,"verse":"There is no sin upon you to enter houses not made especially for someone\u2019s habitation, and you have permission for its use; and Allah knows what you disclose and what you hide."},"2821":{"id":2821,"surah":24,"ayah":30,"verse":"Command the Muslim men to keep their gaze low and to protect their private organs; that is much purer for them; indeed Allah is Aware of their deeds."},"2822":{"id":2822,"surah":24,"ayah":31,"verse":"And command the Muslim women to keep their gaze low and to protect their chastity, and not to reveal their adornment except what is apparent, and to keep the cover wrapped over their bosoms; and not to reveal their adornment except to their own husbands or fathers or husbands\u2019 fathers, or their sons or their husbands\u2019 sons, or their brothers or their brothers\u2019 sons or sisters\u2019 sons, or women of their religion, or the bondwomen they possess, or male servants provided they do not have manliness, or such children who do not know of women\u2019s nakedness, and not to stamp their feet on the ground in order that their hidden adornment be known; and O Muslims, all of you turn in repentance together towards Allah, in the hope of attaining success. (It is incumbent upon women to cover themselves properly.)"},"2823":{"id":2823,"surah":24,"ayah":32,"verse":"And enjoin in marriage those among you who are not married, and your deserving slaves and bondwomen; if they are poor, Allah will make them wealthy by His munificence; and Allah is Most Capable, All Knowing."},"2824":{"id":2824,"surah":24,"ayah":33,"verse":"And those who do not have the means to get married must keep chaste till Allah provides them the resources by His munificence; and the bondwomen in your possession who, in order to earn something, seek a letter of freedom from you \u2013 then write it for them if you consider some goodness in them; and help them in their cause with Allah\u2019s wealth which He has bestowed upon you; and do not force your bondwomen into the dirty profession, while they wish to save themselves, in order to earn some riches of the worldly life; and if one forces them then indeed Allah, upon their remaining compelled, is Oft Forgiving, Most Merciful."},"2825":{"id":2825,"surah":24,"ayah":34,"verse":"And indeed We have sent down towards you clear verses, and some account of those who preceded you, and advice for the pious."},"2826":{"id":2826,"surah":24,"ayah":35,"verse":"Allah is the Light of the heavens and the earth; the example of His light is like a niche in which is a lamp; the lamp is in a glass; the glass is as if it were a star shining like a pearl, kindled by the blessed olive tree, neither of the east nor of the west \u2013 it is close that the oil itself get ablaze although the fire does not touch it; light upon light; Allah guides towards His light whomever He wills; and Allah illustrates examples for mankind; and Allah knows everything. (The Holy Prophet is a light from Allah)"},"2827":{"id":2827,"surah":24,"ayah":36,"verse":"In the houses (mosques) which Allah has commanded to erect and in which His name is taken \u2013 praising Allah in them at morn and evening,"},"2828":{"id":2828,"surah":24,"ayah":37,"verse":"(Are) Those men, whom neither any bargain nor any trade distracts from the Remembrance of Allah and from establishing the prayer and from paying the charity \u2013 they fear the day when the hearts and the eyes will be overturned."},"2829":{"id":2829,"surah":24,"ayah":38,"verse":"In order that Allah may reward them for their best deeds and by His munificence, bestow upon them an increased reward; and Allah gives sustenance to whomever He wills, without account."},"2830":{"id":2830,"surah":24,"ayah":39,"verse":"And those who disbelieved \u2013 their deeds are like a mirage in the wasteland, so the thirsty may believe it to be water; to the extent that when he came close to it, he found it is nothing and found Allah close to him, so He filled his account; and Allah is Swift At Taking Account."},"2831":{"id":2831,"surah":24,"ayah":40,"verse":"Or like realms of darkness on a deep sea, which is covered by a wave, the wave covered by another wave, and above it is a cloud; layers of darkness upon darkness; if he removes his hand it does not seem visible; and the one to whom Allah does not provide light \u2013 there is no light for him anywhere."},"2832":{"id":2832,"surah":24,"ayah":41,"verse":"Have you not seen that all those who are in the heavens and the earth praise Allah, and the birds with their wings spread (also praise Him)? Each one has learnt its prayers and its words of praise; and Allah knows their deeds."},"2833":{"id":2833,"surah":24,"ayah":42,"verse":"And for Allah only is the kingship of the heavens and the earth; and towards Allah is the return."},"2834":{"id":2834,"surah":24,"ayah":43,"verse":"Have you not seen that Allah slowly transports the clouds, then gathers them together, then heaps them together, so you see the rain coming forth from between them? And from the sky He sends down hail from the mountains of ice, then sends them upon whomever He wills, and averts them from whomever He wills; it is close that the flash of its lightning take away the eyesight."},"2835":{"id":2835,"surah":24,"ayah":44,"verse":"Allah alternates the day and the night; indeed in this is a lesson for those who can perceive."},"2836":{"id":2836,"surah":24,"ayah":45,"verse":"And Allah created every animal on earth, from water; so among them is a kind that moves upon its belly; and among them is a kind that moves upon two legs; and among them a kind that moves upon four legs; Allah creates whatever He wills; indeed Allah is Able to do all things."},"2837":{"id":2837,"surah":24,"ayah":46,"verse":"And We indeed sent down clear verses; and Allah may guide whomever He wills to the Straight Path."},"2838":{"id":2838,"surah":24,"ayah":47,"verse":"And they say, \u201cWe have accepted faith in Allah and His Noble Messenger, and we obey\u201d; then after that a group among them turns away; and they are not Muslims."},"2839":{"id":2839,"surah":24,"ayah":48,"verse":"And when called towards Allah and His Noble Messenger in order that the Noble Messenger may judge between them, thereupon a group among them turns away."},"2840":{"id":2840,"surah":24,"ayah":49,"verse":"And if the judgement is in their favour, they come towards it willingly."},"2841":{"id":2841,"surah":24,"ayah":50,"verse":"Are their hearts diseased, or do they have doubts, or do they fear that Allah and His Noble Messenger will oppress them? In fact they themselves are the unjust."},"2842":{"id":2842,"surah":24,"ayah":51,"verse":"The saying of the Muslims when they are called towards Allah and His Noble Messenger in order that the Noble Messenger judge between them is that they submit, \u201cWe hear and we obey\u201d; and it is they who have attained the goal."},"2843":{"id":2843,"surah":24,"ayah":52,"verse":"And whoever obeys the command of Allah and His Noble Messenger, and fears Allah, and practices piety \u2013 so it is they who are the successful."},"2844":{"id":2844,"surah":24,"ayah":53,"verse":"And they swore by Allah most vehemently in their oaths that if you order them, they will go forth to holy war; say, \u201cDo not swear; obedience according to the law is required; indeed Allah knows what you do.\u201d"},"2845":{"id":2845,"surah":24,"ayah":54,"verse":"Proclaim, \u201cObey Allah and obey the Noble Messenger; so if you turn away, then the Noble Messenger is bound only for what is obligatory upon him, and upon you is the duty placed upon you\u201d; and if you obey the Noble Messenger, you will attain guidance; and the Noble Messenger is not liable except to plainly convey."},"2846":{"id":2846,"surah":24,"ayah":55,"verse":"Allah has promised those among you who believe and do good deeds, that He will certainly give them the Caliphate in the earth the way He gave to those before them; and that He will certainly establish for them their religion which He has chosen for them, and will turn their prior fear into peace; they must worship Me and not ascribe anything as a partner to Me; and whoever is ungrateful after this \u2013 it is they who are the disobedient."},"2847":{"id":2847,"surah":24,"ayah":56,"verse":"And keep the prayer established and pay the obligatory charity and obey the Noble Messenger, in the hope of attaining mercy."},"2848":{"id":2848,"surah":24,"ayah":57,"verse":"Do not ever think that the disbelievers can escape from Our control in the land; and their destination is the fire; and indeed what a wretched outcome!"},"2849":{"id":2849,"surah":24,"ayah":58,"verse":"O People who Believe! The slaves you possess and those among you who have not attained adulthood, must seek your permission on three occasions; before the dawn prayer, and when you remove your garments in the afternoon, and after the night prayer; these three times are of privacy for you; other than these three times, it is no sin for you or for them; moving about around you and among each other; and this is how Allah explains the verses for you; and Allah is All Knowing, Wise."},"2850":{"id":2850,"surah":24,"ayah":59,"verse":"And when the boys among you reach adulthood, then they too must seek permission like those before them had sought; this is how Allah explains His verses for you; and Allah is All Knowing, Wise."},"2851":{"id":2851,"surah":24,"ayah":60,"verse":"And the old women residing in houses who do not have the desire of marriage \u2013 it is no sin upon them if they discard their upper coverings provided they do not display their adornment; and avoiding even this is better for them; and Allah is All Hearing, All Knowing."},"2852":{"id":2852,"surah":24,"ayah":61,"verse":"There is no restriction upon the blind nor any restraint upon the lame nor any constraint upon the sick nor on any among you if you eat from your houses, or the houses of your fathers, or the houses of your mothers, or the houses of your brothers, or the houses of your sisters, or the houses of your fathers\u2019 brothers, or the houses of your fathers\u2019 sisters, or the houses of your mothers\u2019 brothers, or the houses of your mothers\u2019 sisters, or from the houses you are entrusted the keys of, or from the house of a friend \u2013 there is no blame upon you if you eat together or apart; therefore when you enter the houses, say greetings to your people \u2013 the excellent prayer at the time of meeting, from Allah, blessed and pure; this is how Allah explains the verses to you in order that you may understand."},"2853":{"id":2853,"surah":24,"ayah":62,"verse":"The believers are only those who accept faith in Allah and His Noble Messenger and when they are present with the Noble Messenger upon being gathered for some task, do not go away until they have taken his permission; indeed those who seek your permission, are those who believe in Allah and His Noble Messenger; so if they seek your permission for some affair of theirs, give the permission to whomever you wish among them, and seek Allah\u2019s forgiveness for them; indeed Allah is Oft Forgiving, Most Merciful."},"2854":{"id":2854,"surah":24,"ayah":63,"verse":"Do not presume among yourselves the calling of the Noble Messenger equal to your calling one another; Allah knows those among you who sneak away by some pretext; so those who go against the orders of the Noble Messenger must fear that a calamity may strike them or a painful punishment befall them. (To honour the Holy Prophet \u2013 peace and blessings be upon him \u2013 is part of faith. To disrespect him is blasphemy.)"},"2855":{"id":2855,"surah":24,"ayah":64,"verse":"Pay heed! Indeed to Allah only belong whatsoever is in the heavens and in the earth; He knows the condition you are in; and on the Day when they are returned to Him, He will then inform them of whatever they did; and Allah knows all things."},"2856":{"id":2856,"surah":25,"ayah":1,"verse":"Most Auspicious is He Who has sent down the Furqan (the Criterion \u2013 the Holy Qur\u2019an) upon His chosen bondman for him to be a Herald of Warning to the entire world. (Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is the Prophet towards all mankind.)"},"2857":{"id":2857,"surah":25,"ayah":2,"verse":"The One to Whom belongs the kingship of the heavens and of the earth, and has not chosen a child, and has no partner in His kingship, and He has created everything and kept it in proper measure."},"2858":{"id":2858,"surah":25,"ayah":3,"verse":"And people have appointed other Gods besides Him, those who do not create anything but are themselves created, and those who do not have the power to harm or benefit themselves, and who do not have any authority over death or life nor over resurrection."},"2859":{"id":2859,"surah":25,"ayah":4,"verse":"And the disbelievers said, \u201cThis is nothing but a slur he has fabricated and others have helped him upon it\u201d; so indeed the disbelievers have stooped to injustice and lie."},"2860":{"id":2860,"surah":25,"ayah":5,"verse":"And they said, \u201cThese are stories of the former people, which he has written down, so they are read to him morning and evening.\u201d"},"2861":{"id":2861,"surah":25,"ayah":6,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIt is sent down by Him Who knows all the secrets of the heavens and the earth; indeed He Oft Forgiving, Most Merciful.\""},"2862":{"id":2862,"surah":25,"ayah":7,"verse":"And they said, \u201cWhat is the matter with this (Noble) Messenger, that he eats food and walks in the markets? Why was not an angel sent down along with him, to give warning along with him?\u201d"},"2863":{"id":2863,"surah":25,"ayah":8,"verse":"\u201cOr that he should have obtained a hidden treasure, or should have had a garden from which to eat\u201d; and the unjust said, \u201cYou do not follow but a man under a magic spell.\u201d"},"2864":{"id":2864,"surah":25,"ayah":9,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him) see how they invent examples for you \u2013 they have therefore gone astray, unable find a road."},"2865":{"id":2865,"surah":25,"ayah":10,"verse":"Most Auspicious is He Who, if He wills, may make it much better for you \u2013 Gardens beneath which rivers flow, and may create high palaces for you."},"2866":{"id":2866,"surah":25,"ayah":11,"verse":"In fact they deny the Last Day; and We have kept prepared a blazing fire for those who deny the Last Day."},"2867":{"id":2867,"surah":25,"ayah":12,"verse":"When it will see them from afar, they will hear its raging and roaring."},"2868":{"id":2868,"surah":25,"ayah":13,"verse":"And when they are thrown into a narrow place in it, bound in chains, they will pray for death in it."},"2869":{"id":2869,"surah":25,"ayah":14,"verse":"It will be said \u201cDo not pray this day for one death \u2013 and pray for many deaths.\u201d"},"2870":{"id":2870,"surah":25,"ayah":15,"verse":"Say, \u201cIs this better or the Everlasting Gardens which are promised to those who fear? That is their reward and their outcome.\u201d"},"2871":{"id":2871,"surah":25,"ayah":16,"verse":"\u201cIn it for them is all that they may desire, abiding in it for ever; a promise incumbent upon your Lord, that is prayed for.\u201d"},"2872":{"id":2872,"surah":25,"ayah":17,"verse":"And on the day when He will assemble them and all what they worship instead of Allah \u2013 then will say to those deities, \u201cDid you mislead my bondmen or did they themselves forget the path?\u201d"},"2873":{"id":2873,"surah":25,"ayah":18,"verse":"They will submit, \u201cPurity is to You \u2013 it was not rightful for us to appoint any Master besides You; but You gave them and their forefathers usage to the extent that they forgot Your Remembrance; and they were indeed the people who destroy themselves.\u201d"},"2874":{"id":2874,"surah":25,"ayah":19,"verse":"\u201cSo the deities have belied what you state \u2013 so now you can neither avert the punishment nor help yourself; and whoever among you is unjust, We shall make him taste a terrible punishment.\u201d"},"2875":{"id":2875,"surah":25,"ayah":20,"verse":"And all the Noble Messengers We sent before you were like this \u2013 eating food and walking in the markets; and We have made some of you a test for others; so will you, O people, patiently endure? And O dear Prophet, your Lord is All Seeing."},"2876":{"id":2876,"surah":25,"ayah":21,"verse":"And those who do not expect to meet Us said, \u201cWhy were the angels not sent down to us or we been able to see our Lord?\u201d Indeed they thought too highly of themselves and they turned extremely rebellious."},"2877":{"id":2877,"surah":25,"ayah":22,"verse":"The day when they will see the angels \u2013 that day will not be of any happiness for the guilty \u2013 and they will cry, \u201cO God, erect a barrier between us and them!\u201d"},"2878":{"id":2878,"surah":25,"ayah":23,"verse":"And We turned all the deeds they had performed into scattered floating specks of dust."},"2879":{"id":2879,"surah":25,"ayah":24,"verse":"And for the People of the Garden on that day is a better destination and account, and a better place of afternoon rest."},"2880":{"id":2880,"surah":25,"ayah":25,"verse":"And the day when the sky will be split asunder with clouds and the angels will be sent down in full."},"2881":{"id":2881,"surah":25,"ayah":26,"verse":"The true kingship on that day belongs to the Most Gracious; and that is hard for the disbelievers."},"2882":{"id":2882,"surah":25,"ayah":27,"verse":"And the day when the unjust will gnaw his hands, saying, \u201cAlas, if only I had chosen a way along with the (Noble) Messenger (of Allah)!\u201d"},"2883":{"id":2883,"surah":25,"ayah":28,"verse":"\u201cWoe to me \u2013 alas, if only I had not taken that one for a friend.\u201d"},"2884":{"id":2884,"surah":25,"ayah":29,"verse":"\u201cHe indeed led me astray from the advice that had come to me\u201d; and Satan deserts man, leaving him unaided."},"2885":{"id":2885,"surah":25,"ayah":30,"verse":"And the Noble Messenger submitted, \u201cO my Lord \u2013 indeed my people took this Qur\u2019an as worthy of being abandoned.\u201d"},"2886":{"id":2886,"surah":25,"ayah":31,"verse":"And this is how We had appointed guilty people as enemies to every Prophet; and Allah is Sufficient, to guide and to aid."},"2887":{"id":2887,"surah":25,"ayah":32,"verse":"And the disbelievers say, \u201cWhy was the Qur\u2019an not sent down to him all at once?\u201d This is how We sent it in stages, in order to strengthen your heart with it and We read it slowly, in stages."},"2888":{"id":2888,"surah":25,"ayah":33,"verse":"And they will never bring you an example, but We shall bring you a true narration and better than it."},"2889":{"id":2889,"surah":25,"ayah":34,"verse":"Those who will be dragged on their faces towards hell \u2013 theirs is the worst destination and they are the most astray."},"2890":{"id":2890,"surah":25,"ayah":35,"verse":"And indeed We gave Moosa the Book and appointed with him his brother Haroon as a deputy."},"2891":{"id":2891,"surah":25,"ayah":36,"verse":"We therefore said, \u201cGo, both of you, to the people who have denied Our signs\u201d; then ruining the people, We destroyed them completely."},"2892":{"id":2892,"surah":25,"ayah":37,"verse":"And the people of Nooh \u2013 when they denied the Noble Messengers, We drowned them and made them a sign for mankind; and We have kept prepared a painful punishment for the unjust."},"2893":{"id":2893,"surah":25,"ayah":38,"verse":"And the tribes of A\u2019ad and the Thamud, and the people of the Wells, and many a generation between them."},"2894":{"id":2894,"surah":25,"ayah":39,"verse":"And We illustrated examples to each one of them; and ruining them, annihilated them all."},"2895":{"id":2895,"surah":25,"ayah":40,"verse":"And indeed they have visited the township upon which had rained a harmful rain; so were they not seeing it? In fact, they never expected to be raised again."},"2896":{"id":2896,"surah":25,"ayah":41,"verse":"And when they see you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) they take you but as a mockery, \u201cIs this the one whom Allah sent as a (Noble) Messenger?\u201d"},"2897":{"id":2897,"surah":25,"ayah":42,"verse":"\u201cPossibly he would have misled us away from our Gods had we not been firm upon them\u201d; soon they will know, when they see the punishment, who was astray from the path."},"2898":{"id":2898,"surah":25,"ayah":43,"verse":"Did you see the one who chose his own desires as his God? So will you accept the responsibility of guarding him?"},"2899":{"id":2899,"surah":25,"ayah":44,"verse":"Or do you think that most of them hear or understand something? They are not but like the cattle \u2013 in fact more astray from the path than them!"},"2900":{"id":2900,"surah":25,"ayah":45,"verse":"O dear Prophet, did you not see your Lord, how He spread the shade? And if He willed, He could have made it still; then We made the sun a proof upon it."},"2901":{"id":2901,"surah":25,"ayah":46,"verse":"We then gradually withdraw it towards Us."},"2902":{"id":2902,"surah":25,"ayah":47,"verse":"And it is He Who made the night a veil for you, and the sleep a rest, and made the day for getting up."},"2903":{"id":2903,"surah":25,"ayah":48,"verse":"And it is He Who sent the winds giving glad tidings before His mercy; and We sent down purifying water from the sky."},"2904":{"id":2904,"surah":25,"ayah":49,"verse":"In order that We may revive a dead city with it, and give it to the many beasts and men that We have created, to drink."},"2905":{"id":2905,"surah":25,"ayah":50,"verse":"And indeed We kept cycles of water among them that they may remember; so most men did not accept, except to be ungrateful."},"2906":{"id":2906,"surah":25,"ayah":51,"verse":"If We willed, We could have sent a Herald of Warning to every village."},"2907":{"id":2907,"surah":25,"ayah":52,"verse":"So do not listen to the disbelievers and, with this Qur\u2019an, fight a great war against them."},"2908":{"id":2908,"surah":25,"ayah":53,"verse":"And it is He Who caused the two joint seas to flow- one is palatable, very sweet, and the other is salty, very bitter; and kept a veil between them and a preventing barrier."},"2909":{"id":2909,"surah":25,"ayah":54,"verse":"And it is He Who created man from water, then appointed relatives and in-laws for him; and your Lord is All Able."},"2910":{"id":2910,"surah":25,"ayah":55,"verse":"And they worship such, other than Allah, which neither benefit nor hurt them; and the disbeliever helps the devil instead of his Lord."},"2911":{"id":2911,"surah":25,"ayah":56,"verse":"And We have not sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) except as a Herald of glad tidings and warnings."},"2912":{"id":2912,"surah":25,"ayah":57,"verse":"Proclaim, \u201cI do not ask any fee from you for this, except that whoever wills may take the way to his Lord.\u201d"},"2913":{"id":2913,"surah":25,"ayah":58,"verse":"And trust the Living One Who will never die, and praising Him proclaim His Purity; and He is Sufficient upon the sins of His bondmen, All Aware."},"2914":{"id":2914,"surah":25,"ayah":59,"verse":"The One Who created the heavens and the earth and all that is between them in six days, then befitting His Majesty, established Himself upon the Throne (of control); the Most Gracious \u2013 therefore ask the one who knows, concerning Him!"},"2915":{"id":2915,"surah":25,"ayah":60,"verse":"And when it is said to them, \u201cProstrate to the Most Gracious\u201d \u2013 they say, \u201cAnd what is the Most Gracious? Shall we prostrate to whatever you command us?\u201d And this command only increases the hatred in them. (Command of Prostration # 7)"},"2916":{"id":2916,"surah":25,"ayah":61,"verse":"Most Auspicious is He Who created lofty towers in the sky and placed a lamp in it, and the luminous moon."},"2917":{"id":2917,"surah":25,"ayah":62,"verse":"And it is He Who created the alternation of the night and day for one who wishes to remember or intends to give thanks."},"2918":{"id":2918,"surah":25,"ayah":63,"verse":"And the bondmen of the Most Gracious who walk upon the earth humbly, and when the ignorant address them they answer, \u201cPeace\u201d. (Good \u2013bye)"},"2919":{"id":2919,"surah":25,"ayah":64,"verse":"And who spend the night prostrating and standing, for their Lord."},"2920":{"id":2920,"surah":25,"ayah":65,"verse":"And who submit, \u201cOur Lord \u2013 avert the punishment of hell from us; indeed its punishment is a permanent neck-shackle.\u201d"},"2921":{"id":2921,"surah":25,"ayah":66,"verse":"Indeed it is a most wretched abode."},"2922":{"id":2922,"surah":25,"ayah":67,"verse":"And those who, when spending, neither exceed the limits nor act miserly, and stay in moderation between the two."},"2923":{"id":2923,"surah":25,"ayah":68,"verse":"And those who do not worship any other deity along with Allah, and do not unjustly kill any living thing which Allah has forbidden, nor commit adultery; and whoever does this will receive punishment."},"2924":{"id":2924,"surah":25,"ayah":69,"verse":"The punishment shall be increased for him on the Day of Resurrection, and he will remain in it forever, with humiliation."},"2925":{"id":2925,"surah":25,"ayah":70,"verse":"Except one who repents and accepts faith and does good deeds \u2013 so Allah will turn their evil deeds into virtues; and Allah is Oft Forgiving, Most Merciful."},"2926":{"id":2926,"surah":25,"ayah":71,"verse":"And whoever repents and does good deeds \u2013 so he has inclined towards Allah with repentance as was required."},"2927":{"id":2927,"surah":25,"ayah":72,"verse":"And those who do not give false testimony, and when they pass near some indecency, they pass by it saving their honour."},"2928":{"id":2928,"surah":25,"ayah":73,"verse":"And those who, when they are reminded of the signs of their Lord, do not fall deaf and blind upon them."},"2929":{"id":2929,"surah":25,"ayah":74,"verse":"And who submit, \u201cOur Lord, soothe our eyes with our wives and our children, and make us leaders of the pious.\u201d"},"2930":{"id":2930,"surah":25,"ayah":75,"verse":"They will be rewarded the highest position in heaven, the recompense of their patience, and they will be welcomed with honour and greetings of peace."},"2931":{"id":2931,"surah":25,"ayah":76,"verse":"They shall abide in it forever; what an excellent abode and place of stay."},"2932":{"id":2932,"surah":25,"ayah":77,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u201cYou have no value before My Lord if you do not worship Him; so you have denied \u2013 therefore the punishment that remains, will occur.\u201d"},"2933":{"id":2933,"surah":26,"ayah":1,"verse":"Ta-Seen-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever he reveals, know their precise meanings.)"},"2934":{"id":2934,"surah":26,"ayah":2,"verse":"These are verses of the clear Book."},"2935":{"id":2935,"surah":26,"ayah":3,"verse":"Possibly you may risk your life (O dear Prophet Mohammed \u2013 peace and blessings be upon him) by grieving, because they did not believe."},"2936":{"id":2936,"surah":26,"ayah":4,"verse":"If We will, We can send down on them a sign from the sky so that their leaders would remain bowed before it."},"2937":{"id":2937,"surah":26,"ayah":5,"verse":"And never does a new advice come to them from the Most Gracious, but they turn away from it."},"2938":{"id":2938,"surah":26,"ayah":6,"verse":"Indeed they have denied, so now soon coming upon them are the tidings of what they used to scoff at."},"2939":{"id":2939,"surah":26,"ayah":7,"verse":"Have they not seen the earth, that how many honourable pairs We have created in it?"},"2940":{"id":2940,"surah":26,"ayah":8,"verse":"Indeed in this is a sign; and most of them will not accept faith."},"2941":{"id":2941,"surah":26,"ayah":9,"verse":"And indeed your Lord \u2013 indeed only He is the Almighty, the Most Merciful."},"2942":{"id":2942,"surah":26,"ayah":10,"verse":"And (remember) when your Lord said to Moosa, \u201cGo to the unjust people.\u201d"},"2943":{"id":2943,"surah":26,"ayah":11,"verse":"\u201cThe nation of Firaun; will they not fear?\u201d"},"2944":{"id":2944,"surah":26,"ayah":12,"verse":"He said, \u201cMy Lord, I fear that they will deny me.\u201d"},"2945":{"id":2945,"surah":26,"ayah":13,"verse":"\u201cI feel hesitant, and my tongue does not speak fast, therefore make Haroon also a Noble Messenger.\u201d"},"2946":{"id":2946,"surah":26,"ayah":14,"verse":"\u201cAnd they have an accusation against me, so I fear that they may kill me.\u201d"},"2947":{"id":2947,"surah":26,"ayah":15,"verse":"He said, \u201cNot like this (any more); both of you go with Our signs, We are with you, All Hearing.\u201d"},"2948":{"id":2948,"surah":26,"ayah":16,"verse":"\u201cTherefore approach Firaun then proclaim, \u2018We both are Noble Messengers of the Lord Of The Creation.\u2019"},"2949":{"id":2949,"surah":26,"ayah":17,"verse":"\u2018That you let the Descendants of Israel go with us.\u2019\u201d"},"2950":{"id":2950,"surah":26,"ayah":18,"verse":"Said Firaun, \u201cDid we do not raise you amongst us, as a child? And you spent many years of your life among us!\u201d"},"2951":{"id":2951,"surah":26,"ayah":19,"verse":"\u201cAnd you committed the deed of yours that you committed, and you were ungrateful.\u201d"},"2952":{"id":2952,"surah":26,"ayah":20,"verse":"Said Moosa, \u201cI did that at a time when I was unaware of the consequences.\u201d (In anger \u2013 See verse 28:15)"},"2953":{"id":2953,"surah":26,"ayah":21,"verse":"\u201cI therefore went away from you as I feared you \u2013 so my Lord commanded me and appointed me as one of the Noble Messengers.\u201d"},"2954":{"id":2954,"surah":26,"ayah":22,"verse":"\u201cAnd is this some great reward for which you express favour upon me \u2013 that you have enslaved the Descendants of Israel?\u201d"},"2955":{"id":2955,"surah":26,"ayah":23,"verse":"Said Firaun, \u201cAnd what is the Lord Of The Creation?"},"2956":{"id":2956,"surah":26,"ayah":24,"verse":"Said Moosa, \u201cLord of the heavens and the earth and all that is between them; if you believe.\u201d"},"2957":{"id":2957,"surah":26,"ayah":25,"verse":"Said Firaun to those around him, \u201cAre you not listening with attention?\u201d"},"2958":{"id":2958,"surah":26,"ayah":26,"verse":"Said Moosa, \u201cYour Lord and the Lord of your forefathers preceding you.\u201d"},"2959":{"id":2959,"surah":26,"ayah":27,"verse":"Said Firaun, \u201cThis (Noble) Messenger of yours, who has been sent towards you, has no intelligence!\u201d"},"2960":{"id":2960,"surah":26,"ayah":28,"verse":"He said, \u201cThe Lord of the East and the West and all that is between them; if you have sense.\u201d"},"2961":{"id":2961,"surah":26,"ayah":29,"verse":"Said Firaun, \u201cIf you ascribe any one else as a God other than me, I will surely imprison you.\u201d"},"2962":{"id":2962,"surah":26,"ayah":30,"verse":"Said Moosa, \u201cEven if I bring to you something clear?\u201d"},"2963":{"id":2963,"surah":26,"ayah":31,"verse":"Said Firaun, \u201cThen bring it, if you are of the truthful.\u201d"},"2964":{"id":2964,"surah":26,"ayah":32,"verse":"So Moosa put down his staff and it became a visible serpent."},"2965":{"id":2965,"surah":26,"ayah":33,"verse":"And he drew forth his hand, thereupon it shone bright before the beholders."},"2966":{"id":2966,"surah":26,"ayah":34,"verse":"Said Firaun to the court members around him, \u201cHe is indeed an expert magician.\u201d"},"2967":{"id":2967,"surah":26,"ayah":35,"verse":"\u201cHe wishes to expel you out of your land by his magic; so what is your advice?\u201d"},"2968":{"id":2968,"surah":26,"ayah":36,"verse":"They said, \u201cStop him and his brother, and send gatherers to the cities.\u201d"},"2969":{"id":2969,"surah":26,"ayah":37,"verse":"\u201cTo bring to you every expert great magician.\u201d"},"2970":{"id":2970,"surah":26,"ayah":38,"verse":"So the magicians were gathered at a set time on a day appointed."},"2971":{"id":2971,"surah":26,"ayah":39,"verse":"And it was said to the people, \u201cHave you (also) gathered?\u201d"},"2972":{"id":2972,"surah":26,"ayah":40,"verse":"The people said, \u201cPerhaps we may follow the magicians if they are victorious.\u201d"},"2973":{"id":2973,"surah":26,"ayah":41,"verse":"So when the magicians came, they said to Firaun, \u201cWill we get some reward if we are victorious?\u201d"},"2974":{"id":2974,"surah":26,"ayah":42,"verse":"He said, \u201cYes, and you will then become close to me.\u201d"},"2975":{"id":2975,"surah":26,"ayah":43,"verse":"Moosa said to them, \u201cCast whatever you intend to cast.\u201d"},"2976":{"id":2976,"surah":26,"ayah":44,"verse":"So they threw down their ropes and their staves and exclaimed, \u201cBy the honour of Firaun, indeed victory is ours!\u201d"},"2977":{"id":2977,"surah":26,"ayah":45,"verse":"Therefore Moosa put forth his staff \u2013 so it immediately began swallowing all their fabrications."},"2978":{"id":2978,"surah":26,"ayah":46,"verse":"The magicians therefore fell down prostrate."},"2979":{"id":2979,"surah":26,"ayah":47,"verse":"They said, \u201cWe have accepted faith in the Lord Of The Creation.\u201d"},"2980":{"id":2980,"surah":26,"ayah":48,"verse":"\u201cThe Lord of Moosa and Haroon.\u201d"},"2981":{"id":2981,"surah":26,"ayah":49,"verse":"Said Firaun, \u201cYou accepted faith in him before I permitted you! He is indeed your leader who taught you magic; so now you will come to know; I swear, I will certainly cut off your hands and your feet from alternate sides, and crucify all of you.\u201d"},"2982":{"id":2982,"surah":26,"ayah":50,"verse":"They said, \u201cNo harm (in it), we will return to our Lord.\u201d"},"2983":{"id":2983,"surah":26,"ayah":51,"verse":"\u201cWe hope that our Lord will forgive us our mistakes, as we are the first to believe.\u201d"},"2984":{"id":2984,"surah":26,"ayah":52,"verse":"And We sent the divine revelation to Moosa that, \u201cJourney along with My bondmen within the night, for you will be pursued.\u201d"},"2985":{"id":2985,"surah":26,"ayah":53,"verse":"So Firaun sent gatherers into the cities."},"2986":{"id":2986,"surah":26,"ayah":54,"verse":"They announced \u201cThese people are a small group.\u201d"},"2987":{"id":2987,"surah":26,"ayah":55,"verse":"\u201cAnd indeed they have angered all of us.\u201d"},"2988":{"id":2988,"surah":26,"ayah":56,"verse":"\u201cAnd indeed we are an alert army.\u201d"},"2989":{"id":2989,"surah":26,"ayah":57,"verse":"We therefore got them out from the gardens and water springs."},"2990":{"id":2990,"surah":26,"ayah":58,"verse":"And from treasures and nice houses."},"2991":{"id":2991,"surah":26,"ayah":59,"verse":"So it was; and We made the Descendants of Israel its inheritors."},"2992":{"id":2992,"surah":26,"ayah":60,"verse":"So the Firaun\u2019s people followed them at sunrise."},"2993":{"id":2993,"surah":26,"ayah":61,"verse":"And when the two groups saw each other, those with Moosa said, \u201cThey have caught us.\u201d"},"2994":{"id":2994,"surah":26,"ayah":62,"verse":"Said Moosa, \u201cNever! Indeed my Lord is with me, He will now show me the way.\u201d"},"2995":{"id":2995,"surah":26,"ayah":63,"verse":"So We sent the divine revelation to Moosa that, \u201cStrike the sea with your staff\u201d; thereupon the sea parted; so each part was like a huge mountain."},"2996":{"id":2996,"surah":26,"ayah":64,"verse":"And We brought the others close to that place."},"2997":{"id":2997,"surah":26,"ayah":65,"verse":"And We saved Moosa and all those with him."},"2998":{"id":2998,"surah":26,"ayah":66,"verse":"Then drowned the others."},"2999":{"id":2999,"surah":26,"ayah":67,"verse":"Indeed in this is surely a sign, and most of them were not Muslims."},"3000":{"id":3000,"surah":26,"ayah":68,"verse":"And indeed your Lord \u2013 only He is the Almighty, the Most Merciful."},"3001":{"id":3001,"surah":26,"ayah":69,"verse":"And recite to them the news of Ibrahim."},"3002":{"id":3002,"surah":26,"ayah":70,"verse":"When he said to his father and his people, \u201cWhat do you worship?\u201d"},"3003":{"id":3003,"surah":26,"ayah":71,"verse":"They said, \u201cWe worship idols, and we keep squatting in seclusion before them.\u201d"},"3004":{"id":3004,"surah":26,"ayah":72,"verse":"He said, \u201cDo they hear you when you call?\u201d"},"3005":{"id":3005,"surah":26,"ayah":73,"verse":"\u201cOr do they benefit you or harm you?\u201d"},"3006":{"id":3006,"surah":26,"ayah":74,"verse":"They said, \u201cIn fact we found our forefathers doing likewise.\u201d"},"3007":{"id":3007,"surah":26,"ayah":75,"verse":"He said, \u201cDo you see these \u2013 (the idols) whom you worship?\u201d"},"3008":{"id":3008,"surah":26,"ayah":76,"verse":"\u201cYou and your forefathers preceding you.\u201d"},"3009":{"id":3009,"surah":26,"ayah":77,"verse":"\u201cThey are all my enemies, except the Lord Of The Creation.\u201d"},"3010":{"id":3010,"surah":26,"ayah":78,"verse":"\u201cThe One Who created me, so He will guide me.\u201d"},"3011":{"id":3011,"surah":26,"ayah":79,"verse":"\u201cAnd the One Who feeds me and gives me to drink.\u201d"},"3012":{"id":3012,"surah":26,"ayah":80,"verse":"\u201cAnd when I fall ill, so it is He Who heals me.\u201d"},"3013":{"id":3013,"surah":26,"ayah":81,"verse":"\u201cAnd He will give me death, then resurrect me.\u201d"},"3014":{"id":3014,"surah":26,"ayah":82,"verse":"\u201cAnd the One Who, upon Whom I pin my hopes, will forgive me my mistakes on the Day of Judgement.\u201d"},"3015":{"id":3015,"surah":26,"ayah":83,"verse":"\u201cMy Lord, bestow me the command and join me with those who deserve your proximity.\u201d"},"3016":{"id":3016,"surah":26,"ayah":84,"verse":"\u201cAnd give me proper fame among the succeeding generations.\u201d"},"3017":{"id":3017,"surah":26,"ayah":85,"verse":"\u201cAnd make me among the inheritors of the Gardens of serenity.\u201d"},"3018":{"id":3018,"surah":26,"ayah":86,"verse":"\u201cAnd forgive my father* \u2013 he is indeed astray.\u201d (His paternal uncle)"},"3019":{"id":3019,"surah":26,"ayah":87,"verse":"\u201cAnd do not disgrace me on the day when everyone will be raised.\u201d"},"3020":{"id":3020,"surah":26,"ayah":88,"verse":"\u201cThe day when neither wealth will benefit nor will sons.\u201d"},"3021":{"id":3021,"surah":26,"ayah":89,"verse":"\u201cExcept he who presented himself before Allah, with a sound* heart.\u201d (Intact or unblemished.)"},"3022":{"id":3022,"surah":26,"ayah":90,"verse":"And Paradise will be brought close for the pious."},"3023":{"id":3023,"surah":26,"ayah":91,"verse":"And hell will be revealed for the astray."},"3024":{"id":3024,"surah":26,"ayah":92,"verse":"And it will be said to them, \u201cWhere are those whom you used to worship?\u201d"},"3025":{"id":3025,"surah":26,"ayah":93,"verse":"\u201cInstead of Allah? Will they help you or retaliate?\u201d"},"3026":{"id":3026,"surah":26,"ayah":94,"verse":"So they and all the astray were flung into hell."},"3027":{"id":3027,"surah":26,"ayah":95,"verse":"And all the armies of Iblis. (Satan)"},"3028":{"id":3028,"surah":26,"ayah":96,"verse":"And they will say, and they will be quarrelling in it:"},"3029":{"id":3029,"surah":26,"ayah":97,"verse":"\u201cBy oath of Allah, we were indeed in open error.\u201d"},"3030":{"id":3030,"surah":26,"ayah":98,"verse":"\u201cWhen we considered you equal to the Lord Of The Creation.\u201d"},"3031":{"id":3031,"surah":26,"ayah":99,"verse":"\u201cAnd none misled us but the guilty.\u201d"},"3032":{"id":3032,"surah":26,"ayah":100,"verse":"\u201cSo now we do not have any intercessors.\u201d (The believers shall have intercessors, the disbelievers none)."},"3033":{"id":3033,"surah":26,"ayah":101,"verse":"\u201cNor a caring friend.\u201d"},"3034":{"id":3034,"surah":26,"ayah":102,"verse":"\u201cSo if only were we to go back, in order to become Muslims!\u201d"},"3035":{"id":3035,"surah":26,"ayah":103,"verse":"Indeed in this is a sign; and most of them were not believers."},"3036":{"id":3036,"surah":26,"ayah":104,"verse":"And indeed your Lord \u2013 only He is the Almighty, the Most Merciful."},"3037":{"id":3037,"surah":26,"ayah":105,"verse":"The people of Nooh denied the Noble Messengers."},"3038":{"id":3038,"surah":26,"ayah":106,"verse":"When their fellowman Nooh said to them, \u201cDo you not fear?\u201d"},"3039":{"id":3039,"surah":26,"ayah":107,"verse":"\u201cI am indeed a trustworthy Noble Messenger of Allah to you.\u201d"},"3040":{"id":3040,"surah":26,"ayah":108,"verse":"\u201cTherefore fear Allah, and obey me.\u201d"},"3041":{"id":3041,"surah":26,"ayah":109,"verse":"\u201cAnd I do not ask from you any fee for it; my reward is only upon the Lord Of The Creation.\u201d"},"3042":{"id":3042,"surah":26,"ayah":110,"verse":"\u201cTherefore fear Allah, and obey me.\u201d"},"3043":{"id":3043,"surah":26,"ayah":111,"verse":"They said, \u201cShall we believe in you, whereas the abject people are with you?\u201d"},"3044":{"id":3044,"surah":26,"ayah":112,"verse":"He said, \u201cWhat do I know what their deeds are?\u201d"},"3045":{"id":3045,"surah":26,"ayah":113,"verse":"\u201cIndeed their account is only upon my Lord, if you perceive.\u201d"},"3046":{"id":3046,"surah":26,"ayah":114,"verse":"\u201cAnd I will not repel the Muslims.\u201d"},"3047":{"id":3047,"surah":26,"ayah":115,"verse":"\u201cI am not but clearly a Herald of Warning.\u201d"},"3048":{"id":3048,"surah":26,"ayah":116,"verse":"They said, \u201cO Nooh, if you do not desist you will surely be stoned.\u201d"},"3049":{"id":3049,"surah":26,"ayah":117,"verse":"He said, \u201cMy Lord, my people have denied me.\u201d"},"3050":{"id":3050,"surah":26,"ayah":118,"verse":"\u201cTherefore make a decisive judgement between me and them, and rescue me and the believers along with me.\u201d"},"3051":{"id":3051,"surah":26,"ayah":119,"verse":"So We saved him and those with him in a laden ship."},"3052":{"id":3052,"surah":26,"ayah":120,"verse":"Then after it, We drowned the rest."},"3053":{"id":3053,"surah":26,"ayah":121,"verse":"Indeed in this is a sign; and most of them were not believers."},"3054":{"id":3054,"surah":26,"ayah":122,"verse":"And indeed your Lord, only He is the Almighty, the Most Merciful."},"3055":{"id":3055,"surah":26,"ayah":123,"verse":"(The tribe of) A\u2019ad denied the Noble Messengers."},"3056":{"id":3056,"surah":26,"ayah":124,"verse":"When their fellowman Hud said to them, \u201cDo you not fear?\u201d"},"3057":{"id":3057,"surah":26,"ayah":125,"verse":"\u201cI am indeed a trustworthy Noble Messenger of Allah to you.\u201d"},"3058":{"id":3058,"surah":26,"ayah":126,"verse":"\u201cTherefore fear Allah and obey me.\u201d"},"3059":{"id":3059,"surah":26,"ayah":127,"verse":"\u201cAnd I do not ask from you any fee for it; my reward is only upon the Lord Of The Creation.\u201d"},"3060":{"id":3060,"surah":26,"ayah":128,"verse":"\u201cWhat! You build a structure on every height, to laugh at the passers-by?\u201d"},"3061":{"id":3061,"surah":26,"ayah":129,"verse":"\u201cAnd prefer strong palaces, that perhaps you may live for ever?\u201d"},"3062":{"id":3062,"surah":26,"ayah":130,"verse":"\u201cAnd when you apprehend someone, you seize him mercilessly?\u201d"},"3063":{"id":3063,"surah":26,"ayah":131,"verse":"\u201cTherefore fear Allah, and obey me.\u201d"},"3064":{"id":3064,"surah":26,"ayah":132,"verse":"\u201cFear Him Who has aided you with the things you know.\u201d"},"3065":{"id":3065,"surah":26,"ayah":133,"verse":"\u201cHe aided you with cattle and sons.\u201d"},"3066":{"id":3066,"surah":26,"ayah":134,"verse":"\u201cAnd with gardens and water-springs.\u201d"},"3067":{"id":3067,"surah":26,"ayah":135,"verse":"\u201cIndeed I fear upon you the punishment of a Great Day.\u201d"},"3068":{"id":3068,"surah":26,"ayah":136,"verse":"They said, \u201cIt is the same for us, whether you advise us or not be of the preachers.\u201d"},"3069":{"id":3069,"surah":26,"ayah":137,"verse":"\u201cThis is nothing but the tradition of former people.\u201d"},"3070":{"id":3070,"surah":26,"ayah":138,"verse":"\u201cAnd we will not be punished.\u201d"},"3071":{"id":3071,"surah":26,"ayah":139,"verse":"In response they denied him \u2013 We therefore destroyed them; indeed in this is a sign; and most of them were not believers."},"3072":{"id":3072,"surah":26,"ayah":140,"verse":"And indeed your Lord \u2013 only He is the Almighty, the Most Merciful."},"3073":{"id":3073,"surah":26,"ayah":141,"verse":"The Thamud tribe denied the Noble Messengers."},"3074":{"id":3074,"surah":26,"ayah":142,"verse":"When their fellowman Saleh said to them, \u201cDo you not fear?\u201d"},"3075":{"id":3075,"surah":26,"ayah":143,"verse":"\u201cI am indeed a trustworthy Noble Messenger of Allah to you.\u201d"},"3076":{"id":3076,"surah":26,"ayah":144,"verse":"\u201cTherefore fear Allah and obey me.\u201d"},"3077":{"id":3077,"surah":26,"ayah":145,"verse":"\u201cAnd I do not ask from you any fee for it; my reward is only upon the Lord Of The Creation.\u201d"},"3078":{"id":3078,"surah":26,"ayah":146,"verse":"\u201cWill you be left peacefully in these favours over here?\u201d"},"3079":{"id":3079,"surah":26,"ayah":147,"verse":"\u201cIn gardens and water springs?\u201d"},"3080":{"id":3080,"surah":26,"ayah":148,"verse":"\u201cAnd fields and palm-trees, with delicate tendrils?\u201d"},"3081":{"id":3081,"surah":26,"ayah":149,"verse":"\u201cAnd you carve out dwellings in the mountains, with skill?\u201d"},"3082":{"id":3082,"surah":26,"ayah":150,"verse":"\u201cTherefore fear Allah and obey me.\u201d"},"3083":{"id":3083,"surah":26,"ayah":151,"verse":"\u201cAnd do not follow those who exceed the limits.\u201d"},"3084":{"id":3084,"surah":26,"ayah":152,"verse":"\u201cThose who spread turmoil in the earth, and do no reform.\u201d"},"3085":{"id":3085,"surah":26,"ayah":153,"verse":"They said, \u201cYou are under a magic spell.\u201d"},"3086":{"id":3086,"surah":26,"ayah":154,"verse":"\u201cYou are just a human like us; therefore bring some sign if you are truthful.\u201d"},"3087":{"id":3087,"surah":26,"ayah":155,"verse":"He said, \u201cThis is the she-camel \u2013 one day shall be her turn to drink, and on the other appointed day, shall be your turn.\u201d"},"3088":{"id":3088,"surah":26,"ayah":156,"verse":"\u201cAnd do not touch her with evil intentions for the punishment of the Great Day will seize you.\u201d"},"3089":{"id":3089,"surah":26,"ayah":157,"verse":"So they hamstrung her, and in the morning could only regret."},"3090":{"id":3090,"surah":26,"ayah":158,"verse":"The punishment therefore seized them; indeed in this is a sign; and most of them were not believers."},"3091":{"id":3091,"surah":26,"ayah":159,"verse":"And indeed your Lord \u2013 only He is the Almighty, the Most Merciful."},"3092":{"id":3092,"surah":26,"ayah":160,"verse":"The people of Lut denied the Noble Messengers."},"3093":{"id":3093,"surah":26,"ayah":161,"verse":"When their fellowman Lut said to them, \u201cDo you not fear?\u201d"},"3094":{"id":3094,"surah":26,"ayah":162,"verse":"\u201cI am indeed a trustworthy Noble Messenger of Allah to you.\u201d"},"3095":{"id":3095,"surah":26,"ayah":163,"verse":"\u201cTherefore fear Allah and obey me.\u201d"},"3096":{"id":3096,"surah":26,"ayah":164,"verse":"\u201cAnd I do not ask from you any fee for it; my reward is only upon the Lord Of The Creation.\u201d"},"3097":{"id":3097,"surah":26,"ayah":165,"verse":"\u201cWhat! Among all the creatures, you commit the immoral acts with men?\u201d"},"3098":{"id":3098,"surah":26,"ayah":166,"verse":"\u201cAnd leave the wives your Lord has created for you? In fact you are people who exceed the limits.\u201d"},"3099":{"id":3099,"surah":26,"ayah":167,"verse":"They said, \u201cO Lut, if you do not desist, you will be expelled.\u201d"},"3100":{"id":3100,"surah":26,"ayah":168,"verse":"He said, \u201cI am disgusted with your works.\u201d"},"3101":{"id":3101,"surah":26,"ayah":169,"verse":"\u201cMy Lord, rescue me and my family from their deeds.\u201d"},"3102":{"id":3102,"surah":26,"ayah":170,"verse":"We therefore rescued him and his entire family."},"3103":{"id":3103,"surah":26,"ayah":171,"verse":"Except one old woman, who stayed behind."},"3104":{"id":3104,"surah":26,"ayah":172,"verse":"We then destroyed the others."},"3105":{"id":3105,"surah":26,"ayah":173,"verse":"And We showered a rain upon them; so what a wretched rain for those who were warned!"},"3106":{"id":3106,"surah":26,"ayah":174,"verse":"Indeed in this is a sign; and most of them were not Muslims."},"3107":{"id":3107,"surah":26,"ayah":175,"verse":"And indeed your Lord \u2013 only He is the Almighty, the Most Merciful."},"3108":{"id":3108,"surah":26,"ayah":176,"verse":"The People of the Woods denied the Noble Messengers."},"3109":{"id":3109,"surah":26,"ayah":177,"verse":"When Shuaib said to all of them, \u201cDo you not fear?\u201d"},"3110":{"id":3110,"surah":26,"ayah":178,"verse":"\u201cI am indeed a trustworthy Noble Messenger of Allah to you.\u201d"},"3111":{"id":3111,"surah":26,"ayah":179,"verse":"\u201cTherefore fear Allah and obey me.\u201d"},"3112":{"id":3112,"surah":26,"ayah":180,"verse":"\u201cAnd I do not ask from you any fee for it; my reward is only upon the Lord Of The Creation.\u201d"},"3113":{"id":3113,"surah":26,"ayah":181,"verse":"\u201cMeasure in full, and do not be of those who reduce.\u201d"},"3114":{"id":3114,"surah":26,"ayah":182,"verse":"\u201cAnd weigh with a proper balance.\u201d"},"3115":{"id":3115,"surah":26,"ayah":183,"verse":"\u201cAnd do not give the people their goods diminished, and do not roam the earth causing turmoil.\u201d"},"3116":{"id":3116,"surah":26,"ayah":184,"verse":"\u201cAnd fear Him Who created you and the earlier creations.\u201d"},"3117":{"id":3117,"surah":26,"ayah":185,"verse":"They said, \u201cYou are under a magic spell.\u201d"},"3118":{"id":3118,"surah":26,"ayah":186,"verse":"\u201cYou are just a human like us, and indeed we consider you a liar.\u201d"},"3119":{"id":3119,"surah":26,"ayah":187,"verse":"\u201cTherefore cause a part of the sky to fall upon us, if you are of the truthful.\u201d"},"3120":{"id":3120,"surah":26,"ayah":188,"verse":"He said, \u201cMy Lord is Well Aware of what you do.\u201d"},"3121":{"id":3121,"surah":26,"ayah":189,"verse":"In response they denied him \u2013 therefore the punishment of the day of the tent* seized them; that was indeed a punishment of a Great Day. (The clouds formed a tent and rained fire upon them)."},"3122":{"id":3122,"surah":26,"ayah":190,"verse":"Indeed in this is a sign; and most of them were not Muslims."},"3123":{"id":3123,"surah":26,"ayah":191,"verse":"And indeed your Lord \u2013 only He is the Almighty, the Most Merciful."},"3124":{"id":3124,"surah":26,"ayah":192,"verse":"And indeed this Qur\u2019an has been sent down by the Lord Of The Creation."},"3125":{"id":3125,"surah":26,"ayah":193,"verse":"The Trustworthy Spirit brought it down. (Angel Jibreel \u2013 peace be upon him.)"},"3126":{"id":3126,"surah":26,"ayah":194,"verse":"Upon your heart, for you to convey warning."},"3127":{"id":3127,"surah":26,"ayah":195,"verse":"In plain Arabic language."},"3128":{"id":3128,"surah":26,"ayah":196,"verse":"And indeed it is mentioned in the earlier Books."},"3129":{"id":3129,"surah":26,"ayah":197,"verse":"And was this not a sign for them, that the scholars of the Descendants of Israel may recognise this Prophet?"},"3130":{"id":3130,"surah":26,"ayah":198,"verse":"And had We sent it down upon a person other than an Arab,"},"3131":{"id":3131,"surah":26,"ayah":199,"verse":"In order that he read it to them, even then they would not have believed in it."},"3132":{"id":3132,"surah":26,"ayah":200,"verse":"This is how We have made (the habit of) denying embedded in the hearts of the guilty."},"3133":{"id":3133,"surah":26,"ayah":201,"verse":"They will not believe in it to the extent that they see the painful punishment."},"3134":{"id":3134,"surah":26,"ayah":202,"verse":"So it will come upon them suddenly, whilst they will be unaware."},"3135":{"id":3135,"surah":26,"ayah":203,"verse":"They will therefore say, \u201cWill we get some respite?\u201d"},"3136":{"id":3136,"surah":26,"ayah":204,"verse":"So do they wish to hasten Our punishment?"},"3137":{"id":3137,"surah":26,"ayah":205,"verse":"Therefore observe, that if We give them some comforts for some years, \u2013"},"3138":{"id":3138,"surah":26,"ayah":206,"verse":"And then the promise that is given to them, does come upon them-"},"3139":{"id":3139,"surah":26,"ayah":207,"verse":"So of what benefit will be the comforts that they were using?"},"3140":{"id":3140,"surah":26,"ayah":208,"verse":"And never did We destroy a township which did not have Heralds of warning, \u2013"},"3141":{"id":3141,"surah":26,"ayah":209,"verse":"To advise; and We never oppress."},"3142":{"id":3142,"surah":26,"ayah":210,"verse":"And this Qur\u2019an was not brought down by the devils."},"3143":{"id":3143,"surah":26,"ayah":211,"verse":"They are unworthy of it, nor can they do it."},"3144":{"id":3144,"surah":26,"ayah":212,"verse":"Indeed they have been banished from the place of hearing."},"3145":{"id":3145,"surah":26,"ayah":213,"verse":"Therefore do not worship another deity along with Allah, for you will be punished."},"3146":{"id":3146,"surah":26,"ayah":214,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him), warn your closest relatives."},"3147":{"id":3147,"surah":26,"ayah":215,"verse":"And spread your wing of mercy for the believers following you."},"3148":{"id":3148,"surah":26,"ayah":216,"verse":"So if they do not obey you, then say, \u201cIndeed I am unconcerned with what you do.\u201d"},"3149":{"id":3149,"surah":26,"ayah":217,"verse":"And rely upon (Allah) the Almighty, the Most Merciful."},"3150":{"id":3150,"surah":26,"ayah":218,"verse":"Who watches you when you stand up."},"3151":{"id":3151,"surah":26,"ayah":219,"verse":"And watches your movements among those who prostrate in prayer."},"3152":{"id":3152,"surah":26,"ayah":220,"verse":"Indeed only He is the All Hearing, the All Knowing."},"3153":{"id":3153,"surah":26,"ayah":221,"verse":"Shall I inform you upon whom do the devils descend?"},"3154":{"id":3154,"surah":26,"ayah":222,"verse":"They descend on every great accuser, extreme sinner. (The magicians)"},"3155":{"id":3155,"surah":26,"ayah":223,"verse":"The devils convey upon them what they heard, but most of them are liars."},"3156":{"id":3156,"surah":26,"ayah":224,"verse":"And the astray follow the poets."},"3157":{"id":3157,"surah":26,"ayah":225,"verse":"Did you not see that they keep wandering in every valley?"},"3158":{"id":3158,"surah":26,"ayah":226,"verse":"And they speak what they do not do?"},"3159":{"id":3159,"surah":26,"ayah":227,"verse":"Except those who believed and did good deeds, and profusely remembered Allah, and took revenge after they had been wronged*; and soon the unjust will come to know upon which side they will be overturned**. (* The Muslim poets who praise Allah and the Prophet. ** The disbelievers will be punished.)"},"3160":{"id":3160,"surah":27,"ayah":1,"verse":"Ta-Seen*; these are verses of the Qur\u2019an and the clear Book. (Alphabets of the Arabic language \u2013 Allah, and to whomever he reveals, know their precise meanings.)"},"3161":{"id":3161,"surah":27,"ayah":2,"verse":"A guidance and glad tidings for the believers."},"3162":{"id":3162,"surah":27,"ayah":3,"verse":"Those who keep the prayer established and pay the charity and are certain of the Hereafter."},"3163":{"id":3163,"surah":27,"ayah":4,"verse":"Those who do not believe in the Hereafter \u2013 We have made their deeds seem good to them, so they are astray."},"3164":{"id":3164,"surah":27,"ayah":5,"verse":"It is they for whom is the worst punishment, and they will be the greatest losers in the Hereafter."},"3165":{"id":3165,"surah":27,"ayah":6,"verse":"And indeed you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), are taught the Qur\u2019an from the Wise, the All Knowing."},"3166":{"id":3166,"surah":27,"ayah":7,"verse":"(Remember) when Moosa said to his wife, \u201cI have sighted a fire; I will soon bring its news to you, or bring for you an ember from it so that you may warm yourselves.\u201d"},"3167":{"id":3167,"surah":27,"ayah":8,"verse":"So when he reached it, it was proclaimed, \u201cBlessed is he who is in the location of the fire (Moosa) and those who are close to it (the angels); and Purity is to Allah, the Lord Of The Creation.\u201d"},"3168":{"id":3168,"surah":27,"ayah":9,"verse":"\u201cO Moosa, I am indeed, in truth, Allah the Almighty, the Wise.\u201d"},"3169":{"id":3169,"surah":27,"ayah":10,"verse":"\u201cAnd put down your staff\u201d; so when he saw it writhing like a serpent, he turned moving away without looking back; We said, \u201cO Moosa, do not fear; indeed the Noble Messengers do not fear in My presence.\u201d"},"3170":{"id":3170,"surah":27,"ayah":11,"verse":"\u201cExcept the one* who does injustice and then after evil changes it for virtue \u2013 then indeed I am Oft Forgiving, Most Merciful.\u201d (Other than the Prophets.)"},"3171":{"id":3171,"surah":27,"ayah":12,"verse":"\u201cAnd put your hand inside your armpit \u2013 it will come out shining white, not due to any illness; a sign among the nine signs towards Firaun and his people; they are indeed a lawless nation.\u201d"},"3172":{"id":3172,"surah":27,"ayah":13,"verse":"Then when Our enlightening signs came to them, they said, \u201cThis is clear magic.\u201d"},"3173":{"id":3173,"surah":27,"ayah":14,"verse":"And they denied them \u2013 whereas in their hearts they were certain of them \u2013 due to injustice and pride; therefore see what sort of fate befell the mischievous!"},"3174":{"id":3174,"surah":27,"ayah":15,"verse":"And We indeed bestowed great knowledge to Dawud and Sulaiman; and they both said, \u201cAll praise is to Allah, Who bestowed us superiority over many of His believing bondmen.\u201d"},"3175":{"id":3175,"surah":27,"ayah":16,"verse":"And Sulaiman became Dawud\u2019s heir; and he said, \u201cO people, we have indeed been taught the language of birds, and have been given from all things; this surely is a manifest favour.\u201d"},"3176":{"id":3176,"surah":27,"ayah":17,"verse":"And assembled together for Sulaiman were his armies of jinns and men, and of birds \u2013 so they had to be restricted."},"3177":{"id":3177,"surah":27,"ayah":18,"verse":"Until when they came to the valley of the ants, a she ant exclaimed, \u201cO ants, enter your houses \u2013 may not Sulaiman and his armies crush you, unknowingly.\u201d"},"3178":{"id":3178,"surah":27,"ayah":19,"verse":"He therefore smiled beamingly at her speech*, and submitted, \u201cMy Lord, bestow me guidance so that I thank you for the favour which You bestowed upon me and my parents, and so that I may perform the good deeds which please You, and by Your mercy include me among Your bondmen who are worthy of Your proximity.\u201d (Prophet Sulaiman heard the voice of the she ant from far away.)"},"3179":{"id":3179,"surah":27,"ayah":20,"verse":"And he surveyed the birds \u2013 he therefore said, \u201cWhat is to me that I do not see the Hudhud (hoopoe), or is he really absent?\u201d"},"3180":{"id":3180,"surah":27,"ayah":21,"verse":"\u201cI will indeed punish him severely or slay him, or he must bring to me some clear evidence.\u201d"},"3181":{"id":3181,"surah":27,"ayah":22,"verse":"So Hudhud did not stay absent for long, and presenting himself submitted, \u201cI have witnessed a matter that your majesty has not seen, and I have brought definite information to you from the city of Saba.\u201d"},"3182":{"id":3182,"surah":27,"ayah":23,"verse":"\u201cI have seen a woman who rules over them, and she has been given from all things, and she has a mighty throne.\u201d"},"3183":{"id":3183,"surah":27,"ayah":24,"verse":"\u201cI found her and her nation prostrating before the sun instead of Allah, and Satan has made their deeds seem good to them thereby preventing them from the Straight Path \u2013 so they do not attain guidance.\u201d"},"3184":{"id":3184,"surah":27,"ayah":25,"verse":"\u201cWhy do they not prostrate to Allah, Who brings forth the things hidden in the heavens and the earth, and knows all what you hide and all what you disclose?\u201d"},"3185":{"id":3185,"surah":27,"ayah":26,"verse":"\u201cAllah \u2013 there is no True God except Him, the Owner of the Great Throne.\u201d (Command of Prostration # 8)"},"3186":{"id":3186,"surah":27,"ayah":27,"verse":"Said Sulaiman, \u201cWe shall now see whether you spoke the truth or are among the liars.\u201d"},"3187":{"id":3187,"surah":27,"ayah":28,"verse":"\u201cGo with this letter of mine and drop it upon them \u2013 then move aside from them and see what they answer in return.\u201d"},"3188":{"id":3188,"surah":27,"ayah":29,"verse":"The woman said, \u201cO chieftains, indeed a noble letter has been dropped upon me.\u201d"},"3189":{"id":3189,"surah":27,"ayah":30,"verse":"\u201cIndeed it is from Sulaiman, and it is (begins) with \u2018Allah \u2013 beginning with the name of \u2013 the Most Gracious, the Most Merciful.\u2019\u201d"},"3190":{"id":3190,"surah":27,"ayah":31,"verse":"\u201cThat \u2018Do not wish eminence above me, and present yourselves humbly to me, with submission.\u2019\u201d"},"3191":{"id":3191,"surah":27,"ayah":32,"verse":"She said, \u201cO chieftains, advise me in this matter of mine; I do not give a final decision until you are present with me.\u201d"},"3192":{"id":3192,"surah":27,"ayah":33,"verse":"They said, \u201cWe possess great strength and are great warriors, and the decision is yours, therefore consider what you will command.\u201d"},"3193":{"id":3193,"surah":27,"ayah":34,"verse":"She said, \u201cIndeed the kings, when they enter a township, destroy it and disgrace its honourable people; and this is what they do.\u201d"},"3194":{"id":3194,"surah":27,"ayah":35,"verse":"\u201cAnd I shall send a present to them, then see what reply the envoys bring.\u201d"},"3195":{"id":3195,"surah":27,"ayah":36,"verse":"So when the envoy came to Sulaiman, he said, \u201cAre you helping me with wealth? What Allah has bestowed upon me is better than what He has given you; rather it is you who are delighted at your gift.\u201d"},"3196":{"id":3196,"surah":27,"ayah":37,"verse":"\u201cGo back to them \u2013 so we shall indeed come upon them with an army they cannot fight, and degrading them shall certainly drive them out from that city, so they will be humiliated.\u201d"},"3197":{"id":3197,"surah":27,"ayah":38,"verse":"Said Sulaiman, \u201cO court members, which one of you can bring me her throne before they come humbled in my presence?\u201d"},"3198":{"id":3198,"surah":27,"ayah":39,"verse":"An extremely evil jinn said, \u201cI will bring it in your presence before you disperse the assembly; and I am indeed strong and trustworthy upon it.\u201d"},"3199":{"id":3199,"surah":27,"ayah":40,"verse":"Said one who had knowledge of the Book, \u201cI will bring it in your majesty\u2019s presence before you bat your eyelid\u201d; then when he saw it set in his presence*, Sulaiman said, \u201cThis is of the favours of my Lord; so that He may test me whether I give thanks or am ungrateful; and whoever gives thanks only gives thanks for his own good; and whoever is ungrateful \u2013 then indeed my Lord is the Independent (Not Needing Anything), the Owner Of All Praise.\u201d (A miracle which occurred through one of Allah\u2019s friends.)"},"3200":{"id":3200,"surah":27,"ayah":41,"verse":"Said Sulaiman, \u201cDisguise her throne in front of her so that we may see whether she finds the way* or becomes of those who remain unknowing.\u201d (*Recognises her throne.)"},"3201":{"id":3201,"surah":27,"ayah":42,"verse":"Then when she came, it was said to her, \u201cIs your throne like this? She said, \u201cAs if this is it! And we came to know about this incident beforehand and submit (to you).\u201d"},"3202":{"id":3202,"surah":27,"ayah":43,"verse":"And the thing she used to worship instead of Allah prevented her; she was indeed from the disbelieving people."},"3203":{"id":3203,"surah":27,"ayah":44,"verse":"It was said to her, \u201cEnter the hall\u201d; and when she saw it she thought it was a pool and bared her shins*; said Sulaiman, \u201cThis is only a smooth hall, affixed with glass\u201d; she said, \u201cMy Lord, I have indeed wronged myself, and I now submit myself along with Sulaiman to Allah, the Lord Of The Creation.\u201d (* In order to cross it)"},"3204":{"id":3204,"surah":27,"ayah":45,"verse":"And indeed We sent to the Thamud, their fellowman Saleh that, \u201cWorship Allah\u201d, thereupon they became two parties quarrelling."},"3205":{"id":3205,"surah":27,"ayah":46,"verse":"He said, \u201cO my people, why do you hasten upon the evil before the good? Why do you not seek forgiveness from Allah? Perhaps there may be mercy upon you.\u201d"},"3206":{"id":3206,"surah":27,"ayah":47,"verse":"They said, \u201cWe consider you an evil omen, and your companions\u201d; he said, \u201cYour evil omen is with Allah \u2013 in fact you people have fallen into trial.\u201d"},"3207":{"id":3207,"surah":27,"ayah":48,"verse":"And there were nine persons in the city who used to cause turmoil in the land and did not wish reform."},"3208":{"id":3208,"surah":27,"ayah":49,"verse":"Swearing by Allah they said to one another, \u201cWe will indeed attack him and his family at night, and then say to his heir, \u2018We were not present at the time of slaying of this household, and indeed we are truthful.\u2019\u201d"},"3209":{"id":3209,"surah":27,"ayah":50,"verse":"So they devised a scheme, and We made our secret plan, and they remained neglectful."},"3210":{"id":3210,"surah":27,"ayah":51,"verse":"Therefore see what was the result of their scheming \u2013 We destroyed them and their entire nation."},"3211":{"id":3211,"surah":27,"ayah":52,"verse":"So these are their houses fallen flat, the recompense of their injustice; indeed in this is a sign for people who know."},"3212":{"id":3212,"surah":27,"ayah":53,"verse":"And We rescued those who accepted faith and used to fear."},"3213":{"id":3213,"surah":27,"ayah":54,"verse":"And (remember) Lut when he said to his people, \u201cWhat! You stoop to the shameful whereas you can see?\u201d"},"3214":{"id":3214,"surah":27,"ayah":55,"verse":"\u201cWhat! You lustfully go towards men, leaving the women?! In fact, you are an ignorant people.\u201d"},"3215":{"id":3215,"surah":27,"ayah":56,"verse":"Therefore the answer of his people was nothing except that they said, \u201cExpel the family of Lut from your township; these people wish purity!\u201d"},"3216":{"id":3216,"surah":27,"ayah":57,"verse":"We therefore rescued him and his family, except his wife; We had decreed that she is of those who will remain behind."},"3217":{"id":3217,"surah":27,"ayah":58,"verse":"And We showered a rain upon them; so what a wretched rain for those who were warned!"},"3218":{"id":3218,"surah":27,"ayah":59,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAll praise is to Allah, and peace upon His chosen bondmen; is Allah better or what you ascribe as His partners?\u201d"},"3219":{"id":3219,"surah":27,"ayah":60,"verse":"Or He Who created the heavens and the earth, and sent down water from the sky for you; so We grew delightful gardens with it; you had no strength to grow its trees; is there a God along with Allah?! In fact they are those who shun the right path."},"3220":{"id":3220,"surah":27,"ayah":61,"verse":"Or He Who made the earth for habitation, and placed rivers within it, and created mountains as anchors for it, and kept a barrier between the two seas? Is there a God along with Allah?! In fact, most of them are ignorant."},"3221":{"id":3221,"surah":27,"ayah":62,"verse":"Or He Who answers the prayer of the helpless when he invokes Him and removes the evil, and makes you inheritors of the earth? Is there a God along with Allah?! Very little do they ponder!"},"3222":{"id":3222,"surah":27,"ayah":63,"verse":"Or He Who shows you the path in the darkness of the land and the sea, and He Who sends the winds before His mercy, heralding glad tidings? Is there a God along with Allah?! Supremacy is to Allah above all that they ascribe as partners!"},"3223":{"id":3223,"surah":27,"ayah":64,"verse":"Or He Who initiates the creation, then will create it again, and He Who provides you sustenance from the skies and the earth? Is there a God along with Allah?! Proclaim, \u201cBring your proof, if you are truthful!\u201d"},"3224":{"id":3224,"surah":27,"ayah":65,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cNone in the heavens and the earth know the hidden by themselves, except Allah; and they do not know when they will be raised.\u201d"},"3225":{"id":3225,"surah":27,"ayah":66,"verse":"Has their knowledge advanced so much as to know the Hereafter? On the contrary, they are in doubt concerning it; in fact they are blind towards it."},"3226":{"id":3226,"surah":27,"ayah":67,"verse":"And the disbelievers said, \u201cWill we, when we and our forefathers have turned into dust, be removed again?\u201d"},"3227":{"id":3227,"surah":27,"ayah":68,"verse":"\u201cIndeed this promise was given to us, and before us to our forefathers \u2013 this is nothing but stories of former people.\u201d"},"3228":{"id":3228,"surah":27,"ayah":69,"verse":"\u201cProclaim, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cTravel in the land and see what sort of fate befell the guilty.\u201d"},"3229":{"id":3229,"surah":27,"ayah":70,"verse":"And do not grieve upon them, nor be distressed because of their scheming."},"3230":{"id":3230,"surah":27,"ayah":71,"verse":"And they say, \u201cWhen will this promise come, if you are truthful?\u201d"},"3231":{"id":3231,"surah":27,"ayah":72,"verse":"Say, \u201cIt could be that a part of what you are impatient for is (already) after you.\u201d"},"3232":{"id":3232,"surah":27,"ayah":73,"verse":"Indeed your Lord is Most Munificent upon mankind, but most men do not give thanks."},"3233":{"id":3233,"surah":27,"ayah":74,"verse":"And indeed your Lord knows what is hidden in their bosoms, and what they disclose."},"3234":{"id":3234,"surah":27,"ayah":75,"verse":"And all the hidden secrets of the heavens and the earth are (written) in a clear Book. (The Preserved Tablet)"},"3235":{"id":3235,"surah":27,"ayah":76,"verse":"Indeed this Qur\u2019an narrates to the Descendants of Israel most of the matters in which they differ."},"3236":{"id":3236,"surah":27,"ayah":77,"verse":"And indeed it is a guidance and a mercy for the Muslims."},"3237":{"id":3237,"surah":27,"ayah":78,"verse":"Indeed your Lord judges between them with His command; and He is the Almighty, the All Knowing."},"3238":{"id":3238,"surah":27,"ayah":79,"verse":"Therefore rely on Allah; you are indeed upon the clear Truth."},"3239":{"id":3239,"surah":27,"ayah":80,"verse":"Indeed the dead* do not listen to your call nor do the deaf* listen to your call, when they flee turning back. (The dead and deaf implies the disbelievers.)"},"3240":{"id":3240,"surah":27,"ayah":81,"verse":"And you will not guide the blind out of their error; and none listen to you except those who accept faith in Our signs, and they are Muslims."},"3241":{"id":3241,"surah":27,"ayah":82,"verse":"And when the Word (promise) appears upon them, We shall bring forth for them a beast from the earth to speak to them \u2013 because the people were not accepting faith in Our signs. (This beast will rise from the earth, when the Last Day draws near.)"},"3242":{"id":3242,"surah":27,"ayah":83,"verse":"And the Day when We will raise from every nation an army that denies Our signs \u2013 so the former groups will be held back for the latter to join them."},"3243":{"id":3243,"surah":27,"ayah":84,"verse":"Until when they all come together, He will say, \u201cDid you deny My signs whereas your knowledge had not reached it, or what were the deeds you were doing?\u201d"},"3244":{"id":3244,"surah":27,"ayah":85,"verse":"And the Word has appeared upon them because of their injustice, so now they do not speak."},"3245":{"id":3245,"surah":27,"ayah":86,"verse":"Did they not see that We created the night for them to rest in, and created the day providing sight? Indeed in this are signs for the people who have faith."},"3246":{"id":3246,"surah":27,"ayah":87,"verse":"And the Day when the Trumpet is blown \u2013 so all those who are in the heavens and the earth will be terrified, except whomever Allah wills; and everyone has come to Him, in submission."},"3247":{"id":3247,"surah":27,"ayah":88,"verse":"And you will see the mountains presuming them fixed whereas they will be moving like the clouds; this is Allah\u2019s making, Who created all things with wisdom; indeed He is Informed of what you do."},"3248":{"id":3248,"surah":27,"ayah":89,"verse":"Whoever brings a good deed \u2013 for him is a reward better than it; and they will be safe from the terror on that Day."},"3249":{"id":3249,"surah":27,"ayah":90,"verse":"And whoever brings evil \u2013 so their faces will be dipped into the fire; what reward will you get, except what you did?"},"3250":{"id":3250,"surah":27,"ayah":91,"verse":"\u201cI (Prophet Mohammed \u2013 peace and blessings be upon him) have been commanded to worship the Lord of this city, Who has deemed it sacred, and everything belongs to Him; and I have been commanded to be among the obedient.\u201d"},"3251":{"id":3251,"surah":27,"ayah":92,"verse":"\u201cAnd to recite the Qur\u2019an\u201d; so whoever attained the right path, has attained it for his own good; and whoever goes astray \u2013 so say, \u201cIndeed I am only a Herald of Warning.\u201d"},"3252":{"id":3252,"surah":27,"ayah":93,"verse":"And proclaim, \u201cAll praise is to Allah \u2013 He will soon show you His signs so you will recognise them\u201d; and (O dear Prophet Mohammed \u2013 peace and blessings be upon him) your Lord is not unaware of what you, O people, do.\u201d"},"3253":{"id":3253,"surah":28,"ayah":1,"verse":"Ta-Seen-Meem. (Alphabets of the Arabic language \u2013 Allah and to whomever he reveals, know their precise meanings.)"},"3254":{"id":3254,"surah":28,"ayah":2,"verse":"These are verses of the clear Book."},"3255":{"id":3255,"surah":28,"ayah":3,"verse":"We shall narrate to you the true tidings of Moosa and Firaun, for the people who have faith."},"3256":{"id":3256,"surah":28,"ayah":4,"verse":"Indeed Firaun had achieved dominance in the earth and made its people subservient to him \u2013 seeing a group among them weak, he used to kill their sons and spare their women; he was indeed very mischievous."},"3257":{"id":3257,"surah":28,"ayah":5,"verse":"And We willed to favour those who were weak in the earth, and to make them leaders and to make them the inheritors (of the land and wealth of Firaun\u2019s people)."},"3258":{"id":3258,"surah":28,"ayah":6,"verse":"And to give them control in the land, and to show Firaun and Haman and their armies what they fear from them."},"3259":{"id":3259,"surah":28,"ayah":7,"verse":"And We inspired the mother of Moosa that, \u201cSuckle him; then when you fear for him, cast him into the river and do not fear nor grieve; We shall indeed return him back to you and make him one of the Noble Messengers.\u201d"},"3260":{"id":3260,"surah":28,"ayah":8,"verse":"So the family of Firaun picked him up, in order that he become their enemy and a sorrow upon them; indeed Firaun and Haman and their armies were guilty."},"3261":{"id":3261,"surah":28,"ayah":9,"verse":"And Firaun\u2019s wife said, \u201cThis child is the comfort of my eyes and yours*; do not kill him; perhaps he may benefit us, or we may adopt him as our son\u201d \u2013 and they were unaware. (Had Firaun also said the same, Allah would have granted him faith.)"},"3262":{"id":3262,"surah":28,"ayah":10,"verse":"And in the morning, the heart of Moosa\u2019s mother became impatient; and she would have almost certainly given away his secret had We not strengthened her heart, so that she may have faith in Our promise."},"3263":{"id":3263,"surah":28,"ayah":11,"verse":"And she said to his sister, \u201cGo after him\u201d \u2013 she therefore observed him from far, and they were not aware."},"3264":{"id":3264,"surah":28,"ayah":12,"verse":"And We had already forbidden suckle-nurses for him, so she said, \u201cShall I show you a household that will nurse this child of yours, and they are his well-wishers?\u201d"},"3265":{"id":3265,"surah":28,"ayah":13,"verse":"So We returned him to his mother in order to soothe her eyes and not grieve, and to know that Allah\u2019s promise is true \u2013 but most people do not know."},"3266":{"id":3266,"surah":28,"ayah":14,"verse":"And when he reached his maturity and full strength, We gave him wisdom and knowledge; and this is how We reward the virtuous."},"3267":{"id":3267,"surah":28,"ayah":15,"verse":"And he entered the city when its inhabitants were sleeping unaware in the afternoon \u2013 he therefore found two men fighting; one was from Moosa\u2019s group, and the other from among his enemies; so the one who was of Moosa\u2019s group pleaded to Moosa for help against him who was of his enemies \u2013 therefore Moosa punched him thereby finishing him; he said, \u201cThis act was from the devil*; indeed he is an open enemy, a misleader.\u201d (* The act of oppressing the man from Bani Israel)."},"3268":{"id":3268,"surah":28,"ayah":16,"verse":"He said, \u201cMy Lord! I have indeed wronged my soul* therefore forgive me\u201d \u2013 He therefore forgave him; indeed only He is the Oft Forgiving, the Most Merciful. (* By getting angry.)"},"3269":{"id":3269,"surah":28,"ayah":17,"verse":"He said, \u201cMy Lord! The way You have bestowed favour upon me, so never will I be a supporter of the guilty.\u201d"},"3270":{"id":3270,"surah":28,"ayah":18,"verse":"So he was in the city at morning fearing, waiting to see what happens \u2013 thereupon he sighted the one who had appealed to him the day before, crying out to him for help; Moosa said to him, \u201cIndeed you are clearly astray.\u201d"},"3271":{"id":3271,"surah":28,"ayah":19,"verse":"So when Moosa wished to apprehend the man who was an enemy to them both, he said, \u201cO Moosa! Do you wish to kill me the way you killed a man yesterday? You only wish to become a strict ruler in the land, and not to make reform.\u201d"},"3272":{"id":3272,"surah":28,"ayah":20,"verse":"And a man came running from the outer part of the city; he said, \u201cO Moosa! Indeed the court members are considering killing you, therefore go away \u2013 I surely am your well-wisher.\u201d"},"3273":{"id":3273,"surah":28,"ayah":21,"verse":"So he left the city in fear, waiting to see what happens; he said, \u201cMy Lord! Rescue me from the unjust people.\u201d"},"3274":{"id":3274,"surah":28,"ayah":22,"verse":"And when he turned his attention towards Madyan he said, \u201cIt is likely that my Lord will show me the right path.\u201d"},"3275":{"id":3275,"surah":28,"ayah":23,"verse":"And when he came to the water of Madyan he found a group of men, watering their animals; and away from them he found two women restraining their animals; he said, \u201cWhat is the matter with you?\u201d They said, \u201cWe do not water our animals till all the shepherds water and take away their animals; and our father is very old.\u201d"},"3276":{"id":3276,"surah":28,"ayah":24,"verse":"Therefore Moosa watered their animals for them, and then turned towards the shade and said, \u201cMy Lord! I am in need of the sustenance you may send down for me.\u201d"},"3277":{"id":3277,"surah":28,"ayah":25,"verse":"So one of the two women approached him, walking shyly; she said, \u201cMy father is calling you, in order to give you wages because you watered our animals for us\u201d; when Moosa came to him and had told him the story, he said, \u201cDo not fear, you are safe from the unjust people.\u201d"},"3278":{"id":3278,"surah":28,"ayah":26,"verse":"One of the two women said, \u201cO my father! Employ him \u2013 indeed a strong and trustworthy employee is better.\u201d"},"3279":{"id":3279,"surah":28,"ayah":27,"verse":"He said, \u201cI wish to give you one of these two daughters of mine in marriage, the bridal money being that you work for me for eight years; then if you complete ten years, it will be from you; and I do not wish to put you in hardship; Allah willing, you will probably find me of the righteous.\u201d"},"3280":{"id":3280,"surah":28,"ayah":28,"verse":"Said Moosa, \u201cThis is agreed between me and you; there shall be no claim upon me if I fulfil any of these two terms; and Allah is the Trustee upon this word of ours.\u201d"},"3281":{"id":3281,"surah":28,"ayah":29,"verse":"So when Moosa completed his term and was travelling with his wife, he saw a fire in the direction of the Mount (Sinai); he said to his wife \u201cStay here \u2013 I have sighted a fire in the direction of the mount \u2013 perhaps I may bring you some news from it, or an ember so that you may warm yourselves.\u201d"},"3282":{"id":3282,"surah":28,"ayah":30,"verse":"And when he reached the fire, he was called out to from the right side of the valley in the blessed field, from the tree, \u201cO Moosa! Indeed I am truly Allah, the Lord Of The Creation.\u201d"},"3283":{"id":3283,"surah":28,"ayah":31,"verse":"\u201cPut down your staff\u201d; so when Moosa saw it writhing like a serpent, he turned moving away without looking back; \u201cO Moosa! Come forth and do not fear; indeed you are in peace.\u201d"},"3284":{"id":3284,"surah":28,"ayah":32,"verse":"\u201cPut your hand inside your armpit \u2013 it will come out shining white, not due to any illness; and put your hand on your chest to remove the fear \u2013 so these are two proofs from your Lord to Firaun and his court members; indeed they are a lawless people.\u201d"},"3285":{"id":3285,"surah":28,"ayah":33,"verse":"He submitted, \u201cMy Lord! I killed a soul among them and I fear they will kill me.\u201d"},"3286":{"id":3286,"surah":28,"ayah":34,"verse":"\u201cAnd my brother Haroon \u2013 he is more eloquent than I am in speech, therefore in order to help me, appoint him as a Noble Messenger so that he confirms me; I fear that they will deny me.\u201d"},"3287":{"id":3287,"surah":28,"ayah":35,"verse":"He said, \u201cWe will soon strengthen your arm with your brother, and give you both dominance, so they will not be able to harm you; due to Our signs; you both, and those who will follow you, will be victorious.\u201d"},"3288":{"id":3288,"surah":28,"ayah":36,"verse":"Therefore when Moosa came to them with Our clear signs they said, \u201cThis is nothing but invented magic, and we never heard anything like this among our forefathers.\u201d"},"3289":{"id":3289,"surah":28,"ayah":37,"verse":"And said Moosa, \u201cMy Lord well knows him who has brought guidance from Him, and for whom will be the abode the Hereafter; indeed the unjust never attain success.\u201d"},"3290":{"id":3290,"surah":28,"ayah":38,"verse":"And Firaun said, \u201cO court members! I do not know of any other God for you, except myself; therefore, O Haman, build a lofty palace for me by baking concrete in order that I may sneak a look at the God of Moosa \u2013 and according to me, he is indeed a liar.\u201d"},"3291":{"id":3291,"surah":28,"ayah":39,"verse":"And he and his soldiers wrongfully sought greatness in the land, and assumed they would never be brought back to Us."},"3292":{"id":3292,"surah":28,"ayah":40,"verse":"We therefore seized him and his armies, and hurled them into the sea; therefore see what sort of fate befell the unjust!"},"3293":{"id":3293,"surah":28,"ayah":41,"verse":"And We made them leaders of the people of hell, inviting towards the fire; and they will not be helped on the Day of Resurrection."},"3294":{"id":3294,"surah":28,"ayah":42,"verse":"And We set a curse after them in this world; and evil is for them on the Day of Resurrection."},"3295":{"id":3295,"surah":28,"ayah":43,"verse":"And We indeed gave Moosa the Book after We had destroyed the former generations, having enlightenment for mankind, and a guidance and a mercy, for them to accept advice."},"3296":{"id":3296,"surah":28,"ayah":44,"verse":"And you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) were not on the western side of the Mount when We sent the command of Prophethood to Moosa, and you were not present. (Yet you narrate the account very clearly to the Jews and Christians.)"},"3297":{"id":3297,"surah":28,"ayah":45,"verse":"However, We created generations and ages passed by upon them; and nor were you dwelling with the people of Madyan reciting Our verses to them \u2013 however it is We Who made the Noble Messengers."},"3298":{"id":3298,"surah":28,"ayah":46,"verse":"And nor were you beside the Mount when We proclaimed \u2013 however it is a mercy from your Lord* so that you may warn a nation towards whom no Herald of Warning came before you, in the hope of their heeding advice. (*That He has bestowed knowledge of the hidden to you)"},"3299":{"id":3299,"surah":28,"ayah":47,"verse":"Otherwise, if some disaster befell them because of what their own hands have sent forward they would say, \u201cOur Lord! Why did you not send a Noble Messenger towards us, so we may have followed Your signs and accepted faith?\u201d"},"3300":{"id":3300,"surah":28,"ayah":48,"verse":"So when the Truth* from Us came to them, they said, \u201cWhy has he not been given what was given to Moosa?\u201d Had they not disbelieved in what was earlier given to Moosa? They said, \u201cThey are two magicians, who support each other\u201d; and said, \u201cWe disbelieve in both of them.\u201d (* Prophet Mohammed \u2013 peace and blessings be upon him)."},"3301":{"id":3301,"surah":28,"ayah":49,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cTherefore bring a Book from Allah which is better guiding than these two \u2013 I will follow it \u2013 if you are truthful.\u201d"},"3302":{"id":3302,"surah":28,"ayah":50,"verse":"So if they do not accept your challenge, then know that they only follow their desires; and who is more astray than one who follows his desires away from the guidance of Allah? Indeed Allah does not guide the unjust."},"3303":{"id":3303,"surah":28,"ayah":51,"verse":"And We indeed sent down the Word continuously for them, so that they may ponder."},"3304":{"id":3304,"surah":28,"ayah":52,"verse":"Those to whom We gave the Book before it, accept faith in it. (Some scholars among Jews \/ Christians)."},"3305":{"id":3305,"surah":28,"ayah":53,"verse":"And when these verses are recited to them, they say, \u201cWe accept faith in it \u2013 indeed it is the Truth from our Lord \u2013 and indeed we had surrendered even before this.\u201d"},"3306":{"id":3306,"surah":28,"ayah":54,"verse":"They will be given double the reward, the recompense of their patience \u2013 and they repel evil with good, and spend part of what We have provided them."},"3307":{"id":3307,"surah":28,"ayah":55,"verse":"And when they hear indecent speech, they ignore it and say, \u201cFor us are our deeds and for you are your deeds; peace be to you (good bye); we are not interested in the ignorant.\u201d"},"3308":{"id":3308,"surah":28,"ayah":56,"verse":"Indeed it is not such that you can guide whomever you love, but Allah guides whomever He wills; and He well knows the people upon guidance."},"3309":{"id":3309,"surah":28,"ayah":57,"verse":"And they say, \u201cIf we follow the guidance along with you, people would snatch us away from our land\u201d; did We not establish them in a safe Sacred Land, towards which are brought fruits of all kinds \u2013 the sustenance from Us? But most of them do not know."},"3310":{"id":3310,"surah":28,"ayah":58,"verse":"And how many towns did We destroy which had stooped to self indulgence! So here lie their dwellings, uninhabited after them except a little; and only We are the Inheritors."},"3311":{"id":3311,"surah":28,"ayah":59,"verse":"And never does your Lord destroy towns until He sends a Noble Messenger to their principal town, reciting Our verses to them; and We never destroy towns unless its people are unjust."},"3312":{"id":3312,"surah":28,"ayah":60,"verse":"And whatever you have been given, is a usage in the life of this world and its adornment; and that which is with Allah is better and more lasting; so do you not have sense?"},"3313":{"id":3313,"surah":28,"ayah":61,"verse":"So is he whom We have given a good promise \u2013 he will therefore get it \u2013 equal to him whom We gave the usage of the life of this world to enjoy, and who will then be brought captive on the Day of Resurrection?"},"3314":{"id":3314,"surah":28,"ayah":62,"verse":"On the day when He will call to them \u2013 He will therefore proclaim, \u201cWhere are those partners of Mine, whom you had assumed?\u201d"},"3315":{"id":3315,"surah":28,"ayah":63,"verse":"Those against whom the Word will have proved true will say, \u201cOur Lord! These are they whom we led astray; we led them astray the way we ourselves went astray; we are unconcerned with them and we incline towards You \u2013 they never worshipped us!\u201d"},"3316":{"id":3316,"surah":28,"ayah":64,"verse":"And it will be said to them, \u201cCall unto your ascribed partners\u201d \u2013 so they will call unto them and they will not listen to them, and they will behold the punishment; if only they had obtained guidance!"},"3317":{"id":3317,"surah":28,"ayah":65,"verse":"And on the Day when He will call to them and proclaim, \u201cWhat answer did you give to the Noble Messengers?\u201d"},"3318":{"id":3318,"surah":28,"ayah":66,"verse":"So on that Day the tidings will be blinded* for them, therefore they will not ask one another. (* They will forget at that moment)."},"3319":{"id":3319,"surah":28,"ayah":67,"verse":"So whoever repented and accepted faith and did good deeds, so it is likely that he will be among the successful."},"3320":{"id":3320,"surah":28,"ayah":68,"verse":"And your Lord creates whatever He wills, and chooses; they do not have any power to choose; Purity and Supremacy are to Allah above their ascribing partners (to Him)."},"3321":{"id":3321,"surah":28,"ayah":69,"verse":"And your Lord knows what is hidden in their breasts, and what they disclose."},"3322":{"id":3322,"surah":28,"ayah":70,"verse":"And He is Allah \u2013 there is no God except Him; only His is the praise in this world and in the Hereafter; and only His is the command, and it is towards Him that you will be returned."},"3323":{"id":3323,"surah":28,"ayah":71,"verse":"Proclaim, \u201cWhat is your opinion \u2013 if Allah makes it night continuously for you till the Day of Resurrection then, other than Allah, who is the God who could bring you light? So do you not heed?\u201d"},"3324":{"id":3324,"surah":28,"ayah":72,"verse":"Say, \u201cWhat is your opinion \u2013 if Allah makes it day continuously for you till the Day of Resurrection then, other than Allah, who is the God who could bring you night for you to rest during it? So do you not perceive?\u201d"},"3325":{"id":3325,"surah":28,"ayah":73,"verse":"And with His mercy He made the night and day for you, so that you may rest during the night and seek His munificence during the day, and for you to be thankful."},"3326":{"id":3326,"surah":28,"ayah":74,"verse":"On the day when He will call to them \u2013 He will therefore proclaim, \u201cWhere are those partners of Mine, whom you had assumed?\u201d"},"3327":{"id":3327,"surah":28,"ayah":75,"verse":"And We shall proclaim taking out a witness from each group, \u201cBring your proof\u201d \u2013 so they will realise that the Truth (Right) is for Allah, and they will lose all that they had fabricated."},"3328":{"id":3328,"surah":28,"ayah":76,"verse":"Indeed Qaroon was from the people of Moosa \u2013 he then oppressed them; and We gave him so many treasures that their keys were a heavy burden for a strong group; when his people said to him, \u201cDo not show off \u2013 indeed Allah does not like the boastful.\u201d"},"3329":{"id":3329,"surah":28,"ayah":77,"verse":"\u201cAnd seek the abode of the Hereafter with the wealth that Allah has given you, and do not forget your part in this world, and do favours (to others) the way Allah has favoured you, and do not seek to cause turmoil in the earth; indeed Allah does not like the mischievous.\u201d"},"3330":{"id":3330,"surah":28,"ayah":78,"verse":"He said, \u201cI got this only due to a knowledge I have\u201d; and does he not know that before him Allah destroyed the generations who were stronger than him and more in number? And there is no questioning of the guilty regarding their sins."},"3331":{"id":3331,"surah":28,"ayah":79,"verse":"He therefore came before his people in his pomp; said those who desired the worldly life, \u201cIf only we were to get what Qaroon has been given \u2013 he is indeed very fortunate.\u201d"},"3332":{"id":3332,"surah":28,"ayah":80,"verse":"And said those who were given the knowledge, \u201cWoe to you \u2013 the reward of Allah is better for one who accepts faith and does good deeds; and only those who are patient will receive it.\u201d"},"3333":{"id":3333,"surah":28,"ayah":81,"verse":"We therefore buried him and his house into the earth; so he had no group to help save him from Allah; nor could he take revenge."},"3334":{"id":3334,"surah":28,"ayah":82,"verse":"And those who had the day before desired his status, said in the morning, \u201cIt is strange \u2013 Allah expands the sustenance for whomever He wills among His bondmen, and restricts it; if Allah had not been gracious to us He would have buried us too; strangely, the disbelievers do not prosper.\u201d"},"3335":{"id":3335,"surah":28,"ayah":83,"verse":"This abode of the Hereafter \u2013 We make it for those who do not wish greatness in the land nor turmoil; and the Hereafter is only for the pious."},"3336":{"id":3336,"surah":28,"ayah":84,"verse":"Whoever brings virtue, for him is better than it; and whoever brings evil \u2013 so those who commit evil will not be repaid except to the extent of their deeds."},"3337":{"id":3337,"surah":28,"ayah":85,"verse":"Indeed He Who has ordained the Qur\u2019an upon you, will surely bring you back to where you wish; proclaim, \u201cMy Lord well knows him who came with guidance, and him who is in open error.\u201d"},"3338":{"id":3338,"surah":28,"ayah":86,"verse":"And you held no expectations of the Book being sent down upon you, except that it is a mercy from your Lord \u2013 therefore never support the disbelievers. (You did not crave for it but trusted in the mercy of your Lord)."},"3339":{"id":3339,"surah":28,"ayah":87,"verse":"And never may they prevent you from the verses of Allah after they have been sent down upon you, and call towards your Lord, and never be among those who ascribe partners (to Him)."},"3340":{"id":3340,"surah":28,"ayah":88,"verse":"And do not worship any other God along with Allah; there is no God except Him; all things are destructible except His Entity; only His is the command, and it is towards Him that you will be returned."},"3341":{"id":3341,"surah":29,"ayah":1,"verse":"Alif-Lam-Meem. (Alphabets of the Arabic language \u2013 Allah and to whomever He reveals, know their precise meanings.)"},"3342":{"id":3342,"surah":29,"ayah":2,"verse":"Do men fancy that they will be left just upon their declaring, \u201cWe believe\u201d, and they will not be tested?"},"3343":{"id":3343,"surah":29,"ayah":3,"verse":"We indeed tested those before them \u2013 so Allah will surely test the truthful, and will surely test the liars."},"3344":{"id":3344,"surah":29,"ayah":4,"verse":"Or do they who commit evil deeds imagine that they can escape from Us? What an evil judgement they impose!"},"3345":{"id":3345,"surah":29,"ayah":5,"verse":"Whoever expects to meet Allah \u2013 then indeed the time appointed by Allah will come; and He is the All Hearing, the All Knowing."},"3346":{"id":3346,"surah":29,"ayah":6,"verse":"And whoever strives in Allah's cause, strives only for his own benefit; indeed Allah is Independent of the entire creation."},"3347":{"id":3347,"surah":29,"ayah":7,"verse":"And those who accepted faith and did good deeds \u2013 We will indeed relieve their sins and reward them for the best of their deeds."},"3348":{"id":3348,"surah":29,"ayah":8,"verse":"And upon man We ordained kindness towards parents; and if they strive to make you ascribe a partner with Me, about which you do not have any knowledge, then do not obey them; towards Me only is your return and I will tell you what you used to do."},"3349":{"id":3349,"surah":29,"ayah":9,"verse":"And those who accepted faith and did good deeds \u2013 We shall indeed include them among the virtuous."},"3350":{"id":3350,"surah":29,"ayah":10,"verse":"And some people say, \u201cWe believe in Allah\u201d \u2013 so if they are afflicted with some adversity in Allah\u2019s way, they consider the chaos created by men as the punishment from Allah; and if the help comes from your Lord, they will surely say, \u201cIndeed we were with you\u201d; does not Allah well know what is in the hearts of the entire creation?"},"3351":{"id":3351,"surah":29,"ayah":11,"verse":"And indeed Allah will make known the believers, and indeed He will expose the hypocrites."},"3352":{"id":3352,"surah":29,"ayah":12,"verse":"And the disbelievers said to the Muslims, \u201cFollow our path and we will bear your sins\u201d; whereas they will not bear anything from their sins; they are indeed liars."},"3353":{"id":3353,"surah":29,"ayah":13,"verse":"And they will surely bear their own burdens and some other burdens along with their own; and they will undoubtedly be questioned on the Day of Resurrection concerning what they had fabricated."},"3354":{"id":3354,"surah":29,"ayah":14,"verse":"And indeed We sent Nooh to his people \u2013 he therefore stayed with them for a thousand years, less fifty; so the flood seized them, and they were unjust."},"3355":{"id":3355,"surah":29,"ayah":15,"verse":"And We rescued him and the people aboard the ship, and made the ship a sign for the entire world."},"3356":{"id":3356,"surah":29,"ayah":16,"verse":"And (remember) Ibrahim \u2013 when he said to his people, \u201cWorship Allah and fear Him; that is better for you, if you knew!\u201d"},"3357":{"id":3357,"surah":29,"ayah":17,"verse":"\u201cYou worship only idols instead of Allah, and only forge a clear lie; indeed those whom you worship instead of Allah do not control your sustenance \u2013 therefore seek your sustenance from Allah, and worship Him, and be grateful to Him; it is towards Him that you will be returned.\u201d"},"3358":{"id":3358,"surah":29,"ayah":18,"verse":"\u201cAnd if you deny, then many nations have denied before you; and the Noble Messenger is not responsible except to plainly convey (the message).\u201d"},"3359":{"id":3359,"surah":29,"ayah":19,"verse":"Have they not seen how Allah initiates the creation, then will create it again? Indeed that is easy for Allah."},"3360":{"id":3360,"surah":29,"ayah":20,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cTravel in the land and see how He creates for the first time, then Allah brings forth the next development; indeed Allah is Able to do all things.\u201d"},"3361":{"id":3361,"surah":29,"ayah":21,"verse":"He punishes whomever He wills and has mercy upon whomever He wills; and towards Him only you are to return."},"3362":{"id":3362,"surah":29,"ayah":22,"verse":"Neither can you escape (from His control) in the earth nor in the sky; and for you, other than Allah, there is neither a friend nor a supporter."},"3363":{"id":3363,"surah":29,"ayah":23,"verse":"And those who did not believe in My signs and in meeting Me, are those who have no hope of My mercy, and for them is a painful punishment."},"3364":{"id":3364,"surah":29,"ayah":24,"verse":"So his people could not answer him except to say, \u201cKill him or burn him\u201d \u2013 so Allah rescued him from the fire; indeed in this are signs for people who believe."},"3365":{"id":3365,"surah":29,"ayah":25,"verse":"And Ibrahim said, \u201cYou have chosen only idols instead of Allah, with whom your friendship is only in the life of this world; then on the Day of Resurrection you will deny and curse each other; and the destination for all of you is hell, and you do not have supporters.\u201d"},"3366":{"id":3366,"surah":29,"ayah":26,"verse":"So Lut believed in him; and Ibrahim said, \u201cI migrate towards my Lord; indeed only He is the Almighty, the Wise.\u201d"},"3367":{"id":3367,"surah":29,"ayah":27,"verse":"And We bestowed Ishaq and Yaqub to him, and kept the Prophethood and the Book among his descendants, and We gave him his reward in the world; and in the Hereafter he is indeed among those who deserve Our proximity."},"3368":{"id":3368,"surah":29,"ayah":28,"verse":"And We rescued Lut when he said to his people, \u201cYou indeed commit the shameful; such that no one in the creation has ever done before you.\u201d"},"3369":{"id":3369,"surah":29,"ayah":29,"verse":"\u201cWhat! You commit the immoral with males, and cut off the roads; and you speak evilly in your gatherings?\u201d So his people had no answer except to say, \u201cBring the punishment of Allah upon us if you are truthful!\u201d"},"3370":{"id":3370,"surah":29,"ayah":30,"verse":"He submitted, \u201cMy Lord! Help me against these mischievous people.\u201d"},"3371":{"id":3371,"surah":29,"ayah":31,"verse":"And when Our sent angels came with glad tidings to Ibrahim, they said, \u201cWe will surely destroy the people of that town; indeed its inhabitants are unjust.\u201d"},"3372":{"id":3372,"surah":29,"ayah":32,"verse":"He said, \"Lut is in it!\u201d; they said, \u201cWe know very well who all are there; we shall rescue him and his family, except his wife; she is of those who will stay behind.\u201d"},"3373":{"id":3373,"surah":29,"ayah":33,"verse":"And when Our sent angels came to Lut, he was unhappy at their arrival and was depressed \u2013 and they said, \u201cDo not fear or grieve; we will surely rescue you and your family, except your wife, who is of those who will stay behind.\u201d"},"3374":{"id":3374,"surah":29,"ayah":34,"verse":"\u201cWe will indeed cause a punishment from the sky to descend upon the inhabitants of this town \u2013 the recompense of their disobedience.\u201d"},"3375":{"id":3375,"surah":29,"ayah":35,"verse":"And We have undoubtedly kept a part of it as a clear sign for people of intellect."},"3376":{"id":3376,"surah":29,"ayah":36,"verse":"And We sent towards Madyan, their fellowman Shuaib \u2013 he therefore said, \u201cO my people! Worship Allah, and anticipate the Last Day, and do not roam the earth spreading turmoil.\u201d"},"3377":{"id":3377,"surah":29,"ayah":37,"verse":"In response they denied him \u2013 thereupon the earthquake seized them \u2013 so at morning they remained lying flattened in their homes."},"3378":{"id":3378,"surah":29,"ayah":38,"verse":"And We destroyed the A\u2019ad and the Thamud, and you already know their dwellings; Satan made their deeds appear good to them and prevented them from the path, whereas they could perceive."},"3379":{"id":3379,"surah":29,"ayah":39,"verse":"And (We destroyed) Qaroon, Firaun and Haman; and indeed Moosa came to them with clear signs, so they were arrogant in the land, and they could never escape from Our control."},"3380":{"id":3380,"surah":29,"ayah":40,"verse":"So We caught each one upon his sin; so among them is one upon whom We sent a shower of stones; and among them is one who was seized by the Scream; and among them is one whom We buried in the earth, and among them is one whom We drowned; it did not befit Allah\u2019s Majesty to oppress them, but they were wronging themselves."},"3381":{"id":3381,"surah":29,"ayah":41,"verse":"The example of those who choose masters other than Allah is like that of the spider; it makes the web its house; and indeed the weakest house of all is that of the spider; if only they knew."},"3382":{"id":3382,"surah":29,"ayah":42,"verse":"Allah knows what they worship instead of Him; He is the Almighty, the Wise."},"3383":{"id":3383,"surah":29,"ayah":43,"verse":"And We illustrate these examples for mankind; and none except the knowledgeable understand them."},"3384":{"id":3384,"surah":29,"ayah":44,"verse":"Allah created the heavens and the earth with the truth; indeed in this is a sign for the Muslims."},"3385":{"id":3385,"surah":29,"ayah":45,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him), recite from the Book which has been sent down to you, and establish the prayer; indeed the prayer stops from indecency and evil; and indeed the remembrance of Allah is the greatest; and Allah knows all what you do."},"3386":{"id":3386,"surah":29,"ayah":46,"verse":"And O Muslims, do not argue with the People given the Book(s) except in the best manner \u2013 except (with) those among them who oppress, and say, \u201cWe believe in what has been sent down towards us and what has been sent towards you \u2013 and ours and your God is One, and we have submitted ourselves to Him.\u201d"},"3387":{"id":3387,"surah":29,"ayah":47,"verse":"And this is how We sent down the Book to you O dear Prophet (Mohammed \u2013 peace and blessings be upon him); so those to whom We gave the Book believe in it; and some of these (polytheists) accept faith in it; and none except the disbelievers reject Our signs."},"3388":{"id":3388,"surah":29,"ayah":48,"verse":"And you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) were not reading any Book before it, nor writing with your right hand \u2013 if it were, the people of falsehood would surely have doubted."},"3389":{"id":3389,"surah":29,"ayah":49,"verse":"In fact they are clear verses in the hearts of those who have been given knowledge; and none deny Our verses except the unjust."},"3390":{"id":3390,"surah":29,"ayah":50,"verse":"And they said, \u201cWhy did not signs come down to him from his Lord?\u201d Say, \u201cSigns are only with Allah; and I am purely a Herald of plain warning.\u201d"},"3391":{"id":3391,"surah":29,"ayah":51,"verse":"Is it not enough for them that We have sent down the Book upon you, which is read to them? Indeed in it are mercy and advice for the Muslims."},"3392":{"id":3392,"surah":29,"ayah":52,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAllah is Sufficient as a Witness between me and you; He knows all what is in the heavens and in the earth; and those who accepted faith in falsehood and denied faith in Allah \u2013 it is they who are the losers.\u201d"},"3393":{"id":3393,"surah":29,"ayah":53,"verse":"And they urge you to hasten the punishment; and if a time had not been fixed, the punishment would have certainly come upon them; and it will indeed come upon them suddenly whilst they are unaware."},"3394":{"id":3394,"surah":29,"ayah":54,"verse":"They urge you to hasten the punishment; and indeed hell has encompassed the disbelievers."},"3395":{"id":3395,"surah":29,"ayah":55,"verse":"The day when the punishment will envelop them from above them and from below their feet, and He will say, \u201cTaste the flavour of your deeds!\u201d"},"3396":{"id":3396,"surah":29,"ayah":56,"verse":"O My bondmen who believe! Indeed My earth is spacious \u2013 therefore worship Me alone."},"3397":{"id":3397,"surah":29,"ayah":57,"verse":"Every soul must taste death; then it is to Us that you will return."},"3398":{"id":3398,"surah":29,"ayah":58,"verse":"And those who believed and did good deeds \u2013 indeed We will, surely, place them on high positions in Paradise beneath which rivers flow, abiding in it for ever; what an excellent reward of the performers!"},"3399":{"id":3399,"surah":29,"ayah":59,"verse":"Those who patiently endured and who rely only upon their Lord."},"3400":{"id":3400,"surah":29,"ayah":60,"verse":"And many creatures walk upon the earth that do not carry their own sustenance; Allah provides the sustenance to them and to you; and only He is the All Hearing, the All Knowing."},"3401":{"id":3401,"surah":29,"ayah":61,"verse":"And if you ask them, \u201cWho created the heavens and the earth, and subjected the sun and the moon?\u201d, they will surely say, \u201cAllah\u201d; so where are they reverting?"},"3402":{"id":3402,"surah":29,"ayah":62,"verse":"Allah eases the sustenance for whomever He wills among His bondmen, and restricts it for whomever He wills; indeed Allah knows all things."},"3403":{"id":3403,"surah":29,"ayah":63,"verse":"And if you ask them, \u201cWho sent down the water from the sky, and with it revived the earth after its death?\u201d, they will surely say, \u201cAllah\u201d; proclaim, \u201cAll praise is to Allah\u201d; in fact most of them do not have any sense."},"3404":{"id":3404,"surah":29,"ayah":64,"verse":"And this worldly life is nothing but pastime and game; indeed the abode of the Hereafter \u2013 that is indeed the true life; if only they knew."},"3405":{"id":3405,"surah":29,"ayah":65,"verse":"And when they board the ships they pray to Allah, believing purely in Him; so when He delivers them safely to land, they immediately begin ascribing partners (to Him)!"},"3406":{"id":3406,"surah":29,"ayah":66,"verse":"In order to be ungrateful for Our favours; and to use the comforts; therefore they will soon come to know."},"3407":{"id":3407,"surah":29,"ayah":67,"verse":"Did they not observe that We have created a refuge in the Sacred Land, and people around them are snatched away? So do they believe in falsehood and are ungrateful for the favours of Allah?"},"3408":{"id":3408,"surah":29,"ayah":68,"verse":"And who is more unjust than one who fabricates lies against Allah, or denies the truth* when it comes to him? Is there not a place in hell for disbelievers? (* The Holy Prophet and \/ or the Holy Qur\u2019an)."},"3409":{"id":3409,"surah":29,"ayah":69,"verse":"And those who strove in Our way \u2013 We shall surely show them Our paths; and indeed Allah is with the virtuous."},"3410":{"id":3410,"surah":30,"ayah":1,"verse":"Alif-Laam-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"3411":{"id":3411,"surah":30,"ayah":2,"verse":"The Romans have been defeated."},"3412":{"id":3412,"surah":30,"ayah":3,"verse":"In the nearby land, and after their defeat they will soon be victorious."},"3413":{"id":3413,"surah":30,"ayah":4,"verse":"Within a few (up to nine) years time; only for Allah is the command, before and after; and the believers will rejoice on that day."},"3414":{"id":3414,"surah":30,"ayah":5,"verse":"With the help of Allah; He helps whomever He wills; and only He is the Almighty, the Most Merciful."},"3415":{"id":3415,"surah":30,"ayah":6,"verse":"The promise of Allah; Allah does not renege on His promise, but most people do not know."},"3416":{"id":3416,"surah":30,"ayah":7,"verse":"They know only the visible worldly life; and are totally neglectful of the Hereafter."},"3417":{"id":3417,"surah":30,"ayah":8,"verse":"Have they not inwardly pondered \u2013 that Allah has not created the heavens and the earth, and all that is between them, except with truth and a fixed term? And indeed many people deny the meeting with their Lord."},"3418":{"id":3418,"surah":30,"ayah":9,"verse":"And did they not travel in the land to see what sort of fate befell those before them? They were stronger than them, and they cultivated the land and inhabited it more than them, and their Noble Messengers came to them with clear proofs; so it did not befit Allah\u2019s Majesty to oppress them, but it was they who used to wrong themselves."},"3419":{"id":3419,"surah":30,"ayah":10,"verse":"Then the fate of those who committed evil to the extreme was that they denied the signs of Allah and used to mock at them."},"3420":{"id":3420,"surah":30,"ayah":11,"verse":"Allah initiates the creation, then will create it again \u2013 then it is to Him that you will return."},"3421":{"id":3421,"surah":30,"ayah":12,"verse":"And on the Last Day\u2013 when it is established, the guilty* will lose all hope. (* The disbelievers.)"},"3422":{"id":3422,"surah":30,"ayah":13,"verse":"And the partners they ascribed will not be their intercessors \u2013 and they will deny their partners."},"3423":{"id":3423,"surah":30,"ayah":14,"verse":"And on the Last Day \u2013 that day they will all separate."},"3424":{"id":3424,"surah":30,"ayah":15,"verse":"So those who believed and did good deeds \u2013 they will be hosted in the Garden."},"3425":{"id":3425,"surah":30,"ayah":16,"verse":"And those who disbelieved, and denied Our signs and confronting the Hereafter \u2013 they will be hauled into the punishment."},"3426":{"id":3426,"surah":30,"ayah":17,"verse":"So proclaim the Purity of Allah when you witness the night and the morning."},"3427":{"id":3427,"surah":30,"ayah":18,"verse":"And only His is the praise in the heavens and the earth \u2013 and before the day ends and at noon."},"3428":{"id":3428,"surah":30,"ayah":19,"verse":"He brings forth the living from the dead, and brings forth the dead from the living, and He revives the earth after its death; and this how you will be raised."},"3429":{"id":3429,"surah":30,"ayah":20,"verse":"And among His signs is that He created you from dust, and therefore you are human beings spread in the earth."},"3430":{"id":3430,"surah":30,"ayah":21,"verse":"And among His signs is that He created spouses for you from yourselves for you to gain rest from them, and kept love and mercy between yourselves; indeed in this are signs for the people who ponder."},"3431":{"id":3431,"surah":30,"ayah":22,"verse":"And among His signs is the creation of the heavens and the earth, and the differences in your languages and colours; indeed in this are signs for people who know."},"3432":{"id":3432,"surah":30,"ayah":23,"verse":"And among His signs is your sleeping during the night and day, and your seeking His munificence; indeed in this are signs for people who heed."},"3433":{"id":3433,"surah":30,"ayah":24,"verse":"And among His signs is that He shows you the lightning, instilling fear and hope, and sends down water from the sky, so revives the earth after its death; indeed in this are signs for people of intellect."},"3434":{"id":3434,"surah":30,"ayah":25,"verse":"And among His signs is that the heavens and the earth remain established by His command; then when He calls you \u2013 from the earth \u2013 you will thereupon emerge."},"3435":{"id":3435,"surah":30,"ayah":26,"verse":"And to Him only belong all those who are in the heavens and the earth; all are submissive to Him."},"3436":{"id":3436,"surah":30,"ayah":27,"verse":"And it is He Who initiates the creation, and will then create it again \u2013 and this, according to you, should be easier for Him! And for Him only is the Supreme Majesty in the heavens and the earth; and only He is the Almighty, the Wise."},"3437":{"id":3437,"surah":30,"ayah":28,"verse":"He illustrates an example for you from your own selves; do you have for yourselves, among the bondmen you possess, partners in the sustenance We have bestowed upon you, thereby you become equal in respect of it \u2013 you fearing them the way you fear each other? This is how We illustrate detailed signs for the people of intellect."},"3438":{"id":3438,"surah":30,"ayah":29,"verse":"Rather the unjust followed their own desires, without knowledge; so who can guide one whom Allah has sent astray? And they do not have supporters."},"3439":{"id":3439,"surah":30,"ayah":30,"verse":"Therefore set your attention for obeying Allah, devoted solely to Him; the foundation set by Allah, upon which He created man; do not change what Allah has created; this is the proper religion \u2013 but most people do not know."},"3440":{"id":3440,"surah":30,"ayah":31,"verse":"Inclining towards Him \u2013 and fear Him, and keep the prayer established, and never be of the polytheists."},"3441":{"id":3441,"surah":30,"ayah":32,"verse":"Those who broke their religion into several parts and became different groups; every group is happy with what it has."},"3442":{"id":3442,"surah":30,"ayah":33,"verse":"And when some affliction reaches men, they pray to their Lord inclining towards Him \u2013 and when He gives them a taste of His mercy, thereupon a group among them begins setting up partners to their Lord!"},"3443":{"id":3443,"surah":30,"ayah":34,"verse":"In order to be ungrateful for Our bestowal; \u201cTherefore enjoy\u201d; you will soon come to know."},"3444":{"id":3444,"surah":30,"ayah":35,"verse":"Or have We sent down to them any proof, which therefore tells them of My partners?"},"3445":{"id":3445,"surah":30,"ayah":36,"verse":"And when We give people the taste of mercy they rejoice at it; and if an ill fortune reaches them because of what their hands have sent ahead \u2013 thereupon they lose hope!"},"3446":{"id":3446,"surah":30,"ayah":37,"verse":"And did they not see that Allah eases the sustenance for whomever He wills and restricts it for whomever He wills? Indeed in this are signs for people who believe."},"3447":{"id":3447,"surah":30,"ayah":38,"verse":"Therefore give the relative his right, and to the needy, and to the traveller; this is better for those who seek the pleasure of Allah; and it is they who are the successful."},"3448":{"id":3448,"surah":30,"ayah":39,"verse":"And that which you give upon usury, in order that it may increase the creditors\u2019 property, will not increase before Allah; and the charity you give seeking the pleasure of Allah \u2013 only that will increase manifold."},"3449":{"id":3449,"surah":30,"ayah":40,"verse":"It is Allah Who created you and then provided you sustenance, then will cause you to die, and will then give you life again; is there any among your ascribed partners that can do any of these things? Purity and Supremacy are to Him, above their ascribing of partners (to Him)!"},"3450":{"id":3450,"surah":30,"ayah":41,"verse":"Chaos appears in the land and the sea because of the evil deeds which people\u2019s hands have earned, in order to make them taste the flavour of some of their misdeeds \u2013 in order that they may come back."},"3451":{"id":3451,"surah":30,"ayah":42,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cTravel in the land, and see what sort of fate befell the former people; and most of them were polytheists.\u201d"},"3452":{"id":3452,"surah":30,"ayah":43,"verse":"Therefore set your attention for worshipping, before the day from Allah which cannot be averted \u2013 on that day people will be split, separated."},"3453":{"id":3453,"surah":30,"ayah":44,"verse":"Whoever disbelieves, then the calamity of his disbelief is only on him; and those who do good deeds, are preparing for themselves."},"3454":{"id":3454,"surah":30,"ayah":45,"verse":"In order to reward those who believed and did good deeds, by His munificence; indeed He does not like the disbelievers."},"3455":{"id":3455,"surah":30,"ayah":46,"verse":"And among His signs is that He sends winds heralding glad tidings, to make you taste His mercy, and so that the ships may sail by His command, and so that you may seek His munificence, and for you to give thanks."},"3456":{"id":3456,"surah":30,"ayah":47,"verse":"And indeed We sent several Noble Messengers before you, to their nations \u2013 so they came to them with clear signs \u2013 We therefore took revenge from the guilty; and it is incumbent upon Our mercy, to help the Muslims."},"3457":{"id":3457,"surah":30,"ayah":48,"verse":"It is Allah Who sends the winds raising the clouds \u2013 therefore spreads them in the sky as He wills and shatters them \u2013 so you see the rain dropping from inside it; so when He delivers it upon whomever He wills among His bondmen, thereupon they rejoice."},"3458":{"id":3458,"surah":30,"ayah":49,"verse":"Although they had lost hope before it was sent down upon them."},"3459":{"id":3459,"surah":30,"ayah":50,"verse":"Therefore observe the result of Allah\u2019s mercy, how He revives the earth after its death; He will indeed resurrect the dead; and He is Able to do all things."},"3460":{"id":3460,"surah":30,"ayah":51,"verse":"And if We send a wind and they see their fields yellow because of it, then indeed they would become ungrateful after it."},"3461":{"id":3461,"surah":30,"ayah":52,"verse":"For you do not make the dead hear nor make the deaf hear the call when they flee, turning back. (The disbelievers are referred to as dead and deaf.)"},"3462":{"id":3462,"surah":30,"ayah":53,"verse":"Nor do you guide the blind out of their error; you only make those hear who believe in Our signs, so they have submitted. (The disbelievers are referred to as blind.)"},"3463":{"id":3463,"surah":30,"ayah":54,"verse":"It is Allah Who created you weak, then after weakness gave you strength, then after strength gave you weakness and old age; He creates whatever He wills; and only He is the All Knowing, the Able."},"3464":{"id":3464,"surah":30,"ayah":55,"verse":"And on the day when the Last Day is established, the guilty will swear that they did not stay except for an hour; this is how they keep straying."},"3465":{"id":3465,"surah":30,"ayah":56,"verse":"And said those who received knowledge and faith, \u201cYou have indeed stayed in the decree of Allah until the Day of Restoration; so this is the Day of Restoration, but you did not know.\u201d"},"3466":{"id":3466,"surah":30,"ayah":57,"verse":"So on that day the unjust will not benefit from their excuses, nor will anyone ask them for compensation."},"3467":{"id":3467,"surah":30,"ayah":58,"verse":"And indeed We have illustrated all kinds of examples in this Qur\u2019an for mankind; and indeed if you bring a sign to them, the disbelievers will say, \u201cYou are upon nothing but falsehood.\u201d"},"3468":{"id":3468,"surah":30,"ayah":59,"verse":"This is how Allah sets seal upon the hearts of the ignorant."},"3469":{"id":3469,"surah":30,"ayah":60,"verse":"Therefore patiently endure, indeed the promise of Allah is true; and may not those who do not have faith make you impatient."},"3470":{"id":3470,"surah":31,"ayah":1,"verse":"Alif-Lam-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"3471":{"id":3471,"surah":31,"ayah":2,"verse":"These are verses of the wise Book."},"3472":{"id":3472,"surah":31,"ayah":3,"verse":"Guidance and mercy for the righteous."},"3473":{"id":3473,"surah":31,"ayah":4,"verse":"Those who keep the prayer established and pay the charity and accept faith in the Hereafter."},"3474":{"id":3474,"surah":31,"ayah":5,"verse":"It is they who are on guidance from their Lord, and only they are the successful."},"3475":{"id":3475,"surah":31,"ayah":6,"verse":"And some people buy words of play, in order to mislead from Allah\u2019s path, without knowledge; and to make it an article of mockery; for them is a disgraceful punishment."},"3476":{"id":3476,"surah":31,"ayah":7,"verse":"And when Our verses are recited to him he haughtily turns away as if he did not hear them \u2013 as if there is deafness in his ears; so give him the glad tidings of a painful punishment."},"3477":{"id":3477,"surah":31,"ayah":8,"verse":"Indeed those who believed and did good deeds \u2013 for them are Gardens of serenity."},"3478":{"id":3478,"surah":31,"ayah":9,"verse":"In which they will abide; it is a true promise of Allah; and only He is the Almighty, the Wise."},"3479":{"id":3479,"surah":31,"ayah":10,"verse":"He created the heavens without any supports visible to you, and cast mountains as anchors into the earth so that it may not shake with you, and spread all kinds of beasts in it; and We sent down water from the sky so thereby grew all kinds of refined pairs in it."},"3480":{"id":3480,"surah":31,"ayah":11,"verse":"Allah has created this \u2013 therefore show me what has been created by those other than Him; in fact the unjust are in open error."},"3481":{"id":3481,"surah":31,"ayah":12,"verse":"And indeed We bestowed wisdom to Luqman (saying) that, \u201cBe grateful to Allah\u201d; and whoever is grateful, is grateful for his own good; and whoever is ungrateful \u2013 then indeed Allah is the Absolute, the Most Praiseworthy."},"3482":{"id":3482,"surah":31,"ayah":13,"verse":"And remember when Luqman said to his son, and he used to advise him, \u201cO my son! Never ascribe anything as a partner to Allah; indeed ascribing partners to Him is a tremendous injustice.\u201d"},"3483":{"id":3483,"surah":31,"ayah":14,"verse":"And We ordained upon man concerning his parents; his mother bore him enduring weakness upon weakness, and his suckling is up to two years \u2013 therefore be thankful to Me and to your parents; finally towards Me is the return."},"3484":{"id":3484,"surah":31,"ayah":15,"verse":"And if they force you, that you ascribe a partner to Me a thing concerning which you do not have knowledge \u2013 so do not obey them and support them well in the world; and follow the path of one who has inclined towards Me; then towards Me only is your return, and I shall tell you what you used to do."},"3485":{"id":3485,"surah":31,"ayah":16,"verse":"\u201cO my son! If the evil deed is equal to the weight of a mustard-seed, and even if it is in a rock, or in the heavens, or wherever in the earth, Allah will bring it forth; indeed Allah knows all the minutest things, the All Aware.\u201d"},"3486":{"id":3486,"surah":31,"ayah":17,"verse":"\u201cO my son! Keep the prayer established, and enjoin goodness and forbid from evil, and be patient upon the calamity that befalls you; indeed these are acts of great courage.\u201d"},"3487":{"id":3487,"surah":31,"ayah":18,"verse":"\u201cAnd do not contort your cheek while talking to anyone, nor boastfully walk upon the earth; indeed Allah does not like any boastful, haughty person.\u201d"},"3488":{"id":3488,"surah":31,"ayah":19,"verse":"\u201cAnd walk moderately and soften your voice; indeed the worst voice is the voice of the donkey.\u201d"},"3489":{"id":3489,"surah":31,"ayah":20,"verse":"Did you not see that Allah has made all whatever is in the heavens and all whatever is in the earth, subservient for you and bestowed His favours upon you in full, both visible and hidden? And some men argue regarding Allah, with neither knowledge, nor guidance, nor a clear Book!"},"3490":{"id":3490,"surah":31,"ayah":21,"verse":"And when it is said to them, \u201cFollow what Allah has sent down\u201d, they say, \u201cOn the contrary, we shall only follow that upon which we found our forefathers\u201d; even if the devil was calling them to the punishment of hell?"},"3491":{"id":3491,"surah":31,"ayah":22,"verse":"Whoever submits his face to Allah and is righteous \u2013 so he has indeed grasped the firm knot; and towards Allah only is the culmination of all things."},"3492":{"id":3492,"surah":31,"ayah":23,"verse":"And whoever disbelieves \u2013 then do not be aggrieved by his disbelief (O dear Prophet Mohammed \u2013 peace and blessings be upon him); they have to return to Us, and We will inform them what they were doing; indeed Allah knows what lies within the hearts."},"3493":{"id":3493,"surah":31,"ayah":24,"verse":"We shall give them some usage, then making them helpless take them towards a severe punishment."},"3494":{"id":3494,"surah":31,"ayah":25,"verse":"And if you ask them, \u201cWho created the heavens and the earth?\u201d \u2013 they will surely answer, \u201cAllah\u201d; proclaim, \u201cAll Praise is to Allah\u201d; in fact most of them do not know."},"3495":{"id":3495,"surah":31,"ayah":26,"verse":"To Allah only belongs whatsoever is in the heavens and in the earth; indeed Allah only is the Absolute, the Most Praiseworthy."},"3496":{"id":3496,"surah":31,"ayah":27,"verse":"And if all the trees in the earth were pens, and the seas were its ink, with seven more seas to back it up \u2013 the Words of Allah will not finish; indeed Allah is Almighty, Wise."},"3497":{"id":3497,"surah":31,"ayah":28,"verse":"Creating you all and raising you from the dead are like that of one soul*; indeed Allah is All Hearing, All Knowing. (He can create you slowly or all at once)."},"3498":{"id":3498,"surah":31,"ayah":29,"verse":"O listener, did you not see that Allah brings the night in a part of the day and brings the day in a part of the night, and that He has subjected the sun and the moon \u2013 each one runs for its fixed term \u2013 and that Allah is Well Aware of your deeds?"},"3499":{"id":3499,"surah":31,"ayah":30,"verse":"This is because only Allah is the Truth, and all that they worship besides Him are falsehood, and because only Allah is the Supreme, the Great."},"3500":{"id":3500,"surah":31,"ayah":31,"verse":"Did you not see that the ship sails on the sea by Allah\u2019s grace, so that He may show you some of His signs? Indeed in this are signs for every greatly enduring, grateful person."},"3501":{"id":3501,"surah":31,"ayah":32,"verse":"And if a wave comes upon them like mountains, they pray to Allah believing purely in Him; then when He brings them safely to land, some of them remain upon justice; and none will deny Our signs except an extremely unfaithful, ungrateful person."},"3502":{"id":3502,"surah":31,"ayah":33,"verse":"O mankind! Fear your Lord, and fear the Day in which no father will benefit his child; nor will any good child be of any benefit to the father; indeed Allah\u2019s promise is true; so never may the worldly life deceive you; and never may the great cheat deceive you in respect of Allah\u2019s commands."},"3503":{"id":3503,"surah":31,"ayah":34,"verse":"Indeed Allah has the knowledge of the Last Day! And He sends down the rain; and He knows all what is in the mothers\u2019 wombs; and no soul knows what it will earn tomorrow; and no soul knows the place where it will die; indeed Allah is the All Knowing, the Informer. (He may reveal the knowledge to whomever He wills.)"},"3504":{"id":3504,"surah":32,"ayah":1,"verse":"Alif-Lam-Meem. (Alphabets of the Arabic language \u2013 Allah and to whomever He reveals, know their precise meanings.)"},"3505":{"id":3505,"surah":32,"ayah":2,"verse":"The revelation of the Book is, without doubt, from the Lord Of The Creation."},"3506":{"id":3506,"surah":32,"ayah":3,"verse":"What! They dare say that, \u201cHe has fabricated it\u201d? In fact it is the Truth from your Lord, in order that you warn a nation towards whom no Herald of Warning came before you, in the hope of their attaining guidance."},"3507":{"id":3507,"surah":32,"ayah":4,"verse":"It is Allah Who created the heavens and the earth, and all what is between them, in six days, then (befitting His Majesty) established Himself over the Throne (of control); leaving Allah, there is neither a friend nor an intercessor for you; so do you not ponder?"},"3508":{"id":3508,"surah":32,"ayah":5,"verse":"He plans (all) the job(s) from the heaven to the earth \u2013 then it will return to Him on the Day which amounts to a thousand years in your count."},"3509":{"id":3509,"surah":32,"ayah":6,"verse":"This is the All Knowing \u2013 of all the hidden and the visible, the Most Honourable, the Most Merciful."},"3510":{"id":3510,"surah":32,"ayah":7,"verse":"The One Who created all things excellent, and Who initiated the creation of man from clay."},"3511":{"id":3511,"surah":32,"ayah":8,"verse":"Then kept his posterity with a part of an abject fluid."},"3512":{"id":3512,"surah":32,"ayah":9,"verse":"Then made him proper and blew into him a spirit from Him, and bestowed ears and eyes and hearts to you; very little thanks do you offer!"},"3513":{"id":3513,"surah":32,"ayah":10,"verse":"And they said, \u201cWhen we have mingled into the earth, will we be created again?\u201d; in fact they disbelieve in the meeting with their Lord."},"3514":{"id":3514,"surah":32,"ayah":11,"verse":"Proclaim, \u201cThe angel of death, who is appointed over you, causes you to die and then towards your Lord you will return.\u201d"},"3515":{"id":3515,"surah":32,"ayah":12,"verse":"And if you see when the guilty will hang their heads before their Lord; \u201cOur Lord! We have seen and heard, therefore send us back in order that we do good deeds \u2013 we are now convinced!\u201d"},"3516":{"id":3516,"surah":32,"ayah":13,"verse":"And had We willed We would have given every soul its guidance, but My Word is decreed that I will certainly fill hell with these jinns and men, combined."},"3517":{"id":3517,"surah":32,"ayah":14,"verse":"\u201cTherefore taste the recompense of your forgetting the confronting of this day of yours; We have abandoned you \u2013 now taste the everlasting punishment, the recompense of your deeds!\u201d"},"3518":{"id":3518,"surah":32,"ayah":15,"verse":"Only those believe in Our signs who, when they are reminded of them, fall down in prostration and proclaim the Purity of their Lord while praising Him, and are not conceited. (Command of prostration # 9)."},"3519":{"id":3519,"surah":32,"ayah":16,"verse":"Their sides stay detached from their beds and they pray to their Lord with fear and hope \u2013 and they spend from what We have bestowed upon them."},"3520":{"id":3520,"surah":32,"ayah":17,"verse":"So no soul knows the comfort of the eyes that is kept hidden for them*; the reward of their deeds. (Paradise)"},"3521":{"id":3521,"surah":32,"ayah":18,"verse":"So will the believer ever be equal to the one who is lawless? They are not equal!"},"3522":{"id":3522,"surah":32,"ayah":19,"verse":"Those who accepted faith and did good deeds \u2013 for them are the Gardens of (everlasting) stay; a welcome in return for what they did."},"3523":{"id":3523,"surah":32,"ayah":20,"verse":"And those who are lawless \u2013 their destination is the fire; whenever they wish to come out of it, they will be returned into it, and it will be said to them, \u201cTaste the punishment of the fire you used to deny!\u201d"},"3524":{"id":3524,"surah":32,"ayah":21,"verse":"And We shall indeed make them taste the smaller punishment before the greater punishment, so that they may return."},"3525":{"id":3525,"surah":32,"ayah":22,"verse":"And who is more unjust than one who is preached to from the verses his Lord, then he turns away from them? We will indeed take revenge from the guilty."},"3526":{"id":3526,"surah":32,"ayah":23,"verse":"And indeed We bestowed the Book to Moosa, therefore have no doubt in its acquisition, and made it a guidance for the Descendants of Israel."},"3527":{"id":3527,"surah":32,"ayah":24,"verse":"And We made some leaders among them, guiding by Our command, when they had persevered; and they used to accept faith in Our signs."},"3528":{"id":3528,"surah":32,"ayah":25,"verse":"Indeed your Lord will judge between them on the Day of Resurrection concerning the matters in which they used to differ."},"3529":{"id":3529,"surah":32,"ayah":26,"verse":"And did they not obtain guidance by the fact that We did destroy many generations before them, so now they walk in their houses? Indeed in this are signs; so do they not heed?"},"3530":{"id":3530,"surah":32,"ayah":27,"verse":"And do they not see that We send the water to the barren land and produce crops with it, so their animals and they themselves eat from it? So do they not perceive?"},"3531":{"id":3531,"surah":32,"ayah":28,"verse":"And they say, \u201cWhen will this decision take place, if you are truthful?\u201d"},"3532":{"id":3532,"surah":32,"ayah":29,"verse":"Proclaim, \u201cOn the Day of Decision*, the disbelievers will not benefit from their accepting faith, nor will they get respite.\u201d (* Of death or of resurrection)"},"3533":{"id":3533,"surah":32,"ayah":30,"verse":"Therefore turn away from them and wait \u2013 indeed they too have to wait."},"3534":{"id":3534,"surah":33,"ayah":1,"verse":"O Herald of the Hidden (the Prophet), continue to fear Allah and never listen to the disbelievers and the hypocrites; indeed Allah is All Knowing, Wise."},"3535":{"id":3535,"surah":33,"ayah":2,"verse":"And keep following what is sent down to you from your Lord; O people! Indeed Allah is seeing your deeds."},"3536":{"id":3536,"surah":33,"ayah":3,"verse":"And trust Allah; and Allah is Sufficient as a Trustee."},"3537":{"id":3537,"surah":33,"ayah":4,"verse":"Allah has not kept two hearts in the body of any man; nor has He made them your mothers those wives of yours, whom you declare to be your mothers; nor has He made them your sons, whom you have adopted; this is the statement of your mouths; and Allah proclaims the truth and it is He Who shows the path."},"3538":{"id":3538,"surah":33,"ayah":5,"verse":"Call them with their fathers\u2019 names \u2013 this is more suitable in the sight of Allah; and if you do not know their fathers, then they are your brothers in the faith, (and your cousins as humans) and your friends; and there is no sin upon you for what you did unknowingly in the past \u2013 however it is a sin what you do with your heart\u2019s intention; and Allah is Oft Forgiving, Most Merciful."},"3539":{"id":3539,"surah":33,"ayah":6,"verse":"The Prophet is closer to the Muslims than their own lives, and his wives are their mothers; and the relatives are closer to each other in the Book of Allah, than other Muslims and immigrants, except that you may be kind towards your friends; this is written in the Book."},"3540":{"id":3540,"surah":33,"ayah":7,"verse":"And remember O dear Prophet (Mohammed \u2013 peace and blessings be upon him) when We took a covenant from the Prophets \u2013 and from you \u2013 and from Nooh, and Ibrahim, and Moosa, and Eisa the son of Maryam; and We took a firm covenant from them."},"3541":{"id":3541,"surah":33,"ayah":8,"verse":"So that He may question the truthful regarding their truth; and He has kept prepared a painful punishment for the disbelievers."},"3542":{"id":3542,"surah":33,"ayah":9,"verse":"O People who believe! Remember the favour of Allah upon you when some armies came against you, so We sent against them a windstorm and the armies you could not see; and Allah sees your deeds."},"3543":{"id":3543,"surah":33,"ayah":10,"verse":"When the disbelievers came upon you from above you and from below you, and when the eyes became fixed in stare and the hearts came up to the throats, and you were imagining matters regarding Allah."},"3544":{"id":3544,"surah":33,"ayah":11,"verse":"That proved be the testing ground for the Muslims, and they were subjected to a severe shake."},"3545":{"id":3545,"surah":33,"ayah":12,"verse":"And when the hypocrites, and those in whose hearts is a disease, began saying, \u201cAllah and His Noble Messenger have not given us a promise except one of deceit.\u201d"},"3546":{"id":3546,"surah":33,"ayah":13,"verse":"And when a group among said, \u201cO people of Medinah! This is no place of stay for you, therefore go back to your homes\u201d; and a group among them sought exemption from the Prophet by saying, \u201cOur homes are unprotected\u201d whereas their homes were not unprotected; they willed nothing except to flee."},"3547":{"id":3547,"surah":33,"ayah":14,"verse":"And if the armies had come upon them from the outskirts of Medinah and demanded disbelief from them, they would certainly have given them their demand and would not have hesitated in it except a little."},"3548":{"id":3548,"surah":33,"ayah":15,"verse":"And indeed before this they had agreed with Allah that they would not turn their backs; and the covenant of Allah will be questioned about."},"3549":{"id":3549,"surah":33,"ayah":16,"verse":"Proclaim, \u201cFleeing will never benefit you if you flee from death or killing, and even then you will not be given the usage of this world except a little.\u201d"},"3550":{"id":3550,"surah":33,"ayah":17,"verse":"Say, \u201cWho is he who can avert the command of Allah from you, if He wills harm for you or wills to have mercy upon you?\u201d And other than Allah, they will not find any friend or supporter."},"3551":{"id":3551,"surah":33,"ayah":18,"verse":"Indeed Allah knows those among you who prevent others from the holy war, and those who say to their brothers, \u201cCome towards us\u201d; and they do not come to fight, except a few."},"3552":{"id":3552,"surah":33,"ayah":19,"verse":"They reduce the help towards you; so when a fearful time comes, you will observe them looking at you with eyes rolling like one enveloped by death; then when the time of fear is over, they begin slandering you with sharp tongues in their greed for the war booty; they have not accepted faith, therefore Allah has nullified their deeds; and this is easy for Allah."},"3553":{"id":3553,"surah":33,"ayah":20,"verse":"They assume that the armies of the disbelievers have not gone away; and were the armies to come again, their wish would be to go out to the villages seeking information about you; and were they to stay among you even then they would not fight, except a few."},"3554":{"id":3554,"surah":33,"ayah":21,"verse":"Indeed following the Noble Messenger of Allah is better for you \u2013 for one who is confident of Allah and the Last Day, and remembers Allah much."},"3555":{"id":3555,"surah":33,"ayah":22,"verse":"And when the Muslims saw the armies, they said, \u201cThis is what Allah and His Noble Messenger promised us, and Allah and His Noble Messenger have spoken the truth\u201d; and it did not increase anything for them but faith and acceptance of Allah\u2019s will."},"3556":{"id":3556,"surah":33,"ayah":23,"verse":"Among the Muslims are the men who have proved true what they had covenanted with Allah; so among them is one who has already fulfilled his vow, and among them is one still waiting; and they have not changed a bit."},"3557":{"id":3557,"surah":33,"ayah":24,"verse":"In order that Allah may reward the truthful for their truthfulness, and punish the hypocrites if He wills, or give them repentance; indeed Allah is Oft Forgiving, Most Merciful."},"3558":{"id":3558,"surah":33,"ayah":25,"verse":"And Allah turned back the disbelievers in their hearts\u2019 jealousy, gaining no good; and Allah sufficed the Muslims in their fight; and Allah is Almighty, Most Honourable."},"3559":{"id":3559,"surah":33,"ayah":26,"verse":"And He brought down from their forts, the People given the Book(s) who had supported them, and instilled awe into their hearts \u2013 you slay a group among them and another group you make captive."},"3560":{"id":3560,"surah":33,"ayah":27,"verse":"And He bequeathed you their lands and their houses and their wealth, and land that you have not yet set foot upon; and Allah is Able to do all things."},"3561":{"id":3561,"surah":33,"ayah":28,"verse":"O Herald of the Hidden! Say to your wives, \u201cIf you desire the worldly life and its adornment \u2013 therefore come, I shall give you wealth and a befitting release!\u201d"},"3562":{"id":3562,"surah":33,"ayah":29,"verse":"\u201cAnd if you desire Allah and His Noble Messenger and the abode of the Hereafter \u2013 then indeed Allah has kept prepared an immense reward for the virtuous among you.\u201d"},"3563":{"id":3563,"surah":33,"ayah":30,"verse":"O the wives of the Prophet! If any among you dares to commit an act contrary to decency, for her will be double the punishment of others; and this is easy for Allah."},"3564":{"id":3564,"surah":33,"ayah":31,"verse":"And whoever among you remains obedient towards Allah and His Noble Messenger and does good deeds \u2013 We shall give her double the reward of others, and have kept prepared for her an honourable sustenance."},"3565":{"id":3565,"surah":33,"ayah":32,"verse":"O the wives of the Prophet! You are not like any other women \u2013 if you really fear Allah, do not speak softly lest the one in whose heart is a disease have any inclination, and speak fairly."},"3566":{"id":3566,"surah":33,"ayah":33,"verse":"And remain in your houses and do not unveil yourselves like the unveiling prevalent in the times of ignorance, and keep the prayer established, and pay the charity, and obey Allah and His Noble Messenger; Allah only wills to remove all impurity from you, O the People of the Household, and by cleansing you make you utterly pure. (*The Holy Prophet\u2019s household.)"},"3567":{"id":3567,"surah":33,"ayah":34,"verse":"And remember what is recited in your houses, from Allah\u2019s verses and wisdom*; indeed Allah knows the minutest things, is All Aware. (The Holy Qur\u2019an and the sayings- hadith \u2013 of the Holy Prophet.)"},"3568":{"id":3568,"surah":33,"ayah":35,"verse":"Indeed the Muslim men and Muslim women, and the believing men and the believing women, and the men who obey and the women who obey, and the truthful men and the truthful women, and the patient men and the patient women, and the humble men and the humble women, and charitable men and the charitable women, and the fasting men and the fasting women, and the men who guard their chastity and the women who guard their chastity, and the men who profusely remember Allah and the women who profusely remember Allah \u2013 for all of them, Allah has kept prepared forgiveness and an immense reward."},"3569":{"id":3569,"surah":33,"ayah":36,"verse":"And no Muslim man or woman has any right in the affair, when Allah and His Noble Messenger have decreed a command regarding it; and whoever does not obey the command of Allah and His Noble Messenger, has indeed clearly gone very astray."},"3570":{"id":3570,"surah":33,"ayah":37,"verse":"And O dear Prophet, remember when you said to him upon whom Allah has bestowed favour and you had bestowed favour, \u201cKeep your wife with you, and fear Allah\u201d, and you kept in your heart what Allah willed to make known, and you feared criticism from the people; and Allah has more right that you should fear Him; so when Zaid became unconcerned with her, We gave her to you in marriage, so that there may be no sin upon believers in respect of the wives of their adopted sons when they have become unconcerned with them; and the command of Allah must be carried out."},"3571":{"id":3571,"surah":33,"ayah":38,"verse":"There is no blame upon the Prophet regarding the matter that Allah has decreed for him; it has been Allah\u2019s tradition in those who passed before; and the command of Allah is a certain destiny."},"3572":{"id":3572,"surah":33,"ayah":39,"verse":"Those who deliver the messages of Allah and fear Him, and do not fear anyone except Allah; and Allah is Sufficient to take account."},"3573":{"id":3573,"surah":33,"ayah":40,"verse":"Mohammed (peace and blessings be upon him) is not the father of any man among you \u2013 but he is the Noble Messenger of Allah and the Last of the Prophets*; and Allah knows all things. (* Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is the Last Prophet. There can be no new Prophet after him)."},"3574":{"id":3574,"surah":33,"ayah":41,"verse":"O People who Believe! Remember Allah profusely."},"3575":{"id":3575,"surah":33,"ayah":42,"verse":"And proclaim His Purity, morning and evening."},"3576":{"id":3576,"surah":33,"ayah":43,"verse":"It is He Who sends blessings upon you*, and so do His angels, so that He may bring you out from darkness into light; and He is Most Merciful upon the Muslims. (* Upon the companions of the Holy Prophet)."},"3577":{"id":3577,"surah":33,"ayah":44,"verse":"For them the salutation upon their meeting* is \u201cPeace\u201d; and an honourable reward is kept prepared for them. (* Upon death \/ while entering Paradise \/ meeting with Allah)."},"3578":{"id":3578,"surah":33,"ayah":45,"verse":"O Herald of the Hidden! We have indeed sent you as an observing present witness and a Herald of glad tidings and warning."},"3579":{"id":3579,"surah":33,"ayah":46,"verse":"And as a caller towards Allah, by His command, and as a sun that enlightens. (The Holy Prophet is a light from Allah.)"},"3580":{"id":3580,"surah":33,"ayah":47,"verse":"And give glad tidings to the believers that for them is Allah\u2019s extreme munificence."},"3581":{"id":3581,"surah":33,"ayah":48,"verse":"And do not please the disbelievers and the hypocrites, and overlook the pain they cause and rely upon Allah; and Allah suffices as a Trustee."},"3582":{"id":3582,"surah":33,"ayah":49,"verse":"O People who Believe! If you marry Muslim women and divorce them without having touched them, so for you there is no waiting period for the women, which you may count; therefore give them some benefit and release them with a proper release."},"3583":{"id":3583,"surah":33,"ayah":50,"verse":"O Herald of the Hidden! We have indeed made lawful for you the wives to whom you have paid their bridal money, and the bondwomen you possess whom Allah gave you as war booty, and the daughters of your paternal uncles, and the daughters of your paternal aunts, and the daughters of your maternal uncles, and the daughters of your maternal aunts, those who migrated with you; and the believing woman if she gifts her life to the Prophet, if the Prophet desires to take her in marriage; this is exclusively for you, not for your followers; We indeed know what We have enjoined upon the Muslims concerning their wives and the bondwomen they possess \u2013 this exclusivity for you is so that you may not have constraints; and Allah is Oft Forgiving, Most Merciful."},"3584":{"id":3584,"surah":33,"ayah":51,"verse":"Put back in the order whom you wish among them and give your proximity to whomever you wish; and if you incline towards one whom you had set aside, there is no sin upon you in it; this command is closer to their eyes remaining soothed and not grieving, and all of them remaining happy with whatever you give them; and Allah knows what is in the hearts of you all; and Allah is All Knowing, Most Forbearing."},"3585":{"id":3585,"surah":33,"ayah":52,"verse":"Other women are not permitted for you after these, nor that you change them for other wives even if their beauty pleases you except the bondwomen whom you possess; and Allah is the Guardian over all things."},"3586":{"id":3586,"surah":33,"ayah":53,"verse":"O People who Believe! Do not enter the houses of the Prophet without permission, as when called for a meal but not to linger around waiting for it \u2013 and if you are invited then certainly present yourself and when you have eaten, disperse \u2013 not staying around delighting in conversation; indeed that was causing harassment to the Prophet, and he was having regard for you; and Allah does not shy in proclaiming the truth; and when you ask the wives of the Prophet for anything to use, ask for it from behind a curtain; this is purer for your hearts and for their hearts; and you have no right to trouble the Noble Messenger of Allah, nor ever marry any of his wives after him; indeed that is a very severe matter in the sight of Allah. (To honour the Holy Prophet \u2013 peace and blessings be upon him \u2013 is part of faith. To disrespect him is blasphemy.)"},"3587":{"id":3587,"surah":33,"ayah":54,"verse":"Whether you disclose a thing or keep it hidden \u2013 then indeed Allah knows all things."},"3588":{"id":3588,"surah":33,"ayah":55,"verse":"There is no sin upon your wives in (dealing with) their fathers, or their sons, or their brothers, or their brothers\u2019 sons, or their sisters\u2019 sons or women of their own religion, or their bondwomen; and O women, keep fearing Allah; indeed all things are present in front of Allah."},"3589":{"id":3589,"surah":33,"ayah":56,"verse":"Indeed Allah and His angels send blessings on the Prophet; O People who Believe! Send blessings and abundant salutations upon him. (Everlasting peace and unlimited blessings be upon the Holy Prophet Mohammed.)"},"3590":{"id":3590,"surah":33,"ayah":57,"verse":"Indeed those who trouble Allah and His Noble Messenger \u2013 upon them is Allah\u2019s curse in the world and in the Hereafter, and Allah has kept prepared a disgraceful punishment for them. (To disrespect \/ trouble the Holy Prophet \u2013 peace and blessings be upon him \u2013 is blasphemy.)"},"3591":{"id":3591,"surah":33,"ayah":58,"verse":"And those who unnecessarily harass Muslim men and women, have burdened themselves with slander and open sin."},"3592":{"id":3592,"surah":33,"ayah":59,"verse":"O Prophet! Command your wives and your daughters and the women of the Muslims to cover their faces with a part of their cloaks; this is closer to their being recognised and not being harassed; and Allah is Oft Forgiving, Most Merciful. (It is incumbent upon women to cover themselves properly.)"},"3593":{"id":3593,"surah":33,"ayah":60,"verse":"If the hypocrites, and those in whose hearts is a disease, and the rumour mongers in Medinah do not desist, We shall then surely impel you against them, so then they will not remain your neighbours in Medinah except for a few days."},"3594":{"id":3594,"surah":33,"ayah":61,"verse":"Accursed \u2013 to be caught wherever found and to be slain thoroughly."},"3595":{"id":3595,"surah":33,"ayah":62,"verse":"The tradition of Allah prevalent among those who passed away before; and you will never find the traditions of Allah changing."},"3596":{"id":3596,"surah":33,"ayah":63,"verse":"People ask you regarding the Last Day; proclaim, \u201cIts knowledge is only with Allah; and what do you know, the Last Day may really be near!\u201d"},"3597":{"id":3597,"surah":33,"ayah":64,"verse":"Allah has indeed cursed the disbelievers, and has kept prepared for them a blazing fire."},"3598":{"id":3598,"surah":33,"ayah":65,"verse":"In which they will remain forever; in it they will find neither a protector nor any supporter."},"3599":{"id":3599,"surah":33,"ayah":66,"verse":"On the day when their faces will be overturned being roasted inside the fire, they will say, \u201cAlas \u2013 if only we had obeyed Allah and obeyed His Noble Messenger!\u201d"},"3600":{"id":3600,"surah":33,"ayah":67,"verse":"And they will say, \u201cOur Lord! We followed our chiefs and our elders \u2013 so they misled us from the path!\u201d"},"3601":{"id":3601,"surah":33,"ayah":68,"verse":"\u201cOur Lord! Give them double the punishment of the fire and send upon them a mighty curse!\u201d"},"3602":{"id":3602,"surah":33,"ayah":69,"verse":"O People who Believe! Do not be like the people who troubled Moosa \u2013 so Allah freed him from the allegations they had uttered; and Moosa is honourable in the sight of Allah."},"3603":{"id":3603,"surah":33,"ayah":70,"verse":"O People who Believe! Fear Allah, and speak rightly."},"3604":{"id":3604,"surah":33,"ayah":71,"verse":"He will rectify your deeds for you and forgive you your sins; and whoever obeys Allah and His Noble Messenger, has indeed achieved a great success."},"3605":{"id":3605,"surah":33,"ayah":72,"verse":"We indeed offered the trust to the heavens and the earth and the mountains, but they refused it and were afraid of it \u2013 and man accepted it; indeed he is one who puts himself into hardship, is extremely unwise."},"3606":{"id":3606,"surah":33,"ayah":73,"verse":"In order that Allah may punish the hypocrite men, and the hypocrite women, and the polytheist men, and the polytheist women \u2013 and accept the repentance of believing men and believing women; and Allah is Oft Forgiving, Most Merciful."},"3607":{"id":3607,"surah":34,"ayah":1,"verse":"All praise is to Allah \u2013 to Him only belongs all whatever is in the heavens and all whatever is in the earth \u2013 and His is the praise in the Hereafter; and He is the Wise, the All Aware."},"3608":{"id":3608,"surah":34,"ayah":2,"verse":"He knows all that goes into the earth and all that comes out of it, and all that descends from the skies and all that ascends into it; and only He is the Most Merciful, the Oft Forgiving."},"3609":{"id":3609,"surah":34,"ayah":3,"verse":"And the disbelievers said, \u201cThe Last Day will never come upon us\u201d; proclaim, \u201cSurely yes, why not? By oath of my Lord, it will surely come upon you \u2013 the All Knowing of the hidden; nothing is hidden from Him \u2013 equal to an atom or less than it or greater \u2013 in the heavens or in the earth, but it is in a clear Book.\u201d"},"3610":{"id":3610,"surah":34,"ayah":4,"verse":"So that He may reward those who believed and did good deeds; it is these for whom is forgiveness, and an honourable sustenance."},"3611":{"id":3611,"surah":34,"ayah":5,"verse":"And those who strove in Our signs in order to defeat \u2013 for them is a punishment from the severe painful punishments."},"3612":{"id":3612,"surah":34,"ayah":6,"verse":"And those who received the knowledge know that what is sent down upon you from your Lord is the truth, and it shows the path of the Most Honourable, the Most Praiseworthy."},"3613":{"id":3613,"surah":34,"ayah":7,"verse":"And the disbelievers said, \u201cShall we show you a man who will tell you that \u2018When you have disintegrated into the smallest pieces, you are to be created again\u2019?\u201d"},"3614":{"id":3614,"surah":34,"ayah":8,"verse":"\u201cHas he fabricated a lie against Allah, or is he insane?\u201d Rather those who disbelieve in the Hereafter are in the punishment and extreme error."},"3615":{"id":3615,"surah":34,"ayah":9,"verse":"So did they not see what is before them and what is behind them in the sky and the earth? If We will, We can bury them into the earth or cause a part of the sky to fall on them; indeed in this is a sign for every repentant bondman."},"3616":{"id":3616,"surah":34,"ayah":10,"verse":"And indeed We gave Dawud the utmost excellence from Us; \u201cO the hills and birds, repent towards Allah along with him\u201d; and We made iron soft for him."},"3617":{"id":3617,"surah":34,"ayah":11,"verse":"\u201cMake large coats of armour and keep proper measure while making; and all of you perform good deeds; I am indeed seeing your deeds.\u201d"},"3618":{"id":3618,"surah":34,"ayah":12,"verse":"And We gave the wind in Sulaiman\u2019s control \u2013 its morning journey equal to a month\u2019s course and the evening journey equal to a month\u2019s course; and We sprung a stream of molten copper for him; and from the jinns, who worked before him by the command of his Lord; and those among them who turned away from Our command \u2013 We shall make them taste the punishment of the blazing fire."},"3619":{"id":3619,"surah":34,"ayah":13,"verse":"They made for him whatever he wished \u2013 synagogues and statues, basins like ponds, and large pots built into the ground; \u201cBe thankful, O the people of Dawud!\u201d And few among My bondmen are grateful."},"3620":{"id":3620,"surah":34,"ayah":14,"verse":"So when We sent the command of death towards him, no one revealed his death to the jinns except the termite of the earth which ate his staff; and when he came to the ground, the truth about the jinns was exposed \u2013 if they had known the hidden, they would not have remained in the disgraceful toil."},"3621":{"id":3621,"surah":34,"ayah":15,"verse":"Indeed for (the tribe of) Saba was a sign in their dwelling-place \u2013 two gardens on the right and the left; \u201cEat the sustenance provided by your Lord and be grateful to Him\u201d; a pure land and an Oft Forgiving Lord!"},"3622":{"id":3622,"surah":34,"ayah":16,"verse":"In response they turned away \u2013 We therefore sent upon them a tremendous flood, and in exchange of their two gardens gave them two gardens bearing bitter fruit, and tamarisk, and some berries."},"3623":{"id":3623,"surah":34,"ayah":17,"verse":"We gave them this reward \u2013 the recompense of their ingratitude; and whom do We punish, except the ungrateful?"},"3624":{"id":3624,"surah":34,"ayah":18,"verse":"And We had made several towns upon the road between them and the towns which We had blessed \u2013 and kept them according to the length of the journey; \u201cTravel safely in them, by night and by day.\u201d"},"3625":{"id":3625,"surah":34,"ayah":19,"verse":"So they said, \u201cOur Lord! Make the stage between our journeys longer\u201d and they wronged themselves \u2013 We therefore turned them into fables and scattered them completely with adversity; indeed in this are signs for every greatly enduring, most grateful person."},"3626":{"id":3626,"surah":34,"ayah":20,"verse":"And indeed Iblis made his assumptions regarding them seem true, so they all followed him except the group of Muslims."},"3627":{"id":3627,"surah":34,"ayah":21,"verse":"And Satan had no control over them at all, except that We willed to reveal as to who believes in the Hereafter and who is in doubt of it; and your Lord is a Guardian over all things."},"3628":{"id":3628,"surah":34,"ayah":22,"verse":"Proclaim, \u201cCall those whom you assume (as Gods) besides Allah\u201d; they do not own anything equal even to an atom either in the heavens or in the earth, nor do they have any share in them, nor is any one among them an aide to Allah."},"3629":{"id":3629,"surah":34,"ayah":23,"verse":"And intercession does not benefit before Him, except for one whom He permits; to the extent that when the fear is removed from their hearts by giving permission, they say to each other, \u201cHow splendidly has your Lord spoken!\u201d They say, \u201cAll that He has proclaimed is the Truth; and He is the Supreme, the Great.\u201d"},"3630":{"id":3630,"surah":34,"ayah":24,"verse":"Proclaim, \u201cWho provides you sustenance from the sky and the earth?\u201d Proclaim, \u201cAllah \u2013 and indeed either we or you are upon guidance, or in open error.\u201d"},"3631":{"id":3631,"surah":34,"ayah":25,"verse":"Proclaim, \u201cYou will not be questioned regarding the sins you assume we have committed, nor will we be questioned regarding your misdeeds.\u201d"},"3632":{"id":3632,"surah":34,"ayah":26,"verse":"Say, \u201cOur Lord will bring us all together, and then judge truthfully between us; and He is the Best Judge, the All Knowing.\u201d"},"3633":{"id":3633,"surah":34,"ayah":27,"verse":"Say, \u201c(Dare you) Show me those whom you have matched with Him \u2013 never! Rather only He is Allah, the Most Honourable, the Wise.\u201d"},"3634":{"id":3634,"surah":34,"ayah":28,"verse":"And O dear Prophet, We have not sent you except with a Prophethood that covers the entire mankind, heralding glad tidings and warnings, but most people do not know. (Prophet Mohammed \u2013 peace and blessings be upon him \u2013 is the Prophet towards all mankind.)"},"3635":{"id":3635,"surah":34,"ayah":29,"verse":"And they say, \u201cWhen will this promise come, if you are truthful?"},"3636":{"id":3636,"surah":34,"ayah":30,"verse":"Proclaim \u201cFor you is the promise of a Day which you cannot postpone by one moment nor can you advance it.\u201d"},"3637":{"id":3637,"surah":34,"ayah":31,"verse":"And the disbelievers said, \u201cWe shall never believe in this Qur\u2019an nor in the Books that were before it\u201d; and if only you see, when the unjust will be brought before their Lord; they will hurl allegations on one another; those who were subdued will say to those who were conceited, \u201cIf it were not for you, we would have certainly accepted faith.\u201d"},"3638":{"id":3638,"surah":34,"ayah":32,"verse":"Those who were conceited will say to those who were subdued, \u201cDid we stop you from the guidance after it came to you? In fact you yourselves were guilty!\u201d"},"3639":{"id":3639,"surah":34,"ayah":33,"verse":"Those who were subdued will say to those who were conceited, \u201cRather it was your deceit during night and day, for you commanded us to deny Allah and to set up equals to Him\u201d; and inwardly they began regretting when they saw the punishment; and We placed shackles around the necks of the disbelievers; what recompense will they get except what they used to do?"},"3640":{"id":3640,"surah":34,"ayah":34,"verse":"And whenever We sent a Herald of Warning to any town, its wealthy people said, \u201cWe disbelieve in what you have been sent with.\u201d"},"3641":{"id":3641,"surah":34,"ayah":35,"verse":"And they said, \u201cWe are greater in wealth and children; we will not be punished!\u201d"},"3642":{"id":3642,"surah":34,"ayah":36,"verse":"Proclaim \u201cIndeed my Lord eases the sustenance for whomever He wills and restricts it for whomever He wills, but most people do not know.\u201d"},"3643":{"id":3643,"surah":34,"ayah":37,"verse":"And your wealth and your children are not capable of bringing you near to Us, but one who believes and did good deeds (is brought close); for them is double the reward \u2013 the recompense of their deeds and they are in high positions, in peace."},"3644":{"id":3644,"surah":34,"ayah":38,"verse":"And those who strive in Our signs in order to defeat, will be brought into the punishment."},"3645":{"id":3645,"surah":34,"ayah":39,"verse":"Say, \u201cIndeed my Lord eases the sustenance for whomever He wills among His bondmen, and restricts it for whomever He wills; and whatever you spend in Allah's cause, He will restore it; and He is the Best Sustainer.\u201d"},"3646":{"id":3646,"surah":34,"ayah":40,"verse":"And on the day when He will raise them all, and then say to the angels, \u201cDid they worship you?\u201d"},"3647":{"id":3647,"surah":34,"ayah":41,"verse":"They will say, \u201cPurity is to you \u2013 only you are our Supporter, not they; in fact they worshipped the jinns; most of them believed only in them.\u201d"},"3648":{"id":3648,"surah":34,"ayah":42,"verse":"So this day you will not have the power to benefit or hurt one another; and We shall say to the unjust, \u201cTaste the punishment of the fire which you used to deny!\u201d"},"3649":{"id":3649,"surah":34,"ayah":43,"verse":"And if Our clear verses are recited to them, they say, \u201cHe is nothing but a man who wishes to turn you away from the deities of your forefathers!\u201d; and they say, \u201cThis is nothing but a fabricated lie\u201d; and said the disbelievers regarding the truth when it reached them, \u201cThis is nothing but obvious magic.\u201d"},"3650":{"id":3650,"surah":34,"ayah":44,"verse":"And We did not give them any Books which they may read, nor did We send to them any Herald of Warning before you."},"3651":{"id":3651,"surah":34,"ayah":45,"verse":"And those before them had denied \u2013 and these have not reached even a tenth of what We had given them \u2013 so they denied My Noble Messengers; so how did My rejection turn out?"},"3652":{"id":3652,"surah":34,"ayah":46,"verse":"Proclaim, \u201cI advise you just one thing; that stand up for Allah's sake, in twos and ones, and then think\u201d; there is not a trace of insanity in this companion of yours; he is not but a Herald of Warning for you, before the advent of a severe punishment!"},"3653":{"id":3653,"surah":34,"ayah":47,"verse":"Say, \u201cWhatever fee I might have asked from you upon this, is yours; my reward is only upon Allah; and He is Witness over all things.\u201d"},"3654":{"id":3654,"surah":34,"ayah":48,"verse":"Proclaim, \u201cIndeed my Lord sends down the truth; the All Knowing of all the hidden.\u201d"},"3655":{"id":3655,"surah":34,"ayah":49,"verse":"Proclaim, \u201cThe truth has come, and falsehood dare not commence nor return.\u201d"},"3656":{"id":3656,"surah":34,"ayah":50,"verse":"Proclaim, \u201cIf I stray, I stray only for my own harm; and if I attain guidance, it is because of what my Lord has sent down to me; indeed He is All Hearing, Close.\u201d"},"3657":{"id":3657,"surah":34,"ayah":51,"verse":"And if only you see, when they will be forced into a terror from which they will be unable to escape, and are seized from a place nearby. (Wherever they go, they are never far)."},"3658":{"id":3658,"surah":34,"ayah":52,"verse":"And they will say, \u201cWe accept faith in it\u201d; and how can they attain it from so far away? (After they have crossed the limit of life allotted to them.)"},"3659":{"id":3659,"surah":34,"ayah":53,"verse":"For they had disbelieved in it before; and they hurl allegations without seeing, from far away."},"3660":{"id":3660,"surah":34,"ayah":54,"verse":"And a barrier is set between them and what they desire*, as was done for their earlier groups; indeed they are in a doubt that deceives. (To accept faith or return to earth)"},"3661":{"id":3661,"surah":35,"ayah":1,"verse":"All praise is to Allah, the Maker of the heavens and the earth, Who assigns angels as messengers \u2013 who have pairs of two, three, four wings; He increases in creation whatever He wills; indeed Allah is Able to do all things."},"3662":{"id":3662,"surah":35,"ayah":2,"verse":"No one can withhold the mercy which Allah opens to mankind; and whatever He withholds \u2013 so after it, none can release it; and only He is the Most Honourable, the Wise."},"3663":{"id":3663,"surah":35,"ayah":3,"verse":"O people! Remember the favour of Allah upon you; is there a Creator other than Allah who can provide you sustenance from the sky and the earth? There is no God except Him; so where are you reverting?"},"3664":{"id":3664,"surah":35,"ayah":4,"verse":"And if they deny you, many Noble Messengers were denied before you; and towards Allah only is the return of all matters."},"3665":{"id":3665,"surah":35,"ayah":5,"verse":"O people! Allah\u2019s promise is indeed true; therefore do not ever let the worldly life deceive you; nor ever let the great cheat deceive you in respect of Allah\u2019s commands."},"3666":{"id":3666,"surah":35,"ayah":6,"verse":"Indeed Satan is your enemy, therefore you too take him as an enemy; he only calls his group so that they become the people of hell!"},"3667":{"id":3667,"surah":35,"ayah":7,"verse":"For the disbelievers is a severe punishment; and for those who accepted faith and did good deeds is forgiveness, and an immense reward."},"3668":{"id":3668,"surah":35,"ayah":8,"verse":"So will one whose misdeeds are made seeming good to him \u2013 so he deems them good \u2013 be ever equal to the one who is upon guidance? Therefore indeed Allah sends astray whomever He wills, and guides whomever He wills; so may not your life be lost in despairing after them; Allah knows their deeds very well."},"3669":{"id":3669,"surah":35,"ayah":9,"verse":"And it is Allah Who sent the winds \u2013 so they raise clouds and We then direct it towards a dead city \u2013 so with it We revive the earth after its death; and this is how the resurrection will be."},"3670":{"id":3670,"surah":35,"ayah":10,"verse":"Whoever desires honour \u2013 therefore all honour belongs to Allah!* Towards Him only ascends the pure good speech, and He raises high the pious deed; and for those who conspire evil is a severe punishment; and their own conspiracy will be destroyed. (It is Allah Who bestows honour, to whomever He wills.)"},"3671":{"id":3671,"surah":35,"ayah":11,"verse":"And Allah created you from clay, then a drop of liquid, then made you as couples; and no female conceives or gives birth except with His knowledge; and every aged being that is given the age, and every one whose life is kept short \u2013 all this is in a Book; indeed this is easy for Allah."},"3672":{"id":3672,"surah":35,"ayah":12,"verse":"And the two seas are not alike; this is sweet, very sweet and palatable \u2013 and this is salty, bitter; and from each you eat fresh meat and extract the ornament which you wear; and you see the ship cleaving through it, so that you may seek His munificence, and in some way become grateful."},"3673":{"id":3673,"surah":35,"ayah":13,"verse":"He brings the night in a part of the day and He brings the day in a part of the night; and He has subjected the sun and moon; each one runs to its fixed term; such is Allah, your Lord \u2013 only His is the kingship; and those whom you worship instead of Him do not own even the husk of a date-seed."},"3674":{"id":3674,"surah":35,"ayah":14,"verse":"If you call them, they do not hear your call; and supposedly if they heard it, they cannot fulfil your needs; and on the Day of Resurrection they will deny your ascribed partnership; and none will inform you like Him Who informs."},"3675":{"id":3675,"surah":35,"ayah":15,"verse":"O people! You are dependant on Allah; and Allah only is the Independent (Absolute, Not Needing Anything), the Most Praiseworthy."},"3676":{"id":3676,"surah":35,"ayah":16,"verse":"If He wills, He can take you away and bring other creatures."},"3677":{"id":3677,"surah":35,"ayah":17,"verse":"And this not at all difficult for Allah."},"3678":{"id":3678,"surah":35,"ayah":18,"verse":"And no burdened soul will carry another soul\u2019s burden; and if a burdened soul calls another to share the burden, no one will carry any part of it, even if he is a close relative; O dear Prophet (Mohammed \u2013 peace and blessings be upon him), your warning only benefits those who fear their Lord without seeing and who keep the prayer established; and whoever cleansed himself, has cleansed for his own benefit; and towards Allah only is the return."},"3679":{"id":3679,"surah":35,"ayah":19,"verse":"And the blind and the sighted are not equal!"},"3680":{"id":3680,"surah":35,"ayah":20,"verse":"And neither are darkness and brightness!"},"3681":{"id":3681,"surah":35,"ayah":21,"verse":"And neither are the shadow and the hot sunshine!"},"3682":{"id":3682,"surah":35,"ayah":22,"verse":"And not equal are the living and the dead! Indeed Allah causes them to listen*, whomever He wills; and you cannot make those who are in the graves** listen. (* Listen to guidance **The disbelievers are referred to as dead, whose fates are sealed.)"},"3683":{"id":3683,"surah":35,"ayah":23,"verse":"You are purely a Herald of Warning."},"3684":{"id":3684,"surah":35,"ayah":24,"verse":"And O dear Prophet (Mohammed \u2013 peace and blessings be upon him), We have indeed sent you with the Truth, giving glad tidings and heralding warnings; and there was a Herald of Warning in every group."},"3685":{"id":3685,"surah":35,"ayah":25,"verse":"And if these disbelievers deny you, those before them had also denied; their Noble Messengers came to them with clear proofs and scriptures and the bright Book."},"3686":{"id":3686,"surah":35,"ayah":26,"verse":"Then I seized the disbelievers; so how did denial My rejection turn out?"},"3687":{"id":3687,"surah":35,"ayah":27,"verse":"Have you not seen that it is Allah Who causes the water to descend from the sky? So with it We have grown various colourful fruits; and among the mountains are tracks white and red, of different hues, and others dark black."},"3688":{"id":3688,"surah":35,"ayah":28,"verse":"And similarly the colours of men and beasts and cattle, are different; among the bondmen of Allah, only the people of knowledge fear Him; indeed Allah is the Most Honourable, Oft Forgiving."},"3689":{"id":3689,"surah":35,"ayah":29,"verse":"Indeed those who read the Book of Allah, and keep the prayer established, and spend from what We have bestowed upon them in secret and publicly, are hopeful of a trade in which there is never a loss."},"3690":{"id":3690,"surah":35,"ayah":30,"verse":"In order that He may reward them with goodness in full, and further increase it with His munificence; indeed He is Oft Forgiving, Most Appreciative."},"3691":{"id":3691,"surah":35,"ayah":31,"verse":"And the Book which We have divinely revealed to you \u2013 that is the Truth, confirming the Books which were before it; indeed Allah is Aware of His bondmen, All Seeing."},"3692":{"id":3692,"surah":35,"ayah":32,"verse":"We then made Our chosen bondmen the inheritors of the Book; so among them is one who wrongs himself; and among them is one who stays on the middle course; and among them is one who, by the command of Allah, surpassed others in good deeds; this is the great favour!"},"3693":{"id":3693,"surah":35,"ayah":33,"verse":"They shall enter the Gardens of everlasting stay (Eden) \u2013 in which they shall be given to adorn armlets of gold and pearls; and their garment in it is silk."},"3694":{"id":3694,"surah":35,"ayah":34,"verse":"And they will say; \u201cAll praise is to Allah Who has put away our grief; indeed Our Lord is Oft Forgiving, Most Appreciative.\u201d"},"3695":{"id":3695,"surah":35,"ayah":35,"verse":"\u201cThe One Who has, by His munificence, established us in a place of serenity; in which no hardship shall ever reach us nor any fatigue affect us.\u201d"},"3696":{"id":3696,"surah":35,"ayah":36,"verse":"And for those who disbelieved is the fire of hell; neither does their final seizure come that they may die, nor is its punishment lightened for them; this is how We punish every extremely ungrateful person."},"3697":{"id":3697,"surah":35,"ayah":37,"verse":"And they shall be screaming in it; \u201cOur Lord! Extricate us, so that we may do good deeds, the opposite of what we used to do\u201d; (It will be said to them) \u201cAnd did We not give you an age long enough, in which anyone who wants to understand would have understood? And the Herald of Warning did come to you; therefore now taste it \u2013 for the unjust do not have any supporter.\u201d"},"3698":{"id":3698,"surah":35,"ayah":38,"verse":"Indeed Allah is All Knowing \u2013 of all the hidden things in the heavens and in the earth; indeed He knows what lies within the hearts."},"3699":{"id":3699,"surah":35,"ayah":39,"verse":"It is He Who has made you the successors of your predecessors in the earth; so whoever disbelieves \u2013 (the harm of) his disbelief falls only on him; and for the disbelievers, their disbelief increases nothing in their Lord\u2019s sight except disgust; and for the disbelievers, their disbelief increases nothing for them except ruin."},"3700":{"id":3700,"surah":35,"ayah":40,"verse":"Proclaim, \u201cJust show me your partners (false deities) whom you worship other than Allah; show me which part of the earth have they created \u2013 or do they have any share in the heavens?\u201d Or have We given them some Book, so they are on its clear proofs? In fact the unjust do not give promises to each other, except of deceit."},"3701":{"id":3701,"surah":35,"ayah":41,"verse":"Indeed Allah restrains the heavens and the earth from convulsing; and were they to convulse, who could stop them except Allah? Indeed He is Most Forbearing, Oft Forgiving."},"3702":{"id":3702,"surah":35,"ayah":42,"verse":"And they swore by Allah most vehemently in their oaths, that if a Herald of Warning came to them, they would be more upon guidance than any other group; then when a Herald of Warning did come to them, he increased nothing in them except hatred."},"3703":{"id":3703,"surah":35,"ayah":43,"verse":"Priding themselves in the earth and scheming evil; and the evil scheming falls only upon those who scheme it; so what are they waiting for, except the tradition of the earlier nations? So you will never find the traditions of Allah changing; and you will never find Allah\u2019s law being avoided."},"3704":{"id":3704,"surah":35,"ayah":44,"verse":"And did they not travel in the land in order to see what sort of fate befell those who were before them, whereas they exceeded them in strength? And Allah is not such that anything in the heavens or in the earth can break away from His control; indeed He is All Knowing, Able."},"3705":{"id":3705,"surah":35,"ayah":45,"verse":"If Allah were to seize people upon their deeds, He would leave no creature walking on the surface of the earth, but He gives them respite till a fixed term; and when their term comes \u2013 so indeed Allah is observing all His bondmen!"},"3706":{"id":3706,"surah":36,"ayah":1,"verse":"Yaa-Seen (Alphabets of Arabic language \u2013 Allah and to whomever He reveals, know their precise meanings.)"},"3707":{"id":3707,"surah":36,"ayah":2,"verse":"By oath of the wise Qur\u2019an."},"3708":{"id":3708,"surah":36,"ayah":3,"verse":"You (O dear Prophet Mohammed \u2013 peace and blessings be upon him) are indeed one of the Noble Messengers."},"3709":{"id":3709,"surah":36,"ayah":4,"verse":"On the Straight Path."},"3710":{"id":3710,"surah":36,"ayah":5,"verse":"(The Qur\u2019an is) Sent down by the Almighty, the Most Merciful."},"3711":{"id":3711,"surah":36,"ayah":6,"verse":"So that you may warn these people whose ancestors were not warned, they are therefore unaware."},"3712":{"id":3712,"surah":36,"ayah":7,"verse":"Undoubtedly, it (their disbelief) has proved true for most of them, so they will not believe."},"3713":{"id":3713,"surah":36,"ayah":8,"verse":"We have indeed put shackles around their necks reaching up to the chins, so they remain facing upwards."},"3714":{"id":3714,"surah":36,"ayah":9,"verse":"And We have set a wall before them and a wall behind them, and covered the top \u2013 so they are unable to see anything."},"3715":{"id":3715,"surah":36,"ayah":10,"verse":"And it is the same for them, whether you warn them or do not warn them \u2013 they will not believe."},"3716":{"id":3716,"surah":36,"ayah":11,"verse":"You warn only him who follows the advice and fears the Most Gracious without seeing; therefore give him glad tidings of forgiveness and an honourable reward."},"3717":{"id":3717,"surah":36,"ayah":12,"verse":"We will surely bring the dead to life and We record what they send ahead and the signs they will leave behind; and We have accounted all things in a clear Book."},"3718":{"id":3718,"surah":36,"ayah":13,"verse":"And relate to them the signs of the people of the city \u2013 when two emissaries came to them."},"3719":{"id":3719,"surah":36,"ayah":14,"verse":"When We had sent two towards them and they denied them both, so We fortified them with a third, and they all said, \u201cIndeed we have been sent to you.\u201d"},"3720":{"id":3720,"surah":36,"ayah":15,"verse":"They said, \u201cYou are nothing but mortals like us; the Most Gracious has not sent down anything \u2013 you are nothing but liars.\u201d"},"3721":{"id":3721,"surah":36,"ayah":16,"verse":"They answered, \u201cOur Lord knows that surely, without doubt, we have been sent towards you.\u201d"},"3722":{"id":3722,"surah":36,"ayah":17,"verse":"\u201cAnd our duty is nothing but to plainly convey (the message).\u201d"},"3723":{"id":3723,"surah":36,"ayah":18,"verse":"They (the people of the city) said, \u201cWe think you are ominous; indeed, if you do not desist, we shall surely stone you to death, and you will surely face a grievous torture at our hands.\u201d"},"3724":{"id":3724,"surah":36,"ayah":19,"verse":"They said, \u201cYour evil omens are with you! What! You get annoyed for being advised? In fact you are a people who transgress the limits!\u201d"},"3725":{"id":3725,"surah":36,"ayah":20,"verse":"And from the outermost part of the city came a man running; he said, \u201cO my people! Obey those who have been sent.\u201d"},"3726":{"id":3726,"surah":36,"ayah":21,"verse":"\u201cObey those who do not ask any fee from you, and they are on guidance.\u201d"},"3727":{"id":3727,"surah":36,"ayah":22,"verse":"\u201cAnd what is the matter with me that I should not worship Him Who created me, whereas it is towards Him that you are to return?\u201d"},"3728":{"id":3728,"surah":36,"ayah":23,"verse":"\u201cWhat! Shall I appoint Gods other than Allah? So that if the Most Gracious should wish me any harm, their intercession would be of no use to me, nor would they be able to save me?\u201d"},"3729":{"id":3729,"surah":36,"ayah":24,"verse":"\u201cUndoubtedly, I am then in open error.\u201d"},"3730":{"id":3730,"surah":36,"ayah":25,"verse":"\u201cIndeed I have believed in your Lord, so heed me.\u201d"},"3731":{"id":3731,"surah":36,"ayah":26,"verse":"It was said to him, \u201cEnter Paradise\u201d; he said, \u201cIf only my people knew!\u201d"},"3732":{"id":3732,"surah":36,"ayah":27,"verse":"\u201cThe manner in which my Lord has pardoned me and made me of the honoured ones!\u201d"},"3733":{"id":3733,"surah":36,"ayah":28,"verse":"And after him, We did not send down any army from heaven against his people, nor did We intend to send down an army."},"3734":{"id":3734,"surah":36,"ayah":29,"verse":"It was just one scream, and with it they were extinguished."},"3735":{"id":3735,"surah":36,"ayah":30,"verse":"And it was said, \u201cWoe to those bondmen \u2013 whenever a Noble Messenger comes to them, they mock at him!\u201d"},"3736":{"id":3736,"surah":36,"ayah":31,"verse":"Have they not seen how many generations We destroyed before them, which will not return to them?"},"3737":{"id":3737,"surah":36,"ayah":32,"verse":"And without exception, all of them will be brought forth before Us."},"3738":{"id":3738,"surah":36,"ayah":33,"verse":"And a sign for them is the dead earth; We gave it life and We produced from it grain, so they eat from it."},"3739":{"id":3739,"surah":36,"ayah":34,"verse":"And We have placed in it gardens of dates and grapes, and We have made springs of water in it."},"3740":{"id":3740,"surah":36,"ayah":35,"verse":"So that they may eat from its fruits, whereas they are not manufactured by their hands! So will they not be grateful?"},"3741":{"id":3741,"surah":36,"ayah":36,"verse":"Purity is to Him Who created all pairs, from what the earth grows, and of themselves, and from the things they do not know."},"3742":{"id":3742,"surah":36,"ayah":37,"verse":"And a sign for them is the night; We strip the day out of it, thereupon they are in darkness."},"3743":{"id":3743,"surah":36,"ayah":38,"verse":"And the sun runs its course for its final destination; this is a command of the Almighty, the All Knowing."},"3744":{"id":3744,"surah":36,"ayah":39,"verse":"And We have appointed positions for the moon till it returns like an old branch of the date palm."},"3745":{"id":3745,"surah":36,"ayah":40,"verse":"It is not for the sun to catch up with the moon, nor does the night surpass the day; and each one of them floats in its orbit."},"3746":{"id":3746,"surah":36,"ayah":41,"verse":"And a sign for them is that We lodged them in a laden ship, while they were in their forefathers backs."},"3747":{"id":3747,"surah":36,"ayah":42,"verse":"And We have created for them similar ships, in which they now ride."},"3748":{"id":3748,"surah":36,"ayah":43,"verse":"And if We will, We can drown them, so there would be no help in their distress, nor would they be saved."},"3749":{"id":3749,"surah":36,"ayah":44,"verse":"Unless by mercy from Us, and as a comfort for a while."},"3750":{"id":3750,"surah":36,"ayah":45,"verse":"And when it is said to them, \u201cBeware of what is before you and what is behind you, in the hope of your gaining mercy\u201d, they turn away!"},"3751":{"id":3751,"surah":36,"ayah":46,"verse":"And whenever a sign comes to them from the signs of their Lord, they always turn away from it!"},"3752":{"id":3752,"surah":36,"ayah":47,"verse":"And when it is said to them, \u201cSpend in Allah\u2019s cause, from what Allah has provided you\u201d, the disbelievers say regarding the believers, \u201cShall we feed these, whom if Allah willed, would have fed? You are not but in open error!\u201d"},"3753":{"id":3753,"surah":36,"ayah":48,"verse":"And they say, \u201cWhen will this promise be fulfilled, if you are truthful?\u201d"},"3754":{"id":3754,"surah":36,"ayah":49,"verse":"They await just one scream, which will overcome them while they are involved in worldly disputes."},"3755":{"id":3755,"surah":36,"ayah":50,"verse":"Therefore neither able to make a will, nor returning to their homes."},"3756":{"id":3756,"surah":36,"ayah":51,"verse":"And the Trumpet will be blown \u2013 so they will come forth from the graves, running towards their Lord."},"3757":{"id":3757,"surah":36,"ayah":52,"verse":"Saying, \u201cO our misfortune! Who has raised us from our sleep? This is what the Most Gracious had promised, and the Noble Messengers had spoken the truth!\u201d"},"3758":{"id":3758,"surah":36,"ayah":53,"verse":"It is just one scream, and every one of them will be brought together before Us!"},"3759":{"id":3759,"surah":36,"ayah":54,"verse":"So this day no soul will be wronged in the least; and you will not be compensated except for your deeds."},"3760":{"id":3760,"surah":36,"ayah":55,"verse":"Indeed this day the dwellers of Paradise are in comfort, with blissful hearts."},"3761":{"id":3761,"surah":36,"ayah":56,"verse":"They and their wives are in shades, reclining on thrones."},"3762":{"id":3762,"surah":36,"ayah":57,"verse":"In it (paradise) are fruits for them and whatever they ask for."},"3763":{"id":3763,"surah":36,"ayah":58,"verse":"Upon them will be \u201cPeace\u201d \u2013 a Word from their Merciful Lord!"},"3764":{"id":3764,"surah":36,"ayah":59,"verse":"\u201cAnd be separated (from others) this day, O you criminals!\u201d"},"3765":{"id":3765,"surah":36,"ayah":60,"verse":"\u201cO Descendants of Adam! Did I not take a covenant from you that you shall not worship the devil? Undoubtedly, he is your open enemy.\u201d"},"3766":{"id":3766,"surah":36,"ayah":61,"verse":"\u201cAnd that you shall worship Me? This is the Straight Path.\u201d"},"3767":{"id":3767,"surah":36,"ayah":62,"verse":"\u201cAnd he has indeed led a large number of you astray; so did you not have sense?\u201d"},"3768":{"id":3768,"surah":36,"ayah":63,"verse":"\u201cThis is hell, which you were promised.\u201d"},"3769":{"id":3769,"surah":36,"ayah":64,"verse":"\u201cEnter it this day \u2013 the recompense of your disbelief.\u201d"},"3770":{"id":3770,"surah":36,"ayah":65,"verse":"This day We will set a seal on their mouths, and their hands will speak out to Us and their feet will bear witness to their deeds."},"3771":{"id":3771,"surah":36,"ayah":66,"verse":"And had We willed, We could have quenched their eyes so they would rush towards the path, unable to see a thing."},"3772":{"id":3772,"surah":36,"ayah":67,"verse":"And had We willed, We could have disfigured their faces while they were in their homes, therefore unable to go forward or turn back."},"3773":{"id":3773,"surah":36,"ayah":68,"verse":"And whomever We bring to an old age, We reverse him in creation; so do they not understand?"},"3774":{"id":3774,"surah":36,"ayah":69,"verse":"And We have not taught him (Prophet Mohammed- peace and blessings be upon him) to recite poetry, nor does it befit him; it is nothing but an advice and the bright Qur\u2019an."},"3775":{"id":3775,"surah":36,"ayah":70,"verse":"To warn the living*, and to prove the Word against disbelievers. (Only the believers are deemed alive in Allah\u2019s sight.)"},"3776":{"id":3776,"surah":36,"ayah":71,"verse":"Did they not see that We have created animals for them from Our handiwork, so they are their owners?"},"3777":{"id":3777,"surah":36,"ayah":72,"verse":"And have subjected the animals for them, so they ride some animals and eat some?"},"3778":{"id":3778,"surah":36,"ayah":73,"verse":"And for them in the animals are numerous different benefits and drinks; so will they not be grateful?"},"3779":{"id":3779,"surah":36,"ayah":74,"verse":"And they have appointed Gods other than Allah, that perhaps they may be helped!"},"3780":{"id":3780,"surah":36,"ayah":75,"verse":"They (the appointed Gods) cannot help them; and they and their armies will come (to Us), as captives."},"3781":{"id":3781,"surah":36,"ayah":76,"verse":"Therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him) do not grieve because of what they (the disbelievers) say; indeed We know what they conceal and what they disclose."},"3782":{"id":3782,"surah":36,"ayah":77,"verse":"And did not man see that We have created him from a drop of semen? Yet he is an open quarreller!"},"3783":{"id":3783,"surah":36,"ayah":78,"verse":"And he invents an example for Us, while forgetting his own creation, saying, \u201cWho is such that can revive the bones when they have completely rotted away?\u201d"},"3784":{"id":3784,"surah":36,"ayah":79,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cThey will be revived by the One Who created them the first time; and He is the All Knowing of every creation.\u201d"},"3785":{"id":3785,"surah":36,"ayah":80,"verse":"\u201cThe One Who has created for you fire from the green tree, so you kindle from it.\u201d"},"3786":{"id":3786,"surah":36,"ayah":81,"verse":"And is it not possible for the One Who created the heavens and the earth to create the likes of them? It is surely possible, why not? And He is the Great Creator, the All Knowing of everything."},"3787":{"id":3787,"surah":36,"ayah":82,"verse":"And His only task when He intends a thing is to command it, \u201cBe\u201d \u2013 and it thereupon happens!"},"3788":{"id":3788,"surah":36,"ayah":83,"verse":"Therefore Purity is to Him in Whose Hand* is the control over all things and it is towards Him that you will be returned. (Used as a metaphor to mean Power)."},"3789":{"id":3789,"surah":37,"ayah":1,"verse":"By oath of those who establish proper ranks."},"3790":{"id":3790,"surah":37,"ayah":2,"verse":"And by oath of those who herd with a stern warning."},"3791":{"id":3791,"surah":37,"ayah":3,"verse":"And by oath of the groups that read the Qur\u2019an."},"3792":{"id":3792,"surah":37,"ayah":4,"verse":"Indeed your God is surely only One."},"3793":{"id":3793,"surah":37,"ayah":5,"verse":"Lord of the heavens and the earth and all that is between them \u2013 and the Lord of the sun\u2019s rising points."},"3794":{"id":3794,"surah":37,"ayah":6,"verse":"We have indeed adorned the lowest heaven with stars as ornaments."},"3795":{"id":3795,"surah":37,"ayah":7,"verse":"And to protect it from every rebellious devil."},"3796":{"id":3796,"surah":37,"ayah":8,"verse":"They cannot listen to the speech of those on higher elevations and they are targeted from every side."},"3797":{"id":3797,"surah":37,"ayah":9,"verse":"To make them flee, and for them is a never-ending punishment."},"3798":{"id":3798,"surah":37,"ayah":10,"verse":"Except one who sometimes steals a part, so a blazing flame goes after him."},"3799":{"id":3799,"surah":37,"ayah":11,"verse":"Therefore ask them (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAre they a stronger creation, or are other things of our creation, (the angels, the heavens etc.)?\u201d We have indeed created them from sticky clay."},"3800":{"id":3800,"surah":37,"ayah":12,"verse":"Rather you are surprised, whereas they keep mocking."},"3801":{"id":3801,"surah":37,"ayah":13,"verse":"And they do not understand, when explained to."},"3802":{"id":3802,"surah":37,"ayah":14,"verse":"And whenever they see a sign, they mock at it."},"3803":{"id":3803,"surah":37,"ayah":15,"verse":"And they say, \u201cThis is nothing but clear magic.\u201d"},"3804":{"id":3804,"surah":37,"ayah":16,"verse":"\u201cWhen we are dead and have turned into dust and bones, will we certainly be raised again?\u201d"},"3805":{"id":3805,"surah":37,"ayah":17,"verse":"\u201cAnd also our forefathers?\u201d"},"3806":{"id":3806,"surah":37,"ayah":18,"verse":"Proclaim, \u201cYes, and with disgrace.\u201d"},"3807":{"id":3807,"surah":37,"ayah":19,"verse":"So that is just a single jolt, thereupon they will begin staring."},"3808":{"id":3808,"surah":37,"ayah":20,"verse":"And will say, \u201cO our misfortune!\u201d It will be said to them, \u201cThis is the Day of Justice.\u201d"},"3809":{"id":3809,"surah":37,"ayah":21,"verse":"\u201cThis is the Day of Judgement, which you used to deny!\u201d"},"3810":{"id":3810,"surah":37,"ayah":22,"verse":"\u201cGather all the unjust persons and their spouses, and all that they used to worship!\u201d \u2013"},"3811":{"id":3811,"surah":37,"ayah":23,"verse":"\u201cInstead of Allah \u2013 and herd them to the path leading to hell.\u201d"},"3812":{"id":3812,"surah":37,"ayah":24,"verse":"\u201cAnd stop them \u2013 they are to be questioned.\u201d"},"3813":{"id":3813,"surah":37,"ayah":25,"verse":"\u201cWhat is the matter with you, that you do not help one another?\u201d"},"3814":{"id":3814,"surah":37,"ayah":26,"verse":"In fact this day they have all surrendered."},"3815":{"id":3815,"surah":37,"ayah":27,"verse":"And some of them inclined towards others, mutually questioning."},"3816":{"id":3816,"surah":37,"ayah":28,"verse":"They said, \u201cIt is you who used to come to us from our right, in order to sway.\u201d"},"3817":{"id":3817,"surah":37,"ayah":29,"verse":"They will answer, \u201cYou yourselves did not have faith!\u201d"},"3818":{"id":3818,"surah":37,"ayah":30,"verse":"\u201cAnd we did not have any control over you; but in fact you yourselves were rebellious.\u201d"},"3819":{"id":3819,"surah":37,"ayah":31,"verse":"\u201cSo the Word of our Lord has proved true upon us; we will surely have to taste (the punishment).\u201d"},"3820":{"id":3820,"surah":37,"ayah":32,"verse":"\u201cWe therefore led you astray, for we ourselves were astray!\u201d"},"3821":{"id":3821,"surah":37,"ayah":33,"verse":"So this day they all are partners in the punishment."},"3822":{"id":3822,"surah":37,"ayah":34,"verse":"This is how We deal with the guilty."},"3823":{"id":3823,"surah":37,"ayah":35,"verse":"Indeed, when it was said to them, \u201cThere is no God except Allah\u201d, they were haughty."},"3824":{"id":3824,"surah":37,"ayah":36,"verse":"And they used to say, \u201cShall we forsake our Gods upon the sayings of a mad poet?\u201d"},"3825":{"id":3825,"surah":37,"ayah":37,"verse":"In fact he has brought the Truth, and testified for the Noble Messengers!"},"3826":{"id":3826,"surah":37,"ayah":38,"verse":"\u201cYou surely have to taste the painful punishment.\u201d"},"3827":{"id":3827,"surah":37,"ayah":39,"verse":"\u201cAnd you will not be compensated except for your deeds.\u201d"},"3828":{"id":3828,"surah":37,"ayah":40,"verse":"Except the chosen bondmen of Allah."},"3829":{"id":3829,"surah":37,"ayah":41,"verse":"For them is the sustenance known to Us."},"3830":{"id":3830,"surah":37,"ayah":42,"verse":"Fruits; and they will be honoured."},"3831":{"id":3831,"surah":37,"ayah":43,"verse":"In Gardens of peace."},"3832":{"id":3832,"surah":37,"ayah":44,"verse":"Facing one another on thrones."},"3833":{"id":3833,"surah":37,"ayah":45,"verse":"Cups of wine will be presented to them in rounds, from a spring flowing in front of them."},"3834":{"id":3834,"surah":37,"ayah":46,"verse":"White, delicious for the drinkers."},"3835":{"id":3835,"surah":37,"ayah":47,"verse":"Neither does it intoxicate, nor give a headache."},"3836":{"id":3836,"surah":37,"ayah":48,"verse":"And with them are those who do not set gaze upon men except their husbands, the maidens with gorgeous eyes."},"3837":{"id":3837,"surah":37,"ayah":49,"verse":"As if they were eggs, safely hidden."},"3838":{"id":3838,"surah":37,"ayah":50,"verse":"So one among them turns to the other, questioning. \u2013"},"3839":{"id":3839,"surah":37,"ayah":51,"verse":"The speaker among them said, \u201cI had a companion.\u201d \u2013"},"3840":{"id":3840,"surah":37,"ayah":52,"verse":"\u201cWho used to tell me, \u2018Do you believe it is true?\u2019 \u2013"},"3841":{"id":3841,"surah":37,"ayah":53,"verse":"\u2018That when we are dead and have turned into dust and bones, that we will either be rewarded or punished?\u2019\u201d"},"3842":{"id":3842,"surah":37,"ayah":54,"verse":"He said (to others on the thrones), \u201cWill you take a glimpse (into hell) below?\u201d"},"3843":{"id":3843,"surah":37,"ayah":55,"verse":"He therefore looked down and saw him amidst the blazing fire."},"3844":{"id":3844,"surah":37,"ayah":56,"verse":"He said, \u201cBy Allah, you had nearly ruined me!\u201d"},"3845":{"id":3845,"surah":37,"ayah":57,"verse":"\u201cAnd were it not for the munificence of my Lord, I too would have been seized and brought forth (captive)!\u201d"},"3846":{"id":3846,"surah":37,"ayah":58,"verse":"\u201cSo are we never to die?\u201d (The people of Paradise will ask the angels, with delight, after the announcement of everlasting life.)"},"3847":{"id":3847,"surah":37,"ayah":59,"verse":"\u201cExcept our earlier death, and nor will we be punished?\u201d"},"3848":{"id":3848,"surah":37,"ayah":60,"verse":"\u201cThis is, most certainly, the supreme success.\u201d"},"3849":{"id":3849,"surah":37,"ayah":61,"verse":"For such a reward should the workers perform."},"3850":{"id":3850,"surah":37,"ayah":62,"verse":"So is this welcome better, or the tree of Zaqqum?"},"3851":{"id":3851,"surah":37,"ayah":63,"verse":"We have indeed made that a punishment for the unjust."},"3852":{"id":3852,"surah":37,"ayah":64,"verse":"Indeed it is a tree that sprouts from the base of hell."},"3853":{"id":3853,"surah":37,"ayah":65,"verse":"Its fruit like the heads of demons."},"3854":{"id":3854,"surah":37,"ayah":66,"verse":"So indeed they will eat from it, and fill their bellies with it."},"3855":{"id":3855,"surah":37,"ayah":67,"verse":"Then after it, indeed for them is the drink of boiling hot water."},"3856":{"id":3856,"surah":37,"ayah":68,"verse":"Then surely their return is towards hell."},"3857":{"id":3857,"surah":37,"ayah":69,"verse":"They had indeed found their forefathers astray."},"3858":{"id":3858,"surah":37,"ayah":70,"verse":"So they hastily follow their footsteps!"},"3859":{"id":3859,"surah":37,"ayah":71,"verse":"And indeed before them, most of the former people went astray."},"3860":{"id":3860,"surah":37,"ayah":72,"verse":"And indeed We had sent Heralds of warnings among them."},"3861":{"id":3861,"surah":37,"ayah":73,"verse":"Therefore see what sort of fate befell those who were warned!"},"3862":{"id":3862,"surah":37,"ayah":74,"verse":"Except the chosen bondmen of Allah."},"3863":{"id":3863,"surah":37,"ayah":75,"verse":"And indeed Nooh prayed to Us \u2013 so what an excellent Acceptor of Prayer We are!"},"3864":{"id":3864,"surah":37,"ayah":76,"verse":"And We rescued him and his household from the great calamity."},"3865":{"id":3865,"surah":37,"ayah":77,"verse":"And We preserved his descendants."},"3866":{"id":3866,"surah":37,"ayah":78,"verse":"And We kept his praise among the latter generations."},"3867":{"id":3867,"surah":37,"ayah":79,"verse":"Peace be upon Nooh, among the entire people."},"3868":{"id":3868,"surah":37,"ayah":80,"verse":"This is how We reward the virtuous."},"3869":{"id":3869,"surah":37,"ayah":81,"verse":"He is indeed one of Our high ranking, firmly believing bondmen."},"3870":{"id":3870,"surah":37,"ayah":82,"verse":"We then drowned the others."},"3871":{"id":3871,"surah":37,"ayah":83,"verse":"And indeed Ibrahim is from his (Nooh\u2019s) group."},"3872":{"id":3872,"surah":37,"ayah":84,"verse":"When he came to his Lord, with a sound heart. (Free from falsehood)."},"3873":{"id":3873,"surah":37,"ayah":85,"verse":"When he said to his father (paternal uncle) and his people, \u201cWhat do you worship?\u201d"},"3874":{"id":3874,"surah":37,"ayah":86,"verse":"\u201cWhat! You desire, through fabrication, Gods other than Allah?\u201d"},"3875":{"id":3875,"surah":37,"ayah":87,"verse":"\u201cSo what do you assume regarding the Lord Of The Creation?\u201d (That He will not punish you?)"},"3876":{"id":3876,"surah":37,"ayah":88,"verse":"He then shot a glance at the stars."},"3877":{"id":3877,"surah":37,"ayah":89,"verse":"He then said, \u201cI feel sick (of you)!\u201d"},"3878":{"id":3878,"surah":37,"ayah":90,"verse":"And they turned their backs on him and went away. (The pagans thought he would transmit the disease)."},"3879":{"id":3879,"surah":37,"ayah":91,"verse":"He then sneaked upon their deities and said, \u201cDo you not eat?\u201d"},"3880":{"id":3880,"surah":37,"ayah":92,"verse":"\u201cWhat is the matter with you, that you do not say anything?\u201d"},"3881":{"id":3881,"surah":37,"ayah":93,"verse":"He then began striking them with his right hand, unseen by the people."},"3882":{"id":3882,"surah":37,"ayah":94,"verse":"So the disbelievers came running towards him."},"3883":{"id":3883,"surah":37,"ayah":95,"verse":"He said, \u201cWhat! You worship what you yourselves have sculpted?\u201d"},"3884":{"id":3884,"surah":37,"ayah":96,"verse":"\u201cWhereas Allah has created you and your actions?\u201d"},"3885":{"id":3885,"surah":37,"ayah":97,"verse":"They said, \u201cConstruct a building (furnace) for him, and then cast him in the blazing fire!\u201d"},"3886":{"id":3886,"surah":37,"ayah":98,"verse":"So they tried to execute their evil scheme upon him \u2013 We therefore degraded them. (Allah saved him, by commanding the fire to turn cool)."},"3887":{"id":3887,"surah":37,"ayah":99,"verse":"And he said, \u201cIndeed I shall go to my Lord Who will guide me.\u201d"},"3888":{"id":3888,"surah":37,"ayah":100,"verse":"\u201cMy Lord! Give me a meritorious child.\u201d"},"3889":{"id":3889,"surah":37,"ayah":101,"verse":"We therefore gave him the glad tidings of an intelligent son."},"3890":{"id":3890,"surah":37,"ayah":102,"verse":"And when he became capable of working with him, Ibrahim said, \u201cO my son, I dreamt that I am sacrificing you \u2013 therefore now consider what is your opinion\u201d; he said, \u201cO my father! Do what you are commanded! Allah willing, you will soon find me patiently enduring!\u201d"},"3891":{"id":3891,"surah":37,"ayah":103,"verse":"Then (remember) when they both submitted to Allah\u2019s command, and Ibrahim lay his son facing downwards. (The knife did not hurt Ismail)"},"3892":{"id":3892,"surah":37,"ayah":104,"verse":"And We called out to him, \u201cO Ibrahim!\u201d"},"3893":{"id":3893,"surah":37,"ayah":105,"verse":"\u201cYou have indeed made the dream come true\u201d; and this is how We reward the virtuous."},"3894":{"id":3894,"surah":37,"ayah":106,"verse":"Indeed this was a clear test."},"3895":{"id":3895,"surah":37,"ayah":107,"verse":"And We rescued him in exchange of a great sacrifice. (The sacrifice of Ibrahim and Ismail \u2013 peace be upon them \u2013 is commemorated every year on 10, 11 and 12 Zil Haj)."},"3896":{"id":3896,"surah":37,"ayah":108,"verse":"And We kept his praise among the latter generations."},"3897":{"id":3897,"surah":37,"ayah":109,"verse":"Peace be upon Ibrahim!"},"3898":{"id":3898,"surah":37,"ayah":110,"verse":"This is how We reward the virtuous."},"3899":{"id":3899,"surah":37,"ayah":111,"verse":"He is indeed one of Our high ranking, firmly believing bondmen."},"3900":{"id":3900,"surah":37,"ayah":112,"verse":"And We gave him the glad tidings of Ishaq, a Herald of the Hidden, from among those who deserve Our proximity."},"3901":{"id":3901,"surah":37,"ayah":113,"verse":"And We sent blessings upon him and Ishaq; and among their descendants \u2013 some who do good deeds, and some who clearly wrong themselves."},"3902":{"id":3902,"surah":37,"ayah":114,"verse":"And We indeed did a great favour to Moosa and Haroon."},"3903":{"id":3903,"surah":37,"ayah":115,"verse":"And rescued them and their people from the great calamity."},"3904":{"id":3904,"surah":37,"ayah":116,"verse":"And We helped them, so they were victorious."},"3905":{"id":3905,"surah":37,"ayah":117,"verse":"And We bestowed the clear Book to both of them."},"3906":{"id":3906,"surah":37,"ayah":118,"verse":"And guided them to the Straight Path."},"3907":{"id":3907,"surah":37,"ayah":119,"verse":"And We kept their praise among the latter generations."},"3908":{"id":3908,"surah":37,"ayah":120,"verse":"Peace be upon Moosa and Haroon!"},"3909":{"id":3909,"surah":37,"ayah":121,"verse":"This is how We reward the virtuous."},"3910":{"id":3910,"surah":37,"ayah":122,"verse":"Indeed they are two of Our high ranking, firmly believing bondmen."},"3911":{"id":3911,"surah":37,"ayah":123,"verse":"And indeed Ilyas is one of the Noble Messengers."},"3912":{"id":3912,"surah":37,"ayah":124,"verse":"When he said to his people, \u201cDo you not fear?\u201d"},"3913":{"id":3913,"surah":37,"ayah":125,"verse":"\u201cWhat! You worship Baal (an idol) and leave the Best Creator?\u201d \u2013"},"3914":{"id":3914,"surah":37,"ayah":126,"verse":"\u201cAllah \u2013 Who is your Lord and the Lord of your forefathers?\u201d"},"3915":{"id":3915,"surah":37,"ayah":127,"verse":"In response they denied him, so they will surely be brought forth as captives."},"3916":{"id":3916,"surah":37,"ayah":128,"verse":"Except the chosen bondmen of Allah."},"3917":{"id":3917,"surah":37,"ayah":129,"verse":"And We kept his praise among the latter generations."},"3918":{"id":3918,"surah":37,"ayah":130,"verse":"Peace be upon Ilyas!"},"3919":{"id":3919,"surah":37,"ayah":131,"verse":"This is how We reward the virtuous."},"3920":{"id":3920,"surah":37,"ayah":132,"verse":"He is indeed one of Our high ranking, firmly believing bondmen."},"3921":{"id":3921,"surah":37,"ayah":133,"verse":"And indeed Lut is one of the Noble Messengers."},"3922":{"id":3922,"surah":37,"ayah":134,"verse":"When We rescued him and his entire household."},"3923":{"id":3923,"surah":37,"ayah":135,"verse":"Except an old woman, who became of those who stayed behind."},"3924":{"id":3924,"surah":37,"ayah":136,"verse":"We then destroyed the others."},"3925":{"id":3925,"surah":37,"ayah":137,"verse":"And indeed you pass over them in the morning. \u2013"},"3926":{"id":3926,"surah":37,"ayah":138,"verse":"And during the night; so do you not have sense?"},"3927":{"id":3927,"surah":37,"ayah":139,"verse":"And indeed Yunus is one of the Noble Messengers."},"3928":{"id":3928,"surah":37,"ayah":140,"verse":"When he left towards the laden ship."},"3929":{"id":3929,"surah":37,"ayah":141,"verse":"Then lots were drawn and he became of those who were pushed into the sea."},"3930":{"id":3930,"surah":37,"ayah":142,"verse":"The fish then swallowed him and he blamed himself. (For not waiting for Allah\u2019s command.)"},"3931":{"id":3931,"surah":37,"ayah":143,"verse":"And were he not one of those who praise. \u2013"},"3932":{"id":3932,"surah":37,"ayah":144,"verse":"He would have remained in its belly till the day when all will be raised."},"3933":{"id":3933,"surah":37,"ayah":145,"verse":"We then put him ashore on a plain, and he was sick."},"3934":{"id":3934,"surah":37,"ayah":146,"verse":"And We grew a tree of gourd (as a shelter) above him."},"3935":{"id":3935,"surah":37,"ayah":147,"verse":"And We sent him towards a hundred thousand people, in fact more."},"3936":{"id":3936,"surah":37,"ayah":148,"verse":"So they accepted faith \u2013 We therefore gave them usage for a while."},"3937":{"id":3937,"surah":37,"ayah":149,"verse":"Therefore ask them (O dear Prophet Mohammed \u2013 peace and blessings be upon him), whether the daughters are for your Lord and the sons for them!"},"3938":{"id":3938,"surah":37,"ayah":150,"verse":"Or that have We created the angels as females, while they were present?"},"3939":{"id":3939,"surah":37,"ayah":151,"verse":"Pay heed! It is their slander that they say. \u2013"},"3940":{"id":3940,"surah":37,"ayah":152,"verse":"That \u201cAllah has offspring\u201d; and indeed, surely, they are liars."},"3941":{"id":3941,"surah":37,"ayah":153,"verse":"\u201cHas he chosen daughters instead of sons?\u201d"},"3942":{"id":3942,"surah":37,"ayah":154,"verse":"\u201cWhat is the matter with you? What sort of a judgement you impose!\u201d"},"3943":{"id":3943,"surah":37,"ayah":155,"verse":"\u201cSo do you not ponder?\u201d"},"3944":{"id":3944,"surah":37,"ayah":156,"verse":"\u201cOr do you have some clear proof?\u201d"},"3945":{"id":3945,"surah":37,"ayah":157,"verse":"\u201cThen bring forth your Book, if you are truthful!\u201d"},"3946":{"id":3946,"surah":37,"ayah":158,"verse":"And they have appointed a relationship between Him and the jinns; and indeed the jinns surely know that they will be brought forth."},"3947":{"id":3947,"surah":37,"ayah":159,"verse":"Purity is to Allah from the matters they fabricate."},"3948":{"id":3948,"surah":37,"ayah":160,"verse":"Except the chosen bondmen of Allah."},"3949":{"id":3949,"surah":37,"ayah":161,"verse":"Therefore you and all what you worship. (The disbelievers and their deities.)"},"3950":{"id":3950,"surah":37,"ayah":162,"verse":"You cannot make anyone rebel against Him."},"3951":{"id":3951,"surah":37,"ayah":163,"verse":"Except the one who will go into the blazing fire."},"3952":{"id":3952,"surah":37,"ayah":164,"verse":"And the angels say, \u201cEach one of us has an appointed known position.\u201d"},"3953":{"id":3953,"surah":37,"ayah":165,"verse":"\u201cAnd indeed we, with our wings spread, await the command.\u201d"},"3954":{"id":3954,"surah":37,"ayah":166,"verse":"\u201cAnd indeed we are those who say His purity.\u201d"},"3955":{"id":3955,"surah":37,"ayah":167,"verse":"And indeed the disbelievers used to say, \u2013"},"3956":{"id":3956,"surah":37,"ayah":168,"verse":"\u201cIf we had some advice from the earlier generations,\u201d \u2013"},"3957":{"id":3957,"surah":37,"ayah":169,"verse":"\u201cWe would certainly be the chosen bondmen of Allah.\u201d"},"3958":{"id":3958,"surah":37,"ayah":170,"verse":"They therefore denied it, so they will soon come to know."},"3959":{"id":3959,"surah":37,"ayah":171,"verse":"And indeed Our Word has already gone forth for Our bondmen who were sent."},"3960":{"id":3960,"surah":37,"ayah":172,"verse":"That undoubtedly, only they will be helped."},"3961":{"id":3961,"surah":37,"ayah":173,"verse":"And surely, only Our army will be victorious."},"3962":{"id":3962,"surah":37,"ayah":174,"verse":"Therefore turn away from them for some time."},"3963":{"id":3963,"surah":37,"ayah":175,"verse":"And watch them, for they will soon see."},"3964":{"id":3964,"surah":37,"ayah":176,"verse":"So are they being impatient for Our punishment?"},"3965":{"id":3965,"surah":37,"ayah":177,"verse":"So when it does descend in their courtyards \u2013 so what an evil morning it will be for those who were warned!"},"3966":{"id":3966,"surah":37,"ayah":178,"verse":"And turn away from them for some time."},"3967":{"id":3967,"surah":37,"ayah":179,"verse":"And wait, for they will soon see."},"3968":{"id":3968,"surah":37,"ayah":180,"verse":"Purity is to your Lord, the Lord of Honour, from all what they say."},"3969":{"id":3969,"surah":37,"ayah":181,"verse":"And peace is upon the Noble Messengers."},"3970":{"id":3970,"surah":37,"ayah":182,"verse":"And all praise is to Allah, the Lord Of The Creation."},"3971":{"id":3971,"surah":38,"ayah":1,"verse":"Saad* \u2013 By oath of the renowned Qur\u2019an, (Alphabet of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"3972":{"id":3972,"surah":38,"ayah":2,"verse":"In fact, the disbelievers are in false pride and opposition."},"3973":{"id":3973,"surah":38,"ayah":3,"verse":"Many a generation We did destroy before them \u2013 thereupon they cried out whereas it is not the time to escape!"},"3974":{"id":3974,"surah":38,"ayah":4,"verse":"And they were surprised that a Herald of Warning came to them from among themselves; and the disbelievers said, \u201cHe is a magician, a great liar!\u201d"},"3975":{"id":3975,"surah":38,"ayah":5,"verse":"\u201cHas he made all the Gods into One God? This is really something very strange!\u201d"},"3976":{"id":3976,"surah":38,"ayah":6,"verse":"And their leaders went about, \u201cLeave him and cling steadfastly to your Gods! Indeed he has a hidden objective in this!\u201d"},"3977":{"id":3977,"surah":38,"ayah":7,"verse":"\u201cWe never heard of this even in Christianity, the latest religion; this is clearly a newly fabricated matter.\u201d"},"3978":{"id":3978,"surah":38,"ayah":8,"verse":"\u201cIs the Qur\u2019an which is sent to him, among us?\u201d In fact they are in a doubt concerning My Book; in fact, they have not yet tasted My punishment."},"3979":{"id":3979,"surah":38,"ayah":9,"verse":"Or do they hold the treasures of the mercy of your Lord, the Almighty, the Great Bestower?"},"3980":{"id":3980,"surah":38,"ayah":10,"verse":"Is the kingdom of the heavens and the earth and all that is between them, for them? So would they not just ascend using ropes?"},"3981":{"id":3981,"surah":38,"ayah":11,"verse":"This is just one of the disgraced armies, that will be routed there and then."},"3982":{"id":3982,"surah":38,"ayah":12,"verse":"Before them, the people of Nooh had denied, and the tribe of A\u2019ad, and Firaun who used to crucify."},"3983":{"id":3983,"surah":38,"ayah":13,"verse":"And the tribe of Thamud, and the people of Lut, and the People of the Woods; these are the groups."},"3984":{"id":3984,"surah":38,"ayah":14,"verse":"None of them was such that it did not deny the Noble Messengers, therefore My punishment became inevitable."},"3985":{"id":3985,"surah":38,"ayah":15,"verse":"They await just one Scream, which no one can avert."},"3986":{"id":3986,"surah":38,"ayah":16,"verse":"And they said, \u201cOur Lord! Give us our share quickly, before the Day of Reckoning.\u201d"},"3987":{"id":3987,"surah":38,"ayah":17,"verse":"Have patience upon what they say, and remember Our bondman Dawud, the one blessed with favours; he is indeed most inclined (towards His Lord)."},"3988":{"id":3988,"surah":38,"ayah":18,"verse":"Indeed We subjected the hills to say the praise with him, at night and at morn."},"3989":{"id":3989,"surah":38,"ayah":19,"verse":"And birds gathered together; they were all obedient to him."},"3990":{"id":3990,"surah":38,"ayah":20,"verse":"And We strengthened his kingdom and gave him wisdom and just speech."},"3991":{"id":3991,"surah":38,"ayah":21,"verse":"And did the news of the two disputants reach you? When they scaled over the wall into Dawud\u2019s mosque."},"3992":{"id":3992,"surah":38,"ayah":22,"verse":"When they entered upon David, so he feared them \u2013 they said, \u201cDo not fear! We are two disputants, one of whom has wronged the other, therefore judge fairly between us and do not judge unjustly \u2013 and show us the right way.\u201d"},"3993":{"id":3993,"surah":38,"ayah":23,"verse":"\u201cThis is my brother; he has ninety nine ewes and I have one ewe; and he now says \u2018Give that one also to me\u2019 \u2013 and he is very demanding in speech.\u201d"},"3994":{"id":3994,"surah":38,"ayah":24,"verse":"Said Dawud, \u201cHe is indeed being unjust to you in that he demands to add your ewe to his ewes; and indeed most partners wrong one another, except those who believe and do good deeds \u2013 and they are very few!\u201d Thereupon Dawud realised that We had tested him, so he sought forgiveness from his Lord, and fell prostrate and inclined (towards his Lord). (Command of Prostration # 10)"},"3995":{"id":3995,"surah":38,"ayah":25,"verse":"We therefore forgave him this; and indeed for him in Our presence are, surely, proximity and an excellent abode."},"3996":{"id":3996,"surah":38,"ayah":26,"verse":"\u201cO Dawud! We have indeed appointed you as a Viceroy in the earth, therefore judge between mankind with the truth, and do not follow desire for it will lead you astray from Allah\u2019s path; indeed for those who stray away from Allah\u2019s path is a severe punishment, because they forgot the Day of Reckoning.\u201d"},"3997":{"id":3997,"surah":38,"ayah":27,"verse":"And We have not created the heaven and the earth and all that is between them without purpose; this is what the disbelievers assume; therefore ruin is for the disbelievers, by the fire."},"3998":{"id":3998,"surah":38,"ayah":28,"verse":"Shall We make those who believe and do good deeds equal to those who spread turmoil in the earth? Or shall We equate the pious with the disobedient?"},"3999":{"id":3999,"surah":38,"ayah":29,"verse":"This is a Book which We have sent down upon you, a blessed Book, for them to ponder upon its verses, and for men of intellect to accept advice."},"4000":{"id":4000,"surah":38,"ayah":30,"verse":"And We bestowed Sulaiman to Dawud; what an excellent bondman! He is indeed most inclined."},"4001":{"id":4001,"surah":38,"ayah":31,"verse":"When fast footed steeds were presented to him at evening."},"4002":{"id":4002,"surah":38,"ayah":32,"verse":"Therefore Sulaiman said, \u201cI cherish the love of these horses*, out of remembrance of my Lord\u201d; he then ordered them to be raced until they vanished in a curtain out of sight. (To be used in holy war.)"},"4003":{"id":4003,"surah":38,"ayah":33,"verse":"He then ordered, \u201cBring them back to me\u201d; and he began caressing their shins and necks."},"4004":{"id":4004,"surah":38,"ayah":34,"verse":"And We indeed tested Sulaiman, and placed a dead body on his throne \u2013 he therefore inclined towards His Lord."},"4005":{"id":4005,"surah":38,"ayah":35,"verse":"He said, \u201cMy Lord! Forgive me and bestow upon me a kingdom, which shall not befit anyone after me; indeed only You are the Great Bestower.\u201d"},"4006":{"id":4006,"surah":38,"ayah":36,"verse":"We therefore gave the wind under his control, moving steadily by his command wherever he wished."},"4007":{"id":4007,"surah":38,"ayah":37,"verse":"And made the demons subservient to him, all builders and divers."},"4008":{"id":4008,"surah":38,"ayah":38,"verse":"And other demons bound in chains."},"4009":{"id":4009,"surah":38,"ayah":39,"verse":"\u201cThis is Our bestowal \u2013 you may therefore bestow favours or withhold them \u2013 you will not be questioned.\u201d"},"4010":{"id":4010,"surah":38,"ayah":40,"verse":"And indeed for him in Our presence are, surely, proximity and an excellent abode."},"4011":{"id":4011,"surah":38,"ayah":41,"verse":"And remember Our bondman Ayyub (Job); when he cried out* to his Lord, \u201cThe devil has struck me with hardship and pain.\u201d (After seven years of patience.)"},"4012":{"id":4012,"surah":38,"ayah":42,"verse":"We said to him, \u201cStrike the earth with your foot; this cool spring is for bathing and drinking.\u201d (A spring of gushed forth when he struck the earth \u2013 this was a miracle.)"},"4013":{"id":4013,"surah":38,"ayah":43,"verse":"And We bestowed his household to him and one more similar to it \u2013 as a mercy from Us, and as a remembrance for the people of intellect."},"4014":{"id":4014,"surah":38,"ayah":44,"verse":"And We said, \u201cTake a broom in your hand and strike her with it, and do not break your vow\u201d; We indeed found him patiently enduring; what an excellent bondman! He is indeed most inclined."},"4015":{"id":4015,"surah":38,"ayah":45,"verse":"And remember Our bondmen Ibrahim, and Ishaq, and Yaqub \u2013 the men of power and knowledge."},"4016":{"id":4016,"surah":38,"ayah":46,"verse":"We indeed gave them distinction with a genuine affair \u2013 the remembrance of the (everlasting) abode."},"4017":{"id":4017,"surah":38,"ayah":47,"verse":"And in Our sight, they are indeed the chosen ones, the beloved."},"4018":{"id":4018,"surah":38,"ayah":48,"verse":"And remember Ismail and Yasa\u2019a (Elisha) and Zul-Kifl; and they are all excellent."},"4019":{"id":4019,"surah":38,"ayah":49,"verse":"This is an advice; and indeed for the pious is an excellent abode."},"4020":{"id":4020,"surah":38,"ayah":50,"verse":"Everlasting Gardens \u2013 all its gates are open for them."},"4021":{"id":4021,"surah":38,"ayah":51,"verse":"Reclining on pillows, in it they ask for fruits and drinks in plenty."},"4022":{"id":4022,"surah":38,"ayah":52,"verse":"And with them are the pure spouses, who do not set gaze upon men except their husbands, of single age."},"4023":{"id":4023,"surah":38,"ayah":53,"verse":"This is the promise being given to you, for the Day of Reckoning."},"4024":{"id":4024,"surah":38,"ayah":54,"verse":"Indeed this is Our sustenance, which will never end."},"4025":{"id":4025,"surah":38,"ayah":55,"verse":"This is for the virtuous; and indeed for the rebellious is a wretched destination."},"4026":{"id":4026,"surah":38,"ayah":56,"verse":"Hell; which they shall enter; what an evil resting-place!"},"4027":{"id":4027,"surah":38,"ayah":57,"verse":"This is for the criminals \u2013 so that they may taste it \u2013 boiling hot water and pus."},"4028":{"id":4028,"surah":38,"ayah":58,"verse":"And similar other punishments in pairs."},"4029":{"id":4029,"surah":38,"ayah":59,"verse":"\u201cHere is another group that was with you, falling along with you\u201d; they will answer, \u201cDo not give them plenty of open space; they surely have to enter the fire \u2013 let them also be confined!\u201d"},"4030":{"id":4030,"surah":38,"ayah":60,"verse":"The followers will say, \u201cIn fact, for you! May you not get open space! It is you who brought this calamity upon us!\u201d So what a wretched destination."},"4031":{"id":4031,"surah":38,"ayah":61,"verse":"They say, \u201cOur Lord! Whoever has brought this calamity upon us \u2013 double the punishment of the fire for him!\u201d"},"4032":{"id":4032,"surah":38,"ayah":62,"verse":"And they say, \u201cWhat is the matter with us that we do not see the men whom we thought were evil?\u201d"},"4033":{"id":4033,"surah":38,"ayah":63,"verse":"\u201cDid we mock at them or did our eyes turn away from them?\u201d"},"4034":{"id":4034,"surah":38,"ayah":64,"verse":"Indeed this is really true \u2013 the people of the hell quarrelling among themselves."},"4035":{"id":4035,"surah":38,"ayah":65,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI am purely a Herald of Warning \u2013 and there is no God except Allah, the One, the All Dominant.\u201d"},"4036":{"id":4036,"surah":38,"ayah":66,"verse":"\u201cLord of the heavens and the earth and all that is between them \u2013 the Almighty, the Oft Forgiving.\u201d"},"4037":{"id":4037,"surah":38,"ayah":67,"verse":"Say, \u201cThat is a great tidings.\u201d"},"4038":{"id":4038,"surah":38,"ayah":68,"verse":"\u201cYou are neglectful of it!\u201d"},"4039":{"id":4039,"surah":38,"ayah":69,"verse":"\u201cWhat did I know of the heavenly world, when the angels had disputed.\u201d"},"4040":{"id":4040,"surah":38,"ayah":70,"verse":"\u201cI receive only the divine revelations, that I am purely a clear Herald of Warning.\u201d"},"4041":{"id":4041,"surah":38,"ayah":71,"verse":"(Remember) When your Lord said to the angels, \u201cI will create a human from clay.\u201d \u2013"},"4042":{"id":4042,"surah":38,"ayah":72,"verse":"\u201cSo when I have perfected him and breathed into him a spirit from Myself, (you all) fall down before him in prostration.\u201d"},"4043":{"id":4043,"surah":38,"ayah":73,"verse":"So all the angels prostrated, every one, without exception."},"4044":{"id":4044,"surah":38,"ayah":74,"verse":"Except Iblis; he was proud and was, from the beginning, a disbeliever."},"4045":{"id":4045,"surah":38,"ayah":75,"verse":"Said Allah, \u201cO Iblis! What prevented you from prostrating before one whom I have created with My hands*? Have you become proud or were you haughty from the beginning?\u201d (Used as a metaphor)."},"4046":{"id":4046,"surah":38,"ayah":76,"verse":"Said Iblis, \u201cI am better than him; You made me from fire, and You have created him from clay!\u201d"},"4047":{"id":4047,"surah":38,"ayah":77,"verse":"He said, \u201cTherefore exit from heaven, for you have been outcast.\u201d (To disrespect the Prophets \u2013 peace and blessings be upon them \u2013 is blasphemy.)"},"4048":{"id":4048,"surah":38,"ayah":78,"verse":"\u201cAnd indeed My curse is upon you till the Day of Judgement.\u201d"},"4049":{"id":4049,"surah":38,"ayah":79,"verse":"He said, \u201cMy Lord! Therefore give me respite till the day when all will be raised.\u201d"},"4050":{"id":4050,"surah":38,"ayah":80,"verse":"Said Allah, \u201cYou are therefore among those given respite.\u201d"},"4051":{"id":4051,"surah":38,"ayah":81,"verse":"\u201cUntil the time of the known day.\u201d"},"4052":{"id":4052,"surah":38,"ayah":82,"verse":"He said, \u201cTherefore, by oath of Your honour, I will surely mislead all of them.\u201d"},"4053":{"id":4053,"surah":38,"ayah":83,"verse":"\u201cExcept Your chosen bondmen among them.\u201d"},"4054":{"id":4054,"surah":38,"ayah":84,"verse":"Said Allah, \u201cSo this is the truth; and I speak only the truth.\u201d \u2013"},"4055":{"id":4055,"surah":38,"ayah":85,"verse":"\u201cThat I will fill hell with you and with those among them who follow you, all together.\u201d"},"4056":{"id":4056,"surah":38,"ayah":86,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u201cI do not ask any fee from you for the Qur\u2019an, and I am not a fabricator.\u201d"},"4057":{"id":4057,"surah":38,"ayah":87,"verse":"\u201cIt is not but an advice for the entire world.\u201d"},"4058":{"id":4058,"surah":38,"ayah":88,"verse":"\u201cAnd you will come to know of its tidings, after a while.\u201d"},"4059":{"id":4059,"surah":39,"ayah":1,"verse":"The revelation of the Book is from Allah, the Most Honourable, the Wise."},"4060":{"id":4060,"surah":39,"ayah":2,"verse":"We have indeed divinely revealed the Book to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) with the truth, therefore worship Allah, as His sincere bondman."},"4061":{"id":4061,"surah":39,"ayah":3,"verse":"Pay heed! Worship is for Allah only; and those who have taken others as their supporters beside Him say; \u201cWe worship* them only so that they get us closer to Allah\u201d; Allah will surely judge between them regarding the matter in which they dispute; indeed Allah does not guide one who is a big liar, extremely ungrateful. (*The pagans regarded their idols as smaller Gods and worshipped them. So did the Christians. This does not apply to Muslims who only respect their elders, and ask for their blessings.)"},"4062":{"id":4062,"surah":39,"ayah":4,"verse":"Were Allah to create a son* for Himself, He would have chosen any one from His creation! Purity is to Him! He is Allah, the One, the All Dominant. (*Which is impossible.)"},"4063":{"id":4063,"surah":39,"ayah":5,"verse":"It is He Who created the heavens and the earth with the truth; He envelops the night over day and envelops the day over night, and He has subjected the sun and the moon; each one runs for its appointed term; pay heed! He only is the Most Honourable, the Oft Forgiving."},"4064":{"id":4064,"surah":39,"ayah":6,"verse":"It is He Who created you from a single being, and then from the same being created its spouse, and sent down for you eight pairs of animals; He creates you in your mothers\u2019 wombs, from one sort to another, in a triple darkness; such is Allah, your Lord \u2013 for Him only is the kingship; there is no God except Him; so where are you being turned away?"},"4065":{"id":4065,"surah":39,"ayah":7,"verse":"If you become ungrateful, then (know that) indeed Allah is Independent of you; and He does not like the ungratefulness of His bondmen; and if you give thanks, He is pleased with it for you; and no burdened soul will bear another soul\u2019s burden; you have then to return towards your Lord \u2013 He will therefore inform you of what you used to do; undoubtedly, He knows what lies within the hearts."},"4066":{"id":4066,"surah":39,"ayah":8,"verse":"And when some hardship strikes man, he prays to his Lord, inclined only towards Him \u2013 then when Allah grants him a favour from Himself, he forgets why he had prayed to Him earlier, and sets up equals to Allah in order to mislead from Allah\u2019s way; proclaim, \u201cRevel in your disbelief for some days; you are indeed of the people of the fire.\u201d"},"4067":{"id":4067,"surah":39,"ayah":9,"verse":"Will he, whose night hours pass in obedience while prostrating and standing, fearing the Hereafter and hoping for the mercy of his Lord, ever be equal to the disobedient? Proclaim, \u201cAre the knowledgeable and the ignorant equal?\u201d It is only the men of intellect who heed advice."},"4068":{"id":4068,"surah":39,"ayah":10,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO my slaves who have accepted faith! Fear your Lord; for those who do good deeds in this world is goodness (in return); and Allah\u2019s earth is spacious; it is the steadfast who will be paid their full reward, without account.\u201d"},"4069":{"id":4069,"surah":39,"ayah":11,"verse":"Say, \u201cI am commanded that I must worship Allah as His sincere bondman.\u201d (And so must you too.)"},"4070":{"id":4070,"surah":39,"ayah":12,"verse":"\u201cAnd I am commanded that I be the first to submit.\u201d"},"4071":{"id":4071,"surah":39,"ayah":13,"verse":"Say, \u201cWere I to disobey my Lord, I too would fear the punishment of the great Day (of Resurrection).\u201d"},"4072":{"id":4072,"surah":39,"ayah":14,"verse":"Proclaim, \u201cI worship only Allah, as His sincere bondman.\u201d"},"4073":{"id":4073,"surah":39,"ayah":15,"verse":"\u201cTherefore you can worship what you want besides Him!\u201d Say, \u201cThe worst losers are those who will lose themselves and their household on the Day of Resurrection; pay heed! This is the plain loss!\u201d"},"4074":{"id":4074,"surah":39,"ayah":16,"verse":"Above them are mountains of fire and beneath them are mountains of fire; Allah scares His bondmen with this; \u201cO My bondmen! Fear Me!\u201d"},"4075":{"id":4075,"surah":39,"ayah":17,"verse":"And those who stayed away from the worship of idols, and inclined towards Allah \u2013 for them are glad tidings; therefore give glad tidings to My bondmen. \u2013"},"4076":{"id":4076,"surah":39,"ayah":18,"verse":"Those who heed attentively and follow the best from it; it is these whom Allah has guided, and it is these who have intelligence."},"4077":{"id":4077,"surah":39,"ayah":19,"verse":"So will the one upon whom the Word of punishment has proved true, ever be equal to those who are forgiven? So will you guide and save the one who deserves the fire? (Because the hearts of some disbelievers are sealed)."},"4078":{"id":4078,"surah":39,"ayah":20,"verse":"But for those who fear their Lord, are the high chambers with more high chambers built above them \u2013 rivers flowing beneath them; a promise of Allah; Allah does not renege on His promise."},"4079":{"id":4079,"surah":39,"ayah":21,"verse":"Did you not see that it is Allah Who sent down water from the sky, and then with it made springs in the earth, and then with it produces crops of various colours, and then it dries up and you see it has become yellow, and He then fragments them into small pieces; indeed in this is a reminder for people of intellect."},"4080":{"id":4080,"surah":39,"ayah":22,"verse":"So will he whose bosom Allah has opened up for Islam \u2013 he is therefore upon a light from his Lord \u2013 ever be equal to one who is stone hearted? Therefore ruin is for those whose hearts are hardened towards the remembrance of Allah; they are in open error."},"4081":{"id":4081,"surah":39,"ayah":23,"verse":"Allah has sent down the best of Books (the Holy Qur\u2019an), which is consistent throughout, the one with paired statements; the hairs on the skins of those who fear their Lord, stand on end with it; then their skins and their hearts soften, inclined towards the remembrance of Allah; this is the guidance of Allah, He may guide whomever He wills with it; and whomever Allah sends astray, there is no guide for him."},"4082":{"id":4082,"surah":39,"ayah":24,"verse":"So will he who will not have a shield except his own face against the wretched punishment on the Day of Resurrection, ever be equal to one who is forgiven? And it will be said to the unjust, \u201cTaste what you have earned!\u201d"},"4083":{"id":4083,"surah":39,"ayah":25,"verse":"Those before them had denied, therefore the punishment came to them from a place they did not know."},"4084":{"id":4084,"surah":39,"ayah":26,"verse":"So Allah made them taste humiliation in the life of this world; and indeed the punishment of the Hereafter is the greatest; if only they knew!"},"4085":{"id":4085,"surah":39,"ayah":27,"verse":"And We have indeed illustrated examples of all kinds in this Qur\u2019an, so that they may ponder."},"4086":{"id":4086,"surah":39,"ayah":28,"verse":"The Qur\u2019an in Arabic, having no deviation at all, so that they may be pious."},"4087":{"id":4087,"surah":39,"ayah":29,"verse":"Allah illustrates an example \u2013 \u201cA man having several wicked masters as partners, and another man belonging wholly to just one master; are the two equal in comparison?\u201d All praise is to Allah; in fact, most among them do not know."},"4088":{"id":4088,"surah":39,"ayah":30,"verse":"Indeed you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) will taste death, and they (the disbelievers) too will die."},"4089":{"id":4089,"surah":39,"ayah":31,"verse":"Then you will dispute before your Lord on the Day of Resurrection."},"4090":{"id":4090,"surah":39,"ayah":32,"verse":"So who is more unjust than one who fabricates a lie against Allah, and denies the truth when it comes to him? Is not the destination of disbelievers in hell?"},"4091":{"id":4091,"surah":39,"ayah":33,"verse":"And those who brought this truth and those who testify for it \u2013 these are the pious."},"4092":{"id":4092,"surah":39,"ayah":34,"verse":"For them is whatever they wish, before their Lord; this is the reward of the virtuous."},"4093":{"id":4093,"surah":39,"ayah":35,"verse":"So that Allah may relieve them of their worst deeds, and reward them for the best deeds they had done."},"4094":{"id":4094,"surah":39,"ayah":36,"verse":"Is not Allah Sufficient for His slave? And they threaten you with others beside Him! And whomever Allah sends astray \u2013 there is no guide for him."},"4095":{"id":4095,"surah":39,"ayah":37,"verse":"And no one can mislead whomever Allah guides; is not Allah the Most Honourable, the Avenger?"},"4096":{"id":4096,"surah":39,"ayah":38,"verse":"And if you ask them, \u201cWho has created the heavens and the earth?\u201d, they will surely say, \u201cAllah\u201d; say, \u201cWhat is your opinion regarding those whom you worship other than Allah \u2013 if Allah wills to cause me some hardship, so will they avert the hardship sent by Him? Or if He wills to have mercy upon me, so will they restrain His mercy?\u201d Proclaim, \u201cAllah is Sufficient for me; the people who trust must rely only upon Him.\u201d"},"4097":{"id":4097,"surah":39,"ayah":39,"verse":"Proclaim, \u201cO my people! Keep on with your works in your positions, I am doing mine; so you will soon come to know.\u201d \u2013"},"4098":{"id":4098,"surah":39,"ayah":40,"verse":"\u201cTo whom will come a punishment that will disgrace, and upon whom descends the punishment that never ends.\u201d"},"4099":{"id":4099,"surah":39,"ayah":41,"verse":"We have indeed sent down this Book to you with the truth, in order to guide mankind; so whoever attains guidance has attained it is for his own good, and whoever strays, has strayed for his own harm; and you are not at all responsible for them."},"4100":{"id":4100,"surah":39,"ayah":42,"verse":"It is Allah Who gives death* to living beings at the time of their demise, and to those who do not die, during their sleep; so He restrains the soul on which the decree of death has been passed, and leaves the other till the appointed term; indeed in this are signs for people who reflect. (*Death is of 2 types \u2013 passing to the next world, and sleeping.)"},"4101":{"id":4101,"surah":39,"ayah":43,"verse":"What! Have they chosen intercessors against Allah? Proclaim, \u201cWhat! Even if they do not own anything nor have any intelligence?\u201d"},"4102":{"id":4102,"surah":39,"ayah":44,"verse":"Proclaim, \u201cTo Allah only* belongs all the intercession! For Him only is the kingship of the heavens and the earth; and then it is towards you have to return.\u201d (*Allah does not intercede \u2013 He gives the permission to intercede with Him, to whomever He wills.)"},"4103":{"id":4103,"surah":39,"ayah":45,"verse":"And when Allah the One God is mentioned, the hearts of those who do not believe in the Hereafter get constricted; and when others (false deities) besides Him are mentioned, they rejoice!"},"4104":{"id":4104,"surah":39,"ayah":46,"verse":"Invoke (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO Allah! The Creator of the heavens and the earth, the All Knowing of all the hidden and the open, You will judge between Your bondmen, regarding the matters in which they differed.\u201d"},"4105":{"id":4105,"surah":39,"ayah":47,"verse":"And if the unjust owned all that is in the earth, and another one in addition to it, they would surely give it in exchange for freedom from the wretched punishment on the Day of Resurrection; and a matter which they had never imagined, appeared to them from their Lord."},"4106":{"id":4106,"surah":39,"ayah":48,"verse":"And the evils they earned appeared to them, and the thing they used to mock at descended upon them."},"4107":{"id":4107,"surah":39,"ayah":49,"verse":"So when some hardship reaches man, he prays to Us; then later when We bestow a favour upon him, he says, \u201cI obtained this only because of some knowledge\u201d; in fact it is a test, but most of them do not have any knowledge."},"4108":{"id":4108,"surah":39,"ayah":50,"verse":"Those before them also said the same, so their earnings did not benefit them at all."},"4109":{"id":4109,"surah":39,"ayah":51,"verse":"Therefore the evils of their earnings befell them; and those who are unjust among them \u2013 soon the evils of the earnings will befall them, and they cannot escape."},"4110":{"id":4110,"surah":39,"ayah":52,"verse":"Do they not know that Allah eases the sustenance for whomever He wills, and restricts it? Indeed in this are signs for the People who Believe."},"4111":{"id":4111,"surah":39,"ayah":53,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cO my* slaves, who have wronged themselves, do not lose hope in Allah\u2019s mercy; indeed Allah forgives all sins; indeed He only is the Oft Forgiving, the Most Merciful.\u201d (All Muslims are truly the slaves of Prophet Mohammed \u2013 peace and blessings be upon him.)"},"4112":{"id":4112,"surah":39,"ayah":54,"verse":"\u201cAnd incline towards your Lord and submit to Him, before the punishment comes you and then you may not be helped.\u201d"},"4113":{"id":4113,"surah":39,"ayah":55,"verse":"\u201cAnd follow this, the best among all, which has been sent down towards you from your Lord, before the punishment comes suddenly upon you whilst you are unaware.\u201d"},"4114":{"id":4114,"surah":39,"ayah":56,"verse":"So that no soul may say, \u201cAlas! The failures that I committed regarding Allah \u2013 and indeed I used to mock!\u201d"},"4115":{"id":4115,"surah":39,"ayah":57,"verse":"Or (so that no soul) may say, \u201cIf Allah had shown me the right path, I would then be among the pious!\u201d"},"4116":{"id":4116,"surah":39,"ayah":58,"verse":"Or (so that no soul) may say when it beholds the punishment, \u201cIf only I may be sent back, so that I may do good deeds!\u201d"},"4117":{"id":4117,"surah":39,"ayah":59,"verse":"It will be said to him, \u201cSurely yes, why not?* My signs did come to you, so you denied them and you were haughty and you were a disbeliever.\u201d (They will be mocked at.)"},"4118":{"id":4118,"surah":39,"ayah":60,"verse":"And you will see on the Day of Resurrection the fabricators of lies against Allah, with their faces blackened; is not the destination of the haughty in hell?"},"4119":{"id":4119,"surah":39,"ayah":61,"verse":"And Allah will rescue the pious, to their place of salvation; neither will the punishment touch them, nor shall they grieve."},"4120":{"id":4120,"surah":39,"ayah":62,"verse":"Allah is Creator of all things; and He only is the Controller of all things."},"4121":{"id":4121,"surah":39,"ayah":63,"verse":"For Him only are the keys of the heavens and the earth; and those who denied the signs of Allah \u2013 it is they who are the losers."},"4122":{"id":4122,"surah":39,"ayah":64,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u201cSo do you instruct me to worship (deities) other than Allah? You ignorant fools!\u201d"},"4123":{"id":4123,"surah":39,"ayah":65,"verse":"And it was indeed revealed to you and to those before you; that, \u201cO listener! If you ascribe a partner to Allah, all your deeds will go to waste and you will surely be a loser.\u201d"},"4124":{"id":4124,"surah":39,"ayah":66,"verse":"But rather worship only Allah, and be among the thankful!"},"4125":{"id":4125,"surah":39,"ayah":67,"verse":"And they did not realise the importance of Allah as was His right; and on the Day of Resurrection, He will compress the lands and the heavens will be rolled up by His power; Purity and Supremacy are to Him, from all what they ascribe as partners."},"4126":{"id":4126,"surah":39,"ayah":68,"verse":"And the Trumpet will be blown, so everyone in the heavens and everyone in the earth will fall unconscious, except whomever* Allah wills; it will then be blown again, thereupon they will get up staring! (*Prophet Moosa and \/ or some angels)."},"4127":{"id":4127,"surah":39,"ayah":69,"verse":"And the earth will shine bright by the light of its Lord, and the Book will be established, and the Prophets and this Noble Prophet and the witnesses upon them from this nation will be brought, and it will be judged between them with the truth, and they will not be wronged."},"4128":{"id":4128,"surah":39,"ayah":70,"verse":"And every soul will be repaid for its deeds in full, and He knows very well what they used to do."},"4129":{"id":4129,"surah":39,"ayah":71,"verse":"And the disbelievers will be herded towards hell in groups; to the extent that when they reach it, its gates will be opened, and its guards will say to them, \u201cDid not the Noble Messengers come to you from amongst you, who used to recite to you the verses of your Lord and warn you of confronting this Day?\u201d They will say, \u201cYes indeed, why not?\u201d But the Word of punishment proved true upon the disbelievers."},"4130":{"id":4130,"surah":39,"ayah":72,"verse":"It will be said, \u201cEnter the gates of hell to remain in it forever\u201d; so what a wretched destination for the haughty!"},"4131":{"id":4131,"surah":39,"ayah":73,"verse":"And the mounts of those who feared their Lord will be led towards Paradise, in groups; to the extent that when they reach it, its gates will be opened and its guards will say to them, \u201cPeace be upon you! You have done well! Therefore enter Paradise, to abide in it forever.\u201d"},"4132":{"id":4132,"surah":39,"ayah":74,"verse":"And they will say, \u201cAll praise is to Allah, Who has made His promise to us come true, and has bequeathed us this land, to stay in Paradise wherever we wish!\u201d So what an excellent reward for the performers!"},"4133":{"id":4133,"surah":39,"ayah":75,"verse":"And you will see the angels gathered around the Throne, saying the Purity of their Lord, with praise; and a true judgement will be delivered between the people, and it will be said, \u201cAll praise is to Allah, the Lord Of The Creation!\u201d"},"4134":{"id":4134,"surah":40,"ayah":1,"verse":"Ha-Meem. (Alphabets of the Arabic Language \u2013 Allah, and to whomever he reveals, know their precise meanings.)"},"4135":{"id":4135,"surah":40,"ayah":2,"verse":"The revelation of the Book is from Allah, the Most Honourable, the All Knowing."},"4136":{"id":4136,"surah":40,"ayah":3,"verse":"The Forgiver of sin, and the Most Acceptor of Repentance, the Severe in Punishing, the Greatly Rewarding; there is no God except Him; towards Him only is the return."},"4137":{"id":4137,"surah":40,"ayah":4,"verse":"None except the disbelievers dispute the signs of Allah, therefore do not let their free movements in the land deceive you."},"4138":{"id":4138,"surah":40,"ayah":5,"verse":"Before them, the people of Nooh and the groups after them had denied; and every nation intended to apprehend its Noble Messenger, and they fought along with falsehood to avert the truth, so I seized them; so how did My punishment turn out?"},"4139":{"id":4139,"surah":40,"ayah":6,"verse":"And this is how the Word of your Lord has proved true upon the disbelievers that they are people of the hell."},"4140":{"id":4140,"surah":40,"ayah":7,"verse":"Those* who bear the Throne, and those who are around it, say the Purity of their Lord while praising Him, and they believe in Him and seek forgiveness for the believers; \u201cO Our Lord! Your mercy and knowledge encompass all things, therefore forgive those who repented and followed Your path, and save them from the punishment of hell.\u201d (* The angels.)"},"4141":{"id":4141,"surah":40,"ayah":8,"verse":"\u201cO Our Lord! And admit them into the Gardens of everlasting stay which you have promised them, and those who are virtuous among their forefathers and their wives and their offspring; indeed You only are the Most Honourable, the Wise.\u201d"},"4142":{"id":4142,"surah":40,"ayah":9,"verse":"\u201cAnd save them from the evil consequences of sins; and whomever You save from the evil consequences of sins on that Day \u2013 so You have indeed had mercy upon him; and this only is the greatest success.\u201d"},"4143":{"id":4143,"surah":40,"ayah":10,"verse":"The disbelievers will indeed be called out to \u2013 \u201cIndeed Allah\u2019s disgust with you is greater than your own abhorrence of yourselves, whereas you used to deny when you were called towards the faith!\u201d"},"4144":{"id":4144,"surah":40,"ayah":11,"verse":"They will say, \u201cO Our Lord! Twice You have given us death and twice You have given us life* \u2013 we now confess to our sins \u2013 so is there a way out of the fire?\u201d (From nothingness to life in this world, to death and then Resurrection.)"},"4145":{"id":4145,"surah":40,"ayah":12,"verse":"\u201cThis has occurred because when Allah the One was prayed to, you used to disbelieve; and when a partner was ascribed with Him, you used to believe; so the command is only for Allah, the Supreme, the Great.\u201d"},"4146":{"id":4146,"surah":40,"ayah":13,"verse":"It is He Who shows you His signs, and sends down sustenance from the sky for you; and none accept guidance except those who incline (towards Him)."},"4147":{"id":4147,"surah":40,"ayah":14,"verse":"Therefore worship Allah, as His sincere bondmen even if the disbelievers get annoyed."},"4148":{"id":4148,"surah":40,"ayah":15,"verse":"Bestower of High Ranks, Owner of the Throne; it is He Who instils the spirit of faith into the one He wills among His bondmen, in order that he may warn of the Day of Meeting. \u2013"},"4149":{"id":4149,"surah":40,"ayah":16,"verse":"A day when they will be fully exposed; nothing from their affairs will be hidden from Allah; \u201cFor whom is the kingship this day? For Allah, the One, the All Dominant.\u201d"},"4150":{"id":4150,"surah":40,"ayah":17,"verse":"This day each soul will be repaid for what it has earned; no one will be wronged this day; indeed Allah is Swift At Taking Account."},"4151":{"id":4151,"surah":40,"ayah":18,"verse":"And warn them of the day of impending calamity, when hearts will rise up to the throats filled with grief; and the disbelievers will have neither any friend nor any intercessor who will be obeyed. (Intercession will be accepted only for the Muslims, not for the disbelievers)"},"4152":{"id":4152,"surah":40,"ayah":19,"verse":"Allah well knows the covert glance and all what lies hidden in the hearts."},"4153":{"id":4153,"surah":40,"ayah":20,"verse":"And Allah renders the true judgement; and those whom they worship instead of Him do not judge at all; indeed Allah only, is the All Hearing, the All Seeing."},"4154":{"id":4154,"surah":40,"ayah":21,"verse":"So did they not travel in the land in order to see what sort of fate befell those before them? Their strength and the signs they left behind in the earth, exceeded them \u2013 so Allah seized them on account of their sins, and they had no one to save them from Allah."},"4155":{"id":4155,"surah":40,"ayah":22,"verse":"This occurred because their Noble Messengers came to them with clear signs, thereupon they used to disbelieve, so Allah seized them; indeed Allah is All Powerful, Severe in Punishment."},"4156":{"id":4156,"surah":40,"ayah":23,"verse":"And indeed We sent Moosa with Our signs and a clear proof."},"4157":{"id":4157,"surah":40,"ayah":24,"verse":"Towards Firaun and Haman and Qaroon \u2013 in response they said, \u201c(He is) a magician, a big liar!\u201d"},"4158":{"id":4158,"surah":40,"ayah":25,"verse":"So when he brought the truth from Our presence to them, they said, \u201cKill the sons of those who believe in him, and keep their women alive\u201d; and the evil scheme of the disbelievers only keeps wandering astray."},"4159":{"id":4159,"surah":40,"ayah":26,"verse":"And said Firaun, \u201cAllow me to kill Moosa and let him pray to his Lord; I fear that he will change your religion or cause chaos in the land!\u201d"},"4160":{"id":4160,"surah":40,"ayah":27,"verse":"Said Moosa, \u201cI seek the refuge of mine and your Lord, from every haughty person who does not believe in the Day of Reckoning.\u201d"},"4161":{"id":4161,"surah":40,"ayah":28,"verse":"And said a Muslim man from the people of Firaun, who used to hide his faith, \u201cWhat! You want to kill a man just because he says, \u2018Allah is my Lord\u2019 whereas he has indeed brought clear signs to you from your Lord? And supposedly if he is speaking wrongly, then the calamity of wrongful speech is upon him; and if he is truthful, then part of what he promises you will reach you; indeed Allah does not guide any transgressor, excessive liar.\u201d"},"4162":{"id":4162,"surah":40,"ayah":29,"verse":"\u201cO my people! You now rule the earth, dominant; so who will save us from Allah\u2019s punishment if it comes upon us?\u201d Said Firaun, \u201cI only explain to you what I think is correct, and I show you only the right path.\u201d"},"4163":{"id":4163,"surah":40,"ayah":30,"verse":"And the believer said, \u201cO my people! I fear for you a day similar to that of the earlier groups!\u201d"},"4164":{"id":4164,"surah":40,"ayah":31,"verse":"\u201cLike the tradition of the people of Nooh, and Aad, and Thamud and others after them; and Allah does not will injustice upon bondmen.\u201d"},"4165":{"id":4165,"surah":40,"ayah":32,"verse":"\u201cO my people! I fear for you a day on which will be a great outcry!\u201d"},"4166":{"id":4166,"surah":40,"ayah":33,"verse":"\u201cA day when you will turn back fleeing; none can save you from Allah; and whomever Allah sends astray, there is no guide for him.\u201d"},"4167":{"id":4167,"surah":40,"ayah":34,"verse":"\u201cAnd indeed Yusuf came to you with clear signs before this, thereupon you remained doubtful concerning what he had brought; to the extent that when he died, you said, \u2018Allah will surely not send any Noble Messenger after him\u2019\u201d; this is how Allah sends astray whoever transgresses, is doubtful. \u2013"},"4168":{"id":4168,"surah":40,"ayah":35,"verse":"Those who dispute regarding the signs of Allah without any proof having come to them; how very disgusting this is, in the sight of Allah and in the sight of the believers! This is how Allah seals the entire heart of every haughty, rebellious person."},"4169":{"id":4169,"surah":40,"ayah":36,"verse":"And said Firaun, \u201cO Haman! Build a high palace for me, in order that I may reach the routes.\u201d \u2013"},"4170":{"id":4170,"surah":40,"ayah":37,"verse":"\u201cThe routes of the heavens, in order to glance at the God of Moosa \u2013 and indeed I think he is a liar\u201d; this is how the evil deeds of Firaun were made seeming good to him, and he was stopped from the path; and the evil scheme of Firaun was destined to be ruined."},"4171":{"id":4171,"surah":40,"ayah":38,"verse":"And the believer said, \u201cO my people! Follow me, I shall show you the way of righteousness.\u201d"},"4172":{"id":4172,"surah":40,"ayah":39,"verse":"\u201cO my people! The life of this world is just a brief usage, and indeed the next abode is one of everlasting stay.\u201d"},"4173":{"id":4173,"surah":40,"ayah":40,"verse":"\u201cWhoever commits an evil deed will not be repaid except to the same extent; and whoever does good deeds, whether a man or a woman, and is a Muslim, will be admitted into Paradise, in which they will receive sustenance without account.\u201d"},"4174":{"id":4174,"surah":40,"ayah":41,"verse":"\u201cAnd O my people! What is the matter with me that I call you towards salvation whereas you call me towards hell?\u201d"},"4175":{"id":4175,"surah":40,"ayah":42,"verse":"\u201cYou call me to disbelieve in Allah and ascribe such as partners to Him, regarding whom I do not have any knowledge \u2013 whereas I call you towards the Most Honourable, the Oft Forgiving!\u201d"},"4176":{"id":4176,"surah":40,"ayah":43,"verse":"\u201cSo it is self evident that what you call me towards has no benefit being prayed to, either in this world or in the Hereafter, and that our return is towards Allah, and that the transgressors only are the people of the fire.\u201d"},"4177":{"id":4177,"surah":40,"ayah":44,"verse":"\u201cAnd soon the time will come when you will remember what I now say to you; and I entrust my tasks to Allah; indeed Allah sees the bondmen.\u201d"},"4178":{"id":4178,"surah":40,"ayah":45,"verse":"Therefore Allah saved him from the evils of their scheming, and an evil punishment enveloped the people of Firaun. \u2013"},"4179":{"id":4179,"surah":40,"ayah":46,"verse":"The fire \u2013 upon which they are presented morning and evening; and when the Last Day is established \u2013 \u201cPut the people of Firaun into the most severe punishment.\u201d (Punishment in the grave is proven by this verse.)"},"4180":{"id":4180,"surah":40,"ayah":47,"verse":"And when they will quarrel amongst themselves in the fire, those who were weak will say to those who sought greatness, \u201cWe were your followers, so will you reduce from us some of the punishment of the fire?\u201d"},"4181":{"id":4181,"surah":40,"ayah":48,"verse":"Those who were proud will say, \u201cWe are all in the fire \u2013 indeed Allah has already passed the judgement among the bondmen.\u201d"},"4182":{"id":4182,"surah":40,"ayah":49,"verse":"And those who are in the fire said to its guards, \u201cPray to your Lord to decrease the punishment upon us for one day.\u201d"},"4183":{"id":4183,"surah":40,"ayah":50,"verse":"They said, \u201cIs it not that your Noble Messengers used to come to you with clear signs?\u201d They said, \u201cWhy not, surely yes!\u201d They said, \u201cThen you yourselves pray\u201d; and the prayer of the disbelievers is nothing but astray."},"4184":{"id":4184,"surah":40,"ayah":51,"verse":"Indeed We will surely help Our Noble Messengers, and the believers, in the life of this world and on the day when the witnesses will be standing."},"4185":{"id":4185,"surah":40,"ayah":52,"verse":"The day on which the unjust will not gain any benefit from their excuses, and for them is the curse, and for them is the wretched home."},"4186":{"id":4186,"surah":40,"ayah":53,"verse":"And indeed We gave Moosa the guidance, and bequeathed the Book to the Descendants of Israel."},"4187":{"id":4187,"surah":40,"ayah":54,"verse":"A guidance and an advice for the people of intellect."},"4188":{"id":4188,"surah":40,"ayah":55,"verse":"Therefore be patient (O dear Prophet Mohammed \u2013 peace and blessings be upon him), undoubtedly Allah\u2019s promise is true, and seek forgiveness for the sins of your own people, and praising your Lord, proclaim His Purity morning and evening."},"4189":{"id":4189,"surah":40,"ayah":56,"verse":"Those who dispute concerning the signs of Allah without any proof having come to them \u2013 in their hearts is nothing but a craving for greatness which they shall never achieve; therefore seek the refuge of Allah; indeed He only is the All Hearing, the All Seeing."},"4190":{"id":4190,"surah":40,"ayah":57,"verse":"Certainly the creation of the heavens and the earth is far greater than the creation of men, but most people do not know."},"4191":{"id":4191,"surah":40,"ayah":58,"verse":"And the blind and the sighted are not equal \u2013 and neither are the believers who perform good deeds and the wicked equal; how very little do you ponder!"},"4192":{"id":4192,"surah":40,"ayah":59,"verse":"Indeed the Last Day will surely come, there is no doubt in it \u2013 but most people do not accept faith."},"4193":{"id":4193,"surah":40,"ayah":60,"verse":"And your Lord proclaimed, \u201cPray to Me, I will accept; indeed those who stay conceited towards worshipping Me, will enter hell in disgrace.\""},"4194":{"id":4194,"surah":40,"ayah":61,"verse":"It is Allah Who created night for you so that you may gain rest in it, and the day giving sight; indeed Allah is Most Munificent towards mankind, but most people do not give thanks."},"4195":{"id":4195,"surah":40,"ayah":62,"verse":"Such is Allah, your Lord, the Creator of all things; there is no God except Him; so where are you reverting?"},"4196":{"id":4196,"surah":40,"ayah":63,"verse":"This is how those who deny the signs of Allah go reverting."},"4197":{"id":4197,"surah":40,"ayah":64,"verse":"It is Allah Who made for you the earth your resting place and the sky a canopy, and moulded you so gave you the best shape, and gave you pure things for sustenance; such is Allah, your Lord; so Most Auspicious is Allah, the Lord Of The Creation."},"4198":{"id":4198,"surah":40,"ayah":65,"verse":"It is He, the Alive \u2013 there is no God except Him; therefore worship Him as His sincere bondmen; all praise is to Allah, the Lord Of The Creation."},"4199":{"id":4199,"surah":40,"ayah":66,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI have been forbidden to worship those whom you worship besides Allah whilst clear proofs have come to me from my Lord; and I have been commanded to submit to the Lord Of The Creation.\u201d"},"4200":{"id":4200,"surah":40,"ayah":67,"verse":"It is He Who created you from clay, then from a drop of liquid, then from a clot of blood, and then brings you forth as a child, then keeps you alive for you to reach adulthood and then to become old; and some among you pass away earlier, and for you to reach an appointed term, and so that you may understand."},"4201":{"id":4201,"surah":40,"ayah":68,"verse":"It is He Who gives life and death; so whenever He wills a thing, He only says to it \u201cBe\u201d \u2013 it thereupon happens!"},"4202":{"id":4202,"surah":40,"ayah":69,"verse":"Did you not see those who dispute concerning the signs of Allah? Where are they being diverted?"},"4203":{"id":4203,"surah":40,"ayah":70,"verse":"Those who denied the Book and what We sent with Our Noble Messengers; so they will soon come to know. \u2013"},"4204":{"id":4204,"surah":40,"ayah":71,"verse":"When around their necks will be shackles and chains; they will be dragged. \u2013"},"4205":{"id":4205,"surah":40,"ayah":72,"verse":"In boiling water; they will then be ignited* in the fire. (Like fuel \u2013 see verse 2:24)"},"4206":{"id":4206,"surah":40,"ayah":73,"verse":"It will then be said to them, \u201cWhere are the partners you used to appoint?\u201d \u2013"},"4207":{"id":4207,"surah":40,"ayah":74,"verse":"\u201cAs rivals to Allah?\u201d; they will say, \u201cWe have lost them \u2013 in fact we never used to worship anything before!\u201d; this is how Allah sends the disbelievers astray."},"4208":{"id":4208,"surah":40,"ayah":75,"verse":"\u201cThis is the recompense of your being happy upon falsehood in the earth, and the recompense of your conceit.\u201d"},"4209":{"id":4209,"surah":40,"ayah":76,"verse":"\u201cEnter the gates of hell, to remain in it forever\u201d; so what a wretched destination for the haughty!"},"4210":{"id":4210,"surah":40,"ayah":77,"verse":"Therefore be patient (O dear Prophet Mohammed \u2013 peace and blessings be upon him), undoubtedly Allah\u2019s promise is true; and whether We show some of what We promise them, or cause you to pass away before it \u2013 in any case they will return to Us."},"4211":{"id":4211,"surah":40,"ayah":78,"verse":"Indeed We sent many Noble Messengers before you, so We have related to you the affairs of some among them, and not related the affairs of some; and no Noble Messenger has the right to bring any sign except with the command of Allah; so the time when the command of Allah comes, the true judgement will be delivered and there will the people of falsehood be ruined."},"4212":{"id":4212,"surah":40,"ayah":79,"verse":"It is Allah Who created the animals for you, in order for you to ride some of them, and some to eat."},"4213":{"id":4213,"surah":40,"ayah":80,"verse":"And in them are numerous benefits for you, and for you to reach your hearts\u2019 desires while riding them \u2013 and you ride upon them and upon the ships."},"4214":{"id":4214,"surah":40,"ayah":81,"verse":"And He shows you His signs; so which sign of Allah will you deny?"},"4215":{"id":4215,"surah":40,"ayah":82,"verse":"Did they not travel in the land to see what sort of fate befell those before them? They were more than these in number, and they exceeded them in strength and the signs they left behind in the earth \u2013 so what benefit did they get from what they earned?"},"4216":{"id":4216,"surah":40,"ayah":83,"verse":"So when their Noble Messengers came to them with clear signs, they remained happy over the worldly knowledge they possessed, and upon them only reverted what they used to mock at!"},"4217":{"id":4217,"surah":40,"ayah":84,"verse":"So when they saw Our punishment, they said, \u201cWe accept faith in the One Allah, and reject those whom we ascribed with Him.\u201d"},"4218":{"id":4218,"surah":40,"ayah":85,"verse":"So their accepting of faith did not benefit them when they saw Our punishment; the tradition of Allah which has passed among His bondmen; and there were the disbelievers ruined."},"4219":{"id":4219,"surah":41,"ayah":1,"verse":"Ha-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4220":{"id":4220,"surah":41,"ayah":2,"verse":"This (Qur\u2019an) is sent down from the Most Gracious, the Most Merciful."},"4221":{"id":4221,"surah":41,"ayah":3,"verse":"A Book the verses of which are explained in detail, the Qur\u2019an in Arabic for people of intellect."},"4222":{"id":4222,"surah":41,"ayah":4,"verse":"Giving glad tidings and heralding warning; so most of them turned away, therefore they cannot listen (to the truth)."},"4223":{"id":4223,"surah":41,"ayah":5,"verse":"And they say, \u201cOur hearts are covered against the affair you call us to, and there is deafness in our ears, and there is a barrier between us and you \u2013 therefore mind your own business, we are minding ours.\u201d"},"4224":{"id":4224,"surah":41,"ayah":6,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cPhysically I am a human like you \u2013 I receive the divine revelation that your God is only One God, therefore be upright towards Him and seek forgiveness from Him\u201d; and woe is to the polytheists. \u2013"},"4225":{"id":4225,"surah":41,"ayah":7,"verse":"Those who do not give the obligatory charity, and who deny the Hereafter."},"4226":{"id":4226,"surah":41,"ayah":8,"verse":"Indeed for those who believed and did good deeds, is a limitless reward."},"4227":{"id":4227,"surah":41,"ayah":9,"verse":"Say \u201cWhat! You disbelieve in Him Who created the earth in two days, and you appoint equals to Him? He is the Lord Of The Creation!\u201d"},"4228":{"id":4228,"surah":41,"ayah":10,"verse":"And in it He placed mountains as anchors from above it, and blessings in it, and appointed the sustenance for those who dwell in it \u2013 all this in four days; a proper answer to those who question."},"4229":{"id":4229,"surah":41,"ayah":11,"verse":"He then inclined towards the heavens and it was smoke \u2013 thereupon He said to it and to the earth, \u201cBoth of you present yourselves, willingly or with reluctance\u201d; they said, \u201cWe present ourselves, with zeal.\u201d"},"4230":{"id":4230,"surah":41,"ayah":12,"verse":"He then established them into seven heavens in two days, and to each heaven He sent the command of its affairs; and We decorated the lower heaven with lamps; and for its protection; this is the command set by the Most Honourable, the All Knowing."},"4231":{"id":4231,"surah":41,"ayah":13,"verse":"Then if they turn away say, \u201cI warn you of a thunderbolt like the thunderbolt which came upon A\u2019ad and Thamud.\u201d"},"4232":{"id":4232,"surah":41,"ayah":14,"verse":"When their Noble Messengers approached them from front and from behind saying, \"Do not worship any one except Allah\"; they said, \"If our Lord willed, He would surely have sent down angels \u2013 we therefore deny whatever you have been sent with.\u201d"},"4233":{"id":4233,"surah":41,"ayah":15,"verse":"So regarding the A\u2019ad, they were haughty in the land without right, and they said, \u201cWho is more powerful than us?\u201d Did they not realise that Allah, Who created them, is more powerful than them? And they used to deny Our signs."},"4234":{"id":4234,"surah":41,"ayah":16,"verse":"We therefore sent a violent thunderstorm towards them in their days of misfortune, in order to make them taste a disgraceful punishment in the life of this world; and indeed the punishment of the Hereafter is more disgracing, and they will not be helped."},"4235":{"id":4235,"surah":41,"ayah":17,"verse":"And regarding the Thamud, We showed them the right path \u2013 so they chose to be blind above being guided, therefore the thunderbolt of the disgraceful punishment overcame them \u2013 the recompense of their deeds."},"4236":{"id":4236,"surah":41,"ayah":18,"verse":"And We rescued those who accepted faith and were pious."},"4237":{"id":4237,"surah":41,"ayah":19,"verse":"And on the day when the enemies of Allah are herded towards the fire, the earlier groups will be restrained till the latter join them."},"4238":{"id":4238,"surah":41,"ayah":20,"verse":"To the extent that when they reach it, their ears and their eyes and their skins will testify against them for what they used to do."},"4239":{"id":4239,"surah":41,"ayah":21,"verse":"And they will say to their skins, \u201cWhy did you testify against us?\"; they will say, \u201cAllah has made us talk, Who has given all things the power of speech, and it is He Who created you the first time, and it is to Him that you have to return.\u201d"},"4240":{"id":4240,"surah":41,"ayah":22,"verse":"And where could you hide from Him, so that your ears and your eyes and your skins may not testify against you? But you had assumed that Allah does not know most of your deeds!"},"4241":{"id":4241,"surah":41,"ayah":23,"verse":"And this is the assumption you had regarding your Lord, which has ruined you, so you are now among those who have been defeated."},"4242":{"id":4242,"surah":41,"ayah":24,"verse":"Then if they are patient, even then the fire is their home; and if they plead, none will listen to their pleading."},"4243":{"id":4243,"surah":41,"ayah":25,"verse":"And We appointed companions for them, who made what is before them and what is after them seem good to them, and the Word proved true upon them along with the groups of jinns and men who passed away before them; they were indeed losers."},"4244":{"id":4244,"surah":41,"ayah":26,"verse":"And the disbelievers said, \u201cDo not listen to this Qur\u2019an and engulf it in noise \u2013 perhaps you may be victorious this way.\u201d"},"4245":{"id":4245,"surah":41,"ayah":27,"verse":"So We will indeed make the disbelievers taste the severe punishment, and indeed We will repay them for their worst deeds."},"4246":{"id":4246,"surah":41,"ayah":28,"verse":"This is the recompense of Allah\u2019s enemies \u2013 the fire; they have to stay in it forever; the penalty for denying Our signs."},"4247":{"id":4247,"surah":41,"ayah":29,"verse":"And the disbelievers said, \u201cOur Lord! Show us both \u2013 among jinns and men \u2013 who misled us, for us to put them beneath our feet so that they be the lowest of the low.\u201d"},"4248":{"id":4248,"surah":41,"ayah":30,"verse":"Indeed those who said, \u201cAllah is our Lord\u201d and remained firm upon it \u2013 upon them descend the angels, (saying), \u201cDo not fear nor grieve, and be happy for the Paradise which you are promised.\u201d"},"4249":{"id":4249,"surah":41,"ayah":31,"verse":"\u201cWe are your friends in the life of this world and in the Hereafter; in it for you is all that you may wish for, and for you is all what you ask.\u201d"},"4250":{"id":4250,"surah":41,"ayah":32,"verse":"\u201cThe hospitality of the Oft Forgiving, Most Merciful.\u201d"},"4251":{"id":4251,"surah":41,"ayah":33,"verse":"And whose speech is better than one who calls towards his Lord and does righteous deeds, and says, \u201cI am a Muslim.\u201d?"},"4252":{"id":4252,"surah":41,"ayah":34,"verse":"And the good deed and the evil deed will never be equal; O listener! Repel the evil deed with a good one, thereupon the one between whom and you was enmity, will become like a close friend."},"4253":{"id":4253,"surah":41,"ayah":35,"verse":"And none receive this great treasure except those who are patient; and none receives this except one who is extremely fortunate."},"4254":{"id":4254,"surah":41,"ayah":36,"verse":"And O listener! If a distracting thought from the devil reaches you, seek the refuge of Allah; indeed He is the All Hearing, the All Knowing."},"4255":{"id":4255,"surah":41,"ayah":37,"verse":"And the night, and the day, and the sun, and the moon are among His signs; do not prostrate for the sun or the moon, but prostrate for Allah Who has created them, if you are His bondmen."},"4256":{"id":4256,"surah":41,"ayah":38,"verse":"So if these (disbelievers) be haughty, so (in any case) those (the angels) who are with your Lord say His Purity night and day, and they do not get weary. (Command of prostration # 11)"},"4257":{"id":4257,"surah":41,"ayah":39,"verse":"And among His signs is that you see the earth lying neglected, so when We sent down water on it, it freshened up and grew forth; indeed He Who gave it life, will revive the dead; indeed He is Able to do all things."},"4258":{"id":4258,"surah":41,"ayah":40,"verse":"Indeed those who distort Our verses are not hidden from Us; so is one who is cast into the fire better, or one who comes in safety on the Day of Resurrection? Do whatever you wish! He is indeed seeing your deeds."},"4259":{"id":4259,"surah":41,"ayah":41,"verse":"Indeed those who denied the Remembrance when it came to them \u2013 they are truly ruined; and indeed it is an honourable Book."},"4260":{"id":4260,"surah":41,"ayah":42,"verse":"Falsehood cannot approach it \u2013 neither from its front nor from its back; it is sent down by the Wise, the Most Praiseworthy."},"4261":{"id":4261,"surah":41,"ayah":43,"verse":"You will not be told except what was said to the Noble Messengers before you; that \u201cYour Lord is the Owner of Forgiveness, and the Owner of Painful Punishment.\u201d"},"4262":{"id":4262,"surah":41,"ayah":44,"verse":"And if We had made it as a Qur\u2019an in a foreign language they would have certainly said, \u201cWhy were its verses not explained in detail?\u201d What! The Book in a foreign language, and the Prophet an Arab?! Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIt is a guidance and a cure for the believers\u201d; and there is deafness in the ears of those who do not believe, and it is blindness upon them; as if they are being called from a place far away!"},"4263":{"id":4263,"surah":41,"ayah":45,"verse":"And We indeed gave the Book to Moosa, so a dispute was created regarding it; and were it not for a Word that had already gone forth from your Lord, the judgement would have been immediately passed upon them; and indeed they are in an intriguing doubt regarding it."},"4264":{"id":4264,"surah":41,"ayah":46,"verse":"Whoever does good deeds, so it is for his own good, and whoever commits evil, so it is for his own harm; and your Lord does not at all oppress the bondmen."},"4265":{"id":4265,"surah":41,"ayah":47,"verse":"The knowledge of the Last Day is directed towards Him; and no fruit comes out from its cover, and nor does any female conceive or give birth, but with His knowledge; and on the day when He will call out to them, \u201cWhere are My partners?\u201d They will say, \u201cWe have told you that none among us can testify.\u201d"},"4266":{"id":4266,"surah":41,"ayah":48,"verse":"And they have lost what they used to worship before, and be sure, they do not have a place to escape."},"4267":{"id":4267,"surah":41,"ayah":49,"verse":"Man does not weary of seeking goodness; and if some misfortune reaches him, he loses hope, gets disappointed."},"4268":{"id":4268,"surah":41,"ayah":50,"verse":"And if We make him taste Our mercy after the hardship which befell him, he will say, \u201cThis is mine! And I do not think that the Last Day will ever be established \u2013 and even if I am returned to my Lord, with Him is only goodness for me\u201d; so We shall indeed inform the disbelievers of what they did; and We shall indeed make them taste a solid punishment."},"4269":{"id":4269,"surah":41,"ayah":51,"verse":"And when We favour man, he turns away and goes back afar; and when some hardship reaches him, he comes with a vast prayer!"},"4270":{"id":4270,"surah":41,"ayah":52,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhat is your opinion \u2013 if this Qur\u2019an is from Allah and then you deny it \u2013 so who is more astray than whoever is in extreme opposition?\u201d"},"4271":{"id":4271,"surah":41,"ayah":53,"verse":"We shall now show them Our signs in all the directions and within their own selves until it becomes clear to them that it is certainly the truth; is not your Lord sufficient as a Witness over all things?"},"4272":{"id":4272,"surah":41,"ayah":54,"verse":"Pay heed! They are certainly doubtful regarding the meeting with their Lord; pay heed! He encompasses all things!"},"4273":{"id":4273,"surah":42,"ayah":1,"verse":"Ha-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4274":{"id":4274,"surah":42,"ayah":2,"verse":"A\u2019in-Seen-Qaf. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4275":{"id":4275,"surah":42,"ayah":3,"verse":"This is how Allah the Most Honourable, the All Knowing sends the divine revelation to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) and to those before you."},"4276":{"id":4276,"surah":42,"ayah":4,"verse":"To Him only belongs all whatever is in the heavens and all whatever is in the earth; and He is the Supreme, the Greatest."},"4277":{"id":4277,"surah":42,"ayah":5,"verse":"The heavens nearly split apart from above \u2013 and the angels say the Purity of their Lord while praising Him, and seek forgiveness for those on earth; pay heed! Indeed Allah only is the Oft Forgiving, the Most Merciful."},"4278":{"id":4278,"surah":42,"ayah":6,"verse":"And Allah is watching those who have chosen supporters besides Him; and you are not responsible for them."},"4279":{"id":4279,"surah":42,"ayah":7,"verse":"And this is how We have divinely revealed to you the Qur\u2019an in Arabic, for you to warn the people of the mother of all towns \u2013 Mecca \u2013 and those around it, and to warn of the Day of Assembling of which there is no doubt; a group is in Paradise, and another group is in hell."},"4280":{"id":4280,"surah":42,"ayah":8,"verse":"And had Allah willed, He could have made them all upon one religion, but He admits whomever He wills into His mercy; and the unjust do not have any friend nor any supporter."},"4281":{"id":4281,"surah":42,"ayah":9,"verse":"What! Have they appointed supporters other than Allah? So (know that) Allah only is the Supporter, and He will revive the dead; and He is Able to do all things."},"4282":{"id":4282,"surah":42,"ayah":10,"verse":"\u201cThe final decision for the matters in which you differ rests with Allah; such is my Lord \u2013 I rely on Him; and towards Him do I incline.\u201d"},"4283":{"id":4283,"surah":42,"ayah":11,"verse":"The Maker of the heavens and the earth; He has created pairs for you from yourselves and pairs from the animals; He spreads your generation; nothing is like Him; and He only is the All Hearing, the All Seeing."},"4284":{"id":4284,"surah":42,"ayah":12,"verse":"For Him only are the keys of the heavens and the earth; He increases the sustenance for whomever He wills and restricts it; indeed He is the All Knowing."},"4285":{"id":4285,"surah":42,"ayah":13,"verse":"He has kept for you the same path of religion which He commanded Nooh, and what We divinely reveal to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), and what We had commanded to Ibrahim and Moosa and Eisa that, \u201cKeep the religion proper, and do not create divisions in it\u201d; the polytheists find the matter what you call them to as intolerable; Allah chooses for His proximity whomever He wills, and guides towards Himself whoever inclines (towards Him)."},"4286":{"id":4286,"surah":42,"ayah":14,"verse":"And they did not cause divisions except after the knowledge had come to them, because of jealousy among themselves; and were it not for a Word that had already gone forth from your Lord, the judgement would have been passed upon them long ago; and indeed those who inherited the Book after them are in an intriguing doubt regarding it."},"4287":{"id":4287,"surah":42,"ayah":15,"verse":"For this reason, call them (to Islam); and remain firm, as you are commanded to; and do not follow their desires; and say, \u201cI accept faith in whichever Book Allah has sent down; and I am commanded to judge fairly between you; Allah is the Lord of all \u2013 ours and yours; for us are our deeds and for you are your misdeeds; there is no debate between us and you; Allah will gather all of us together; and towards Him is the return.\u201d"},"4288":{"id":4288,"surah":42,"ayah":16,"verse":"And those who fight regarding Allah after the Muslims have accepted His call, their reasoning does not hold at all before their Lord, and upon them is wrath, and for them is a severe punishment."},"4289":{"id":4289,"surah":42,"ayah":17,"verse":"It is Allah Who has sent down the Book with the truth, and the Scales of Justice; and what do you know \u2013 possibly the Last Day could really be near!"},"4290":{"id":4290,"surah":42,"ayah":18,"verse":"Those who do not believe in it are impatient for it, and those who believe in it fear it and know that indeed it is the truth; pay heed! Those who doubt the Last Day are certainly in extreme error."},"4291":{"id":4291,"surah":42,"ayah":19,"verse":"Allah is Benevolent upon His bondmen \u2013 He bestows sustenance to whomever He wills; and He only is the All Powerful, the Most Honourable."},"4292":{"id":4292,"surah":42,"ayah":20,"verse":"Whoever aspires for the yield of the Hereafter \u2013 We increase its yield for him; and whoever aspires for the yield of this world \u2013 We give him part of it, and he has no portion in the Hereafter."},"4293":{"id":4293,"surah":42,"ayah":21,"verse":"Or do they have associate deities who have appointed for them a religion, which Allah has not permitted? And were it not for a Word that had already been decided, the judgement would have been passed between them here itself; and indeed for the unjust is a painful punishment."},"4294":{"id":4294,"surah":42,"ayah":22,"verse":"You will see the unjust getting terrified of their own deeds, and (evil of) their deeds will certainly befall them; and (that) those who accepted faith and performed good deeds are in blossoming Gardens of Paradise; for them is whatever they wish from their Lord; this only is the great munificence (of Allah)."},"4295":{"id":4295,"surah":42,"ayah":23,"verse":"This is what Allah gives the glad tidings of to His bondmen who accept faith and do good deeds; say (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u201cI do not ask any fee from you upon this, except the love between close ones\u201d; and whoever performs a good deed \u2013 We further increase the goodness in it for him; indeed Allah is Oft Forgiving, Most Appreciative."},"4296":{"id":4296,"surah":42,"ayah":24,"verse":"What! They dare say that, \u201cHe has fabricated a lie against Allah\u201d? And if Allah wills, He can seal your heart by His mercy and protection*; and Allah wipes out falsehood and proves the truth by His Words; indeed He knows what lies within the hearts. (* So that you may not be agonised by what they do)."},"4297":{"id":4297,"surah":42,"ayah":25,"verse":"And it is He Who accepts repentance from His bondmen, and pardons sins*, and knows all your deeds. (Repentance for the cardinal sins, while lesser sins are wiped out by good deeds.)"},"4298":{"id":4298,"surah":42,"ayah":26,"verse":"And He accepts the prayers of those who accept faith and do good deeds, and gives them a greater reward by His munificence; and for the disbelievers is a severe punishment."},"4299":{"id":4299,"surah":42,"ayah":27,"verse":"And had Allah increased the sustenance for all His slaves, they would have surely caused turmoil in the land, but He sends it down by a proper assessment as He wills; indeed He is Well Aware of, Seeing His bondmen."},"4300":{"id":4300,"surah":42,"ayah":28,"verse":"And it is He Who sends down the rain when they have despaired, and spreads out His mercy; and He is the Benefactor, the Most Praiseworthy."},"4301":{"id":4301,"surah":42,"ayah":29,"verse":"And among His signs is the creation of the heavens and the earth, and the moving creatures dispersed in it; and He is Able to gather them whenever He wills."},"4302":{"id":4302,"surah":42,"ayah":30,"verse":"And whatever calamity befalls you, is because of what your hands have earned \u2013 and there is a great deal He pardons!"},"4303":{"id":4303,"surah":42,"ayah":31,"verse":"And you cannot escape in the earth; and you have neither a friend nor any supporter against Allah."},"4304":{"id":4304,"surah":42,"ayah":32,"verse":"And among His signs are the ships sailing on the sea, like hills."},"4305":{"id":4305,"surah":42,"ayah":33,"verse":"If He wills, He can calm the winds so the ships remain still on the sea surface; indeed in this are signs for every greatly enduring, grateful person."},"4306":{"id":4306,"surah":42,"ayah":34,"verse":"Or He can ruin them due to peoples\u2019 sins and He can forgive a great deal."},"4307":{"id":4307,"surah":42,"ayah":35,"verse":"And for those who dispute regarding Our signs to realise; that they do not have a place to escape to."},"4308":{"id":4308,"surah":42,"ayah":36,"verse":"Whatever you have received is only for usage in the life of this world, and that which is with Allah is much better and more lasting \u2013 for those who believe and rely upon their Lord."},"4309":{"id":4309,"surah":42,"ayah":37,"verse":"And (for) those who avoid cardinal sins and indecencies, and forgive (even) when they are angry."},"4310":{"id":4310,"surah":42,"ayah":38,"verse":"And those who obeyed the command of their Lord and kept the prayer established; and whose affairs are with mutual consultation; and who spend in Our cause from what We have bestowed upon them."},"4311":{"id":4311,"surah":42,"ayah":39,"verse":"And those who take revenge when rebellion harms them."},"4312":{"id":4312,"surah":42,"ayah":40,"verse":"The retribution of a harmful deed is the harm equal to it; so whoever forgives and makes amends, so his reward is upon Allah; indeed He does not befriend the unjust."},"4313":{"id":4313,"surah":42,"ayah":41,"verse":"And there is no way of reproach against those who take revenge after being wronged."},"4314":{"id":4314,"surah":42,"ayah":42,"verse":"Reproach is only against those who oppress people, and wrongfully spread rebellion in the land; for them is a painful punishment."},"4315":{"id":4315,"surah":42,"ayah":43,"verse":"And indeed whoever patiently endured and forgave \u2013 then indeed these are acts of great courage."},"4316":{"id":4316,"surah":42,"ayah":44,"verse":"And whomever Allah sends astray, there is no friend for him against Allah; and you will see the unjust when they behold the punishment saying, \u201cIs there a way to return?\u201d"},"4317":{"id":4317,"surah":42,"ayah":45,"verse":"And you will see them being presented upon the fire, cowering with disgrace watching with concealed eyes; and the believers will say, \u201cIndeed ruined are those who have lost themselves and their families on the Day of Resurrection\u201d; pay heed! Indeed the unjust are in a punishment that will never end."},"4318":{"id":4318,"surah":42,"ayah":46,"verse":"And they had no friends to help them against Allah; and there is no way for one whom Allah sends astray."},"4319":{"id":4319,"surah":42,"ayah":47,"verse":"Obey your Lord before the advent of a Day from Allah, which cannot be averted; you will not have any refuge on that day, nor will you be able to deny."},"4320":{"id":4320,"surah":42,"ayah":48,"verse":"So if they turn away from you, We have not sent you as a guardian over them; upon you is nothing but to convey (the message); and when We make man taste some mercy from Us, he rejoices upon it; and if some harm reaches them because of what their own hands have sent before, thereupon man is ungrateful!"},"4321":{"id":4321,"surah":42,"ayah":49,"verse":"For Allah only is the kingship of the heavens and the earth; He creates whatever He wills; He may bestow daughters to whomever He wills, and sons to whomever He wills."},"4322":{"id":4322,"surah":42,"ayah":50,"verse":"Or may mix them, the sons and daughters; and may make barren whomever He wills; indeed He is All Knowing, Able."},"4323":{"id":4323,"surah":42,"ayah":51,"verse":"And it is not for any human that Allah may speak to him except as a divine revelation or while the human is on this side of the veil of greatness, or that He sends an angel to reveal by His permission, whatever He wills; indeed He is Supreme, Wise."},"4324":{"id":4324,"surah":42,"ayah":52,"verse":"And this is how We sent the divine revelation to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u2013 a life giving thing, by Our command; neither did you know the Book nor the detailed commands of religion, but We have made this Qur\u2019an a light by which We guide whomever We will from Our bondmen; and indeed you surely do guide to the Straight Path."},"4325":{"id":4325,"surah":42,"ayah":53,"verse":"The path of Allah \u2013 the One to Whom only belongs all whatever is in the heavens and all whatever is in the earth; pay heed! Towards Allah only do all matters return."},"4326":{"id":4326,"surah":43,"ayah":1,"verse":"Ha-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4327":{"id":4327,"surah":43,"ayah":2,"verse":"By oath of the clear Qur\u2019an."},"4328":{"id":4328,"surah":43,"ayah":3,"verse":"We have indeed sent it as a Qur\u2019an in the Arabic language, so that you may understand."},"4329":{"id":4329,"surah":43,"ayah":4,"verse":"And the Qur\u2019an is undoubtedly, in the Original Book with Us, surely the exalted, full of wisdom."},"4330":{"id":4330,"surah":43,"ayah":5,"verse":"So shall We divert the advice away from you, because you are a nation that exceeds the limits?"},"4331":{"id":4331,"surah":43,"ayah":6,"verse":"And how many Prophets We did send among the earlier people!"},"4332":{"id":4332,"surah":43,"ayah":7,"verse":"And whenever any Prophet came to them, they only mocked at him."},"4333":{"id":4333,"surah":43,"ayah":8,"verse":"We therefore destroyed the people who were more forceful than these, and the example of the earlier ones has already gone by."},"4334":{"id":4334,"surah":43,"ayah":9,"verse":"And if you ask them, \u201cWho has created the heavens and the earth?\u201d, they will surely say, \u201cThey are created by the Most Honourable, the All Knowing.\u201d"},"4335":{"id":4335,"surah":43,"ayah":10,"verse":"The One Who made the earth a bed you, and made roads for you in it, so that you may be guided."},"4336":{"id":4336,"surah":43,"ayah":11,"verse":"And Who sent down water from the sky with a proper measure, so We revived a dead city with it; this is how you will be taken out."},"4337":{"id":4337,"surah":43,"ayah":12,"verse":"And the One Who created all pairs, and made the ships and cattle as rides for you."},"4338":{"id":4338,"surah":43,"ayah":13,"verse":"So that you may properly mount their backs, and may remember your Lord\u2019s favour when you have mounted them, and say, \u201cPurity is to Him, Who has given this ride in our control, and we did not have the strength for it.\u201d"},"4339":{"id":4339,"surah":43,"ayah":14,"verse":"\u201cAnd indeed we have to return to our Lord.\u201d"},"4340":{"id":4340,"surah":43,"ayah":15,"verse":"And from* His bondmen, they appointed a portion for Him; man is indeed an open ingrate. (* Appointed some of His creation as His sons and daughters)."},"4341":{"id":4341,"surah":43,"ayah":16,"verse":"Has He chosen daughters for Himself from His creation, and selected only sons for you?"},"4342":{"id":4342,"surah":43,"ayah":17,"verse":"And if one of them is given the glad tidings of what he professes regarding the Most Gracious, his face blackens and he is mournful!"},"4343":{"id":4343,"surah":43,"ayah":18,"verse":"And (do they chose for Him) one who is brought up among ornaments, and cannot express herself clearly in debate?"},"4344":{"id":4344,"surah":43,"ayah":19,"verse":"And they appoint the angels, who are the bondmen of the Most Gracious, as females; were they present at the time of the angels\u2019 creation? Their declaration will be now recorded and they will be questioned."},"4345":{"id":4345,"surah":43,"ayah":20,"verse":"And they say, \u201cIf the Most Gracious had willed, we would not have worshipped them!\u201d They do not know its truth at all; they only make guesses."},"4346":{"id":4346,"surah":43,"ayah":21,"verse":"Or have We given them some Book before this, to which they hold fast?"},"4347":{"id":4347,"surah":43,"ayah":22,"verse":"Rather they said, \u201cWe found our forefathers upon a religion, and we are following their footsteps.\u201d"},"4348":{"id":4348,"surah":43,"ayah":23,"verse":"And similarly, whenever We sent a Herald of Warning before you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) into any town, its wealthy people said, \u201cWe found our forefathers upon a religion, and we are behind their footsteps.\u201d"},"4349":{"id":4349,"surah":43,"ayah":24,"verse":"The prophet said, \u201cWhat! Even if I bring to you a path better than what you found your forefathers following?\u201d; they said, \u201cWe do not believe in whatever you have been sent with.\u201d"},"4350":{"id":4350,"surah":43,"ayah":25,"verse":"So We took revenge from them \u2013 therefore see what sort of fate befell those who denied!"},"4351":{"id":4351,"surah":43,"ayah":26,"verse":"And when Ibrahim said to his father and his people, \u201cI have no relation whatsoever with your deities.\u201d"},"4352":{"id":4352,"surah":43,"ayah":27,"verse":"\u201cExcept Him Who has created me, for He will soon direct me.\u201d (To my place of migration.)"},"4353":{"id":4353,"surah":43,"ayah":28,"verse":"And Ibrahim kept this declaration among his progeny, in order that they may desist."},"4354":{"id":4354,"surah":43,"ayah":29,"verse":"In fact I gave them and their forefathers the usage of this world until the truth and the Noble Messenger who conveyed the message clearly, came to them."},"4355":{"id":4355,"surah":43,"ayah":30,"verse":"And when the truth came to them they said, \u201cThis is magic, and we disbelieve in it.\u201d"},"4356":{"id":4356,"surah":43,"ayah":31,"verse":"And they said, \u201cWhy was the Qur\u2019an not sent down upon some chieftain of these two great towns?\u201d (The chiefs of Mecca and Taif)."},"4357":{"id":4357,"surah":43,"ayah":32,"verse":"Are they the distributors of your Lord\u2019s mercy? We have distributed among them their comforts in the life of this world, and gave high status to some over others so that they mock at each other; and the mercy of your Lord is better than all what they hoard."},"4358":{"id":4358,"surah":43,"ayah":33,"verse":"And were it not for that all people be on one religion, We would have made for the disbelievers of the Most Gracious, roofs and stairs of silver which they would climb."},"4359":{"id":4359,"surah":43,"ayah":34,"verse":"And doors of silver for their houses and couches of silver upon which they would recline."},"4360":{"id":4360,"surah":43,"ayah":35,"verse":"And diverse adornments; and all this is usage only of the life of this world; and the Hereafter with your Lord, is for the pious."},"4361":{"id":4361,"surah":43,"ayah":36,"verse":"And whoever is averse to the Remembrance of the Most Gracious, We appoint a devil upon him, who stays with him."},"4362":{"id":4362,"surah":43,"ayah":37,"verse":"And indeed those devils prevent them from the Straight Path, and they think they are on guidance!"},"4363":{"id":4363,"surah":43,"ayah":38,"verse":"To the extent that when the disbeliever will be brought to Us, he will say to his devil, \u201cAlas \u2013 if only there was the distance* of east and west, between you and me!\u201d \u2013 so what an evil companion** he is! (* Had I not listened to you. **They will be bound together in chains.)"},"4364":{"id":4364,"surah":43,"ayah":39,"verse":"(It will be said to them) \u201cAnd you will not benefit from them this day, whereas you are unjust \u2013 for you are all partners in the punishment.\u201d"},"4365":{"id":4365,"surah":43,"ayah":40,"verse":"So will you make the deaf listen or show the path to the blind, and to those who are in open error? (The disbelievers whose hearts are sealed, are deaf and blind to the truth)."},"4366":{"id":4366,"surah":43,"ayah":41,"verse":"So if We take you away, We shall then indeed take revenge from them."},"4367":{"id":4367,"surah":43,"ayah":42,"verse":"Or show you what We have promised them \u2013 We therefore have complete control over them."},"4368":{"id":4368,"surah":43,"ayah":43,"verse":"Therefore hold fast to what We have divinely revealed to you; indeed you are upon the Straight Path. (The Holy Prophets can never go astray)."},"4369":{"id":4369,"surah":43,"ayah":44,"verse":"And it is undoubtedly an honour for you and your people; and you will soon be questioned."},"4370":{"id":4370,"surah":43,"ayah":45,"verse":"And ask them \u2013 did any of the Noble Messengers We sent before you, appoint any other Gods except the Most Gracious, whom they used to worship?"},"4371":{"id":4371,"surah":43,"ayah":46,"verse":"And indeed We sent Moosa along with Our signs towards Firaun and his chieftains \u2013 he therefore said, \u201cIndeed I am a Noble Messenger of the Lord of the Creation.\u201d"},"4372":{"id":4372,"surah":43,"ayah":47,"verse":"So when he brought Our signs to them, they started mocking at them!"},"4373":{"id":4373,"surah":43,"ayah":48,"verse":"And all the signs We showed them were always greater than the earlier ones; and We seized them with calamities, so that they may return."},"4374":{"id":4374,"surah":43,"ayah":49,"verse":"And they said, \u201cO you magician! Pray for us to your Lord, by the means of His covenant which is with you; we will certainly come to guidance.\u201d"},"4375":{"id":4375,"surah":43,"ayah":50,"verse":"So when We averted the calamity from them, they immediately broke the promise!"},"4376":{"id":4376,"surah":43,"ayah":51,"verse":"And said Firaun, \u201cO my people! Is not the kingdom of Egypt for me, and these rivers that flow beneath me? So do you not see?\u201d"},"4377":{"id":4377,"surah":43,"ayah":52,"verse":"\u201cOr that I am better than him, for he is lowly \u2013 and he does not seem to talk plainly.\u201d"},"4378":{"id":4378,"surah":43,"ayah":53,"verse":"\u201cSo why was he not bestowed with armlets of gold? Or angels should have come with him staying at his side!\u201d"},"4379":{"id":4379,"surah":43,"ayah":54,"verse":"So when he had brainwashed his people, they followed him; indeed they were a disobedient nation."},"4380":{"id":4380,"surah":43,"ayah":55,"verse":"So when they made Us wrathful, We took revenge from them \u2013 We therefore drowned all of them."},"4381":{"id":4381,"surah":43,"ayah":56,"verse":"So We made them a bygone fable and a lesson for the latter generations."},"4382":{"id":4382,"surah":43,"ayah":57,"verse":"And when the example of the son of Maryam is given, your people laugh at it!"},"4383":{"id":4383,"surah":43,"ayah":58,"verse":"And they say, \u201cAre our deities better or he?\u201d They did not say this to you except to unjustly argue; in fact they are a quarrelsome people."},"4384":{"id":4384,"surah":43,"ayah":59,"verse":"He is purely a bondman (of Ours), whom We have favoured upon, and We made him an extraordinary example for the Descendants of Israel."},"4385":{"id":4385,"surah":43,"ayah":60,"verse":"And if We willed, We could have established angels on the earth instead of you."},"4386":{"id":4386,"surah":43,"ayah":61,"verse":"And indeed Eisa is a sign* of the Last Day, therefore do not ever doubt in the Last Day, and obey Me**; this is the Straight Path. (* The advent of Prophet Eisa to earth for the second time. ** By obeying the Noble Messenger.)"},"4387":{"id":4387,"surah":43,"ayah":62,"verse":"And do not ever let the devil stop you; he is indeed your open enemy."},"4388":{"id":4388,"surah":43,"ayah":63,"verse":"And when Eisa came to them with clear signs, he said, \u201cI have come to you with wisdom, and to explain to you some of the matters regarding which you dispute; therefore fear Allah, and obey me.\u201d"},"4389":{"id":4389,"surah":43,"ayah":64,"verse":"\u201cAllah is indeed my Lord, and yours \u2013 therefore worship Him; this is the Straight Path.\u201d"},"4390":{"id":4390,"surah":43,"ayah":65,"verse":"Then the groups differed amongst themselves; therefore ruin is for the unjust by the punishment of an agonising day."},"4391":{"id":4391,"surah":43,"ayah":66,"verse":"What are they waiting for \u2013 except the Last Day, that it may suddenly come upon them while they are unaware?"},"4392":{"id":4392,"surah":43,"ayah":67,"verse":"Close friends will turn into enemies of one another on that Day, except the pious."},"4393":{"id":4393,"surah":43,"ayah":68,"verse":"To such will be said, \u201cO My bondmen! This day there shall be no fear upon you, nor any grief.\u201d"},"4394":{"id":4394,"surah":43,"ayah":69,"verse":"Those who believed in Our signs, and were Muslims."},"4395":{"id":4395,"surah":43,"ayah":70,"verse":"\u201cEnter Paradise \u2013 you and your wives \u2013 and you will be honoured guests.\u201d"},"4396":{"id":4396,"surah":43,"ayah":71,"verse":"\u201cRounds of golden cups and wine will be presented upon them; and in it is whatever the hearts wish for, and what pleases the eye; and you will stay in it forever.\u201d"},"4397":{"id":4397,"surah":43,"ayah":72,"verse":"\u201cAnd this is the Paradise which is bequeathed to you, because of your deeds.\u201d"},"4398":{"id":4398,"surah":43,"ayah":73,"verse":"\u201cFor you are many fruits in it, for you to eat therefrom.\u201d"},"4399":{"id":4399,"surah":43,"ayah":74,"verse":"Indeed the criminals will stay in the punishment of hell forever."},"4400":{"id":4400,"surah":43,"ayah":75,"verse":"And it will never be lightened upon them, and they will remain in it devastated."},"4401":{"id":4401,"surah":43,"ayah":76,"verse":"And We did not oppress them at all, but they themselves were unjust."},"4402":{"id":4402,"surah":43,"ayah":77,"verse":"And they will cry out, \u201cO Malik*, ask your Lord to finish us!\u201d He will answer, \u201cRather you are to stay (forever).\u201d (*The guard of hell)"},"4403":{"id":4403,"surah":43,"ayah":78,"verse":"Indeed We have brought the Truth to you, but most of you detest the Truth."},"4404":{"id":4404,"surah":43,"ayah":79,"verse":"Do they assume that they have made their work thorough? So We shall make Our work thorough."},"4405":{"id":4405,"surah":43,"ayah":80,"verse":"Do they fancy that We do not listen to their whispers and their counselling? Why not, We surely do! And Our angels are with them, writing down."},"4406":{"id":4406,"surah":43,"ayah":81,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIf supposedly, the Most Gracious had an offspring* \u2013 I would be the first to worship!\u201d (*Which is impossible)"},"4407":{"id":4407,"surah":43,"ayah":82,"verse":"Purity is to the Lord of the heavens and the earth, the Lord of the Throne, from all what they fabricate."},"4408":{"id":4408,"surah":43,"ayah":83,"verse":"Therefore leave them to utter vile speech and to play, until they confront the day which they are promised."},"4409":{"id":4409,"surah":43,"ayah":84,"verse":"And only He is the God of the heavenly creation and the God of the earthly creation; and He only is the Wise, the All Knowing."},"4410":{"id":4410,"surah":43,"ayah":85,"verse":"And Most Auspicious is He, for Whom is the kingship of the heavens and the earth and all that is between them; and only with Him is the knowledge of the Last Day; and towards Him you are to return."},"4411":{"id":4411,"surah":43,"ayah":86,"verse":"And those whom they worship besides Allah do not have the right of intercession \u2013 the right of intercession is only for those who testify to the Truth and have knowledge."},"4412":{"id":4412,"surah":43,"ayah":87,"verse":"And if you ask them as to Who created them, they will surely answer \u201cAllah\u201d \u2013 so where are they reverting?"},"4413":{"id":4413,"surah":43,"ayah":88,"verse":"And by oath of the saying of My Prophet \u201cO my Lord! These people do not accept faith!\u201d"},"4414":{"id":4414,"surah":43,"ayah":89,"verse":"Therefore excuse them and proclaim, \u201cPeace\u201d; for they will soon come to know."},"4415":{"id":4415,"surah":44,"ayah":1,"verse":"Ha-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4416":{"id":4416,"surah":44,"ayah":2,"verse":"By oath of this clear Book."},"4417":{"id":4417,"surah":44,"ayah":3,"verse":"We have indeed sent it down in a blessed night \u2013 indeed it is We Who warn."},"4418":{"id":4418,"surah":44,"ayah":4,"verse":"During it are distributed all the works of wisdom."},"4419":{"id":4419,"surah":44,"ayah":5,"verse":"By a command from Us \u2013 indeed it is We Who send."},"4420":{"id":4420,"surah":44,"ayah":6,"verse":"A mercy from your Lord; indeed He only is the All Hearing, the All Knowing."},"4421":{"id":4421,"surah":44,"ayah":7,"verse":"The Lord of the heavens and the earth and all that is between them; if you people believe."},"4422":{"id":4422,"surah":44,"ayah":8,"verse":"There is no worship except for Him \u2013 He gives life and causes death; your Lord and the Lord of your forefathers."},"4423":{"id":4423,"surah":44,"ayah":9,"verse":"Rather they are in doubt, playing."},"4424":{"id":4424,"surah":44,"ayah":10,"verse":"So you await the day when the sky will bring forth a visible smoke. \u2013"},"4425":{"id":4425,"surah":44,"ayah":11,"verse":"Which will envelop the people; this is a painful punishment."},"4426":{"id":4426,"surah":44,"ayah":12,"verse":"Thereupon they will say, \u201cO our Lord! Remove the punishment from us \u2013 we now accept faith.\u201d"},"4427":{"id":4427,"surah":44,"ayah":13,"verse":"How is it possible for them to accept guidance, whereas a Noble Messenger who speaks clearly has already come to them?"},"4428":{"id":4428,"surah":44,"ayah":14,"verse":"Whereas they had then turned away from him and said, \u201cHe is a madman, tutored!\u201d?"},"4429":{"id":4429,"surah":44,"ayah":15,"verse":"We now remove the punishment for some days \u2013 so you will again commit the same."},"4430":{"id":4430,"surah":44,"ayah":16,"verse":"The day when We will seize with the greatest seizure \u2013 We will indeed take revenge."},"4431":{"id":4431,"surah":44,"ayah":17,"verse":"And before them We indeed tried the people of Firaun, and an Honourable Noble Messenger came to them."},"4432":{"id":4432,"surah":44,"ayah":18,"verse":"Who said, \u201cGive the bondmen of Allah into my custody; I am indeed a trustworthy Noble Messenger for you.\u201d"},"4433":{"id":4433,"surah":44,"ayah":19,"verse":"And saying, \u201cAnd do not rebel against Allah; I have brought a clear proof to you.\u201d"},"4434":{"id":4434,"surah":44,"ayah":20,"verse":"\u201cAnd I take the refuge of my Lord and yours, against your stoning me.\u201d"},"4435":{"id":4435,"surah":44,"ayah":21,"verse":"\u201cAnd if you do not believe in me, then have no relation with me.\u201d"},"4436":{"id":4436,"surah":44,"ayah":22,"verse":"He therefore prayed to his Lord, \u201cThese are a guilty nation!\u201d"},"4437":{"id":4437,"surah":44,"ayah":23,"verse":"We commanded him, \u201cJourney with My bondmen in a part of the night \u2013 you will be pursued.\u201d"},"4438":{"id":4438,"surah":44,"ayah":24,"verse":"\u201cAnd leave the sea as it is, parted in several places; indeed that army will be drowned.\u201d"},"4439":{"id":4439,"surah":44,"ayah":25,"verse":"How many gardens and water-springs they left behind!"},"4440":{"id":4440,"surah":44,"ayah":26,"verse":"And fields and grand palaces!"},"4441":{"id":4441,"surah":44,"ayah":27,"verse":"And favours amongst which they were rejoicing!"},"4442":{"id":4442,"surah":44,"ayah":28,"verse":"That is what We did; and We made another nation their heirs."},"4443":{"id":4443,"surah":44,"ayah":29,"verse":"So the heavens and the earth did not weep for them, and they were not given respite."},"4444":{"id":4444,"surah":44,"ayah":30,"verse":"And indeed We rescued the Descendants of Israel from a disgraceful torture."},"4445":{"id":4445,"surah":44,"ayah":31,"verse":"From Firaun; he was indeed proud, among the transgressors."},"4446":{"id":4446,"surah":44,"ayah":32,"verse":"And We knowingly chose them, among all others of their time."},"4447":{"id":4447,"surah":44,"ayah":33,"verse":"And We gave them signs in which lay clear favours."},"4448":{"id":4448,"surah":44,"ayah":34,"verse":"Indeed these disbelievers proclaim; \u2013"},"4449":{"id":4449,"surah":44,"ayah":35,"verse":"\u201cThere is nothing except our dying just once, and we will not be raised.\u201d"},"4450":{"id":4450,"surah":44,"ayah":36,"verse":"\u201cTherefore bring back our forefathers, if you are truthful!\u201d"},"4451":{"id":4451,"surah":44,"ayah":37,"verse":"Are they better, or the people of Tubba? And those who were before them? We destroyed them; they were indeed criminals."},"4452":{"id":4452,"surah":44,"ayah":38,"verse":"And We did not create the heavens and the earth, and all that is between them, just for play."},"4453":{"id":4453,"surah":44,"ayah":39,"verse":"We did not create them except with the truth, but most of them do not know."},"4454":{"id":4454,"surah":44,"ayah":40,"verse":"Indeed the Day of Decision is the appointment for all of them."},"4455":{"id":4455,"surah":44,"ayah":41,"verse":"The day on which, no friends will benefit each other at all, nor will they be helped. (Except those who are pious \u2013 see verse 43:67)"},"4456":{"id":4456,"surah":44,"ayah":42,"verse":"Except those upon whom Allah has mercy; indeed He only is the Most Honourable, the Most Merciful."},"4457":{"id":4457,"surah":44,"ayah":43,"verse":"Indeed the tree of Zaqqum, \u2013"},"4458":{"id":4458,"surah":44,"ayah":44,"verse":"Is the food of the sinners."},"4459":{"id":4459,"surah":44,"ayah":45,"verse":"Like molten copper; it churns in their bellies."},"4460":{"id":4460,"surah":44,"ayah":46,"verse":"Like the churning of boiling water."},"4461":{"id":4461,"surah":44,"ayah":47,"verse":"\u201cSeize him, and forcibly drag him right to the blazing fire.\u201d"},"4462":{"id":4462,"surah":44,"ayah":48,"verse":"\u201cThen pour on his head the punishment of boiling water.\u201d"},"4463":{"id":4463,"surah":44,"ayah":49,"verse":"Saying \u201cTaste it! Indeed you only are the most honourable, the dignified!\u201d"},"4464":{"id":4464,"surah":44,"ayah":50,"verse":"\u201cIndeed this is what you used to doubt about.\u201d"},"4465":{"id":4465,"surah":44,"ayah":51,"verse":"Indeed the pious are in a place of peace."},"4466":{"id":4466,"surah":44,"ayah":52,"verse":"In Gardens and water-springs."},"4467":{"id":4467,"surah":44,"ayah":53,"verse":"They will be dressed in fine silk and embroidery, facing one another (on thrones)."},"4468":{"id":4468,"surah":44,"ayah":54,"verse":"So it is; and We have wedded them to maidens with gorgeous eyes."},"4469":{"id":4469,"surah":44,"ayah":55,"verse":"In it they will ask for all kinds of fruit, with safety."},"4470":{"id":4470,"surah":44,"ayah":56,"verse":"They will not taste death again in it, except their former death; and Allah has saved them from the punishment of fire."},"4471":{"id":4471,"surah":44,"ayah":57,"verse":"By the munificence of your Lord; this is the great success."},"4472":{"id":4472,"surah":44,"ayah":58,"verse":"And We have made this Qur\u2019an easy in your language, for them to understand."},"4473":{"id":4473,"surah":44,"ayah":59,"verse":"Therefore wait (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u2013 they too are waiting."},"4474":{"id":4474,"surah":45,"ayah":1,"verse":"Ha; Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4475":{"id":4475,"surah":45,"ayah":2,"verse":"The sending down of the Book is from Allah, the Most Honourable, the Wise."},"4476":{"id":4476,"surah":45,"ayah":3,"verse":"Indeed in the heavens and the earth are signs for believers."},"4477":{"id":4477,"surah":45,"ayah":4,"verse":"And in your creation, and all the creatures He scatters in the earth \u2013 in them are signs for the people who are certain."},"4478":{"id":4478,"surah":45,"ayah":5,"verse":"And in the alternation of night and day, and in the means of sustenance \u2013 water \u2013 which Allah sends down from the sky whereby He gave life to the earth after its death, and in the movement of the winds \u2013 (in all these) are signs for people of intellect."},"4479":{"id":4479,"surah":45,"ayah":6,"verse":"These are the verses of Allah which We recite to you with the truth; so forsaking Allah and His signs, what will they believe in?"},"4480":{"id":4480,"surah":45,"ayah":7,"verse":"Ruin is for every great slanderer, excessive sinner."},"4481":{"id":4481,"surah":45,"ayah":8,"verse":"Who hears the verses of Allah which are recited to him, then remains stubborn, proud, as if he did not hear them; therefore give him the glad tidings of a painful punishment."},"4482":{"id":4482,"surah":45,"ayah":9,"verse":"And when he comes to know about any of the verses, he mocks at it; for them is a disgraceful punishment."},"4483":{"id":4483,"surah":45,"ayah":10,"verse":"Hell is after them; and what they have earned will not benefit them at all, nor those whom they have chosen as supporters besides Allah; and for them is a terrible punishment."},"4484":{"id":4484,"surah":45,"ayah":11,"verse":"This is the (true) guidance; and for those who disbelieve in the verses of their Lord is the severest of painful punishments."},"4485":{"id":4485,"surah":45,"ayah":12,"verse":"It is Allah Who has subjected the sea for you so that ships may sail upon it by His command, and for you to seek His munificence, and so that you may give thanks."},"4486":{"id":4486,"surah":45,"ayah":13,"verse":"And has subjected for you all whatever is in the heavens and in the earth, by His command; indeed in this are signs for people who ponder."},"4487":{"id":4487,"surah":45,"ayah":14,"verse":"Tell the believers to ignore those who do not expect the days of Allah, in order that Allah may give a nation the reward of what they used to earn."},"4488":{"id":4488,"surah":45,"ayah":15,"verse":"Whoever does a good deed, so it is for his own good; and whoever commits evil, does for his own harm; and you will then be returned towards your Lord."},"4489":{"id":4489,"surah":45,"ayah":16,"verse":"And indeed We gave the Descendants of Israel the Book, and the rule, and the Prophethood, and gave good things for sustenance and gave them superiority over all others of their time."},"4490":{"id":4490,"surah":45,"ayah":17,"verse":"And We gave them clear proofs in the command; so they did not differ except after the knowledge had come to them \u2013 due to jealousy among themselves; indeed your Lord will judge between them on the Day of Resurrection concerning the matter in which they differ."},"4491":{"id":4491,"surah":45,"ayah":18,"verse":"We then placed you (O dear prophet Mohammed \u2013 peace and blessings be upon him) upon the clear path of the command, therefore continue following it and do not listen to the desires of the ignorant."},"4492":{"id":4492,"surah":45,"ayah":19,"verse":"Indeed they cannot benefit you at all against Allah; and indeed the unjust are the friends of each other; and Allah is the Friend of the pious."},"4493":{"id":4493,"surah":45,"ayah":20,"verse":"This is an enlightenment for mankind, and a guidance and mercy for the people of faith."},"4494":{"id":4494,"surah":45,"ayah":21,"verse":"Do those who commit evil assume that We will make them equal to those who believe and do good deeds, therefore both becoming equal in life and death? What an evil judgement they impose!"},"4495":{"id":4495,"surah":45,"ayah":22,"verse":"And Allah has created the heavens and the earth with the truth, and so that every soul is repaid for what it has earned, and they will not be wronged."},"4496":{"id":4496,"surah":45,"ayah":23,"verse":"Just look at him who makes his desire as his God, and Allah has sent him astray despite his having knowledge, and set a seal upon his ears and his heart, and a covering upon his eyes; so who will guide him after Allah? So do you not ponder?"},"4497":{"id":4497,"surah":45,"ayah":24,"verse":"And they said, \u201cThere is nothing except our life of this world \u2013 we die and we live \u2013 and nothing destroys us except the passage of time\u201d; and they do not have any knowledge of it; they only make guesses."},"4498":{"id":4498,"surah":45,"ayah":25,"verse":"And when Our clear verses are recited to them, their only argument is, \u201cBring back our forefathers, if you are truthful!\u201d"},"4499":{"id":4499,"surah":45,"ayah":26,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIt is Allah Who gives you life, then will give you death, then will gather you all on the Day of Resurrection in which there is no doubt, but most people do not know.\u201d"},"4500":{"id":4500,"surah":45,"ayah":27,"verse":"And to Allah only belongs the kingship of the heavens and the earth; and when the Last Day is established \u2013 on that day the followers of falsehood will suffer loss."},"4501":{"id":4501,"surah":45,"ayah":28,"verse":"And you will see every group down on its knees; every group will be called towards its book of deeds; \u201cThis day you will be repaid for what you did.\u201d"},"4502":{"id":4502,"surah":45,"ayah":29,"verse":"\u201cThis Book of Ours speaks against you with the truth; indeed We had been recording all what you did.\u201d"},"4503":{"id":4503,"surah":45,"ayah":30,"verse":"So those who believed and did good deeds \u2013 their Lord will admit them into His mercy; this is the clear triumph."},"4504":{"id":4504,"surah":45,"ayah":31,"verse":"And to those who disbelieved, it will be said \u201cWas it not that Our verses were recited to you? In response you were haughty and were a guilty people.\u201d"},"4505":{"id":4505,"surah":45,"ayah":32,"verse":"\u201cAnd when it was said, \u2018Indeed Allah\u2019s promise is true and there is no doubt regarding the Last Day\u201d, you used to say, \u2018We do not know what the Last Day is \u2013 we think it is nothing except an imagination, and we are not convinced.\u2019\u201d"},"4506":{"id":4506,"surah":45,"ayah":33,"verse":"And the evils of their deeds appeared to them, and the punishment they used to mock at encompassed them."},"4507":{"id":4507,"surah":45,"ayah":34,"verse":"And it will be said, \u201cThis day We shall forsake you, the way you had forgotten the confronting of this day of yours \u2013 and your destination is the fire, and you do not have any supporters.\u201d"},"4508":{"id":4508,"surah":45,"ayah":35,"verse":"\u201cThis is because you mocked at the signs of Allah, and the worldly life deceived you\u201d; so this day they are not to be removed from the fire, nor will anyone seek amends from them."},"4509":{"id":4509,"surah":45,"ayah":36,"verse":"So all praise is for Allah only, the Lord of the heavens and the Lord of the earth, the Lord of the Creation!"},"4510":{"id":4510,"surah":45,"ayah":37,"verse":"And for Him only is the Greatness, in the heavens and in the earth; and He only is the Most Honourable, the Wise."},"4511":{"id":4511,"surah":46,"ayah":1,"verse":"Ha-Meem. (Alphabets of the Arabic language \u2013 Allah, and to whomever He reveals, know their precise meanings.)"},"4512":{"id":4512,"surah":46,"ayah":2,"verse":"The sending down of this Book is from Allah the Most Honourable, the Wise."},"4513":{"id":4513,"surah":46,"ayah":3,"verse":"We did not create the heavens and the earth and all that is between them except with the truth, and for a fixed term; and the disbelievers have turned away from what they are warned!"},"4514":{"id":4514,"surah":46,"ayah":4,"verse":"Proclaim, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhat is your opinion \u2013 regarding those whom you worship other than Allah \u2013 show me which part of the earth they have created, or do they own any portion of the heavens? Bring to me a Book preceding this (Qur\u2019an), or some remnants of knowledge, if you are truthful.\u201d"},"4515":{"id":4515,"surah":46,"ayah":5,"verse":"And who is more astray than one who worships those, instead of Allah, who cannot listen to their prayer until* the Day of Resurrection, and who do not even know of their worship! (Will never listen.)"},"4516":{"id":4516,"surah":46,"ayah":6,"verse":"And on the day when people are gathered, they will become their enemies, and will reject their worshippers. (The idols will give testimony against the polytheists.)"},"4517":{"id":4517,"surah":46,"ayah":7,"verse":"And when Our clear verses are recited to them, the disbelievers say regarding the Truth that has come to them, \u201cThis is an obvious magic!\u201d"},"4518":{"id":4518,"surah":46,"ayah":8,"verse":"What! They dare say, \u201cHe has fabricated it\u201d? Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIf I have fabricated it, then (I know that) you have no power to support me against Allah; He well knows the matters you are involved in; He is Sufficient as a Witness between me and you; and He only is the Oft Forgiving, the Most Merciful.\u201d"},"4519":{"id":4519,"surah":46,"ayah":9,"verse":"Say, \u201cI am not something new* among the Noble Messengers, nor do I know (on my own) what will happen to me** or with you; I only follow that which is divinely revealed to me, and I am purely a Herald of clear warning. (* I am like the earlier ones. ** My knowledge comes due to divine revelations.)"},"4520":{"id":4520,"surah":46,"ayah":10,"verse":"Say, \u201cWhat is your opinion \u2013 if the Qur\u2019an is from Allah and you have rejected faith in it, and a witness among the Descendants of Israel has already testified upon this and accepted faith, while you became arrogant? Indeed Allah does not guide the unjust.\u201d"},"4521":{"id":4521,"surah":46,"ayah":11,"verse":"And the disbelievers said regarding the Muslims, \u201cIf there were any good in it, the Muslims would not have surpassed us in achieving it!\u201d; and since they did not receive guidance with it, they will now say, \u201cThis is an old slander.\u201d"},"4522":{"id":4522,"surah":46,"ayah":12,"verse":"And whereas before this exists the Book of Moosa, a guide and a mercy; and this is a Book giving testimony, in the Arabic language, to warn the unjust; and to give glad tidings to the virtuous."},"4523":{"id":4523,"surah":46,"ayah":13,"verse":"Indeed those who said, \u201cAllah is our Lord\u201d, and then remained steadfast \u2013 upon them is neither any fear nor shall they grieve."},"4524":{"id":4524,"surah":46,"ayah":14,"verse":"They are the People of Paradise, abiding in it forever; the reward of their deeds."},"4525":{"id":4525,"surah":46,"ayah":15,"verse":"And We have commanded man to be good towards parents; his mother bore him with hardship, and delivered him with hardship; and carrying him and weaning him is for thirty months; until when he* reached maturity and became forty years of age, he said, \u201cMy Lord! Inspire me to be thankful for the favours you bestowed upon me and my parents, and that I may perform the deeds pleasing to You, and keep merit among my offspring; I have inclined towards you and I am a Muslim.\u201d (* This verse was revealed concerning S. Abu Bakr \u2013 the first caliph, R. A. A)"},"4526":{"id":4526,"surah":46,"ayah":16,"verse":"These are the ones whose good deeds We will accept, and overlook their shortfalls \u2013 among the People of Paradise; a true promise which is being given to them."},"4527":{"id":4527,"surah":46,"ayah":17,"verse":"And the one who said to his parents, \u201cUff \u2013 I am fed up with both of you! What! You promise me that I will be raised again whereas generations have passed away before me?\u201d And they both seek Allah\u2019s help and say to him, \u201cMay you be ruined, accept faith! Indeed Allah\u2019s promise is true\u201d; he therefore answers, \u201cThis is nothing except stories of former people.\u201d"},"4528":{"id":4528,"surah":46,"ayah":18,"verse":"It is these upon whom the Word has proved true, among the nations that passed away before them, of jinns and men; indeed they were losers."},"4529":{"id":4529,"surah":46,"ayah":19,"verse":"And for all persons are ranks according to their deeds; and so that He may repay them in full for their deeds, and they will not be wronged."},"4530":{"id":4530,"surah":46,"ayah":20,"verse":"And on the day when the disbelievers will be presented upon the fire; it will be said to them, \u201cYou have wasted your portion of the good things in the life of this world and enjoyed them; so this day you will be repaid with the disgraceful punishment, the recompense of your wrongfully priding yourself in the land, and because you used to disobey.\u201d"},"4531":{"id":4531,"surah":46,"ayah":21,"verse":"And remember the fellowman of the tribe of A\u2019ad; when he warned his nation in the Ahqaf* \u2013 and indeed Heralds of warning passed away before and after him \u2013 that, \u201cDo not worship anyone except Allah; indeed I fear the punishment of a Great Day upon you. (* A country among sand dunes.)"},"4532":{"id":4532,"surah":46,"ayah":22,"verse":"They said, \u201cHave you come to us in order to turn us away from our Gods? Therefore bring upon us what you promise us, if you are truthful.\u201d"},"4533":{"id":4533,"surah":46,"ayah":23,"verse":"He said, \u201cIts knowledge is only with Allah; and I convey to you the messages of my Lord, but I perceive that you are an ignorant nation.\u201d"},"4534":{"id":4534,"surah":46,"ayah":24,"verse":"So when they saw the punishment coming towards their valleys, spread like a cloud on the horizon, they said, \u201cThat is a cloud which will shower rain upon us\u201d; said Hud \u201cIn fact this is what you were being impatient for; a windstorm carrying a painful punishment.\u201d"},"4535":{"id":4535,"surah":46,"ayah":25,"verse":"\u201cThat destroys all things by the command of its Lord\u201d \u2013 so at morning none could be seen except their empty houses; this is the sort of punishment We mete out to the guilty."},"4536":{"id":4536,"surah":46,"ayah":26,"verse":"And We had indeed given them the means which We have not given you, and made ears and eyes and hearts for them; so their ears and eyes and hearts did not benefit them at all because they used to deny the signs of Allah, and the punishment they used to mock at encompassed them."},"4537":{"id":4537,"surah":46,"ayah":27,"verse":"And We indeed destroyed townships surrounding you, and brought several signs so that they may desist."},"4538":{"id":4538,"surah":46,"ayah":28,"verse":"So why did they \u2013 the ones whom they had chosen as Gods as a means of attaining Allah\u2019s proximity \u2013 not help them? In fact the disbelievers lost them; and this is just their slander and fabrication."},"4539":{"id":4539,"surah":46,"ayah":29,"verse":"And when We sent a number of jinns towards you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), listening attentively to the Qur\u2019an; so when they presented themselves there, they said to each other, \u201cListen quietly!\u201d; and when the recitation finished, they turned back to their people, giving them warning."},"4540":{"id":4540,"surah":46,"ayah":30,"verse":"They said, \u201cO our people! We have indeed heard a Book, sent down after Moosa, which confirms the Books preceding it, and guides towards the Truth and the Straight Path.\u201d"},"4541":{"id":4541,"surah":46,"ayah":31,"verse":"\u201cO our people! Listen to Allah\u2019s caller and accept faith in Him, so that He may forgive you some of your sins and save you from the painful punishment.\u201d"},"4542":{"id":4542,"surah":46,"ayah":32,"verse":"\u201cAnd whoever does not listen to Allah\u2019s caller cannot escape in the earth, and he has no supporters against Allah; they are in open error.\u201d"},"4543":{"id":4543,"surah":46,"ayah":33,"verse":"Have they not realised that Allah, Who did not tire in creating the heavens and the earth and in creating them, is Able to revive the dead? Surely yes, why not? Indeed He is Able to do all things."},"4544":{"id":4544,"surah":46,"ayah":34,"verse":"And on the day when the disbelievers are presented upon the fire; it will be said, \u201cIs this not a reality?\u201d They will answer, \u201cBy oath of our Lord, surely yes, why not?\u201d; it will be said, \u201cTherefore taste the punishment, the recompense of your disbelief.\u201d"},"4545":{"id":4545,"surah":46,"ayah":35,"verse":"Therefore patiently endure (O dear Prophet Mohammed \u2013 peace and blessings be upon him) like the courageous Noble Messengers had endured, and do not be impatient for them; on the day when they see what they are promised, it will be as if they had not stayed on earth except part of a day; this is to be conveyed; will anyone be destroyed, except the disobedient?"},"4546":{"id":4546,"surah":47,"ayah":1,"verse":"Allah has destroyed the deeds of those who disbelieved and prevented from Allah\u2019s way."},"4547":{"id":4547,"surah":47,"ayah":2,"verse":"And those who accepted faith and did good deeds and believed in what has been sent down upon Mohammed (peace and blessings be upon him) \u2013 and that is the truth from their Lord \u2013 Allah has relieved them of some of their evils and refined their condition."},"4548":{"id":4548,"surah":47,"ayah":3,"verse":"This is because the disbelievers followed falsehood and the believers followed the Truth which is from their Lord; this is how Allah illustrates the examples of people to them."},"4549":{"id":4549,"surah":47,"ayah":4,"verse":"So when you confront the disbelievers, strike at their necks; until when you have slain them in plenty, tie them up firmly; then after that, you may either release them as a favour or take ransom, until the war lays down its ordeal; this is it; and had Allah willed He Himself could have taken revenge from them, but this is to test some of you with others; and Allah will surely never waste the deeds of those who were killed in His way."},"4550":{"id":4550,"surah":47,"ayah":5,"verse":"He will soon guide them (towards Paradise) and make them succeed."},"4551":{"id":4551,"surah":47,"ayah":6,"verse":"And He will admit them into Paradise \u2013 they have been made familiar with it."},"4552":{"id":4552,"surah":47,"ayah":7,"verse":"O People who Believe! If you help the religion of Allah, He will help you and will stabilise you."},"4553":{"id":4553,"surah":47,"ayah":8,"verse":"And for those who disbelieve \u2013 may they be ruined, and may Allah destroy all their deeds!"},"4554":{"id":4554,"surah":47,"ayah":9,"verse":"This is because they disliked what Allah has sent down \u2013 He has therefore squandered all their deeds."},"4555":{"id":4555,"surah":47,"ayah":10,"verse":"So did they not travel in the land to see what sort of fate befell those who preceded them? Allah poured ruin upon them; and for the disbelievers are several like it."},"4556":{"id":4556,"surah":47,"ayah":11,"verse":"This is because Allah is the Supporter of the believers, whereas the disbelievers do not have any supporter."},"4557":{"id":4557,"surah":47,"ayah":12,"verse":"Allah will indeed admit those who believed and did good deeds into Gardens beneath which rivers flow; and the disbelievers enjoy, and they eat like the cattle eat, and their destination is in the fire."},"4558":{"id":4558,"surah":47,"ayah":13,"verse":"And many a township existed which was stronger than your town (O dear Prophet Mohammed \u2013 peace and blessings be upon him) \u2013 those who removed you from your town \u2013 We destroyed them, so they do not have a supporter!"},"4559":{"id":4559,"surah":47,"ayah":14,"verse":"So will one who is upon on a clear proof from his Lord, ever be like any of those whose evil deeds are made to appear good to them and they follow their own desires?"},"4560":{"id":4560,"surah":47,"ayah":15,"verse":"A description of the Garden which is promised to the pious; in it are rivers of water which shall never pollute; and rivers of milk the taste of which shall never change; and rivers of wine delicious to drink; and rivers of purified honey; and in it for them are fruits of all kinds, and the forgiveness of their Lord; so will such ever be equal to those who are to stay in the fire for ever and who will be given boiling water to drink so that it tears their guts apart?"},"4561":{"id":4561,"surah":47,"ayah":16,"verse":"And among them are some who listen to what you say; until when they go away from you, they say to those who have been given knowledge, \u201cWhat did he say now?\u201d; they are those whose hearts Allah has sealed, and they follow their own desires."},"4562":{"id":4562,"surah":47,"ayah":17,"verse":"And those who attained the right path \u2013 Allah increases the guidance for them and bestows their piety to them."},"4563":{"id":4563,"surah":47,"ayah":18,"verse":"So what are they waiting for, except that the Last Day suddenly come upon them? For its signs have already appeared; so when it does come, of what use is their realising it?"},"4564":{"id":4564,"surah":47,"ayah":19,"verse":"Therefore know (O dear Prophet Mohammed \u2013 peace and blessings be upon him) that there is none worthy of worship except Allah, and seek the forgiveness of sins of your close ones and for the common believing men and women; and Allah knows your movements during the day and your resting during the night."},"4565":{"id":4565,"surah":47,"ayah":20,"verse":"And the Muslims say, \u201cWhy was not a chapter sent down?\u201d So when a positive chapter was sent down, and war was commanded in it, you will see those in whose hearts is a disease looking at you with the dazed looks of a dying man; so it would be better for them. \u2013"},"4566":{"id":4566,"surah":47,"ayah":21,"verse":"To obey and speak good; so when the manifest command came \u2013 it would have been better for them if they had remained faithful to Allah."},"4567":{"id":4567,"surah":47,"ayah":22,"verse":"So do you* portray that if you get governance, you would spread chaos in the land and sever your relations? (* The hypocrites)"},"4568":{"id":4568,"surah":47,"ayah":23,"verse":"It is these whom Allah has cursed, so He made them deaf to the Truth and blinded their eyes."},"4569":{"id":4569,"surah":47,"ayah":24,"verse":"So do they not ponder the Qur\u2019an deeply, or are there locks on some of their hearts?"},"4570":{"id":4570,"surah":47,"ayah":25,"verse":"Indeed those who turn back after the guidance had become clear to them \u2013 Satan has deceived them; and made them optimistic of living for ages."},"4571":{"id":4571,"surah":47,"ayah":26,"verse":"This is because they said to those who dislike what Allah has sent down, \u201cWe will obey you regarding one matter*\u201d; and Allah knows their secrets. (* To fight against the Holy Prophet)."},"4572":{"id":4572,"surah":47,"ayah":27,"verse":"So how (dreadful) will it be when the angels remove their souls, striking at their faces and their backs!"},"4573":{"id":4573,"surah":47,"ayah":28,"verse":"This is because they followed the matter which displeases Allah, and they disliked what pleases Him \u2013 He therefore squandered away all their deeds."},"4574":{"id":4574,"surah":47,"ayah":29,"verse":"What! Do they, in whose hearts is a disease, fancy that Allah will not expose concealed enmities?"},"4575":{"id":4575,"surah":47,"ayah":30,"verse":"And if We will, We can show them to you so that you may recognise them by their faces; and you will surely recognise them by the way they talk; and Allah knows your deeds."},"4576":{"id":4576,"surah":47,"ayah":31,"verse":"And We shall indeed test you until We make known the warriors and the steadfast among you \u2013 and to test your proclamations."},"4577":{"id":4577,"surah":47,"ayah":32,"verse":"Indeed those who disbelieved and prevented others from Allah\u2019s way, and opposed the Noble Messenger after the guidance had become clear to them \u2013 they cannot harm Allah in the least; and soon He will squander away their deeds."},"4578":{"id":4578,"surah":47,"ayah":33,"verse":"O People who Believe! Obey Allah and obey the Noble Messenger, and do not render your deeds void."},"4579":{"id":4579,"surah":47,"ayah":34,"verse":"Indeed those who disbelieved and prevented others from Allah\u2019s way, and then died as disbelievers \u2013 so Allah will never forgive them."},"4580":{"id":4580,"surah":47,"ayah":35,"verse":"Therefore do not relax, nor call towards truce by yourself; and it is you who will dominate; and Allah is with you, and He will never cause a loss in your deeds."},"4581":{"id":4581,"surah":47,"ayah":36,"verse":"The worldly life is just a sport and pastime; and if you accept faith and be pious, He will bestow your rewards to you, and not at all ask you for your wealth."},"4582":{"id":4582,"surah":47,"ayah":37,"verse":"Were He to ask it from you, and ask in plenty, you would be miserly, and the miserliness would expose the filth within your hearts."},"4583":{"id":4583,"surah":47,"ayah":38,"verse":"Yes, undoubtedly it is you who are being called, that you may spend in Allah\u2019s way; so some among you act miserly; and whoever is miserly, is being a miser upon himself; and Allah is the Independent (Wealthy \u2013 Not requiring anything), whereas you all are needy; and if you renege, He will replace you with other people \u2013 and they will not be like you."},"4584":{"id":4584,"surah":48,"ayah":1,"verse":"We have indeed, for your sake (O dear Prophet Mohammed \u2013 peace and blessings be upon him), bestowed a clear victory."},"4585":{"id":4585,"surah":48,"ayah":2,"verse":"So that Allah may forgive, for your sake, the sins of those before you and those after you, and complete His favours upon you, and to show you the Straight Path."},"4586":{"id":4586,"surah":48,"ayah":3,"verse":"And so that Allah may provide you a great help."},"4587":{"id":4587,"surah":48,"ayah":4,"verse":"It is He Who instilled peace in the hearts of the believers, so that it may increase their strength of conviction; and to Allah only belong the armies of the heavens and the earth; and Allah is All Knowing, Wise."},"4588":{"id":4588,"surah":48,"ayah":5,"verse":"In order to admit the believing men and believing women into Gardens beneath which rivers flow, in which they will abide, and to relieve them of their misdeeds; and this, in Allah\u2019s sight, is the greatest success."},"4589":{"id":4589,"surah":48,"ayah":6,"verse":"And to punish the hypocrite men and hypocrite women, and the polytheist men and polytheist women, who think evilly about Allah; upon them only is the evil cycle of misfortune; and Allah has wreaked anger upon them, and has cursed them, and has prepared hell for them; and what an evil destination."},"4590":{"id":4590,"surah":48,"ayah":7,"verse":"And to Allah only belong the armies of the heavens and the earth; and Allah is Most Honourable, Wise."},"4591":{"id":4591,"surah":48,"ayah":8,"verse":"We have indeed sent you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) as a present witness and a Herald of glad tidings and warnings. (The Holy Prophet is a witness from Allah.)"},"4592":{"id":4592,"surah":48,"ayah":9,"verse":"In order that you, O people, may accept faith in Allah and His Noble Messenger, and honour and revere the Noble Messenger; and may say the Purity of Allah, morning and evening. (To honour the Holy Prophet \u2013 peace and blessings be upon him \u2013 is part of faith. To disrespect him is blasphemy.)"},"4593":{"id":4593,"surah":48,"ayah":10,"verse":"Those who swear allegiance to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him), do indeed in fact swear allegiance to Allah; Allah's Hand* of Power is above their hands; so whoever breaches his oath, has breached his own greater promise; and whoever fulfils the covenant he has with Allah \u2013 so very soon Allah will bestow upon him a great reward. (Used as a metaphor.)"},"4594":{"id":4594,"surah":48,"ayah":11,"verse":"The ignorant ones who had stayed behind will now say to you, \u201cOur wealth and our families prevented us from going by keeping us pre-occupied, therefore seek forgiveness for us\u201d; they utter with their tongues what is not in their hearts; say, \u201cSo does anyone have any control over you against Allah, if He wills to harm you or provide you benefit? In fact Allah is Aware of what you do.\u201d"},"4595":{"id":4595,"surah":48,"ayah":12,"verse":"\u201cRather you had assumed that the Noble Messenger and the Muslims will never return to their homes, and you thought this as good within your hearts, whereas you had thought evilly; and you were a people about to be ruined.\u201d"},"4596":{"id":4596,"surah":48,"ayah":13,"verse":"And whoever does not accept faith in Allah and His Noble Messenger \u2013 We have indeed kept prepared a blazing fire for disbelievers."},"4597":{"id":4597,"surah":48,"ayah":14,"verse":"And for Allah only is the kingship of the heavens and the earth; He may forgive whomever He wills, and punish whomever He wills; and Allah is Oft Forgiving, Most Merciful."},"4598":{"id":4598,"surah":48,"ayah":15,"verse":"Those who had stayed behind will now say, \u201cWhen you go to receive the war booty, let us also go with you\u201d; they wish to change the Words of Allah; say \u201cYou shall never come with us \u2013 this is already decreed by Allah\u201d; so they will now say, \u201cBut rather you envy us\u201d; in fact they never understood except a little."},"4599":{"id":4599,"surah":48,"ayah":16,"verse":"Say to the ignorant who stayed behind, \u201cYou will soon be called against a nation of great military strength \u2013 to fight against them or that they become Muslims; so if you obey, Allah will give you an excellent reward; and if you turn away, the way you had turned away before, He will mete out a painful punishment to you.\u201d"},"4600":{"id":4600,"surah":48,"ayah":17,"verse":"There is no reproach upon the blind, nor reproach against the lame, nor reproach upon the sick; and whoever obeys Allah and His Noble Messenger \u2013 Allah will admit him into Gardens beneath which rivers flow; and whoever turns away \u2013 He will mete out a painful punishment to him."},"4601":{"id":4601,"surah":48,"ayah":18,"verse":"Indeed Allah was truly pleased with the believers when they swore allegiance to you beneath the tree \u2013 so He knew what was in their hearts \u2013 He therefore sent down peace upon them, and rewarded them with an imminent victory."},"4602":{"id":4602,"surah":48,"ayah":19,"verse":"And plenty of war booty, to take; and Allah is Most Honourable, Wise."},"4603":{"id":4603,"surah":48,"ayah":20,"verse":"Allah has promised you plenty of booty which you will take, and has bestowed this to you quickly, and restrained peoples\u2019 hands from you; and in order that it may be a sign for the believers, and to guide you on the Straight Path."},"4604":{"id":4604,"surah":48,"ayah":21,"verse":"And one more, not within your capacity, is within Allah\u2019s hold; and Allah is Able to do all things."},"4605":{"id":4605,"surah":48,"ayah":22,"verse":"And if the disbelievers were to fight you, they will turn away and flee, and then they will not find any supporter nor any aide."},"4606":{"id":4606,"surah":48,"ayah":23,"verse":"The tradition of Allah, ongoing since before; and you will not find the tradition of Allah changing."},"4607":{"id":4607,"surah":48,"ayah":24,"verse":"And it is He Who restrained their hands from you, and your hands from them in the valley of Mecca, after having given you control over them; and Allah sees all what you do."},"4608":{"id":4608,"surah":48,"ayah":25,"verse":"It was these who disbelieved and prevented you from the Sacred Mosque, and stopped the sacrificial animals from reaching their place; and were it not for some Muslim men and Muslim women, whom you do not know \u2013 lest you may crush them and unintentionally incur some violation due to them \u2013 Allah would have permitted you to slay them; this relief for them, is so that Allah may admit into His mercy whomever He wills; and had they been separated, We would have indeed punished the disbelievers among them with a painful punishment."},"4609":{"id":4609,"surah":48,"ayah":26,"verse":"Whereas the disbelievers had set up in their hearts an obstinacy \u2013 the same obstinacy of the days of ignorance \u2013 so Allah sent down His solace upon His Noble Messenger and upon the believers, and decreed upon them the words of piety, and they were more deserving and suitable for it; and Allah is the All Knowing."},"4610":{"id":4610,"surah":48,"ayah":27,"verse":"Allah has indeed made the truthful dream of His Noble Messenger, come true; indeed you will all enter the Sacred Mosque, if Allah wills, in safety \u2013 with your heads shaven or hair cut short \u2013 without fear; so He knows what you do not know, and has therefore ordained another imminent victory before this."},"4611":{"id":4611,"surah":48,"ayah":28,"verse":"It is He Who has sent His Noble Messenger with the guidance and the true religion, in order to make it prevail over all other religions; and Allah is sufficient as a Witness. (The Holy Prophet is a light from Allah.)"},"4612":{"id":4612,"surah":48,"ayah":29,"verse":"Mohammed (peace and blessings be upon him) is the Noble Messenger of Allah; and his companions are stern with the disbelievers and merciful among themselves \u2013 you will see them bowing and falling in prostration, seeking Allah\u2019s munificence and His pleasure; their signs are on their faces, from the effects of their prostration; this trait of theirs is mentioned in the Taurat; and their trait is mentioned in the Injeel; like a cultivation that sprouted its shoot, then strengthened it, then thickened and then stood firm upon its stem, pleasing the farmer \u2013 in order to enrage the disbelievers with them; Allah has promised forgiveness and a great reward to those among them who have faith and do good deeds."},"4613":{"id":4613,"surah":49,"ayah":1,"verse":"O People who Believe! Do not advance ahead of Allah and His Noble Messenger, and fear Allah; indeed Allah is All Hearing, All Knowing."},"4614":{"id":4614,"surah":49,"ayah":2,"verse":"O People who Believe! Do not raise your voices higher than the voice of the Prophet, nor speak to him loudly the way you shout to one another, lest your deeds go to waste whilst you are unaware. (Faith will go waste due to the slightest disrespect towards the Holy Prophet \u2013 peace and blessings be upon him. To honour him is part of faith. To disrespect him is blasphemy.)"},"4615":{"id":4615,"surah":49,"ayah":3,"verse":"Indeed those who suppress their voices in the presence of Allah\u2019s Noble Messenger, are the ones whose hearts Allah has tested for piety; for them is forgiveness, and a great reward."},"4616":{"id":4616,"surah":49,"ayah":4,"verse":"Indeed most of those who call you from outside the chambers do not have sense."},"4617":{"id":4617,"surah":49,"ayah":5,"verse":"And had they been patient until you yourself came out to them, it would be better for them; and Allah is Oft Forgiving, Most Merciful."},"4618":{"id":4618,"surah":49,"ayah":6,"verse":"O People who Believe! If any miscreant brings you some tidings, verify it, lest you unknowingly cause suffering to some people, and then remain repenting for what you did."},"4619":{"id":4619,"surah":49,"ayah":7,"verse":"And know that Allah\u2019s Noble Messenger is among you; in most of the affairs, were he to listen to you, you would surely fall into hardship, but Allah has made faith dear to you and has instilled it in your hearts, and has made disbelief and rebellion and disobedience abhorred by you; it is people like these who are on guidance."},"4620":{"id":4620,"surah":49,"ayah":8,"verse":"The munificence and favour of Allah; and Allah is All Knowing, Wise."},"4621":{"id":4621,"surah":49,"ayah":9,"verse":"And if two groups of Muslims fight against each other, reconcile them; and if one of them oppresses the other, fight against the oppressor till it returns to the command of Allah; then if it returns, reconcile between them with justice, and be fair; indeed Allah loves the equitable."},"4622":{"id":4622,"surah":49,"ayah":10,"verse":"The Muslims are brothers to each other, therefore make peace between your two brothers and fear Allah, so that you may gain mercy. (The entire Muslim nation is a single brotherhood, without any distinction for caste, creed or colour.)"},"4623":{"id":4623,"surah":49,"ayah":11,"verse":"O People who Believe! Men must not ridicule other men for it could be that the ridiculed are better than the mockers, nor must the women ridicule other women for the ridiculed women may be better than the mockers; and do not insult one another, nor assign evil nicknames; how base it is to be called a sinner after being a Muslim! And whoever does not repent \u2013 then it is they who are unjust."},"4624":{"id":4624,"surah":49,"ayah":12,"verse":"O People who Believe! Avoid excessive assumptions; indeed assumption sometimes becomes a sin, and do not seek faults, and do not slander one another; would any one among you like to eat the flesh of his dead brother? So you will hate that! And fear Allah; indeed Allah is Most Acceptor of Repentance, Most Merciful."},"4625":{"id":4625,"surah":49,"ayah":13,"verse":"O mankind! We have indeed created you from one man and one woman, and have made you into various nations and tribes so that you may know one another; indeed the more honourable among you, in the sight of Allah, is one who is more pious among you; indeed Allah is All Knowing, All Aware. (Piety is the basis of honour in Allah\u2019s sight.)"},"4626":{"id":4626,"surah":49,"ayah":14,"verse":"The ignorant said, \u201cWe have accepted faith\u201d; say, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cYou have surely not accepted faith, but you should say \u2018We have submitted\u2019, for faith has not yet entered your hearts; and if you obey* Allah and His Noble Messenger, He will not reduce the reward of any of your deeds; indeed Allah is Oft Forgiving, Most Merciful.\u201d (* By accepting faith and then obeying the commands)."},"4627":{"id":4627,"surah":49,"ayah":15,"verse":"The true believers are only those who accepted faith in Allah and His Noble Messenger and then did not have any doubt, and fought with their wealth and their lives in Allah\u2019s way; it is they who are the truthful."},"4628":{"id":4628,"surah":49,"ayah":16,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cAre you teaching Allah your own religion? Whereas Allah knows all whatever is in the heavens and all whatever is in the earth; and Allah knows all things.\u201d"},"4629":{"id":4629,"surah":49,"ayah":17,"verse":"They portray that they are doing a great favour to you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) by having become Muslims; say, \u201cDo not think that you have done me a favour by accepting Islam; in fact Allah has bestowed a favour upon you, for He guided you to Islam, if you are truthful.\u201d"},"4630":{"id":4630,"surah":49,"ayah":18,"verse":"Indeed Allah knows all the hidden of the heavens and the earth; and Allah is seeing your deeds."},"4631":{"id":4631,"surah":50,"ayah":1,"verse":"Qaf; by oath of the Glorious Qur\u2019an."},"4632":{"id":4632,"surah":50,"ayah":2,"verse":"Rather they were shocked that a Herald of Warning came to them from among themselves, so the disbelievers said, \u201cThis is something really strange.\u201d"},"4633":{"id":4633,"surah":50,"ayah":3,"verse":"\u201cWhen we are dead and have turned to dust, will we be raised again? That return is impossible!\u201d"},"4634":{"id":4634,"surah":50,"ayah":4,"verse":"We know all what the earth decreases from them; and with Us is a Book that retains."},"4635":{"id":4635,"surah":50,"ayah":5,"verse":"In fact they denied the Truth when it came to them, so they are now in a dilemma."},"4636":{"id":4636,"surah":50,"ayah":6,"verse":"So did they not see the sky above them \u2013 how We have made it and beautified it, and there are no cracks in it?"},"4637":{"id":4637,"surah":50,"ayah":7,"verse":"And (how) We have spread the earth, and placed mountains as anchors in it, and have grown all kinds of beautiful pairs in it?"},"4638":{"id":4638,"surah":50,"ayah":8,"verse":"A perception and an understanding, for every bondman who inclines."},"4639":{"id":4639,"surah":50,"ayah":9,"verse":"And We sent down the auspicious water from the sky, therefore producing gardens with it, and the grain that is harvested."},"4640":{"id":4640,"surah":50,"ayah":10,"verse":"And high date-palms with lush fruit."},"4641":{"id":4641,"surah":50,"ayah":11,"verse":"As sustenance for the bondmen; and with it We revived a dead city; this is how you will be raised."},"4642":{"id":4642,"surah":50,"ayah":12,"verse":"Before these, the people of Nooh had denied, and so did the dwellers of Rass and the Thamud."},"4643":{"id":4643,"surah":50,"ayah":13,"verse":"And the tribe of A\u2019ad, and Firaun, and the fellowmen of Lut."},"4644":{"id":4644,"surah":50,"ayah":14,"verse":"And the Dwellers of the Woods, and the people of Tubb\u2019a; each one of them one denied the Noble Messengers, so My promised punishment proved true."},"4645":{"id":4645,"surah":50,"ayah":15,"verse":"So did We tire by creating the first time? Rather they doubt being created again."},"4646":{"id":4646,"surah":50,"ayah":16,"verse":"And indeed We have created man and We know what his soul instils in him; and We are nearer to him than the hearts artery."},"4647":{"id":4647,"surah":50,"ayah":17,"verse":"When the two receiving angels receive from him, one seated on the right and one on the left."},"4648":{"id":4648,"surah":50,"ayah":18,"verse":"He does not utter a single word, without a ready recorder seated next to him."},"4649":{"id":4649,"surah":50,"ayah":19,"verse":"And the hardship of death came with the truth; \u201cThis is what you were escaping from!\u201d"},"4650":{"id":4650,"surah":50,"ayah":20,"verse":"And the Trumpet was blown; \u201cThis is the Day of the promised punishment.\u201d"},"4651":{"id":4651,"surah":50,"ayah":21,"verse":"And every soul came, along with a herder and a witness."},"4652":{"id":4652,"surah":50,"ayah":22,"verse":"\u201cYou were indeed neglectful of this, so We have removed the veil for you, and your eyesight is sharp this day.\u201d"},"4653":{"id":4653,"surah":50,"ayah":23,"verse":"And his accompanying angel said, \u201cThis is the record of your deeds, available with me.\u201d"},"4654":{"id":4654,"surah":50,"ayah":24,"verse":"It will be said to the angels, \u201cBoth of you fling every excessive ungrateful, stubborn person into hell.\u201d"},"4655":{"id":4655,"surah":50,"ayah":25,"verse":"\u201cThe one who excessively prevented from virtue, the transgressor, the doubtful.\u201d"},"4656":{"id":4656,"surah":50,"ayah":26,"verse":"\u201cThe one who appointed another God along with Allah \u2013 so both of you fling him into the severe punishment.\u201d"},"4657":{"id":4657,"surah":50,"ayah":27,"verse":"His accompanying devil said, \u201cOur Lord! I did not cause him to rebel, but he himself was in extreme error.\u201d"},"4658":{"id":4658,"surah":50,"ayah":28,"verse":"He will say, \u201cDo not dispute before Me \u2013 I had already warned you of the punishment.\u201d"},"4659":{"id":4659,"surah":50,"ayah":29,"verse":"\u201cWith Me, the decree does not change, and nor do I oppress the bondmen.\u201d"},"4660":{"id":4660,"surah":50,"ayah":30,"verse":"The day when We will ask hell, \u201cAre you filled up?\u201d and it will answer, \u201cAre there some more?\u201d"},"4661":{"id":4661,"surah":50,"ayah":31,"verse":"And Paradise will be brought close to the pious, not far away from them."},"4662":{"id":4662,"surah":50,"ayah":32,"verse":"This is what you are promised \u2013 for every repenting, careful person."},"4663":{"id":4663,"surah":50,"ayah":33,"verse":"Who fears the Most Gracious without seeing, and came with a heart that inclines."},"4664":{"id":4664,"surah":50,"ayah":34,"verse":"It will be said to him, \u201cEnter it in peace; this is the day of eternity.\u201d"},"4665":{"id":4665,"surah":50,"ayah":35,"verse":"In it for them is all that they may desire, and with Us is more than it."},"4666":{"id":4666,"surah":50,"ayah":36,"verse":"And many a generation We did destroy before them, who exceeded them in strength, therefore venturing in the cities! Is there a place to escape?"},"4667":{"id":4667,"surah":50,"ayah":37,"verse":"Indeed in this is an advice for anyone who keeps a heart, or listens attentively."},"4668":{"id":4668,"surah":50,"ayah":38,"verse":"And We have indeed created the heavens and the earth, and all what is between them, in six days; and fatigue did not even approach Us."},"4669":{"id":4669,"surah":50,"ayah":39,"verse":"Therefore (O dear prophet Mohammed \u2013 peace and blessings be upon him) patiently bear upon what they say, and praising your Lord say His Purity before the sun rises and before the sun sets."},"4670":{"id":4670,"surah":50,"ayah":40,"verse":"And say His Purity during the night, and after the customary prayers."},"4671":{"id":4671,"surah":50,"ayah":41,"verse":"And listen attentively on the day when an announcer will proclaim from a place nearby."},"4672":{"id":4672,"surah":50,"ayah":42,"verse":"The day when all will hear the Scream with the truth; this is the day of coming out of the graves."},"4673":{"id":4673,"surah":50,"ayah":43,"verse":"Indeed it is We Who give life, and give death, and towards Us only is the return."},"4674":{"id":4674,"surah":50,"ayah":44,"verse":"On the day when the earth will split for them, so they will come out in haste; this is the gathering \u2013 easy for Us."},"4675":{"id":4675,"surah":50,"ayah":45,"verse":"We well know what they say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), and you are not one to use force over them; therefore advise him with the Qur\u2019an, whoever fears My threat."},"4676":{"id":4676,"surah":51,"ayah":1,"verse":"By oath of those which carry away while dispersing."},"4677":{"id":4677,"surah":51,"ayah":2,"verse":"Then by oath of those which carry the burdens."},"4678":{"id":4678,"surah":51,"ayah":3,"verse":"Then by oath of those which move with ease."},"4679":{"id":4679,"surah":51,"ayah":4,"verse":"Then by oath of those which distribute by the command."},"4680":{"id":4680,"surah":51,"ayah":5,"verse":"Undoubtedly, the promise you are given is surely true."},"4681":{"id":4681,"surah":51,"ayah":6,"verse":"And undoubtedly justice will surely be done."},"4682":{"id":4682,"surah":51,"ayah":7,"verse":"And by oath of the decorated heaven."},"4683":{"id":4683,"surah":51,"ayah":8,"verse":"You are indeed in different opinions regarding this Qur\u2019an."},"4684":{"id":4684,"surah":51,"ayah":9,"verse":"Only those who are destined to revert, are reverted from it."},"4685":{"id":4685,"surah":51,"ayah":10,"verse":"Slain be those who mould from their imaginations."},"4686":{"id":4686,"surah":51,"ayah":11,"verse":"Those who have forgotten in a state of intoxication."},"4687":{"id":4687,"surah":51,"ayah":12,"verse":"They ask, \u201cWhen will be the Day of Judgement?\u201d"},"4688":{"id":4688,"surah":51,"ayah":13,"verse":"It will be on the day when they will be roasted in the fire."},"4689":{"id":4689,"surah":51,"ayah":14,"verse":"And it will be said to them, \u201cTaste your own roasting; this is what you were impatient for.\u201d"},"4690":{"id":4690,"surah":51,"ayah":15,"verse":"Indeed the pious are among Gardens and water springs."},"4691":{"id":4691,"surah":51,"ayah":16,"verse":"Accepting the rewards given by their Lord; they were indeed virtuous before this."},"4692":{"id":4692,"surah":51,"ayah":17,"verse":"They used to sleep only a little during the night."},"4693":{"id":4693,"surah":51,"ayah":18,"verse":"And used to seek forgiveness before dawn."},"4694":{"id":4694,"surah":51,"ayah":19,"verse":"And the beggar and the destitute had a share in their wealth."},"4695":{"id":4695,"surah":51,"ayah":20,"verse":"And in the earth are signs for those who are certain."},"4696":{"id":4696,"surah":51,"ayah":21,"verse":"And in your own selves; so can you not perceive?"},"4697":{"id":4697,"surah":51,"ayah":22,"verse":"And in the heaven lies your sustenance, and the promise you are given."},"4698":{"id":4698,"surah":51,"ayah":23,"verse":"So by oath of the Lord of the heavens and the earth, this Qur\u2019an is the Truth, in the same language that you speak."},"4699":{"id":4699,"surah":51,"ayah":24,"verse":"Did the news of Ibrahim\u2019s honourable guests reach you (O dear Prophet Mohammed \u2013 peace and blessings be upon him)?"},"4700":{"id":4700,"surah":51,"ayah":25,"verse":"When they came to him and said, \u201cPeace\u201d; he answered, \u201cPeace\u201d; and thought, \u201cThese people are not familiar.\u201d"},"4701":{"id":4701,"surah":51,"ayah":26,"verse":"Then he went to his home \u2013 so he brought a healthy calf."},"4702":{"id":4702,"surah":51,"ayah":27,"verse":"He then presented it before them and said, \u201cDo you not eat?\u201d"},"4703":{"id":4703,"surah":51,"ayah":28,"verse":"He therefore inwardly sensed fear of them; they said, \u201cDo not fear!\u201d; and they gave him the glad tidings of a knowledgeable son."},"4704":{"id":4704,"surah":51,"ayah":29,"verse":"So his wife came screaming, and striking her forehead cried, \u201cWhat! For a barren old woman?\u201d"},"4705":{"id":4705,"surah":51,"ayah":30,"verse":"They said, \u201cThis is how your Lord has decreed; indeed He only is the Wise, the All Knowing.\u201d"},"4706":{"id":4706,"surah":51,"ayah":31,"verse":"Said Ibrahim, \u201cWhat is your task, O the sent angels?\u201d"},"4707":{"id":4707,"surah":51,"ayah":32,"verse":"They said, \u201cWe have been sent towards a guilty nation.\u201d"},"4708":{"id":4708,"surah":51,"ayah":33,"verse":"\u201cTo throw upon them stones of baked clay.\u201d"},"4709":{"id":4709,"surah":51,"ayah":34,"verse":"\u201cThat are kept marked, with your Lord, for the transgressors.\u201d"},"4710":{"id":4710,"surah":51,"ayah":35,"verse":"So We removed the people who had faith, from that town."},"4711":{"id":4711,"surah":51,"ayah":36,"verse":"We therefore found just one house over there that was Muslim."},"4712":{"id":4712,"surah":51,"ayah":37,"verse":"And We kept a sign remaining in it, for those who fear the painful punishment."},"4713":{"id":4713,"surah":51,"ayah":38,"verse":"And in Moosa, when We sent him with a clear proof towards Firaun."},"4714":{"id":4714,"surah":51,"ayah":39,"verse":"In response, he turned away along with his army and said, \u201cHe is a magician or a madman.\u201d"},"4715":{"id":4715,"surah":51,"ayah":40,"verse":"We therefore seized him and his army and threw them into the sea, while he was blaming himself."},"4716":{"id":4716,"surah":51,"ayah":41,"verse":"And in the tribe of A\u2019ad, when we sent a dry windstorm upon them."},"4717":{"id":4717,"surah":51,"ayah":42,"verse":"It left out nothing in its path, but which it split up into fragments."},"4718":{"id":4718,"surah":51,"ayah":43,"verse":"And in the tribe of Thamud when it was told to them, \u201cEnjoy for a while.\u201d"},"4719":{"id":4719,"surah":51,"ayah":44,"verse":"In response they rebelled against their Lord\u2019s command, so the thunderbolt seized them whilst they were watching."},"4720":{"id":4720,"surah":51,"ayah":45,"verse":"So they were neither able to stand up, nor were they able to take revenge."},"4721":{"id":4721,"surah":51,"ayah":46,"verse":"And before them, We destroyed the people of Nooh; they were indeed a sinning nation."},"4722":{"id":4722,"surah":51,"ayah":47,"verse":"And We have built the heaven with hands (the Divine Power), and it is We Who give the expanse."},"4723":{"id":4723,"surah":51,"ayah":48,"verse":"And We made the earth a base, so how well do We lay out!"},"4724":{"id":4724,"surah":51,"ayah":49,"verse":"And We created all things in pairs, so that you may ponder."},"4725":{"id":4725,"surah":51,"ayah":50,"verse":"\u201cTherefore rush towards Allah; indeed I am from Him, a clear Herald of Warning towards you.\u201d"},"4726":{"id":4726,"surah":51,"ayah":51,"verse":"\u201cAnd do not appoint other deities along with Allah; indeed I am from Him, a clear Herald of Warning towards you.\u201d"},"4727":{"id":4727,"surah":51,"ayah":52,"verse":"Similarly, whenever a Noble Messenger came to those before them they always said, \u201cHe is a magician or a madman.\u201d"},"4728":{"id":4728,"surah":51,"ayah":53,"verse":"What! Have they willed this utterance to one another? In fact they are a rebellious people."},"4729":{"id":4729,"surah":51,"ayah":54,"verse":"Therefore turn away from them (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u2013 so there is no blame upon you."},"4730":{"id":4730,"surah":51,"ayah":55,"verse":"And advise, for advice benefits the Muslims."},"4731":{"id":4731,"surah":51,"ayah":56,"verse":"And I created the jinns and men, only for them to worship Me."},"4732":{"id":4732,"surah":51,"ayah":57,"verse":"I do not ask any sustenance from them, nor wish that they give Me food."},"4733":{"id":4733,"surah":51,"ayah":58,"verse":"Indeed it is Allah, Who is the Greatest Sustainer, the Strong, the Able."},"4734":{"id":4734,"surah":51,"ayah":59,"verse":"And indeed for these unjust is also a turn of punishment, like that of their companions \u2013 so they must not ask Me to hasten."},"4735":{"id":4735,"surah":51,"ayah":60,"verse":"So ruin is for disbelievers, from their day \u2013 which they are promised."},"4736":{"id":4736,"surah":52,"ayah":1,"verse":"By oath of (mount) Tur."},"4737":{"id":4737,"surah":52,"ayah":2,"verse":"And by oath of a passage, written \u2013"},"4738":{"id":4738,"surah":52,"ayah":3,"verse":"On an open record."},"4739":{"id":4739,"surah":52,"ayah":4,"verse":"And by oath of the Inhabited House."},"4740":{"id":4740,"surah":52,"ayah":5,"verse":"And the lofty roof."},"4741":{"id":4741,"surah":52,"ayah":6,"verse":"And the sea set aflame."},"4742":{"id":4742,"surah":52,"ayah":7,"verse":"Indeed your Lord\u2019s punishment will surely take place."},"4743":{"id":4743,"surah":52,"ayah":8,"verse":"No one can avert it."},"4744":{"id":4744,"surah":52,"ayah":9,"verse":"A day on which the heavens will shake with a visible shaking."},"4745":{"id":4745,"surah":52,"ayah":10,"verse":"And the mountains will move with a visible movement."},"4746":{"id":4746,"surah":52,"ayah":11,"verse":"So on that day, ruin is for those who deny."},"4747":{"id":4747,"surah":52,"ayah":12,"verse":"Those who are playing in pursuits."},"4748":{"id":4748,"surah":52,"ayah":13,"verse":"A day when they will be pushed, forcibly shoved towards the fire of hell."},"4749":{"id":4749,"surah":52,"ayah":14,"verse":"\u201cThis is the fire, which you used to deny!\u201d"},"4750":{"id":4750,"surah":52,"ayah":15,"verse":"\u201cSo is this magic, or are you unable to see?\u201d"},"4751":{"id":4751,"surah":52,"ayah":16,"verse":"\u201cEnter it \u2013 now whether you patiently bear it or are impatient \u2013 it is the same for you; for you is a recompense only for what you used to do.\u201d"},"4752":{"id":4752,"surah":52,"ayah":17,"verse":"Indeed the pious are in Gardens and peace."},"4753":{"id":4753,"surah":52,"ayah":18,"verse":"Delighted at the bestowal of their Lord; and their Lord has saved them from the fire."},"4754":{"id":4754,"surah":52,"ayah":19,"verse":"\u201cEat and drink with pleasure, a reward for what you used to do.\u201d"},"4755":{"id":4755,"surah":52,"ayah":20,"verse":"Reclining on thrones, in rows; and We have wedded them to maidens with gorgeous eyes."},"4756":{"id":4756,"surah":52,"ayah":21,"verse":"And those who accepted faith, and whose descendants followed them with faith \u2013 We have joined their descendants with them, and have not reduced anything for them from their deeds; every soul is trapped in its own deeds."},"4757":{"id":4757,"surah":52,"ayah":22,"verse":"And We aided them with fruit and meat, whatever they desire."},"4758":{"id":4758,"surah":52,"ayah":23,"verse":"In it, they accept cups from each other, in which is neither any lewdness nor any sin."},"4759":{"id":4759,"surah":52,"ayah":24,"verse":"And their boy servants shall go around them, as if they were pearls, safely hidden."},"4760":{"id":4760,"surah":52,"ayah":25,"verse":"And one of them turned towards the other, questioning."},"4761":{"id":4761,"surah":52,"ayah":26,"verse":"Saying, \u201cIndeed before this, we were in our houses, worried.\u201d"},"4762":{"id":4762,"surah":52,"ayah":27,"verse":"\u201cSo Allah did us a great favour, and saved us from the punishment of the flame.\u201d"},"4763":{"id":4763,"surah":52,"ayah":28,"verse":"\u201cIndeed we used to worship Him in our previous life; indeed He only is the Benign, the Most Merciful.\u201d"},"4764":{"id":4764,"surah":52,"ayah":29,"verse":"Therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him), enlighten, for by the munificence of your Lord, you are neither a soothsayer nor a madman."},"4765":{"id":4765,"surah":52,"ayah":30,"verse":"Or they allege, \u201cHe is a poet \u2013 we await a calamity of the times to befall him.\u201d"},"4766":{"id":4766,"surah":52,"ayah":31,"verse":"Proclaim, \u201cGo on waiting \u2013 I too am waiting along with you.\u201d"},"4767":{"id":4767,"surah":52,"ayah":32,"verse":"Is this what their senses tell them, or are they a rebellious people?"},"4768":{"id":4768,"surah":52,"ayah":33,"verse":"What! They say, \u201cHe has invented the Qur'an\u201d? Rather they do not have faith."},"4769":{"id":4769,"surah":52,"ayah":34,"verse":"So let them bring a single discourse like it, if they are truthful."},"4770":{"id":4770,"surah":52,"ayah":35,"verse":"Have they not been created from some source, or are they themselves the creators?"},"4771":{"id":4771,"surah":52,"ayah":36,"verse":"Or did they create the heavens and the earth? Rather they are not certain."},"4772":{"id":4772,"surah":52,"ayah":37,"verse":"Or do they have the treasures of your Lord, or are they the authority?"},"4773":{"id":4773,"surah":52,"ayah":38,"verse":"Or do they have any stairs, climbing upon which they eavesdrop? So their eavesdropper should bring some clear proof!"},"4774":{"id":4774,"surah":52,"ayah":39,"verse":"What! The daughters for Him, and the sons for you?"},"4775":{"id":4775,"surah":52,"ayah":40,"verse":"Or do you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) ask any fee from them, so they are burdened with the penalty?"},"4776":{"id":4776,"surah":52,"ayah":41,"verse":"Or is the hidden with them, by which they pass judgements?"},"4777":{"id":4777,"surah":52,"ayah":42,"verse":"Or are they planning a conspiracy? So the conspiracy will befall only upon the disbelievers."},"4778":{"id":4778,"surah":52,"ayah":43,"verse":"Or do they have a God besides Allah? Purity is to Allah from their ascribing of partners to Him."},"4779":{"id":4779,"surah":52,"ayah":44,"verse":"And were they to see a portion of the sky falling, they will say, \u201cIt is a heap of clouds.\u201d"},"4780":{"id":4780,"surah":52,"ayah":45,"verse":"Therefore leave them, until they confront their day, in which they will be stunned."},"4781":{"id":4781,"surah":52,"ayah":46,"verse":"A day on which their scheming will not benefit them at all, nor will they be helped."},"4782":{"id":4782,"surah":52,"ayah":47,"verse":"And indeed for the unjust is another punishment before this, but most of them do not know. (Punishment in the grave is proven by this verse.)"},"4783":{"id":4783,"surah":52,"ayah":48,"verse":"And be patient upon your Lord\u2019s command, (O dear Prophet Mohammed \u2013 peace and blessings be upon him), for you are indeed in Our sight; and proclaim the Purity of your Lord while praising Him, whenever you stand."},"4784":{"id":4784,"surah":52,"ayah":49,"verse":"And proclaim His Purity during the night, and when the stars turn back."},"4785":{"id":4785,"surah":53,"ayah":1,"verse":"By oath of the beloved shining star Mohammed (peace and blessings be upon him), when he returned from the Ascent."},"4786":{"id":4786,"surah":53,"ayah":2,"verse":"Your companion did not err, nor did he go astray."},"4787":{"id":4787,"surah":53,"ayah":3,"verse":"And he does not say anything by his own desire."},"4788":{"id":4788,"surah":53,"ayah":4,"verse":"It is but a divine revelation, which is revealed to him."},"4789":{"id":4789,"surah":53,"ayah":5,"verse":"He has been taught by the Extremely Powerful."},"4790":{"id":4790,"surah":53,"ayah":6,"verse":"The Strong; then the Spectacle inclined towards him."},"4791":{"id":4791,"surah":53,"ayah":7,"verse":"And he was on the horizon of the highest heaven."},"4792":{"id":4792,"surah":53,"ayah":8,"verse":"Then the Spectacle became closer, and came down in full view."},"4793":{"id":4793,"surah":53,"ayah":9,"verse":"So the distance between the Spectacle and the beloved was only two arms\u2019 length, or even less. (The Heavenly Journey of Prophet Mohammed \u2013 peace and blessings be upon him \u2013 was with body and soul.)"},"4794":{"id":4794,"surah":53,"ayah":10,"verse":"So Allah divinely revealed to His bondman, whatever He divinely revealed."},"4795":{"id":4795,"surah":53,"ayah":11,"verse":"The heart did not deny, what it saw. (The Holy Prophet was bestowed with seeing Allah \u2013 see also preceding verses 8, 9, 10.)"},"4796":{"id":4796,"surah":53,"ayah":12,"verse":"What! So do you dispute with him regarding what he saw?"},"4797":{"id":4797,"surah":53,"ayah":13,"verse":"And indeed he did see the Spectacle again."},"4798":{"id":4798,"surah":53,"ayah":14,"verse":"Near the lote-tree of the last boundary."},"4799":{"id":4799,"surah":53,"ayah":15,"verse":"Close to which is the Everlasting Paradise."},"4800":{"id":4800,"surah":53,"ayah":16,"verse":"When the lote-tree was being enveloped, by whatever around it."},"4801":{"id":4801,"surah":53,"ayah":17,"verse":"The sight did not shift, nor did it cross the limits."},"4802":{"id":4802,"surah":53,"ayah":18,"verse":"Indeed he saw the supreme signs of his Lord."},"4803":{"id":4803,"surah":53,"ayah":19,"verse":"So did you observe the idols Lat and Uzza?"},"4804":{"id":4804,"surah":53,"ayah":20,"verse":"And subsequently the third, the Manat?"},"4805":{"id":4805,"surah":53,"ayah":21,"verse":"What! For you the son, and for Him the daughter?"},"4806":{"id":4806,"surah":53,"ayah":22,"verse":"Then that is surely a very unjust distribution!"},"4807":{"id":4807,"surah":53,"ayah":23,"verse":"They are nothing but some names that you have coined, you and your forefathers \u2013 Allah has not sent any proof for them; they follow only guesses and their own desires; whereas the guidance from their Lord has come to them."},"4808":{"id":4808,"surah":53,"ayah":24,"verse":"What! Will man get whatever he dreams of?"},"4809":{"id":4809,"surah":53,"ayah":25,"verse":"So (know that) Allah only is the Owner of all \u2013 the Hereafter and this world."},"4810":{"id":4810,"surah":53,"ayah":26,"verse":"And many an angel is in the heavens, whose intercession does not benefit the least unless Allah gives permission for whomever He wills, and whom He likes."},"4811":{"id":4811,"surah":53,"ayah":27,"verse":"Indeed those who do not believe in the Hereafter, coin the names of angels like those of females."},"4812":{"id":4812,"surah":53,"ayah":28,"verse":"And they do not have any knowledge of it; they just follow assumption; and indeed assumption does not serve any purpose in place of the Truth."},"4813":{"id":4813,"surah":53,"ayah":29,"verse":"Therefore turn away from whoever has turned away from Our remembrance and has desired only the life of this world."},"4814":{"id":4814,"surah":53,"ayah":30,"verse":"This is the extent of their knowledge; indeed your Lord well knows one who has strayed from His path, and He well knows one who has attained guidance."},"4815":{"id":4815,"surah":53,"ayah":31,"verse":"And to Allah only belongs all whatever is in the heavens and all whatever is in the earth, in order to repay those who do evil for what they have done, and give an excellent reward to those who do good."},"4816":{"id":4816,"surah":53,"ayah":32,"verse":"Those who avoid the cardinal sins and lewdness, except that they approached it and refrained; indeed your Lord\u2019s mercy is limitless; He knows you very well \u2013 since He has created you from clay, and when you were foetuses in your mothers\u2019 wombs; therefore do not, on your own, claim yourselves to be clean; He well knows who are the pious."},"4817":{"id":4817,"surah":53,"ayah":33,"verse":"So did you observe him who turned away?"},"4818":{"id":4818,"surah":53,"ayah":34,"verse":"And he gave a little, then refrained?"},"4819":{"id":4819,"surah":53,"ayah":35,"verse":"Does he have knowledge of the hidden, so he can foresee?"},"4820":{"id":4820,"surah":53,"ayah":36,"verse":"Did not the news reach him, of that which is mentioned in the Books of Moosa?"},"4821":{"id":4821,"surah":53,"ayah":37,"verse":"And of Ibrahim, who was most obedient?"},"4822":{"id":4822,"surah":53,"ayah":38,"verse":"That no burdened soul bears another soul\u2019s burden?"},"4823":{"id":4823,"surah":53,"ayah":39,"verse":"And that man will not obtain anything except what he strove for?"},"4824":{"id":4824,"surah":53,"ayah":40,"verse":"And that his effort will soon be scrutinised?"},"4825":{"id":4825,"surah":53,"ayah":41,"verse":"Then he will be fully repaid for it?"},"4826":{"id":4826,"surah":53,"ayah":42,"verse":"And that the end is only towards your Lord?"},"4827":{"id":4827,"surah":53,"ayah":43,"verse":"And that it is He Who made (you) laugh and made (you) cry?"},"4828":{"id":4828,"surah":53,"ayah":44,"verse":"And that it is He Who gave death and gave life?"},"4829":{"id":4829,"surah":53,"ayah":45,"verse":"And that it is He Who has created the two pairs, male and female?"},"4830":{"id":4830,"surah":53,"ayah":46,"verse":"From a drop of liquid, when it is added?"},"4831":{"id":4831,"surah":53,"ayah":47,"verse":"And that only upon Him is the next revival?"},"4832":{"id":4832,"surah":53,"ayah":48,"verse":"And that it is He Who has given wealth and contentment?"},"4833":{"id":4833,"surah":53,"ayah":49,"verse":"And that He only is the Lord of the star Sirius?"},"4834":{"id":4834,"surah":53,"ayah":50,"verse":"And that it is He Who earlier destroyed the tribe of Aad?"},"4835":{"id":4835,"surah":53,"ayah":51,"verse":"And destroyed the tribe of Thamud, not sparing anyone?"},"4836":{"id":4836,"surah":53,"ayah":52,"verse":"And before them, the people of Nooh? Indeed they were more unjust and more rebellious than these!"},"4837":{"id":4837,"surah":53,"ayah":53,"verse":"And that it is He Who threw down the upturned townships?"},"4838":{"id":4838,"surah":53,"ayah":54,"verse":"So they were covered with whatever covered them?"},"4839":{"id":4839,"surah":53,"ayah":55,"verse":"So O listener! Which favour of your Lord will you doubt?"},"4840":{"id":4840,"surah":53,"ayah":56,"verse":"He (Prophet Mohammed \u2013 peace and blessings be upon him) is a Herald of Warning, like the former Heralds of Warning."},"4841":{"id":4841,"surah":53,"ayah":57,"verse":"The approaching event has come near."},"4842":{"id":4842,"surah":53,"ayah":58,"verse":"None except Allah can avert it."},"4843":{"id":4843,"surah":53,"ayah":59,"verse":"So are you surprised at this fact?"},"4844":{"id":4844,"surah":53,"ayah":60,"verse":"And you laugh, and do not weep!"},"4845":{"id":4845,"surah":53,"ayah":61,"verse":"And you are lost in play!"},"4846":{"id":4846,"surah":53,"ayah":62,"verse":"Therefore prostrate for Allah, and worship Him. (Command of Prostration # 12)"},"4847":{"id":4847,"surah":54,"ayah":1,"verse":"The Last Day came near, and the moon split apart."},"4848":{"id":4848,"surah":54,"ayah":2,"verse":"And when they see a sign, they turn away and say, \u201cJust a customary magic!\u201d"},"4849":{"id":4849,"surah":54,"ayah":3,"verse":"And they denied and followed their own desires, whereas each matter has been decided!"},"4850":{"id":4850,"surah":54,"ayah":4,"verse":"And indeed the news which had a lot of deterrence, came to them."},"4851":{"id":4851,"surah":54,"ayah":5,"verse":"The pinnacle of wisdom \u2013 so how will the Heralds of warning provide any benefit?"},"4852":{"id":4852,"surah":54,"ayah":6,"verse":"Therefore turn away from them; on the day when the announcer will call towards a severe unknown matter \u2013"},"4853":{"id":4853,"surah":54,"ayah":7,"verse":"They will come out from the graves with eyes lowered, as if they were spread locusts."},"4854":{"id":4854,"surah":54,"ayah":8,"verse":"Rushing towards the caller; the disbelievers will say, \u201cThis is a tough day.\u201d"},"4855":{"id":4855,"surah":54,"ayah":9,"verse":"Before these, the people of Nooh denied and they belied Our bondman and said, \u201cHe is a madman\u201d and rebuffed him."},"4856":{"id":4856,"surah":54,"ayah":10,"verse":"He therefore prayed to his Lord, \u201cI am overpowered, therefore avenge me.\u201d"},"4857":{"id":4857,"surah":54,"ayah":11,"verse":"We therefore opened the gates of heaven, with water flowing furiously."},"4858":{"id":4858,"surah":54,"ayah":12,"verse":"And caused springs to gush out from the earth, so that the two waters met totalling a quantity that had been destined."},"4859":{"id":4859,"surah":54,"ayah":13,"verse":"And We carried Nooh upon a ship of wooden planks and nails."},"4860":{"id":4860,"surah":54,"ayah":14,"verse":"Sailing in front of Our sight; as a reward for the sake of one who was rejected."},"4861":{"id":4861,"surah":54,"ayah":15,"verse":"And We left it as a sign \u2013 so is there one who would ponder?"},"4862":{"id":4862,"surah":54,"ayah":16,"verse":"So how did My punishment turn out, and My threats?"},"4863":{"id":4863,"surah":54,"ayah":17,"verse":"And We have indeed made the Qur\u2019an easy to memorise, so is there one who would remember?"},"4864":{"id":4864,"surah":54,"ayah":18,"verse":"The tribe of A\u2019ad denied \u2013 so how did My punishment turn out, and My warnings?"},"4865":{"id":4865,"surah":54,"ayah":19,"verse":"We indeed sent towards them a severe windstorm, on a day the ill luck of which lasted upon them forever."},"4866":{"id":4866,"surah":54,"ayah":20,"verse":"Smashing down men as if they were uprooted trunks of date palms."},"4867":{"id":4867,"surah":54,"ayah":21,"verse":"So how did My punishment turn out, and My warnings?"},"4868":{"id":4868,"surah":54,"ayah":22,"verse":"And We have indeed made the Qur\u2019an easy to memorise, so is there one who would remember?"},"4869":{"id":4869,"surah":54,"ayah":23,"verse":"The tribe of Thamud denied the Noble Messengers."},"4870":{"id":4870,"surah":54,"ayah":24,"verse":"So they said, \u201cWhat! Shall we follow a man from among us? If we do, we are indeed astray, and insane!\u201d"},"4871":{"id":4871,"surah":54,"ayah":25,"verse":"\u201cWhat! Of all the men among us, the remembrance has come down upon him? In fact, he is a mischievous, great liar.\u201d"},"4872":{"id":4872,"surah":54,"ayah":26,"verse":"It was said to Saleh, \u201cThey will soon realise tomorrow who is the mischievous great liar.\u201d"},"4873":{"id":4873,"surah":54,"ayah":27,"verse":"\u201cWe shall send a she-camel to test them, therefore O Saleh, wait and have patience.\u201d"},"4874":{"id":4874,"surah":54,"ayah":28,"verse":"\u201cAnd inform them that the water is to be shared between them; only those may come whose turn it is.\u201d"},"4875":{"id":4875,"surah":54,"ayah":29,"verse":"In response they called their companion \u2013 he therefore caught and hamstrung the she-camel."},"4876":{"id":4876,"surah":54,"ayah":30,"verse":"So how did My punishment turn out, and My warnings?"},"4877":{"id":4877,"surah":54,"ayah":31,"verse":"Indeed We sent upon them a single Scream \u2013 thereupon they became like the barrier builder\u2019s residual dry trampled hay."},"4878":{"id":4878,"surah":54,"ayah":32,"verse":"And We have indeed made the Qur\u2019an easy to memorise, so is there one who would remember?"},"4879":{"id":4879,"surah":54,"ayah":33,"verse":"The people of Lut denied the Noble Messengers."},"4880":{"id":4880,"surah":54,"ayah":34,"verse":"Indeed We sent a shower of stones upon them, except the family of Lut; We rescued them before dawn."},"4881":{"id":4881,"surah":54,"ayah":35,"verse":"As a reward from Us; this is how We reward one who gives thanks."},"4882":{"id":4882,"surah":54,"ayah":36,"verse":"And indeed he had warned them of Our seizure \u2013 in response they doubted the warnings."},"4883":{"id":4883,"surah":54,"ayah":37,"verse":"And they tried to persuade him regarding his guests \u2013 We therefore blinded their eyes, \u201cSo taste My punishment, and My warnings.\u201d"},"4884":{"id":4884,"surah":54,"ayah":38,"verse":"And indeed, the everlasting punishment overcame them early in the morning."},"4885":{"id":4885,"surah":54,"ayah":39,"verse":"\u201cSo taste My punishment, and My warnings!\u201d"},"4886":{"id":4886,"surah":54,"ayah":40,"verse":"And We have indeed made the Qur\u2019an easy to memorise, so is there one who would remember?"},"4887":{"id":4887,"surah":54,"ayah":41,"verse":"And indeed the Noble Messengers came to the people of Firaun."},"4888":{"id":4888,"surah":54,"ayah":42,"verse":"They denied all Our signs, We therefore seized them \u2013 the seizure of the Most Honourable, the All Powerful."},"4889":{"id":4889,"surah":54,"ayah":43,"verse":"Are your disbelievers better than they were, or have you been given exemption in the Books?"},"4890":{"id":4890,"surah":54,"ayah":44,"verse":"Or they say, \u201cWe shall all take revenge as a group.\u201d"},"4891":{"id":4891,"surah":54,"ayah":45,"verse":"The group will soon be routed, and will turn their backs to flee."},"4892":{"id":4892,"surah":54,"ayah":46,"verse":"In fact their promise is upon the Last Day \u2013 and the Last Day is very severe and very bitter!"},"4893":{"id":4893,"surah":54,"ayah":47,"verse":"Indeed the criminals are astray and insane."},"4894":{"id":4894,"surah":54,"ayah":48,"verse":"On the day when they are dragged upon their faces in the fire \u2013 \u201cTaste the heat of hell.\u201d"},"4895":{"id":4895,"surah":54,"ayah":49,"verse":"We have indeed created all things by a proper measure."},"4896":{"id":4896,"surah":54,"ayah":50,"verse":"And Our command is only a fleeting one \u2013 like the batting of an eyelid."},"4897":{"id":4897,"surah":54,"ayah":51,"verse":"And We have indeed destroyed your kind, so is there one who would ponder?"},"4898":{"id":4898,"surah":54,"ayah":52,"verse":"And all what they did is in the Books."},"4899":{"id":4899,"surah":54,"ayah":53,"verse":"And every small and great thing is recorded."},"4900":{"id":4900,"surah":54,"ayah":54,"verse":"Indeed the pious are amidst Gardens and springs."},"4901":{"id":4901,"surah":54,"ayah":55,"verse":"Seated in an assembly of the Truth, in the presence of Allah, the Omnipotent King."},"4902":{"id":4902,"surah":55,"ayah":1,"verse":"Allah, the Most Gracious"},"4903":{"id":4903,"surah":55,"ayah":2,"verse":"Has taught the Qur\u2019an to His beloved Prophet (Mohammed \u2013 peace and blessings be upon him)."},"4904":{"id":4904,"surah":55,"ayah":3,"verse":"Has created Prophet Mohammed (peace and blessings be upon him) as the soul of mankind."},"4905":{"id":4905,"surah":55,"ayah":4,"verse":"Has taught him the knowledge of the past and the future."},"4906":{"id":4906,"surah":55,"ayah":5,"verse":"The sun and the moon are scheduled."},"4907":{"id":4907,"surah":55,"ayah":6,"verse":"And the plants and the trees prostrate (for Him)."},"4908":{"id":4908,"surah":55,"ayah":7,"verse":"And Allah has raised the sky; and He has set the balance."},"4909":{"id":4909,"surah":55,"ayah":8,"verse":"In order that you may not corrupt the balance."},"4910":{"id":4910,"surah":55,"ayah":9,"verse":"And establish the measures justly, nor decrease the due weight."},"4911":{"id":4911,"surah":55,"ayah":10,"verse":"And He appointed the earth for the creatures."},"4912":{"id":4912,"surah":55,"ayah":11,"verse":"In which are fruits, and covered dates."},"4913":{"id":4913,"surah":55,"ayah":12,"verse":"And grain covered with husk, and fragrant flowers."},"4914":{"id":4914,"surah":55,"ayah":13,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4915":{"id":4915,"surah":55,"ayah":14,"verse":"He created man from clay like that of earthenware."},"4916":{"id":4916,"surah":55,"ayah":15,"verse":"And created jinn from the flame of fire."},"4917":{"id":4917,"surah":55,"ayah":16,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4918":{"id":4918,"surah":55,"ayah":17,"verse":"Lord of both the Easts, and Lord of both the Wests!"},"4919":{"id":4919,"surah":55,"ayah":18,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4920":{"id":4920,"surah":55,"ayah":19,"verse":"He has set flowing two seas that appear to meet."},"4921":{"id":4921,"surah":55,"ayah":20,"verse":"Whereas there is a barrier between them so they cannot encroach upon one another."},"4922":{"id":4922,"surah":55,"ayah":21,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4923":{"id":4923,"surah":55,"ayah":22,"verse":"Pearls and coral-stone come forth from them."},"4924":{"id":4924,"surah":55,"ayah":23,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4925":{"id":4925,"surah":55,"ayah":24,"verse":"To Him only belong the sailing ships, raised above the sea like hills."},"4926":{"id":4926,"surah":55,"ayah":25,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4927":{"id":4927,"surah":55,"ayah":26,"verse":"For everything on the earth is extinction."},"4928":{"id":4928,"surah":55,"ayah":27,"verse":"And everlasting is the Entity of your Lord, the Most Majestic and the Most Honourable."},"4929":{"id":4929,"surah":55,"ayah":28,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4930":{"id":4930,"surah":55,"ayah":29,"verse":"All those who are in the heavens and the earth seek only from Him; every day is an enterprise for Him."},"4931":{"id":4931,"surah":55,"ayah":30,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4932":{"id":4932,"surah":55,"ayah":31,"verse":"Disposing all works quickly We tend towards your account, O you two large groups!"},"4933":{"id":4933,"surah":55,"ayah":32,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4934":{"id":4934,"surah":55,"ayah":33,"verse":"O the groups of jinns and men, if you can cross the boundaries of the heavens and the earth, then cross them; wherever you may go, His is the kingdom!"},"4935":{"id":4935,"surah":55,"ayah":34,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4936":{"id":4936,"surah":55,"ayah":35,"verse":"Flames of smokeless fire and black smoke without flames, will be let loose on you, so you will not be able to retaliate."},"4937":{"id":4937,"surah":55,"ayah":36,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4938":{"id":4938,"surah":55,"ayah":37,"verse":"And when the heaven will split, it will appear like a rose painted red."},"4939":{"id":4939,"surah":55,"ayah":38,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4940":{"id":4940,"surah":55,"ayah":39,"verse":"On that day no sinner will be questioned about his sins, from men or from jinns."},"4941":{"id":4941,"surah":55,"ayah":40,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4942":{"id":4942,"surah":55,"ayah":41,"verse":"The criminals will be recognised from their faces, so will be caught by their forelocks and feet, and thrown into hell."},"4943":{"id":4943,"surah":55,"ayah":42,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4944":{"id":4944,"surah":55,"ayah":43,"verse":"This is hell, which the criminals deny."},"4945":{"id":4945,"surah":55,"ayah":44,"verse":"They shall keep going back and forth between it and the extremely hot boiling water."},"4946":{"id":4946,"surah":55,"ayah":45,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4947":{"id":4947,"surah":55,"ayah":46,"verse":"And for one who fears to stand before his Lord, are two Gardens."},"4948":{"id":4948,"surah":55,"ayah":47,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4949":{"id":4949,"surah":55,"ayah":48,"verse":"Having numerous branches."},"4950":{"id":4950,"surah":55,"ayah":49,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4951":{"id":4951,"surah":55,"ayah":50,"verse":"In the two Gardens flow two springs."},"4952":{"id":4952,"surah":55,"ayah":51,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4953":{"id":4953,"surah":55,"ayah":52,"verse":"In which are fruits of all kinds, each of two varieties."},"4954":{"id":4954,"surah":55,"ayah":53,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4955":{"id":4955,"surah":55,"ayah":54,"verse":"Reclining upon thrones that are lined with brocade, with the fruit of both Gardens close enough to be picked from under."},"4956":{"id":4956,"surah":55,"ayah":55,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4957":{"id":4957,"surah":55,"ayah":56,"verse":"Upon thrones are the women who do not gaze at men except their husbands, and before them, are untouched by any man or jinn."},"4958":{"id":4958,"surah":55,"ayah":57,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4959":{"id":4959,"surah":55,"ayah":58,"verse":"They are like rubies and coral-stone."},"4960":{"id":4960,"surah":55,"ayah":59,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4961":{"id":4961,"surah":55,"ayah":60,"verse":"What is the reward of virtue except virtue (in return)?"},"4962":{"id":4962,"surah":55,"ayah":61,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4963":{"id":4963,"surah":55,"ayah":62,"verse":"And besides them, there are two more Gardens."},"4964":{"id":4964,"surah":55,"ayah":63,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4965":{"id":4965,"surah":55,"ayah":64,"verse":"Densely covered with foliage, appearing dark."},"4966":{"id":4966,"surah":55,"ayah":65,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4967":{"id":4967,"surah":55,"ayah":66,"verse":"In the Gardens are two springs, overflowing with abundance."},"4968":{"id":4968,"surah":55,"ayah":67,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4969":{"id":4969,"surah":55,"ayah":68,"verse":"In them are fruits (of all kinds), and dates and pomegranate."},"4970":{"id":4970,"surah":55,"ayah":69,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4971":{"id":4971,"surah":55,"ayah":70,"verse":"In them are women of good behaviour and gorgeous faces."},"4972":{"id":4972,"surah":55,"ayah":71,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4973":{"id":4973,"surah":55,"ayah":72,"verse":"They are houris (maidens of Paradise), hidden from view, in pavilions."},"4974":{"id":4974,"surah":55,"ayah":73,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4975":{"id":4975,"surah":55,"ayah":74,"verse":"Untouched by any man or jinn, before them."},"4976":{"id":4976,"surah":55,"ayah":75,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4977":{"id":4977,"surah":55,"ayah":76,"verse":"Reclining on green cushions and beautiful decorated carpets."},"4978":{"id":4978,"surah":55,"ayah":77,"verse":"So O men and jinns! Which favour of your Lord will you deny?"},"4979":{"id":4979,"surah":55,"ayah":78,"verse":"Most Auspicious is the name of your Lord, the Most Majestic and the Most Honourable."},"4980":{"id":4980,"surah":56,"ayah":1,"verse":"When the forthcoming event does occur."},"4981":{"id":4981,"surah":56,"ayah":2,"verse":"Then none will be able to deny its occurrence."},"4982":{"id":4982,"surah":56,"ayah":3,"verse":"The event will be abasing some, and exalting some."},"4983":{"id":4983,"surah":56,"ayah":4,"verse":"When the earth will tremble, shivering."},"4984":{"id":4984,"surah":56,"ayah":5,"verse":"And the mountains will be crushed, blown to bits."},"4985":{"id":4985,"surah":56,"ayah":6,"verse":"So they will become like fine dust, scattered in a shaft of light."},"4986":{"id":4986,"surah":56,"ayah":7,"verse":"And you will become divided into three categories."},"4987":{"id":4987,"surah":56,"ayah":8,"verse":"So those on the right \u2013 how (fortunate) are those on the right!"},"4988":{"id":4988,"surah":56,"ayah":9,"verse":"And those on the left \u2013 how (wretched) are those on the left!"},"4989":{"id":4989,"surah":56,"ayah":10,"verse":"And those who surpassed have indeed excelled."},"4990":{"id":4990,"surah":56,"ayah":11,"verse":"It is they who are the close ones."},"4991":{"id":4991,"surah":56,"ayah":12,"verse":"They are in Gardens of peace."},"4992":{"id":4992,"surah":56,"ayah":13,"verse":"A large group from the earlier generations."},"4993":{"id":4993,"surah":56,"ayah":14,"verse":"And a few from the latter."},"4994":{"id":4994,"surah":56,"ayah":15,"verse":"On studded thrones."},"4995":{"id":4995,"surah":56,"ayah":16,"verse":"Reclining on them, facing each other."},"4996":{"id":4996,"surah":56,"ayah":17,"verse":"Surrounded by immortal boys."},"4997":{"id":4997,"surah":56,"ayah":18,"verse":"Carrying bowls and pitchers \u2013 and cups filled with wine flowing before them."},"4998":{"id":4998,"surah":56,"ayah":19,"verse":"Their heads shall not ache with it, nor shall they lose their senses."},"4999":{"id":4999,"surah":56,"ayah":20,"verse":"And fruits that they may like."},"5000":{"id":5000,"surah":56,"ayah":21,"verse":"And meat of birds that they may wish."},"5001":{"id":5001,"surah":56,"ayah":22,"verse":"And gorgeous eyed fair maidens."},"5002":{"id":5002,"surah":56,"ayah":23,"verse":"Like pearls safely hidden."},"5003":{"id":5003,"surah":56,"ayah":24,"verse":"The reward for what they did."},"5004":{"id":5004,"surah":56,"ayah":25,"verse":"They will not hear any useless speech in it, or any sin."},"5005":{"id":5005,"surah":56,"ayah":26,"verse":"Except the saying, \u201cPeace, peace.\u201d"},"5006":{"id":5006,"surah":56,"ayah":27,"verse":"And those on the right \u2013 how (fortunate) are those on the right!"},"5007":{"id":5007,"surah":56,"ayah":28,"verse":"Among thorn-less lote-trees."},"5008":{"id":5008,"surah":56,"ayah":29,"verse":"And clusters of banana plants."},"5009":{"id":5009,"surah":56,"ayah":30,"verse":"And in everlasting shade."},"5010":{"id":5010,"surah":56,"ayah":31,"verse":"And in perpetually flowing water."},"5011":{"id":5011,"surah":56,"ayah":32,"verse":"And plenty of fruits."},"5012":{"id":5012,"surah":56,"ayah":33,"verse":"That will neither finish, nor ever be stopped."},"5013":{"id":5013,"surah":56,"ayah":34,"verse":"And raised couches."},"5014":{"id":5014,"surah":56,"ayah":35,"verse":"We have indeed developed these women with an excellent development."},"5015":{"id":5015,"surah":56,"ayah":36,"verse":"So made them as maidens."},"5016":{"id":5016,"surah":56,"ayah":37,"verse":"The beloved of their husbands, of one age."},"5017":{"id":5017,"surah":56,"ayah":38,"verse":"For those on the right."},"5018":{"id":5018,"surah":56,"ayah":39,"verse":"A large group from the earlier generations."},"5019":{"id":5019,"surah":56,"ayah":40,"verse":"And a large group from the latter."},"5020":{"id":5020,"surah":56,"ayah":41,"verse":"And those on the left \u2013 how (wretched) are those on the left!"},"5021":{"id":5021,"surah":56,"ayah":42,"verse":"In scorching wind and boiling hot water."},"5022":{"id":5022,"surah":56,"ayah":43,"verse":"And in the shadow of a burning smoke."},"5023":{"id":5023,"surah":56,"ayah":44,"verse":"Which is neither cool nor is for respect."},"5024":{"id":5024,"surah":56,"ayah":45,"verse":"Indeed they were among favours before this."},"5025":{"id":5025,"surah":56,"ayah":46,"verse":"And were stubborn upon the great sin (of disbelief)."},"5026":{"id":5026,"surah":56,"ayah":47,"verse":"And they used to say, \u201cWhen we are dead and have turned into dust and bones, will we surely be raised again?\u201d"},"5027":{"id":5027,"surah":56,"ayah":48,"verse":"\u201cAnd also our forefathers?\u201d"},"5028":{"id":5028,"surah":56,"ayah":49,"verse":"Proclaim, \u201cWithout doubt all \u2013 the former and the latter.\u201d"},"5029":{"id":5029,"surah":56,"ayah":50,"verse":"\u201cThey will all be gathered together \u2013 on the appointed time of the known day.\u201d"},"5030":{"id":5030,"surah":56,"ayah":51,"verse":"Then indeed you, the astray, the deniers \u2013"},"5031":{"id":5031,"surah":56,"ayah":52,"verse":"You will indeed eat from the Zaqqum tree."},"5032":{"id":5032,"surah":56,"ayah":53,"verse":"Then will fill your bellies with it."},"5033":{"id":5033,"surah":56,"ayah":54,"verse":"And upon it, you will drink the hot boiling water."},"5034":{"id":5034,"surah":56,"ayah":55,"verse":"Drinking the way thirsty camels drink."},"5035":{"id":5035,"surah":56,"ayah":56,"verse":"This is their reception on the Day of Justice."},"5036":{"id":5036,"surah":56,"ayah":57,"verse":"It is We Who created you, so why do you not accept the truth?"},"5037":{"id":5037,"surah":56,"ayah":58,"verse":"So what is your opinion regarding the semen you discharge?"},"5038":{"id":5038,"surah":56,"ayah":59,"verse":"Do you make a human out of it, or is it We Who create?"},"5039":{"id":5039,"surah":56,"ayah":60,"verse":"It is We Who have ordained death among you, and We have not been beaten \u2013"},"5040":{"id":5040,"surah":56,"ayah":61,"verse":"In the matter of exchanging you for others, and to transform you into what you do not know."},"5041":{"id":5041,"surah":56,"ayah":62,"verse":"And you have indeed learnt about the first creation, so why do you not ponder?"},"5042":{"id":5042,"surah":56,"ayah":63,"verse":"So what is your opinion regarding what you sow?"},"5043":{"id":5043,"surah":56,"ayah":64,"verse":"Is it you who cultivate it, or is it We Who develop it?"},"5044":{"id":5044,"surah":56,"ayah":65,"verse":"If We will We can make it like dry trampled hay, so you would keep crying out."},"5045":{"id":5045,"surah":56,"ayah":66,"verse":"That, \u201cWe have indeed been penalised!\u201d"},"5046":{"id":5046,"surah":56,"ayah":67,"verse":"\u201cIn fact, we were unfortunate!\u201d"},"5047":{"id":5047,"surah":56,"ayah":68,"verse":"So what is your opinion regarding the water that you drink?"},"5048":{"id":5048,"surah":56,"ayah":69,"verse":"Is it you who caused it to descend from the cloud, or is it We Who cause it to descend?"},"5049":{"id":5049,"surah":56,"ayah":70,"verse":"If We will We can make it bitter, so why do you not give thanks?"},"5050":{"id":5050,"surah":56,"ayah":71,"verse":"So what is your opinion regarding the fire which you kindle?"},"5051":{"id":5051,"surah":56,"ayah":72,"verse":"Is it you who grew its tree, or is it We Who create?"},"5052":{"id":5052,"surah":56,"ayah":73,"verse":"We have made it as a reminder of hell and as a utility for travellers in the jungle."},"5053":{"id":5053,"surah":56,"ayah":74,"verse":"Therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him) proclaim the Purity of the name of your Lord, the Greatest."},"5054":{"id":5054,"surah":56,"ayah":75,"verse":"So I swear by the setting places of the stars."},"5055":{"id":5055,"surah":56,"ayah":76,"verse":"And that is indeed a tremendous oath, if you understand."},"5056":{"id":5056,"surah":56,"ayah":77,"verse":"This is indeed the noble Qur\u2019an."},"5057":{"id":5057,"surah":56,"ayah":78,"verse":"Kept in a secure Book."},"5058":{"id":5058,"surah":56,"ayah":79,"verse":"None may touch it, except with ablution."},"5059":{"id":5059,"surah":56,"ayah":80,"verse":"Sent down by the Lord Of The Creation."},"5060":{"id":5060,"surah":56,"ayah":81,"verse":"So is this the matter regarding which you laze?"},"5061":{"id":5061,"surah":56,"ayah":82,"verse":"And you make its denial your share?"},"5062":{"id":5062,"surah":56,"ayah":83,"verse":"So why was it not, when the soul reaches up to the throat, \u2013"},"5063":{"id":5063,"surah":56,"ayah":84,"verse":"Whereas you watch at that moment!"},"5064":{"id":5064,"surah":56,"ayah":85,"verse":"And We are nearer to him than you are, but you cannot see."},"5065":{"id":5065,"surah":56,"ayah":86,"verse":"So why is it not, that if you are not to be repaid, \u2013"},"5066":{"id":5066,"surah":56,"ayah":87,"verse":"That you bring it back, if you are truthful?"},"5067":{"id":5067,"surah":56,"ayah":88,"verse":"Then if the dying one is of those having proximity, \u2013"},"5068":{"id":5068,"surah":56,"ayah":89,"verse":"Then is relief, and flowers \u2013 and Gardens of peace."},"5069":{"id":5069,"surah":56,"ayah":90,"verse":"And if he is of those on the right, \u2013"},"5070":{"id":5070,"surah":56,"ayah":91,"verse":"Then upon you is the greetings of peace (O dear Prophet Mohammed \u2013 peace and blessings be upon him), from those on the right."},"5071":{"id":5071,"surah":56,"ayah":92,"verse":"And if he is from the deniers, the astray, \u2013"},"5072":{"id":5072,"surah":56,"ayah":93,"verse":"Then his reception is the hot boiling water."},"5073":{"id":5073,"surah":56,"ayah":94,"verse":"And a hurling into the blazing fire."},"5074":{"id":5074,"surah":56,"ayah":95,"verse":"This is indeed an utmost certainty."},"5075":{"id":5075,"surah":56,"ayah":96,"verse":"Therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him) proclaim the Purity of the name of your Lord, the Greatest."},"5076":{"id":5076,"surah":57,"ayah":1,"verse":"All whatever is in the heavens and in the earth proclaims the Purity of Allah; and He only is the Most Honourable, the Wise."},"5077":{"id":5077,"surah":57,"ayah":2,"verse":"For Him only is the kingship of the heavens and the earth; He gives life and causes death; and He is Able to do all things."},"5078":{"id":5078,"surah":57,"ayah":3,"verse":"He only is the First and He only the Last, and He only is the Evident and He only the Concealed; and it is He Who knows all things."},"5079":{"id":5079,"surah":57,"ayah":4,"verse":"It is He Who created the heavens and the earth in six days, then ascended the Throne (of Control), in a manner befitting His Majesty; He knows all what goes into the earth and all what comes out of it, and all what descends from the sky and all that rises in it; and He is with you, wherever you may be; and Allah is seeing your deeds."},"5080":{"id":5080,"surah":57,"ayah":5,"verse":"For Him only is the kingship of the heavens and the earth; and towards Allah only is the return of all matters."},"5081":{"id":5081,"surah":57,"ayah":6,"verse":"He brings the night in a part of the day, and brings the day in a part of the night; and He knows what lies within the hearts."},"5082":{"id":5082,"surah":57,"ayah":7,"verse":"Accept faith in Allah and His Noble Messenger, and spend in His cause from what He has made you the heirs of; so for those among you who accepted faith and spent in His cause, is a great reward."},"5083":{"id":5083,"surah":57,"ayah":8,"verse":"And what is the matter with you, that you should not accept faith in Allah? Whereas this Noble Messenger is calling you to believe in your Lord, and Allah has indeed already taken a covenant from you, if you believe."},"5084":{"id":5084,"surah":57,"ayah":9,"verse":"It is He Who sends down clear verses upon His chosen bondman, in order to take you out from the realms of darkness towards light; and indeed Allah is Most Compassionate, Most Merciful upon you."},"5085":{"id":5085,"surah":57,"ayah":10,"verse":"And what is the matter with you that you should not spend in Allah\u2019s cause, whereas Allah is the Inheritor of all that is in the heavens and in the earth? Those among you who spent and fought before the conquest of Mecca are not equal to others; they are greater in rank than those who spent and fought after the conquest; and Allah has promised Paradise to all of them; and Allah well knows what you do."},"5086":{"id":5086,"surah":57,"ayah":11,"verse":"Who will lend a handsome loan to Allah so that He may double it for him? And for such is an honourable reward."},"5087":{"id":5087,"surah":57,"ayah":12,"verse":"The day when you will see the believing men and believing women, that their light runs before them and on their right \u2013 it being said to them, \u201cThis day, the best tidings for you are the Gardens beneath which rivers flow \u2013 abide in it forever; this is the greatest success.\u201d"},"5088":{"id":5088,"surah":57,"ayah":13,"verse":"The day when hypocrite men and hypocrite women will say to the Muslims, \u201cLook mercifully towards us, so that we may gain some of your light!\u201d; it will be said to them, \u201cTurn back, search light over there!\u201d; so they will turn around, whereupon a wall will be erected between them, in which is a gate; inside the gate is mercy, and on the outer side is the punishment."},"5089":{"id":5089,"surah":57,"ayah":14,"verse":"The hypocrites will call out to the Muslims, \u201cWere we not with you?\u201d; they will answer, \u201cYes you were, why not? But you had put your souls into trial, and you used to await misfortune for the Muslims, and you doubted, and false hopes deceived you until Allah\u2019s command came \u2013 and the big cheat had made you conceited towards the command of Allah.\u201d"},"5090":{"id":5090,"surah":57,"ayah":15,"verse":"\u201cSo this day no ransom is to be taken from you nor from the declared disbelievers; your destination is the fire; that is your companion; and what a wretched outcome!\u201d"},"5091":{"id":5091,"surah":57,"ayah":16,"verse":"Has not the time come for the believers to surrender their hearts to Allah\u2019s remembrance and to this truth that has come down? And do not be like those who were earlier given the Book(s) and when a long term passed over them, their hearts became hardened; and many of them are sinners."},"5092":{"id":5092,"surah":57,"ayah":17,"verse":"Know that it is Allah Who revives the earth after its death; We have indeed illustrated the signs for you, for you to understand."},"5093":{"id":5093,"surah":57,"ayah":18,"verse":"Indeed the charity-giving men and women, and those who lend an excellent loan to Allah \u2013 for them is double, and for them is an honourable reward."},"5094":{"id":5094,"surah":57,"ayah":19,"verse":"And those who believe in Allah and all His Noble Messengers, are the truly sincere; and are witness upon others, before their Lord; for them is their reward, and their light; and those who disbelieved and denied Our signs, are the people of hell."},"5095":{"id":5095,"surah":57,"ayah":20,"verse":"Know that the life of this world is nothing but play and pastime, and adornment, and boasting amongst yourselves, and the desire to surpass each other in wealth and children; like the rain the produce of which pleased the farmer, then dried so you see it yellow, and then turned into dry trampled hay; and in the Hereafter is a severe punishment, and the forgiveness from Allah and His pleasure; and the life of this world is nothing but counterfeit wealth."},"5096":{"id":5096,"surah":57,"ayah":21,"verse":"Rush towards the forgiveness of your Lord and a Paradise wide as the expanse of the heavens and the earth \u2013 made for those who believe in Allah and all His Noble Messengers; this is Allah\u2019s munificence, He may bestow it to whomever He wills; and Allah is Extremely Munificent."},"5097":{"id":5097,"surah":57,"ayah":22,"verse":"There is no misfortune that reaches in the earth or in your selves but is mentioned in a Book, before We initiate it; indeed this is easy for Allah."},"5098":{"id":5098,"surah":57,"ayah":23,"verse":"So that you may not be saddened upon losing something, nor rejoice upon what you are given; and Allah does not like any boastful, conceited person."},"5099":{"id":5099,"surah":57,"ayah":24,"verse":"Those who practice miserliness, and exhort others to miserliness; and whoever turns away, then (know that) Allah is the Independent, the Most Praiseworthy."},"5100":{"id":5100,"surah":57,"ayah":25,"verse":"Indeed We sent Our Noble Messengers with proofs, and sent down the Book and the Balance of Justice along with them, so that people may stay upon justice; and We sent down iron having severe heat and benefits for mankind, and so that Allah may see him, who without seeing aides Him and His Noble Messengers; indeed Allah is Almighty, Dominant."},"5101":{"id":5101,"surah":57,"ayah":26,"verse":"And indeed We sent Nooh and Ibrahim, and placed Prophethood and the Book among their descendants, so some among them took to guidance; and many of them are sinners."},"5102":{"id":5102,"surah":57,"ayah":27,"verse":"We then sent Our other Noble Messengers after them following their footsteps, and after them We sent Eisa, the son of Maryam, and bestowed the Injeel to him; and We instilled compassion and mercy in the hearts of his followers; and they invented monasticism which We had not ordained upon them only to seek Allah\u2019s pleasure, then did not properly abide by it as it should have been rightfully abided; We therefore gave the believers among them their reward; and many of them are sinners."},"5103":{"id":5103,"surah":57,"ayah":28,"verse":"O People who Believe (in the earlier Noble Messengers)! Fear Allah and accept faith in this Noble Messenger of His \u2013 He will bestow two portions of His mercy to you and will create a light for you to walk in it, and will forgive you; and Allah is Oft Forgiving, Most Merciful."},"5104":{"id":5104,"surah":57,"ayah":29,"verse":"This is so that the disbelievers among People given the Book(s) may know that they do not have any control over Allah\u2019s munificence, and that the munificence is in Allah\u2019s Hand (control) \u2013 He bestows to whomever He wills; and Allah is Extremely Munificent."},"5105":{"id":5105,"surah":58,"ayah":1,"verse":"Allah has indeed heard the speech of the woman who is arguing with you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) concerning her husband, and is complaining to Allah; and Allah hears the conversation of you both; indeed Allah is All Hearing, All Knowing."},"5106":{"id":5106,"surah":58,"ayah":2,"verse":"Those among you who have proclaimed their wives as their mothers \u2013 so their wives are not their mothers; their mothers are only those that gave them birth; and undoubtedly they utter evil and a blatant lie; and indeed Allah is Most Pardoning, Oft Forgiving."},"5107":{"id":5107,"surah":58,"ayah":3,"verse":"And those among you who have proclaimed their wives as their mothers and then wish to revert to the matter upon which they had uttered such an enormous word, must then free a slave before they touch one another; this is what you are advised; and Allah is Aware of your deeds."},"5108":{"id":5108,"surah":58,"ayah":4,"verse":"So one who cannot find a slave, must then fast for two successive months before they touch one another; and one who cannot even fast, must then feed sixty needy ones; this is in order that you keep faith in Allah and His Noble Messenger; and these are the limits of Allah; and for the disbelievers is a painful punishment."},"5109":{"id":5109,"surah":58,"ayah":5,"verse":"Indeed those who oppose Allah and His Noble Messenger have been humiliated, like those before them who were humiliated, and We have indeed sent down clear verses; and for the disbelievers is a disgraceful punishment."},"5110":{"id":5110,"surah":58,"ayah":6,"verse":"The day when Allah will raise all of them together and inform them of their misdeeds; Allah has kept count of them, whereas they have forgotten them; and all things are present before Allah."},"5111":{"id":5111,"surah":58,"ayah":7,"verse":"O listener! Did you not see that Allah knows all whatever is in the heavens and all whatever is in the earth? Wherever there is any discussion among three He is the fourth present with them, and among five He is the sixth, and there is no discussion among fewer or more except that He is with them wherever they may be; and then on the Day of Resurrection He will inform them of all what they did; indeed Allah knows all things."},"5112":{"id":5112,"surah":58,"ayah":8,"verse":"Did you not see those who were forbidden from conspiracy, yet they do what was forbidden, and consult each other for sinning, and for exceeding limits, and for disobeying the Noble Messenger? And when they come in your presence, they greet you with words not chosen by Allah for respectfully greeting you, and they inwardly say, \u201cWhy does not Allah punish us for what we say?\u201d; hell is sufficient for them; they will sink into it; and what a wretched outcome!"},"5113":{"id":5113,"surah":58,"ayah":9,"verse":"O People who Believe! When you consult each other, do not consult for sinning, nor for exceeding limits, nor for disobeying the Noble Messenger \u2013 and consult for righteousness and piety; and fear Allah, towards Whom you will be raised."},"5114":{"id":5114,"surah":58,"ayah":10,"verse":"The evil consultation is only from the devil in order that he may upset the believers, whereas he cannot harm them in the least without Allah\u2019s command; and only upon Allah must the Muslims rely."},"5115":{"id":5115,"surah":58,"ayah":11,"verse":"O People who Believe! When you are told to give room in the assemblies, then do give room \u2013 Allah will give you room (in His mercy); and when it is said, \u201cStand up in reverence\u201d, then do stand up \u2013 Allah will raise the believers among you, and those given knowledge, to high ranks; and Allah is Aware of your deeds."},"5116":{"id":5116,"surah":58,"ayah":12,"verse":"O People who Believe! When you wish to humbly consult with the Noble Messenger, give some charity before you consult; that is much better and much purer for you; so if you do not have the means, then (know that) Allah is Oft Forgiving, Most Merciful."},"5117":{"id":5117,"surah":58,"ayah":13,"verse":"Were you afraid to offer charity before you consult? So when you did not do this, and Allah has inclined towards you with His mercy, keep the prayers established and pay the obligatory charity and obey Allah and His Noble Messenger; and Allah is Aware of your deeds."},"5118":{"id":5118,"surah":58,"ayah":14,"verse":"Did you not see those who befriended those upon whom is Allah\u2019s wrath? They are neither of you nor of these \u2013 and they swear a false oath, whereas they know."},"5119":{"id":5119,"surah":58,"ayah":15,"verse":"Allah has kept prepared a severe punishment for them; indeed they commit extremely evil deeds."},"5120":{"id":5120,"surah":58,"ayah":16,"verse":"They use their oaths as a shield therefore preventing from Allah\u2019s way \u2013 so for them is a disgraceful punishment."},"5121":{"id":5121,"surah":58,"ayah":17,"verse":"Their wealth and their children will be of no use to them before Allah; they are the people of hell; they are to remain in it forever."},"5122":{"id":5122,"surah":58,"ayah":18,"verse":"The day when Allah will raise them all, they will swear in His presence the way they now swear in front of you, and they will assume that they have achieved something; pay heed! Indeed it is they who are the liars."},"5123":{"id":5123,"surah":58,"ayah":19,"verse":"The devil has overpowered them, so they forgot the remembrance of Allah; they are the devil\u2019s group; pay heed! Indeed it is the devil\u2019s group who are the losers."},"5124":{"id":5124,"surah":58,"ayah":20,"verse":"Indeed those who oppose Allah and His Noble Messenger, are among the most despicable."},"5125":{"id":5125,"surah":58,"ayah":21,"verse":"Allah has already decreed that, \u201cI will indeed be victorious, and My Noble Messengers\u201d; indeed Allah is Almighty, the Most Honourable."},"5126":{"id":5126,"surah":58,"ayah":22,"verse":"You will not find the people who believe in Allah and the Last Day, befriending those who oppose Allah and His Noble Messenger, even if they are their fathers or their sons or their brothers or their tribesmen; it is these upon whose hearts Allah has ingrained faith, and has aided them with a Spirit from Himself; and He will admit them into Gardens beneath which rivers flow, abiding in them forever; Allah is pleased with them, and they are pleased with Him; this is Allah\u2019s group; pay heed! Indeed it is Allah\u2019s group who are the successful."},"5127":{"id":5127,"surah":59,"ayah":1,"verse":"All whatever is in the heavens and all whatever is in the earth proclaims the Purity of Allah; and He only is the Most Honourable, the Wise."},"5128":{"id":5128,"surah":59,"ayah":2,"verse":"It is He Who expelled the disbelievers among the People given the Book(s) from their homes, for their first gathering; you did not expect them to leave, whereas they assumed that their fortresses would save them from Allah, so Allah\u2019s command came to them from a place they had not imagined; and He instilled awe in their hearts, so they ruin their own houses by their own hands and at the hands of the Muslims; therefore learn a lesson, O those who can perceive!"},"5129":{"id":5129,"surah":59,"ayah":3,"verse":"And had Allah not decreed exile for them, He would have surely punished them in this world; and for them in the Hereafter is the punishment of the fire."},"5130":{"id":5130,"surah":59,"ayah":4,"verse":"This is because they remained opposed to Allah and His Noble Messenger; and whoever remains opposed to Allah, (and His Noble Messenger) \u2013 then indeed Allah\u2019s punishment is severe."},"5131":{"id":5131,"surah":59,"ayah":5,"verse":"The trees you had cut down or left standing on their roots was all by Allah\u2019s permission, and in order to disgrace the sinners."},"5132":{"id":5132,"surah":59,"ayah":6,"verse":"And from them, the booty which Allah gave to His Noble Messenger \u2013 so you had not raced your horses or camels against them, but it is Allah Who gives whomever He wills within the control of His Noble Messengers; and Allah is Able to do all things."},"5133":{"id":5133,"surah":59,"ayah":7,"verse":"The booty which Allah gave to His Noble Messenger from the people of the townships, is for Allah and His Noble Messenger, and for the relatives, and the orphans, and the needy and the travellers \u2013 so that it does not become the wealth of the rich among you; and accept whatever the Noble Messenger gives you; and refrain from whatever he forbids you; and fear Allah; indeed Allah\u2019s punishment is severe."},"5134":{"id":5134,"surah":59,"ayah":8,"verse":"And (the booty is) also for the poor migrants who were expelled from their homes and their wealth, seeking Allah\u2019s munificence and His pleasure, and aiding Allah and His Noble Messenger; it is they who are the truthful."},"5135":{"id":5135,"surah":59,"ayah":9,"verse":"And those who accepted this city as their home and accepted faith before them, befriend those who migrated towards them, and in their breasts do not find any need for what they have been given, and prefer the migrants above themselves even if they themselves are in dire need; and whoever is saved from the greed of his soul \u2013 it is they who are the successful."},"5136":{"id":5136,"surah":59,"ayah":10,"verse":"And those who came after them say, \u201cO our Lord! Forgive us, and our brothers who accepted faith before us, and do not keep any malice in our hearts towards the believers \u2013 O our Lord! Indeed You only are the Most Compassionate, Most Merciful.\u201d."},"5137":{"id":5137,"surah":59,"ayah":11,"verse":"Did you not see the hypocrites, that they say to their disbelieving brothers among the People given the Book(s), \u201cIf you are expelled, then we will definitely go out with you, and we will not listen to anyone in your matters, and if you are fought against we will surely help you\u201d; and Allah testifies that they are indeed liars."},"5138":{"id":5138,"surah":59,"ayah":12,"verse":"If they are expelled, the hypocrites will not go out with them; and if they are fought against they will not help them; and even if they were to help them, they would turn their backs and flee; and then they will not be helped."},"5139":{"id":5139,"surah":59,"ayah":13,"verse":"Indeed in their hearts is a greater fear of you than that of Allah; this is because they are a people who do not understand."},"5140":{"id":5140,"surah":59,"ayah":14,"verse":"They will not fight you even if they all come together, except in barricaded cities or from behind walls; they are severe fighters among themselves; you will assume them to be one, whereas their hearts are divided; this is because they are a people who do not have any sense."},"5141":{"id":5141,"surah":59,"ayah":15,"verse":"Like the example of those who were before them not long ago \u2013 they tasted the evil result of their deeds; and for them is a painful punishment."},"5142":{"id":5142,"surah":59,"ayah":16,"verse":"The example of the devil \u2013 when he said to man \u201cDisbelieve\u201d; so when he has rejected faith, he says, \u201cI am unconcerned with you \u2013 indeed I fear Allah, the Lord of The Creation.\u201d"},"5143":{"id":5143,"surah":59,"ayah":17,"verse":"So the fate of them both is that they are both in the fire, to remain in it forever; and this is the proper punishment for the unjust."},"5144":{"id":5144,"surah":59,"ayah":18,"verse":"O People who Believe! Fear Allah, and every soul must see what it has sent ahead for tomorrow; and fear Allah; indeed Allah is Aware of your deeds."},"5145":{"id":5145,"surah":59,"ayah":19,"verse":"And do not be like those who forgot Allah \u2013 He therefore put them into hardship making them forget themselves; it is they who are the sinners."},"5146":{"id":5146,"surah":59,"ayah":20,"verse":"The People of hell and the People of Paradise are not equal; it is only the People of Paradise who have succeeded."},"5147":{"id":5147,"surah":59,"ayah":21,"verse":"Had We sent down this Qur\u2019an upon a mountain, you would have then surely seen it bowed down, blown to bits by the fear of Allah; and We illustrate such examples for people, for them to ponder."},"5148":{"id":5148,"surah":59,"ayah":22,"verse":"It is Allah, except Whom there is no God; the Knowing of all \u2013 the hidden and the evident; He only is the Most Gracious, the Most Merciful."},"5149":{"id":5149,"surah":59,"ayah":23,"verse":"It is Allah, except Whom there is no God; the King, the Pure, the Giver of Peace, the Bestower of Safety, the Protector, the Most Honourable, the Compeller, the Proud; Purity is to Allah from all what they ascribe as partners (to Him)!"},"5150":{"id":5150,"surah":59,"ayah":24,"verse":"It is Allah only, Who is the Creator, the Initiator, the Designer of all \u2013 His only are all the good names; all whatever is in the heavens and in the earth proclaims His Purity; and He only is the Most Honourable, the Wise."},"5151":{"id":5151,"surah":60,"ayah":1,"verse":"O People who Believe! Do not befriend My and your enemy \u2013 you reveal secrets to them out of friendship whereas they disbelieve in the truth which has come to you! It is they who remove the Noble Messenger and you from your homes, upon your believing in Allah, your Lord; if you have come out to fight for My cause, and to gain My pleasure, then do not befriend them; you send them secret messages of friendship \u2013 and I well know all what you hide and all what you disclose; and whoever among you does it, has indeed strayed away from the right path."},"5152":{"id":5152,"surah":60,"ayah":2,"verse":"If they gain dominance over you they will be your enemies, and will extend their hands and their tongues towards you with evil, and they wish that in some way you turn disbelievers."},"5153":{"id":5153,"surah":60,"ayah":3,"verse":"Neither your relations nor your offspring will in the least benefit you, on the Day of Resurrection; He will separate you from them; and Allah is seeing your deeds."},"5154":{"id":5154,"surah":60,"ayah":4,"verse":"Indeed in Ibrahim, and those along with him, lay a good example for you follow \u2013 when they said to their people, \u201cIndeed we are unconcerned with you and all what you worship besides Allah; we reject you, and between us and you has surfaced enmity and hatred for ever until you accept faith in the One Allah\u201d \u2013 except the saying of Ibrahim to his father, \u201cI will surely seek forgiveness for you, and I do not possess any power to benefit you against Allah\u201d; \u201cO our Lord! We have relied only upon You, and towards You only we have inclined and only towards You is the return\u201d."},"5155":{"id":5155,"surah":60,"ayah":5,"verse":"\u201cO our Lord! Do not put us into the trial of the disbelievers, and forgive us, O our Lord! Indeed You only are the Most Honourable, the Wise.\u201d"},"5156":{"id":5156,"surah":60,"ayah":6,"verse":"Indeed in them lay a good example for you to follow \u2013 for one who looks forward to (meeting) Allah and the Last Day; and whoever turns away \u2013 then (know that) indeed Allah only is the Independent, the Most Praiseworthy."},"5157":{"id":5157,"surah":60,"ayah":7,"verse":"It is likely that Allah may create friendship between you and those among them who are now your enemies; and Allah is All Able; and Allah is Oft Forgiving, Most Merciful."},"5158":{"id":5158,"surah":60,"ayah":8,"verse":"Allah does not forbid you from those who did not fight against you because of religion and did not drive you out from your homes, that you should be kind towards them and deal with them fairly; indeed the equitable are the beloved of Allah."},"5159":{"id":5159,"surah":60,"ayah":9,"verse":"Allah forbids you only from those who fought against you because of religion or drove you out from your homes or helped others to drive you out, that you should befriend them; and whoever befriends them \u2013 it is they who are the unjust."},"5160":{"id":5160,"surah":60,"ayah":10,"verse":"O People who Believe! When Muslim women leave the lands of disbelief and migrate towards you, examine them; Allah knows more about their faith; so if you judge the women to be believers, do not send them back to the disbelievers; neither are they lawful for the disbelievers, nor are the disbelievers lawful for them; and give their disbelieving husbands what they had spent; and there is no sin upon you to marry them if you give them their bridal money; and do not continue the marriage with disbelieving women \u2013 and ask for what you had spent, and the disbelievers may ask for what they had spent; this is Allah\u2019s command; He judges between you; and Allah is All Knowing, Wise."},"5161":{"id":5161,"surah":60,"ayah":11,"verse":"And if some women go away from the Muslims to the disbelievers \u2013 then when you punish the disbelievers, give from the war booty to the Muslims who lost their wives the amount they had spent; and fear Allah in Whom you believe."},"5162":{"id":5162,"surah":60,"ayah":12,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him)! If Muslim women come humbly to you to take oath of allegiance that they will neither ascribe any partner to Allah, nor steal, nor commit adultery, nor kill their children, nor bring the lie that they carry between their hands and feet, nor disobey you in any rightful matter \u2013 then accept their allegiance and seek forgiveness from Allah for them; indeed Allah is Oft Forgiving, Most Merciful."},"5163":{"id":5163,"surah":60,"ayah":13,"verse":"O People who Believe! Do not befriend the people upon whom is Allah\u2019s wrath \u2013 they have lost hope in the Hereafter the way the disbelievers have lost hope in the people of the graves."},"5164":{"id":5164,"surah":61,"ayah":1,"verse":"All whatever is in the heavens and all whatever is in the earth proclaims the Purity of Allah; and He only is the Most Honourable, the Wise."},"5165":{"id":5165,"surah":61,"ayah":2,"verse":"O People who Believe! Why do you preach what you do not practice?"},"5166":{"id":5166,"surah":61,"ayah":3,"verse":"How despicable it is in the sight of Allah that you may preach what you do not practice."},"5167":{"id":5167,"surah":61,"ayah":4,"verse":"Indeed Allah likes those who fight in His cause, positioned in rows, as if they were an edifice fortified."},"5168":{"id":5168,"surah":61,"ayah":5,"verse":"And remember when Moosa said to his people, \u201cO my people! Why do you trouble me, whereas you know that I am Allah\u2019s Noble Messenger towards you?\u201d So when they deviated, Allah deviated their hearts; and Allah does not guide the sinning people."},"5169":{"id":5169,"surah":61,"ayah":6,"verse":"And remember when Eisa the son of Maryam said, \u201cO Descendants of Israel! Indeed I am Allah\u2019s Noble Messenger towards you, confirming the Book Torah which was before me, and heralding glad tidings of the Noble Messenger who will come after me \u2013 his name is Ahmed (the Praised One)\u201d; so when Ahmed came to them with clear proofs, they said, \u201cThis is an obvious magic.\u201d"},"5170":{"id":5170,"surah":61,"ayah":7,"verse":"And who is more unjust than one who fabricates a lie against Allah whereas he is being called towards Islam? And Allah does not guide the unjust people."},"5171":{"id":5171,"surah":61,"ayah":8,"verse":"They wish to put out Allah\u2019s light with their mouths, whereas Allah will perfect His light even if the disbelievers get annoyed."},"5172":{"id":5172,"surah":61,"ayah":9,"verse":"It is He Who has sent His Noble Messenger with guidance and the religion of truth, in order that He may make it prevail over all other religions, even if the polytheists get annoyed."},"5173":{"id":5173,"surah":61,"ayah":10,"verse":"O People who Believe! Shall I show you a trade that can save you from the painful punishment?"},"5174":{"id":5174,"surah":61,"ayah":11,"verse":"Have faith in Allah and His Noble Messenger, and fight in Allah\u2019s cause with your wealth and your lives; this is better for you, if you understand."},"5175":{"id":5175,"surah":61,"ayah":12,"verse":"He will forgive you your sins and admit you into Gardens beneath which rivers flow, and superb dwellings in everlasting Gardens of Eden; this is the greatest success."},"5176":{"id":5176,"surah":61,"ayah":13,"verse":"And He will give you another favour, which is dear to you \u2013 the help from Allah and an imminent victory; and O dear Prophet (Mohammed \u2013 peace and blessings be upon him), give glad tidings to the Muslims."},"5177":{"id":5177,"surah":61,"ayah":14,"verse":"O People who Believe! Become the aides of Allah\u2019s religion, the way Eisa the son of Maryam had said to the disciples, \u201cWho will help me, inclining towards Allah?\u201d The disciples said, \u201cWe are the aides of Allah\u2019s religion\u201d \u2013 so a group among the Descendants of Israel accepted faith and another group disbelieved; We therefore aided the believers against their enemies, so they were victorious."},"5178":{"id":5178,"surah":62,"ayah":1,"verse":"All whatever is in the heavens and all whatever is in the earth proclaims the Purity of Allah, the King, the Most Pure, the Most Honourable, the Wise."},"5179":{"id":5179,"surah":62,"ayah":2,"verse":"It is He Who has sent among the unlettered people a Noble Messenger from themselves, who recites His verses to them and purifies them, and bestows them the knowledge of the Book and wisdom; and indeed before this, they were in open error."},"5180":{"id":5180,"surah":62,"ayah":3,"verse":"And he (the Holy Prophet) bestows knowledge to others along with them, who have not yet joined them; and He only is the Most Honourable, the Wise."},"5181":{"id":5181,"surah":62,"ayah":4,"verse":"This is Allah\u2019s munificence, which He may give to whomever He wills; and Allah is Extremely Munificent."},"5182":{"id":5182,"surah":62,"ayah":5,"verse":"The example of those who were entrusted with the Taurat, and did not carry out its commands, is like the donkey carrying books; what a wretched example is of the people who denied the signs of Allah; and Allah does not guide the unjust."},"5183":{"id":5183,"surah":62,"ayah":6,"verse":"Say \u201cO Jews! If you assume that you are Allah\u2019s friends, others are not, then wish for death if you are truthful!\u201d"},"5184":{"id":5184,"surah":62,"ayah":7,"verse":"And they will never wish for it because of the misdeeds their hands have sent ahead; and Allah well knows the unjust."},"5185":{"id":5185,"surah":62,"ayah":8,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIndeed death which you run away from will surely confront you, then you will be returned to Him Who knows all the hidden and the visible \u2013 He will then inform you of what you did.\u201d"},"5186":{"id":5186,"surah":62,"ayah":9,"verse":"O People who Believe! When the call for prayer is given on (Friday) the day of congregation, rush towards the remembrance of Allah and stop buying and selling; this is better for you if you understand."},"5187":{"id":5187,"surah":62,"ayah":10,"verse":"And when the prayer ends, spread out in the land and seek Allah\u2019s munificence, and profusely remember Allah, in the hope of attaining success."},"5188":{"id":5188,"surah":62,"ayah":11,"verse":"And when they see some trade or sport, they move towards it and leave you standing delivering the sermon; proclaim, \u201cThat which is with Allah, is better than sport and trade; and the sustenance of Allah is the best.\u201d"},"5189":{"id":5189,"surah":63,"ayah":1,"verse":"When the hypocrites come in your presence (O dear Prophet Mohammed \u2013 peace and blessings be upon him) they say, \u201cWe testify that you surely are Allah\u2019s Noble Messenger\u201d; and Allah knows that you indeed are His Noble Messenger, and Allah testifies that the hypocrites are indeed liars."},"5190":{"id":5190,"surah":63,"ayah":2,"verse":"They have taken their oaths as a shield, thereby prevented others from Allah\u2019s way; indeed they commit extremely evil deeds."},"5191":{"id":5191,"surah":63,"ayah":3,"verse":"That is because they accepted faith with their tongues then disbelieved with their hearts, therefore their hearts were sealed \u2013 so now they do not understand anything."},"5192":{"id":5192,"surah":63,"ayah":4,"verse":"And when you see them, their appearance would please you; and when they speak, you would listen carefully to their speech; like wooden blocks propped against the wall; they assume every cry to be against them; they are the enemy, so beware of them; may Allah slay them! Where are they reverting!"},"5193":{"id":5193,"surah":63,"ayah":5,"verse":"And when it is said to them, \u201cCome! Allah\u2019s Noble Messenger may seek forgiveness for you\u201d \u2013 they turn heads away, and you will see them turning away in pride."},"5194":{"id":5194,"surah":63,"ayah":6,"verse":"It is the same for them, whether you seek forgiveness for them or do not seek forgiveness for them; Allah will never forgive them; indeed Allah does not guide the sinning people."},"5195":{"id":5195,"surah":63,"ayah":7,"verse":"It is they who say, \u201cDo not spend upon those who are with Allah\u2019s Noble Messenger until they get distraught\u201d; whereas to Allah only belong the treasures of the heavens and the earth, but the hypocrites do not have sense."},"5196":{"id":5196,"surah":63,"ayah":8,"verse":"They say, \u201cIf we return to Medinah, then indeed the honourable group will soon expel the lowly one\u201d; whereas all honour belongs to Allah and to His Noble Messenger and to the Muslims, but the hypocrites do not know."},"5197":{"id":5197,"surah":63,"ayah":9,"verse":"O People who Believe! May not your wealth or your children cause you to neglect the remembrance of Allah; and whoever does this \u2013 so it is they who are in a loss."},"5198":{"id":5198,"surah":63,"ayah":10,"verse":"And spend from what We have provided you before death approaches any one of you and he then says, \u201cMy Lord! Why did you not give me respite for a little while, so I would have given charity and become of the virtuous?\u201d"},"5199":{"id":5199,"surah":63,"ayah":11,"verse":"And Allah will never give respite to any soul when its promise arrives; and Allah is Aware of your deeds."},"5200":{"id":5200,"surah":64,"ayah":1,"verse":"All whatever is in the heavens and all whatever is in the earth proclaims the Purity of Allah; His only is the kingship and His only is all praise; and He is Able to do all things."},"5201":{"id":5201,"surah":64,"ayah":2,"verse":"It is He Who has created you, so among you one is a disbeliever whereas another is a Muslim; and Allah is seeing your deeds."},"5202":{"id":5202,"surah":64,"ayah":3,"verse":"He created the heavens and the earth with the truth, and fashioned you thereby making you in best shapes; and towards Him only is the return."},"5203":{"id":5203,"surah":64,"ayah":4,"verse":"He knows all whatever is in the heavens and in the earth, and He knows all what you hide and all what you disclose; and Allah knows what lies within the hearts."},"5204":{"id":5204,"surah":64,"ayah":5,"verse":"Did not the news of those who disbelieved before you reach you? So they tasted the evil outcome of their deeds, and for them is a painful punishment."},"5205":{"id":5205,"surah":64,"ayah":6,"verse":"This is because their Noble Messengers used to bring clear proofs to them, in response they said, \u201cWhat! Will humans show us the way?\u201d; they therefore became disbelievers and turned away, and Allah acted independently; and Allah is the Independent, the Most Praiseworthy."},"5206":{"id":5206,"surah":64,"ayah":7,"verse":"The disbelievers alleged that they will surely not be raised again; proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cSurely yes, why not? By oath of Lord, you will surely be raised again and you will then be informed of your misdeeds; and this is easy for Allah.\u201d"},"5207":{"id":5207,"surah":64,"ayah":8,"verse":"Therefore accept faith in Allah and His Noble Messenger and the light which We have sent down; and Allah is Well Aware of what you do."},"5208":{"id":5208,"surah":64,"ayah":9,"verse":"The day when He will gather you, on the Day of Collective Assembly \u2013 that is the day when the loss of the losers will be laid bare; and whoever believes in Allah and does good deeds, Allah will relieve him of his sins and admit him into Gardens beneath which rivers flow, for them to abide in it forever; this is the greatest success."},"5209":{"id":5209,"surah":64,"ayah":10,"verse":"And those who disbelieved and denied Our signs, are the people of the fire \u2013 to remain in it forever; and what a wretched outcome!"},"5210":{"id":5210,"surah":64,"ayah":11,"verse":"No misfortune befalls except by Allah\u2019s command; and Allah will guide the heart of whoever accepts faith in Him; and Allah knows all things."},"5211":{"id":5211,"surah":64,"ayah":12,"verse":"And obey Allah and obey His Noble Messenger; then if you turn away, know that the duty upon Our Noble Messenger is only to plainly convey."},"5212":{"id":5212,"surah":64,"ayah":13,"verse":"Allah \u2013 there is none worthy of worship except Him; and only upon Allah must the Muslims rely."},"5213":{"id":5213,"surah":64,"ayah":14,"verse":"O People who Believe! Some of your wives and children are your enemies, therefore be wary of them; and if you pardon and overlook and forgive, then indeed Allah is Oft Forgiving, Most Merciful."},"5214":{"id":5214,"surah":64,"ayah":15,"verse":"Your wealth and your children are just a test; and with Allah is a tremendous reward."},"5215":{"id":5215,"surah":64,"ayah":16,"verse":"Therefore fear Allah as much as possible, and heed the commands, and obey, and spend in Allah\u2019s cause, for your own good; and whoever is rescued from the greed of his own soul \u2013 is they who will be successful."},"5216":{"id":5216,"surah":64,"ayah":17,"verse":"If you lend an excellent loan to Allah, He will double it for you and forgive you; and Allah is Most Appreciative, Most Forbearing."},"5217":{"id":5217,"surah":64,"ayah":18,"verse":"The All Knowing of all things hidden and visible, the Most Honourable, the Wise."},"5218":{"id":5218,"surah":65,"ayah":1,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him)! When you people divorce women, divorce them at the time of their completing the appointed period, and keep count of the appointed period; and fear Allah, your Lord; do not expel them from their houses during the appointed period nor should they leave on their own, unless they bring about some matter of blatant lewdness; and these are the limits of Allah; and whoever crosses Allah\u2019s limits has indeed wronged himself; do you not know, it is likely that Allah may send some new decree after this?"},"5219":{"id":5219,"surah":65,"ayah":2,"verse":"So when they are about to reach their appointed term, hold them back with kindness or separate them with kindness, and make two just men among you as witnesses, and establish the testimony for Allah; with this is advised whoever believes in Allah and the Last Day; and whoever fears Allah \u2013 Allah will create for him a way of deliverance."},"5220":{"id":5220,"surah":65,"ayah":3,"verse":"And will provide him sustenance from a place he had never expected; and whoever relies on Allah \u2013 then Allah is Sufficient for him; indeed Allah will accomplish His command; indeed Allah has set a proper measure for all things."},"5221":{"id":5221,"surah":65,"ayah":4,"verse":"And for those of your women who have no hope of menstruation, if you doubt, the appointed period is three months \u2013 and also for those who have not yet had menstruation; and the appointed period for the pregnant women is up to the time they deliver their burden; and whoever fears Allah \u2013 Allah will create ease for him in his affairs."},"5222":{"id":5222,"surah":65,"ayah":5,"verse":"This is Allah\u2019s command that He has sent down towards you; and whoever fears Allah \u2013 Allah will relieve his sins and bestow upon him a great reward."},"5223":{"id":5223,"surah":65,"ayah":6,"verse":"Accommodate them where you also reside, according to your means, and do not harass them to make it difficult upon them; and if they are pregnant, give them the provision till they deliver their burden; then if they suckle the child for you, pay them its due; and consult with each other in a reasonable manner; and if you create hardship for one another, the child will get another breast feeding nurse."},"5224":{"id":5224,"surah":65,"ayah":7,"verse":"Whoever has the capacity must give provisions according to his means; and the one whose sustenance is restricted upon him, must give provisions from what Allah has given him; Allah does not burden any soul except according to what He has given it; Allah will soon, after the hardship, create ease."},"5225":{"id":5225,"surah":65,"ayah":8,"verse":"And many a town existed that rebelled against the command of its Lord and His Noble Messengers, so We took a severe account from it \u2013 and struck them with a dreadful punishment."},"5226":{"id":5226,"surah":65,"ayah":9,"verse":"They therefore tasted the evil outcome of their deeds, and the outcome of their deeds was a loss."},"5227":{"id":5227,"surah":65,"ayah":10,"verse":"Allah has kept prepared a severe punishment for them \u2013 therefore fear Allah, O men of intellect! O People who Believe! Allah has sent down a Remembrance (or an honour) for you. (The Holy Prophet is a Remembrance from Allah.)"},"5228":{"id":5228,"surah":65,"ayah":11,"verse":"The Noble Messenger who recites the clear verses of Allah to you, so that he may take those who accept faith and do good deeds, away from the realms of darkness towards light; and whoever accepts faith in Allah and does good deeds, Allah will admit him into Gardens beneath which rivers flow, to abide in them for ever and ever; indeed Allah has kept an excellent sustenance for him."},"5229":{"id":5229,"surah":65,"ayah":12,"verse":"It is Allah Who has created the seven heavens, and seven earthly worlds like them; the command descends within them, so that you may know that Allah is Able to do all things, and that Allah\u2019s knowledge encompasses everything."},"5230":{"id":5230,"surah":66,"ayah":1,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him)! Why do you forbid for yourself the things that Allah has made lawful for you? You wish to please some of your wives; and Allah is Oft Forgiving, Most Merciful."},"5231":{"id":5231,"surah":66,"ayah":2,"verse":"Allah has ordained expiation for your oaths; and Allah is your Master; and Allah is the All Knowing, the Wise."},"5232":{"id":5232,"surah":66,"ayah":3,"verse":"And when the Holy Prophet confided a matter to one of his wives; so when she disclosed it and Allah conveyed this to the Holy Prophet, he told her part of it and held back a part; so when the Holy Prophet informed her about it, she said, \u201cWho informed you?\u201d; he said, \u201cThe All Knowing, the All Aware has informed me.\u201d"},"5233":{"id":5233,"surah":66,"ayah":4,"verse":"If you both, the wives of the Holy Prophet, incline towards Allah, for indeed your hearts have deviated a little; and if you come together against him (the Holy Prophet \u2013 peace and blessings be upon him) then indeed Allah is his Supporter, and Jibreel and the virtuous believers are also his aides; and in addition the angels are also his aides. (Allah has created several supporters for the believers.)"},"5234":{"id":5234,"surah":66,"ayah":5,"verse":"It is likely that, if he divorces you, his Lord will give him wives better than you in your place -widows and maidens who are obedient, believing, respectful, penitent, serving and fasting."},"5235":{"id":5235,"surah":66,"ayah":6,"verse":"O People who Believe! Save yourselves and your families from the fire, the fuel of which is men and stones \u2013 appointed over it are the extremely strict angels, who do not refuse the command of Allah and carry out whatever they are commanded."},"5236":{"id":5236,"surah":66,"ayah":7,"verse":"\u201cO disbelievers! Do not feign excuses this day; you will only be repaid what you used to do.\u201d"},"5237":{"id":5237,"surah":66,"ayah":8,"verse":"O People who Believe! Incline towards Allah in a repentance that becomes a guidance for the future; it is likely that your Lord will relieve you of your sins and admit you into Gardens beneath which rivers flow \u2013 on the day when Allah will not humble the Prophet and the believers along with him; their light will be running ahead of them and on their right; they will say, \u201cOur Lord! Perfect our light for us, and forgive us; indeed You are Able to do all things.\u201d"},"5238":{"id":5238,"surah":66,"ayah":9,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him), wage holy war against the disbelievers and the hypocrites, and be strict with them; and their destination is hell; and what a wretched outcome!"},"5239":{"id":5239,"surah":66,"ayah":10,"verse":"Allah illustrates the example of the disbelievers \u2013 the wife of Nooh and the wife of Lut; they were bonded in marriage to two of Our bondmen deserving Our proximity \u2013 they then betrayed them so they did not benefit them the least against Allah and it was declared, \u201cBoth of you women enter the fire, along with others who enter.\u201d"},"5240":{"id":5240,"surah":66,"ayah":11,"verse":"And Allah illustrates an example of the Muslims \u2013 the wife of Firaun; when she prayed, \u201cMy Lord! Build a house for me near You, in Paradise, and deliver me from Firaun and his works, and rescue me from the unjust people.\u201d"},"5241":{"id":5241,"surah":66,"ayah":12,"verse":"And the example of Maryam the daughter of Imran, who guarded her chastity \u2013 We therefore breathed into her a Spirit from Ourselves \u2013 and she testified for the Words of her Lord and His Books, and was among the obedient."},"5242":{"id":5242,"surah":67,"ayah":1,"verse":"Most Auspicious is He in Whose control is the entire kingship; and He is Able to do all things."},"5243":{"id":5243,"surah":67,"ayah":2,"verse":"The One Who created death and life to test you \u2013 who among you has the better deeds; and He only is the Most Honourable, the Oft Forgiving."},"5244":{"id":5244,"surah":67,"ayah":3,"verse":"The One Who created the seven heavens atop each other; do you see any discrepancy in the creation of the Most Gracious? Therefore lift your gaze \u2013 do you see any cracks?"},"5245":{"id":5245,"surah":67,"ayah":4,"verse":"Then lift your gaze again, your gaze will return towards you, unsuccessful and weak."},"5246":{"id":5246,"surah":67,"ayah":5,"verse":"And indeed We have beautified the lower heaven with lamps, and have made them weapons against the devils, and have kept prepared for them the punishment of the blazing fire."},"5247":{"id":5247,"surah":67,"ayah":6,"verse":"And for those who disbelieved in their Lord, is the punishment of hell; and what a wretched outcome!"},"5248":{"id":5248,"surah":67,"ayah":7,"verse":"They will hear it hissing when they will be thrown into it, and it is boiling."},"5249":{"id":5249,"surah":67,"ayah":8,"verse":"As if about to explode with rage; whenever a group is thrown into it, the guardians of hell will ask them, \u201cDid not a Herald of Warning come to you?\u201d"},"5250":{"id":5250,"surah":67,"ayah":9,"verse":"They will say, \u201cYes, why not \u2013 indeed a Herald of Warning did come to us \u2013 in response we denied and said \u2018Allah has not sent down anything \u2013 you are not except in a great error\u2019.\u201d"},"5251":{"id":5251,"surah":67,"ayah":10,"verse":"And they will say, \u201cHad we listened or understood, we would not have been among the people of hell.\u201d"},"5252":{"id":5252,"surah":67,"ayah":11,"verse":"So now they admit their sins! Therefore accursed be the people of hell!"},"5253":{"id":5253,"surah":67,"ayah":12,"verse":"Indeed for those who fear their Lord without seeing is forgiveness, and a great reward."},"5254":{"id":5254,"surah":67,"ayah":13,"verse":"And whether you speak softly or proclaim it aloud; He indeed knows what lies within the hearts!"},"5255":{"id":5255,"surah":67,"ayah":14,"verse":"What! Will He Who has created not know? Whereas He knows every detail, the All Aware!"},"5256":{"id":5256,"surah":67,"ayah":15,"verse":"It is He Who subjected the earth for you, therefore tread its paths and eat from Allah\u2019s sustenance; and towards Him is the resurrection."},"5257":{"id":5257,"surah":67,"ayah":16,"verse":"Have you become unafraid of the One Who controls the heavens, that He will not cause you to sink into the earth when it trembles?"},"5258":{"id":5258,"surah":67,"ayah":17,"verse":"Or have you become unafraid of the One Who controls the heavens, that He will not send a torrent of stones upon you? So now you will realise, how My warning turned out!"},"5259":{"id":5259,"surah":67,"ayah":18,"verse":"And indeed those before them had denied \u2013 therefore how did My rejection turn out!"},"5260":{"id":5260,"surah":67,"ayah":19,"verse":"And did they not see the birds above them, spreading and closing their wings? None except the Most Gracious holds them up; indeed He sees all things."},"5261":{"id":5261,"surah":67,"ayah":20,"verse":"Or which army do you have that will help you against the Most Gracious? The disbelievers are in nothing except an illusion."},"5262":{"id":5262,"surah":67,"ayah":21,"verse":"Or who is such that will give you sustenance if Allah stops His sustenance? In fact they persist in rebellion and hatred."},"5263":{"id":5263,"surah":67,"ayah":22,"verse":"So is one who walks inverted upon his face more rightly guided, or one who walks upright on the Straight Path?"},"5264":{"id":5264,"surah":67,"ayah":23,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cIt is He Who created you, and made ears and eyes and hearts for you; very little thanks do you offer!\u201d"},"5265":{"id":5265,"surah":67,"ayah":24,"verse":"Say, \u201cIt is He Who has spread you out in the earth, and towards Him you will be raised.\u201d"},"5266":{"id":5266,"surah":67,"ayah":25,"verse":"And they say, \u201cWhen will this promise come, if you are truthful?\u201d"},"5267":{"id":5267,"surah":67,"ayah":26,"verse":"Proclaim, \u201cSurely Allah has its knowledge; and I am only a Herald of plain warning.\u201d"},"5268":{"id":5268,"surah":67,"ayah":27,"verse":"So when they will see it close, the faces of the disbelievers will become ghastly, and it will be declared, \u201cThis is what you were demanding.\u201d"},"5269":{"id":5269,"surah":67,"ayah":28,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cWhat is your opinion \u2013Allah may either destroy me and those with me, or have mercy on us \u2013 so who is such that will protect the disbelievers from the painful punishment?\u201d"},"5270":{"id":5270,"surah":67,"ayah":29,"verse":"Proclaim, \u201cHe only is the Most Gracious \u2013 we have accepted faith in Him and have relied only upon Him; so you will now realise who is in open error.\u201d"},"5271":{"id":5271,"surah":67,"ayah":30,"verse":"Say, \u201cWhat is your opinion \u2013 if in the morning all your water were to sink into the earth, then who is such who can bring you water flowing before you?\u201d"},"5272":{"id":5272,"surah":68,"ayah":1,"verse":"Nuun* \u2013 by oath of the pen and by oath of what is written by it. (Alphabet of the Arabic language; Allah and to whomever He reveals, know their precise meanings.)"},"5273":{"id":5273,"surah":68,"ayah":2,"verse":"You are not insane, by the munificence of your Lord."},"5274":{"id":5274,"surah":68,"ayah":3,"verse":"And indeed for you is an unlimited reward."},"5275":{"id":5275,"surah":68,"ayah":4,"verse":"And indeed you possess an exemplary character."},"5276":{"id":5276,"surah":68,"ayah":5,"verse":"So very soon, you will see and they too will realise \u2013"},"5277":{"id":5277,"surah":68,"ayah":6,"verse":"- That who among you was insane."},"5278":{"id":5278,"surah":68,"ayah":7,"verse":"Indeed your Lord well knows those who have strayed from His path, and He well knows those who are upon guidance."},"5279":{"id":5279,"surah":68,"ayah":8,"verse":"Therefore do not listen to the deniers."},"5280":{"id":5280,"surah":68,"ayah":9,"verse":"They wish that in some way you may yield, so they too might soften their stand."},"5281":{"id":5281,"surah":68,"ayah":10,"verse":"Nor ever listen to any excessive oath maker, ignoble person."},"5282":{"id":5282,"surah":68,"ayah":11,"verse":"The excessively insulting one, spreader of spite."},"5283":{"id":5283,"surah":68,"ayah":12,"verse":"One who excessively forbids the good, transgressor, sinner."},"5284":{"id":5284,"surah":68,"ayah":13,"verse":"Foul mouthed, and in addition to all this, of improper lineage."},"5285":{"id":5285,"surah":68,"ayah":14,"verse":"Because he* has some wealth and sons. (Walid bin Mugaira, who cursed the Holy Prophet.)"},"5286":{"id":5286,"surah":68,"ayah":15,"verse":"When Our verses are recited to him, he says, \u201cThese are stories of earlier people.\u201d"},"5287":{"id":5287,"surah":68,"ayah":16,"verse":"We will soon singe his pig-nose."},"5288":{"id":5288,"surah":68,"ayah":17,"verse":"We have indeed tested them the way We had tested the owners of the garden when they swore that they would reap its harvest the next morning."},"5289":{"id":5289,"surah":68,"ayah":18,"verse":"And they did not say, \u201cIf Allah wills\u201d."},"5290":{"id":5290,"surah":68,"ayah":19,"verse":"So an envoy from your Lord completed his round upon the garden, whilst they were sleeping."},"5291":{"id":5291,"surah":68,"ayah":20,"verse":"So in the morning it became as if harvested."},"5292":{"id":5292,"surah":68,"ayah":21,"verse":"They then called out to each other at daybreak."},"5293":{"id":5293,"surah":68,"ayah":22,"verse":"That, \u201cGo to your fields at early morn, if you want to harvest.\u201d"},"5294":{"id":5294,"surah":68,"ayah":23,"verse":"So they went off, while whispering to one another."},"5295":{"id":5295,"surah":68,"ayah":24,"verse":"\u201cMake sure that no needy person enters your garden this day.\u201d"},"5296":{"id":5296,"surah":68,"ayah":25,"verse":"And they left at early morn, assuming they were in control of their purpose."},"5297":{"id":5297,"surah":68,"ayah":26,"verse":"Then when they saw it, they said, \u201cWe have indeed strayed.\u201d"},"5298":{"id":5298,"surah":68,"ayah":27,"verse":"\u201cIn fact, we are unfortunate.\u201d"},"5299":{"id":5299,"surah":68,"ayah":28,"verse":"The best among them said, \u201cDid I not tell you, \u2018Why do you not proclaim His purity?\u2019\u201d"},"5300":{"id":5300,"surah":68,"ayah":29,"verse":"They said, \u201cPurity is to our Lord \u2013 we have indeed been unjust.\u201d"},"5301":{"id":5301,"surah":68,"ayah":30,"verse":"So they came towards each other, blaming."},"5302":{"id":5302,"surah":68,"ayah":31,"verse":"They said, \u201cWoe to us \u2013 we were indeed rebellious.\u201d"},"5303":{"id":5303,"surah":68,"ayah":32,"verse":"\u201cHopefully, our Lord will give us a better replacement than this \u2013 we now incline towards our Lord.\u201d"},"5304":{"id":5304,"surah":68,"ayah":33,"verse":"Such is the punishment; and indeed the punishment of the Hereafter is the greatest, if only they knew!"},"5305":{"id":5305,"surah":68,"ayah":34,"verse":"Indeed for the pious, with their Lord, are Gardens of Serenity."},"5306":{"id":5306,"surah":68,"ayah":35,"verse":"Shall We equate the Muslims to the guilty?"},"5307":{"id":5307,"surah":68,"ayah":36,"verse":"What is the matter with you? What sort of a judgement you impose!"},"5308":{"id":5308,"surah":68,"ayah":37,"verse":"Is there a Book for you, from which you read?"},"5309":{"id":5309,"surah":68,"ayah":38,"verse":"- That for you in it is whatever you like?"},"5310":{"id":5310,"surah":68,"ayah":39,"verse":"Or is it that you have a covenant from Us, right up to the Day of Judgement, that you will get all what you claim?"},"5311":{"id":5311,"surah":68,"ayah":40,"verse":"Ask them, who among them is a guarantor for it?"},"5312":{"id":5312,"surah":68,"ayah":41,"verse":"Or is it that they have partners in worship? So they should bring their appointed partners, if they are truthful."},"5313":{"id":5313,"surah":68,"ayah":42,"verse":"On the day when the Shin* will be exposed and they will be called to prostrate themselves, they will be unable. (Used as a metaphor)"},"5314":{"id":5314,"surah":68,"ayah":43,"verse":"With lowered eyes, disgrace overcoming them; and indeed they used to be called to prostrate themselves whilst they were healthy."},"5315":{"id":5315,"surah":68,"ayah":44,"verse":"Therefore leave the one who denies this matter, to Me; We shall soon steadily take them away, from a place they do not know."},"5316":{"id":5316,"surah":68,"ayah":45,"verse":"And I will give them respite; indeed My plan is very solid."},"5317":{"id":5317,"surah":68,"ayah":46,"verse":"Or is it that you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) ask any fee from them, so they are burdened with the penalty?"},"5318":{"id":5318,"surah":68,"ayah":47,"verse":"Or that they possess the hidden, so they are writing it?"},"5319":{"id":5319,"surah":68,"ayah":48,"verse":"Therefore wait for your Lord\u2019s command, and do not be like the one of the fish; who cried out when he was distraught. (Prophet Yunus \u2013 peace be upon him.)"},"5320":{"id":5320,"surah":68,"ayah":49,"verse":"Were it not for his Lord\u2019s favour that reached him, he would have surely been cast onto the desolate land, reproached."},"5321":{"id":5321,"surah":68,"ayah":50,"verse":"His Lord therefore chose him and made him among those deserving His proximity."},"5322":{"id":5322,"surah":68,"ayah":51,"verse":"And indeed the disbelievers seem as if they would topple you with their evil gaze when they hear the Qur\u2019an, and they say, \u201cHe is indeed insane.\u201d"},"5323":{"id":5323,"surah":68,"ayah":52,"verse":"Whereas it is not but an advice to the entire creation!"},"5324":{"id":5324,"surah":69,"ayah":1,"verse":"The true event!"},"5325":{"id":5325,"surah":69,"ayah":2,"verse":"How tremendous is the true event!"},"5326":{"id":5326,"surah":69,"ayah":3,"verse":"And what have you understood, how tremendous the true event is!"},"5327":{"id":5327,"surah":69,"ayah":4,"verse":"The tribes of Thamud and A\u2019ad denied the event of great dismay. (The Day of Resurrection)"},"5328":{"id":5328,"surah":69,"ayah":5,"verse":"So regarding the Thamud, they were destroyed by a terrible scream."},"5329":{"id":5329,"surah":69,"ayah":6,"verse":"And as for A\u2019ad, they were destroyed by a severe thundering windstorm."},"5330":{"id":5330,"surah":69,"ayah":7,"verse":"He forced it upon them with strength, consecutively for seven nights and eight days \u2013 so you would see those people overthrown in it, like trunks of date palms fallen down."},"5331":{"id":5331,"surah":69,"ayah":8,"verse":"So do you see any survivor among them?"},"5332":{"id":5332,"surah":69,"ayah":9,"verse":"And Firaun, and those before him, and the dwellings that were inverted and thrown, had brought error."},"5333":{"id":5333,"surah":69,"ayah":10,"verse":"They therefore disobeyed the Noble Messengers of their Lord \u2013 so He seized them with an intense seizure."},"5334":{"id":5334,"surah":69,"ayah":11,"verse":"Indeed when the water swelled up, We boarded you onto the ship."},"5335":{"id":5335,"surah":69,"ayah":12,"verse":"In order to make it a remembrance for you, and in order that the ears that store may remember."},"5336":{"id":5336,"surah":69,"ayah":13,"verse":"So when the Trumpet will be blown, with a sudden single blow."},"5337":{"id":5337,"surah":69,"ayah":14,"verse":"And the earth and the mountains will be lifted up and crushed with a single crush."},"5338":{"id":5338,"surah":69,"ayah":15,"verse":"So that is the day when the forthcoming event will occur."},"5339":{"id":5339,"surah":69,"ayah":16,"verse":"And the heaven will split asunder \u2013 so on that day it will be unstable."},"5340":{"id":5340,"surah":69,"ayah":17,"verse":"And the angels will be on its sides; and on that day, eight angels will carry the Throne of your Lord above them."},"5341":{"id":5341,"surah":69,"ayah":18,"verse":"On that day all of you will be brought forth, so none among you wishing to hide will be able to hide."},"5342":{"id":5342,"surah":69,"ayah":19,"verse":"So whoever is given his book in his right hand \u2013 he will say, \u201cTake, read my account!\u201d"},"5343":{"id":5343,"surah":69,"ayah":20,"verse":"\u201cI was certain that I will confront my account.\u201d"},"5344":{"id":5344,"surah":69,"ayah":21,"verse":"He is therefore in the desired serenity."},"5345":{"id":5345,"surah":69,"ayah":22,"verse":"In a lofty Garden \u2013"},"5346":{"id":5346,"surah":69,"ayah":23,"verse":"The fruit clusters of which are hanging down."},"5347":{"id":5347,"surah":69,"ayah":24,"verse":"\u201cEat and drink with pleasure \u2013 the reward of what you sent ahead, in the past days.\u201d"},"5348":{"id":5348,"surah":69,"ayah":25,"verse":"And whoever is given his book in his left hand \u2013 he will say, \u201cAlas, if only my account were not given to me!\u201d"},"5349":{"id":5349,"surah":69,"ayah":26,"verse":"\u201cAnd had never come to know my account!\u201d"},"5350":{"id":5350,"surah":69,"ayah":27,"verse":"\u201cAlas, if only it had been just death.\u201d"},"5351":{"id":5351,"surah":69,"ayah":28,"verse":"\u201cMy wealth did not in the least benefit me.\u201d"},"5352":{"id":5352,"surah":69,"ayah":29,"verse":"\u201cAll my power has vanished.\u201d"},"5353":{"id":5353,"surah":69,"ayah":30,"verse":"It will be said, \u201cSeize him, and shackle him.\u201d"},"5354":{"id":5354,"surah":69,"ayah":31,"verse":"\u201cThen hurl him into the blazing fire.\u201d"},"5355":{"id":5355,"surah":69,"ayah":32,"verse":"\u201cThen bind him inside a chain which is seventy arm-lengths.\u201d"},"5356":{"id":5356,"surah":69,"ayah":33,"verse":"\u201cIndeed he refused to accept faith in Allah, the Greatest.\u201d"},"5357":{"id":5357,"surah":69,"ayah":34,"verse":"\u201cAnd did not urge to feed the needy.\u201d"},"5358":{"id":5358,"surah":69,"ayah":35,"verse":"\u201cSo he does not have any friend here this day.\u201d"},"5359":{"id":5359,"surah":69,"ayah":36,"verse":"\u201cNor any food except the pus discharged from the people of hell.\u201d"},"5360":{"id":5360,"surah":69,"ayah":37,"verse":"\u201cWhich none except the guilty shall eat.\u201d"},"5361":{"id":5361,"surah":69,"ayah":38,"verse":"So by oath of the things you see."},"5362":{"id":5362,"surah":69,"ayah":39,"verse":"And by oath of those you do not see."},"5363":{"id":5363,"surah":69,"ayah":40,"verse":"This Qur\u2019an is the speech of Allah with a gracious Noble Messenger."},"5364":{"id":5364,"surah":69,"ayah":41,"verse":"And it is not the speech of a poet; how little do you believe!"},"5365":{"id":5365,"surah":69,"ayah":42,"verse":"Nor is it the speech of a soothsayer; how little do you ponder!"},"5366":{"id":5366,"surah":69,"ayah":43,"verse":"Sent down by the Lord Of The Creation."},"5367":{"id":5367,"surah":69,"ayah":44,"verse":"And had he fabricated just one matter upon Us \u2013"},"5368":{"id":5368,"surah":69,"ayah":45,"verse":"We would have definitely taken revenge from him."},"5369":{"id":5369,"surah":69,"ayah":46,"verse":"Then would have cut off his heart\u2019s artery."},"5370":{"id":5370,"surah":69,"ayah":47,"verse":"Then none among you would be his saviour."},"5371":{"id":5371,"surah":69,"ayah":48,"verse":"And indeed this Qur\u2019an is an advice for the pious."},"5372":{"id":5372,"surah":69,"ayah":49,"verse":"And indeed We know that some among you are deniers."},"5373":{"id":5373,"surah":69,"ayah":50,"verse":"And indeed it is a despair for the disbelievers."},"5374":{"id":5374,"surah":69,"ayah":51,"verse":"And indeed it is a certain Truth."},"5375":{"id":5375,"surah":69,"ayah":52,"verse":"Therefore (O dear Prophet Mohammed \u2013 peace and blessings be upon him), proclaim the purity of your Lord, the Greatest."},"5376":{"id":5376,"surah":70,"ayah":1,"verse":"A requester seeks the punishment that will take place \u2013"},"5377":{"id":5377,"surah":70,"ayah":2,"verse":"- Upon the disbelievers \u2013 the punishment that none can avert."},"5378":{"id":5378,"surah":70,"ayah":3,"verse":"From Allah, the Lord of all pinnacles."},"5379":{"id":5379,"surah":70,"ayah":4,"verse":"The angels and Jibreel, ascend towards Him \u2013 the punishment will befall on a day which spans fifty thousand years."},"5380":{"id":5380,"surah":70,"ayah":5,"verse":"Therefore patiently endure, in the best manner (O dear Prophet Mohammed \u2013 peace and blessings be upon him)."},"5381":{"id":5381,"surah":70,"ayah":6,"verse":"They deem it to be remote."},"5382":{"id":5382,"surah":70,"ayah":7,"verse":"Whereas We see it impending."},"5383":{"id":5383,"surah":70,"ayah":8,"verse":"The day when the sky will be like molten silver."},"5384":{"id":5384,"surah":70,"ayah":9,"verse":"And the hills will be light as wool."},"5385":{"id":5385,"surah":70,"ayah":10,"verse":"And no friend will ask concerning his friend."},"5386":{"id":5386,"surah":70,"ayah":11,"verse":"They will be seeing them; the guilty will wish if only he could redeem himself from the punishment of that day, by offering his sons."},"5387":{"id":5387,"surah":70,"ayah":12,"verse":"And his wife and his brother."},"5388":{"id":5388,"surah":70,"ayah":13,"verse":"And the family in which he was."},"5389":{"id":5389,"surah":70,"ayah":14,"verse":"And all those who are in the earth \u2013 then only if the redemption saves him!"},"5390":{"id":5390,"surah":70,"ayah":15,"verse":"Never! That is indeed a blazing fire."},"5391":{"id":5391,"surah":70,"ayah":16,"verse":"A fire that melts the hide."},"5392":{"id":5392,"surah":70,"ayah":17,"verse":"It calls out to him who reverted and turned away."},"5393":{"id":5393,"surah":70,"ayah":18,"verse":"And accumulated wealth and hoarded it."},"5394":{"id":5394,"surah":70,"ayah":19,"verse":"Indeed man is created very impatient, greedy."},"5395":{"id":5395,"surah":70,"ayah":20,"verse":"Very nervous when touched by misfortune."},"5396":{"id":5396,"surah":70,"ayah":21,"verse":"And refraining, when good reaches him."},"5397":{"id":5397,"surah":70,"ayah":22,"verse":"Except those who establish prayer."},"5398":{"id":5398,"surah":70,"ayah":23,"verse":"Those who are regular in their prayers."},"5399":{"id":5399,"surah":70,"ayah":24,"verse":"And those in whose wealth exists a recognised right,"},"5400":{"id":5400,"surah":70,"ayah":25,"verse":"For those who beg, and for the needy who cannot even ask."},"5401":{"id":5401,"surah":70,"ayah":26,"verse":"And those who believe the Day of Judgement to be true."},"5402":{"id":5402,"surah":70,"ayah":27,"verse":"And those who fear the punishment of their Lord."},"5403":{"id":5403,"surah":70,"ayah":28,"verse":"Indeed the punishment of their Lord is not a thing to be unafraid of!"},"5404":{"id":5404,"surah":70,"ayah":29,"verse":"And those who protect their private organs (from adultery)."},"5405":{"id":5405,"surah":70,"ayah":30,"verse":"Except with their wives and the bondwomen in their possession, for there is no reproach on them."},"5406":{"id":5406,"surah":70,"ayah":31,"verse":"So those who desire more than this \u2013 it is they who are the transgressors."},"5407":{"id":5407,"surah":70,"ayah":32,"verse":"And those who protect the property entrusted to them, and their agreements."},"5408":{"id":5408,"surah":70,"ayah":33,"verse":"And those who are firm upon their testimonies."},"5409":{"id":5409,"surah":70,"ayah":34,"verse":"And those who protect their prayers."},"5410":{"id":5410,"surah":70,"ayah":35,"verse":"It is these who will be honoured in Gardens."},"5411":{"id":5411,"surah":70,"ayah":36,"verse":"So what is the matter with these disbelievers, that they stare at you (O dear Prophet Mohammed \u2013 peace and blessings be upon him)?"},"5412":{"id":5412,"surah":70,"ayah":37,"verse":"From the right and the left, in groups?"},"5413":{"id":5413,"surah":70,"ayah":38,"verse":"Does every man among them aspire to be admitted into the Garden of serenity?"},"5414":{"id":5414,"surah":70,"ayah":39,"verse":"Never! We have indeed created them from a thing they know."},"5415":{"id":5415,"surah":70,"ayah":40,"verse":"So I swear by the Lord of every East and every West, that We are indeed Able."},"5416":{"id":5416,"surah":70,"ayah":41,"verse":"To replace them by those better than them; and none can escape from Us."},"5417":{"id":5417,"surah":70,"ayah":42,"verse":"Therefore leave them, involved in their indecencies and play, till they confront their day which they are promised."},"5418":{"id":5418,"surah":70,"ayah":43,"verse":"A day when they will come out of the graves in haste, as if rushing towards their goals."},"5419":{"id":5419,"surah":70,"ayah":44,"verse":"With lowered eyes, disgrace overcoming them; this is the day, which they had been promised."},"5420":{"id":5420,"surah":71,"ayah":1,"verse":"Indeed We sent Nooh towards his people saying that, \u201cWarn your people before the painful punishment comes upon them.\u201d"},"5421":{"id":5421,"surah":71,"ayah":2,"verse":"He said, \u201cO my people! I am indeed a Herald of clear warnings to you.\u201d"},"5422":{"id":5422,"surah":71,"ayah":3,"verse":"\u201cThat you must worship Allah and fear Him, and obey me.\u201d"},"5423":{"id":5423,"surah":71,"ayah":4,"verse":"\u201cHe will forgive you some of your sins, and give you respite up to an appointed term; indeed the promise of Allah cannot be averted when it arrives; if only you knew.\u201d"},"5424":{"id":5424,"surah":71,"ayah":5,"verse":"He said, \u201cMy Lord! I invited my people night and day.\u201d"},"5425":{"id":5425,"surah":71,"ayah":6,"verse":"\u201cSo for them, my calling them increased their fleeing away.\u201d"},"5426":{"id":5426,"surah":71,"ayah":7,"verse":"\u201cAnd whenever I called them, so that You may forgive them, they always thrust their fingers into their ears, and covered themselves with their clothes, and remained stubborn and were extremely haughty.\u201d"},"5427":{"id":5427,"surah":71,"ayah":8,"verse":"\u201cI then called them openly.\u201d"},"5428":{"id":5428,"surah":71,"ayah":9,"verse":"\u201cThen I also told them publicly and also spoke to them softly in private.\u201d"},"5429":{"id":5429,"surah":71,"ayah":10,"verse":"\u201cI therefore told them, \u2018Seek forgiveness from your Lord; He is indeed Most Forgiving.\u2019"},"5430":{"id":5430,"surah":71,"ayah":11,"verse":"\u2018He will send down abundant rain for you from the sky.\u2019"},"5431":{"id":5431,"surah":71,"ayah":12,"verse":"\u2018And will aid you with wealth and sons, and will create gardens for you and cause rivers to flow for you.\u2019"},"5432":{"id":5432,"surah":71,"ayah":13,"verse":"\u2018What is the matter with you, that you do not desire honour from Allah?\u2019"},"5433":{"id":5433,"surah":71,"ayah":14,"verse":"\u2018Whereas it is He Who created you in different stages?\u2019"},"5434":{"id":5434,"surah":71,"ayah":15,"verse":"\u2018Do you not see how Allah has created the seven heavens atop each other?\u2019"},"5435":{"id":5435,"surah":71,"ayah":16,"verse":"\u2018And in them, has illuminated the moon, and made the sun a lamp?\u2019"},"5436":{"id":5436,"surah":71,"ayah":17,"verse":"\u2018And it is Allah Who made you grow like vegetation from the earth.\u2019"},"5437":{"id":5437,"surah":71,"ayah":18,"verse":"\u2018And He will then take you back to it, and again remove you from it.\u201d"},"5438":{"id":5438,"surah":71,"ayah":19,"verse":"\u2018And it is Allah Who made the earth a bed for you.\u2019"},"5439":{"id":5439,"surah":71,"ayah":20,"verse":"\u2018So that you may tread the wide roads in it.\u2019\u201d"},"5440":{"id":5440,"surah":71,"ayah":21,"verse":"Prayed Nooh, \u201cO my Lord! They have disobeyed me, and they follow the one whose wealth and children increase nothing for him except ruin.\u201d"},"5441":{"id":5441,"surah":71,"ayah":22,"verse":"\u201cAnd they carried out a very sinister scheme.\u201d"},"5442":{"id":5442,"surah":71,"ayah":23,"verse":"\u201cAnd they said, \u2018Do not ever abandon your Gods \u2013 and never abandon Wadd, or Suwa \u2013 or Yaghuth or Yauq or Nasr.\u2019\u201d"},"5443":{"id":5443,"surah":71,"ayah":24,"verse":"\u201cAnd they have misled a large number; and (I pray that) You increase nothing for the unjust except error.\u201d"},"5444":{"id":5444,"surah":71,"ayah":25,"verse":"Because of their sins they were drowned and then put into the fire; so they did not find any supporter for themselves against Allah. (Punishment in the grave is proven by this verse.)"},"5445":{"id":5445,"surah":71,"ayah":26,"verse":"And prayed Nooh, \u201cO my Lord! Do not leave any of the disbelievers dwelling in the land.\u201d"},"5446":{"id":5446,"surah":71,"ayah":27,"verse":"\u201cIndeed, if You spare them, they will mislead your bondmen \u2013 and their descendants, if any, will be none except the wicked, very ungrateful.\u201d"},"5447":{"id":5447,"surah":71,"ayah":28,"verse":"\u201cO my Lord! Forgive me, and my parents, and the believers who are in my house, and all other Muslim men and Muslim women; and do not increase anything for the unjust except ruin.\u201d"},"5448":{"id":5448,"surah":72,"ayah":1,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI have received the divine revelation that some jinns attentively listened to my recitation, so they said, \u2018We have heard a unique Qur\u2019an.\u2019"},"5449":{"id":5449,"surah":72,"ayah":2,"verse":"\u2018That guides to the path of goodness, we have therefore accepted faith in it; and we shall never ascribe anyone as a partner to our Lord.\u2019"},"5450":{"id":5450,"surah":72,"ayah":3,"verse":"\u2018And that our Lord\u2019s Majesty is Supreme \u2013 He has neither chosen a wife nor a child.\u2019"},"5451":{"id":5451,"surah":72,"ayah":4,"verse":"\u2018And that the fool among us used to utter false exaggerations against Allah.\u2019"},"5452":{"id":5452,"surah":72,"ayah":5,"verse":"\u2018Whereas we thought that men and jinns would never fabricate a lie against Allah!\u2019"},"5453":{"id":5453,"surah":72,"ayah":6,"verse":"\u2018And indeed some men among humans used to take the protection of some men among jinns, so it further increased their haughtiness.\u2019"},"5454":{"id":5454,"surah":72,"ayah":7,"verse":"\u2018And that they assumed, like you humans assume, that Allah would not send any Noble Messenger.\u2019"},"5455":{"id":5455,"surah":72,"ayah":8,"verse":"\u2018And we reached the sky, so we found it strongly guarded and filled with comets.\u2019"},"5456":{"id":5456,"surah":72,"ayah":9,"verse":"\u2018And that we sometimes used to sit in some places in the sky, to listen; so whoever now listens finds a fiery comet waiting for him.\u2019"},"5457":{"id":5457,"surah":72,"ayah":10,"verse":"\u2018And we do not know whether harm is intended for those on the earth, or whether their Lord intends goodness for them.\u2019"},"5458":{"id":5458,"surah":72,"ayah":11,"verse":"\u2018And among us some are virtuous and some are the other type; we are split into several branches.\u2019"},"5459":{"id":5459,"surah":72,"ayah":12,"verse":"\u2018And we are certain that we cannot defeat Allah in the earth, nor can we run out of His grasp.\u2019"},"5460":{"id":5460,"surah":72,"ayah":13,"verse":"\u2018And that when we heard the guidance, we accepted faith in it; so whoever accepts faith in his Lord, has no fear \u2013 neither of any loss nor of any injustice.\u2019"},"5461":{"id":5461,"surah":72,"ayah":14,"verse":"\u2018And that some among us are Muslims and some are the unjust; and whoever has accepted Islam \u2013 it is they who have thought rightly.\u2019"},"5462":{"id":5462,"surah":72,"ayah":15,"verse":"\u2018And as for the unjust \u2013 they are the fuel of hell.\u2019\u201d"},"5463":{"id":5463,"surah":72,"ayah":16,"verse":"And proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI have received the divine revelation that \u2018Had they remained upright on the straight path, We would have given them abundant water.\u2019"},"5464":{"id":5464,"surah":72,"ayah":17,"verse":"\u2018In order to test them with it; and whoever turns away from the remembrance of his Lord \u2013 He will put him in a punishment that keeps on increasing.\u2019"},"5465":{"id":5465,"surah":72,"ayah":18,"verse":"\u2018And that the mosques are for Allah only \u2013 therefore do not worship anyone along with Allah.\u2019"},"5466":{"id":5466,"surah":72,"ayah":19,"verse":"\u2018And that when Allah\u2019s bondman stood up to worship Him, the jinns had almost crowded upon him.\u2019\u201d"},"5467":{"id":5467,"surah":72,"ayah":20,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI worship only Allah, and I do not ascribe any partner to Him.\u201d"},"5468":{"id":5468,"surah":72,"ayah":21,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI am not the master of any harm or benefit for you.\u201d"},"5469":{"id":5469,"surah":72,"ayah":22,"verse":"Say, \u201cNo one will ever save me from Allah, and other than Him, I will not find any refuge.\u201d"},"5470":{"id":5470,"surah":72,"ayah":23,"verse":"\u201cI only convey the commands from Allah, and His messages; and whoever disobeys the commands of Allah and His Noble Messenger \u2013 then indeed for him is the fire of hell, in which they will remain for ever and ever.\u201d"},"5471":{"id":5471,"surah":72,"ayah":24,"verse":"Till the time when they will see what they are promised \u2013 so they will now come to know whose aide is weak, and who is lesser in number."},"5472":{"id":5472,"surah":72,"ayah":25,"verse":"Say (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI do not know whether what you are promised is close or if my Lord will postpone it for a while.\u201d"},"5473":{"id":5473,"surah":72,"ayah":26,"verse":"The All Knowing of all the hidden does not give anyone the control over His secrets."},"5474":{"id":5474,"surah":72,"ayah":27,"verse":"Except to His chosen Noble Messengers \u2013 so He appoints guards in front and behind him. (Allah gave the knowledge of the hidden to the Holy Prophet \u2013 peace and blessings be upon him.)"},"5475":{"id":5475,"surah":72,"ayah":28,"verse":"In order to see that they have conveyed the messages of their Lord \u2013 and His knowledge encompasses all whatever they have, and He has kept all things accounted for."},"5476":{"id":5476,"surah":73,"ayah":1,"verse":"O the One Wrapped in piety! (Prophet Mohammed \u2013 peace and blessings be upon him)"},"5477":{"id":5477,"surah":73,"ayah":2,"verse":"Stand up for worship during the night, except for some part of it."},"5478":{"id":5478,"surah":73,"ayah":3,"verse":"For half the night, or reduce some from it."},"5479":{"id":5479,"surah":73,"ayah":4,"verse":"Or increase a little upon it, and recite the Qur\u2019an slowly in stages."},"5480":{"id":5480,"surah":73,"ayah":5,"verse":"Indeed We shall soon ordain a heavy responsibility upon you."},"5481":{"id":5481,"surah":73,"ayah":6,"verse":"Indeed getting up in the night is tougher, and the words flow with strength."},"5482":{"id":5482,"surah":73,"ayah":7,"verse":"Indeed you have a lot of matters during the day."},"5483":{"id":5483,"surah":73,"ayah":8,"verse":"And remember the name of your Lord and, leaving others, devote yourself solely to Him."},"5484":{"id":5484,"surah":73,"ayah":9,"verse":"Lord of the East and Lord of the West \u2013 there is no God except Him, therefore make Him your sole Trustee of affairs."},"5485":{"id":5485,"surah":73,"ayah":10,"verse":"And patiently endure upon what the disbelievers say, and leave them for good."},"5486":{"id":5486,"surah":73,"ayah":11,"verse":"And leave them to Me \u2013 these wealthy deniers \u2013 and give them some respite."},"5487":{"id":5487,"surah":73,"ayah":12,"verse":"Indeed We have heavy fetters and a blazing fire."},"5488":{"id":5488,"surah":73,"ayah":13,"verse":"And food that chokes, and a painful punishment."},"5489":{"id":5489,"surah":73,"ayah":14,"verse":"On a day when the earth and the mountains will tremble, and the mountains turn into dunes of flowing sand."},"5490":{"id":5490,"surah":73,"ayah":15,"verse":"We have indeed sent a Noble Messenger towards you, a present witness over you \u2013 the way We had sent a Noble Messenger towards Firaun."},"5491":{"id":5491,"surah":73,"ayah":16,"verse":"In response Firaun disobeyed the Noble Messenger, so We seized him with a severe seizure."},"5492":{"id":5492,"surah":73,"ayah":17,"verse":"So how will you save yourselves, if you disbelieve, on a day that will turn children old?"},"5493":{"id":5493,"surah":73,"ayah":18,"verse":"The heaven will split asunder with its grief; the promise of Allah will surely occur."},"5494":{"id":5494,"surah":73,"ayah":19,"verse":"This is indeed an advice; so whoever wishes may take the path towards his Lord."},"5495":{"id":5495,"surah":73,"ayah":20,"verse":"Indeed your Lord knows that you stand up in prayer, sometimes almost two-thirds of the night, and sometimes half the night or sometimes a third of it \u2013 and also a group of those along with you; Allah keeps measure of the night and day; He knows that you, O Muslims, will not be able to measure the night, so He has inclined towards you with mercy \u2013 therefore recite from the Qur\u2019an as much as is easy for you; He knows that soon some of you will fall ill, and some will travel in the land seeking the munificence of Allah, and some will be fighting in Allah\u2019s cause; therefore recite from the Qur\u2019an as much as is easy for you, and establish prayer and pay the obligatory charity, and lend an excellent loan to Allah; and whatever good you send ahead for yourselves, you will find it with Allah, better and having a great reward; and seek forgiveness from Allah; indeed Allah is Oft Forgiving, Most Merciful."},"5496":{"id":5496,"surah":74,"ayah":1,"verse":"O the Cloaked One! (Prophet Mohammed \u2013 peace and blessings be upon him)"},"5497":{"id":5497,"surah":74,"ayah":2,"verse":"Rise up and warn!"},"5498":{"id":5498,"surah":74,"ayah":3,"verse":"And proclaim the Purity of your Lord."},"5499":{"id":5499,"surah":74,"ayah":4,"verse":"And keep your clothes clean."},"5500":{"id":5500,"surah":74,"ayah":5,"verse":"And stay away from idols."},"5501":{"id":5501,"surah":74,"ayah":6,"verse":"And do not favour others in order to receive more."},"5502":{"id":5502,"surah":74,"ayah":7,"verse":"And for the sake of your Lord, patiently endure."},"5503":{"id":5503,"surah":74,"ayah":8,"verse":"So when the Trumpet will be blown."},"5504":{"id":5504,"surah":74,"ayah":9,"verse":"So that is a tough day."},"5505":{"id":5505,"surah":74,"ayah":10,"verse":"Not easy upon the disbelievers."},"5506":{"id":5506,"surah":74,"ayah":11,"verse":"Leave him to Me, the one whom I created single."},"5507":{"id":5507,"surah":74,"ayah":12,"verse":"And gave him vast wealth."},"5508":{"id":5508,"surah":74,"ayah":13,"verse":"And gave him sons present before him."},"5509":{"id":5509,"surah":74,"ayah":14,"verse":"And made several preparations for him."},"5510":{"id":5510,"surah":74,"ayah":15,"verse":"Yet he desires that I should give more."},"5511":{"id":5511,"surah":74,"ayah":16,"verse":"Never! For he is an enemy to Our signs!"},"5512":{"id":5512,"surah":74,"ayah":17,"verse":"Soon I shall mount him on Saood, the hill of fire."},"5513":{"id":5513,"surah":74,"ayah":18,"verse":"Indeed he thought, and inwardly decided."},"5514":{"id":5514,"surah":74,"ayah":19,"verse":"So accursed be he, how evilly did he decide!"},"5515":{"id":5515,"surah":74,"ayah":20,"verse":"Again accursed be he, how evilly did he decide!"},"5516":{"id":5516,"surah":74,"ayah":21,"verse":"He then dared to lift his gaze."},"5517":{"id":5517,"surah":74,"ayah":22,"verse":"Then frowned and grimaced."},"5518":{"id":5518,"surah":74,"ayah":23,"verse":"Then he turned away, and was haughty."},"5519":{"id":5519,"surah":74,"ayah":24,"verse":"And said, \u201cThis is nothing but magic learnt from earlier men.\u201d"},"5520":{"id":5520,"surah":74,"ayah":25,"verse":"\u201cThis is nothing but the speech of a man.\u201d"},"5521":{"id":5521,"surah":74,"ayah":26,"verse":"I will soon fling him into hell."},"5522":{"id":5522,"surah":74,"ayah":27,"verse":"And what have you understood, what hell is!"},"5523":{"id":5523,"surah":74,"ayah":28,"verse":"It neither leaves, nor spares."},"5524":{"id":5524,"surah":74,"ayah":29,"verse":"It strips away the hide of man."},"5525":{"id":5525,"surah":74,"ayah":30,"verse":"Above it are nineteen guards."},"5526":{"id":5526,"surah":74,"ayah":31,"verse":"We have not appointed the guards of hell, except angels; and did not keep this number except to test the disbelievers \u2013 in order that the People given the Book(s) may be convinced, and to increase the faith of the believers \u2013 and so that the People given the Book(s) and the Muslims may not have any doubt \u2013 and so that those in whose hearts is a disease and the disbelievers, may say, \u201cWhat does Allah mean by this amazing example?\u201d This is how Allah sends astray whomever He wills, and guides whomever He wills; and no one knows the armies of Allah except Him; and this is not but an advice to man."},"5527":{"id":5527,"surah":74,"ayah":32,"verse":"Yes, never!* By oath of the moon. (Hell will never spare the disbelievers)."},"5528":{"id":5528,"surah":74,"ayah":33,"verse":"And by oath of the night when it turns back."},"5529":{"id":5529,"surah":74,"ayah":34,"verse":"And by oath of the morning, when it spreads light."},"5530":{"id":5530,"surah":74,"ayah":35,"verse":"Indeed hell is one of the greatest entities."},"5531":{"id":5531,"surah":74,"ayah":36,"verse":"Warn the men."},"5532":{"id":5532,"surah":74,"ayah":37,"verse":"For the one among you who wishes to come forward or stay back."},"5533":{"id":5533,"surah":74,"ayah":38,"verse":"Every soul is mortgaged for its own deeds."},"5534":{"id":5534,"surah":74,"ayah":39,"verse":"Except those on the right side."},"5535":{"id":5535,"surah":74,"ayah":40,"verse":"In Gardens, they seek answers,"},"5536":{"id":5536,"surah":74,"ayah":41,"verse":"- From the guilty."},"5537":{"id":5537,"surah":74,"ayah":42,"verse":"\u201cWhat took you into the hell?\u201d"},"5538":{"id":5538,"surah":74,"ayah":43,"verse":"They said, \u201cWe never used to offer the prayer.\u201d"},"5539":{"id":5539,"surah":74,"ayah":44,"verse":"\u201cNor used to feed the needy.\u201d"},"5540":{"id":5540,"surah":74,"ayah":45,"verse":"\u201cAnd used to dwell on evil matters with those who think evilly.\u201d"},"5541":{"id":5541,"surah":74,"ayah":46,"verse":"\u201cAnd used to deny the Day of Justice.\u201d"},"5542":{"id":5542,"surah":74,"ayah":47,"verse":"\u201cTill death overcame us.\u201d"},"5543":{"id":5543,"surah":74,"ayah":48,"verse":"So the intercession of the intercessors will not benefit them. (The disbelievers will not have any intercessor.)"},"5544":{"id":5544,"surah":74,"ayah":49,"verse":"So what is the matter with them that they turn away from the advice?"},"5545":{"id":5545,"surah":74,"ayah":50,"verse":"As if they were startled donkeys \u2013"},"5546":{"id":5546,"surah":74,"ayah":51,"verse":"Fleeing away from a lion."},"5547":{"id":5547,"surah":74,"ayah":52,"verse":"Rather each one of them desires that he should be given open Books."},"5548":{"id":5548,"surah":74,"ayah":53,"verse":"Never! In fact they do not fear the Hereafter."},"5549":{"id":5549,"surah":74,"ayah":54,"verse":"Yes indeed, this is an advice."},"5550":{"id":5550,"surah":74,"ayah":55,"verse":"So whoever wills may heed advice from it."},"5551":{"id":5551,"surah":74,"ayah":56,"verse":"And what advice will they heed, except if Allah wills? Only He deserves to be feared, and His only is the greatness of forgiving."},"5552":{"id":5552,"surah":75,"ayah":1,"verse":"I swear by the Day of Resurrection."},"5553":{"id":5553,"surah":75,"ayah":2,"verse":"And by oath of the soul that reproaches itself."},"5554":{"id":5554,"surah":75,"ayah":3,"verse":"Does man assume that We will never assemble his bones?"},"5555":{"id":5555,"surah":75,"ayah":4,"verse":"Surely yes, why not? We can properly make all his phalanxes."},"5556":{"id":5556,"surah":75,"ayah":5,"verse":"In fact man wishes to commit evil in front of Him!"},"5557":{"id":5557,"surah":75,"ayah":6,"verse":"He asks, \u201cWhen will be the Day of Resurrection?\u201d"},"5558":{"id":5558,"surah":75,"ayah":7,"verse":"So when the eyes will be blinded by light."},"5559":{"id":5559,"surah":75,"ayah":8,"verse":"And the moon will be eclipsed."},"5560":{"id":5560,"surah":75,"ayah":9,"verse":"And the sun and the moon will be united."},"5561":{"id":5561,"surah":75,"ayah":10,"verse":"On that day man will cry out, \u201cWhere shall I flee?\u201d"},"5562":{"id":5562,"surah":75,"ayah":11,"verse":"Never! There is no refuge!"},"5563":{"id":5563,"surah":75,"ayah":12,"verse":"On that day, the station is only towards your Lord."},"5564":{"id":5564,"surah":75,"ayah":13,"verse":"On that day, man will be informed of all what he sent ahead and left behind."},"5565":{"id":5565,"surah":75,"ayah":14,"verse":"In fact, man himself is keeping an eye on his state of affairs!"},"5566":{"id":5566,"surah":75,"ayah":15,"verse":"And even if he presents all the excuses he has, none will be listened to."},"5567":{"id":5567,"surah":75,"ayah":16,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him), do not cause your tongue to move along with the Qur\u2019an in order to learn it faster."},"5568":{"id":5568,"surah":75,"ayah":17,"verse":"Indeed assembling the Qur\u2019an and reading it are upon Us."},"5569":{"id":5569,"surah":75,"ayah":18,"verse":"So when We have read it, you should thereupon follow what is read."},"5570":{"id":5570,"surah":75,"ayah":19,"verse":"Then indeed, to explain its details to you is upon Us."},"5571":{"id":5571,"surah":75,"ayah":20,"verse":"None except you, O disbelievers \u2013 you love what you have, the fleeting one."},"5572":{"id":5572,"surah":75,"ayah":21,"verse":"And you have forsaken the Hereafter."},"5573":{"id":5573,"surah":75,"ayah":22,"verse":"On that day, some faces will shine with freshness."},"5574":{"id":5574,"surah":75,"ayah":23,"verse":"Looking toward their Lord."},"5575":{"id":5575,"surah":75,"ayah":24,"verse":"And on that day some faces will be ghastly."},"5576":{"id":5576,"surah":75,"ayah":25,"verse":"Knowing that they will be subjected to a torment that breaks the backs."},"5577":{"id":5577,"surah":75,"ayah":26,"verse":"Yes indeed, when the soul will reach up to the throat."},"5578":{"id":5578,"surah":75,"ayah":27,"verse":"And they will say, \u201cIs there any one \u2013 any magician?\u201d"},"5579":{"id":5579,"surah":75,"ayah":28,"verse":"And he will realise that this is the parting."},"5580":{"id":5580,"surah":75,"ayah":29,"verse":"And one shin will curl up with the other shin."},"5581":{"id":5581,"surah":75,"ayah":30,"verse":"On that day, the herding will be only towards your Lord."},"5582":{"id":5582,"surah":75,"ayah":31,"verse":"Neither did he believe it to be true, nor did he offer the prayer."},"5583":{"id":5583,"surah":75,"ayah":32,"verse":"But he denied and turned away."},"5584":{"id":5584,"surah":75,"ayah":33,"verse":"Then he went back to his home in pride."},"5585":{"id":5585,"surah":75,"ayah":34,"verse":"Your ruin has come close, still closer."},"5586":{"id":5586,"surah":75,"ayah":35,"verse":"Again your ruin has come close, still closer."},"5587":{"id":5587,"surah":75,"ayah":36,"verse":"Does man assume that he will be let loose?"},"5588":{"id":5588,"surah":75,"ayah":37,"verse":"Was he not just a drop of the semen that is discharged?"},"5589":{"id":5589,"surah":75,"ayah":38,"verse":"He then became a clot \u2013 so Allah created him, then made him proper."},"5590":{"id":5590,"surah":75,"ayah":39,"verse":"So created from him a pair, the male and female."},"5591":{"id":5591,"surah":75,"ayah":40,"verse":"So will He, Who has done all this, not be able to revive the dead?"},"5592":{"id":5592,"surah":76,"ayah":1,"verse":"Indeed there has been a time for man, when even his name did not exist anywhere."},"5593":{"id":5593,"surah":76,"ayah":2,"verse":"Indeed We have created man from mixed semen; in order to test him \u2013 We therefore made him hearing, knowing."},"5594":{"id":5594,"surah":76,"ayah":3,"verse":"We have indeed shown him the way \u2013 whether he is grateful or ingrate."},"5595":{"id":5595,"surah":76,"ayah":4,"verse":"We have indeed kept prepared chains, and shackles and a blazing fire for the disbelievers."},"5596":{"id":5596,"surah":76,"ayah":5,"verse":"Indeed the virtuous will drink from a cup, containing a mixture of Kafoor."},"5597":{"id":5597,"surah":76,"ayah":6,"verse":"The Kafoor is a spring, from which the chosen bondmen of Allah will drink, causing it to flow wherever they wish inside their palaces."},"5598":{"id":5598,"surah":76,"ayah":7,"verse":"They fulfil their pledges, and fear a day the evil of which is widespread."},"5599":{"id":5599,"surah":76,"ayah":8,"verse":"And out of His love, they give food to the needy, the orphan and the prisoner."},"5600":{"id":5600,"surah":76,"ayah":9,"verse":"They say to them, \u201cWe give you food, only for the sake of Allah \u2013 we do not seek any reward or thanks from you.\u201d"},"5601":{"id":5601,"surah":76,"ayah":10,"verse":"\u201cIndeed we fear from our Lord a day which is extremely bitter, most severe.\u201d"},"5602":{"id":5602,"surah":76,"ayah":11,"verse":"So Allah saved them from the evil of that day, and gave them freshness and joy."},"5603":{"id":5603,"surah":76,"ayah":12,"verse":"And gave them Paradise and silk clothes, as a reward for their patience."},"5604":{"id":5604,"surah":76,"ayah":13,"verse":"Reclining in it, upon thrones; they will not see the hot sunshine in it, nor the bitter cold."},"5605":{"id":5605,"surah":76,"ayah":14,"verse":"And its shade will cover them, and its fruit clusters brought down low for them."},"5606":{"id":5606,"surah":76,"ayah":15,"verse":"Rounds of silver cups and silver beakers, looking like glass, will be presented upon them."},"5607":{"id":5607,"surah":76,"ayah":16,"verse":"Glass made from silver, which the servers have filled up to the measure."},"5608":{"id":5608,"surah":76,"ayah":17,"verse":"And in Paradise they will be given to drink cups, filled with a mixture of ginger."},"5609":{"id":5609,"surah":76,"ayah":18,"verse":"Which is a spring in Paradise called Salsabeel."},"5610":{"id":5610,"surah":76,"ayah":19,"verse":"Immortal youths shall surround them, waiting upon them; when you see them, you would think they are scattered pearls."},"5611":{"id":5611,"surah":76,"ayah":20,"verse":"And when you look towards it, you will see serenity and a great kingdom."},"5612":{"id":5612,"surah":76,"ayah":21,"verse":"In it they adorn clothes of fine green silk and gold embroidery; and they are given silver bracelets to wear; and their Lord gave them pure wine to drink."},"5613":{"id":5613,"surah":76,"ayah":22,"verse":"It will be said to them, \u201cThis is your reward \u2013 indeed your efforts have been appreciated.\u201d"},"5614":{"id":5614,"surah":76,"ayah":23,"verse":"Indeed We have sent down the Qur\u2019an upon you, in stages."},"5615":{"id":5615,"surah":76,"ayah":24,"verse":"Therefore stay patient upon your Lord\u2019s command, and do not listen to any of the sinners or ingrates among them."},"5616":{"id":5616,"surah":76,"ayah":25,"verse":"And remember the name of your Lord morning and evening."},"5617":{"id":5617,"surah":76,"ayah":26,"verse":"And prostrate for Him in a part of the night, and proclaim His purity into the long night."},"5618":{"id":5618,"surah":76,"ayah":27,"verse":"Indeed these people love what they have, the fleeting one, and have forsaken the immensely important day behind them."},"5619":{"id":5619,"surah":76,"ayah":28,"verse":"It is We Who created them, and strengthened their skeleton; and We can replace them with others like them, whenever We will."},"5620":{"id":5620,"surah":76,"ayah":29,"verse":"This is indeed an advice; so whoever wishes may take the path towards his Lord."},"5621":{"id":5621,"surah":76,"ayah":30,"verse":"And what will you wish, except if Allah wills; indeed Allah is All Knowing, Wise."},"5622":{"id":5622,"surah":76,"ayah":31,"verse":"He admits into His mercy, whomever He wills; and for the unjust He has kept prepared a painful punishment."},"5623":{"id":5623,"surah":77,"ayah":1,"verse":"By oath of those that are sent, one after the other. (The verses of the Holy Qur\u2019an or the angels or the winds)."},"5624":{"id":5624,"surah":77,"ayah":2,"verse":"Then by oath of those that push with a strong gust."},"5625":{"id":5625,"surah":77,"ayah":3,"verse":"Then by oath of those that lift and carry."},"5626":{"id":5626,"surah":77,"ayah":4,"verse":"Then by those that clearly differentiate the right and wrong."},"5627":{"id":5627,"surah":77,"ayah":5,"verse":"And then by those that instil Remembrance into the hearts."},"5628":{"id":5628,"surah":77,"ayah":6,"verse":"To complete the argument or to warn."},"5629":{"id":5629,"surah":77,"ayah":7,"verse":"Indeed what you are promised, will surely befall."},"5630":{"id":5630,"surah":77,"ayah":8,"verse":"So when the lights of the stars are put out."},"5631":{"id":5631,"surah":77,"ayah":9,"verse":"And when the sky is split apart."},"5632":{"id":5632,"surah":77,"ayah":10,"verse":"And when the mountains are made into dust and blown away."},"5633":{"id":5633,"surah":77,"ayah":11,"verse":"And when the time of the Noble Messengers arrives."},"5634":{"id":5634,"surah":77,"ayah":12,"verse":"For which day were they appointed?"},"5635":{"id":5635,"surah":77,"ayah":13,"verse":"For the Day of Decision."},"5636":{"id":5636,"surah":77,"ayah":14,"verse":"And what do you know, what the Day of Decision is!"},"5637":{"id":5637,"surah":77,"ayah":15,"verse":"Ruin is for the deniers on that day!"},"5638":{"id":5638,"surah":77,"ayah":16,"verse":"Did We not destroy the earlier people?"},"5639":{"id":5639,"surah":77,"ayah":17,"verse":"We shall then send the latter after them."},"5640":{"id":5640,"surah":77,"ayah":18,"verse":"This is how We deal with the guilty."},"5641":{"id":5641,"surah":77,"ayah":19,"verse":"Ruin is for the deniers on that day!"},"5642":{"id":5642,"surah":77,"ayah":20,"verse":"Did We not create you from an abject fluid?"},"5643":{"id":5643,"surah":77,"ayah":21,"verse":"We then kept it in a safe place."},"5644":{"id":5644,"surah":77,"ayah":22,"verse":"For a known calculated term."},"5645":{"id":5645,"surah":77,"ayah":23,"verse":"We then calculated; so how excellently do We control!"},"5646":{"id":5646,"surah":77,"ayah":24,"verse":"Ruin is for the deniers on that day!"},"5647":{"id":5647,"surah":77,"ayah":25,"verse":"Did We not make the earth a storehouse?"},"5648":{"id":5648,"surah":77,"ayah":26,"verse":"For the living and the dead among you?"},"5649":{"id":5649,"surah":77,"ayah":27,"verse":"And We placed high mountains as anchors in it and gave you sweet water to drink."},"5650":{"id":5650,"surah":77,"ayah":28,"verse":"Ruin is for the deniers on that day!"},"5651":{"id":5651,"surah":77,"ayah":29,"verse":"\u201cMove towards what you used to deny!\u201d"},"5652":{"id":5652,"surah":77,"ayah":30,"verse":"\u201cMove towards the shadow of the smoke having three branches.\u201d"},"5653":{"id":5653,"surah":77,"ayah":31,"verse":"\u201cWhich neither gives shade, nor saves from the flame.\u201d"},"5654":{"id":5654,"surah":77,"ayah":32,"verse":"Indeed hell throws up sparks like huge castles."},"5655":{"id":5655,"surah":77,"ayah":33,"verse":"Seeming like yellow camels."},"5656":{"id":5656,"surah":77,"ayah":34,"verse":"Ruin is for the deniers on that day!"},"5657":{"id":5657,"surah":77,"ayah":35,"verse":"This is a day in which they will not be able to speak."},"5658":{"id":5658,"surah":77,"ayah":36,"verse":"Nor will they be given permission to present excuses."},"5659":{"id":5659,"surah":77,"ayah":37,"verse":"Ruin is for the deniers on that day!"},"5660":{"id":5660,"surah":77,"ayah":38,"verse":"This is the Day of Decision; We have gathered you and all the earlier men."},"5661":{"id":5661,"surah":77,"ayah":39,"verse":"If you now have any conspiracy, carry it out on Me."},"5662":{"id":5662,"surah":77,"ayah":40,"verse":"Ruin is for the deniers on that day!"},"5663":{"id":5663,"surah":77,"ayah":41,"verse":"Indeed the pious are in shade and springs."},"5664":{"id":5664,"surah":77,"ayah":42,"verse":"And among fruits whichever they may desire."},"5665":{"id":5665,"surah":77,"ayah":43,"verse":"\u201cEat and drink with pleasure, the reward of your deeds.\u201d"},"5666":{"id":5666,"surah":77,"ayah":44,"verse":"This is how We reward the virtuous."},"5667":{"id":5667,"surah":77,"ayah":45,"verse":"Ruin is for the deniers on that day!"},"5668":{"id":5668,"surah":77,"ayah":46,"verse":"\u201cEat and enjoy for a while \u2013 indeed you are guilty.\u201d"},"5669":{"id":5669,"surah":77,"ayah":47,"verse":"Ruin is for the deniers on that day!"},"5670":{"id":5670,"surah":77,"ayah":48,"verse":"And when it is said to them, \u201cOffer the prayer\u201d \u2013 they do not!"},"5671":{"id":5671,"surah":77,"ayah":49,"verse":"Ruin is for the deniers on that day!"},"5672":{"id":5672,"surah":77,"ayah":50,"verse":"So after this, in what matter will they believe?"},"5673":{"id":5673,"surah":78,"ayah":1,"verse":"What are they questioning each other about?"},"5674":{"id":5674,"surah":78,"ayah":2,"verse":"About the great tidings!"},"5675":{"id":5675,"surah":78,"ayah":3,"verse":"Regarding which they hold different views."},"5676":{"id":5676,"surah":78,"ayah":4,"verse":"Surely yes, they will soon come to know!"},"5677":{"id":5677,"surah":78,"ayah":5,"verse":"Again surely yes, they will soon come to know!"},"5678":{"id":5678,"surah":78,"ayah":6,"verse":"Did We not make the earth a bed?"},"5679":{"id":5679,"surah":78,"ayah":7,"verse":"And the mountains as pegs?"},"5680":{"id":5680,"surah":78,"ayah":8,"verse":"And it is We who created you in pairs."},"5681":{"id":5681,"surah":78,"ayah":9,"verse":"And made your sleep a rest."},"5682":{"id":5682,"surah":78,"ayah":10,"verse":"And made the night a cover."},"5683":{"id":5683,"surah":78,"ayah":11,"verse":"And made the day for seeking livelihood."},"5684":{"id":5684,"surah":78,"ayah":12,"verse":"And built seven strong roofs above you."},"5685":{"id":5685,"surah":78,"ayah":13,"verse":"And have kept a very bright lamp in it."},"5686":{"id":5686,"surah":78,"ayah":14,"verse":"And then sent down hard rain from the water bearing clouds."},"5687":{"id":5687,"surah":78,"ayah":15,"verse":"In order to produce grain and plants with it."},"5688":{"id":5688,"surah":78,"ayah":16,"verse":"And dense gardens."},"5689":{"id":5689,"surah":78,"ayah":17,"verse":"Indeed the Day of Decision is a time fixed."},"5690":{"id":5690,"surah":78,"ayah":18,"verse":"The day when the Trumpet will be blown \u2013 you will therefore come forth in multitudes."},"5691":{"id":5691,"surah":78,"ayah":19,"verse":"And the heaven will be opened \u2013 it therefore becomes like gates."},"5692":{"id":5692,"surah":78,"ayah":20,"verse":"And the mountains will be moved \u2013 they will therefore become like mirages."},"5693":{"id":5693,"surah":78,"ayah":21,"verse":"Indeed hell is lying in ambush."},"5694":{"id":5694,"surah":78,"ayah":22,"verse":"The destination of the rebellious."},"5695":{"id":5695,"surah":78,"ayah":23,"verse":"They will remain in it for ages."},"5696":{"id":5696,"surah":78,"ayah":24,"verse":"They will neither taste anything cool in it, nor anything to drink."},"5697":{"id":5697,"surah":78,"ayah":25,"verse":"Except hot boiling water and hot pus discharged from the people of hell."},"5698":{"id":5698,"surah":78,"ayah":26,"verse":"The reward to each is according to what he is."},"5699":{"id":5699,"surah":78,"ayah":27,"verse":"Indeed they had no fear of the account."},"5700":{"id":5700,"surah":78,"ayah":28,"verse":"And they denied Our signs to the extreme."},"5701":{"id":5701,"surah":78,"ayah":29,"verse":"And We have kept recorded everything in a Book, accounted for."},"5702":{"id":5702,"surah":78,"ayah":30,"verse":"Therefore taste it now \u2013 We shall not increase anything for you except the punishment."},"5703":{"id":5703,"surah":78,"ayah":31,"verse":"Indeed the place of success is for the pious."},"5704":{"id":5704,"surah":78,"ayah":32,"verse":"Gardens and grapes."},"5705":{"id":5705,"surah":78,"ayah":33,"verse":"And maidens of a single age."},"5706":{"id":5706,"surah":78,"ayah":34,"verse":"And an overflowing cup."},"5707":{"id":5707,"surah":78,"ayah":35,"verse":"In which they shall neither hear lewd talk, nor any lie."},"5708":{"id":5708,"surah":78,"ayah":36,"verse":"A reward from your Lord \u2013 a grossly sufficient bestowal."},"5709":{"id":5709,"surah":78,"ayah":37,"verse":"Lord of the heavens and the earth, and all whatever is between them \u2013 the Most Gracious \u2013 with Whom no one will have the right to talk."},"5710":{"id":5710,"surah":78,"ayah":38,"verse":"The day when Jibreel and all the angels will stand in rows; none will be able to speak except one whom the Most Gracious commands and who spoke rightly. (Prophet Mohammed \u2013 peace and blessings be upon him \u2013 will be the first to be granted permission to intercede.)"},"5711":{"id":5711,"surah":78,"ayah":39,"verse":"That is the True Day; therefore whoever wills may take the path towards his Lord."},"5712":{"id":5712,"surah":78,"ayah":40,"verse":"We warn you of a punishment that has come near; a day on which man will see what his own hands have sent ahead, and the disbeliever will say, \u201cAlas \u2013 if only I were dust!\u201d"},"5713":{"id":5713,"surah":79,"ayah":1,"verse":"By oath of those who harshly pull out the soul. (Of the disbeliever)"},"5714":{"id":5714,"surah":79,"ayah":2,"verse":"And who softly release the soul. (Of the believer)"},"5715":{"id":5715,"surah":79,"ayah":3,"verse":"And who glide with ease."},"5716":{"id":5716,"surah":79,"ayah":4,"verse":"And who quickly present themselves."},"5717":{"id":5717,"surah":79,"ayah":5,"verse":"And who plan the implementation."},"5718":{"id":5718,"surah":79,"ayah":6,"verse":"On the day when the trembling one will tremble. (The disbelievers will certainly taste the punishment.)"},"5719":{"id":5719,"surah":79,"ayah":7,"verse":"And the following event will come after it."},"5720":{"id":5720,"surah":79,"ayah":8,"verse":"Many a heart will flutter on that day!"},"5721":{"id":5721,"surah":79,"ayah":9,"verse":"Unable to lift their gaze."},"5722":{"id":5722,"surah":79,"ayah":10,"verse":"The disbelievers say, \u201cWill we really return to our former state?\u201d"},"5723":{"id":5723,"surah":79,"ayah":11,"verse":"\u201cWhen we have become decayed bones?\u201d"},"5724":{"id":5724,"surah":79,"ayah":12,"verse":"They said, \u201cSo this return is an obvious loss!\u201d"},"5725":{"id":5725,"surah":79,"ayah":13,"verse":"So that is not but a single shout."},"5726":{"id":5726,"surah":79,"ayah":14,"verse":"So they will immediately be in an open plain."},"5727":{"id":5727,"surah":79,"ayah":15,"verse":"Did the news of Moosa reach you?"},"5728":{"id":5728,"surah":79,"ayah":16,"verse":"When his Lord called him in the holy valley of Tuwa."},"5729":{"id":5729,"surah":79,"ayah":17,"verse":"That \u201cGo to Firaun \u2013 he has rebelled.\u201d"},"5730":{"id":5730,"surah":79,"ayah":18,"verse":"\u201cTell him \u2018Do you have the inclination to become pure?\u2019"},"5731":{"id":5731,"surah":79,"ayah":19,"verse":"\u2018And I may guide you to your Lord, so that you may fear.\u2019\u201d"},"5732":{"id":5732,"surah":79,"ayah":20,"verse":"So Moosa showed him a magnificent sign."},"5733":{"id":5733,"surah":79,"ayah":21,"verse":"In response, he denied and disobeyed."},"5734":{"id":5734,"surah":79,"ayah":22,"verse":"He then turned away, striving in his effort."},"5735":{"id":5735,"surah":79,"ayah":23,"verse":"So he gathered the people, and proclaimed."},"5736":{"id":5736,"surah":79,"ayah":24,"verse":"And then said, \u201cI am your most supreme lord.\u201d"},"5737":{"id":5737,"surah":79,"ayah":25,"verse":"Allah therefore seized him, in the punishment of this world and the Hereafter."},"5738":{"id":5738,"surah":79,"ayah":26,"verse":"Indeed in this is a lesson for one who fears."},"5739":{"id":5739,"surah":79,"ayah":27,"verse":"Do you think that it is harder to create you or the heavens? Allah has created it."},"5740":{"id":5740,"surah":79,"ayah":28,"verse":"He raised its roof and made it proper."},"5741":{"id":5741,"surah":79,"ayah":29,"verse":"And He made its night dark, and started its light."},"5742":{"id":5742,"surah":79,"ayah":30,"verse":"And after it spread out the earth."},"5743":{"id":5743,"surah":79,"ayah":31,"verse":"And from it produced its water and its pasture."},"5744":{"id":5744,"surah":79,"ayah":32,"verse":"And solidified the mountains."},"5745":{"id":5745,"surah":79,"ayah":33,"verse":"In order to benefit you and your cattle."},"5746":{"id":5746,"surah":79,"ayah":34,"verse":"So when the greatest universal disaster arrives,"},"5747":{"id":5747,"surah":79,"ayah":35,"verse":"On that day man will recall all what he strove for."},"5748":{"id":5748,"surah":79,"ayah":36,"verse":"And hell will be made visible to all those who can see."},"5749":{"id":5749,"surah":79,"ayah":37,"verse":"So for one who rebelled,"},"5750":{"id":5750,"surah":79,"ayah":38,"verse":"And preferred the worldly life,"},"5751":{"id":5751,"surah":79,"ayah":39,"verse":"Then indeed hell only is his destination."},"5752":{"id":5752,"surah":79,"ayah":40,"verse":"And for one who feared to stand before his Lord and restrained his soul from desire,"},"5753":{"id":5753,"surah":79,"ayah":41,"verse":"Then indeed Paradise only is his destination."},"5754":{"id":5754,"surah":79,"ayah":42,"verse":"They (the disbelievers) ask you regarding the Last Day, as to when is its appointed time."},"5755":{"id":5755,"surah":79,"ayah":43,"verse":"What concern do you have regarding its explanation? (You are not bound to tell them)"},"5756":{"id":5756,"surah":79,"ayah":44,"verse":"Towards your Lord only is its conclusion."},"5757":{"id":5757,"surah":79,"ayah":45,"verse":"You are but a Herald of Warning, for one who fears it."},"5758":{"id":5758,"surah":79,"ayah":46,"verse":"The day when they will see it, it will seem as if they had not stayed on earth except for an evening or its morning."},"5759":{"id":5759,"surah":80,"ayah":1,"verse":"He frowned and turned away."},"5760":{"id":5760,"surah":80,"ayah":2,"verse":"Because the blind man had come in his august presence."},"5761":{"id":5761,"surah":80,"ayah":3,"verse":"And what do you know, he may be of the pure!"},"5762":{"id":5762,"surah":80,"ayah":4,"verse":"Or that he may accept advice, so the advice may benefit him."},"5763":{"id":5763,"surah":80,"ayah":5,"verse":"For him who does not care,"},"5764":{"id":5764,"surah":80,"ayah":6,"verse":"So you are after him!"},"5765":{"id":5765,"surah":80,"ayah":7,"verse":"And you have nothing to lose if he does not become pure."},"5766":{"id":5766,"surah":80,"ayah":8,"verse":"And for him who came to you striving,"},"5767":{"id":5767,"surah":80,"ayah":9,"verse":"And whereas he fears,"},"5768":{"id":5768,"surah":80,"ayah":10,"verse":"So you leave him, and are engrossed elsewhere!"},"5769":{"id":5769,"surah":80,"ayah":11,"verse":"Not this way \u2013 this is the advice."},"5770":{"id":5770,"surah":80,"ayah":12,"verse":"So whoever wishes may remember it."},"5771":{"id":5771,"surah":80,"ayah":13,"verse":"On honourable pages."},"5772":{"id":5772,"surah":80,"ayah":14,"verse":"Exalted, pure."},"5773":{"id":5773,"surah":80,"ayah":15,"verse":"Written by the hands of emissaries."},"5774":{"id":5774,"surah":80,"ayah":16,"verse":"Who are noble, virtuous."},"5775":{"id":5775,"surah":80,"ayah":17,"verse":"May man be slain \u2013 how ungrateful he is!"},"5776":{"id":5776,"surah":80,"ayah":18,"verse":"From what did He create him?"},"5777":{"id":5777,"surah":80,"ayah":19,"verse":"From a drop of liquid; He created him and then set several measures for him."},"5778":{"id":5778,"surah":80,"ayah":20,"verse":"Then eased the way for him."},"5779":{"id":5779,"surah":80,"ayah":21,"verse":"Then gave him death, so had him put in the grave."},"5780":{"id":5780,"surah":80,"ayah":22,"verse":"Then, when He willed, He brought him out. (As during the night of Holy Prophet\u2019s ascension, when all the Prophets gathered behind him in the Al Aqsa mosque in Jerusalem. Or when Allah will raise everyone on the Day of Resurrection.)"},"5781":{"id":5781,"surah":80,"ayah":23,"verse":"Not one \u2013 he has not yet completed what he was commanded."},"5782":{"id":5782,"surah":80,"ayah":24,"verse":"So man must look at his food."},"5783":{"id":5783,"surah":80,"ayah":25,"verse":"That We watered it in abundance."},"5784":{"id":5784,"surah":80,"ayah":26,"verse":"Then We split the earth properly."},"5785":{"id":5785,"surah":80,"ayah":27,"verse":"Thereby produced grain in it."},"5786":{"id":5786,"surah":80,"ayah":28,"verse":"And grapes and fodder,"},"5787":{"id":5787,"surah":80,"ayah":29,"verse":"And olives and date palms,"},"5788":{"id":5788,"surah":80,"ayah":30,"verse":"And dense gardens,"},"5789":{"id":5789,"surah":80,"ayah":31,"verse":"And fruits and grass,"},"5790":{"id":5790,"surah":80,"ayah":32,"verse":"In order to benefit you and your cattle."},"5791":{"id":5791,"surah":80,"ayah":33,"verse":"So when the deafening Shout arrives,"},"5792":{"id":5792,"surah":80,"ayah":34,"verse":"On that day man will run away from his brother."},"5793":{"id":5793,"surah":80,"ayah":35,"verse":"And from his mother and father,"},"5794":{"id":5794,"surah":80,"ayah":36,"verse":"And from his wife and sons."},"5795":{"id":5795,"surah":80,"ayah":37,"verse":"On that day, each one has just one issue, which is enough for him."},"5796":{"id":5796,"surah":80,"ayah":38,"verse":"Many a face will be glittering on that day."},"5797":{"id":5797,"surah":80,"ayah":39,"verse":"Laughing, rejoicing."},"5798":{"id":5798,"surah":80,"ayah":40,"verse":"And many a face will be covered with dust on that day."},"5799":{"id":5799,"surah":80,"ayah":41,"verse":"Blackness overcoming them."},"5800":{"id":5800,"surah":80,"ayah":42,"verse":"It is they, the disbelievers, the sinners."},"5801":{"id":5801,"surah":81,"ayah":1,"verse":"When the sunlight is rolled up."},"5802":{"id":5802,"surah":81,"ayah":2,"verse":"And when the stars fall down."},"5803":{"id":5803,"surah":81,"ayah":3,"verse":"And when the mountains are driven."},"5804":{"id":5804,"surah":81,"ayah":4,"verse":"And when milch camels roam abandoned."},"5805":{"id":5805,"surah":81,"ayah":5,"verse":"And when wild animals are herded together."},"5806":{"id":5806,"surah":81,"ayah":6,"verse":"And when the seas are set afire."},"5807":{"id":5807,"surah":81,"ayah":7,"verse":"And when the souls are paired."},"5808":{"id":5808,"surah":81,"ayah":8,"verse":"And when the girl-child who was buried alive is asked."},"5809":{"id":5809,"surah":81,"ayah":9,"verse":"Upon what sin was she killed for."},"5810":{"id":5810,"surah":81,"ayah":10,"verse":"And when the Records of Account are laid bare."},"5811":{"id":5811,"surah":81,"ayah":11,"verse":"And when the heaven is torn away."},"5812":{"id":5812,"surah":81,"ayah":12,"verse":"And when hell is further enflamed."},"5813":{"id":5813,"surah":81,"ayah":13,"verse":"And when Paradise is brought near."},"5814":{"id":5814,"surah":81,"ayah":14,"verse":"Every soul will then come know what it has brought."},"5815":{"id":5815,"surah":81,"ayah":15,"verse":"So by oath of those that revolve. (The planets.)"},"5816":{"id":5816,"surah":81,"ayah":16,"verse":"Who move straight and stop."},"5817":{"id":5817,"surah":81,"ayah":17,"verse":"And by oath of the night when it turns back."},"5818":{"id":5818,"surah":81,"ayah":18,"verse":"And by oath of the morning when it takes birth."},"5819":{"id":5819,"surah":81,"ayah":19,"verse":"This is indeed the recitation of an honoured Noble Messenger. (Angel Jibreel \u2013 peace and blessings be upon him.)"},"5820":{"id":5820,"surah":81,"ayah":20,"verse":"The mighty, the honoured in the presence of the Lord of the Throne."},"5821":{"id":5821,"surah":81,"ayah":21,"verse":"The one who is obeyed, and trustworthy. (Other angels obey angel Jibreel)."},"5822":{"id":5822,"surah":81,"ayah":22,"verse":"And your companion is not insane."},"5823":{"id":5823,"surah":81,"ayah":23,"verse":"And indeed he saw him on the clear horizon. (Prophet Mohammed saw Angel Jibreel in his true shape \u2013 peace and blessings be upon them)."},"5824":{"id":5824,"surah":81,"ayah":24,"verse":"And this Prophet is not miserly upon the hidden. (Allah gave the knowledge of the hidden to the Holy Prophet \u2013 peace and blessings be upon him.)"},"5825":{"id":5825,"surah":81,"ayah":25,"verse":"And the Qur\u2019an is not the recitation of Satan the outcast."},"5826":{"id":5826,"surah":81,"ayah":26,"verse":"So where are you going?"},"5827":{"id":5827,"surah":81,"ayah":27,"verse":"That is but an advice to the entire creation!"},"5828":{"id":5828,"surah":81,"ayah":28,"verse":"For the one among you who wishes to become upright."},"5829":{"id":5829,"surah":81,"ayah":29,"verse":"And what will you wish, except if Allah wills \u2013 the Lord of the Creation."},"5830":{"id":5830,"surah":82,"ayah":1,"verse":"When the heaven splits open."},"5831":{"id":5831,"surah":82,"ayah":2,"verse":"And when the stars fall down."},"5832":{"id":5832,"surah":82,"ayah":3,"verse":"And when the oceans are swept away."},"5833":{"id":5833,"surah":82,"ayah":4,"verse":"And when the graves are overturned."},"5834":{"id":5834,"surah":82,"ayah":5,"verse":"Every soul will come to know what it has sent ahead and what it left behind."},"5835":{"id":5835,"surah":82,"ayah":6,"verse":"O man! What has deceived you away from your Lord, the Most Beneficent?"},"5836":{"id":5836,"surah":82,"ayah":7,"verse":"The One Who created you, then moulded you, then made you proper?"},"5837":{"id":5837,"surah":82,"ayah":8,"verse":"He moulded you into whatever shape He willed."},"5838":{"id":5838,"surah":82,"ayah":9,"verse":"Not at all \u2013 but rather you deny the establishment of Justice."},"5839":{"id":5839,"surah":82,"ayah":10,"verse":"And indeed there are some guardians over you."},"5840":{"id":5840,"surah":82,"ayah":11,"verse":"The honourable recorders."},"5841":{"id":5841,"surah":82,"ayah":12,"verse":"Knowing all what you may do."},"5842":{"id":5842,"surah":82,"ayah":13,"verse":"Indeed the virtuous are in serenity."},"5843":{"id":5843,"surah":82,"ayah":14,"verse":"And indeed the sinners are in hell."},"5844":{"id":5844,"surah":82,"ayah":15,"verse":"They will enter it on the Day of Justice."},"5845":{"id":5845,"surah":82,"ayah":16,"verse":"And will not be able to hide from it."},"5846":{"id":5846,"surah":82,"ayah":17,"verse":"And what do you know \u2013 of what sort is the Day of Justice!"},"5847":{"id":5847,"surah":82,"ayah":18,"verse":"Again, what do you know \u2013 of what sort is the Day of Justice!"},"5848":{"id":5848,"surah":82,"ayah":19,"verse":"The day on which no soul will have the authority over any other soul; and on that day, the entire command belongs to Allah."},"5849":{"id":5849,"surah":83,"ayah":1,"verse":"Ruin is for the defrauders. (Those who measure less.)"},"5850":{"id":5850,"surah":83,"ayah":2,"verse":"Those who when they take the measure from others, take it in full!"},"5851":{"id":5851,"surah":83,"ayah":3,"verse":"Whereas when they give others after measuring or weighing, they give them less!"},"5852":{"id":5852,"surah":83,"ayah":4,"verse":"What! Do they not expect that they will be raised?"},"5853":{"id":5853,"surah":83,"ayah":5,"verse":"(To be raised) for a Great Day?"},"5854":{"id":5854,"surah":83,"ayah":6,"verse":"The day when everyone will stand before the Lord Of The Creation."},"5855":{"id":5855,"surah":83,"ayah":7,"verse":"Indeed, the record of the disbelievers is in the lowest place, the Sijjeen."},"5856":{"id":5856,"surah":83,"ayah":8,"verse":"And what do you know, how (wretched) the Sijjeen is!"},"5857":{"id":5857,"surah":83,"ayah":9,"verse":"The record is a sealed text."},"5858":{"id":5858,"surah":83,"ayah":10,"verse":"Ruin is for the deniers on that day!"},"5859":{"id":5859,"surah":83,"ayah":11,"verse":"Those who deny the Day of Justice."},"5860":{"id":5860,"surah":83,"ayah":12,"verse":"And none will deny it, except every transgressor, rebel."},"5861":{"id":5861,"surah":83,"ayah":13,"verse":"When you recite Our verses to him, he says, \u201cStories of earlier men!\u201d"},"5862":{"id":5862,"surah":83,"ayah":14,"verse":"Not at all \u2013 but rather their earnings have heaped rust upon their hearts."},"5863":{"id":5863,"surah":83,"ayah":15,"verse":"Yes indeed \u2013 they will be deprived of seeing their Lord on that day."},"5864":{"id":5864,"surah":83,"ayah":16,"verse":"Then indeed they have to enter hell."},"5865":{"id":5865,"surah":83,"ayah":17,"verse":"It will then be said, \u201cThis is what you used to deny.\u201d"},"5866":{"id":5866,"surah":83,"ayah":18,"verse":"Indeed the record of the virtuous is in the highest place, the Illiyin."},"5867":{"id":5867,"surah":83,"ayah":19,"verse":"And what do you know what the Illiyoon are!"},"5868":{"id":5868,"surah":83,"ayah":20,"verse":"The record is a sealed text."},"5869":{"id":5869,"surah":83,"ayah":21,"verse":"Which is witnessed by the close ones."},"5870":{"id":5870,"surah":83,"ayah":22,"verse":"Indeed the virtuous are in serenity."},"5871":{"id":5871,"surah":83,"ayah":23,"verse":"On thrones, watching."},"5872":{"id":5872,"surah":83,"ayah":24,"verse":"You will recognise the freshness of serenity on their faces."},"5873":{"id":5873,"surah":83,"ayah":25,"verse":"They will be given pure wine to drink, which is kept preserved, sealed."},"5874":{"id":5874,"surah":83,"ayah":26,"verse":"Its seal is upon musk; and for this should those who crave be eager."},"5875":{"id":5875,"surah":83,"ayah":27,"verse":"And it is mixed with Tasneem."},"5876":{"id":5876,"surah":83,"ayah":28,"verse":"The spring from which drink the ones close to Allah."},"5877":{"id":5877,"surah":83,"ayah":29,"verse":"Indeed the guilty used to laugh at the believers."},"5878":{"id":5878,"surah":83,"ayah":30,"verse":"And when the believers used to pass by, they used to gesture at each other with their eyes."},"5879":{"id":5879,"surah":83,"ayah":31,"verse":"And whilst returning to their homes, they used to return rejoicing."},"5880":{"id":5880,"surah":83,"ayah":32,"verse":"And upon seeing the Muslims, they used to say, \u201cIndeed they have gone astray.\u201d"},"5881":{"id":5881,"surah":83,"ayah":33,"verse":"Whereas they have not at all been sent as guardians over them."},"5882":{"id":5882,"surah":83,"ayah":34,"verse":"So this day it is the believers who laugh at the disbelievers."},"5883":{"id":5883,"surah":83,"ayah":35,"verse":"On high thrones, watching."},"5884":{"id":5884,"surah":83,"ayah":36,"verse":"Did not the disbelievers get repaid for what they used to do?"},"5885":{"id":5885,"surah":84,"ayah":1,"verse":"When the heaven breaks apart."},"5886":{"id":5886,"surah":84,"ayah":2,"verse":"And it listens to the command of its Lord \u2013 and that befits it."},"5887":{"id":5887,"surah":84,"ayah":3,"verse":"And when the earth is spread out."},"5888":{"id":5888,"surah":84,"ayah":4,"verse":"And it unburdens itself of all that is in it, and becomes empty."},"5889":{"id":5889,"surah":84,"ayah":5,"verse":"And it listens to the command of its Lord \u2013 and that befits it."},"5890":{"id":5890,"surah":84,"ayah":6,"verse":"O man, indeed you have to surely run towards your Lord, and to meet him."},"5891":{"id":5891,"surah":84,"ayah":7,"verse":"So whoever is given his record of deeds in his right hand \u2013"},"5892":{"id":5892,"surah":84,"ayah":8,"verse":"Soon an easy account will be taken from him."},"5893":{"id":5893,"surah":84,"ayah":9,"verse":"And he will return to his family rejoicing."},"5894":{"id":5894,"surah":84,"ayah":10,"verse":"And whoever is given his record of deeds behind his back \u2013"},"5895":{"id":5895,"surah":84,"ayah":11,"verse":"Soon he will pray for death."},"5896":{"id":5896,"surah":84,"ayah":12,"verse":"And will go into the blazing fire."},"5897":{"id":5897,"surah":84,"ayah":13,"verse":"Indeed he used to rejoice in his home."},"5898":{"id":5898,"surah":84,"ayah":14,"verse":"He assumed that he does not have to return."},"5899":{"id":5899,"surah":84,"ayah":15,"verse":"Surely yes, why not? Indeed his Lord is seeing him."},"5900":{"id":5900,"surah":84,"ayah":16,"verse":"So by oath of the late evening\u2019s light."},"5901":{"id":5901,"surah":84,"ayah":17,"verse":"And by oath of the night and all that gathers in it."},"5902":{"id":5902,"surah":84,"ayah":18,"verse":"And by oath of the moon when it is full."},"5903":{"id":5903,"surah":84,"ayah":19,"verse":"You will surely go up level by level."},"5904":{"id":5904,"surah":84,"ayah":20,"verse":"What is the matter with them that they do not accept faith?"},"5905":{"id":5905,"surah":84,"ayah":21,"verse":"And when the Qur\u2019an is recited to them, they do not fall prostrate? (Command of Prostration # 13)"},"5906":{"id":5906,"surah":84,"ayah":22,"verse":"In fact the disbelievers keep denying."},"5907":{"id":5907,"surah":84,"ayah":23,"verse":"And Allah well knows what they conceal in their hearts."},"5908":{"id":5908,"surah":84,"ayah":24,"verse":"Therefore give them the glad tidings of a painful punishment."},"5909":{"id":5909,"surah":84,"ayah":25,"verse":"Except those who believed and did good deeds \u2013 for them is a reward that will never end."},"5910":{"id":5910,"surah":85,"ayah":1,"verse":"By oath of the heaven which contains the constellations."},"5911":{"id":5911,"surah":85,"ayah":2,"verse":"And by oath of the Promised Day."},"5912":{"id":5912,"surah":85,"ayah":3,"verse":"And by oath of the day that is a witness and by oath of a day in which the people present themselves."},"5913":{"id":5913,"surah":85,"ayah":4,"verse":"Accursed be the People of the Ditch!"},"5914":{"id":5914,"surah":85,"ayah":5,"verse":"The people of the fuelled blazing fire."},"5915":{"id":5915,"surah":85,"ayah":6,"verse":"When they were sitting at its edge."},"5916":{"id":5916,"surah":85,"ayah":7,"verse":"And they themselves are witnesses to what they were doing to the Muslims!"},"5917":{"id":5917,"surah":85,"ayah":8,"verse":"And what did they dislike from the Muslims, except that the Muslims accepted faith in Allah the Most Honourable, the Most Praiseworthy?"},"5918":{"id":5918,"surah":85,"ayah":9,"verse":"To Him only belongs the kingship of the heavens and the earth; and Allah is a Witness over all things."},"5919":{"id":5919,"surah":85,"ayah":10,"verse":"Indeed those who troubled the Muslim men and Muslim women, and then did not repent \u2013 for them is the punishment of hell, and for them is the punishment of fire."},"5920":{"id":5920,"surah":85,"ayah":11,"verse":"Indeed those who believed and did good deeds \u2013 for them are Gardens beneath which rivers flow; this is the great success."},"5921":{"id":5921,"surah":85,"ayah":12,"verse":"Indeed the seizure of your Lord is very severe."},"5922":{"id":5922,"surah":85,"ayah":13,"verse":"Indeed it is He Who initiates and redoes."},"5923":{"id":5923,"surah":85,"ayah":14,"verse":"And He only is the Oft Forgiving, the Beloved of His bondmen."},"5924":{"id":5924,"surah":85,"ayah":15,"verse":"Master of the Honourable Throne."},"5925":{"id":5925,"surah":85,"ayah":16,"verse":"Always doing whatever He wills."},"5926":{"id":5926,"surah":85,"ayah":17,"verse":"Did the story of the armies reach you?"},"5927":{"id":5927,"surah":85,"ayah":18,"verse":"(The armies of) Firaun and the tribe of Thamud."},"5928":{"id":5928,"surah":85,"ayah":19,"verse":"In fact the disbelievers are in denial."},"5929":{"id":5929,"surah":85,"ayah":20,"verse":"And Allah is after them, has them surrounded."},"5930":{"id":5930,"surah":85,"ayah":21,"verse":"In fact it (what they deny) is the Noble Qur\u2019an."},"5931":{"id":5931,"surah":85,"ayah":22,"verse":"In the Preserved Tablet."},"5932":{"id":5932,"surah":86,"ayah":1,"verse":"By oath of the heaven, and by oath of the nightly arriver."},"5933":{"id":5933,"surah":86,"ayah":2,"verse":"And have you understood what the nightly arriver is?"},"5934":{"id":5934,"surah":86,"ayah":3,"verse":"The very brightly shining star!"},"5935":{"id":5935,"surah":86,"ayah":4,"verse":"There is not a soul that does not have a guardian over it."},"5936":{"id":5936,"surah":86,"ayah":5,"verse":"So man must consider from what he has been created."},"5937":{"id":5937,"surah":86,"ayah":6,"verse":"Created from a gushing fluid."},"5938":{"id":5938,"surah":86,"ayah":7,"verse":"That is issued from between the backs and the ribs."},"5939":{"id":5939,"surah":86,"ayah":8,"verse":"Indeed Allah is Able to return him."},"5940":{"id":5940,"surah":86,"ayah":9,"verse":"A day when the secrets will be examined."},"5941":{"id":5941,"surah":86,"ayah":10,"verse":"So man will neither have any strength nor any aide."},"5942":{"id":5942,"surah":86,"ayah":11,"verse":"By oath of the sky from which comes down the rain."},"5943":{"id":5943,"surah":86,"ayah":12,"verse":"And by oath of the earth which flourishes with it."},"5944":{"id":5944,"surah":86,"ayah":13,"verse":"Indeed the Qur\u2019an is a decisive Word."},"5945":{"id":5945,"surah":86,"ayah":14,"verse":"And is not a matter of amusement."},"5946":{"id":5946,"surah":86,"ayah":15,"verse":"Indeed the disbelievers carry out their evil schemes."},"5947":{"id":5947,"surah":86,"ayah":16,"verse":"And I secretly plan."},"5948":{"id":5948,"surah":86,"ayah":17,"verse":"Therefore give them some respite \u2013 give them some time."},"5949":{"id":5949,"surah":87,"ayah":1,"verse":"Proclaim the Purity of your Lord, the Supreme."},"5950":{"id":5950,"surah":87,"ayah":2,"verse":"The One Who created, and then made proper."},"5951":{"id":5951,"surah":87,"ayah":3,"verse":"And the One Who kept proper measure and then guided."},"5952":{"id":5952,"surah":87,"ayah":4,"verse":"The One Who produced pasture."},"5953":{"id":5953,"surah":87,"ayah":5,"verse":"Then made it dry and dark."},"5954":{"id":5954,"surah":87,"ayah":6,"verse":"We shall now make you read (O dear Prophet Mohammed \u2013 peace and blessings be upon him), so you will not forget."},"5955":{"id":5955,"surah":87,"ayah":7,"verse":"Except what Allah wills; indeed He knows all the evident and all the concealed."},"5956":{"id":5956,"surah":87,"ayah":8,"verse":"And We shall create the means of ease for you."},"5957":{"id":5957,"surah":87,"ayah":9,"verse":"Therefore advise, if advising is beneficial."},"5958":{"id":5958,"surah":87,"ayah":10,"verse":"Soon whoever fears will heed advice."},"5959":{"id":5959,"surah":87,"ayah":11,"verse":"And the most wicked will stay away from it."},"5960":{"id":5960,"surah":87,"ayah":12,"verse":"The one who will enter the biggest fire."},"5961":{"id":5961,"surah":87,"ayah":13,"verse":"Then he neither dies in it, nor lives."},"5962":{"id":5962,"surah":87,"ayah":14,"verse":"Indeed successful is the one who became pure."},"5963":{"id":5963,"surah":87,"ayah":15,"verse":"And who mentioned the name of his Lord, then offered prayer."},"5964":{"id":5964,"surah":87,"ayah":16,"verse":"But rather you prefer the life of this world!"},"5965":{"id":5965,"surah":87,"ayah":17,"verse":"Whereas the Hereafter is better and everlasting."},"5966":{"id":5966,"surah":87,"ayah":18,"verse":"Indeed this is in the former scriptures."},"5967":{"id":5967,"surah":87,"ayah":19,"verse":"In the Books of Ibrahim and Moosa."},"5968":{"id":5968,"surah":88,"ayah":1,"verse":"Certainly the news of the calamity that will overwhelm has come to you!"},"5969":{"id":5969,"surah":88,"ayah":2,"verse":"Many a face will be disgraced on that day."},"5970":{"id":5970,"surah":88,"ayah":3,"verse":"Labouring, striving hard."},"5971":{"id":5971,"surah":88,"ayah":4,"verse":"(Yet) Going into the blazing fire."},"5972":{"id":5972,"surah":88,"ayah":5,"verse":"Made to drink water from the boiling hot spring."},"5973":{"id":5973,"surah":88,"ayah":6,"verse":"There is no food for them except thorns of fire."},"5974":{"id":5974,"surah":88,"ayah":7,"verse":"Which neither give strength nor satisfy the hunger."},"5975":{"id":5975,"surah":88,"ayah":8,"verse":"Many a face will be in serenity on that day."},"5976":{"id":5976,"surah":88,"ayah":9,"verse":"Rejoicing over their efforts."},"5977":{"id":5977,"surah":88,"ayah":10,"verse":"In a high Garden."},"5978":{"id":5978,"surah":88,"ayah":11,"verse":"In which they will not hear any lewd speech."},"5979":{"id":5979,"surah":88,"ayah":12,"verse":"In it is a flowing spring."},"5980":{"id":5980,"surah":88,"ayah":13,"verse":"In which are high thrones."},"5981":{"id":5981,"surah":88,"ayah":14,"verse":"And chosen goblets."},"5982":{"id":5982,"surah":88,"ayah":15,"verse":"And arranged carpets."},"5983":{"id":5983,"surah":88,"ayah":16,"verse":"And linen spread out."},"5984":{"id":5984,"surah":88,"ayah":17,"verse":"So do they not see the camel \u2013 how it has been created?"},"5985":{"id":5985,"surah":88,"ayah":18,"verse":"And the heaven \u2013 how it has been raised?"},"5986":{"id":5986,"surah":88,"ayah":19,"verse":"And the mountains \u2013 how they have been established?"},"5987":{"id":5987,"surah":88,"ayah":20,"verse":"And the earth \u2013 how it has been spread out?"},"5988":{"id":5988,"surah":88,"ayah":21,"verse":"Therefore advise; indeed you are a proclaimer of advice. (The Holy Prophet is a Remembrance from Allah.)"},"5989":{"id":5989,"surah":88,"ayah":22,"verse":"You are not at all a guardian over them."},"5990":{"id":5990,"surah":88,"ayah":23,"verse":"Except whoever turns away and disbelieves \u2013"},"5991":{"id":5991,"surah":88,"ayah":24,"verse":"So Allah will mete out to him the greatest punishment."},"5992":{"id":5992,"surah":88,"ayah":25,"verse":"Indeed only towards Us is their return"},"5993":{"id":5993,"surah":88,"ayah":26,"verse":"Then indeed only upon Us is their reckoning."},"5994":{"id":5994,"surah":89,"ayah":1,"verse":"By oath of the (particular) dawn."},"5995":{"id":5995,"surah":89,"ayah":2,"verse":"And by oath of ten nights."},"5996":{"id":5996,"surah":89,"ayah":3,"verse":"And by oath of the even and the odd."},"5997":{"id":5997,"surah":89,"ayah":4,"verse":"And by oath of the night when it recedes"},"5998":{"id":5998,"surah":89,"ayah":5,"verse":"Why is there an oath in this, for the intelligent?"},"5999":{"id":5999,"surah":89,"ayah":6,"verse":"Did you not see how did your Lord deal with (the tribe of) Aad?"},"6000":{"id":6000,"surah":89,"ayah":7,"verse":"(And) the tall giants of Iram?"},"6001":{"id":6001,"surah":89,"ayah":8,"verse":"Like whom no one else was born in the cities."},"6002":{"id":6002,"surah":89,"ayah":9,"verse":"And the tribe of Thamud, who hewed rocks in the valley."},"6003":{"id":6003,"surah":89,"ayah":10,"verse":"And with Firaun, who used to crucify."},"6004":{"id":6004,"surah":89,"ayah":11,"verse":"Those who rebelled in the cities,"},"6005":{"id":6005,"surah":89,"ayah":12,"verse":"And who then spread a lot of turmoil in them."},"6006":{"id":6006,"surah":89,"ayah":13,"verse":"Therefore your Lord struck them hard with the means of punishment."},"6007":{"id":6007,"surah":89,"ayah":14,"verse":"Indeed nothing is hidden from the sight of your Lord."},"6008":{"id":6008,"surah":89,"ayah":15,"verse":"So man, whenever his Lord tests him by giving him honour and favours \u2013 thereupon he says, \u201cMy Lord has honoured me.\u201d"},"6009":{"id":6009,"surah":89,"ayah":16,"verse":"And if He tests him and restricts his livelihood \u2013 thereupon he says, \u201cMy Lord has degraded me!\u201d"},"6010":{"id":6010,"surah":89,"ayah":17,"verse":"Not at all \u2013 but rather you do not honour the orphan."},"6011":{"id":6011,"surah":89,"ayah":18,"verse":"And do not urge one another to feed the needy."},"6012":{"id":6012,"surah":89,"ayah":19,"verse":"And you readily devour the inheritance with greed."},"6013":{"id":6013,"surah":89,"ayah":20,"verse":"And you harbour intense love for wealth."},"6014":{"id":6014,"surah":89,"ayah":21,"verse":"Most certainly \u2013 when the earth is smashed and blown to bits."},"6015":{"id":6015,"surah":89,"ayah":22,"verse":"And when the command of your Lord comes \u2013 and the angels row by row,"},"6016":{"id":6016,"surah":89,"ayah":23,"verse":"And when hell is brought that day \u2013 on that day will man reflect, but where is the time now to think?"},"6017":{"id":6017,"surah":89,"ayah":24,"verse":"He will say, \u201cAlas \u2013 if only I had sent some good deeds ahead, during my lifetime!\u201d"},"6018":{"id":6018,"surah":89,"ayah":25,"verse":"So on that day, no one punishes like He does!"},"6019":{"id":6019,"surah":89,"ayah":26,"verse":"And no one binds like He does!"},"6020":{"id":6020,"surah":89,"ayah":27,"verse":"O the contented soul!"},"6021":{"id":6021,"surah":89,"ayah":28,"verse":"Return towards your Lord \u2013 you being pleased with Him, and He pleased with you!"},"6022":{"id":6022,"surah":89,"ayah":29,"verse":"Then enter the ranks of My chosen bondmen!"},"6023":{"id":6023,"surah":89,"ayah":30,"verse":"And come into My Paradise!"},"6024":{"id":6024,"surah":90,"ayah":1,"verse":"I swear by this city (Mecca) \u2013"},"6025":{"id":6025,"surah":90,"ayah":2,"verse":"For you (O dear Prophet Mohammed \u2013 peace and blessings be upon him) are in this city."},"6026":{"id":6026,"surah":90,"ayah":3,"verse":"And by oath of your forefather Ibrahim, and by you \u2013 his illustrious son!"},"6027":{"id":6027,"surah":90,"ayah":4,"verse":"We have indeed created man surrounded by hardships."},"6028":{"id":6028,"surah":90,"ayah":5,"verse":"Does man think that no one will ever have power over him?"},"6029":{"id":6029,"surah":90,"ayah":6,"verse":"He says, \u201cI destroyed vast wealth.\u201d"},"6030":{"id":6030,"surah":90,"ayah":7,"verse":"Does man think that no one saw him?"},"6031":{"id":6031,"surah":90,"ayah":8,"verse":"Did We not make two eyes for him?"},"6032":{"id":6032,"surah":90,"ayah":9,"verse":"And a tongue and two lips?"},"6033":{"id":6033,"surah":90,"ayah":10,"verse":"And did We not guide him to the two elevated things?"},"6034":{"id":6034,"surah":90,"ayah":11,"verse":"So he did not quickly enter the steep valley."},"6035":{"id":6035,"surah":90,"ayah":12,"verse":"And what have you understood, what the valley is!"},"6036":{"id":6036,"surah":90,"ayah":13,"verse":"The freeing of a slave!"},"6037":{"id":6037,"surah":90,"ayah":14,"verse":"Or the feeding on a day of hunger."},"6038":{"id":6038,"surah":90,"ayah":15,"verse":"Of a related orphan,"},"6039":{"id":6039,"surah":90,"ayah":16,"verse":"Or of a homeless needy person!"},"6040":{"id":6040,"surah":90,"ayah":17,"verse":"And moreover to be of those who accepted faith, and who urged patience to one another and who urged graciousness to one another."},"6041":{"id":6041,"surah":90,"ayah":18,"verse":"These are the people of the right."},"6042":{"id":6042,"surah":90,"ayah":19,"verse":"And those who denied Our signs, are the people of the left."},"6043":{"id":6043,"surah":90,"ayah":20,"verse":"Upon them is a fire, in which they are imprisoned, closed and shut above them."},"6044":{"id":6044,"surah":91,"ayah":1,"verse":"By oath of the sun and its light"},"6045":{"id":6045,"surah":91,"ayah":2,"verse":"And by oath of the moon when it follows the sun"},"6046":{"id":6046,"surah":91,"ayah":3,"verse":"And by oath of the day when it reveals it"},"6047":{"id":6047,"surah":91,"ayah":4,"verse":"And by oath of the night when it hides it"},"6048":{"id":6048,"surah":91,"ayah":5,"verse":"And by oath of the heaven and by oath of Him Who made it."},"6049":{"id":6049,"surah":91,"ayah":6,"verse":"And by oath of the earth and by oath of Him Who spread it."},"6050":{"id":6050,"surah":91,"ayah":7,"verse":"And by oath of the soul and by oath of Him Who made it proper."},"6051":{"id":6051,"surah":91,"ayah":8,"verse":"And inspired in it the knowledge of its sins and its piety."},"6052":{"id":6052,"surah":91,"ayah":9,"verse":"Indeed successful is the one who made it pure."},"6053":{"id":6053,"surah":91,"ayah":10,"verse":"And indeed failed is the one who covered it in sin."},"6054":{"id":6054,"surah":91,"ayah":11,"verse":"The tribe of Thamud denied with rebellion."},"6055":{"id":6055,"surah":91,"ayah":12,"verse":"When the most wicked among them stood up in defiance."},"6056":{"id":6056,"surah":91,"ayah":13,"verse":"So the Noble Messenger of Allah said to them, \u201cStay away from the she camel of Allah, and from its turn to drink.\u201d"},"6057":{"id":6057,"surah":91,"ayah":14,"verse":"In response they belied him, and hamstrung the she camel \u2013 so Allah put ruin over them because of their sins and flattened their dwellings."},"6058":{"id":6058,"surah":91,"ayah":15,"verse":"And He does not fear its coming behind (to take vengeance)."},"6059":{"id":6059,"surah":92,"ayah":1,"verse":"By oath of the night when it covers"},"6060":{"id":6060,"surah":92,"ayah":2,"verse":"And by oath of the day when it shines"},"6061":{"id":6061,"surah":92,"ayah":3,"verse":"And by oath of the One Who created the male and the female."},"6062":{"id":6062,"surah":92,"ayah":4,"verse":"Indeed your efforts differ."},"6063":{"id":6063,"surah":92,"ayah":5,"verse":"So for one who gave and practised piety"},"6064":{"id":6064,"surah":92,"ayah":6,"verse":"And believed the best matter to be true"},"6065":{"id":6065,"surah":92,"ayah":7,"verse":"So We will very soon provide him ease."},"6066":{"id":6066,"surah":92,"ayah":8,"verse":"And for him who hoarded wealth and remained carefree,"},"6067":{"id":6067,"surah":92,"ayah":9,"verse":"And denied the best matter,"},"6068":{"id":6068,"surah":92,"ayah":10,"verse":"So We will very soon provide him hardship."},"6069":{"id":6069,"surah":92,"ayah":11,"verse":"His wealth will not avail him when he falls into ruin."},"6070":{"id":6070,"surah":92,"ayah":12,"verse":"Indeed guiding is upon Us."},"6071":{"id":6071,"surah":92,"ayah":13,"verse":"And indeed the Hereafter and this world both belong to Us."},"6072":{"id":6072,"surah":92,"ayah":14,"verse":"I therefore warn you of the fire that is ablaze."},"6073":{"id":6073,"surah":92,"ayah":15,"verse":"None except the most wicked will enter it."},"6074":{"id":6074,"surah":92,"ayah":16,"verse":"The one who denied and turned away."},"6075":{"id":6075,"surah":92,"ayah":17,"verse":"He will be far away from the most pious. (The first Caliph S. Abu Bakr Siddiq)"},"6076":{"id":6076,"surah":92,"ayah":18,"verse":"Who gives his wealth in order to be pure."},"6077":{"id":6077,"surah":92,"ayah":19,"verse":"And no one has done a favour to him, for which he should be compensated."},"6078":{"id":6078,"surah":92,"ayah":20,"verse":"He desires only to please his Lord, the Supreme."},"6079":{"id":6079,"surah":92,"ayah":21,"verse":"And indeed, soon he will be very pleased."},"6080":{"id":6080,"surah":93,"ayah":1,"verse":"By oath of the late morning,"},"6081":{"id":6081,"surah":93,"ayah":2,"verse":"And by oath of the night when it covers,"},"6082":{"id":6082,"surah":93,"ayah":3,"verse":"Your Lord has not forsaken you nor does He dislike you!"},"6083":{"id":6083,"surah":93,"ayah":4,"verse":"And indeed the latter is better for you than the former."},"6084":{"id":6084,"surah":93,"ayah":5,"verse":"And indeed your Lord will soon give you so much that you will be pleased. (Allah seeks to please the Holy Prophet \u2013 peace and blessings be upon him.)"},"6085":{"id":6085,"surah":93,"ayah":6,"verse":"Did He not find you an orphan, so provided you shelter?"},"6086":{"id":6086,"surah":93,"ayah":7,"verse":"And found you deeply engrossed in His love, so directed you?"},"6087":{"id":6087,"surah":93,"ayah":8,"verse":"And found you in need, so made you prosperous?"},"6088":{"id":6088,"surah":93,"ayah":9,"verse":"Therefore do not oppress the orphan."},"6089":{"id":6089,"surah":93,"ayah":10,"verse":"And do not rebuke the beggar."},"6090":{"id":6090,"surah":93,"ayah":11,"verse":"And abundantly proclaim the favours of your Lord."},"6091":{"id":6091,"surah":94,"ayah":1,"verse":"Did We not widen your bosom?"},"6092":{"id":6092,"surah":94,"ayah":2,"verse":"And relieve you of the burden \u2013"},"6093":{"id":6093,"surah":94,"ayah":3,"verse":"Which had broken your back?"},"6094":{"id":6094,"surah":94,"ayah":4,"verse":"And We have exalted your remembrance for you."},"6095":{"id":6095,"surah":94,"ayah":5,"verse":"So indeed with hardship is ease."},"6096":{"id":6096,"surah":94,"ayah":6,"verse":"Indeed with hardship is ease."},"6097":{"id":6097,"surah":94,"ayah":7,"verse":"So when you finish the prayer, strive in supplication."},"6098":{"id":6098,"surah":94,"ayah":8,"verse":"And incline towards your Lord."},"6099":{"id":6099,"surah":95,"ayah":1,"verse":"By oath of the fig and of the olive,"},"6100":{"id":6100,"surah":95,"ayah":2,"verse":"And by oath of Mount Sinai,"},"6101":{"id":6101,"surah":95,"ayah":3,"verse":"And by oath of this secure land,"},"6102":{"id":6102,"surah":95,"ayah":4,"verse":"We have indeed created man in the best shape."},"6103":{"id":6103,"surah":95,"ayah":5,"verse":"We then turned him towards all the lowest of the low states."},"6104":{"id":6104,"surah":95,"ayah":6,"verse":"Except those who accepted faith and did good deeds \u2013 for them is a never ending reward."},"6105":{"id":6105,"surah":95,"ayah":7,"verse":"So after this, what causes you to deny the judgement?"},"6106":{"id":6106,"surah":95,"ayah":8,"verse":"Is not Allah the Greatest Judge upon all judges?"},"6107":{"id":6107,"surah":96,"ayah":1,"verse":"Read with the name of your Lord Who created,"},"6108":{"id":6108,"surah":96,"ayah":2,"verse":"Created man from a clot."},"6109":{"id":6109,"surah":96,"ayah":3,"verse":"Read, and your Lord only is the Most Beneficent,"},"6110":{"id":6110,"surah":96,"ayah":4,"verse":"The One Who taught to write with the pen."},"6111":{"id":6111,"surah":96,"ayah":5,"verse":"The One Who taught man all what he did not know."},"6112":{"id":6112,"surah":96,"ayah":6,"verse":"Yes indeed, man is surely rebellious."},"6113":{"id":6113,"surah":96,"ayah":7,"verse":"As he considers himself independent!"},"6114":{"id":6114,"surah":96,"ayah":8,"verse":"Indeed towards your Lord only is the return."},"6115":{"id":6115,"surah":96,"ayah":9,"verse":"What is your opinion \u2013 regarding him who forbids \u2013"},"6116":{"id":6116,"surah":96,"ayah":10,"verse":"A bondman when he offers the prayer?"},"6117":{"id":6117,"surah":96,"ayah":11,"verse":"What is your opinion \u2013 if he were upon guidance,"},"6118":{"id":6118,"surah":96,"ayah":12,"verse":"He would enjoin piety, so how good it would be!"},"6119":{"id":6119,"surah":96,"ayah":13,"verse":"What is your opinion \u2013 if he denies and turns away, how wretched will be his state!"},"6120":{"id":6120,"surah":96,"ayah":14,"verse":"Did he not realise that Allah is watching?"},"6121":{"id":6121,"surah":96,"ayah":15,"verse":"Yes certainly, if he does not desist, We will seize him by the forelock."},"6122":{"id":6122,"surah":96,"ayah":16,"verse":"A forelock that lies, is sinful."},"6123":{"id":6123,"surah":96,"ayah":17,"verse":"So let him now call his gang!"},"6124":{"id":6124,"surah":96,"ayah":18,"verse":"We will now call the guards!"},"6125":{"id":6125,"surah":96,"ayah":19,"verse":"Yes indeed; do not listen to him \u2013 and prostrate, and become close to Us. (Command of Prostration # 14)"},"6126":{"id":6126,"surah":97,"ayah":1,"verse":"We have indeed sent down the Qur\u2019an in the Night of Destiny."},"6127":{"id":6127,"surah":97,"ayah":2,"verse":"And what have you understood, what the Night of Destiny is!"},"6128":{"id":6128,"surah":97,"ayah":3,"verse":"The Night of Destiny is better than a thousand months."},"6129":{"id":6129,"surah":97,"ayah":4,"verse":"In it descend the angels and Jibreel, by the command of their Lord \u2013 for all works."},"6130":{"id":6130,"surah":97,"ayah":5,"verse":"It is peace until the rising of dawn."},"6131":{"id":6131,"surah":98,"ayah":1,"verse":"The disbelieving People of the Book(s) and the polytheists would not have left their religion until the clear proof came to them."},"6132":{"id":6132,"surah":98,"ayah":2,"verse":"(The clear proof is) the Noble Messenger from Allah, who reads the pure pages."},"6133":{"id":6133,"surah":98,"ayah":3,"verse":"In which are written proper affairs."},"6134":{"id":6134,"surah":98,"ayah":4,"verse":"Nor did the People of the Book(s) get divided until after the clear proof came to them."},"6135":{"id":6135,"surah":98,"ayah":5,"verse":"And they were ordered only to worship Allah, believing purely in Him \u2013 devoted solely (to Him), and to establish the prayer and to pay the obligatory charity \u2013 and this is the straight religion."},"6136":{"id":6136,"surah":98,"ayah":6,"verse":"Indeed all disbelievers, the People of the Book(s) and the polytheists, are in the fire of hell \u2013 they will remain in it for ever; it is they who are the worst among the creation."},"6137":{"id":6137,"surah":98,"ayah":7,"verse":"Indeed those who accepted faith and did good deeds \u2013 it is they who are the best among the creation."},"6138":{"id":6138,"surah":98,"ayah":8,"verse":"Their reward is \u2013 with their Lord \u2013 everlasting Gardens of Eden beneath which rivers flow, in which they will abide for ever and ever; Allah is pleased with them and they are pleased with Him; this is for one who fears his Lord."},"6139":{"id":6139,"surah":99,"ayah":1,"verse":"When the earth is shaken with its appointed tremor."},"6140":{"id":6140,"surah":99,"ayah":2,"verse":"And the earth throws out its burdens."},"6141":{"id":6141,"surah":99,"ayah":3,"verse":"And man says, \u201cWhat has happened to it?\u201d"},"6142":{"id":6142,"surah":99,"ayah":4,"verse":"On that day earth will narrate its news,"},"6143":{"id":6143,"surah":99,"ayah":5,"verse":"Because your Lord sent a command to it."},"6144":{"id":6144,"surah":99,"ayah":6,"verse":"On that day men will return towards their Lord, in different groups, in order to be shown their deeds."},"6145":{"id":6145,"surah":99,"ayah":7,"verse":"So whoever does a good deed equal to the weight of the minutest particle, will see it."},"6146":{"id":6146,"surah":99,"ayah":8,"verse":"And whoever does an evil deed equal to the weight of the minutest particle, will see it."},"6147":{"id":6147,"surah":100,"ayah":1,"verse":"By oath of those that sprint, breathing heavily. (The horses used in Holy War.)"},"6148":{"id":6148,"surah":100,"ayah":2,"verse":"Striking stones with their hooves, sparking fire."},"6149":{"id":6149,"surah":100,"ayah":3,"verse":"And by oath of those who raid at dawn."},"6150":{"id":6150,"surah":100,"ayah":4,"verse":"So thereupon raising dust."},"6151":{"id":6151,"surah":100,"ayah":5,"verse":"Then penetrate to the centre of the enemy army."},"6152":{"id":6152,"surah":100,"ayah":6,"verse":"Indeed man is very ungrateful towards his Lord."},"6153":{"id":6153,"surah":100,"ayah":7,"verse":"And indeed he himself is a witness to it."},"6154":{"id":6154,"surah":100,"ayah":8,"verse":"And indeed he loves wealth to the extreme."},"6155":{"id":6155,"surah":100,"ayah":9,"verse":"So does he not know? When those in the graves are raised,"},"6156":{"id":6156,"surah":100,"ayah":10,"verse":"And all what lies in the breasts is opened \u2013"},"6157":{"id":6157,"surah":100,"ayah":11,"verse":"On that day their Lord surely knows all about them!"},"6158":{"id":6158,"surah":101,"ayah":1,"verse":"The event that terrifies the hearts!"},"6159":{"id":6159,"surah":101,"ayah":2,"verse":"What is the event that terrifies the hearts?"},"6160":{"id":6160,"surah":101,"ayah":3,"verse":"And what have you understood, what the event that terrifies the hearts is!"},"6161":{"id":6161,"surah":101,"ayah":4,"verse":"The day when men will be like scattered moths."},"6162":{"id":6162,"surah":101,"ayah":5,"verse":"And mountains will be like flying wool."},"6163":{"id":6163,"surah":101,"ayah":6,"verse":"So for one whose scales prove heavy,"},"6164":{"id":6164,"surah":101,"ayah":7,"verse":"He is therefore in the desired bliss."},"6165":{"id":6165,"surah":101,"ayah":8,"verse":"And for one whose scales prove light,"},"6166":{"id":6166,"surah":101,"ayah":9,"verse":"He is in the lap of one that abases."},"6167":{"id":6167,"surah":101,"ayah":10,"verse":"And what have you understood, what the one that abases is!"},"6168":{"id":6168,"surah":101,"ayah":11,"verse":"A flaming fire!"},"6169":{"id":6169,"surah":102,"ayah":1,"verse":"The craving for excess wealth kept you people neglectful."},"6170":{"id":6170,"surah":102,"ayah":2,"verse":"Until you confronted the graves."},"6171":{"id":6171,"surah":102,"ayah":3,"verse":"Yes certainly, you will soon realise!"},"6172":{"id":6172,"surah":102,"ayah":4,"verse":"Again, yes certainly, you will soon realise!"},"6173":{"id":6173,"surah":102,"ayah":5,"verse":"Yes certainly, if you had believed with certainty, you would not have craved for wealth."},"6174":{"id":6174,"surah":102,"ayah":6,"verse":"Indeed you will see hell."},"6175":{"id":6175,"surah":102,"ayah":7,"verse":"Again, indeed you will see it with certainty."},"6176":{"id":6176,"surah":102,"ayah":8,"verse":"Then, on that day, you will surely be questioned regarding the favours."},"6177":{"id":6177,"surah":103,"ayah":1,"verse":"By oath of this era of yours (O dear Prophet Mohammed \u2013 peace and blessings be upon him)."},"6178":{"id":6178,"surah":103,"ayah":2,"verse":"Indeed man is surely in a loss."},"6179":{"id":6179,"surah":103,"ayah":3,"verse":"Except those who accepted faith, and did good deeds and urged one another to the truth \u2013 and urged one another to have patience."},"6180":{"id":6180,"surah":104,"ayah":1,"verse":"Ruin is for every open slanderer, backbiter."},"6181":{"id":6181,"surah":104,"ayah":2,"verse":"Who accumulated wealth and hoarded it, counting."},"6182":{"id":6182,"surah":104,"ayah":3,"verse":"Does he think that his wealth will prolong his stay on earth forever?"},"6183":{"id":6183,"surah":104,"ayah":4,"verse":"Never! He will certainly be thrown into the Crushing One."},"6184":{"id":6184,"surah":104,"ayah":5,"verse":"And what have you understood what the Crushing One is!"},"6185":{"id":6185,"surah":104,"ayah":6,"verse":"The fire of Allah, that is ablaze."},"6186":{"id":6186,"surah":104,"ayah":7,"verse":"Which will climb on to the hearts."},"6187":{"id":6187,"surah":104,"ayah":8,"verse":"Indeed it will be shut over them."},"6188":{"id":6188,"surah":104,"ayah":9,"verse":"In extended columns."},"6189":{"id":6189,"surah":105,"ayah":1,"verse":"O dear Prophet (Mohammed \u2013 peace and blessings be upon him), did you not see how did your Lord deal with the People of the Elephant?"},"6190":{"id":6190,"surah":105,"ayah":2,"verse":"Did He not put their scheme into ruin?"},"6191":{"id":6191,"surah":105,"ayah":3,"verse":"And send flocks of birds upon them,"},"6192":{"id":6192,"surah":105,"ayah":4,"verse":"Which hit them with stones of baked clay,"},"6193":{"id":6193,"surah":105,"ayah":5,"verse":"So He made them like the leftover devoured leaves of farms?"},"6194":{"id":6194,"surah":106,"ayah":1,"verse":"Because of giving alliances to the Quraish."},"6195":{"id":6195,"surah":106,"ayah":2,"verse":"(We) gave them alliances during both their travels of winter and summer."},"6196":{"id":6196,"surah":106,"ayah":3,"verse":"So they must worship the Lord of this House. (The Ka\u2019aba)"},"6197":{"id":6197,"surah":106,"ayah":4,"verse":"The One Who gave them food in hunger, and bestowed them safety from a great fear."},"6198":{"id":6198,"surah":107,"ayah":1,"verse":"Just look at him, who belies the religion!"},"6199":{"id":6199,"surah":107,"ayah":2,"verse":"So it is he, who pushes away the orphan,"},"6200":{"id":6200,"surah":107,"ayah":3,"verse":"And does not urge to feed the needy."},"6201":{"id":6201,"surah":107,"ayah":4,"verse":"So ruin is to those offerers of prayer \u2013"},"6202":{"id":6202,"surah":107,"ayah":5,"verse":"Those who are neglectful of their prayer."},"6203":{"id":6203,"surah":107,"ayah":6,"verse":"Those who make a display (of their deeds)."},"6204":{"id":6204,"surah":107,"ayah":7,"verse":"And do not let others ask for small utilities."},"6205":{"id":6205,"surah":108,"ayah":1,"verse":"We have indeed bestowed the Kausar* upon you (O dear prophet Mohammed \u2013 peace and blessings be upon him). (*Infinite excellent qualities \/ the greatest number of followers \/ the sweet pond on the Day of Resurrection)"},"6206":{"id":6206,"surah":108,"ayah":2,"verse":"So offer the prayers for your Lord, and perform the sacrifice."},"6207":{"id":6207,"surah":108,"ayah":3,"verse":"Indeed it is your enemy who is bereft of all goodness."},"6208":{"id":6208,"surah":109,"ayah":1,"verse":"Proclaim, (O dear prophet Mohammed \u2013 peace and blessings be upon him), \u201cO disbelievers!\u201d"},"6209":{"id":6209,"surah":109,"ayah":2,"verse":"Neither do I worship what you worship."},"6210":{"id":6210,"surah":109,"ayah":3,"verse":"Nor do you worship Whom I worship."},"6211":{"id":6211,"surah":109,"ayah":4,"verse":"And neither will I ever worship what you worship."},"6212":{"id":6212,"surah":109,"ayah":5,"verse":"Nor will you worship Whom I worship."},"6213":{"id":6213,"surah":109,"ayah":6,"verse":"For you is your religion, and for me is mine."},"6214":{"id":6214,"surah":110,"ayah":1,"verse":"When the help and victory of Allah come,"},"6215":{"id":6215,"surah":110,"ayah":2,"verse":"And you see people entering the religion of Allah in multitudes,"},"6216":{"id":6216,"surah":110,"ayah":3,"verse":"Then proclaim the Purity of your Lord while praising Him, and seek forgiveness from Him; indeed He is the Most Acceptor of Repentance."},"6217":{"id":6217,"surah":111,"ayah":1,"verse":"May both the hands of Abu Lahab be destroyed \u2013 and they are destroyed!"},"6218":{"id":6218,"surah":111,"ayah":2,"verse":"His wealth did not benefit him in the least, nor did whatever he earned."},"6219":{"id":6219,"surah":111,"ayah":3,"verse":"He will soon enter the flaming fire."},"6220":{"id":6220,"surah":111,"ayah":4,"verse":"And so will his wife; carrying a bundle of firewood on her head."},"6221":{"id":6221,"surah":111,"ayah":5,"verse":"A rope made from palm fibre around her neck!"},"6222":{"id":6222,"surah":112,"ayah":1,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cHe is Allah, He is One.\u201d"},"6223":{"id":6223,"surah":112,"ayah":2,"verse":"\u201cAllah is the Un-wanting.\u201d (Perfect, does not require anything.)"},"6224":{"id":6224,"surah":112,"ayah":3,"verse":"\u201cHe has no offspring, nor is He born from anything.\u201d"},"6225":{"id":6225,"surah":112,"ayah":4,"verse":"\u201cAnd there is none equal to Him.\u201d"},"6226":{"id":6226,"surah":113,"ayah":1,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI take refuge of the One Who creates the Daybreak.\u201d"},"6227":{"id":6227,"surah":113,"ayah":2,"verse":"\u201cFrom the evil of His entire creation.\u201d"},"6228":{"id":6228,"surah":113,"ayah":3,"verse":"\u201cAnd from the evil of the matter that darkens when it sets.\u201d"},"6229":{"id":6229,"surah":113,"ayah":4,"verse":"\u201cAnd from the evil of the witches who blow into knots.\u201d"},"6230":{"id":6230,"surah":113,"ayah":5,"verse":"\u201cAnd from the evil of the envier when he is envious of me.\u201d"},"6231":{"id":6231,"surah":114,"ayah":1,"verse":"Proclaim (O dear Prophet Mohammed \u2013 peace and blessings be upon him), \u201cI take refuge of the One Who is the Lord of all mankind.\u201d"},"6232":{"id":6232,"surah":114,"ayah":2,"verse":"\u201cThe King of all mankind.\u201d"},"6233":{"id":6233,"surah":114,"ayah":3,"verse":"\u201cThe God of all mankind.\u201d"},"6234":{"id":6234,"surah":114,"ayah":4,"verse":"\u201cFrom the evil of the one who instils evil thoughts in the hearts \u2013 and stays hidden.\u201d"},"6235":{"id":6235,"surah":114,"ayah":5,"verse":"\u201cThose who instil evil thoughts into the hearts of men.\u201d"},"6236":{"id":6236,"surah":114,"ayah":6,"verse":"\u201cAmong the jinns and men.\u201d"}}}};