            "arabicnumber": 1333,
            "text": "\u0646\u0627\u0641\u0639 \u0633\u06d2 \u0631\u0648\u0627\u06cc\u062a \u06c1\u06d2 \u06a9\u06c1 \u062d\u06a9\u06cc\u0645 \u0628\u0646 \u062d\u0632\u0627\u0645 \u0646\u06d2 \u063a\u0644\u06c1 \u062e\u0631\u06cc\u062f\u0627 \u062c\u0648 \u062d\u0636\u0631\u062a \u0639\u0645\u0631 \u0646\u06d2 \u0644\u0648\u06af\u0648\u06ba \u06a9\u0648 \u062f\u0644\u0648\u0627\u06cc\u0627 \u062a\u06be\u0627 \u067e\u06be\u0631 \u062d\u06a9\u06cc\u0645 \u0628\u0646 \u062d\u0632\u0627\u0645 \u0646\u06d2 \u0627\u0633 \u063a\u0644\u06c1 \u06a9\u0648 \u0628\u06cc\u0686 \u0688\u0627\u0644\u0627 \u0642\u0628\u0636\u06c1 \u0633\u06d2 \u067e\u06c1\u0644\u06d2 \u062c\u0628 \u062d\u0636\u0631\u062a \u0639\u0645\u0631 \u06a9\u0648 \u0627\u0633 \u06a9\u06cc \u062e\u0628\u0631 \u067e\u06c1\u0646\u0686\u06cc \u0622\u067e \u0646\u06d2 \u0648\u06c1 \u063a\u0644\u06c1 \u062d\u06a9\u06cc\u0645 \u0628\u0646 \u062d\u0632\u0627\u0645 \u06a9\u0648 \u067e\u06be\u0631 \u0648\u0627 \u062f\u06cc\u0627 \u0627\u0648\u0631 \u06a9\u06c1\u0627 \u062c\u0633 \u063a\u0644\u06c1 \u06a9\u0648 \u062a\u0648 \u062e\u0631\u06cc\u062f\u06d2 \u067e\u06be\u0631 \u0627\u0633 \u06a9\u0648 \u0645\u062a \u0628\u06cc\u0686 \u062c\u0628 \u062a\u06a9 \u0627\u0633 \u067e\u0631 \u0642\u0628\u0636\u06c1 \u0646\u06c1 \u06a9\u0631 \u0644\u06d2 \u06d4 \u0627\u0645\u0627\u0645 \u0645\u0627\u0644\u06a9 \u06a9\u0648 \u067e\u06c1\u0646\u0686\u0627 \u06a9\u06c1 \u0645\u0631\u0648\u0627\u0646 \u0628\u0646 \u062d\u06a9\u0645 \u06a9\u06d2 \u0639\u06c1\u062f \u062d\u06a9\u0648\u0645\u062a \u0645\u06cc\u06ba \u0644\u0648\u06af\u0648\u06ba \u06a9\u0648 \u0633\u0646\u062f\u06cc\u06ba \u0645\u0644\u06cc\u06ba \u062c\u0627\u0631 \u06a9\u06d2 \u063a\u0644\u06c1 \u06a9\u06cc \u0644\u0648\u06af\u0648\u06ba \u0646\u06d2 \u0627\u0646 \u0633\u0646\u062f\u0648\u06ba \u06a9\u0648 \u0628\u06cc\u0686\u0627 \u0627\u06cc\u06a9 \u062f\u0648\u0633\u0631\u06d2 \u06a9\u06d2 \u06c1\u0627\u062a\u06be \u0642\u0628\u0644 \u0627\u0633 \u0628\u0627\u062a \u06a9\u06d2 \u06a9\u06c1 \u063a\u0644\u06c1 \u0627\u067e\u0646\u06d2 \u0642\u0628\u0636\u0629 \u0645\u06cc\u06ba \u0644\u0627\u0626\u06cc\u06ba \u062a\u0648 \u0632\u06cc\u062f \u0628\u0646 \u062b\u0627\u0628\u062a \u0627\u0648\u0631 \u0627\u06cc\u06a9 \u0627\u0648\u0631 \u0635\u062d\u0627\u0628\u06c1 \u0645\u0631\u0648\u0627\u0646 \u06a9\u06d2 \u067e\u0627\u0633 \u06af\u0626\u06d2 \u0627\u0648\u0631 \u06a9\u06c1\u0627 \u06a9\u06cc\u0627 \u062a\u0648 \u0631\u0628\u0627 \u06a9\u0648 \u062f\u0631\u0633\u062a \u062c\u0627\u0646\u062a\u0627 \u06c1\u06d2 \u0627\u06d2 \u0645\u0631\u0648\u0627\u0646 \u0645\u0631\u0648\u0627\u0646 \u0646\u06d2 \u06a9\u06c1\u0627 \u0645\u0639\u0627\u0630\u0627\u0644\u0644\u06c1 \u06a9\u06cc\u0627 \u06a9\u06c1\u062a\u06d2 \u06c1\u0648 \u0627\u0646\u06c1\u0648\u06ba \u0646\u06d2 \u06a9\u06c1\u0627 \u06a9\u06c1 \u06cc\u06c1 \u0633\u0646\u062f\u06cc\u06ba \u062c\u0646 \u0644\u0648\u06af\u0648\u06ba \u0646\u06d2 \u062e\u0631\u06cc\u062f\u0627 \u067e\u06be\u0631 \u062e\u0631\u06cc\u062f \u06a9\u0631 \u062f\u0648\u0628\u0627\u0631\u06c1 \u0628\u06cc\u0686\u0627 \u0642\u0628\u0644\u06c1 \u063a\u0644\u06c1 \u0644\u06cc\u0646\u06d2 \u06a9\u06d2 \u0645\u0631\u0648\u0627\u0646 \u0646\u06d2 \u0686\u0648\u06a9\u06cc\u062f\u0627\u0631 \u06a9\u0648 \u0628\u06be\u06cc\u062c\u0627 \u06a9\u06c1 \u0648\u06c1 \u0633\u0646\u062f\u06cc\u06ba \u0644\u0648\u06af\u0648\u06ba \u0633\u06d2 \u0686\u06be\u06cc\u0646 \u06a9\u0631 \u0633\u0646\u062f \u0648\u0627\u0644\u0648\u06ba \u06a9\u06d2 \u062d\u0648\u0627\u0644\u06d2 \u06a9\u0631 \u062f\u06cc\u06ba \u06d4 \u0627\u0645\u0627\u0645 \u0645\u0627\u0644\u06a9 \u06a9\u0648 \u067e\u06c1\u0646\u0686\u0627 \u0627\u06cc\u06a9 \u0634\u062e\u0635 \u0646\u06d2 \u0627\u0646\u0627\u062c \u062e\u0631\u06cc\u062f\u0646\u0627 \u0686\u0627\u06c1\u0627 \u0627\u06cc\u06a9 \u0634\u062e\u0635 \u0633\u06d2 \u0648\u0639\u062f\u06d2 \u067e\u0631 \u062a\u0648 \u0628\u0627\u0626\u0639 (\u0628\u0686\u0646\u06d2 \u0648\u0627\u0644\u0627) \u0645\u0634\u062a\u0631\u06cc (\u062e\u0631\u06cc\u062f\u0646\u06d2 \u0648\u0627\u0644\u0627) \u06a9\u0648 \u0628\u0627\u0632\u0627\u0631 \u0645\u06cc\u06ba \u0644\u06d2 \u06af\u06cc\u0627 \u0627\u0648\u0631 \u0627\u0633 \u06a9\u0648 \u0628\u0648\u0631\u06d2 \u062f\u06a9\u06be\u0627 \u06a9\u0631 \u06a9\u06c1\u0646\u06d2 \u0644\u06af\u0627 \u06a9\u0648\u0646 \u0633\u06d2 \u063a\u0644\u06c1 \u0645\u06cc\u06ba \u062a\u0645\u06c1\u0627\u0631\u06cc \u0648\u0627\u0633\u0637\u06d2 \u062e\u0631\u06cc\u062f \u06a9\u0631\u0648\u06ba \u0645\u0634\u062a\u0631\u06cc (\u062e\u0631\u06cc\u062f\u0646\u06d2 \u0648\u0627\u0644\u0627) \u0646\u06d2 \u06a9\u06c1\u0627 \u06a9\u06cc\u0627 \u062a\u0648 \u0645\u06cc\u0631\u06d2 \u06c1\u0627\u062a\u06be \u0627\u0633 \u0686\u06cc\u0632 \u06a9\u0627 \u0628\u06cc\u0686\u062a\u0627 \u06c1\u06d2 \u062c\u0648 \u062e\u0648\u062f \u062a\u06cc\u0631\u06d2 \u067e\u0627\u0633 \u0646\u06c1\u06cc\u06ba \u06c1\u06d2 \u067e\u06be\u0631 \u0628\u0627\u0626\u0639 (\u0628\u0686\u0646\u06d2 \u0648\u0627\u0644\u0627) \u0627\u0648\u0631 \u0645\u0634\u062a\u0631\u06cc (\u062e\u0631\u06cc\u062f\u0646\u06d2 \u0648\u0627\u0644\u0627 \u0627\u0648\u0631 \u0628\u06cc\u0686\u0646\u06d2 \u0648\u0627\u0644\u0627) \u062f\u0648\u0646\u0648\u06ba \u0639\u0628\u062f\u0627\u0644\u0644\u06c1 \u0628\u0646 \u0639\u0645\u0631 \u06a9\u06d2 \u067e\u0627\u0633 \u0622\u0626\u06d2 \u0627\u0648\u0631 \u0627\u0646 \u0633\u06d2 \u0628\u06cc\u0627\u0646 \u06a9\u06cc\u0627 \u0639\u0628\u062f\u0627\u0644\u0644\u06c1 \u0628\u0646 \u0639\u0645\u0631 \u0646\u06d2 \u0645\u0634\u062a\u0631\u06cc (\u062e\u0631\u06cc\u062f\u0646\u06d2 \u0648\u0627\u0644\u0627) \u0633\u06d2 \u06a9\u06c1\u0627 \u0645\u062a \u062e\u0631\u06cc\u062f\u0648 \u0627\u0633 \u0686\u06cc\u0632 \u06a9\u0648 \u062c\u0648 \u0628\u0627\u0626\u0639 (\u0628\u0686\u0646\u06d2 \u0648\u0627\u0644\u0627) \u06a9\u06d2 \u067e\u0627\u0633 \u0646\u06c1\u06cc\u06ba \u06c1\u06d2 \u0627\u0648\u0631 \u0628\u0627\u0626\u0639 (\u0628\u0686\u0646\u06d2 \u0648\u0627\u0644\u0627) \u0633\u06d2 \u06a9\u06c1\u0627 \u0645\u062a \u0628\u06cc\u0686 \u0627\u0633 \u0686\u06cc\u0632 \u06a9\u0648 \u062c\u0648 \u062a\u06cc\u0631\u06d2 \u067e\u0627\u0633 \u0646\u06c1\u06cc\u06ba \u06c1\u06d2 \u06d4",
